(window.webpackJsonp=window.webpackJsonp||[]).push([[1394],{1747:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2225-xich-tieu-kiem"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2225-xich-tieu-kiem"}},[n._v("#")]),n._v(" Chương 2225: Xích Tiêu kiếm")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Muốn nhìn kiếm thuật, khẳng định liền không có ác ý."),i("br"),i("br"),n._v("Thích võ thành si người, bình thường đều tâm tư đơn thuần, không có nhiều như vậy cong cong lượn quanh lượn quanh, cũng không có như vậy thích giết chóc thành cuồng."),i("br"),i("br"),n._v("Rõ ràng những này, không có quá nhiều chần chừ, Trương Huyền đứng dậy, cổ tay khẽ đảo, Thần Vương kiếm xuất hiện tại lòng bàn tay."),i("br"),i("br"),n._v("Thấy hắn muốn thi triển kiếm thuật, thanh niên sau lưng lão giả, đưa tay về phía trước, sân nhỏ lập tức bị một cỗ đặc thù lực lượng bao phủ, ngoại nhân không cách nào cảm ứng."),i("br"),i("br"),n._v("Nhìn thấy hắn chiêu này, Trương Huyền lông mày lần nữa hơi nhúc nhích một chút."),i("br"),i("br"),n._v("Loại này lăng không bày trận, tốc độ nhanh như vậy, hắn cũng có thể làm được, nhưng muốn cùng đối phương như vậy tự nhiên, liền không gian lực lượng đều dùng tới, liền không hoàn thành được."),i("br"),i("br"),n._v("Nhìn tới vị lão giả này, cho dù không phải Đế Quân, tu vi cũng so với bình thường phong hào Thần Vương mạnh mẽ không biết bao nhiêu, bản thân hoàn toàn không phải đối thủ."),i("br"),i("br"),n._v("Biết thanh niên trước mắt không có ác ý, không suy nghĩ thêm nữa những này, nắm chặt Thần Vương kiếm, trong tích tắc, cả người đi vào đặc thù ý cảnh, phảng phất biến thành một chuôi ngạo nghễ thiên địa trường kiếm."),i("br"),i("br"),n._v("Phần phật!"),i("br"),i("br"),n._v("Kiếm động."),i("br"),i("br"),n._v("Gió nhẹ chợt nổi lên, sóng nước nhộn nhạo."),i("br"),i("br"),n._v("Tâm Tự Song Ti Võng, Trung Hữu Thiên Thiên Kết!"),i("br"),i("br"),n._v("Khởi Vi Sá Du Quan, Can Tràng Hiếu Tư Kích!"),i("br"),i("br"),n._v("Xuân Tàm Đáo Tử Ti Phương Tẫn, Chá Cự Thành Hôi Lệ Thủy Kiền!"),i("br"),i("br"),n._v("Thụ Dục Tĩnh Nhi Phong Bất Chỉ, Tử Dục Dưỡng Nhi Thân Bất Đãi!"),i("br"),i("br"),n._v("Bốn bộ kiếm pháp, như nước chảy bừng lên, trên không trung tràn ngập, như là sương mù, lại hình như là mộng cảnh, để cho người ta phân biệt không ra đến đáy mạnh cỡ nào."),i("br"),i("br"),n._v("Thanh niên nhìn trước mắt kiếm pháp, lặng im không nói."),i("br"),i("br"),n._v("Trương Huyền thu kiếm, đầy trời kiếm khí, thu về thân kiếm, ôm quyền mà đứng: “Mong rằng Đế Quân chỉ điểm!”"),i("br"),i("br"),n._v("Trước mắt vị này là Thần giới dùng kiếm tồn tại cường đại nhất, từ hắn chỉ điểm một câu, đối với mình kiếm thuật, nhất định có chỗ tốt rất lớn."),i("br"),i("br"),n._v("“Có thể lĩnh ngộ ra loại kiếm pháp này, quả thực bất phàm...” Thanh niên một mực lạnh lùng trên mặt, lộ ra tươi cười: “Chỉ điểm, ta chỉ điểm không được, ngươi mở rộng ra con đường, chỉ có mình có thể tiếp tục tiến lên! Chẳng qua... Trong kiếm ý của ngươi, tràn đầy tiếc nuối, ít đi bảo vệ cùng kiên nghị!”"),i("br"),i("br"),n._v("“Bảo vệ cùng kiên nghị?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("Tâm Tự Song Ti Võng, Can Tràng Hiếu Tư Kích, Xuân Tàm Đáo Tử Ti Phương Tẫn, Chá Cự Thành Hôi Lệ Thủy Kiền, Thụ Dục Tĩnh Nhi Phong Bất Chỉ..."),i("br"),i("br"),n._v("Quả thực đều mang triền miên."),i("br"),i("br"),n._v("Bất quá, Thiên Nhược Hữu Tình công pháp, lấy tình lập bản, lấy tình làm kiếm, tự nhiên muốn tình ý rả rích."),i("br"),i("br"),n._v("“Loạn thế lúc đến, người người khó mà tự vệ, nếu như thân nhân của ngươi, bằng hữu, người yêu nhận lấy uy hiếp, nên làm cái gì?”"),i("br"),i("br"),n._v("Thanh niên hỏi."),i("br"),i("br"),n._v("“Cái này...” Trương Huyền dừng lại một chút, nói: “Đương nhiên là đem hết toàn lực, bảo vệ bọn hắn...”"),i("br"),i("br"),n._v("“Đúng, là bảo vệ, không tiếc bất cứ giá nào, không tiếc thân tử bảo vệ! Kiếm pháp của ngươi bên trong... Không có!” Thanh niên nói."),i("br"),i("br"),n._v("Trương Huyền nói không ra lời."),i("br"),i("br"),n._v("Quả thực, kiếm pháp của hắn, ít đi loại này kiên trì."),i("br"),i("br"),n._v("“Lấy tình làm kiếm, không chỉ có là triền miên, càng quan trọng hơn là trách nhiệm cùng chịu trách nhiệm! Liền giống như ngươi cuối cùng một kiếm, tràn đầy đối cha mẹ không bỏ, đối không cách nào phụng dưỡng thân nhân hối hận, nhưng, ngươi có bao giờ nghĩ tới, cha mẹ khi còn sống, muốn nhìn nhất đến là cái gì?” Thanh niên hỏi."),i("br"),i("br"),n._v("“Muốn nhìn nhất đến là... Ta sống ra tốt nhất bản thân, mỗi ngày đều vui vẻ hạnh phúc...” Chần chờ một chút, Trương Huyền nói."),i("br"),i("br"),n._v("Mặc dù không biết ý tưởng của cha mẹ, nhưng thân là lão sư, biết mình đối học sinh thái độ."),i("br"),i("br"),n._v("Hắn chỉ hy vọng Triệu Nhã đám người, có thể sống ra bản thân, sống vui vẻ, còn có thể đi đến loại cảnh giới nào, tu vi đi đến loại tình trạng nào, đối với hắn mà nói, đều không quan trọng."),i("br"),i("br"),n._v("Dù là hiện tại, chỉ là phổ thông Thần Linh... Cũng sẽ không nặng bên này nhẹ bên kia."),i("br"),i("br"),n._v("“Không sai, bọn họ muốn nhìn nhất đến là sống ra tốt nhất bản thân, mà không phải, lưu luyến không rời, một mực tại hối hận. Người vĩnh viễn đều phải nhìn về phía trước, mà không phải trì trệ không tiến!” Thanh niên nói."),i("br"),i("br"),n._v("“Đúng...” Chấn động trong lòng, Trương Huyền ngây người."),i("br"),n._v(" Đúng a!"),i("br"),i("br"),n._v("Một mực đắm chìm trong trong thống khổ, tuyệt không phải người thân nguyện ý nhìn thấy."),i("br"),i("br"),n._v("Bọn họ càng muốn, ngươi sinh hoạt hạnh phúc."),i("br"),i("br"),n._v("Tận hiếu là đúng, đau khổ cũng là đúng, nhưng một mực chìm đắm trong đó, liền không đúng."),i("br"),i("br"),n._v("Mỗi người đều có nhân sinh của mình, còn có con đường của mình cần tiến lên, không thể một mực trì trệ không tiến."),i("br"),i("br"),n._v("Sinh lão bệnh tử, yêu xa cách, oán lâu dài, cầu không được, không buông được..."),i("br"),i("br"),n._v("Nhân sinh tám khổ, nhất định phải tiếp nhận, cũng nhất định phải thả ra."),i("br"),i("br"),n._v("Cái này... Chính là nhân sinh."),i("br"),i("br"),n._v("Muốn sống sót, lại đắng chát, cũng phải nhịn lấy."),i("br"),i("br"),n._v("“Đa tạ tiền bối chỉ điểm...” Trong lòng dường như bắt được một đạo hào quang, Trương Huyền biết cứ việc rất nhỏ yếu, lại tương đương thay đổi tiến lên phương hướng, lúc này cũng không nhiều lời, khom người đến cùng."),i("br"),i("br"),n._v("Ngắn ngủi vài câu chỉ điểm, được lợi rất nhiều."),i("br"),i("br"),n._v("“Có thể nhanh như vậy rõ ràng, nhìn tới cũng không chọn lầm người...” Thanh niên cười nhạt một tiếng, đứng dậy: “Tốt, ta cũng muốn đi, con đường tiếp theo, cần bản thân, tự giải quyết cho tốt!”"),i("br"),i("br"),n._v("“Vậy thì muốn đi?”"),i("br"),i("br"),n._v("Trương Huyền sững sờ."),i("br"),i("br"),n._v("Còn tưởng rằng mục đích của đối phương là cái gì, không nghĩ tới chỉ nhìn bản thân thi triển kiếm pháp, hơn nữa chỉ điểm vài câu, liền muốn rời khỏi, chẳng lẽ, lần này tới là chuyên môn chỉ điểm mình?"),i("br"),i("br"),n._v("Đứng dậy, thanh niên nói: “Kiếm Nghiêu, đem Xích Tiêu kiếm, cho hắn đi!”"),i("br"),i("br"),n._v("“Vâng!” Lão giả tiến về phía trước một bước, cổ tay rung lên, một thanh trường kiếm xuất hiện tại phía trước, nhẹ nhàng đưa tới."),i("br"),i("br"),n._v("Chuôi kiếm này mới xuất hiện trên không trung, không gian bốn phía liền xuất hiện từng đạo vết rách, Trương Huyền trong tay thần Vương Kiếm kìm lòng không được tránh thoát bàn tay, quỳ mọp xuống đất."),i("br"),i("br"),n._v("Trương Huyền lông mày nhảy dựng."),i("br"),i("br"),n._v("Thần Vương kiếm là Thần Vương cảnh giới trường kiếm, là trước mắt hắn mới thôi cường đại nhất vũ khí, loại cấp bậc này kiếm, gặp được chuôi này Xích Tiêu, trực tiếp quỳ gối..."),i("br"),i("br"),n._v("Cái kia mạnh bao nhiêu?"),i("br"),i("br"),n._v("“Vô công bất thụ lộc...”"),i("br"),i("br"),n._v("Vội vàng khom người, không dám nhận nhận."),i("br"),i("br"),n._v("“Không cần khách khí, chuôi kiếm này, là ta mấy chục năm trước, chuyên môn vì ngươi luyện chế, cầm đi...” Thanh niên hai tay chắp sau lưng, cũng không xoay người."),i("br"),i("br"),n._v("“Mấy chục năm trước?” Trương Huyền ngẩn ngơ, không biết trả lời như thế nào."),i("br"),i("br"),n._v("Hắn tính toán đâu ra đấy cũng chỉ chừng hai mươi tuổi mà thôi, hơn nữa còn là danh sư đại lục thời gian, đổi lại Thần giới thời gian, đến bây giờ không sống qua hơn một tháng..."),i("br"),i("br"),n._v("Mấy chục năm trước, ngươi xác định là chuyên môn vì ta luyện chế..."),i("br"),i("br"),n._v("“Đa tạ Đế Quân!”"),i("br"),i("br"),n._v("Thấy đối phương thái độ dứt khoát, bản thân cũng gấp cần tăng thực lực lên, Trương Huyền không tại từ chối, đưa tay đem trường kiếm tiếp lấy."),i("br"),i("br"),n._v("Không có trọng lượng, không có mạnh mẽ khí linh, cũng không có bất kỳ khó chịu nào, thật giống chuôi kiếm này, vừa xuất hiện liền sinh trưởng ở cánh tay hắn bên trên đồng dạng, thuộc về hắn cánh tay kéo dài."),i("br"),i("br"),n._v("Cường đại như thế bảo kiếm, rơi vào bàn tay một khắc này, cũng đã luyện hóa, thành vật thuộc về chính hắn."),i("br"),i("br"),n._v("Trước đó uy áp, lực lượng, cũng trong nháy mắt biến mất, trở nên chất phác tự nhiên, cùng trường kiếm bình thường đồng dạng, nhìn không ra đẳng cấp, nhưng thân là kiếm chủ nhân, biết ẩn chứa trong đó lực lượng kinh khủng."),i("br"),i("br"),n._v("Nếu như dùng chuôi kiếm này lần nữa thi triển Thiên Nhược Hữu Tình kiếm pháp lời nói, Linh Lung tiên tử ý niệm hội tụ mặt to, đều có thể không chống đỡ được, tại chỗ bị đánh nát!"),i("br"),i("br"),n._v("Trong lòng cảm ơn, lần nữa quỳ gối, Trương Huyền nhớ tới cái gì, nhìn về phía thanh niên trước mắt, trong ánh mắt mang theo trông đợi: “Không biết vãn bối... Có thể hay không nhìn một chút Đế Quân lĩnh ngộ kiếm thuật?”"),i("br"),i("br"),n._v("Đối phương chỉ điểm kiếm pháp của mình, nếu như lại có thể quan sát thoáng cái đối phương, có lẽ cảm ngộ sẽ càng lớn."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2239"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2241"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);