(window.webpackJsonp=window.webpackJsonp||[]).push([[1398],{1753:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2229-thien-đao-chi-tranh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2229-thien-đao-chi-tranh"}},[n._v("#")]),n._v(" Chương 2229: Thiên đạo chi tranh")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("“Không sai, cùng ngươi nghĩ đồng dạng, đây là Thiên đạo chi tranh!”"),i("br"),i("br"),n._v("Dường như nhìn ra ý nghĩ của hắn, Khổng sư thở dài nói: “Không có cách nào tránh khỏi, cũng không thể tránh khỏi! Thiên hạ đại thế, phân lâu tất hợp, hợp lâu tất phân, đây là nhất định, không có người có thể chạy trốn, cũng không cách nào chạy trốn. Ta thắng, sẽ thành Thiên đạo mới chưởng khống giả, thua, hồn phi phách tán, tất cả đều triệt để tiêu tán!”"),i("br"),i("br"),n._v("“Thiên đạo chi tranh...” Trương Huyền sắc mặt tái nhợt: “Vậy ta cùng nàng...”"),i("br"),i("br"),n._v("Nếu như nói chính là thật, Lạc Nhược Hi về sau chẳng phải đồng dạng sẽ cùng bản thân chiến đấu?"),i("br"),i("br"),n._v("Đồng dạng tranh thiên mệnh?"),i("br"),i("br"),n._v("Chẳng trách mình thích nàng thời điểm, nàng cũng không nguyện ý, thậm chí các loại từ chối, còn tưởng rằng phía sau nàng có một loại nào đó lợi hại bối cảnh, nhận gia tộc cản trở, hiện tại xem ra, căn bản không phải chuyện này."),i("br"),i("br"),n._v("Thiên đạo chi tranh, một mất một còn!"),i("br"),i("br"),n._v("Tại danh sư đại lục thời điểm, nhiều lần hỏi thăm thân phận chân thật của nàng, một mực không được đến chuẩn xác trả lời, cũng không phải là nàng không muốn nói... Mà là đều là Thiên đạo một phần, một khi mở miệng, nhất định sẽ dẫn tới Thiên đạo phản phệ."),i("br"),i("br"),n._v("Làm không cẩn thận, trước thời hạn nhất định phải phân ra sinh tử."),i("br"),i("br"),n._v("“Ta thắng... Sau này sẽ là ngươi cùng ta tranh đấu, thua, chính là ngươi cùng nàng! Đây là mệnh, không cách nào kháng cự.” Khổng sư nhàn nhạt nhìn qua."),i("br"),i("br"),n._v("Nói đến sinh tử của mình, liền giống như nói người khác đồng dạng, trên mặt không có bất kỳ cái gì sóng lớn."),i("br"),i("br"),n._v("“Ngươi nói Thiên đạo, là Thần giới Thiên đạo?” Trương Huyền khó tin: “Thật không có cách nào tránh khỏi?”"),i("br"),i("br"),n._v("“Không có cách nào tránh khỏi!” Khổng sư lắc đầu."),i("br"),i("br"),n._v("“Nhưng... Thần giới rất bình thường ah, đồng dạng có thể tu luyện, có thể sinh ra vô số cường giả, nếu như Thiên đạo thật bị chúng ta phân ra, không phải là như vậy...” Trương Huyền vội vàng mở miệng."),i("br"),i("br"),n._v("Nếu Thiên đạo bị hắn bọn họ ba người chia hết, cần tranh thiên mệnh... Thần giới Thiên đạo, nên hỗn loạn kinh khủng mới là, vì sao ổn định như cũ?"),i("br"),i("br"),n._v("“Bình thường?” Khổng sư cười khổ: “Hơn bốn mươi năm trước liền không bình thường, linh khí suy yếu, vô số giống loài diệt tuyệt, Thần Linh bị ép chuyển tới nội thành cư trú, không có Thần Linh chi khí không cách nào đột phá... Tài nguyên tu luyện thiếu thốn, ngắn ngủi mấy chục năm còn có thể chống đỡ, lại quá trăm năm, ngàn năm, sẽ triệt để hoang vu, sinh mệnh đều có thể không còn tồn tại...”"),i("br"),i("br"),n._v("“Cái này...” Muốn nói lại thôi, Trương Huyền nói không ra lời."),i("br"),i("br"),n._v("Đối phương nói là sự thật."),i("br"),i("br"),n._v("Linh khí suy yếu chẳng qua hơn bốn mươi năm thời gian, không được người địa phương, đã xuất hiện hoang vu, vô số Thần Linh cùng sinh mệnh, bởi vậy tử vong, không biết bao nhiêu tu luyện giả, nhận hạn chế tài nguyên, thực lực lại không cách nào tăng lên..."),i("br"),i("br"),n._v("Thần giới nội tình dày, thời gian ngắn, nhìn không ra cái gì, có thể một trăm năm sau, năm trăm năm về sau, thậm chí một ngàn năm sau đâu?"),i("br"),i("br"),n._v("Hơn bốn mươi năm trước Thiên Thần, tuổi thọ đi đến cuối cùng về sau, mới sinh ra Thiên Thần lại có bao nhiêu?"),i("br"),i("br"),n._v("Thiên Thần số lượng đều không đáng nhắc tới, chớ nói chi là Thần Vương, phong hào Thần Vương!"),i("br"),i("br"),n._v("Nếu thực như thế, Thần giới sinh mệnh, lại có thể còn lại bao nhiêu?"),i("br"),i("br"),n._v("Nghĩ cũng không dám nghĩ!"),i("br"),i("br"),n._v("Chẳng lẽ... Đây là Thiên đạo thiếu hụt tạo thành?"),i("br"),i("br"),n._v("“Không phải có linh khí thuỷ triều ư? Nghe nói mỗi lần thuỷ triều lúc đến, đều có thể đạt được rất nhiều tài nguyên...” Trương Huyền nhịn không được nói."),i("br"),i("br"),n._v("Linh khí thuỷ triều, thực ra chính là linh khí thủy triều, mỗi mười năm phát sinh một lần, mỗi lần tới lâm, đều sẽ mang đến vô số tài nguyên, để hoang dã bên trong vốn bất cứ lúc nào cũng sẽ tử vong thực vật, lần nữa tiếp tục kéo dài."),i("br"),i("br"),n._v("Nguyên nhân chính là như vậy, Linh Cơ sơn cứ việc rất nhiều năm chưa thấy qua linh khí, vẫn như cũ có không ít thảm thực vật, ngoan cường sinh hoạt, kéo dài hơi tàn."),i("br"),i("br"),n._v("“Nếu như đem Thần giới, so sánh cái này kiến trúc, Thiên đạo chính là chống đỡ nóc phòng trụ cột, hiện tại hai cái trụ cột, tại ngươi ta nơi này, nóc phòng sớm muộn đều sẽ không chịu nổi, ầm ầm bên trong sập.”"),i("br"),n._v("Khổng sư nói: “Linh khí thuỷ triều, như tạm thời dựng cây gậy, cứ việc có thể ngăn cản đổ sụp tốc độ, trên thực tế yếu ớt kinh khủng, không thể duy trì bao lâu. Hơn nữa... Mỗi lần thuỷ triều, đều sẽ dẫn tới Thần giới rung chuyển, vô số sinh mệnh vì đạt được tài nguyên, không từ thủ đoạn... Nhìn như duy trì Thần giới ổn định, trên thực tế lại gia tốc tử vong. Ngươi có biết... Mỗi lần linh khí thuỷ triều, Thần giới đều sẽ chết đi bao nhiêu Thần Linh, bao nhiêu Thiên Thần, lại có bao nhiêu Thần Vương?”"),i("br"),i("br"),n._v("Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Loại sự tình này, là Thần giới cơ mật, trước đó nhìn qua thư tịch, chưa bao giờ có ghi chép."),i("br"),i("br"),n._v("“Thần Linh, Thiên Thần số lượng, đếm không hết, cũng không có cách nào đếm rõ, ta sẽ nói cho ngươi biết, linh khí thuỷ triều phía trước, Thần Vương cùng phong hào Thần Vương số lượng, ngươi liền hiểu!”"),i("br"),i("br"),n._v("Khổng sư nói: “Lúc ấy, Cửu Thiên tổng cộng có phong hào Thần Vương hơn ba trăm vị, Thần Vương hơn một ngàn vị!”"),i("br"),i("br"),n._v("“Ba trăm, một ngàn?” Trương Huyền thân thể chấn động."),i("br"),i("br"),n._v("Hiện tại Thần giới, phong hào Thần Vương chỉ có gần ba mươi vị, Thần Vương hơn một trăm cái..."),i("br"),i("br"),n._v("Nói như vậy lên, chẳng phải biểu thị... Toàn bộ Thần giới, tổn thất chín phần mười đỉnh cao cường giả?"),i("br"),i("br"),n._v("Cường giả đỉnh phong đều mất đi nhiều như vậy, Thiên Thần lại đã chết đi bao nhiêu?"),i("br"),i("br"),n._v("“Thần giới Thiên đạo không thể tiếp tục thiếu hụt đi xuống, nếu như vẫn như cũ phân tán tại trên người chúng ta, loại này tình huống bi thảm, sẽ tiếp tục đi xuống, Thiên Địa hội tiếp tục rung chuyển, Thần Linh cũng lại không ngày lành có thể qua...”"),i("br"),i("br"),n._v("Khổng sư ánh mắt lộ ra trách trời thương dân chi ý."),i("br"),i("br"),n._v("Loại tình cảnh này, tuyệt không phải hắn nguyện ý nhìn thấy."),i("br"),i("br"),n._v("“Nhưng... Vì cứu thế người, liền muốn hi sinh trong các ngươi một cái? Các ngươi chẳng lẽ... Không thể cùng duy trì Thiên đạo vận chuyển?” Trương Huyền nghiến răng."),i("br"),i("br"),n._v("Thật chẳng lẽ cũng không có cái gì phương pháp trung hòa?"),i("br"),i("br"),n._v("Nếu Thiên đạo có thể tách ra, vậy có thể hay không tách ra quản lý?"),i("br"),i("br"),n._v("“Tự nhiên không thể! Thiên đạo tròn trịa như ý, tựa như Thái Cực, giao hòa liên hợp mới có thể để cho thiên địa vững chắc, không đến mức xuất hiện thay đổi, tách ra lại như thế nào chưởng khống?”"),i("br"),i("br"),n._v("Khổng sư nhìn qua: “Tựa như thùng gỗ... Tất cả tấm ván gỗ không liên hợp cùng một chỗ, mà là đơn độc chứa nước, lại có thể chứa bao nhiêu?”"),i("br"),i("br"),n._v("Trương Huyền nhớ tới một việc, nghi ngờ nhìn qua: “Nếu là đạo lý này, vì sao Lạc Nhược Hi ban đầu ở danh sư đại lục thời điểm, biết ta nắm trong tay một phần Thiên đạo, lại không giết ta? Còn có Khổng sư ngươi... Nếu như bây giờ giết ta, đem ta bộ phận này Thiên đạo lấy đi, có thể hay không phần thắng lớn hơn một chút?”"),i("br"),i("br"),n._v("Tuy là nói như vậy tổn thương cảm tình, nhưng mà là sự thật."),i("br"),i("br"),n._v("Nếu quả thật cùng Khổng sư nói như vậy, song phương tranh đoạt, vì Thiên đạo, vì giải cứu Thần giới muôn dân, cái kia vì sao lúc trước không giết?"),i("br"),i("br"),n._v("Vừa lúc gặp mặt, bản thân tại Lạc Nhược Hi loại này Đế Quân trong mắt, chẳng qua là hạ giới sâu kiến mà thôi, lại không tình cảm gì, giết có thể đạt được càng cao lực lượng, cứu nhiều hơn nữa người, khẳng định sẽ đi làm đi!"),i("br"),i("br"),n._v("Không nói nàng, liền nói cục diện trước mắt, Khổng sư một khi đạt được trên người mình Thiên đạo thư viện, có thể hay không trở nên càng thêm cường đại, cuối cùng siêu Việt Lạc Nhược Hi, đạt được tỷ thí lần này thắng lợi?"),i("br"),i("br"),n._v("Chưởng khống nguyên vẹn Thiên đạo... Làm một cái chân chính thiên mệnh người, đối với bất kỳ người nào tới nói, đây đều là hấp dẫn cực lớn, khó mà từ chối."),i("br"),i("br"),n._v("Huống chi, đây không phải là vì bản thân sinh tử, mà là vì Thần giới vô số sinh mệnh."),i("br"),i("br"),n._v("“Giết ngươi?”"),i("br"),i("br"),n._v("Khổng sư nhìn lại, mắt sáng lên, than thở một tiếng: “Nếu như giết ngươi, có thể cứu muôn dân, lớn hơn nữa tội nghiệt, ta đều nguyện ý đến cõng, lớn hơn nữa đau khổ, ta cũng có thể kháng, đáng tiếc... Không được! Chí ít, bây giờ còn chưa được!”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2243"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2245"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);