(window.webpackJsonp=window.webpackJsonp||[]).push([[14],{369:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1-ten-lua-đao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1-ten-lua-đao"}},[n._v("#")]),n._v(" Chương 1: Tên lừa đảo")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Tên lừa đảo! Tên lừa gạt!”"),t("br"),t("br"),n._v("Một cái phẫn nộ khẽ kêu, tiếp theo vang lên bàn chân giẫm lên nền đá mặt đào tẩu âm thanh."),t("br"),t("br"),n._v("Trương Huyền bất đắc dĩ duỗi ra hai tay: “Ta thật không phải lừa đảo, là học viện lão sư... Chỉ là muốn để ngươi làm học sinh của ta! Hơn nữa, coi như nói ta là tên lừa đảo, làm gì còn muốn thêm cái ‘Lớn’ chữ? Khiến cho ta cùng tội ác tày trời như thế...”"),t("br"),t("br"),n._v("Nói thầm xong, nhớ tới chính giáo chủ nhiệm đã nói, Trương Huyền vò vò mi tâm: “Thứ mười bảy cái! Ngày hôm nay nếu như ngay cả một học sinh đều chiêu không tới, ngày mai ta là có thể cuốn gói về nhà!”"),t("br"),t("br"),n._v("Trương Huyền bản không phải người của thế giới này, mà là Địa cầu một cái bình thường trường đại học thư viện nhân viên quản lý, chỉ nhớ rõ lửa lớn rừng rực, sau đó... Sẽ không có sau đó, tỉnh lại lần nữa đến thế giới này."),t("br"),t("br"),n._v("Cùng trong tiểu thuyết ghi lại gần như, võ đạo làm đầu, thực lực là vua!"),t("br"),t("br"),n._v("Vốn tưởng rằng có thể xuyên qua cái vô dụng, chịu khổ từ hôn, sau đó làm mất mặt đột kích ngược, một đường hát vang... Xem ra cả nghĩ quá rồi, đến nơi này, mới phát hiện lại không phải học viên, mà là... Lão sư!"),t("br"),t("br"),n._v("Toàn bộ học viện nhất khổ rồi một vị lão sư!"),t("br"),t("br"),n._v("Lớp học của người khác, đều là người đông như mắc cửi, chen chúc không ngồi được đi, mà lớp học của hắn, một cái đều không có, thật vất vả bị hắn kéo vào được mấy cái, cuối cùng đều liền mắng vài âm thanh “Tên lừa đảo”, xoay người bỏ chạy!"),t("br"),t("br"),n._v("Nghiên cứu nguyên nhân, bị tự mình hồn xuyên gia hỏa, trong phòng học thực lực thấp nhất không nói, còn ánh mắt cực sai, cái gì cũng không thấy, then chốt... Còn dạy sai, từng ra đồng loạt tẩu hỏa nhập ma!"),t("br"),t("br"),n._v("Này cùng bệnh viện người chết như thế, danh khí lớn tổn hại, bị người phỉ nhổ, dù cho mới tới học viên, cũng từng cái từng cái kính sợ tránh xa, chỉ lo rơi xuống trong tay hắn, bị dạy cái nửa chết nửa sống!"),t("br"),t("br"),n._v("Không có học sinh, lại gặp phải lên án, năm ngoái thầy giáo kiểm tra, toàn bộ học viện thứ nhất đếm ngược, thậm chí được trong lịch sử duy nhất không điểm."),t("br"),t("br"),n._v("Đọc ngantruyen.com"),t("br"),t("br"),n._v("Phiền muộn phía dưới, mượn rượu giải sầu, kết quả được như nguyện treo, tự mình mượn cơ hội xuyên việt tới."),t("br"),t("br"),n._v("Học kỳ mới bắt đầu, học viện rơi xuống tối hậu thư, nếu như năm nay trong lớp của hắn lại không cách nào chiêu thu một học sinh, liền trực tiếp khai trừ giáo sư tư cách!"),t("br"),t("br"),n._v("Hôm nay đã mười bảy cái học viên mới đi ngang qua của hắn phòng học, kết quả, vừa nghe đến tên, tất cả đều đều không ngoại lệ đào tẩu, giống là tiểu cô nương gặp quái thúc thúc, nhanh bao nhiêu chạy bao nhanh."),t("br"),t("br"),n._v("“Xem ra phải nghĩ biện pháp lắc lư một cái mới được!”"),t("br"),t("br"),n._v("Trong lòng đang muốn nên làm sao mở miệng kéo nhân, chỉ thấy một cái một mặt ngốc manh nữ hài từ cửa lộ ra đầu."),t("br"),t("br"),n._v("“Xin hỏi, nơi này là Lục Tầm lão sư lớp học sao?”"),t("br"),t("br"),n._v("Âm thanh vui tươi, đáng yêu, dáng dấp thanh tú, động lòng người."),t("br"),t("br"),n._v("Lục Tầm lão sư, học viện minh tinh giáo sư, lớp học mỗi lần đều là chật ních, vô số người mộ danh mà tới."),t("br"),t("br"),n._v("“Liền nàng!” Thấy có người tự chui đầu vào lưới, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đem kiếp trước xem qua các loại tinh tướng động tác võ thuật hồi ức một lần, yên tĩnh ngồi tại vị trí trước, xếp đặt cái thế ngoại cao nhân tư thế: “Ngươi muốn bái ông ta làm thầy?”"),t("br"),t("br"),n._v("Nữ hài chim sẻ giống như gật đầu liên tục, đen thui trong đôi mắt, tràn đầy sùng bái: “Ta nghe nói Lục Tầm lão sư là Hồng Thiên học viện lợi hại nhất giáo sư, giáo sư học sinh, mỗi người thực lực phi phàm, tất cả mọi người lấy có thể gia nhập lớp học của hắn làm vinh!”"),t("br"),t("br"),n._v("“Nghe đồn chưa chắc là thật sự, lão sư cùng giày như thế, ở chỗ thích hợp không thích hợp! Hắn giảng cho dù tốt, cùng việc tu luyện của ngươi lý luận không hợp, không những sẽ không tiến bước, làm không cẩn thận còn lùi về sau! Lão sư tiếng tăm không hiện ra, cùng ngươi lý luận kết hợp lại, cũng có thể nhanh chóng tiến bộ, tu vi tăng nhiều!”"),t("br"),t("br"),n._v("“Là như thế này a... Ta cũng nghe ca ca đã nói lời tương tự!” Nữ hài sửng sốt một chút, mỹ lệ hai con mắt có chút mê man: “Bất quá, ta không biết mình thích hợp dạng gì lớp học!”"),t("br"),t("br"),n._v("Thấy nàng mắc câu, Trương Huyền ánh mắt sáng lên, do dự một chút, Marx, ân Coase chờ tiên hiền ở đầu óc liên tiếp hiện lên, giả ra vẻ khó khăn: “Tương phùng chính là duyên, như vậy đi, ta cũng là cái này học viện giáo sư, có thể miễn phí giúp ngươi nhìn gân cốt, thiên phú, bản tính, thuận tiện giúp ngươi đề cử một cái thích hợp lão sư!”"),t("br"),t("br"),n._v("“Vậy thì làm phiền tiên sinh!” Không nghĩ tới tùy tiện gặp phải vị giáo sư này, đại nghĩa như vậy,"),t("br"),t("br"),n._v("Nữ hài hưng phấn liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Ngươi trước tiên phơi bày một ít tu vi để cho ta nhìn!”"),t("br"),t("br"),n._v("Trương Huyền con mắt nửa mở nửa khép, tựa hồ không để ý chút nào."),t("br"),t("br"),n._v("“Rõ!”"),t("br"),t("br"),n._v("Hô hô hô hô hô!"),t("br"),t("br"),n._v("Thời gian ngắn ngủi, trong phòng quyền phong gào thét, từng đạo từng đạo kình khí như du long ở trên người cô gái tán loạn, cả người khí tức, ngưng tụ không tan, uy mà không hiện ra, đại biểu nàng có cực tốt cơ sở."),t("br"),t("br"),n._v("“Tốt, ta nhìn ra rồi, ngươi bình thường tu luyện chăm chú khắc khổ, cơ sở vững chắc, thiên phú tuyệt hảo, là không thể thấy nhiều thiên tài!” Một bộ quyền đánh xong, Trương Huyền thoả mãn gật đầu."),t("br"),t("br"),n._v("Hắn đây là cùng trên Địa cầu thầy tướng số học, lời đều là dầu cao Vạn Kim, phạm vi rất rộng, khiến người ta nghe không hiểu sai lầm, trái lại thầm hô chuẩn xác."),t("br"),t("br"),n._v("“Đặc biệt là ngươi trên hai chân sức mạnh, Bàn Long giống như vậy, nhất cử nhất động, như nước sông cuồn cuộn, vô cùng vô tận, sau đó cố gắng tu luyện, khẳng định rất có tiền đồ...”"),t("br"),t("br"),n._v("“Lão sư, ta trên đùi nhận qua thương, y sư nói, cơ bản ngang ngửa phế bỏ...” Nữ hài đánh gãy của hắn thao thao bất tuyệt, trong mắt hơi nghi hoặc một chút."),t("br"),t("br"),n._v("“Bị thương...” Trương Huyền mặt già đỏ ửng, bất quá, da mặt dày, người khác cũng nhìn không ra đến, tiếp tục hồ khản loạn phiến: “Ngươi cho rằng ta không có nhìn ra sao? Vừa nãy ngươi triển khai sức mạnh thời điểm, ta liền biết! Sở dĩ nói như vậy, là bởi vì... Không phá thì không xây được! Ngươi hai chân tuy rằng bị thương, nhưng bất ngờ được những người khác đều không tưởng tượng được cơ duyên, sau đó cố gắng nắm chắc, chân của ngươi công, tất nhiên trở thành mạnh nhất sức chiến đấu! Khiến người khác hít khói!”"),t("br"),t("br"),n._v("Ngược lại đều là lắc lư, có thể nói mò, liền nói mò, trước tiên lắc lư lại đây một cái đệ tử lại nói."),t("br"),t("br"),n._v("“Cơ duyên? Lão sư, là dạng gì cơ duyên?” Nữ hài ánh mắt sáng lên."),t("br"),t("br"),n._v("Nàng chân tổn thương về sau, vẫn cảm thấy không bằng người khác, ngầm sinh tự ti, không nghĩ tới còn có thể nhân họa đắc phúc."),t("br"),t("br"),n._v("“Cơ duyên này, có thể để cho ngươi nhất phi trùng thiên, trở thành tân sinh đệ nhất cũng không quá đáng, dù sao ngươi thiên phú vốn là rất tốt, là vạn cổ không một thiên tài tuyệt thế, bất quá...” Trương Huyền biển khẩu liên tục, còn kém không lấy ra một quyển “Như Lai Thần Chưởng”."),t("br"),t("br"),n._v("Nghe được có thể trở thành tân sinh số một, lại khen nàng là thiên tài, trên mặt cô gái không kìm lòng được lộ ra hừng hực, vội vã hỏi dò: “Bất quá, cái gì?”"),t("br"),t("br"),n._v("“Bất quá...” Trương Huyền thở dài một tiếng, một bộ lương tài sắp gặp phải mai một cảm thán: “Có thể nhìn ra cơ duyên như thế này lão sư toàn bộ học viện cũng không nhiều! Thêm vào ta, cũng chỉ có ba cái, cái khác hai vị, ba năm trước cũng đã không thu học sinh, vì lẽ đó... Ta cũng không tốt giúp ngươi mở miệng...”"),t("br"),n._v(" “Không thu học sinh?” Nữ hài nguyên bản ôm rất lớn hi vọng, nghe nói như thế ánh mắt thoáng ảm đạm rồi một hồi, lập tức nghĩ thông suốt cái gì đột nhiên nhảy lên: “Bọn họ không thu học sinh, người lão sư kia ngươi... Có thu hay không?”"),t("br"),t("br"),n._v("“Ta đương nhiên còn thu, chỉ là, ngươi cũng nhìn ra rồi, ta không màng danh lợi, cũng không nhiều thời gian như vậy!” Trương Huyền trên mặt lộ ra vẻ khó khăn: “Không phải lương chất ngọc thô chưa mài dũa, cùng ta có duyên, sẽ không dễ dàng đáp ứng...”"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Lời còn chưa nói hết, nữ hài quỳ mọp xuống đất: “Ta biết tiên sinh cao thượng, kính xin thu ta làm đồ đệ! Ta nhất định sẽ cố gắng tu luyện, không ném thể diện của lão sư!”"),t("br"),t("br"),n._v("Trương Huyền trong lòng mừng như điên, trên mặt vẫn lộ vẻ khó khăn: “Ngươi ta thật có duyên, chỉ là... Ngươi cũng nhìn ra rồi, ta thích thanh tĩnh...”"),t("br"),t("br"),n._v("“Học sinh bảo đảm không phải vạn bất đắc dĩ, tuyệt đối không quấy rầy lão sư!” Thấy hắn có chút không tình nguyện, nữ hài liền vội vàng gật đầu, một mặt thành khẩn."),t("br"),t("br"),n._v("“Học trò ta không nhiều, khả năng tài nguyên trên không sánh được các lão sư khác, hơn nữa còn sẽ phải chịu bị nhân chê trách...” Trương Huyền nói tiếp."),t("br"),t("br"),n._v("“Như vậy a... Ta nghe nói luyện tài nguyên rất trọng yếu...” Nữ hài do dự một chút."),t("br"),t("br"),n._v("Lão sư ở học viện lĩnh tài nguyên, là cùng giáo sư học sinh thành tích, số lượng rất nhiều điều kiện móc nối, nếu như tài nguyên không đủ, tu luyện đem rất khó tiến bộ."),t("br"),t("br"),n._v("“Khụ khụ, ta là cố ý thử thách ngươi mới nói như vậy, đã ngươi đáy lòng kiên định, thành tâm nhận ta làm lão sư, ta cũng cố hết sức, đưa ngươi nhận lấy!” Thấy nàng chần chờ, Trương Huyền lập tức đánh gãy, vội vàng nói: “Thân phận nghiệm chứng đi!”"),t("br"),t("br"),n._v("“Chuyện này... Nhanh như vậy?”"),t("br"),t("br"),n._v("Không nghĩ tới vị lão sư này trở mặt dường như lật sách, nhanh thái quá, nữ hài hơi kinh ngạc, tiếp nhận Trương Huyền đưa tới đại biểu thân phận lệnh bài, đang suy nghĩ đến cùng muốn hay không nghiệm chứng, chỉ thấy trước bình tĩnh cực kỳ Trương lão sư, đã kéo qua bàn tay của nàng, lấy ra một thanh đao nhọn nhẹ nhàng vạch một cái, một giọt máu liền rơi trên ngọc bài."),t("br"),t("br"),n._v("Vù!"),t("br"),t("br"),n._v("Hào quang lấp loé."),t("br"),t("br"),n._v("“A...”"),t("br"),t("br"),n._v("Nữ hài đờ ra."),t("br"),t("br"),n._v("Vừa nãy người lão sư này không còn nói muốn suy tính một chút, không màng danh lợi sao? Làm sao động tác nhanh như vậy, hơn nữa... Dao đều chuẩn bị xong?"),t("br"),t("br"),n._v("“Bắt đầu từ bây giờ, ngươi chính là học sinh của ta!” Nhỏ máu nhận chủ, Trương Huyền lúc này mới thở phào nhẹ nhõm, lần thứ hai khôi phục cao nhân dáng dấp: “Ngươi tên là gì?”"),t("br"),t("br"),n._v("“Hồi bẩm lão sư, ta gọi Vương Dĩnh!”"),t("br"),t("br"),n._v("Biết không cách nào thay đổi, nữ hài không nói thêm lời, một mặt manh manh gật đầu."),t("br"),t("br"),n._v("“Hừm, cầm thân phận bài, đi dẫn ngươi đệm chăn, thư tịch, thuận tiện tìm chỗ ở của chính mình đi! Ngày mai chính thức đi học, tới nơi này tìm ta là được!”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("“Rõ!” Vương Dĩnh gật gù, xoay người rời đi."),t("br"),t("br"),n._v("“Hô! Thành công lắc lư cái trước!”"),t("br"),t("br"),n._v("Thấy nàng trở thành tự mình học viên, Trương Huyền lúc này mới thở phào nhẹ nhõm, khẽ mỉm cười."),t("br"),t("br"),n._v("Thật không dễ dàng, nếu không phải trên internet xem qua không ít tinh tướng kỹ xảo, e sợ ngày hôm nay vẫn đúng là khó có thể thành công."),t("br"),t("br"),n._v("Có một người học viên, liền có thể miễn gặp bị khai trừ vận rủi, Trương Huyền tâm tình thả lỏng phía dưới, nhất thời cảm giác toàn bộ tinh thần của người ta trong nháy mắt thông thuận không ít, xoay quanh trong đầu tiền thân này điểm chấp niệm, cũng đang chầm chậm biến mất."),t("br"),t("br"),n._v("“Yên tâm đi, nếu xuyên qua thành ngươi, sẽ thay ngươi cẩn thận sống!”"),t("br"),t("br"),n._v("Trước cái kia Trương Huyền bởi vì chiêu thu không tới học sinh mà chết, có nhiều không cam lòng, giờ khắc này có học viên, sau cùng kiên trì cũng biến mất Vô Ảnh Vô Tung, cho tới giờ khắc này, Trương Huyền xem như là hoàn chỉnh nắm giữ thân thể này."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Triệt để khống chế thân thể, Trương Huyền đang muốn đón lấy lại lắc lư mấy cái học sinh, liền cảm thấy đầu óc chấn động, một đường hoàng chung đại lữ âm thanh ầm ầm vang lên."),t("br"),t("br"),n._v("“Thiên địa bất nhân, dĩ vạn vật vi sô cẩu...”"),t("br"),t("br"),n._v("“Nhật nguyệt doanh trắc, thiên địa có thiếu...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Các loại huyền diệu ngôn ngữ, chấn động hắn có chút choáng váng đầu hoa mắt, tiếp theo đầu óc xuất hiện một toà cung điện to lớn, mặt trên năm cái chữ lớn sáng lên lấp loá."),t("br"),t("br"),n._v("Thiên Đạo Thư Viện!"),t("br"),t("br"),n._v("Đẩy cửa đi vào, vô số giá sách san sát, các loại thư tịch vô cùng vô tận, một chút không nhìn thấy phần cuối."),t("br"),t("br"),n._v("“Đây chẳng lẽ là người xuyên việt gói quà lớn? Thư viện? Giời ạ, ta đời trước là thư viện nhân viên quản lý, sẽ không tới thế giới này, vẫn là như thế đi!”"),t("br"),t("br"),n._v("Người khác gói quà lớn, không phải lão gia gia, chính là hệ thống, các loại trâu bò đến nổ thứ tốt, tự mình nhưng là cái thư viện, Trương Huyền chỉ cảm thấy mắt tối sầm lại, kém chút không ngất đi."),t("br"),t("br"),n._v("Thư viện? Làm chim? Lẽ nào sau đó đánh nhau, người khác vứt kiếm, ta vứt sách?"),t("br"),t("br"),n._v("“Nhìn đều là sách gì?”"),t("br"),t("br"),n._v("Trong lòng bất đắc dĩ, Trương Huyền đưa tay hướng về trên giá sách thư tịch tóm tới, muốn nhìn một chút cái này thư viện đến cùng có cái gì tác dụng, bất quá một trảo mới phát hiện, bàn tay trực tiếp từ giá sách xuyên qua, bắt hụt."),t("br"),t("br"),n._v("“Ngươi đang đùa ta? Cho cái thư viện, nhưng sách gì đều không lấy được, nhìn không được, đến cùng muốn làm gì?”"),t("br"),t("br"),n._v("Trương Huyền đầy mặt không nói gì, khóc không ra nước mắt."),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-0"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);