(window.webpackJsonp=window.webpackJsonp||[]).push([[140],{493:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1102-cac-nguoi-huyen-huyen-hoi-con-thieu-nguoi-khong-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1102-cac-nguoi-huyen-huyen-hoi-con-thieu-nguoi-khong-ha"}},[n._v("#")]),n._v(" Chương 1102: Các ngươi Huyền Huyền hội còn thiếu người không? (Hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Hai tay trói lại? Công bằng...”"),t("br"),t("br"),n._v("Một cái lảo đảo, Tiêu Tần suýt chút nữa không đã hôn mê."),t("br"),t("br"),n._v("Hai tay trói lại chiến đấu, kêu cái gì công bằng?"),t("br"),t("br"),n._v("Cũng quá xem thường người đi!"),t("br"),t("br"),n._v("Ta Thánh vực nhất trọng cường giả đỉnh phong, đè thấp tu vi cùng ngươi chiến đấu, ngươi nhưng muốn trói chặt hai tay..."),t("br"),t("br"),n._v("Ngực một hồi kịch liệt đau đớn, Tiêu Tần cảm thấy bất cứ lúc nào cũng sẽ lần nữa phun ra máu tươi."),t("br"),t("br"),n._v("“Nếu như ngươi cảm thấy có thể chịu được công kích của ta, tùy tiện...”"),t("br"),t("br"),n._v("Hàm răng cắn sắp vỡ vụn, Tiêu Tần biết tức giận cũng không dùng, hít sâu một hơi bình phục tâm cảnh, bàn tay lăng không một trảo, vừa vặn nhào tới."),t("br"),t("br"),n._v("Vừa rồi vị kia nữ hài, là Thánh vực nhất trọng cường giả, hơn nữa thiên phú cực cao, đánh không lại ngược lại cũng thôi, không tin tùy tiện cầm ra một cái, cũng chiến thắng bất quá."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Năm ngón tay như câu, lòng bàn tay mang theo tiếng gió gào thét."),t("br"),t("br"),n._v("Võ kỹ, Bôn Lôi Xuyên Tâm Trảo!"),t("br"),t("br"),n._v("Chiêu này võ kỹ, mặc dù không đạt tới Thánh cấp, nhưng ở Linh cấp bên trong lại là vô cùng nổi danh, tu luyện tới cảnh giới đại thành, tựa như bôn lôi, khí thế bên trên trước đem người chấn nhiếp, không dám cùng chi chống lại."),t("br"),t("br"),n._v("Đương nhiên, chỉ có khí thế, không có quá lớn danh khí, chủ yếu nhất là tàn nhẫn!"),t("br"),t("br"),n._v("Một khi bị công kích người, bị ngón tay chạm đến, liền sẽ lập tức tiếp nhận như lưỡi đao xé rách, thậm chí liền tâm tạng đều có thể cầm ra!"),t("br"),t("br"),n._v("Bởi vì quá mức hung ác, danh sư bình thường sẽ không đi học, nhưng Chiến sư học liền là kỹ thuật giết người, càng là hung ác chiêu số, trong chiến đấu càng có thể tạo được tác dụng không tưởng tượng nổi. Bởi vậy, chiêu này đã sớm tu luyện đến mức lô hỏa thuần thanh, giờ phút này thi triển đi ra, tiếng như bôn lôi, nhanh như mũi tên."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Bôn Lôi Xuyên Tâm Trảo ngay lúc sắp rơi vào trắng nõn thanh niên trên thân, chỉ thấy dưới chân hắn nhoáng một cái, đã trốn đến một bên, động tác của đối phương không vui, thậm chí so với hắn còn chậm mấy phần, nhưng chẳng biết tại sao, hình như trước thời hạn biết hắn muốn công kích vị trí đồng dạng, hời hợt tránh thoát đi, đồng thời bàn chân nâng lên, đối với mình bắp chân đạp tới."),t("br"),t("br"),n._v("Tiêu Tần con ngươi co rụt lại."),t("br"),t("br"),n._v("Trước đó vẫn cho là, tùy tiện tuyển ra người tới, tu vi nhiều nhất, nhìn thấy thế mới biết, so trong tưởng tượng đáng sợ nhiều."),t("br"),t("br"),n._v("Bôn Lôi Xuyên Tâm Trảo, trên tay công kích mạnh mẽ, nhưng hạ bàn có chút bất ổn, đối phương một chân đạp tới địa phương, công bằng, đúng là mình không thể không phòng, mà khó mà trốn tránh chỗ."),t("br"),t("br"),n._v("“Thiên Ti Xuyên Diệp Thủ!”"),t("br"),t("br"),n._v("Biết bị đá bên trong, tất nhiên bị thương, Tiêu Tần biến trảo vì tay, phủ tới."),t("br"),t("br"),n._v("Lần này, tựa như đánh đàn, thoạt nhìn không có mảy may uy lực, trên thực tế nhưng khắp nơi hung hiểm."),t("br"),t("br"),n._v("Thiên Ti Xuyên Diệp Thủ, là một vị ma âm sư sáng lập ra võ kỹ, thoạt nhìn là tại đánh đàn, không có lực sát thương, trên thực tế năm ngón tay, mỗi một cây đều mang kiếm khí, một khi đụng vào, liền sẽ bắn vào đối thủ trong cơ thể, để cho người ta khó lòng phòng bị, mười phần âm độc."),t("br"),t("br"),n._v("Bôn Lôi Xuyên Tâm Trảo, thời gian nháy mắt biến thành Thiên Ti Xuyên Diệp Thủ, không chần chờ chút nào cùng dừng lại, thậm chí trúng liền ở giữa nối tiếp đều không có, thoạt nhìn như là một cái chiêu số liên tiếp sử dụng ra, chỉ bằng vào điểm ấy, cũng có thể thấy được Tiêu Tần không hổ võ kỹ điện thiên tài danh dự."),t("br"),t("br"),n._v("Đối võ kỹ vận dụng, quả thực đạt đến mức lô hỏa thuần thanh."),t("br"),t("br"),n._v("Bất quá, chiêu số của hắn tinh diệu, đối phương càng cao minh hơn, trước thời hạn biết sẽ biến chiêu đồng dạng, thân thể đột nhiên nhoáng một cái, lăng không khơi mào, mũi chân vừa vặn đón nhận ngón tay của hắn."),t("br"),t("br"),n._v("Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Mũi chân chỗ một cỗ cuồng bạo hung mãnh lực lượng cùng Tiêu Tần ngón tay đối đầu, chỉ một chút, người sau liền cảm thấy xương ngón tay đứt gãy thanh âm, ngay sau đó, mắt tối sầm lại, lại một cái bàn chân rơi xuống."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn chưa kịp trốn tránh, một nguồn sức mạnh vọt tới, Tiêu Tần bay ngược mà ra, ngã xuống đất, máu tươi phun mạnh."),t("br"),t("br"),n._v("Giãy dụa lấy đứng lên, tràn đầy khó tin."),t("br"),t("br"),n._v("Đối phương từ đầu đến cuối, đều không có vận dụng hai tay, chỉ dùng hai chân, liền đem hắn nhẹ nhõm đánh bại!"),t("br"),t("br"),n._v("Chiến thắng không qua nữ hài, có thể nói đối phương có thể là Huyền Huyền hội nổi danh nhất thiên tài, nhưng trước mắt cái này thế nhưng là hắn tùy tiện chọn... Trắng nõn nà, thoạt nhìn người hiền lành... Kết quả giống nhau không đi qua hai chiêu!"),t("br"),t("br"),n._v("Chẳng lẽ mình tu luyện võ kỹ, thật như thế có vấn đề?"),t("br"),t("br"),n._v("“Cũng không cần chán nản, tu vi của ngươi đã coi như là không tệ, chỉ bất quá gặp ta! Đương nhiên, cũng may mắn gặp ta, ta tại Huyền Huyền hội, xem như bài danh thấp!”"),t("br"),t("br"),n._v("Thấy hắn có chút hoài nghi nhân sinh, trắng nõn thanh niên mở miệng an ủi: “Chiêu số của ngươi hoa lệ, biến chiêu nhanh chóng, đối võ kỹ nắm giữ cùng đối nắm chắc thời cơ, cũng còn không sai, sở dĩ không cách nào chiến thắng, là bởi vì quá tin tưởng võ kỹ, mà không để ý đến bản thân.”"),t("br"),t("br"),n._v("“Quá tin tưởng võ kỹ?” Không biết hắn ý tứ là cái gì, Tiêu Tần mặt mũi không hiểu."),t("br"),t("br"),n._v("“Rất đơn giản, võ kỹ được sáng tạo ra mục đích, là cùng người chiến đấu, có thể phát huy ra lực chiến đấu mạnh hơn... Nói trắng ra là, võ kỹ chỉ là một loại phụ trợ chiến đấu thủ đoạn, mà không tất thắng thủ đoạn!”"),t("br"),t("br"),n._v("Trắng nõn thanh niên cười cười: “Ngươi vừa rồi bất kể là đối Thất Thất học tỷ ra tay, vẫn là đối ta, một công đánh, chiêu số liền hung mãnh vô cùng, lực lượng mười phần, đổi lại người bình thường, quả thực sẽ bị đánh trúng, bởi vậy bị thương! Nhưng ở trong mắt chúng ta, bởi vì quá muốn chiến thắng, ngược lại khắp nơi đều là sơ hở!”"),t("br"),t("br"),n._v("“Thật giống như ngươi đối ta công kích cái kia trảo pháp, bị bắt lại tuy hung mãnh vô cùng, uy lực vô cùng, nhưng bắt không được đâu?”"),t("br"),t("br"),n._v("Nói đến đây, trắng nõn thanh niên dừng lại một chút, tiếp tục nói: “Bắt không được, lợi hại hơn nữa công kích, đều vô dụng! Vừa rồi ta cố ý chờ ở nghìn cân treo sợi tóc tránh thoát công kích, là bởi vì lúc này chiêu số của ngươi đã dùng hết, sơ hở lớn nhất! Lúc này lực lượng toàn bộ tập trung ở trên người, hạ bàn liền là lớn nhất sơ hở!”"),t("br"),t("br"),n._v("“Ta lập tức công kích bắp chân của ngươi, theo bàn tay đến bắp chân, là thân thể hai thái cực, chân khí cho dù chạy lại nhanh, cũng rất khó đầu đuôi chú ý, cái này sơ hở, mặc dù là ngươi vốn là có, nhưng ở ta dụ dỗ bên dưới, khó khăn nhất che lấp. Bất quá, phản ứng của ngươi cũng không tệ, lúc này, đổi chiêu số, đổi dùng một loại đánh đàn giống như công kích.”"),t("br"),t("br"),n._v("“Chiêu này công kích, nhìn như lướt nhẹ, trên thực tế là đem kiếm khí chứa đựng trong đó, chạm vào tức phát, để cho người ta khó lòng phòng bị. Đoán không sai, cũng là một loại võ kỹ đi! Chiêu số là tốt, nhưng chỉ có thể lấy chỉ lưng công kích, ta nhảy đến trên không, tránh né sắc nhọn nhất phong mang. Nhường ngươi chiêu số rơi vào khoảng không, sau đó bàn chân đạp mạnh... Kiếm là rất sắc bén, nhưng đối công sống kiếm, mà không phải lưỡi kiếm cùng mũi kiếm, cũng liền không có hiệu quả, trái lại, rất nhẹ nhàng liền đem ngươi xương ngón tay bẻ gãy!”"),t("br"),t("br"),n._v("Lần nữa lắc đầu, trắng nõn thanh niên mãn thị tiếc hận."),t("br"),n._v(" Kẻ trước mắt này tổng thể tới nói, coi như không tệ, chỉ tiếc dùng chiêu số quá già rồi."),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Thân thể run lên, Tiêu Tần như là ngũ lôi oanh đỉnh."),t("br"),t("br"),n._v("Đối phương giải thích có lý có chứng cứ, mỗi một chiêu đều là hắn võ kỹ bên trong thiếu hụt... Khó trách ngăn cản không nổi!"),t("br"),t("br"),n._v("“Các ngươi... Cũng đã nhìn ra?”"),t("br"),t("br"),n._v("Yết hầu phát khô, nhìn về phía chung quanh."),t("br"),t("br"),n._v("“Thực lực ngươi kém như vậy, đương nhiên đã nhìn ra!”"),t("br"),t("br"),n._v("“Rất dễ dàng có thể nhìn ra được, không tính là gì...”"),t("br"),t("br"),n._v("“Thật ra thì, hắn chỉ nói một nửa, hung mãnh công kích, đánh tới người xác thực tổn thương lớn, đánh không đến người... Khá hơn nữa võ kỹ thì có ích lợi gì?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thấy hắn hỏi thăm, chung quanh mồm năm miệng mười mở miệng nói."),t("br"),t("br"),n._v("“Đánh không đến người?”"),t("br"),t("br"),n._v("Đứng tại chỗ, Tiêu Tần như là ngu ngốc."),t("br"),t("br"),n._v("Quả thực."),t("br"),t("br"),n._v("Võ kỹ là vì đả thương người mới nghiên cứu ra được, có thể đả thương không đến người... Còn có cái gì ý nghĩa?"),t("br"),t("br"),n._v("Thi triển nhiều hơn nữa, cũng chỉ chủ nghĩa hình thức thôi!"),t("br"),t("br"),n._v("“Thụ giáo...”"),t("br"),t("br"),n._v("Hiểu điểm ấy, Tiêu Tần bừng tỉnh hiểu ra, có loại đẩy ra mây mù thấy mặt trời mọc cảm giác, nhịn không được khom người đến cùng."),t("br"),t("br"),n._v("Những năm này, hắn lấy luyện thành càng nhiều võ kỹ, càng mạnh võ kỹ làm vinh, tưởng là chỉ cần biết cường đại hơn võ kỹ, chẳng khác nào nắm giữ cường đại hơn sức chiến đấu..."),t("br"),t("br"),n._v("Làm như vậy, đối phó người bình thường quả thực có thể, nhưng đối đầu với cao thủ chân chính, chẳng khác nào tất cả đều là sơ hở!"),t("br"),t("br"),n._v("Tham thì thâm!"),t("br"),t("br"),n._v("Biến chiêu lại trôi chảy, cũng là mặt khác chiêu số, cũng có đủ loại thiếu hụt cùng sơ hở, rất dễ dàng bị người ta tóm lấy."),t("br"),t("br"),n._v("“Không cần khách khí, thật ra thì, chúng ta cũng là cùng viện trưởng học, hắn cho chúng ta lên lớp, chúng ta mới có thể nắm giữ hiện tại kiến thức cũng nhãn lực, nếu không, chỉ là chút bình thường danh sư thôi, khẳng định không phải là đối thủ của ngươi!”"),t("br"),t("br"),n._v("Trắng nõn thanh niên vội vàng xua tay."),t("br"),t("br"),n._v("“Viện trưởng?”"),t("br"),t("br"),n._v("“Không sai, viện trưởng kỳ tài ngút trời, tuổi tác so với chúng ta còn nhỏ, đối với chiến đấu lý giải, nhưng vượt ra khỏi tưởng tượng của mọi người, tựa như chiến đấu mới vừa rồi, nếu như ngươi chiêu thứ nhất công kích, có thể đi phía trái chếch lên ba phần, chân trái lại hướng trước bước ra một bước... Ta đừng nói công kích, chỉ sợ trốn cũng không tránh thoát!”"),t("br"),t("br"),n._v("Trắng nõn thanh niên nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Tiêu Tần đem chiêu số của mình dựa theo hắn nói phương thức, nhớ lại thoáng cái, mí mắt bỗng nhiên nhảy một cái."),t("br"),t("br"),n._v("Quả nhiên!"),t("br"),t("br"),n._v("Trước đó hắn muốn như vậy làm, đối phương chỉ sợ liền tránh né thời gian đều không có, chớ nói chi là nhìn ra bản thân hạ bàn bất ổn, tiến hành công kích."),t("br"),t("br"),n._v("“Chiêu thứ hai, ngươi năm ngón tay khẽ vuốt, dùng rất tốt, nhưng mà chiêu số quá già rồi, ngón tay cũng quá kiên cường, nếu như phối hợp thêm miên chưởng, liền có thể đem trên dưới trái phải toàn bộ bao phủ, cho dù ta nhảy đến trên không, cũng chỉ có thể cùng ngươi liều! Đến lúc đó, ai thắng ai thua, liền không nói được rồi...”"),t("br"),t("br"),n._v("Trắng nõn thanh niên tiếp tục phân tích."),t("br"),t("br"),n._v("Tiêu Tần lần nữa cứng đờ."),t("br"),t("br"),n._v("Vừa rồi đốt ngón tay của hắn sở dĩ bị đối phương đạp gãy, thứ nhất là bị phát hiện sơ hở, càng quan trọng hơn là, ẩn chứa trong đó kiếm khí, đánh không trúng đối phương, xuất hiện phản phệ."),t("br"),t("br"),n._v("Nếu có thể phối hợp thêm miên chưởng lời nói, liền có thể giải quyết cái này tai hoạ ngầm, đem bốn phía toàn bộ bao phủ, đối phương chiêu kia cũng liền một cách tự nhiên phá giải..."),t("br"),t("br"),n._v("“Chúng ta viện trưởng từng nói, tin hết chiêu, không bằng vô chiêu! Chiến đấu liền là như vậy, ngươi quá ỷ lại võ kỹ, ngược lại rơi xuống bên dưới thành...” Trắng nõn thanh niên than thở."),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Tiêu Tần tiêu hóa nửa ngày rốt cục chậm lại, một ngụm trọc khí phun ra, trong mắt tràn đầy bội phục: “Các ngươi viện trưởng có thể dạy dỗ những công pháp này, quả thật khoáng thế kỳ tài...”"),t("br"),t("br"),n._v("Những này phương pháp chiến đấu, so Chiến sư đường đều cao minh hơn, tất nhiên nghiên cứu vô số năm tháng, không nói trước vị viện trưởng này thực lực đến cùng làm sao, chỉ phần này truyền thừa, cũng làm người ta cảm thấy đáng sợ."),t("br"),t("br"),n._v("“Viện trưởng xác thực có thể nói khoáng thế kỳ tài, hơn nữa chủ yếu nhất là lòng dạ rộng lớn, tựa như biển, chỉ cần bằng lòng học tập, từ trước tới nay hữu giáo vô loại, dốc túi truyền thụ!”"),t("br"),t("br"),n._v("Trắng nõn thanh niên cười cười."),t("br"),t("br"),n._v("“Hữu giáo vô loại? Ai cũng có thể học?”"),t("br"),t("br"),n._v("“Đúng vậy a, nhưng phàm là ta Huyền Huyền hội thành viên, cũng có tư cách, nguyên nhân chính là như vậy, vô số người đều muốn gia nhập!”"),t("br"),t("br"),n._v("Nhược Hoan công tử xen vào nói."),t("br"),t("br"),n._v("“Cái này... Cái này...”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Tiêu Tần vẻ mặt lúc trắng lúc xanh, sau một lúc lâu, nhịn không được mở miệng: “Xin hỏi... Các ngươi Huyền Huyền hội còn thiếu người không?”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1110"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1112"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);