(window.webpackJsonp=window.webpackJsonp||[]).push([[1404],{1767:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-216-dung-suc-lon-hon"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-216-dung-suc-lon-hon"}},[n._v("#")]),n._v(" Chương 216: Dùng sức lớn hơn")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Tuy rằng trước Trương Huyền danh tiếng không tốt lắm, nhưng Lục Tầm có thể từng bước một quật khởi, đi tới hiện tại tình trạng này, không những dựa vào thiên phú cùng thực lực, càng quan trọng chính là tâm thái, bất luận gặp phải chuyện gì, đều bình tĩnh xử lý, không có một chút nào tự đại cùng kiêu ngạo."),t("br"),t("br"),n._v("Vì xác định lần này tỷ thí danh sách, chuyên môn đã điều tra Trương Huyền mấy học sinh, theo toàn trường tuyển ra tốt nhất đối kháng đội hình."),t("br"),t("br"),n._v("Đang điều tra bên trong, Trịnh Dương cùng Mạc Hiểu từ nhỏ cùng nhau lớn lên, đồng thời luyện tập thương, người sau thiên phú rõ ràng muốn cao hơn người trước, chiến đấu càng là trải qua vô số lần, đều là Mạc Hiểu thắng lợi, chưa bao giờ từng thất thủ."),t("br"),t("br"),n._v("Chính vì như thế, mới theo bạn tốt nơi đó đem người khoét đến, càng làm cho hắn truyền thụ Vương gia thương."),t("br"),t("br"),n._v("Vốn tưởng rằng nắm chắc, kết quả... Còn không có tỷ thí, liền đem thương ném, chủ động chịu thua..."),t("br"),t("br"),n._v("Lục Tầm chỉ cảm thấy toàn thân run rẩy, cả người đều sắp rung mạnh."),t("br"),t("br"),n._v("Vừa mới cái kia Khổng Kiệt, hai câu dao động bị lừa, bị một quyền đánh ngất, ta nhịn."),t("br"),t("br"),n._v("Quá mức 600 ngàn kim tệ (3 triệu chia làm năm người) đổ xuống sông xuống biển, có thể ngươi... Xảy ra chuyện gì?"),t("br"),t("br"),n._v("Gặp qua bẫy người, chưa từng thấy như thế bẫy người!"),t("br"),t("br"),n._v("Biết rõ ta làm túc không ngủ, giúp các ngươi tăng cao thực lực, chính là vì thắng lợi, hiện tại ngược lại tốt, thực lực tăng lên, tiền cũng bỏ ra, lại đánh liên tục đều không đánh liền trực tiếp chịu thua..."),t("br"),t("br"),n._v("“Làm sao làm?”"),t("br"),t("br"),n._v("“Hai cái Chân Khí cảnh học sinh, cái thứ nhất bị một quyền đánh bay, thứ hai trực tiếp chịu thua? Làm cái gì?”"),t("br"),t("br"),n._v("Không những Vương Siêu, Lục Tầm phát rồ, xung quanh xem so tài học sinh lão sư, cũng tất cả đều trên đầu bốc lên dấu chấm hỏi, từng cái từng cái không rõ vì sao."),t("br"),t("br"),n._v("Nhìn thấy Lục lão sư mang theo Chân Khí cảnh học sinh lại đây, mọi người vốn tưởng rằng là nghiêng về một phía cục diện, kết quả... Xác thực là nghiêng về một phía, đáng tiếc ngã về một mặt khác..."),t("br"),t("br"),n._v("Kỳ thực không những mọi người bộ này dáng vẻ, liền ngay cả người có liên quan Trương Huyền, cũng vẻ mặt vô cùng nghi hoặc."),t("br"),t("br"),n._v("Mạc Hiểu, Trịnh Dương huynh đệ lúc tỷ đấu, hắn đang tại Lục Trầm đại sư trong nhà, cũng không biết hai người kỳ thực từng giao thủ, cũng sẽ không rõ ràng vì sao còn không có chiến đấu, Mạc Hiểu liền trực tiếp chịu thua."),t("br"),t("br"),n._v("“Ta sở dĩ chịu thua, là bởi vì mấy ngày trước đây, chúng ta lén lút tỷ thí qua một lần, Trịnh Dương chỉ dùng một chiêu, liền đem ta đánh bại! Thậm chí muốn giết ta, đều dễ như trở bàn tay.”"),t("br"),t("br"),n._v("Triệu Nhã mấy người vẫn chưa trả lời, trên đài Mạc Hiểu âm thanh liền tiếng vang lên, ngữ khí bằng phẳng, không có một chút nào ủ rũ cùng hối hận, ngược lại mang theo một tia Tiêu Sái."),t("br"),t("br"),n._v("“Mấy ngày nay ta vẫn tại muốn như thế nào phá giải, vốn tưởng rằng đột phá đến Chân Khí cảnh dựa vào sức mạnh to lớn, có thể hóa giải. Vừa nãy thử một hồi mới phát giác, vẫn như cũ không chống đỡ được. Trịnh Dương cùng Trương lão sư học tập chiêu số, thẳng tới thương đạo chân ý, chỉ sợ ta nghiên cứu cả đời, cũng không có cách nào lý giải, chớ nói chi là ngăn cản.”"),t("br"),t("br"),n._v("“Biết rõ tiếp tục so xuống, như thế sẽ bị hắn một thương đánh bại, còn không bằng chủ động chịu thua!”"),t("br"),t("br"),n._v("Nói đến đây Mạc Hiểu đối với bạn tốt liền ôm quyền, nhẹ nhàng nở nụ cười: “Trịnh Dương, ngươi có cái tốt lão sư, nhất định phải cố gắng quý trọng!”"),t("br"),t("br"),n._v("Nói xong đem trường thương cầm lấy, xoay người quay trở về."),t("br"),t("br"),n._v("Tiếng nói của hắn vang vọng bốn phía, hầu như tất cả mọi người cũng nghe được."),t("br"),t("br"),n._v("“Mạc Hiểu nói Trương lão sư truyền ra chiêu số, nghiên cứu cả đời đều không cách nào phá giải?”"),t("br"),t("br"),n._v("“Nói như vậy đứng dậy, chẳng phải là so Vương gia thương còn cường đại hơn? Làm sao có khả năng?”"),t("br"),t("br"),n._v("“Song phương giao đấu, theo lý thuyết hẳn là sẽ không nói láo đi... Không phải vậy, Lục lão sư còn không đem hắn đánh chết?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người từng cái từng cái con mắt trợn tròn."),t("br"),t("br"),n._v("Vương gia thương, tại Thiên Huyền Vương quốc thực sự quá nổi danh, rất nhiều người nhìn thấy chiêu số liền có thể nhận ra, hầu như là thương pháp bên trong vô địch đại biểu."),t("br"),t("br"),n._v("Có thể hiện tại Mạc Hiểu nói, căn bản không phải là đối thủ... Làm cho tất cả mọi người đều nổ."),t("br"),t("br"),n._v("“Xem ra Trương Huyền lão sư, không hề giống trong truyền thuyết như vậy kinh khủng, trái lại rất lợi hại!”"),t("br"),t("br"),n._v("“Đúng đấy, tổng cộng năm cuộc tỷ thí, hiện tại đã hai thắng liên tiếp, chẳng lẽ Lục lão sư muốn thua?”"),t("br"),t("br"),n._v("“Cũng không ai biết, chỉ mong Lục lão sư phía sau có thể quật khởi đi...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Xung quanh nghị luận sôi nổi, trên đài Lục Tầm lồng ngực cùng một chỗ một phục, cả người sắc mặt càng ngày càng lục."),t("br"),t("br"),n._v("Ma đản, đều dạy một đám thứ đồ gì? Bẫy lão sư không phải như vậy bẫy đi."),t("br"),t("br"),n._v("Ngươi cuối cùng câu nói kia có ý gì?"),t("br"),t("br"),n._v("Trịnh Dương có cái tốt lão sư... Lẽ nào ngươi sẽ không có một cái tốt lão sư?"),t("br"),t("br"),n._v("Ta tiêu tốn ba triệu giúp các ngươi mua đan dược, không ngại tiêu hao chân khí, giúp các ngươi tăng cao thực lực, kết quả... Phải đến một câu nói như vậy?"),t("br"),t("br"),n._v("Tuy rằng phẫn nộ, nhưng cũng biết, hiện tại không phải lúc nổi giận, một khi nổi giận, nhiều năm tích lũy uy vọng, sẽ rơi ngàn trượng, triệt để đổ nát."),t("br"),t("br"),n._v("Nhìn quanh một vòng."),t("br"),t("br"),n._v("Hiện ở trước mắt còn không có tỷ thí còn lại ba cái."),t("br"),t("br"),n._v("Bạch Siêu, Đỗ Lỗi cùng Chu Hồng."),t("br"),t("br"),n._v("Bạch Siêu am hiểu quyền pháp, chuyên môn khắc chế tay phải kinh mạch có vấn đề Lưu Dương."),t("br"),t("br"),n._v("Đỗ Lỗi thoái công được,, tốc độ cực nhanh, lại thêm lên hắn truyền thụ một bộ rất lợi hại thân pháp, dùng tới đối phó trên đùi có thiếu hụt Vương Dĩnh, chắc là vấn đề không lớn."),t("br"),t("br"),n._v("Cho tới Chu Hồng, thì đối chiến Triệu Dĩnh."),t("br"),t("br"),n._v("Liên tục hai cục bất lợi, ván kế tiếp nhất định phải thắng lợi, bằng không, phía sau so càng nhiều, sẽ càng mất mặt."),t("br"),t("br"),n._v("“Đỗ Lỗi, ngươi đi!”"),t("br"),t("br"),n._v("Suy nghĩ nhiều lần, quyết định để Đỗ Lỗi đi qua."),t("br"),t("br"),n._v("Căn cứ điều tra, Vương Dĩnh tính cách tương đối mềm yếu, chỉ cần Đỗ Lỗi vừa lên đến liền tấn công nhanh, tất nhiên làm cho nàng lòng sinh sợ hãi ý."),t("br"),t("br"),n._v("Lại nói, nàng chân không được, Đỗ Lỗi coi như thắng không được, dựa vào thân pháp cũng có thể an toàn thoát thân, không đến mức cùng trước hai trận như thế, trực tiếp bại trận."),t("br"),t("br"),n._v("Đỗ Lỗi gật đầu, đi tới tỷ thí trận."),t("br"),t("br"),n._v("“Vương Dĩnh!”"),t("br"),t("br"),n._v("Nhìn thấy đối phương phái ra người, Trương Huyền cũng khoát tay áo một cái."),t("br"),t("br"),n._v("Vương Dĩnh đáp một tiếng, khuôn mặt đỏ lên, đi lên phía trước."),t("br"),t("br"),n._v("Chủ tịch vị."),t("br"),n._v(" “Lục lão sư không hổ là Lục lão sư, liên tục hai trận bất lợi, đã nhìn ra Trương Huyền chỗ sơ hở.”"),t("br"),t("br"),n._v("Thẩm Bích Như lông mày nhăn lại, tràn đầy lo lắng."),t("br"),t("br"),n._v("“Kẽ hở? Cái gì kẽ hở?” Hàn quỳnh tràn đầy nghi hoặc."),t("br"),t("br"),n._v("“Cái này Vương Dĩnh, là tứ đại gia tộc một trong Vương Hoằng tộc trưởng con gái một, tính cách mềm yếu, là người đơn thuần, rất dễ dàng tin tưởng người khác. Trương Huyền lão sư năm vị học sinh, nàng là dễ dàng nhất bị đánh hạ một vị.”"),t("br"),t("br"),n._v("Thẩm Bích Như nói."),t("br"),t("br"),n._v("“Thực sự là như vậy, xác thực là Trương Huyền kẽ hở.”"),t("br"),t("br"),n._v("Hàn quỳnh gật đầu."),t("br"),t("br"),n._v("Người tu luyện nghịch thiên mà đi, tính cách quá yếu, cùng người chiến đấu là tương đối chịu thiệt."),t("br"),t("br"),n._v("“Không những như vậy, vị này Vương Dĩnh lúc nhỏ từng nhận qua tổn thương, trên đùi nắm giữ tàn tật, bình thường đi bộ không ngại, nhưng một trận chiến đấu, sẽ xuất hiện thiếu hụt. Chuyện này, Vương Hoằng tộc trưởng từng xin khắp nơi Vương thành danh y, liền Nguyên Ngữ đại sư đều tự thân đi trị liệu qua, cuối cùng đều bó tay toàn tập, hậm hực mà trở về.”"),t("br"),t("br"),n._v("Thẩm Bích Như đối với Vương Dĩnh sự tình biết đến rất tỉ mỉ: “Nói cách khác... Vị này Vương Dĩnh chân, là không có cách nào chiến đấu.”"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Hàn quỳnh sững sờ, hiển nhiên không nghĩ tới sẽ là tình huống như thế."),t("br"),t("br"),n._v("“Cho tới vị này Đỗ Lỗi, đã từng bái tại trần Hồng lão sư môn hạ.”"),t("br"),t("br"),n._v("“Trần Hồng lão sư? Học viện am hiểu thân pháp dạy học trần Hồng lão sư?”"),t("br"),t("br"),n._v("“Ân, chính là hắn! Đỗ Lỗi là hết thảy học sinh bên trong, thân pháp tốt nhất, tốc độ cực nhanh, dùng người như thế tới đối phó chân có thương tích Vương Dĩnh, không phải kẽ hở là cái gì?” Thẩm Bích Như nói đến đây lắc lắc đầu: “Xem ra cuộc tỷ thí này, Vương Dĩnh... Thua chắc rồi!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Xin... Xin chỉ giáo nhiều hơn!”"),t("br"),t("br"),n._v("Trên đài, Vương Dĩnh sắc mặt ửng đỏ, với trước mắt Đỗ Lỗi ôm quyền."),t("br"),t("br"),n._v("“Chỉ giáo không dám làm, muốn động thủ liền bắt đầu đi!”"),t("br"),t("br"),n._v("Biết mình gánh vác nhất định phải thắng lợi trọng trách, Đỗ Lỗi nhướng mày, bàn chân đạp xuống, gấp sát mặt đất liền vọt tới."),t("br"),t("br"),n._v("Tốc độ cực nhanh, giống như mũi tên rời cung."),t("br"),t("br"),n._v("“Thật nhanh!”"),t("br"),t("br"),n._v("“Hắn thực sự là võ giả tam trọng? Coi như tứ trọng Bì Cốt cảnh cường giả, cũng không có tốc độ nhanh như vậy chứ?”"),t("br"),t("br"),n._v("“Chiêu này kêu , là Lục lão sư tuyệt kỹ thành danh, không nghĩ tới Lục lão sư thật là hào phóng, đem chiêu này cũng truyền thụ!”"),t("br"),t("br"),n._v("“Huyễn Ảnh Cửu Cung Bộ? Hồng Thiên học viện thân pháp cao nhất bí tịch? Coi như Thiên Huyền Vương quốc đều phải kể đến tuyệt kỹ?”"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("“Chiêu này ta tha thiết ước mơ, nếu như ta có thể học được nên tốt bao nhiêu, cái này Vương Dĩnh e sợ muốn rơi vào khổ chiến!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Học viên bên trong không ít nhận ra Đỗ Lỗi chiêu số, khiếp sợ không thôi."),t("br"),t("br"),n._v("Huyễn Ảnh Cửu Cung Bộ, là bộ pháp bên trong cực kỳ lợi hại tuyệt chiêu, Lục lão sư từng dựa vào bộ này thân pháp, chiến thắng qua không ít cường giả, ai cũng không nghĩ tới, truyền cho Đỗ Lỗi."),t("br"),t("br"),n._v("Tuy rằng hắn chỉ học đơn giản nhất một phần, nhưng cũng không phải cùng cấp bậc cường giả có thể chống lại."),t("br"),t("br"),n._v("Huống chi vị này Vương Dĩnh, chân vốn có thiếu hụt, tu vi lại chỉ có Đan Điền cảnh."),t("br"),t("br"),n._v("Cùng mọi người khiếp sợ không giống, nhìn thấy Đỗ Lỗi nhanh chóng tấn công tới, Vương Dĩnh ngọc cắn răng một cái, thân thể mềm mại xoay một cái, liền né qua."),t("br"),t("br"),n._v("“Khà khà, có thể trốn một chiêu, xem ngươi có thể tránh thoát mấy chiêu!”"),t("br"),t("br"),n._v("Thấy nàng có thể tránh thoát bản thân nhanh chóng công kích, Đỗ Lỗi hiển nhiên cũng có chút kỳ quái, bất quá, cũng không để ý, cười lạnh, chân khí trong cơ thể phun trào, cả người tốc độ càng lúc càng nhanh, trên đài phảng phất xuất hiện huyễn ảnh."),t("br"),t("br"),n._v("Huyễn Ảnh Cửu Cung Bộ, nhanh nhất có thể xuất hiện chín cái huyễn ảnh, để người khó phân biệt thật giả, không có cách nào tránh né, khó lòng phòng bị."),t("br"),t("br"),n._v("Đỗ Lỗi tuy rằng khoảng cách chín cái huyễn ảnh còn kém xa lắm, toàn lực vận chuyển, lại bóng người hư huyễn, để người có chút không nhận rõ."),t("br"),t("br"),n._v("“Quá mạnh mẽ!”"),t("br"),t("br"),n._v("“Tốc độ nhanh như vậy, mọi người không nhìn thấy, đánh như thế nào?”"),t("br"),t("br"),n._v("Hết thảy học viên, đều khóe miệng co giật, đặt mình vào hoàn cảnh người khác suy nghĩ một chút, trọn vẹn cũng không nhịn được lắc đầu."),t("br"),t("br"),n._v("Dựa vào mượn thực lực của bọn họ, xem đều không nhìn thấy, đánh như thế nào?"),t("br"),t("br"),n._v("“Đỗ Lỗi thắng chắc!”"),t("br"),t("br"),n._v("Vương Siêu lần thứ hai nở nụ cười."),t("br"),t("br"),n._v("Liên tục hai lần làm mất mặt, để hắn đều có chút bóng ma trong lòng, bất quá nhìn thấy Đỗ Lỗi, không giống Khổng Kiệt như vậy bị người dao động, vừa lên đến liền động thủ; Cũng không muốn Mạc Hiểu như vậy vô căn cứ, lúc này mới yên lòng lại."),t("br"),t("br"),n._v("Đường đường Chân Khí cảnh cường giả, ra tay toàn lực, đối phó một cái trên đùi có vấn đề Đan Điền cảnh bé gái, như vậy đều thắng không được, thật liền mất mặt."),t("br"),t("br"),n._v("“Đúng đấy, tốc độ nhanh như vậy, coi như là ta đè thấp cảnh giới cùng đối chiến, đều rất khó phá giải, huống chi cái tiểu nha đầu này.”"),t("br"),t("br"),n._v("Hồng Hạo trưởng lão vuốt râu, đột nhiên khẽ mỉm cười: “Ngươi xem, cái kia Vương Dĩnh muốn động thủ...”"),t("br"),t("br"),n._v("Vương Siêu nhìn sang, quả nhiên thấy Vương Dĩnh giơ lên đùi phải, trên mặt mang theo chần chờ, tựa hồ không biết làm sao công kích."),t("br"),t("br"),n._v("“Ha ha, đối phó loại này nhanh chóng thân pháp, chắc là dùng trên tay chiêu số, chung quy tay so chân càng thêm linh xảo! Chân rõ ràng có thương tích, còn muốn dùng chân, thực sự là đồ ngu, có thể đá trúng mới là lạ...”"),t("br"),t("br"),n._v("Cười lạnh, Vương Siêu đang muốn tiếp tục cười nhạo, liền thấy nữ hài quay về trước mắt hư huyễn thân ảnh đạp tới."),t("br"),t("br"),n._v("Oành!"),t("br"),t("br"),n._v("Trào phúng nụ cười còn không có triệt để tỏa ra, Vương Siêu liền nhìn thấy trước mắt hư huyễn bóng người đột nhiên ngừng lại, Đỗ Lỗi hơi đỏ mặt, bay ngược mười mấy mét, tầng tầng ngã xuống đất, từng ngụm từng ngụm phun ra máu tươi."),t("br"),t("br"),n._v("“Cái này Đỗ Lỗi... Thật không tiện, ta đã tận lực đã khống chế, vẫn là dùng sức lớn...”"),t("br"),t("br"),n._v("Vương Dĩnh luống cuống tay chân, đầy mặt áy náy, thẹn thùng sắp khóc."),t("br"),t("br"),n._v("(Mọi người có thể chú ý già nhai tinh vi, tin công chúng hào, tinh vi tin tìm tòi “Quét ngang thiên nhai”, ngày hôm qua phát một tấm Vương Dĩnh bức ảnh, là độc giả hỗ trợ cầm, xem ra rất đẹp, rất ngốc nảy sinh, mọi người có thể đi qua nhắn lại, chà đạp hai chân.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-224"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-226"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);