(window.webpackJsonp=window.webpackJsonp||[]).push([[1408],{1762:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2238-lao-su-nguon-goc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2238-lao-su-nguon-goc"}},[n._v("#")]),n._v(" Chương 2238: Lão sư nguồn gốc")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Chỉ có thể ở trước mặt hỏi thăm...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Bên ngoài, Thần giới mạnh nhất là Lạc Nhược Hi, nhưng chẳng biết tại sao, hắn trước sau cảm giác có một bàn tay lớn tại đẩy, để cho bọn họ rơi vào vòng xoáy, không cách nào tự kềm chế."),t("br"),t("br"),n._v("“Trảm tam thi công pháp, có thể hay không truyền ta?”"),t("br"),t("br"),n._v("Không đi nghĩ chuyện này, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Mặc kệ có thể hay không giúp mình thoát khỏi Thiên đạo trói buộc, chỉ cần biết loại phương pháp này, liền biểu thị nhiều ba cái tính mạng, thủ đoạn bảo mệnh gia tăng thật lớn."),t("br"),t("br"),n._v("Hơn nữa còn có thể chém xuống ác thi, thiện thi, bản thân thi, trở nên càng thêm thuần khiết, về sau tu luyện càng thêm thuận buồm xuôi gió."),t("br"),t("br"),n._v("“Bộ công pháp này, muốn tu luyện, giấu diếm được Thiên đạo, không thể lưu tại trong trí nhớ... Nếu không đồng dạng sẽ bị thăm dò! Chúng ta là rời đi danh sư đại lục không gian loạn lưu bên trong, được đến lão sư truyền thụ, luyện qua về sau, liền quên mất... Cho dù là lão sư, cũng sẽ không tại trong óc lưu giữ pháp quyết... Cho nên, công pháp, chúng ta nơi này cũng không có!”"),t("br"),t("br"),n._v("Tử Uyên Cổ Thánh xấu hổ cười một tiếng."),t("br"),t("br"),n._v("Không phải là không muốn truyền, mà là không có cách nào truyền."),t("br"),t("br"),n._v("Muốn giấu diếm được Thiên đạo, chỉ có thể theo trí nhớ bên trong loại bỏ, một khi làm như vậy, công pháp chẳng khác nào mất đi, muốn truyền người, đều không làm được!"),t("br"),t("br"),n._v("Hiểu được, Trương Huyền cười khổ một tiếng, có chút hiếu kỳ: “Bộ công pháp này, là Khổng sư bản thân sáng tạo ra? Chẳng lẽ... Tại danh sư đại lục, hắn liền biết, tất nhiên sẽ có người cùng hắn tranh thiên mệnh?”"),t("br"),t("br"),n._v("Đây là hắn kỳ quái nhất địa phương."),t("br"),t("br"),n._v("Danh sư đại lục rời đi thời điểm, hắn còn ngây thơ hoàn toàn không biết gì cả, đối thư viện vô cùng nương tựa."),t("br"),t("br"),n._v("Dựa theo bình thường đạo lý, Thiên đạo chi tranh, là Thần giới mới có thể biết được sự thật, vì sao Khổng sư tại danh sư đại lục, liền sớm chuẩn bị?"),t("br"),t("br"),n._v("Chẳng lẽ chưởng khống Thiên đạo hữu tự, nắm giữ biết trước năng lực?"),t("br"),t("br"),n._v("“Lão sư tuy là Thánh Nhân, thiên tư vô song, nhưng cũng không phải là sinh ra đã biết, cũng có người truyền thụ, dẫn đường...” Tử Uyên Cổ Thánh nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Khổng sư theo danh sư đại lục, liền biểu hiện quá chói mắt, để hắn từng một lần cho rằng, đột nhiên xuất hiện, không cần học tập... Bây giờ suy nghĩ một chút, quá phiến diện."),t("br"),t("br"),n._v("Chỉ cần là người, đều là chưa từng biết đến biết, đều có quá trình học tập."),t("br"),t("br"),n._v("Khổng sư thiên phú không thể thay thế, nhưng làm sao bước vào tu luyện? Lại cùng người nào học được tu luyện? Khẳng định có chỉ đường người."),t("br"),t("br"),n._v("Liền giống như bản thân, nắm giữ Thiên đạo thư viện loại này nghịch thiên máy gian lận, đồng dạng cần Khổng sư chỉ dẫn phương hướng đi tới đồng dạng."),t("br"),t("br"),n._v("“Bộ công pháp này, là sư tổ truyền thụ...” Tử Uyên Cổ Thánh gật đầu."),t("br"),t("br"),n._v("“Sư tổ?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("Hắn còn lần đầu tiên nghe nói, Khổng sư lại có lão sư."),t("br"),t("br"),n._v("“Trước đó danh sư đại lục, truyền thụ kiến thức, chỉ gọi là ‘Sư’!” Tử Uyên Cổ Thánh nói: “Là sư tổ xuất hiện, hậu nhân mới đưa, thân truyền sư phụ, gọi là ‘Lão sư’!”"),t("br"),t("br"),n._v("“...” Trương Huyền không hiểu ra sao."),t("br"),t("br"),n._v("Đối phương nói lời, cảm giác giống như là vè đọc nhịu... Làm sao nghe không hiểu chứ?"),t("br"),t("br"),n._v("“Chúng ta xưng hô ngươi là Trương sư, ngươi gọi chúng ta lão sư vì Khổng sư, cũng có rất nhiều người xưng hô ta vì bộ mặt sư... Đều là lấy họ tới tôn xưng sư phụ. ‘Lão sư’ cái từ này, vừa mới bắt đầu là lão sư, gọi sư phụ hắn. Về sau bị chúng ta tiếp tục sử dụng, mới truyền thừa đến bây giờ...” Tử Uyên Cổ Thánh tiếp tục nói."),t("br"),t("br"),n._v("Trương Huyền sửng sốt nửa ngày, nghĩ đến cái gì, nghi ngờ nhìn qua: “Ngươi ý tứ chẳng lẽ là... Các ngươi sư tổ, họ ‘Lão’ ? Cho nên gọi ‘Lão sư’, lúc này mới có lão sư cái chức vị này?”"),t("br"),t("br"),n._v("“Không sai!” Tử Uyên Cổ Thánh gật gật đầu."),t("br"),t("br"),n._v("“Còn có người họ Lão?” Cười khổ một tiếng, Trương Huyền không truy cứu nữa, lần nữa kỳ quái nhìn qua: “Nếu vị sư tổ kia có thể truyền thụ Khổng sư cao thâm như vậy công pháp, tu vi nhất định không yếu, làm sao... Ta chưa từng nghe qua?”"),t("br"),t("br"),n._v("Danh sư đại lục lịch sử, hắn nhìn qua không biết bao nhiêu lần, cũng có Ngoan Nhân như vậy sống dẫn đường... Nhưng từ chưa nghe qua vị này họ “Lão” siêu tuyệt nhân vật ah!"),t("br"),t("br"),n._v("“Nghe lão sư giảng giải, sư tổ chỉ ở danh sư đại lục đợi thời gian rất ngắn, nhìn thoáng qua, liền ngồi Thanh Ngưu rời đi, cụ thể đi nơi nào, hắn không biết, chúng ta tự nhiên càng không biết được...”"),t("br"),t("br"),n._v("“Tốt a...” Nhìn tới đồng dạng chỉ có thể chờ đợi Khổng sư phục sinh, mới có thể hỏi thăm rõ ràng."),t("br"),t("br"),n._v("Biết học tập không đến trảm tam thi, Trương Huyền đành phải nhìn qua: “Không biết chư vị bên trong, nhưng có am hiểu luyện đan cường giả, trong tay nhưng có tuyệt phẩm Thần Nguyên đan loại hình, có thể cấp tốc tăng cao tu vi dược vật?”"),t("br"),n._v(" Linh hồn của hắn, đã đi đến phong hào Thần Vương đỉnh phong, cũng lĩnh ngộ đột phá Đế Quân công pháp, hiện nay thiếu hụt chính là tăng lên chân khí đan dược và tăng lên thân thể cơ hội."),t("br"),t("br"),n._v("Chỉ cần cả hai thỏa mãn điều kiện, hoàn toàn có thể đem tu vi một lần đẩy tới chân chính Thần Vương đỉnh phong, thậm chí... Xung kích Đế Quân!"),t("br"),t("br"),n._v("Thân thể đã nghĩ kỹ, Bất Tử Đế Quân phục sinh, Bất Tử Dung Nham trì khẳng định cũng lần nữa có thể sử dụng, hoàn toàn có thể tiến vào bên trong rèn luyện, nhưng chân khí, cũng không đủ đan dược, không dễ dàng như vậy tiến bộ."),t("br"),t("br"),n._v("Thần giới luyện đan thuật mạnh nhất Phù Giang Thần, trong tay tuyệt phẩm Thần Nguyên đan cũng không nhiều, có lẽ Khổng sư những năm này tích lũy, hẳn là sẽ có không ít."),t("br"),t("br"),n._v("“Tuyệt phẩm Thần Nguyên đan, lão sư năm đó luyện chế qua một ít, sư huynh đệ chúng ta ăn không ít, hẳn là còn thừa lại mấy trăm viên, ta đi lấy tới...”"),t("br"),t("br"),n._v("Tử Uyên Cổ Thánh cười cười, xoay người rời đi."),t("br"),t("br"),n._v("“Mấy trăm viên?” Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Không hổ là Khổng sư, quả nhiên tài đại khí thô."),t("br"),t("br"),n._v("Thời gian không dài, Tử Uyên Cổ Thánh lấy ra một cái túi vải, tiện tay quăng ra: “Đều ở nơi này...”"),t("br"),t("br"),n._v("Trương Huyền mở ra, ngay sau đó nhìn thấy lít nha lít nhít tuyệt phẩm Thần Nguyên đan, quả nhiên có mấy trăm viên nhiều."),t("br"),t("br"),n._v("“Các ngươi đem... Thần Nguyên đan để ở chỗ này?” Lông mày nhảy dựng."),t("br"),t("br"),n._v("Người khác luyện chế ra Thần Nguyên đan, đều là đặt ở hộp ngọc hoặc là trong bình ngọc, đối phương trực tiếp dùng vải rách cái túi nhặt lên, cùng chứa rác rưởi đồng dạng..."),t("br"),t("br"),n._v("Đều để hắn nghi ngờ, có phải hay không cầm tới giả đan dược."),t("br"),t("br"),n._v("Tiện tay nặn ra một viên, há miệng nuốt xuống."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Một đạo mạnh mẽ linh khí, lập tức ở trong cơ thể tản mát ra, ngay sau đó lấy ra cái thứ hai, quả thứ ba... Một cái tiếp theo một cái nuốt xuống."),t("br"),t("br"),n._v("“Ngươi... Ăn như vậy đan dược?” Phù Giang Thần thể diện nhảy dựng."),t("br"),t("br"),n._v("Bọn họ đan dược là rất nhiều, có thể đây là tuyệt phẩm Thần Nguyên đan, hắn phong hào Thần Vương thực lực, luyện hóa đều cần tiêu phí một đoạn thời gian, đối phương mở miệng một tiếng, ăn kẹo đậu đồng dạng..."),t("br"),t("br"),n._v("Nhiều hơn nữa dược vật cũng không đủ ngươi ăn ah!"),t("br"),t("br"),n._v("Chính tràn đầy im lặng, liền thấy thanh niên trước mắt tu vi theo Thần Vương sơ kỳ, cấp tốc đột phá."),t("br"),t("br"),n._v("Thần Vương trung kỳ!"),t("br"),t("br"),n._v("Thần Vương hậu kỳ!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thời gian qua một lát liền đạt đến đỉnh phong, lúc này mới chậm chạp ngừng lại."),t("br"),t("br"),n._v("“Đáng tiếc... Tuyệt phẩm Thần Nguyên đan hiệu quả không còn...” Ngừng lại, Trương Huyền cảm xúc một câu."),t("br"),t("br"),n._v("Tuyệt phẩm Thần Nguyên đan đối phổ thông phong hào Thần Vương đều hữu dụng, nhưng đối Trương Huyền tới nói, đi đến Thần Vương đỉnh phong, hiệu quả liền không lớn, thôn phệ nhiều hơn nữa, cũng không cách nào để hắn đột phá phong Vương."),t("br"),t("br"),n._v("“May mắn không có hiệu quả, không phải, còn không ăn hết tất cả?”"),t("br"),t("br"),n._v("Tử Uyên Cổ Thánh tràn đầy bất đắc dĩ âm thanh vang lên."),t("br"),t("br"),n._v("Trương Huyền cúi đầu nhìn lại, trước mắt trong túi đan dược, dĩ nhiên chỉ còn lại có gần một nửa..."),t("br"),t("br"),n._v("Một mặt xấu hổ, gãi đầu một cái: “Chủ yếu là Khổng sư luyện chế mùi vị ăn quá ngon, dứa vị, táo vị, nho vị... Không có khống chế ở, thoáng cái ăn nhiều!”"),t("br"),t("br"),n._v("Tử Uyên Cổ Thánh: “...”"),t("br"),t("br"),n._v("(Thấy có người nói lão nhai chép bên cạnh tu chân, ta liền ha ha. Tu chân ta chỉ thấy máy kéo giải thi đấu. Nhiều năm chưa có xem qua.. Các ngươi nói gần nhất tình huống, ta không có chút nào biết, làm sao chép? Xích Tiêu kiếm, là Trung Quốc mười đại danh kiếm một trong.. Không cần chép người khác đi. Còn trảm tam thi, ta Vô Tận Đan Điền, liền viết qua. Không tin có thể lật xem, đó là 13 năm sách cũ, tu chân cũng còn không có mở sách, chẳng lẽ chép đại cương? Ta có biết trước năng lực?"),t("br"),t("br"),n._v("Những này là đã sớm quyết định tình huống. Tu chân mặc dù hot, Thiên đạo cũng không kém, chép, không đến mức. Ta lén lút hỏi qua nãi kỵ, hắn gần nhất cũng chưa có xem qua ta, ta gần nhất cũng chưa có xem qua hắn, nếu như nói... Hai ta thần giao cách cảm, không uổng công vào tháng năm cùng một chỗ ăn, cùng nhau chơi đùa, cùng ngủ. Khụ khụ, cùng một tòa nhà ngủ.. Các ngươi sẽ nôn ư?)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2252"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2254"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);