(window.webpackJsonp=window.webpackJsonp||[]).push([[141],{495:function(n,h,i){"use strict";i.r(h);var c=i(44),t=Object(c.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1103-huyen-huyen-hoi-đem-chung-ta-hop-nhat"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1103-huyen-huyen-hoi-đem-chung-ta-hop-nhat"}},[n._v("#")]),n._v(" Chương 1103: Huyền Huyền hội đem chúng ta hợp nhất!")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Thiếu người?”"),i("br"),i("br"),n._v("Nghe hắn hỏi như vậy, mọi người lộ ra mê hoặc chi sắc."),i("br"),i("br"),n._v("“Đúng!” Tiêu Tần cắn răng: “Ta cũng muốn gia nhập các ngươi Huyền Huyền hội!”"),i("br"),i("br"),n._v("“Cái này...” Mọi người ánh mắt sáng lên, Nhược Hoan công tử càng là cười to lên: “Tốt, nếu như chúng ta viện trưởng biết còn trẻ như vậy có triển vọng cường giả cũng muốn thêm đi vào, khẳng định hết sức cao hứng!”"),i("br"),i("br"),n._v("“Các ngươi đây là... Đồng ý?” Tiêu Tần có chút khẩn trương."),i("br"),i("br"),n._v("Đối phương nếu như chỉ có một cái, hai cái, sức chiến đấu cao cường, có thể quy kết làm thiên phú, nhưng mấy ngàn người cũng mạnh như vậy, chỉ có thể nói rõ bọn họ có một cái lão sư tốt."),i("br"),i("br"),n._v("Chiến sư chủ yếu mục đích là trở nên mạnh hơn, chỉ có thực lực tốt hơn mới có thể lại cùng Dị Linh tộc đối kháng quá trình bên trong, bảo tồn thực lực, bảo hộ nhân tộc."),i("br"),i("br"),n._v("Trước mắt cái này Huyền Huyền hội, hữu giáo vô loại, chỉ cần học tập đều có thể nhanh chóng tăng thực lực lên, đã như vậy, sao không gia nhập trong đó?"),i("br"),i("br"),n._v("“Có khác biệt gì ý, chúng ta Huyền Huyền hội hoan nghênh đủ loại thiên tài!”"),i("br"),i("br"),n._v("Cười ha ha một tiếng, Nhược Hoan công tử lật bàn tay một cái, lấy ra một quyển sách: “Đây là chính chúng ta tổng kết viện trưởng giảng bài nội dung, cũng không hoàn toàn, có điều, bao gồm viện trưởng đại bộ phận liên quan tới chiến đấu lý giải cùng lĩnh ngộ, ngươi trước tiên có thể nhìn một chút, có lẽ đối thực lực tăng lên không nhỏ trợ giúp!”"),i("br"),i("br"),n._v("“Cái này...” Tiện tay nhận lấy, nhìn một cái, Tiêu Tần chấn động trong lòng."),i("br"),i("br"),n._v("Tuy chỉ nhìn tờ thứ nhất, nhưng bằng mượn hắn ánh mắt cùng đối võ kỹ nhận biết, đó có thể thấy được quyển sách này tổng kết nội dung, tuyệt đối là chiến đấu vô thượng bảo điển."),i("br"),i("br"),n._v("Chỉ cần nghiêm túc học tập, tất nhiên có thể để cho thực lực của hắn nâng cao một bước."),i("br"),i("br"),n._v("“Bọn họ...” Khiếp sợ đồng thời, cũng đầy là cảm động."),i("br"),i("br"),n._v("Hắn vừa mới nói nhập hội, thậm chí liền đủ loại thủ tục đều không có xử lý, đối phương liền đem trân quý như thế đồ vật đem ra, không có chút nào hoài nghi cùng do dự."),i("br"),i("br"),n._v("Chỉ phần này tín nhiệm cùng khí độ, cũng làm người ta bội phục."),i("br"),i("br"),n._v("“Chúng ta Huyền Huyền hội chỉ có một cái tôn chỉ, cái kia chính là nghe theo viện trưởng Trương Huyền lời nói, vì hắn ra sức trâu ngựa! Còn cái khác, trên cơ bản đều là lẫn nhau câu thông lẫn nhau chia sẻ, không có việc riêng tư cùng ẩn tàng.”"),i("br"),i("br"),n._v("Nhìn ra ý nghĩ của hắn, Nhược Hoan công tử giải thích."),i("br"),i("br"),n._v("Huyền Huyền hội là một cái học sinh hệ thống, cũng không phải là cái gì đặc thù cơ quan, quản lý vốn là lỏng lẻo, Trương Huyền vừa mới bắt đầu xây dựng mục đích là liên hợp tất cả tân sinh, miễn trừ lão sinh áp bức, dùng để tự vệ."),i("br"),i("br"),n._v("Mặc dù hắn làm tới viện trưởng, cái này dự tính ban đầu đã không tồn tại nữa, nhưng mà trong hội đủ loại trao đổi, câu thông, trên cơ bản đều là không giữ lại chút nào, đại công vô tư."),i("br"),i("br"),n._v("Học tốt, cho không tốt giảng giải, thực lực không kém bao nhiêu lẫn nhau luyện tập, chung tiến bộ... Nguyên nhân chính là như vậy, Huyền Huyền hội nhiều như vậy bình thường thành viên, mới có thể tại ngắn ngủi thời gian mấy tháng phía trong, thực lực chợt tăng nhiều như vậy, ngay cả Chiến sư đường thiên phu trưởng cũng theo không kịp."),i("br"),i("br"),n._v("Thấy đối phương tôn chỉ, không bàn mà hợp “Sư” đạo chân ý, chân chính hữu giáo vô loại, dạy và học cùng tiến bộ, Tiêu Tần hoàn toàn bội phục."),i("br"),i("br"),n._v("“Tại hạ Tiêu Tần, là Chiến sư đường một vị thiên phu trưởng, lần này tới vốn là muốn tìm các ngươi phiền phức, nhìn một chút các ngươi có phải hay không có tư cách cùng chúng ta trao đổi, tận mắt thấy mới biết được, Huyền Huyền hội danh bất hư truyền, Trương Huyền viện trưởng càng là lòng dạ rộng lớn... Có thể nói danh sư mẫu mực!”"),i("br"),i("br"),n._v("Dừng lại một chút, Tiêu Tần hai mắt tỏa ánh sáng: “Thực tế không dám giấu giếm, bên ngoài còn có chúng ta mấy trăm tên Chiến sư, đang đợi tin tức của ta, ta hiện tại liền đem bọn họ kêu đi vào, chư vị có thể cùng bọn họ tuyên dương thoáng cái Huyền Huyền hội tôn chỉ, để bọn hắn cũng thêm đi vào, cùng một chỗ trao đổi, chung tiến bộ!”"),i("br"),i("br"),n._v("Cùng hắn tới đám người kia, hắn đều quen thuộc, cả đám đều đối thực lực tăng lên, tràn đầy khát vọng, nếu như cho bọn hắn biết có Huyền Huyền hội như thế một cái tốt bình đài, khẳng định tất cả đều sẽ không chút do dự thêm đi vào."),i("br"),i("br"),n._v("“Chiến sư đường mấy trăm người, cũng thêm đi vào?” Mọi người sững sờ."),i("br"),i("br"),n._v("“Chiến sư đường cho phép các ngươi gia nhập cái khác hệ thống?” Nhược Hoan công tử nhịn không được nói."),i("br"),i("br"),n._v("Bất kể nói thế nào, Chiến sư đường đều là Danh Sư đường bên trong độc lập ra một cái đặc thù đoàn thể, làm sao có thể cho phép trong đó thành viên tùy tiện gia nhập những tổ chức khác?"),i("br"),i("br"),n._v("“Chiến sư đường không có quá nhiều quy củ, huống chi chúng ta cũng đều là danh sư một phần tử, gia nhập danh sư hệ thống không tính là gì!” Tiêu Tần cười nói."),i("br"),i("br"),n._v("Chiến sư đường cứ việc quản lý nghiêm khắc, nhưng mà là nhằm vào không thể cõng phản nhân tộc, đối với trong đó thành viên gia nhập không gia nhập một ít hệ thống, cũng không có yêu cầu nghiêm khắc cùng quy định."),i("br"),i("br"),n._v("Lại nói Huyền Huyền hội là một cái lỏng lẻo trao đổi hệ thống, cũng không phải gì đó đặc thù chức nghiệp, cho dù đường chủ biết, hẳn là cũng sẽ không nói thêm cái gì."),i("br"),i("br"),n._v("“Đã như vậy, ngươi liền đem những bằng hữu kia cùng một chỗ mời tới đi... Mọi người cùng nhau trao đổi, cùng một chỗ tiến bộ!” Nghe hắn nói không có gì, Nhược Hoan công tử gật đầu."),i("br"),i("br"),n._v("“Tốt!” Tiêu Tần đi ra ngoài, thời gian không dài mang theo hơn hai trăm người đi đến."),i("br"),i("br"),n._v("Hai bên gặp mặt, khó tránh khỏi lại chiến đấu mấy trận, Tiêu Tần cũng không là đối thủ, những người khác tự nhiên càng không có phương pháp."),i("br"),i("br"),n._v("Chưa tới một canh giờ, hơn hai trăm vị Chiến sư liền đối Huyền Huyền hội mọi người bội phục đầu rạp xuống đất, tranh nhau chen lấn gia nhập trong đó, tiến hành cấp độ càng sâu trao đổi."),i("br"),i("br"),n._v("Càng trao đổi càng ngày càng hiện vị này Trương viện trưởng đối với chiến đấu lý giải, có thể nói kinh khủng, trong lòng cũng càng ngày càng bội phục."),i("br"),i("br"),n._v("Cao thâm như vậy kỹ xảo, đều có thể dốc túi truyền cho, đực mà chịu chi, không có chút nào bảo lưu cùng giấu dốt, vị này Trương sư đến cùng là cái dạng gì người?"),i("br"),i("br"),n._v("Tất cả mới gia nhập Huyền Huyền hội Chiến sư, cũng đối với hắn tràn ngập tò mò."),i("br"),n._v(" ..."),i("br"),i("br"),n._v("Chiến sư đường rộng lớn trưởng lão đại thính nghị sự."),i("br"),i("br"),n._v("“Trác Thanh Phong cùng Phùng Huân bọn họ báo lên tình huống ta đã kỹ càng nhìn, có thể trong thời gian ngắn để bình thường danh sư, nắm giữ Chiến sư thực lực, vị này Trương Huyền viện trưởng thật có lấy chỗ hơn người. Lần này trao đổi, vừa vặn có thể quan sát quan sát, nếu có thích hợp hạt giống, nghĩ biện pháp kéo vào được, để chúng ta Thanh Nguyên phân bộ càng thêm lớn mạnh!”"),i("br"),i("br"),n._v("Đại điện chủ vị bên trên một cái lão giả vuốt chòm râu, nói."),i("br"),i("br"),n._v("Thanh Nguyên đế quốc Chiến sư đường đường chủ, nửa bước Xuất Khiếu cảnh cường giả, Hình Trác Viễn."),i("br"),i("br"),n._v("Lần này trao đổi mặc dù nghe dở dở ương ương, nhưng mà nếu là bọn họ Chiến sư đường thiên phu trưởng, tự mình đo lường, đủ loại tôn sùng, hắn cũng muốn tận mắt nhìn, một cái nho nhỏ danh sư học viện, đến cùng phải hay không như trong truyền thuyết cường đại như vậy."),i("br"),i("br"),n._v("Thật sự là như vậy, ngược lại là có thể hấp thụ nhiều một chút thiên tài, để bọn hắn phân bộ mạnh lên."),i("br"),i("br"),n._v("“Đường chủ nhìn xa trông rộng, là chúng ta tư tưởng nhỏ hẹp!”"),i("br"),i("br"),n._v("“Đường chủ ánh mắt siêu quần, ta Thanh Nguyên phân bộ, tất nhiên càng ngày càng thịnh vượng!”"),i("br"),i("br"),n._v("Nghe được giải thích của hắn, phía dưới rất nhiều điện chủ cùng trưởng lão, đồng thời cảm khái, lộ ra bội phục chi sắc."),i("br"),i("br"),n._v("Trước đó nghe đường chủ đáp ứng trao đổi, cả đám đều cảm thấy không thể tưởng tượng nổi."),i("br"),i("br"),n._v("Chiến sư đường, luôn luôn là danh sư bên trong chiến lực cường đại cọc tiêu, một cái không thể vượt qua mục tiêu, Hồng Viễn học viện bất quá là Thanh Nguyên đế quốc cấp dưới một cái bồi dưỡng danh sư nơi chốn, đến đây cái gọi là cùng cấp trao đổi... Quả thực liền là kéo xuống trình độ của bọn hắn!"),i("br"),i("br"),n._v("Thẳng đến lúc này, mới hiểu được đường chủ khổ tâm."),i("br"),i("br"),n._v("Muốn nói danh sư, là người bình thường bên trong thiên tài, Chiến sư chính là thiên tài bên trong thiên tài, mấy vạn danh sư bên trong, cũng chưa chắc có thể tuyển ra một vị."),i("br"),i("br"),n._v("Nguyên nhân chính là như vậy, Chiến sư số lượng mười phần có hạn, nếu là cái này Hồng Viễn học viện, thật cùng Trác Thanh Phong đám người nói đồng dạng, người người như rồng, cũng nắm giữ vượt cấp chiến đấu thực lực, ngược lại thật sự là có thể cân nhắc đem hắn thu nhập trong đó."),i("br"),i("br"),n._v("“Đúng rồi, đường chủ, chúng ta mặc dù xác định sau ba ngày trao đổi, nhưng không ít Chiến sư, trong lòng không phục, đã lặng lẽ chạy đến Hồng Viễn học viện chỗ ở, tính toán thăm dò một phen...” Nhớ tới một việc, một vị lão giả nói."),i("br"),i("br"),n._v("Võ kỹ điện, chân dĩnh điện chủ!"),i("br"),i("br"),n._v("Chiến sư đường cùng Hồng Viễn học viện phân ra viện hệ, cũng không phải là một cái thống nhất toàn thể, mà là phân ra rất nhiều điện đường, tổng cộng mười cái, theo thứ tự là: Vũ khí điện, quyền pháp điện, chưởng pháp điện, thân pháp điện, võ kỹ điện, nội tức điện, phòng ngự điện, hồn lực điện, thối công điện, cùng thần bí nhất tâm điện!"),i("br"),i("br"),n._v("Mỗi một cái điện đường, cũng có hoàn chỉnh nhất truyền thừa, đem nào đó một bộ phận lực lượng phát huy đến cực hạn."),i("br"),i("br"),n._v("Tỷ như vũ khí điện, chia làm đao, kiếm, thương rất nhiều vũ khí, có thể đi vào trong đó, cơ bản cũng nắm giữ vũ khí chân ý, chiến lực cường đại."),i("br"),i("br"),n._v("Chưởng pháp điện Chiến sư, thì chủ tu chưởng lực, chưởng pháp tinh diệu, lực lượng hùng hậu; Thân pháp điện, thì am hiểu thân pháp, tốc độ cực nhanh."),i("br"),i("br"),n._v("Còn hồn lực điện, cũng không phải là Vu hồn, mà là tu luyện hồn phách, học tập một loại có thể tiến hành công kích linh hồn võ kỹ."),i("br"),i("br"),n._v("Một người tinh lực có hạn, không có khả năng mỗi một dạng cũng mạnh, Chiến sư điện đem đủ loại năng lực phân hoá tu luyện, có thể để cho bên trong Chiến sư, nhanh hơn tăng lên sức chiến đấu, ở chỗ Dị Linh tộc nhân giao chiến quá trình bên trong, phát huy ra uy lực mạnh hơn."),i("br"),i("br"),n._v("“A, chuyện này ta cũng nghe nói, bọn họ liều lĩnh để bọn hắn náo đi, vừa vặn cũng có thể nhìn một chút cái này Hồng Viễn học viện Huyền Huyền hội, đến cùng phải hay không cùng Trác Thanh Phong đám người nói đồng dạng, từng cái đều có thể vượt cấp chiến đấu!”"),i("br"),i("br"),n._v("Hình đường chủ cười cười."),i("br"),i("br"),n._v("Tiêu Tần đám người mặc dù đi rất bí mật, tự nhận là lừa gạt được tất cả mọi người, nhưng mà nhiều như vậy học sinh đột nhiên đi ra ngoài, trốn đến Hồng Viễn học viện cư trú ngoài trang viên mặt, tin tức vẫn là truyền tới."),i("br"),i("br"),n._v("Ở đây rất nhiều điện chủ, trưởng lão hầu như đều biết tình, chỉ là không ngăn cản thôi."),i("br"),i("br"),n._v("“Ta cũng là nghĩ như vậy, cho nên không ngăn cản, vừa rồi đã phái người đi nhìn một cái hỏi thăm, hẳn là qua không được bao lâu, liền có thể biết tin tức xác thật!”"),i("br"),i("br"),n._v("Chân dĩnh điện chủ gật đầu."),i("br"),i("br"),n._v("“Chúng ta cũng đều phái người hỏi thăm, cái này Hồng Viễn học viện, đến cùng có hay không chân tài thực học, hẳn là có thể thấy được... Được rồi, tin tức!”"),i("br"),i("br"),n._v("Lại một vị điện chủ cũng nhẹ gật đầu, đang tại nói chuyện, cổ tay khẽ đảo, một cái đưa tin ngọc bài, xuất hiện tại lòng bàn tay, cười khanh khách cúi đầu nhìn sang, chỉ nhìn một cái, con ngươi co rụt lại."),i("br"),i("br"),n._v("“Làm sao vậy? Chúng ta Chiến sư, cùng đối phương giao đấu, là thắng là thua?”"),i("br"),i("br"),n._v("Thấy hắn bộ biểu tình này, ở giữa hình đường chủ sững sờ, mở miệng hỏi."),i("br"),i("br"),n._v("“Tin tức của ta cũng tới, ta xem một chút...”"),i("br"),i("br"),n._v("Chân dĩnh điện chủ cũng cảm ứng được cái gì, lòng bàn tay cũng nhiều ra một cái ngọc bài, đem phía trên nội dung quét qua, cũng cùng vừa rồi vị điện chủ kia đồng dạng, ngốc tại chỗ, cũng lại nói không ra lời."),i("br"),i("br"),n._v("“Đến cùng làm sao vậy? Các ngươi hai cái ngược lại là nói một chút ah...”"),i("br"),i("br"),n._v("Thấy hai vị điện chủ cũng bộ dáng này, một vị lão giả cũng nhịn không được nữa, đi tới trước mặt, đem ngọc bài cầm trong tay xem xét, cũng đứng chết trân tại chỗ, như đồng hóa thạch."),i("br"),i("br"),n._v("Qua nửa ngày, chân dĩnh điện chủ dùng sắp khóc ra thanh âm hô lên: “Đường chủ... Chúng ta không hợp nhất bọn họ, mà là... Huyền Huyền hội người đem chúng ta đi qua khiêu chiến Chiến sư, đều hợp nhất...”"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1111"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1113"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);