(window.webpackJsonp=window.webpackJsonp||[]).push([[1415],{1770:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-217-hoa-nhau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-217-hoa-nhau"}},[n._v("#")]),n._v(" Chương 217: Hoà nhau")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Yên tĩnh."),t("br"),t("br"),n._v("Toàn trường yên tĩnh."),t("br"),t("br"),n._v("Tất cả mọi người đều ngoác to miệng, từng cái từng cái trợn mắt ngoác mồm."),t("br"),t("br"),n._v("Mới vừa nói xong Vương Dĩnh phải thua không thể nghi ngờ Thẩm Bích Như, cũng giống bị người đánh một cái tát, đen nhánh tú mục sắp rơi trên mặt đất."),t("br"),t("br"),n._v("“Một chân... Liền đem Đỗ Lỗi đạp bay? Nàng... Làm sao biết cái nào là huyễn ảnh, cái nào là chân thân?”"),t("br"),t("br"),n._v("Đỗ Lỗi tốc độ nhanh như vậy, coi như cùng cấp bậc cao thủ, cũng chưa chắc phân phân biệt rõ ràng, cái này Vương Dĩnh ngược lại tốt, tùy tiện một chân... Liền đem hắn đá bay, cùng đá một cái bao tải rách như thế."),t("br"),t("br"),n._v("“Không những là phân biệt ra được huyễn ảnh, nàng này một chân tốc độ, muốn so với Đỗ Lỗi nhanh rất nhiều mới được, bằng không, liền hắn đuổi đều không đuổi kịp, làm sao đem hắn đả thương?”"),t("br"),t("br"),n._v("Hàn quỳnh sắc mặt nghiêm túc: “Xem ra, vị này Vương Dĩnh chân tổn thương không chỉ chữa khỏi, còn học tập rất lợi hại thoái công cùng thân pháp.”"),t("br"),t("br"),n._v("“Đúng đấy!”"),t("br"),t("br"),n._v("Thẩm Bích Như cũng phản ứng lại."),t("br"),t("br"),n._v("Không chỉ nhìn ra đối phương bóng người đơn giản như vậy, không có đầy đủ võ kỹ phối hợp, làm sao có khả năng một chân liền đem hắn đạp bay?"),t("br"),t("br"),n._v("“Chuyện này...” Vương Siêu nắm lấy tóc."),t("br"),t("br"),n._v("Mới vừa mắng xong đối phương đồ ngu, liền một chân đem Đỗ Lỗi đạp bay, không mang theo như vậy chơi, sẽ đùa chết người."),t("br"),t("br"),n._v("Một bên Hồng Hạo trưởng lão cũng giống bị đánh bạt tai, sắp khóc."),t("br"),t("br"),n._v("Vốn là hắn tới đây là muốn xem Trương Huyền bị làm mất mặt, thân bại danh liệt, làm sao chơi đùa chút, của hắn tiếng tăm càng ngày càng vang dội, trái lại Lục Tầm bị đánh?"),t("br"),t("br"),n._v("“Khặc khặc khặc!”"),t("br"),t("br"),n._v("Tỷ thí đài một mặt khác Lục Tầm bị nước bọt sặc, suýt chút nữa một đầu ngã chổng vó."),t("br"),t("br"),n._v("Hồng Hạo trưởng lão khóc, hắn khóc càng ác hơn."),t("br"),t("br"),n._v("Đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("Đây chính là Huyễn Ảnh Cửu Cung Bộ, Đỗ Lỗi vận chuyển không có một chút nào sai lầm, tuy rằng khoảng cách hắn còn cách một đoạn, nhưng Chân Khí cảnh phát huy đến trình độ loại này, đã tính được là thiên phú tuyệt hảo!"),t("br"),t("br"),n._v("Lợi hại như vậy bộ pháp, bị một chân đạp bay?"),t("br"),t("br"),n._v("Mấu chốt nhất chính là..."),t("br"),t("br"),n._v("Trên đài bé gái, giờ khắc này vành mắt đều đỏ, vẫn xin lỗi, nói nàng xuất cước nặng..."),t("br"),t("br"),n._v("Nặng ngươi muội a!"),t("br"),t("br"),n._v("Chẳng lẽ ngươi vừa nãy chần chờ, cũng không phải không biết làm sao ra tay, mà là thế nào ra tay mới không nặng?"),t("br"),t("br"),n._v("Không nặng đều đem người một chân đạp bay mười mấy mét, một khi nặng, há không tại chỗ đạp chết?"),t("br"),t("br"),n._v("Người xem cuộc chiến từng cái từng cái khác nào điên rồi, trên đài Đỗ Lỗi rốt cục giẫy giụa leo lên, nhìn về phía trước mắt đầy mặt áy náy nữ hài, càng thêm không nhịn được, lần thứ hai phun ra một ngụm máu tươi."),t("br"),t("br"),n._v("“Ngươi không sao chứ... Ta thật là không phải cố ý... Ta mới học võ kỹ, khống chế không được sức mạnh, dùng sức lớn...”"),t("br"),t("br"),n._v("Vương Dĩnh vành mắt ửng hồng."),t("br"),t("br"),n._v("“Lớn ngươi muội a!”"),t("br"),t("br"),n._v("Rít lên một tiếng, Đỗ Lỗi hung tợn đánh tới."),t("br"),t("br"),n._v("Hắn thật sự nổi giận."),t("br"),t("br"),n._v("Một chân đem ta đạp bay liền thôi, ngươi đạo cái gì xin lỗi? Cảm thấy ta không bằng ngươi, cố ý đến nhục nhã ta sao?"),t("br"),t("br"),n._v("Còn vẫn cường điệu dùng sức lớn... Có phải là cảm thấy ta quá yếu? Kinh khủng ngươi dụng hết toàn lực?"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Không nghĩ tới thành khẩn xin lỗi, đối phương ngược lại tức giận giống như đến xông lại, Vương Dĩnh sợ đến sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Nàng tuy rằng tu vi lên so Trịnh Dương, Lưu Dương bọn người cao, có thể không có một chút nào kinh nghiệm thực chiến, tính cách lại quá mức hiền lành, nhìn thấy đối phương nhào tới, sợ đến sắc mặt đều liếc."),t("br"),t("br"),n._v("Dưới tình thế cấp bách, dưới chân xoay một cái."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Loé lên một cái, đã đến mười mấy mét có hơn."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("“Một cái chớp mắt thoát ra mười mấy mét khoảng cách? Đây là... Thân pháp gì?”"),t("br"),t("br"),n._v("“Quá nhanh đi! Sao có thể có chuyện đó?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Xung quanh đầu tiên là một hồi yên lặng, tiếp theo nổ tung giống như âm thanh âm vang lên."),t("br"),t("br"),n._v("Tất cả mọi người đều cho rằng vừa nãy Đỗ Lỗi Huyễn Ảnh Cửu Cung Bộ, được cho trâu bò nhất thân pháp võ kỹ, nằm mơ đều không nghĩ tới, Vương Dĩnh cái này tăng thêm sự kinh khủng."),t("br"),t("br"),n._v("Liền nửa cái hô hấp cũng chưa tới, liền thoát ra mười mấy mét, này đã không thể dùng thân pháp hình dạng, mà dùng quỷ mị."),t("br"),t("br"),n._v("Đừng nói Đan Điền cảnh, coi như Ích Huyệt cảnh võ giả, khẳng định cũng không có nhanh như vậy."),t("br"),t("br"),n._v("Làm thế nào đến?"),t("br"),t("br"),n._v("“Vương gia khẳng định không có loại thân pháp này, nếu như có, tất nhiên đã sớm nổi danh...”"),t("br"),t("br"),n._v("“Đúng đấy, không phải là Vương gia, học từ ai vậy?”"),t("br"),t("br"),n._v("“Nhất định là Trương lão sư, vừa nãy Trịnh Dương lợi hại thương pháp không phải là cùng Trương lão sư học sao?”"),t("br"),t("br"),n._v("“Lợi hại như vậy thân pháp, đơn giản truyền thụ? Ta nghĩ lùi khóa, quỳ gối vị này Trương lão sư môn hạ!”"),t("br"),t("br"),n._v("“Đâu chỉ là ngươi, ta cũng muốn...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài tất cả mọi người ánh mắt đều có chút kịch liệt."),t("br"),t("br"),n._v("Thương pháp, cũng không phải người nào đều có thể tu luyện, còn chưa đủ làm cho người động tâm, vừa vặn pháp liền không giống, đây chính là lúc chiến đấu bảo mệnh, thắng lợi to lớn nhất dựa dẫm, nếu như có thể học được, coi như sau đó gặp phải nguy hiểm, thủ đoạn bảo mệnh cũng biết tăng cường không ít."),t("br"),t("br"),n._v("Loại này lợi hại võ kỹ, ai không muốn học?"),t("br"),t("br"),n._v("“Ta muốn ngươi thua...”"),t("br"),t("br"),n._v("Thấy Vương Dĩnh một hồi chợt hiện mười mấy mét, Đỗ Lỗi cũng có chút choáng váng, bất quá, giờ khắc này hắn đã bị phẫn nộ làm choáng váng đầu óc, cái gì đều không để ý tới, lần thứ hai vọt tới."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Nhìn thấy đối phương hung thần ác sát, khóe miệng còn tràn đầy máu tươi, Vương Dĩnh doạ lại phải kinh ngạc thốt lên, thân thể loáng một cái, lại chạy ra ngoài."),t("br"),t("br"),n._v("Một cái đuổi một cái trốn."),t("br"),t("br"),n._v("“Nàng tính cách xác thực quá yếu, xem ra sau này phải cố gắng tôi luyện một hồi.”"),t("br"),t("br"),n._v("Đem tình cảnh này nhìn ở trong mắt, Trương Huyền lắc lắc đầu."),t("br"),n._v(" Hiện tại loại cục diện này, đừng nói Trịnh Dương, Lưu Dương, Viên Đào ba vị nam học sinh, coi như Triệu Nhã gặp gỡ, cũng khẳng định một chân đem Đỗ Lỗi đạp bay, đạp không chết, cũng biết để hắn cũng lại không đứng lên nổi."),t("br"),t("br"),n._v("Có thể Vương Dĩnh, lại bị đuổi toàn trường chạy."),t("br"),t("br"),n._v("Nàng tính cách ngốc nảy sinh ôn nhu, lúc này mới dẫn đến cục diện này."),t("br"),t("br"),n._v("Xem ra nàng thiếu không phải võ kỹ cùng tu vi, mà là đối địch kinh nghiệm chiến đấu."),t("br"),t("br"),n._v("“Thiên Đạo thân pháp, cực kỳ tiêu hao thể lực, một hai lần còn có thể, nhiều liền không xong rồi!”"),t("br"),t("br"),n._v("Thiên Đạo thân pháp, tốc độ cực nhanh, một cái chớp mắt liền có thể bay lượn khoảng mười mấy thước, xem ra rất lợi hại, nhưng cũng có thiếu hụt."),t("br"),t("br"),n._v("Vậy thì là đúng thể lực tiêu hao thực sự quá lớn."),t("br"),t("br"),n._v("Vương Dĩnh như vậy đào tẩu một hai lần còn có thể, nhiều đào tẩu mấy lần, nhất định sẽ bị đối phương nắm lấy."),t("br"),t("br"),n._v("Quả nhiên, hắn ý nghĩ này còn không có kết thúc, liền thấy Vương Dĩnh trên đầu đổ mồ hôi, miệng lớn thở hổn hển, cũng lại không chạy nổi."),t("br"),t("br"),n._v("Lúc này, cũng bị đối phương bức đến tỷ thí đài bên cạnh."),t("br"),t("br"),n._v("Rào rào!"),t("br"),t("br"),n._v("Đỗ Lỗi đi tới bênh cạnh, kéo nàng lại cánh tay: “Ta thắng không được cũng sẽ không để ngươi thắng...”"),t("br"),t("br"),n._v("Hống xong, vừa tung người hướng dưới đài nhảy xuống."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không có nghĩ tới tên này như vậy chơi xấu, muốn đem nàng kéo xuống tỷ thí đài, Vương Dĩnh dưới tình thế cấp bách, cũng lại quản không đến rất nhiều, lần thứ hai một chân đạp tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đỗ Lỗi lập tức giống đá bay bóng cao su, trong nháy mắt bay ra ngoài, trên không trung liên tục trở mình vài cái bổ nhào, rơi xuống hơn ba mươi mét ở ngoài."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Bất quá, đá xong đối phương, nàng cũng khí lực tiêu hao hết, trực tiếp theo tỷ thí đài té xuống, rơi trên mặt đất."),t("br"),t("br"),n._v("Hai người đều rơi xuống tỷ thí dưới đài, hơn nữa gần như đồng thời rơi xuống đất, chỉ có một kết quả... Hoà nhau!"),t("br"),t("br"),n._v("Đỗ Lỗi biết mình thắng không được Vương Dĩnh, cố ý làm ra hung ác hình, kéo nàng rơi xuống."),t("br"),t("br"),n._v("Bất quá, cuối cùng Vương Dĩnh dưới tình thế cấp bách phản kích cũng rất lợi hại, một hồi bay ra hơn ba mươi mét, coi như không chết, cũng gần như muốn nằm trên giường hai tháng."),t("br"),t("br"),n._v("“Chuyện này... Này thuộc về chơi xấu!”"),t("br"),t("br"),n._v("“Vô sỉ...”"),t("br"),t("br"),n._v("“Rõ ràng không phải là đối thủ, còn đem người quăng bên dưới tỷ thí đài, bị đá bay đáng đời...”"),t("br"),t("br"),n._v("“Tại sao có thể không biết xấu hổ như vậy, quả thực so Viên Đào còn không biết xấu hổ!”"),t("br"),t("br"),n._v("Dưới đài một hồi ồ lên."),t("br"),t("br"),n._v("Mọi người cũng nhìn ra rồi, Đỗ Lỗi này thuộc về chơi xấu."),t("br"),t("br"),n._v("Tỷ thí liền tỷ thí, hắn rất rõ ràng thân pháp, thoái pháp cũng không bằng đối phương, bắt nạt đối phương là nữ hài, ỷ vào khí lực đem người kéo xuống, quả thực vô sỉ đến cực điểm."),t("br"),t("br"),n._v("Cho tới bị đá bay, cái kia là đáng đời... Không có một người đồng tình."),t("br"),t("br"),n._v("Nghe được tiếng mắng, Viên Đào một mặt không nói gì, làm sao nói cái gì không có nói, liền nằm thương?"),t("br"),t("br"),n._v("Ai không biết xấu hổ? Các ngươi mới không biết xấu hổ, cả nhà cũng không muốn mặt..."),t("br"),t("br"),n._v("“Đồng thời rơi xuống đất, hoà nhau!”"),t("br"),t("br"),n._v("Nghe được cãi vã, trọng tài lão sư có chút đau đầu, bất quá, vẫn gật đầu, nói ra rồi kết quả."),t("br"),t("br"),n._v("Tuy rằng Đỗ Lỗi có chút chơi xấu hiềm nghi, xác thực hai người đồng thời rơi xuống, chỉ có thể tính đánh ngang."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Nghe được kết quả này, Vương Dĩnh khóe miệng vểnh lên, đi tới Trương Huyền trước mặt, đầy mặt áy náy."),t("br"),t("br"),n._v("Trương lão sư đối với nàng như thế tín nhiệm, không chỉ hỗ trợ bác sĩ tốt chân nhanh, còn truyền thụ thoái công, thân pháp, kết quả... Lại không đánh thắng, làm cho nàng rất là xấu hổ."),t("br"),t("br"),n._v("“Không có chuyện gì, lần sau chú ý là được, không cần để ở trong lòng.” Trương Huyền gật gù: “Là ta sơ sẩy, ngươi kém không phải tu vi, công pháp, mà là tâm tính.”"),t("br"),t("br"),n._v("“Đa tạ lão sư!”"),t("br"),t("br"),n._v("Thấy hắn không trách tội, trái lại an ủi, Vương Dĩnh càng thêm thật không tiện, bất quá kết cục đã xuất hiện, lại không cách nào thay đổi, không thể làm gì khác hơn là rầu rĩ không vui đi tới đoàn người phía sau."),t("br"),t("br"),n._v("Nếu như Đỗ Lỗi nhìn thấy nàng dáng dấp này, nhất định sẽ trực tiếp điên rồi."),t("br"),t("br"),n._v("Ta đều bị ngươi đạp nằm trên đất không thể di chuyển, ngươi còn phiền muộn... Nên phiền muộn chính là ta có được hay không?"),t("br"),t("br"),n._v("“Trương lão sư liền thắng hai trận, bình một hồi, đã đứng ở thế bất bại.”"),t("br"),t("br"),n._v("“Đúng đấy, coi như Lục lão sư phía sau hai trận trọn vẹn thắng, cũng chỉ là hoà nhau.”"),t("br"),t("br"),n._v("“Liền xem phía sau làm sao so, ta cảm thấy Lục lão sư cơ hội chiến thắng đã không lớn.”"),t("br"),t("br"),n._v("“Ta cũng cho là như thế, vốn đang cho rằng Lục lão sư khẳng định nghiền ép, không nghĩ tới... Trương lão sư nghiền ép, đến cùng xảy ra chuyện gì?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tuy rằng cảm thấy Đỗ Lỗi cách làm quá mức vô sỉ, nhưng rất nhanh mọi người cũng là tiếp nhận rồi sự thực này."),t("br"),t("br"),n._v("Quy tắc như vậy, dù cho xem ra không công bằng, cũng không có cách nào."),t("br"),t("br"),n._v("Song phương hoà nhau, vấn đề lại tới nữa rồi."),t("br"),t("br"),n._v("Năm học sinh tỷ thí, Trương lão sư đã hai thắng một bình, áp lực đến hết Lục lão sư trên người, nếu như phía sau học sinh lại thua một lần, cái cuối cùng cũng sẽ không dùng so."),t("br"),t("br"),n._v("Mà nếu như tất cả thắng lợi, hai người tỷ thí, chính là hoà nhau, song phương đều không tổn thương bộ mặt, được cho tốt nhất kết cục."),t("br"),t("br"),n._v("Nghĩ đến điểm này, mọi người trọn vẹn đưa ánh mắt tập trung đến Lục Tầm trên người, muốn nhìn một chút hắn lần này sẽ phái ra ai, như thế nào chiến đấu."),t("br"),t("br"),n._v("“Không thể lại thua... Ta bốc lên sư giả bình trắc, vốn là mở mày mở mặt, lại thua, liền biến không mặt mũi gặp người...”"),t("br"),t("br"),n._v("Lục Tầm cắn răng một cái."),t("br"),t("br"),n._v("Người khác đều có thể nghĩ thông suốt, hắn tự nhiên cũng rõ ràng."),t("br"),t("br"),n._v("Giờ khắc này không thể lại thua xuống, một khi tiếp tục, đừng nói lập uy, sau đó tại toàn bộ học viện, đều sẽ không nhấc nổi đầu lên."),t("br"),t("br"),n._v("“Chu Hồng đã từng thua với qua Triệu Nhã, hiện tại coi như tu vi tiến bộ, trong lòng khả năng còn có thể có bóng mờ, để hắn lên sân khấu, không có thể bảo đảm trăm phần trăm thắng lợi... Bạch Siêu lên đi! Hắn am hiểu quyền pháp, đối phó Lưu Dương, nên vấn đề không lớn.”"),t("br"),t("br"),n._v("Khoảnh khắc, Lục Tầm làm ra quyết định, vẫy tay."),t("br"),t("br"),n._v("Bạch Siêu từ trong đám người đi ra, nhanh chân đi tới tỷ thí đài."),t("br"),t("br"),n._v("“Nên ta!”"),t("br"),t("br"),n._v("Thấy hắn lên sân khấu, Lưu Dương nhẹ nhàng nở nụ cười, không đợi Trương Huyền dặn dò, cũng đi vào."),t("br"),t("br"),n._v("(Niên quan trước mặt, lão nhai không có cách nào bạo phát, mọi người thứ lỗi. Mặt khác, mọi người có thể chú ý lão nhai tinh vi tin công chúng hào, tinh vi tin tìm tòi “Quét ngang thiên nhai” liền có thể, hồi phục “Vương Dĩnh” là có thể nhìn thấy Vương muội muội bức ảnh, thư hữu cầm, xem ra rất đẹp đẽ, trước thêm vào hồi phục không có tác dụng, hiện tại có thể một lần nữa thử một chút. Sau đó sẽ từng bước đem Triệu Nhã mấy người bức ảnh cùng một chỗ gửi đi, như có bạo phát, cũng biết ở phía trên sớm thông báo. Mặt khác, còn có vé tháng sao, đừng quên ném, đa tạ mọi người.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-225"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-227"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);