(window.webpackJsonp=window.webpackJsonp||[]).push([[1426],{1784:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-218-đoi-quyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-218-đoi-quyen"}},[n._v("#")]),n._v(" Chương 218: Đối quyền")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chủ tịch vị bên trên, Vương Siêu cùng Hồng Hạo trưởng lão mắt to mắt nhỏ nhìn nhau."),t("br"),t("br"),n._v("“Bạch Siêu am hiểu quyền pháp, Lưu Dương... Chắc là không phải là đối thủ đi!”"),t("br"),t("br"),n._v("Vương Siêu chần chờ một chút, nói chính mình cũng không có cái gì sức lực."),t("br"),t("br"),n._v("Trước mấy cái đều lời thề son sắt, cho rằng có thể thắng, kết quả... Đều thua rất thảm."),t("br"),t("br"),n._v("Cái này Bạch Siêu, dựa theo trước ý nghĩ, lẽ ra có thể dễ như trở bàn tay thắng lợi, có thể... Trương Huyền này nhóm học sinh, quá quỷ dị, liên tục suy đoán sai lầm, để hắn đều không có tự tin."),t("br"),t("br"),n._v("“Ta cũng cảm thấy, chắc là... Có thể thắng đi...”"),t("br"),t("br"),n._v("Hồng Hạo trưởng lão cũng đầy mặt ưu thương."),t("br"),t("br"),n._v("Hắn rất nhớ này vị Trương lão sư danh dự quét rác, bị mạnh mẽ làm mất mặt, bây giờ nhìn lại, mặt đã đánh không được, hiện tại nhất muốn làm chính là, phòng ngừa làm sao bị đánh..."),t("br"),t("br"),n._v("“Bạch Siêu, đừng do dự, vừa ra tay liền đánh ra mạnh nhất quyền pháp, đừng cho đối phương có thể thừa dịp!”"),t("br"),t("br"),n._v("Cắn răng một cái, Vương Siêu đối với trên đài Bạch Siêu rống lên một tiếng."),t("br"),t("br"),n._v("Hiện tại đã không có đường lui, trận này lại thua, cuối cùng một hồi cũng sẽ không dùng so, kế trước mắt, chỉ có thể được ăn cả ngã về không, phá phủ chìm bên trong."),t("br"),t("br"),n._v("Bạch Siêu am hiểu quyền pháp, cái kia vừa ra tay, hay dùng ra công kích mạnh nhất, đem đối phương đánh tan."),t("br"),t("br"),n._v("Không phải vậy, tràn ngập hi vọng mà đến, lại thất bại tan tác mà quay trở về..."),t("br"),t("br"),n._v("Bản thân người bạn thân này, còn làm sao lại bái ba sư vi sư? Làm sao mới có thể lên làm học đồ?"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Bạch Siêu gật đầu đáp một tiếng, híp mắt lại."),t("br"),t("br"),n._v("Phía trước ba cuộc tỷ thí hắn đều nhìn, đã biết Trương lão sư mấy học sinh, tu vi tuy rằng không cao, lại mỗi người thực lực không thấp."),t("br"),t("br"),n._v("Không thể có mảy may băn khoăn cùng do dự."),t("br"),t("br"),n._v("Bằng không, nhất định sẽ dẫm vào trước vết xe đổ."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Vương Hoằng tộc trưởng, ngươi cảm thấy Bạch Siêu có thể thắng sao?”"),t("br"),t("br"),n._v("Trên khán đài, Bạch Minh tộc trưởng nhìn về phía cách đó không xa Vương Hoằng."),t("br"),t("br"),n._v("Hai người đều là gia tộc lớn tộc trưởng, khống chế bá chủ một phương, cũng đều là Thông Huyền cảnh cường giả, có quyền lên tiếng nhất."),t("br"),t("br"),n._v("“Ta cảm thấy không có, Trương lão sư hai thắng một hòa, thành lập uy thế, Lưu Dương chỉ cần không kém, rất khó trở mình.”"),t("br"),t("br"),n._v("Vương Hoằng lắc đầu."),t("br"),t("br"),n._v("Tỷ thí đều quá nửa, Lục lão sư học sinh vẫn bị đè lên đánh, lại muốn thắng lợi, nào có như vậy dễ dàng."),t("br"),t("br"),n._v("Lại nói, nữ nhi mình tại Trương lão sư môn hạ, biết Trương Huyền truyền thụ lý luận là cỡ nào kinh người, không phải vậy, cái này đần độn con gái, cũng không đến mức một chân liền đem một cái võ giả tam trọng Đỗ Lỗi, đá bay ra ngoài."),t("br"),t("br"),n._v("Liền nàng đều lợi hại như vậy, Lưu Dương khẳng định cũng sẽ không kém."),t("br"),t("br"),n._v("“Ta cùng quan điểm của ngươi không giống!” Bạch Minh tộc trưởng hai mắt như điện."),t("br"),t("br"),n._v("“Ồ?” Vương Hoằng nghi hoặc nhìn sang."),t("br"),t("br"),n._v("“Vị này Bạch Siêu là ta người của Bạch gia, bất quá là cái chi mạch, vô cùng xa xôi, trước cũng không biết. Vừa nãy ta để người chuyên môn điều tra, hắn lại có thể tu luyện Thiết Giáp công!”"),t("br"),t("br"),n._v("Bạch Minh tộc trưởng trong giọng nói tựa hồ có hơi không thể tin được."),t("br"),t("br"),n._v("“Thiết Giáp công? Lẽ nào là...”"),t("br"),t("br"),n._v("Vương Hoằng tộc trưởng đầu tiên là sững sờ, lập tức con ngươi co rụt lại: “Lẽ nào là bảy mươi năm trước, Thiết Giáp lão nhân tu luyện cái kia công pháp? Không phải đã sớm thất truyền sao? Làm sao ngươi cái này tộc nhân biết?”"),t("br"),t("br"),n._v("Nói tới Thiết Giáp công, rất ít người biết, nhưng nói tới Thiết Giáp lão nhân, coi như trẻ tuổi, cũng không có thiếu người biết."),t("br"),t("br"),n._v("Chỉ là bảy mươi năm trước rất nổi danh một cái quái trộm."),t("br"),t("br"),n._v("** cướp giật, không chuyện ác nào không làm, không ít người nghe đến đã biến sắc, là Thiên Huyền Vương quốc trong lịch sử đều xếp hạng cao gia hỏa."),t("br"),t("br"),n._v("Bởi vì quá mức hung hăng, Vương quốc cùng dân gian đều phái ra không ít cao thủ, đối với hắn tiến hành đánh lén, đáng tiếc, những cường giả này đều thất bại tan tác mà quay trở về, có thể nói đi bao nhiêu chết bao nhiêu."),t("br"),t("br"),n._v("Rốt cục có một lần, hành tung của hắn phơi bày, bị mấy chục cao thủ vây nhốt ở một cái trong sơn cốc, mọi người biết thủ đoạn hắn lợi hại, không có tới gần, dùng cường nỏ viễn trình bắn, kết quả phát hiện kinh người, hắn bên ngoài thân sẽ xuất hiện một tầng cùng động vật vảy như thế đồ vật, để mũi tên đều đâm không thủng."),t("br"),t("br"),n._v("Nói cách khác, cường nỏ cung tên, đối với hắn căn bản không tạo được thương tổn."),t("br"),t("br"),n._v("Hết cách rồi, không thể làm gì khác hơn là xông lên trên đánh nhau tay đôi, trên người đối phương vảy hầu như phòng ngự vô địch, đao kiếm đều không có cách nào đâm thủng, trận chiến đó, giết máu chảy thành sông, nhật nguyệt ảm đạm."),t("br"),t("br"),n._v("Cuối cùng, tuy rằng chính nghĩa một phương thắng lợi, tuy nhiên tổn thất chí ít hơn ba mươi vị Thông Huyền cảnh cường giả, để Thiên Huyền Vương quốc toàn thể quốc lực, đều hạ xuống một đoạn dài, suýt chút nữa không có tỉnh lại đến."),t("br"),t("br"),n._v("Truyền thuyết, thân thể mọc đầy vảy tuyệt chiêu chính là Thiết Giáp công!"),t("br"),t("br"),n._v("Bảy mươi năm trước, bạn Thiết Giáp lão nhân tử vong, không phải đã sớm thất truyền sao? Làm sao cái này Bạch Siêu biết?"),t("br"),t("br"),n._v("“Thiết Giáp công là thất truyền, hắn tu luyện cũng không phải chính tông Thiết Giáp công.” Bạch Minh sắc mặt trầm tĩnh."),t("br"),t("br"),n._v("“Cái kia...” Vương Hoằng tộc trưởng chớp con mắt."),t("br"),t("br"),n._v("“Chính tông Thiết Giáp công, tu luyện lên đến, toàn thân che kín vảy, phòng ngự vô địch, mà hắn chi tu luyện tới trên nắm tay, nói cách khác, một khi vận công, nắm đấm biết trở nên cùng sắt thép như thế đáng sợ, thậm chí có thể xem như binh khí sử dụng!”"),t("br"),t("br"),n._v("Bạch Minh tộc trưởng đem biết đến tin tức tất cả nói ra, cuối cùng đưa ra trước như vậy nói nguyên nhân: “Lưu Dương tay phải bản thân có thương tích, mà cái tên này nắm đấm lại phòng ngự vô địch, sức mạnh càng là lớn đến kinh người, ngươi cảm thấy ai có thể thắng lợi?”"),t("br"),t("br"),n._v("“Chuyện này... Nếu như đúng là Thiết Giáp công, e sợ thật sự rất khó thắng lợi...”"),t("br"),t("br"),n._v("Vương Hoằng tộc trưởng sầm mặt lại."),t("br"),t("br"),n._v("Biết cái kia đoạn lịch sử, biết Thiết Giáp lão nhân mạnh mẽ, cũng liền không thể nào không biết Thiết Giáp công đáng sợ."),t("br"),t("br"),n._v("Này đã không phải là loài người, mà là cùng man thú giống như phòng ngự cùng sức mạnh."),t("br"),t("br"),n._v("Sự công kích này, đừng nói Lưu Dương đã từng bị thương qua, coi như không có nhận qua, lại làm sao có khả năng đánh bại?"),t("br"),n._v(" “Kỳ thực... Chỉ cần Lưu Dương không ngốc cùng hắn đối quyền, lấy bơi thân công kích, có lẽ không đến mức thất bại thảm hại đi!”"),t("br"),t("br"),n._v("Trầm tư chốc lát, Vương Hoằng tộc trưởng nói."),t("br"),t("br"),n._v("Quả đấm đối phương rất lợi hại, nhưng cũng có thiếu sót."),t("br"),t("br"),n._v("Vậy thì là những nơi khác phòng ngự, công kích đều đối lập thấp hơn, đã như vậy, chỉ cần không cùng hắn chính diện tiếp xúc, lấy bơi thân công kích, chưa chắc sẽ thua."),t("br"),t("br"),n._v("“Chỉ hy vọng như thế đi, bất quá... Ta sợ Lưu Dương không biết, biết trúng kế hoạch của hắn.” Bạch Minh tộc trưởng cười khổ."),t("br"),t("br"),n._v("Tuy rằng cái này Bạch Siêu là của hắn tộc nhân, lại không tình cảm gì, trên bản chất vẫn là hi vọng Trương Huyền đan sư có thể thắng lợi."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Đang muốn phản bác, Vương Hoằng tộc trưởng liền nghe đến tỷ thí trên đài hai người thiếu niên đối thoại, sáng sủa vang lên."),t("br"),t("br"),n._v("“Lưu Dương, ta biết ngươi tay phải trước kinh mạch bị hao tổn, bất quá, chắc là giống như Vương Dĩnh, bị Trương lão sư chữa khỏi đi!” Bạch Siêu nhìn sang."),t("br"),t("br"),n._v("“Không sai, Trương lão sư xem xảy ra vấn đề, tự mình đi luyện đan sư công hội tìm đến Ôn Mạch đan, lúc này, cánh tay phải của ta dĩ nhiên hoàn hảo.” Lưu Dương gật đầu."),t("br"),t("br"),n._v("Nói tới chuyện này, hắn tràn đầy tự hào cùng sùng bái."),t("br"),t("br"),n._v("Chỉ có Trương lão sư loại này đại công vô tư người, mới sẽ vì học sinh trả giá nhiều như vậy, không tính đến bất luận cái gì được mất."),t("br"),t("br"),n._v("“Nếu đã được rồi, ta có cái đề nghị, không biết có hứng thú hay không... Vậy thì là chúng ta cũng đừng loè loẹt tỷ thí, trực tiếp đối quyền, ai không chịu nổi, ai liền thất bại.” Trắng siêu cười lạnh: “Đừng nói với ta không dám, trừ phi... Các ngươi Trương lão sư căn bản không chữa khỏi cánh tay của ngươi, lời nói mới rồi đều là khoác lác.”"),t("br"),t("br"),n._v("“Đối quyền liền đối quyền, ai sợ ai?” Nghe được đối phương hoài nghi Trương lão sư, Lưu Dương nhất thời giận dữ, bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Cái này ngốc hàng...”"),t("br"),t("br"),n._v("Thấy lo lắng cái gì đến cái gì, cái tên này quả nhiên bị lừa, Vương Hoằng tộc trưởng biến sắc mặt."),t("br"),t("br"),n._v("Đối phương tu luyện thế nhưng Thiết Giáp công, đáp ứng cùng chi đối quyền, này không phải muốn chết sao?"),t("br"),t("br"),n._v("“Đi nói cho Trương lão sư, Bạch Siêu tu luyện chính là Thiết Giáp công, nắm đấm cứng rắn không gì sánh được, ngàn vạn không thể để cho Lưu Dương cùng với đối quyền...”"),t("br"),t("br"),n._v("Gấp vội vàng xoay người dặn dò phía sau một tên hộ vệ."),t("br"),t("br"),n._v("Hộ vệ gật gù, nhanh chóng đi tới Trương Huyền trước mặt, đem hắn muốn nói sự tình nói rồi."),t("br"),t("br"),n._v("“Trương lão sư Thiết Giáp công, ta nghe nói qua, là lúc trước Thiết Giáp lão nhân lưu lại bí quyết, từ nhỏ đã hai tay ngâm Thiết Giáp thú dòng máu, lâu dần, cái này bàn tay biết trở nên cùng sắt thép giống như cứng rắn, bất quá, cũng đồng dạng mất đi bình thường bàn tay mềm mại, chạm đến đồ vật thời điểm, rất khó phân biệt ra được.”"),t("br"),t("br"),n._v("Triệu Nhã tựa hồ đối với cái này Thiết Giáp công biết rất nhiều, mở miệng nói."),t("br"),t("br"),n._v("“Cùng sắt thép giống như cứng rắn? Cái kia cùng mang theo cái thiết thủ dẫn khác nhau ở chỗ nào?”"),t("br"),t("br"),n._v("Trương Huyền không nói gì."),t("br"),t("br"),n._v("Tu luyện là để người sinh mệnh cấp độ tiến hóa, càng là cao thủ, đối thủ yêu cầu càng cao, cũng là càng nhạy bén."),t("br"),t("br"),n._v("Cái tên này ngược lại tốt, lấy tay luyện thành sắt thép, thậm chí ngay cả cơ bản xúc giác, cảm giác đều biến mất, coi như công pháp lại ngưu bức, cũng không có gì dùng a."),t("br"),t("br"),n._v("Thật không biết ra sao biến thái, nghĩ ra loại này biến thái công pháp."),t("br"),t("br"),n._v("“Trương lão sư, tộc trưởng để ta cho ngươi biết, nhất định phải khuyên can Lưu Dương, không muốn cùng hắn đối quyền...”"),t("br"),t("br"),n._v("Thấy hai người còn có tâm tình ở đây thảo luận Thiết Giáp công, hộ vệ vội vàng nói chen vào."),t("br"),t("br"),n._v("“Không muốn đối quyền? Tại sao?” Trương Huyền kỳ quái nhìn sang: “Ta cảm thấy Lưu Dương đáp ứng rất tốt a, Bạch Siêu là võ giả tam trọng, nắm giữ chân khí gia trì, bất luận tốc độ vẫn là năng lực phản ứng, đều mạnh hơn Lưu Dương không ít, đánh không lại liền chạy mà nói, một ngày cũng quyết không ra thắng bại. Đứng tại chỗ đối quyền, vừa vặn có thể đem Lưu Dương rất nhiều thế yếu che lấp, vì sao khuyên can?”"),t("br"),t("br"),n._v("Vậy thì cùng trận đầu Viên Đào cùng Khổng Kiệt chiến đấu như thế."),t("br"),t("br"),n._v("Lưu Dương mấy người tu vi so với đối phương thấp là không thể thay đổi sự thực, đối phương thật muốn triển khai loè loẹt chiêu số, vẫn đúng là bế tắc, nếu bọn họ lựa chọn đứng yên bất động đối chiến, chẳng phải chính hợp ta ý?"),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Hộ vệ rung mạnh."),t("br"),t("br"),n._v("Căn bản không phải chuyện này có được hay không?"),t("br"),t("br"),n._v("Ta vừa nãy không nói sao?"),t("br"),t("br"),n._v("Đối phương tu luyện chính là Thiết Giáp công, vậy cũng là có thể so với sắt thép nắm đấm, đối quyền là không thể thắng lợi..."),t("br"),t("br"),n._v("Đang muốn giải thích hai câu, liền thấy tỷ thí trên đài, bóng người lay động, hai người chiến đấu dĩ nhiên bắt đầu."),t("br"),t("br"),n._v("Lúc này coi như muốn khuyên can, cũng đã chậm."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Nếu đáp ứng, vậy liền bắt đầu đi!”"),t("br"),t("br"),n._v("Thấy Lưu Dương trúng kế, Bạch Siêu hưng phấn hét lên một tiếng, về phía trước hai bước, một quyền đánh ra ngoài."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nắm đấm áp bức không khí phát ra âm bạo, quả nhiên cùng nói như thế, mặt ngoài che kín tỉ mỉ lân, giống như đã biến thành man thú móng trảo, làm cho người ta một loại phi thường áp bức."),t("br"),t("br"),n._v("“Đến đúng lúc!”"),t("br"),t("br"),n._v("Lưu Dương nhướng mày, cũng nắm đấm giơ lên tiến lên nghênh tiếp."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Xong!”"),t("br"),t("br"),n._v("Không nghĩ tới bản thân vội vàng phái ra hộ vệ, đều không có ngăn cản lại hai người đối quyền, Vương Hoằng tộc trưởng sắc mặt trắng bệch, thân thể không kìm lòng được lung lay hai lần."),t("br"),t("br"),n._v("Nhân loại nắm đấm, làm sao có khả năng cùng đối phương man thú nắm đấm chống lại? Hơn nữa còn là thực lực kém xa tít tắp tình huống?"),t("br"),t("br"),n._v("Nhắm mắt lại thở dài, đang muốn cảm khái Lưu Dương lập tức liền biết xương tay gãy vỡ, thảm bại lên sân khấu, liền nghe đến bênh cạnh Bạch Minh tộc trưởng hô hấp khí gấp rút, “A!” Kinh kêu thành tiếng."),t("br"),t("br"),n._v("“Chuyện này... Chuyện này...”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-226"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-228"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);