(window.webpackJsonp=window.webpackJsonp||[]).push([[1437],{1793:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-219-nguoi-dam-tiep-hay-khong-【-đai-cao-trao-đen-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-219-nguoi-dam-tiep-hay-khong-【-đai-cao-trao-đen-】"}},[n._v("#")]),n._v(" Chương 219: Ngươi dám tiếp hay không? 【 đại cao trào đến! 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Hả?”"),t("br"),t("br"),n._v("Nghe được âm thanh không đúng, vội vàng mở mắt nhìn lại."),t("br"),t("br"),n._v("Nhìn chút bên dưới, Vương Hoằng tộc trưởng cũng thân thể loáng một cái, suýt chút nữa không có ngất đi."),t("br"),t("br"),n._v("Thế này thì quá mức rồi?"),t("br"),t("br"),n._v("Chỉ thấy tỷ thí trên đài, Lưu Dương giơ nắm đấm, trên mặt không có bất kỳ biểu lộ gì, mà đối diện nắm giữ sắt thép nắm đấm Bạch Siêu, trên đầu tràn đầy mồ hôi lạnh ngã quỳ trên mặt đất."),t("br"),t("br"),n._v("Giờ khắc này quả đấm của hắn bị mạnh mẽ đánh gãy xương, vặn vẹo cùng bánh quai chèo như thế, phảng phất gặp phải khó có thể chống lại sức mạnh trọng kích."),t("br"),t("br"),n._v("“Ngươi không phải... Thiết Giáp công sao? Làm sao như thế yếu?”"),t("br"),t("br"),n._v("Thu hồi nắm đấm, Lưu Dương nhìn về phía trước mắt Bạch Siêu."),t("br"),t("br"),n._v("Triệu Nhã muốn tỷ thí, Diêu Hàn sớm liền đem đối thủ tình huống sờ soạng cái ngọn nguồn đi, cái tên này biết Thiết Giáp công, nắm đấm có thể biến thành man thú móng trảo dáng dấp tình huống, cũng đã sớm nói cho hắn."),t("br"),t("br"),n._v("“Ngươi biết...”"),t("br"),t("br"),n._v("Bạch Siêu quái đản như thế biểu cảm."),t("br"),t("br"),n._v("Vốn tưởng rằng cố ý dùng phép khích tướng, đối phương đã trúng kế, kết quả mới phát giác... Người ta đã sớm biết, là hắn ngốc không sót gần như thu thập đi lên..."),t("br"),t("br"),n._v("“Đương nhiên biết rồi!” Lưu Dương gật đầu, tiếp theo đầy mặt kỳ quái: “Không phải nói ngươi cái này Thiết Giáp công rất lợi hại, liền sắt thép đều có thể một quyền xuyên thủng sao? Làm sao như thế yếu?”"),t("br"),t("br"),n._v("“Yếu?”"),t("br"),t("br"),n._v("Bạch Siêu muốn khóc."),t("br"),t("br"),n._v("Không phải ta yếu, là ngươi quá mạnh mẽ có được hay không?"),t("br"),t("br"),n._v("truy"),t("br"),n._v("cập http://ngantruyen.comđể  đọc truyện  Ta xác thực từng đánh xuyên qua qua sắt thép, bất quá cũng là nửa tấc, có thể ngươi cái này nắm đấm, hai, ba tấc sắt thép cũng có thể đánh xuyên qua chứ?"),t("br"),t("br"),n._v("“Ta biết rồi, là ngươi không có dụng hết toàn lực? Đến, chúng ta lại đối một quyền...”"),t("br"),t("br"),n._v("Suy tư chốc lát, Lưu Dương hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Hắn luyện thành Thiên Đạo thần quyền sau đó, chỉ tự mình tu luyện, căn bản không biết chiêu này đáng sợ, cùng Trịnh Dương mấy người đối chiến, cũng không dám triển khai toàn lực, chỉ lo bạn học bị thương, thật vất vả gặp phải một cái quyền lực mạnh mẽ, sao có thể buông tha."),t("br"),t("br"),n._v("Lại nói, hắn cũng có chút cố ý, muốn lặng lẽ đen ta? Vậy ta liền đánh ngươi không dám tiếp tục bốc lên ý nghĩ này!"),t("br"),t("br"),n._v("“Không được...”"),t("br"),t("br"),n._v("Bạch Siêu còn chưa kịp nói chuyện, liền thấy thiếu niên ở trước mắt cầm lấy tay trái của hắn nắm thành quả đấm, một hồi đập tới."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Tay trái cũng đã biến thành gãy xương."),t("br"),t("br"),n._v("“Ô u Ô u...”"),t("br"),t("br"),n._v("Bạch Siêu muốn tự tử đều có."),t("br"),t("br"),n._v("Tay phải hắn luyện qua Thiết Giáp công, tay trái chưa từng luyện có được hay không?"),t("br"),t("br"),n._v("Lại nói... Ta đều nói không thể so, ngươi cầm lấy ta nắm đấm trực tiếp đập nát, cái gì quỷ?"),t("br"),t("br"),n._v("Mãnh liệt đau đớn cùng phẫn nộ, dự định mắng đối phương một trận, còn chưa kịp mở miệng tại, liền nghe đến thiếu niên đối diện âm thanh lại vang lên: “A, ngươi Thiết Giáp công có phải là cũng luyện đến trên đầu? Để cho ta tới thử xem...”"),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Nghe nói như thế, suýt chút nữa không có một ngụm lão huyết phun ra."),t("br"),t("br"),n._v("Nắm đấm ngươi đập gãy xương, còn có thể bác sĩ tốt, đầu muốn mở ra gáo, liền thật sự chết rồi..."),t("br"),t("br"),n._v("“Ta chưa từng luyện, trọng tài lão sư, ta chịu thua, ta chịu thua...”"),t("br"),t("br"),n._v("Chỉ lo cái tên này thật bắt hắn đầu làm thí nghiệm, Bạch Siêu sợ đến liên tiếp lui về phía sau, điên cuồng gào thét, chỉ hận cha mẹ ít sinh một cái miệng, không phải vậy, khẳng định gọi đến âm thanh cũng đại chút..."),t("br"),t("br"),n._v("“Chịu thua?”"),t("br"),t("br"),n._v("Trọng tài lão sư đi lên phía trước."),t("br"),t("br"),n._v("“Ân, ừm!” Bạch Siêu gật đầu liên tục."),t("br"),t("br"),n._v("“Các ngươi đã thua hai trận, bình một hồi, nếu như lại thua, chẳng khác nào sư giả bình trắc thất bại...” Trọng tài lão sư nói."),t("br"),t("br"),n._v("“Ta biết, ta chịu thua...” Bạch Siêu vội vàng hô."),t("br"),t("br"),n._v("Thất bại liền thất bại, dù sao cũng hơn hắn chết rồi mạnh hơn đi..."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Nghe hắn nói như vậy, trọng tài lão sư biết lần này sư giả bình trắc đã có kết quả, không nhịn được lắc đầu một cái, âm thanh âm vang lên: “Bạch Siêu chịu thua! Sư giả bình trắc kết thúc, Trương Huyền lão sư thắng lợi!”"),t("br"),t("br"),n._v("“Trương lão sư thắng rồi?”"),t("br"),t("br"),n._v("“Quá lợi hại, của hắn gần như học sinh thật sự đều quá lợi hại!”"),t("br"),t("br"),n._v("“Đúng đấy, ngoại trừ Viên Đào ở ngoài, cái khác mấy cái đều quá mạnh mẽ!”"),t("br"),t("br"),n._v("“Trịnh Dương thương pháp, Vương Dĩnh thân pháp, thoái pháp, Trịnh Dương nắm đấm, đều thật là đáng sợ!”"),t("br"),t("br"),n._v("“Bọn họ giống như chúng ta, đều là mới vừa vào học tân sinh, có thể lợi hại như vậy, khẳng định là Trương lão sư dạy tốt!”"),t("br"),t("br"),n._v("“Ân, chờ thi đấu kết thúc, ta nhất định phải bái ông ta làm thầy...”"),t("br"),t("br"),n._v("“Chờ ta, ta cũng cùng một chỗ...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được trọng tài lão sư thông báo ra sư giả bình trắc kết quả, tất cả mọi người đều ồ lên."),t("br"),t("br"),n._v("Trước tại Diêu Hàn nỗ lực, đã có không ít người đối vị này Trương lão sư danh tiếng chuyển tốt, lại nhìn tới hình ảnh trước mắt, tất cả đều kích động sắc mặt thấu hồng."),t("br"),t("br"),n._v("Đến học viện mục đích là cái gì?"),t("br"),t("br"),n._v("Không phải là tìm một cái tốt lão sư sao?"),t("br"),t("br"),n._v("Hiện tại cái này Trương lão sư đối mặt Lục Tầm lão sư đều trực tiếp nghiền ép, nên lợi hại bao nhiêu dạy học năng lực?"),t("br"),t("br"),n._v("Không ít học sinh đã quyết định, tân sinh thi đấu kết thúc, hãy cùng vị này Trương lão sư học tập."),t("br"),t("br"),n._v("“Chuyện này... Liền thắng?”"),t("br"),t("br"),n._v("Học sinh hưng phấn, chủ chỗ ngồi Thẩm Bích Như thì chớp con mắt, có chút không dám tin tưởng."),t("br"),t("br"),n._v("Nàng từ khi tiến vào học viện, liền vẫn lấy Lục Tầm vì mục tiêu, nghĩ làm sao vượt qua, kết quả... Nàng còn không làm được, liền bị một cái danh tiếng luôn luôn không tốt gia hỏa ngược tơi bời hoa lá."),t("br"),t("br"),n._v("Vậy cũng là Lục Tầm, hơn nữa hết thảy học sinh, đều đạt đến Chân Khí cảnh..."),t("br"),t("br"),n._v("“Bích như, ánh mắt của ngươi thật tốt, ta hiện tại cũng đối cái này Trương lão sư cảm thấy hứng thú...”"),t("br"),t("br"),n._v("Một bên Hàn Quỳnh đôi mắt đẹp Nhất Thiểm."),t("br"),t("br"),n._v("“Chú ý một chút thân phận, cái gì cảm thấy hứng thú?” Nghe được bạn thân mà nói, Thẩm Bích Như đôi mi thanh tú một túc."),t("br"),n._v(" “Vâng, là! Là có hứng thú, ngươi nói ta tối đi tìm vị này Trương lão sư thỉnh giáo vấn đề, hắn có thể hay không mở cửa thả ta đi vào?” Hàn Quỳnh cười hì hì, không nói ra được quyến rũ."),t("br"),t("br"),n._v("“Ta cảm thấy hắn sẽ đánh chết ngươi!”"),t("br"),t("br"),n._v("Trong lòng thầm mắng một câu không biết xấu hổ, Thẩm Bích Như hừ nói."),t("br"),t("br"),n._v("Nói thật, nàng đối chính hắn một bạn thân cũng là không nói gì, làm là lão sư, dĩ nhiên câu nói như thế này đều nói ra được đến..."),t("br"),t("br"),n._v("Kính xin dạy vấn đề, có vấn đề gì không thể ban ngày thỉnh giáo, nhất định phải buổi tối?"),t("br"),t("br"),n._v("“Ha ha, ta cảm thấy là ngươi đánh chết ta đi!”"),t("br"),t("br"),n._v("Hàn Quỳnh nở nụ cười."),t("br"),t("br"),n._v("Hai nữ đấu võ mồm, chủ tịch vị ở chính giữa nơi."),t("br"),t("br"),n._v("“Nhìn thấy không?”"),t("br"),t("br"),n._v("Lưu Lăng nhìn về phía còn lại hai người."),t("br"),t("br"),n._v("“Ừm! Lục Tầm gần như học sinh tham gia thi đấu, cũng giống như là hoàn thành nhiệm vụ, thắng thua cũng không đáng kể, chỉ cần không bị thương là tốt rồi.”"),t("br"),t("br"),n._v("Trang Hiền ánh mắt lấp loé, mang theo nhìn xa trông rộng cùng phán đoán: “Từ điểm đó có thể thấy được, hắn cùng học sinh quan hệ, chỉ dừng lại tại giao dịch cái giai tầng này, dù cho hắn trợ giúp đối phương tăng cao thực lực, tại những người này trong lòng, cũng chỉ là đồng giá hối đoái, không phải ân tình.”"),t("br"),t("br"),n._v("“Đúng đấy, hắn giúp học sinh tăng cao thực lực mục đích không thuần, tự nhiên cũng là không chiếm được học sinh ủng hộ.”"),t("br"),t("br"),n._v("Trịnh Phi lắc đầu."),t("br"),t("br"),n._v("Danh sư thu đồ đệ, không chỉ nhìn thiên phú, dạy học trình độ, còn phải xem học sinh đối lão sư bảo vệ trình độ."),t("br"),t("br"),n._v("Lục Tầm năm học sinh, Chu Hồng còn không thấy biểu hiện, nhưng theo Khổng Kiệt, Mạc Hiểu, Đỗ Lỗi, Bạch Siêu bốn người thái độ, liền có thể nhìn ra một ít đầu mối."),t("br"),t("br"),n._v("Dạy học thời điểm, quá mức công danh lợi lộc, chỉ là vì thắng được sư giả bình trắc, mà không phải vì phát triển của bọn họ."),t("br"),t("br"),n._v("Như vậy là có thể trong thời gian ngắn để cho tăng cao thực lực, để bọn họ nắm giữ Chân Khí cảnh sức mạnh, nhưng không có đạt được bọn họ thành thật cùng tôn kính."),t("br"),t("br"),n._v("Thử hỏi một chút, ai sẽ tôn kính coi bọn họ là thành công cụ người?"),t("br"),t("br"),n._v("“Trương Huyền gần như học sinh không giống nhau, là thành thật yêu quý hắn, đồng ý vì hắn trả giá tất cả.”"),t("br"),t("br"),n._v("Thấy hai vị lão hữu nhìn ra, Lưu Lăng gật đầu."),t("br"),t("br"),n._v("Nếu như chỉ nhìn một cách đơn thuần cuộc tranh tài này, khả năng không nhìn ra những cái này, nhưng kết hợp mấy ngày nay bọn họ “Nhìn trộm”, liền có thể nhìn ra rất hơn nhiều."),t("br"),t("br"),n._v("Triệu Nhã, Vương Dĩnh mấy người, không phải vì tu luyện mà tu luyện, cũng không phải vì mình có thể tăng lên bao nhiêu mà tu luyện, mà là... Trong lòng có một cái cùng chung mục tiêu!"),t("br"),t("br"),n._v("Vậy thì là... Vì Trương lão sư giành vẻ vang, vì Trương lão sư thắng được vinh dự!"),t("br"),t("br"),n._v("Trong lòng bọn họ nghĩ tới là lão sư, mà không phải mình."),t("br"),t("br"),n._v("Đồng dạng là tu luyện, dạy học, một cái nghĩ nhiệm vụ, một cái nghĩ là lão sư giành vẻ vang, không cho hắn thất vọng cùng thương tâm... Dĩ nhiên có khác biệt một trời một vực."),t("br"),t("br"),n._v("“Khó trách Dương sư vừa ý, vị này Trương lão sư, thực sự là một vị Danh sư vật liệu, e sợ không tốn thời gian dài, rồi cùng chúng ta chân chính đứng ngang hàng!” Lưu Lăng cảm khái."),t("br"),t("br"),n._v("“Đúng đấy!”"),t("br"),t("br"),n._v("Hai vị lão hữu đồng thời gật đầu."),t("br"),t("br"),n._v("Rào rào!"),t("br"),t("br"),n._v("Ba người đang muốn tiếp tục cảm khái, liền thấy tỷ thí trận mặt khác một bên Lục Tầm đứng lên."),t("br"),t("br"),n._v("Sư giả bình trắc thua, có hai con đường có thể đi, số một, trước mọi người xin lỗi, dựa theo trước cá cược, tiến hành bồi thường; Thứ hai, rời đi học viện, không về nữa."),t("br"),t("br"),n._v("“Ngươi nói Lục lão sư, thì như thế nào lựa chọn?”"),t("br"),t("br"),n._v("“Không biết, ta cảm thấy nên nói xin lỗi đi!”"),t("br"),t("br"),n._v("“Thắng bại là binh gia chuyện thường, ai cũng không có thể bảo đảm nhất định sẽ thắng!”"),t("br"),t("br"),n._v("“Đúng đấy, thua liền thua, không có cái gì! Thật muốn rời khỏi học viện, lợi hại đến đâu minh tinh giáo sư, cũng giống như mất đi nguồn nước cá, không có rễ cỏ dại, sớm muộn muốn xui xẻo!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Xung quanh tất cả nhỏ giọng."),t("br"),t("br"),n._v("Sư giả bình trắc, rất nhiều năm không có từng xuất hiện, Lục lão sư là bốc lên người, lại gặp phải thảm bại, trong lòng khẳng định vô cùng khó chịu."),t("br"),t("br"),n._v("Tất cả mọi người đều muốn nhìn một chút, hắn đến cùng sẽ làm sao lựa chọn."),t("br"),t("br"),n._v("Nếu như chỉ là chịu thua xin lỗi, thì cũng chẳng có gì, thật muốn rời khỏi học viện, vậy thì đáng tiếc."),t("br"),t("br"),n._v("Có “Sinh” mới có thể xưng là “Sư”, rời đi học viện, không còn học sinh, vẫn là cái gì “Sư giả” ?"),t("br"),t("br"),n._v("Hơn nữa, có rời đi học viện cùng sư giả bình trắc thất bại trải qua, đi những học viện khác, giống như cũng sẽ không thu."),t("br"),t("br"),n._v("“Trương Huyền lão sư!”"),t("br"),t("br"),n._v("Vài bước đi tới tỷ thí giữa đài ở giữa, Lục Tầm quăng một cái ống tay áo, vóc người thẳng tắp."),t("br"),t("br"),n._v("Trương Huyền nhìn sang."),t("br"),t("br"),n._v("“Sư giả bình trắc ta thua! Ngươi dạy học sinh xác thực có thủ đoạn, bất quá, thế giới này, cuối cùng vẫn là muốn xem thực lực bản thân, coi như ngươi dạy cho dù tốt, bản thân không có thực lực, như thế vô dụng, ta muốn cùng ngươi khiêu chiến!”"),t("br"),t("br"),n._v("Lục Tầm nắm đấm xiết chặt, mang theo không cho phản bác quyết tâm, trên người rất nhiều huyệt đạo sáng tối chập chờn, cho thấy Ích Huyệt cảnh mạnh mẽ tu vi, đồng thời thanh âm hùng hồn xông thẳng lên trời."),t("br"),t("br"),n._v("“Ngươi... Dám tiếp không dám nhận?”"),t("br"),t("br"),n._v("Tiêu tốn nhiều như vậy tâm huyết, chuẩn bị như thế sung túc, lại có thể bại triệt để như vậy, hắn biết Hồng Thiên học viện đã lại không mặt mũi tiếp tục tiếp tục chờ đợi."),t("br"),t("br"),n._v("Trước khi đi, phải cho cái này Trương Huyền một bài học, cho hắn biết, cái khác đều là giả, thực lực chân chính mới là vương đạo!"),t("br"),t("br"),n._v("“Khiêu chiến ta?”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương sẽ làm ra loại này quyết định, Trương Huyền đang muốn nói chuyện, liền thấy Triệu Nhã bước lên trước, che ở của hắn phía trước, cả người đứng ngạo nghễ tại chỗ, giống như tiên nữ hạ phàm."),t("br"),t("br"),n._v("“Nhục lão sư ta, ta liền đánh với hắn một trận, ai muốn cùng ta cùng một chỗ?”"),t("br"),t("br"),n._v("“Ta!”"),t("br"),t("br"),n._v("“Ta!”"),t("br"),t("br"),n._v("“Ta!”"),t("br"),t("br"),n._v("“Ta!”"),t("br"),t("br"),n._v("Liên tiếp bốn tiếng, Vương Dĩnh, Trịnh Dương, Lưu Dương, Viên Đào, tất cả đều đi ra, từng cái từng cái lộ ra không thể nghi ngờ kiên định."),t("br"),t("br"),n._v("“Ngươi nhục thầy ta, chúng ta năm người nguyện đánh với ngươi một trận, vì hắn tranh đến vinh dự, Lục Tầm, ngươi dám tiếp hay không?”"),t("br"),t("br"),n._v("Năm người âm thanh giống như sấm rền, gió lốc mà lên, vang vọng toàn bộ học viện."),t("br"),t("br"),n._v("“Vi sư chính danh, sư nhục sinh tử... Đây là giảng bài cấp bậc cao nhất chính danh cảnh?”"),t("br"),t("br"),n._v("Lưu Lăng thân thể run rẩy, đột nhiên đứng dậy."),t("br"),t("br"),n._v("(Đại cao trào đến, hết thảy bố cục chỉ vì tình cảnh này, vé tháng đâu? Mặt khác, tiếp tục cầu tinh vi tin công chúng hào, mở ra thông tin lục, click tăng thêm bạn mới, tìm tòi “Quét ngang thiên nhai”, click ở giữa tìm tòi “Quét ngang thiên nhai” tương quan công chúng hào, liền có thể nhìn thấy ta! Mọi người có thể chú ý một hồi, hiện tại đã bên trên truyền “Triệu Nhã”, “Vương Dĩnh” cùng “Mạc Hiểu” bức ảnh, mọi người hồi âm ba người tên là được. Ba cái tên này rất đơn giản, làm sao ta thấy có hồi phục “Quên ưng”, “Lưới nghiện” “Vương ảnh”... Mẹ nó, làm sao còn có hồi phục “Vọng ngữ” ? Ngươi xác định thật sự không đi sai gian phòng?)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-227"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-229"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);