(window.webpackJsonp=window.webpackJsonp||[]).push([[1439],{1794:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-lac-huyen-thanh-tieu-truyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-lac-huyen-thanh-tieu-truyen"}},[n._v("#")]),n._v(" Chương: Lạc Huyền Thanh tiểu truyện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Cuối cùng đi ra...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh mang theo Ngọc Phi Nhi cùng Hồ Yêu Yêu, tràn đầy chật vật nhìn phía sau Linh Cơ sơn, đồng thời thở dài một hơi."),t("br"),t("br"),n._v("Mặc dù đã đột phá Thần Linh, chân khí, linh hồn cũng kèm theo Trương Huyền lưu lại huyết dịch thoải mái, tiến bộ rõ ràng, nhưng đối với Thần giới tới nói, vẫn như cũ quá yếu."),t("br"),t("br"),n._v("Nghĩ đến trước đó chịu khổ đầu, liền tràn đầy cảm xúc cùng tâm tắc."),t("br"),t("br"),n._v("“Đây là Thần giới...” Than thở một tiếng, Lạc Huyền Thanh lắc đầu."),t("br"),t("br"),n._v("Thượng Thương, danh sư đại lục, hắn đều là siêu cấp thiên tài tồn tại, mặc dù không có vị kia như vậy ánh sáng chói mắt, nhưng cũng đủ để lưu danh sử sách."),t("br"),t("br"),n._v("Tu luyện chẳng qua mấy chục năm công phu, liền theo người bình thường tấn cấp đến Thần Linh, cho dù Thượng Thương trong lịch sử, Hàn Kiếm Thu tông chủ dạng này thiên tài, đều khó mà hoàn thành."),t("br"),t("br"),n._v("Nhưng là dạng này thiên tài, trong hốc núi rút căn củ cải, bị một cái thỏ liên tục truy sát ba ngày... Trong lúc đó còn bị sâu róm đánh lén, suýt chút nữa bị châu chấu dùng cánh cắt ra yết hầu, càng bị một gốc cỏ xanh đâm xuyên làn da, máu chảy đầy đất..."),t("br"),t("br"),n._v("Thần giới, quả nhiên không đơn giản, dù là một con côn trùng, một cái Thượng Thương ngoan ngoãn hiền lành động vật, đều không thể khinh thường."),t("br"),t("br"),n._v("May mắn... Sống sót đi ra!"),t("br"),t("br"),n._v("“Ba người chúng ta, đều đạt đến Thần Linh, trong núi đều nguy hiểm như vậy, suýt chút nữa tử vong, Trương Huyền lúc trước mang theo một đoàn liền Thần Linh đều không có đi đến người, đến cùng làm sao tại đây ngọn núi bên trên sống tiếp?”"),t("br"),t("br"),n._v("Hồ Yêu Yêu đột nhiên mở miệng."),t("br"),t("br"),n._v("Lạc Huyền Thanh, Ngọc Phi Nhi liếc mắt nhìn nhau, mỗi người lặng im."),t("br"),t("br"),n._v("Trương Huyền Thần giới hơn một tháng trước tới thời điểm, mang theo mười một vị đệ tử, cùng cha mẹ, Tôn Cường, làm mười bốn người, những này đều không có đi đến Thần Linh cảnh giới, nếu như đồng dạng giáng lâm tại đây ngọn núi lên, nguy hiểm khẳng định càng lớn!"),t("br"),t("br"),n._v("Kiến, gián, chuột, ruồi, muỗi... Trước kia những này tiện tay có thể lấy đập chết đồ vật, tại đây chút đều đủ để để hắn có thể chết người!"),t("br"),t("br"),n._v("“Khẳng định không đơn giản... Chúng ta theo trên núi đi ra, đều tiêu phí trọn vẹn bốn, năm ngày, hắn chỉ sợ cần càng lâu, nửa tháng, thậm chí một tháng...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh cảm xúc một câu, con mắt đột nhiên sáng lên, ngay sau đó xiết chặt: “Có lẽ, hắn hiện tại cũng vừa vừa rời đi sơn mạch, liền tại phụ cận thành trấn, tìm xem liền có thể gặp được, đến lúc đó, cùng một chỗ tiến bộ, ta ngược lại muốn xem xem... Tại một cái mới tinh môi trường, ai tu luyện càng nhanh, mạnh hơn!”"),t("br"),t("br"),n._v("Đối với vị này trước đó hảo hữu, hắn tình cảm là hết sức phức tạp."),t("br"),t("br"),n._v("Đã tức giận đối muội muội bắt nạt, lại đối hắn thiên phú và quyết đoán, cảm thấy khâm phục."),t("br"),t("br"),n._v("Nếu như không có đối phương mở rộng, đừng nói đi vào Thần giới, hắn hiện tại khả năng vẫn tại danh sư đại lục, liền Cổ Thánh cảnh giới, đều không thể đi đến."),t("br"),t("br"),n._v("Chính là bởi vì như vậy cảm giác, mới vẫn nghĩ muốn vượt qua đối phương, mạnh mẽ giáo huấn một lần, cho hắn biết, bắt nạt muội muội của hắn đánh đổi!"),t("br"),t("br"),n._v("Đáng tiếc, ý nghĩ này, tại danh sư đại lục, Thượng Thương đều thực hiện không được, có lẽ, chỉ có thể ở Thần giới thực hiện!"),t("br"),t("br"),n._v("“Mau nhìn, phía trước có một đám người...”"),t("br"),t("br"),n._v("Ba người một bên cảm xúc, một bên tiến lên, đột nhiên Ngọc Phi Nhi chỉ về phía trước."),t("br"),t("br"),n._v("Lạc Huyền Thanh nhìn về phía trước, quả nhiên thấy mấy cái học sinh trang phục người, chậm rãi hướng núi rừng đi tới, những người này, đều chỉ là Ngụy Thần cảnh giới, thoạt nhìn khoảng cách Thần Linh còn cách một đoạn."),t("br"),t("br"),n._v("“Liền Thần Linh đều không có đi đến, liền lên núi... Chẳng lẽ bọn họ muốn chết hay sao?”"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ liếc mắt nhìn nhau, Lạc Huyền Thanh kìm nén không được, sải bước đi đi qua."),t("br"),t("br"),n._v("Thấy có người tới, mấy cái học sinh đầu tiên là cảnh giác, trò chuyện một hồi, trầm tĩnh lại."),t("br"),t("br"),n._v("“Cách nơi này thành thị gần nhất, gọi Dạ Minh thành, thành chủ Ngô Phương Thanh chính là Thiên Thần cường giả, tung hoành vô địch, cho nên, đến nội thành, nhất định đừng làm loạn, nếu không, sẽ bị đưa vào toà án, chỗ lấy trừng phạt!”"),t("br"),t("br"),n._v("Nghe nói bọn họ quanh năm cư trú núi rừng, chưa hề từng tiến vào thành trấn, một vị hảo tâm học sinh chỉ điểm nói."),t("br"),t("br"),n._v("Từ lúc linh khí suy yếu về sau, Thần giới tất cả thành thị, đều chú ý quy củ, thành chủ địa vị chí cao vô thượng, thật sợ đám này “Nhiệt tình” “Người hoang dã”, không hiểu quy củ mà nhận xử phạt."),t("br"),t("br"),n._v("“Thiên Thần cường giả?”"),t("br"),t("br"),n._v("Lạc Huyền Thanh nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Vừa tới đến Thần giới, lần thứ nhất gặp được sinh mệnh, chỉ biết là Thần Linh, Thiên Thần cái từ này, còn là lần đầu tiên nghe nói."),t("br"),t("br"),n._v("“Đúng vậy a, là một loại cao cao tại thượng siêu tuyệt nhân vật! Thần Linh, chia làm hạ phẩm, trung phẩm, thượng phẩm ba cái cấp bậc, đột phá thượng phẩm, chính là Thiên Thần, đồng dạng chia làm hạ phẩm, trung phẩm, thượng phẩm! Mỗi một cái đi đến Thiên Thần, Đô Thiên trao tuyệt đỉnh, vạn người chưa chắc có được một, tại bất luận cái gì một chỗ, đều được cho siêu cấp cường giả, có thể độc chiếm một phương!”"),t("br"),t("br"),n._v("Người học sinh này trong mắt tràn đầy hâm mộ: “Không nói Thiên Thần, coi như có thể đi đến thượng phẩm Thần Linh, tại Dạ Minh thành, cũng có thể đủ xây dựng gia tộc, dương danh lập vạn!”"),t("br"),t("br"),n._v("“Thượng phẩm Thần Linh, Thiên Thần?”"),t("br"),t("br"),n._v("Lạc Huyền Thanh kích động nắm đấm xiết chặt, trong lòng mong mỏi."),t("br"),t("br"),n._v("Đi tới Thần giới thời gian, tuy là rất ngắn, nhưng cũng đã phát hiện, Thần giới mỗi một cái cấp bậc, đều như là lạch trời đồng dạng, khó mà vượt quá, không nói mặt khác, hạ phẩm Thiên Thần, tấn cấp đến cảnh giới tiếp theo, cho dù tài nguyên đầy đủ, không có mấy năm, hơn mười năm khổ tu, cũng rất khó hoàn thành."),t("br"),t("br"),n._v("“Đúng vậy a, Thiên Thần cùng thượng phẩm Thiên Thần, ta không dám nghĩ, đời này mục tiêu lớn nhất chính là trở thành chân chính Thần Linh, sau khi tốt nghiệp, tìm công việc tốt, đi một ít mọi người làm hoặc là phủ thành chủ làm hộ vệ... Cầm không thấp tiền lương, cưới bạch phú mỹ, đi đến nhân sinh đỉnh phong...”"),t("br"),t("br"),n._v("Người học sinh này nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Nhân sinh cần kế hoạch, đây chính là hắn khát vọng nhất."),t("br"),t("br"),n._v("Thiên Thần, không phải người nào cũng có thể làm đến, hắn học tập, tu vi ở trường học chỉ tính được trung hạ, không lớn như vậy hy vọng xa vời, chỉ cầu có thể thuận lợi tốt nghiệp, tìm một phần có cao thu nhập tiền lương, cũng theo đó sinh không tiếc."),t("br"),t("br"),n._v("“Làm hộ vệ tốt... Ta đến nội thành, cũng muốn làm hộ vệ, kiếm tiền lương, cố gắng tu luyện...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh ánh mắt kiên định."),t("br"),t("br"),n._v("Vạn sự khởi đầu nan, bây giờ tại Thần giới, bọn họ nghèo rớt mùng tơi, chỉ cần tìm được công việc, thật tốt cố gắng, tu vi tất nhiên sẽ cấp tốc tăng thêm..."),t("br"),t("br"),n._v("Một khi cơ duyên đến, có thể đột phá trung phẩm Thần Linh, thậm chí thượng phẩm... Trương Huyền lại có thể mạnh hơn chính mình ở đâu?"),t("br"),t("br"),n._v("Lại nói, lấy cái kia loại rắm thúi tính tình, thích trang bức tính cách, tương đương hộ vệ, người ta chưa hẳn có thể muốn đây... Cẩn thận nói đến, ưu thế của mình còn là rất lớn!"),t("br"),t("br"),n._v("“Hỏi các ngươi sự kiện, ta có người bằng hữu, đại khái hơn một tháng trước, cùng chúng ta trong núi lạc đường... Đoán không sai, cũng đã đi các ngươi nói Dạ Minh thành, không biết các ngươi nghe nói qua không...”"),t("br"),t("br"),n._v("Ngọc Phi Nhi đột nhiên mở miệng."),t("br"),t("br"),n._v("“Dạ Minh thành như vậy lớn, mỗi ngày lui tới mấy triệu người, cũng không phải là người nào, chúng ta đều nghe qua...” Học sinh khoát tay áo, khẽ nói."),t("br"),t("br"),n._v("“Này ngược lại là... Bất quá, ta người bạn này, thiên phú cũng không tệ lắm, cũng có chút ngạo khí, đi tới nơi nào, đều ưa thích làm một ít chuyện, có lẽ... Đã làm gì trái với lệ thường chuyện...”"),t("br"),t("br"),n._v("Dừng lại một chút, Ngọc Phi Nhi nói."),t("br"),t("br"),n._v("Trương Huyền mới so với bọn hắn đến sớm Thần giới một tháng kế tiếp, tuy là dưới cái nhìn của nàng, để người khác biết đến khả năng không lớn, nhưng vẫn như cũ ôm hi vọng."),t("br"),n._v("Dù sao, quá lâu không thấy đối phương, trong lòng tưởng niệm, kèm theo thời gian dời đổi, càng ngày càng diếu càng nhiều, quá cấp thiết muốn biết tin tức, dù là chỉ có một chút xíu..."),t("br"),t("br"),n._v("“Làm ra trái với lệ thường? Các ngươi cũng quá đem cái này bằng hữu coi là chuyện đáng kể... Nói thật cho các ngươi biết, tại Dạ Minh thành, là rồng muốn cuộn lại, là hổ muốn nằm lấy... Nếu không, Ngô thành chủ vài phút dạy ngươi đối nhân xử thế...” Học sinh cười nhạo một tiếng."),t("br"),t("br"),n._v("Một đám chưa hề xảy ra sơn thổ dân mà thôi, không biết cơ duyên gì trùng hợp đột phá Thần Linh, vậy mà cho rằng trong thành có thể gây ra động tĩnh..."),t("br"),t("br"),n._v("Thật sự là vô tri đáng sợ!"),t("br"),t("br"),n._v("“Tốt, Ngọc Phi Nhi, tâm tình của ngươi ta có thể lý giải, tuy là tên kia tại Thượng Thương, danh sư đại lục huyên náo động tĩnh không nhỏ, nhưng nơi này là Thần giới, cường giả vô số, không phải là cái gì người cũng có thể gây ra động tĩnh, để cho người ta biết được...”"),t("br"),t("br"),n._v("Thấy trước mắt cô bé này, bộ dáng này, Lạc Huyền Thanh hừ một tiếng."),t("br"),t("br"),n._v("Cái loại này chó tính tình, đều có người ưa thích, không tiếc ra sức, bản thân thiện lương như vậy, như vậy suất khí, vậy mà một cái hồng nhan đều không có... Thật mẹ nó không có thiên lý!"),t("br"),t("br"),n._v("“Ta biết...” Ngọc Phi Nhi cười khổ một tiếng, cũng cảm thấy bản thân lỗ mãng."),t("br"),t("br"),n._v("Trương sư, cứ việc thiên tài, nhưng tại Thần giới, ngắn ngủi hơn một tháng, lại có thể lật lên dạng gì bọt sóng?"),t("br"),t("br"),n._v("“Tốt, đừng rầu rĩ, Trương Huyền tên kia, từ trước tới nay không bản phận, coi như hiện tại không có động tĩnh, về sau cũng sẽ nghe được tin tức, chúng ta bây giờ cần phải làm là an tâm tu luyện, chờ nhìn thấy hắn thời điểm, để hắn giật nảy cả mình...”"),t("br"),t("br"),n._v("Chuyển biến tốt bạn bộ dạng, Hồ Yêu Yêu an ủi."),t("br"),t("br"),n._v("“Này ngược lại là...”"),t("br"),t("br"),n._v("Ngọc Phi Nhi gật đầu, đang muốn từ bỏ tìm hiểu suy nghĩ, chỉ thấy trước mắt mấy vị học sinh, tất cả đều mở to hai mắt nhìn, lời mới vừa nói thiếu niên, càng là răng run lên: “Các ngươi mới vừa nói ai? Trương, Trương Huyền tiền bối? Tuổi còn trẻ, chừng hai mươi tuổi, hơn một tháng trước đột nhiên xuất hiện Trương Huyền?”"),t("br"),t("br"),n._v("“Trương Huyền tiền bối?”"),t("br"),t("br"),n._v("Ba người nhìn nhau, Lạc Huyền Thanh sửng sốt một chút, nghi ngờ nhìn qua: “Đúng vậy a, làm sao ngươi nghe qua cái tên này?”"),t("br"),t("br"),n._v("“Đương nhiên nghe nói qua...” Người học sinh này thân thể run rẩy: “Vị này Trương Huyền, nhưng mà thần tượng của ta, hơn một tháng trước chính là trong núi xuất hiện, lúc ấy gặp Mặc Vân lão sư cùng Ngô Tiêu Tiêu học tỷ bọn họ! Đi vào Dạ Minh thành về sau, mấy ngày ngắn ngủi công phu, liền danh tiếng vang xa, thời gian vài ngày, đã đột phá Thiên Thần cảnh giới, hiện tại càng là Thần Vương chi cảnh, nghe nói, liền phong hào Thần Vương đều tự tay chém giết qua...”"),t("br"),t("br"),n._v("Trương Huyền thuốc, tại U Hồn thiên danh khí rất lớn, các nơi truyền thuyết sớm đã đem hắn khoa trương thành thần thoại."),t("br"),t("br"),n._v("“Mấy ngày đột phá Thiên Thần?” Lạc Huyền Thanh ngẩn ngơ: “Thần Vương chi cảnh? Thần Vương là cái gì? Phong hào Thần Vương đâu?”"),t("br"),t("br"),n._v("“Thần Vương bao trùm tại thượng phẩm Thiên Thần phía trên, có thể nói là đương đại người mạnh nhất... Còn phong hào Thần Vương, là Đế Quân tự thân sắc phong Thần Vương, loại trừ chín đại Đế Quân, không người có thể địch...” Người học sinh này trong mắt tràn đầy hừng hực."),t("br"),t("br"),n._v("Trương Huyền chém giết Bách Diệp Thanh Hồng sự tình, truyền tới, U Hồn thiên thế hệ trẻ tuổi người, sớm đã đem hắn trở thành sùng bái thần tượng."),t("br"),t("br"),n._v("“So với Thượng phẩm Thiên Thần còn lợi hại hơn? Cái kia... Đế Quân lại là cái gì?”"),t("br"),t("br"),n._v("Lạc Huyền Thanh da mặt co rúm."),t("br"),t("br"),n._v("“Thần giới chưởng khống giả, Thần giới Cửu Thiên, hết thảy chỉ có chín vị Đế Quân, có Cửu Thiên bắt đầu, bọn họ liền xuất hiện, không phải tu luyện có thể làm được, chân chính chí cao vô thượng...” Học sinh giải thích."),t("br"),t("br"),n._v("Tiếng nói còn không có kết thúc, đột nhiên cảm thấy mặt đất một hồi lay động, ngay sau đó một cái thanh âm nhàn nhạt, trong đầu vang lên."),t("br"),t("br"),n._v("“Hôm nay, ta làm Đế Quân!”"),t("br"),t("br"),n._v("Kèm theo câu nói này, ngay sau đó đám người trong đầu bất ngờ xuất hiện một cái nhàn nhạt thân ảnh, ngạo nghễ đứng thẳng trên không, trong hai mắt ẩn chứa nồng đậm uy nghiêm."),t("br"),t("br"),n._v("“Trương Huyền...”"),t("br"),t("br"),n._v("“Hắn thành Đế Quân? Không phải nói Đế Quân, thiên địa sinh ra, chí cao vô thượng, không phải tu luyện có thể làm được ư?”"),t("br"),t("br"),n._v("Lạc Huyền Thanh thân thể vụt qua, mắt tối sầm lại."),t("br"),t("br"),n._v("Làm cái gì?"),t("br"),t("br"),n._v("Ta mẹ nó mới từ thỏ cùng châu chấu phía trước chạy trốn thành công, cho rằng có thể đuổi kịp ngươi... Ngươi lại cho ta nói, biến thành Đế Quân..."),t("br"),t("br"),n._v("Không mang theo chơi như vậy..."),t("br"),t("br"),n._v("Trái tim thật đau..."),t("br"),t("br"),n._v("Làm sao đột nhiên cảm thấy hô hấp không đến..."),t("br"),t("br"),n._v("Không chỉ có là hắn, một bên Hồ Yêu Yêu, Ngọc Phi Nhi cũng mở to hai mắt nhìn."),t("br"),t("br"),n._v("Trước đó liền suy đoán, cái tên này sẽ gây ra động tĩnh, không nghĩ tới náo như vậy lớn..."),t("br"),t("br"),n._v("Tới Thần giới một tháng kế tiếp, các nàng mới hạ phẩm Thần Linh, người ta đã Đế Quân..."),t("br"),t("br"),n._v("Bên cạnh rất nhiều học sinh, cũng hiển nhiên không nghĩ tới điểm ấy, tất cả đều choáng váng đồng dạng."),t("br"),t("br"),n._v("Bất quá, ngốc thời gian không dài, lại một thân ảnh bất ngờ xuất hiện tại trong óc, âm thanh đồng thời vang lên."),t("br"),t("br"),n._v("“Vậy mà để ngươi đột phá trước... Còn để cho ta làm sao trang bức? Từ hôm nay, ta mẹ nó cũng là Đế Quân!”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Đám người nhìn nhau, mỗi người sửng sốt."),t("br"),t("br"),n._v("Hồ Yêu Yêu, Ngọc Phi Nhi càng là trợn mắt hốc mồm..."),t("br"),t("br"),n._v("Ý là..."),t("br"),t("br"),n._v("Không chỉ hắn thành Đế Quân, còn liền phân thân một nhanh làm được chứ?"),t("br"),t("br"),n._v("Tràn đầy khiếp sợ, quay đầu chỉ thấy Lạc Huyền Thanh nước mắt chảy ra đến, không ngừng nức nở..."),t("br"),t("br"),n._v("Ma đản... Coi như không muốn để cho ta tìm ngươi phiền phức, cũng không đến mức tiến bộ nhanh như vậy đi..."),t("br"),t("br"),n._v("Ngươi đều Đế Quân, ta mẹ nó vẫn còn đang đánh thỏ, đánh châu chấu... Cái này còn thế nào đuổi, đánh như thế nào?"),t("br"),t("br"),n._v("Ta Lạc Huyền Thanh... Cũng là thiên tài..."),t("br"),t("br"),n._v("(Cái này phiên ngoại một, tại công chúng số đã sớm phát, hôm nay phát  mặt khác, lão nhai công chúng số, bán ra bản thân đại ngôn bít tất, báo lão nhai tên, có thể đánh gãy, nửa mua nửa tặng, mọi người có thể mua sắm thoáng cái, chất lượng cũng không tệ lắm. Mùa đông đến, để lão nhai tới che chở chân của các ngươi... Yên tâm, nhổ thời gian lâu dài, liền sẽ thói quen. Đi khắp chân trời, quét ngang Cửu châu, đem chân trời góc biển giẫm tại dưới chân, trải nghiệm vượt qua người ta một bậc cảm giác... Giờ khắc này, ngươi đáng giá nắm giữ..."),t("br"),t("br"),n._v("Còn sách mới, đã bắt đầu chuẩn bị, cùng chủ biên đã quyết định tên sách, ngay tại thiết lập đại cương.. Khà khà. Cuối cùng, lão nhai đăng kí cái TikTok số, liền thách đấu quét chân trời, có lão nhai ảnh chụp cùng giới thiệu vắn tắt, chỉ là còn không có chứng nhận, hai ngày này phát một cái chứng nhận trong video đi, phiền phức mọi người hỗ trợ chút hồng tâm, thật giống cần bao nhiêu hồng tâm mới có thể chứng nhận thành công. Chứng nhận tốt, mở trực tiếp, công bố sách mới một ít tin tức, cùng với thu thập diễn viên quần chúng.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2280"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2282"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);