(window.webpackJsonp=window.webpackJsonp||[]).push([[1441],{1796:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-ton-cuong-ngoai-truyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-ton-cuong-ngoai-truyen"}},[n._v("#")]),n._v(" Chương: Tôn Cường ngoại truyện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("(Sách mới tháng sau số 11 xuất ra đầu tiên khởi đầu, hai ngày này sẽ ở công chúng hào, truyền lên một ít sách mới tin tức. Hi vọng đại gia chú ý. Bản này, công chúng hào trước đó cũng phát qua, nhìn qua có thể không cần nhìn, đồng dạng miễn phí.)"),t("br"),t("br"),n._v("“Đây mới là Thần giới chói mắt nhất quyến lữ...”"),t("br"),t("br"),n._v("Nhớ lại Trương Huyền mang theo Nhiếp Linh Tê cùng Lạc Thất Thất, đạp trời mà đi cảnh tượng, Linh Lung tiên tử liền từng cơn hâm mộ."),t("br"),t("br"),n._v("Vừa ra đời bất phàm, chú định ánh mắt của nàng cũng không bình thường, cho tới nay, đều hy vọng có thể tìm tới một vị, vượt qua bản thân, đem bản thân hoàn toàn nghiền ép cường giả, cuối cùng đi cùng một chỗ."),t("br"),t("br"),n._v("Đáng tiếc, rất nhiều Đại Đế, đều tới không kém nhiều, thậm chí hơi có không bằng, cứ việc cũng có người biểu đạt qua yêu thương, đều không có thấy vừa mắt."),t("br"),t("br"),n._v("Không phải thanh cao, mà là nàng muốn theo đuổi phàm phu tục tử cái loại này lãng mạn tình yêu, Thần giới cường giả đỉnh cao, đều không làm được."),t("br"),t("br"),n._v("Vân Ly Đại Đế, Bất Tử Đế Quân, Phù Mãnh Đế Quân, Huyền Minh Đại Đế, Càn Khôn Ma Đế, Chước Dương Đại Đế..."),t("br"),t("br"),n._v("Đối bọn hắn hiểu rất rõ, chính là bởi vì tìm hiểu, mới không có hứng thú."),t("br"),t("br"),n._v("Vân Ly Đại Đế phong lưu thành tính, hậu thế trải rộng chư thiên; Bất Tử Đế Quân ngày ngày nghĩ đến tự sát, một bộ sinh không thể yêu, đối với nữ nhân không có hứng thú; Phù Mãnh Đế Quân thô cuồng nôn nóng, kiên nhẫn không đủ; Càn Khôn Ma Đế âm nhu thành tính, vì nàng không thích; Chước Dương Đại Đế, không câu nệ tiểu tiết, vì người phóng đãng ngỗ ngược, càng không phải là lý tưởng loại hình..."),t("br"),t("br"),n._v("Còn còn lại mấy vị, Kiếm Thần thiên vị kia, ai cũng không để ý tới, Thiên Túng Đại Đế thanh tâm quả dục, Tự Tại thiên là nữ tử..."),t("br"),t("br"),n._v("Cùng cấp bậc, vậy mà không một người có thể để mắt, nguyên nhân chính là như vậy, mới một mực độc thân, cô độc đến bây giờ."),t("br"),t("br"),n._v("Nhưng... Cô gái nào không hoài xuân?"),t("br"),t("br"),n._v("Cô gái nào không hy vọng có cái siêu cấp cường giả, làm hậu thuẫn của mình, chống đỡ tương lai mình thời gian?"),t("br"),t("br"),n._v("Nàng cứ việc là cao quý Đại Đế, cũng làm không được thanh tâm quả dục, vô dục vô cầu, vì thế, thành đêm mất ngủ, ngủ không mạnh khỏe."),t("br"),t("br"),n._v("Một mảnh trái tim, không chỗ ký thác."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, nhìn thấy Trương Huyền đám người, hữu tình người cuối cùng xông thân thuộc, mới phát giác được hâm mộ."),t("br"),t("br"),n._v("Đáng tiếc... Cuối cùng không phải là của mình."),t("br"),t("br"),n._v("Đem ý nghĩ thế này áp chế xuống, đi tại Linh Lung hoàng thành trên đường phố rộng rãi."),t("br"),t("br"),n._v("Trương Huyền cùng Linh Tê Đế Tôn đại hôn đã qua hơn một tháng, nhưng nhiệt lượng thừa vẫn không có biến mất, vẫn như cũ là vô số người, tranh nhau đối tượng bàn luận."),t("br"),t("br"),n._v("Nghe một hồi, đi vào một nhà rộng lớn quán rượu."),t("br"),t("br"),n._v("Nàng đã đem khuôn mặt cùng thực lực làm ngụy trang, không có người nhận ra được."),t("br"),t("br"),n._v("Đế Quân cũng không phải một mực đợi tại cao cao tại thượng cung điện, nhiều khi, cũng sẽ huyễn hóa thành phàm nhân, trải nghiệm phàm nhân hỉ nộ ái ố, nhân sinh muôn màu."),t("br"),t("br"),n._v("Yêu cầu một ít thịt rượu, ngồi ở chỗ gần cửa sổ tinh tế thưởng thức."),t("br"),t("br"),n._v("Ngay tại tâm cảnh thả lỏng, cảm thấy vừa mới tỉnh lại thời điểm, trong tửu lâu, một đám người đi vào."),t("br"),t("br"),n._v("“Chính là hắn!”"),t("br"),t("br"),n._v("Kèm theo một cái đè thấp âm thanh, mấy chục người ánh mắt khí đồng loạt tập trung ở cách mình cách đó không xa một người trung niên trên người."),t("br"),t("br"),n._v("Là người mập mạp, hình thể thoạt nhìn có chút to mập, bởi vì đưa lưng về phía bản thân, thấy không rõ dung mạo."),t("br"),t("br"),n._v("Dường như cũng không biết những người này ở đây nhìn hắn, mà là bưng chén rượu lên, từ từ thưởng thức."),t("br"),t("br"),n._v("Trên bàn một phần thịt kho tàu cùng một cái chân giò muối, ăn miệng đầy dầu mỡ."),t("br"),t("br"),n._v("“Vây quanh...”"),t("br"),t("br"),n._v("Xác định người, trước một thủ lĩnh bộ dáng trung niên nhân bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Mấy chục cái người mặc thống nhất trang phục người liền đem to mập trung niên nhân vây quanh ở trung tâm, còn không có động thủ, dĩ nhiên đã đằng đằng sát khí, nhìn bốn phía tất cả mọi người kinh hồn bạt vía."),t("br"),t("br"),n._v("“Các vị bằng hữu, tại hạ có một số việc cần xử lý, mong rằng chư vị có thể trước về tránh thoáng cái, hôm nay thịt rượu, toàn bộ tính tới trên đầu ta...”"),t("br"),t("br"),n._v("Thủ lĩnh bộ dáng trung niên nhân cung kính chờ đợi ôm quyền, nhìn quanh một tuần."),t("br"),t("br"),n._v("“Là là, chúng ta lập tức liền đi...”"),t("br"),t("br"),n._v("“Là Thượng Quan gia người!”"),t("br"),t("br"),n._v("“Thượng Quan gia?”"),t("br"),t("br"),n._v("“Bách Diệp Thanh Hồng phong hào Thần Vương bị giết về sau, Thượng Quan Vân Uyển phong hào, một nhà độc đại, gia tộc thế lực bạo tăng, đã là Linh Lung hoàng thành đệ nhất, ngàn vạn không thể đắc tội...”"),t("br"),t("br"),n._v("“Hoàng thành đệ nhất gia tộc? Vậy cái này mập mạp là ai? Tại sao lại muốn tới bắt hắn?”"),t("br"),t("br"),n._v("“Ta đây cũng không biết, cũng không đáng chúng ta biết, đi nhanh đi...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lặng lẽ tiếng nghị luận bên trong, trong tửu điếm người, vội vã trốn ra phía ngoài đi."),t("br"),t("br"),n._v("Việc không liên quan đến mình treo lên thật cao, huống chi, quan hệ hoàng thành đệ nhất thế gia."),t("br"),t("br"),n._v("Rất nhanh, lớn như vậy trong tửu lâu chỉ còn lại có mấy chục người cùng mập mạp, cùng với ngồi tại bên cửa sổ Linh Lung tiên tử."),t("br"),t("br"),n._v("Nàng tâm tình không tốt, thật vất vả tìm được nghỉ ngơi địa phương, đương nhiên sẽ không rời đi."),t("br"),t("br"),n._v("“Vị bằng hữu này, chúng ta Thượng Quan gia có chuyện xử lý, mong rằng có thể rời đi, nếu không tay chân không có mắt, bị làm bị thương liền được không bù mất...”"),t("br"),t("br"),n._v("Thấy nàng không rời đi, thủ lĩnh bộ dáng trung niên nhân nhướng mày."),t("br"),t("br"),n._v("“Các ngươi vội vàng các ngươi, ta chỉ là đi ngang qua...” Bưng lên bầu rượu, Linh Lung tiên tử rót một chén rượu, thản nhiên nói, lời còn chưa dứt, liền cảm thấy thấy hoa mắt, cách đó không xa mập mạp đã ngồi ở đối diện, cầm một cái chân giò muối đưa qua, cười nhẹ nhàng nói: “Tiểu cô nương, ngươi hay là đi thôi, bọn họ tìm ta có chút phiền phức, làm không cẩn thận sẽ động thủ, đem ngươi ngộ thương liền không tốt lắm...”"),t("br"),t("br"),n._v("Đi tới đối diện mới nhìn rõ ràng, cái tên mập mạp này mặt mũi ôn hoà, tuy là không phải rất tuấn tú, nhưng lại có đặc biệt mị lực."),t("br"),t("br"),n._v("“Bọn họ là Thượng Quan gia người, ngươi không sợ?”"),t("br"),t("br"),n._v("Khoát tay áo, cũng không có nhận chân giò, Linh Lung tiên tử có chút hiếu kỳ."),t("br"),t("br"),n._v("Lấy nàng nhãn lực, tự nhiên đó có thể thấy được cái tên mập mạp này tu vi, chẳng qua thượng phẩm đỉnh phong Thần Linh mà thôi, mặt Thượng Quan gia vô số cường giả, vậy mà không có chút nào sợ hãi, còn để nàng rời đi, phần này tâm tính, không khỏi quá cường đại đi."),t("br"),t("br"),n._v("“Sợ? Có gì có thể sợ?”"),t("br"),t("br"),n._v("Mập mạp ánh mắt lộ ra vẻ ngạo nhiên: “Một đám gà đất chó sành mà thôi!”"),t("br"),t("br"),n._v("Thấy đối phương là thật không sợ hãi chút nào, Linh Lung tiên tử càng thêm tò mò."),t("br"),t("br"),n._v("Thượng Quan Vân Uyển, là nàng sắc phong phong hào Thần Vương, tuy là chưa lập gia đình, nhưng có bảy vị đệ đệ, nhất mạch truyền thừa, gia tộc lớn, Thần giới đều được cho nổi danh."),t("br"),t("br"),n._v("Trước mắt cái tên mập mạp này, chỉ là cái thượng phẩm Thần Linh, liền không sợ hãi, dũng khí đến cùng từ đâu mà đến?"),t("br"),t("br"),n._v("“Ngươi tự tìm cái chết...”"),t("br"),t("br"),n._v("Nghe được lời của hắn, thủ lĩnh trung niên nhân, lông mày nhảy dựng, ánh mắt lộ ra tiêu sát chi ý."),t("br"),t("br"),n._v("“Vị tiểu cô nương này, ta trước đó cũng không quen biết, cùng ta cũng không việc gì, các ngươi tìm ta có thể, không cần làm khó nàng...”"),t("br"),t("br"),n._v("Biết động thủ sắp đến, mập mạp đem chân giò đặt lên bàn, vung một cái ống tay áo, đứng lên, hai tay chắp sau lưng, vẻ mặt lãnh đạm."),t("br"),t("br"),n._v("Thấy đối phương nguy hiểm sắp đến, lại còn tại bảo hộ chính mình, Linh Lung tiên tử ánh mắt lộ ra khen ngợi chi sắc."),t("br"),t("br"),n._v("Trước mặc kệ đúng sai, chí ít vị này phong thái, cũng không phải là Thượng Quan gia tộc những người này, có thể so sánh được."),t("br"),n._v("Tư thế hiên ngang, làm người ta say mê."),t("br"),t("br"),n._v("Chí ít sống nhiều như vậy vạn năm, lần thứ nhất nhìn thấy."),t("br"),t("br"),n._v("“Hừ, chúng ta vốn tìm cũng là ngươi, động thủ!” Thủ lĩnh quát lạnh một tiếng, vây chung quanh thuộc hạ, đồng thời động thủ."),t("br"),t("br"),n._v("Trong tích tắc, kiếm khí nhanh chóng bắn mà đến, đem hắn bao phủ."),t("br"),t("br"),n._v("Mập mạp trên mặt không có chút nào bối rối, ngược lại cười nhạt một tiếng, thân thể mập mạp, tựa như xuyên qua bụi hoa hồ điệp, đủ loại kiếm chiêu, vây công, vậy mà xuống dốc ở trên người nửa phần."),t("br"),t("br"),n._v("Bành bành bành bành!"),t("br"),t("br"),n._v("Liên tiếp kêu rên, hơn mười vị trung phẩm Thần Linh toàn bộ nằm trên mặt đất, từng cái bị trọng thương."),t("br"),t("br"),n._v("“Thật mạnh chiến đấu lĩnh ngộ...”"),t("br"),t("br"),n._v("Linh Lung tiên tử âm thầm gật đầu."),t("br"),t("br"),n._v("Lấy nàng nhãn lực, tự nhiên đó có thể thấy được hai bên giao chiến chân thực tình huống."),t("br"),t("br"),n._v("Vị này mập mạp thực lực, là thượng phẩm đỉnh phong Thần Linh, bình thường không có gì lạ, nhưng có thể trong khoảng thời gian ngắn, đem nhiều như vậy trung phẩm Thần Linh đánh bại, dựa vào là cũng không phải là lực lượng hùng hậu, mà là đối với chiến đấu lĩnh ngộ."),t("br"),t("br"),n._v("Hắn thật giống có thể nhìn ra tất cả mọi người tấn công thiếu hụt, trước thời hạn làm ra dự phán, hơn nữa xuất chiêu ứng đối."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, nhiều người như vậy đồng thời tấn công, đều không có làm bị thương mảy may, ngược lại bị nhẹ nhõm đánh bại."),t("br"),t("br"),n._v("“Đừng vội ngông cuồng!”"),t("br"),t("br"),n._v("Thủ lĩnh thấy thuộc hạ nhanh như vậy bị đánh bại, biến sắc, đi tới trước mặt."),t("br"),t("br"),n._v("Hắn vừa thi triển lực lượng, lập tức hiện ra tu vi, nửa bước Thần Vương!"),t("br"),t("br"),n._v("Đơn thuần tu vi cùng lực lượng lời nói, vượt xa quá trước mắt vị này mập mạp, hơn nữa chiêu số tinh diệu, vừa nhìn liền biết đạt được Thượng Quan gia tộc hạch tâm truyền thừa, địa vị không thấp."),t("br"),t("br"),n._v("Hô hô!"),t("br"),t("br"),n._v("Kiếm khí chứa tiêu, ngân mang nổ bắn ra, lực lượng tuyệt đối phía trước, mập mạp tuy là đối với chiến đấu hiểu rất cao, vẫn như cũ có chút ngăn cản không nổi, liên tục bại lui."),t("br"),t("br"),n._v("“Cẩn thận...”"),t("br"),t("br"),n._v("Linh Lung tiên tử nhíu nhíu mày."),t("br"),t("br"),n._v("Cái tên mập mạp này cho nàng ấn tượng rất tốt, thấy cảnh này, tự nhiên không nguyện để hắn bị thương."),t("br"),t("br"),n._v("“Không cần lo lắng, ta nếu nói qua, gà đất chó sành, chính là gà đất chó sành!”"),t("br"),t("br"),n._v("Cười ha ha một tiếng, mập mạp đột nhiên lui về phía sau một bước, lần nữa cầm lấy trên bàn chân giò muối, ăn một miếng, ánh mắt lộ ra ngạo nghễ vạn vật khí thế: “Để cho ta nhìn một chút ngươi, rốt cuộc mạnh cỡ nào!”"),t("br"),t("br"),n._v("Nói xong hướng về phía trước một bước, dày nặng thân thể, như núi cao biển rộng."),t("br"),t("br"),n._v("“Đây mới là... Anh hùng khí khái!”"),t("br"),t("br"),n._v("Linh Lung tiên tử con mắt sáng lên, không hề bận tâm tâm cảnh, nhịn không được bị xúc động thoáng cái."),t("br"),t("br"),n._v("Trước đó vẫn cho là, sẽ không còn được gặp lại anh hùng, không thấy được để nàng ngưỡng mộ trong lòng nam tử, không nghĩ tới cái này dung mạo bình thường không có gì lạ mập mạp, đối mặt thực lực cao hơn hắn người, vẫn là như thế vững vàng, như vậy có quyết đoán."),t("br"),t("br"),n._v("Phần tự tin này, tuyệt không phải người bình thường có thể làm được."),t("br"),t("br"),n._v("Bị mập mạp khí thế áp bức, thủ lĩnh có chút sợ hãi, cuối cùng vẫn cắn răng, lần nữa lao đến."),t("br"),t("br"),n._v("Bành bành bành!"),t("br"),t("br"),n._v("Liên tục mấy chiêu, một mặt tự tin mập mạp, bị đánh liên tiếp lui về phía sau."),t("br"),t("br"),n._v("Tự tin, không thể thay đổi thực lực chưa đủ thế yếu."),t("br"),t("br"),n._v("“Làm càn, dám động thủ với ta, ngươi có biết ta là ai không?”"),t("br"),t("br"),n._v("Liên tục lui về phía sau mấy bước sau đó, mập mạp ngừng lại, lộ ra vẻ ngạo nhiên."),t("br"),t("br"),n._v("“Ngươi là ai?”"),t("br"),t("br"),n._v("Gặp hắn bộ dáng này, thủ lĩnh sửng sốt một chút, nhịn không được dừng lại."),t("br"),t("br"),n._v("Cứ việc Thượng Quan gia rất cường đại, cũng là có không ít người không dám đắc tội, liền giống như lúc trước Bách Diệp Thanh Hồng, một chiêu làm sai, toàn cả gia tộc lập tức biến mất."),t("br"),t("br"),n._v("“Ta chính là thiên địa quản gia...”"),t("br"),t("br"),n._v("Ngạo nghễ mà đứng, mập mạp cả người giống như là đứng ở thiên địa bên ngoài, bỗng nhiên hướng về phía trước: “Ngươi động thủ với ta, chính là đối Thần giới động thủ, đối với thiên địa bất mãn, phản kháng thiên địa, tự tìm cái chết!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lòng bàn tay một cái ngọc bài bất ngờ mà ra, càng kéo càng lớn, tỏa ra dày đặc uy nghiêm, tựa như thiên đạo, bất cứ lúc nào cũng sẽ hàng lâm xuống."),t("br"),t("br"),n._v("“Đây là... Trương Huyền Đế Tôn khí tức?”"),t("br"),t("br"),n._v("Linh Lung tiên tử sững sờ."),t("br"),t("br"),n._v("Ngọc bài bên trong ẩn chứa khí tức, nàng rất rõ ràng, không phải người khác, chính là vị kia Trương Huyền đặc thù."),t("br"),t("br"),n._v("Chẳng lẽ cái này để nàng hơi ngưỡng mộ trong lòng mập mạp, cùng Trương Huyền liên quan đến?"),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Ngay tại kỳ lạ, tế ra ngọc bài mập mạp, chiếm cứ thượng phong, đem thủ lĩnh đánh thành trọng thương, lúc này mới lắc đầu thở dài, tràn đầy nhẹ như mây gió: “Quả nhiên không đỡ nổi một đòn...”"),t("br"),t("br"),n._v("Nói xong bỏ xuống chân giò, hai tay chắp sau lưng, xoay người đi ra ngoài, không mang đi một áng mây."),t("br"),t("br"),n._v("“Vị bằng hữu này, còn xin dừng bước...”"),t("br"),t("br"),n._v("Thấy có người vậy mà như thế tiêu sái, Linh Lung tiên tử cũng nhịn không được nữa, đứng dậy, nhịn không được nhìn qua: “Không biết bằng hữu xưng hô như thế nào?”"),t("br"),t("br"),n._v("“Tại hạ...”"),t("br"),t("br"),n._v("Mập mạp cái cằm nâng lên, trong mắt mang theo ngạo nghễ, xem ra, bất cứ lúc nào cũng sẽ theo gió mà đi: “Tôn Cường!”"),t("br"),t("br"),n._v("“Tôn Cường?”"),t("br"),t("br"),n._v("Ghi nhớ tên, Linh Lung tiên tử nhẹ nhàng cười một tiếng, nhìn về phía mặt bàn: “Ngươi chân giò muối...”"),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Tôn Cường quay đầu cười một tiếng: “Là ngươi chân giò muối...”"),t("br"),t("br"),n._v("Linh Lung tiên tử khuôn mặt đỏ lên."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Một năm sau, Linh Lung tiên tử, Tôn Cường đại hôn."),t("br"),t("br"),n._v("Linh Lung tiên tử mất ngủ, giải quyết triệt để."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2282"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2284"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);