(window.webpackJsonp=window.webpackJsonp||[]).push([[1447],{1802:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-224-con-khong-goi-thuc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-224-con-khong-goi-thuc"}},[n._v("#")]),n._v(" Chương 224: Còn không gọi thúc!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ta thua? Còn sớm lắm!”"),t("br"),t("br"),n._v("Nhìn thấy trước mắt trường thương, Lục Tầm chỉ cảm thấy mãnh liệt nhục nhã xông thẳng đầu óc, rít lên một tiếng, cắn răng một cái, đột nhiên xông về phía trước."),t("br"),t("br"),n._v("“Hả?”"),t("br"),t("br"),n._v("Không nghĩ tới hắn đối với mình mũi thương vọt tới, Trịnh Dương sững sờ bên dưới, không kìm lòng được thu rụt lại cánh tay."),t("br"),t("br"),n._v("Này vừa thu lại, mất đi tiên cơ, Lục Tầm bàn chân giẫm, đột nhiên bắn lên, huyễn ảnh Cửu Cung Bộ triển khai, phảng phất đã biến thành huyễn ảnh, thời gian nháy mắt đi tới Trịnh Dương trước mặt, vừa vặn đâm đến."),t("br"),t("br"),n._v("Hắn hiện tại bị Viên Đào gắt gao ôm lấy, hai tay đều bị khóa lại, chỉ có thể dựa vào chiêu này."),t("br"),t("br"),n._v("Oành! Phốc!"),t("br"),t("br"),n._v("Không nghĩ tới thương đều rơi vào cổ đối phương lên, còn không biết xấu hổ phản kháng, Trịnh Dương không có một chút nào phòng bị, một lần bị đánh vào trên ngực, hơi đỏ mặt, máu tươi phun mạnh, đồng thời cả người bay ngược mà ra."),t("br"),t("br"),n._v("“Quá vô liêm sỉ...”"),t("br"),t("br"),n._v("“Đường đường học viện minh tinh giáo sư, làm sao có thể như vậy?”"),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),t("br"),n._v("Thấy Lục Tầm không chịu thua, xung quanh cũng là một trận ồ lên."),t("br"),t("br"),n._v("“Không thể tha thứ!”"),t("br"),t("br"),n._v("Thấy hắn đã thua, còn muốn phản kháng, thậm chí đem Trịnh Dương đả thương, Triệu Nhã tức giận mặt cười phấn hồng, bóng người loáng một cái, đã đi tới trước mặt, tay ngọc lật một cái, một thanh trường kiếm đã xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Nàng từ nhỏ đã học tập kiếm pháp, am hiểu sử dụng kiếm, bởi vì mấy người vây công, vẫn chưa lấy ra, giờ khắc này thấy vị này đã từng sùng bái Lục lão sư, làm ra như vậy chuyện vô sỉ, cũng không nhịn được nữa."),t("br"),t("br"),n._v("Ong!"),t("br"),t("br"),n._v("Trường kiếm lăng không, mờ ảo như vẽ."),t("br"),t("br"),n._v("Võ kỹ , sông lớn cuồn cuộn, tà dương như đuốc!"),t("br"),t("br"),n._v("“Hắc!”"),t("br"),t("br"),n._v("Hai tay bị cầm cố lại, Lục Tầm hừ lạnh, thân thể nhảy lên, một chân quay về thân kiếm đá tới."),t("br"),t("br"),n._v("Lần này ứng biến, bất luận theo điểm nào, cũng có thể nói là giây đến đỉnh phong, đáng tiếc, hắn vẫn là coi thường phía sau ôm hắn Viên Đào."),t("br"),t("br"),n._v("Trịnh Dương bị thương, cũng kích phát rồi hắn huyết tính, rít lên một tiếng."),t("br"),t("br"),n._v("“Cho ta xuống đây đi!”"),t("br"),t("br"),n._v("Thân thể mập mạp, như là đã biến thành trầm trọng tảng đá, Lục Tầm vừa mới bốc lên, đều không ngừng rơi xuống dưới."),t("br"),t("br"),n._v("Lần này truỵ xuống, hắn này một chân không những đá không tới trường kiếm, còn có thể bởi vì mất đi cân bằng, làm không cẩn thận sẽ bị một kiếm đâm thủng lồng ngực."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Biến cố đột ngột lên, Lục Tầm hiển nhiên cũng không phán đoán đến điểm ấy, con ngươi co rụt lại, lại muốn tránh tránh đã trốn không tránh nổi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Ngay lúc sắp chết người, không chết thì bị thương, một bóng người đột ngột xuất hiện tại hai người bên trong, ngón tay nhẹ nhàng bắn ra."),t("br"),t("br"),n._v("Ong!"),t("br"),t("br"),n._v("Một tiếng vang lên, Triệu Nhã trường kiếm trong tay, cũng lại không cầm được, thẳng tắp bay ra ngoài, nghiêng cắm ở cách đó không xa đất lên."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Bị trêu chọc đạn đi trường kiếm, Triệu Nhã đang muốn nổi giận, thấy rõ dáng dấp của đối phương, lập tức ôm quyền."),t("br"),t("br"),n._v("Đến chính là Trương Huyền."),t("br"),t("br"),n._v("Hắn nhìn ra, song phương đã đánh cho nổi trận lôi đình, không kịp ngăn cản nữa, làm không cẩn thận, ngày hôm nay khẳng định xảy ra đại sự."),t("br"),t("br"),n._v("Bỏ Triệu Nhã trường kiếm, đi tới Trịnh Dương trước mặt, một đạo chân khí rót vào trong đó, phát giác thương thế không nặng, chỉ cần phối hợp đan dược, tu luyện, trong thời gian ngắn nên sẽ vô sự, lúc này mới thở phào nhẹ nhõm, quay đầu nhìn về phía Viên Đào: “Ngươi cũng xuống đây đi!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Viên Đào lúc này mới buông ra câu tại cùng một chỗ bàn tay, đặt mông ngồi dưới đất."),t("br"),t("br"),n._v("Tuy rằng hắn này cả buổi chỉ ôm lấy đối phương, không nhúc nhích, kỳ thực trong năm người đếm hắn mệt nhất, cực khổ nhất, bị thương cũng nặng nhất."),t("br"),t("br"),n._v("Nếu không là Long Tê huyết mạch kích hoạt, phòng ngự vô địch, sợ là sớm đã không chịu nổi tấn công dữ dội, ngất đi."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Điều chỉnh chốc lát, năm người khôi phục như cũ, kéo uể oải thân thể, đi tới trước mặt."),t("br"),t("br"),n._v("Tuy nhưng đã không khí lực, lại từng cái từng cái trong mắt phóng thích vẻ hưng phấn."),t("br"),t("br"),n._v("Chiến đấu trước, bọn họ cũng không cảm thấy có thể thắng, kết quả lại ngoài ý muốn tốt, đường đường minh tinh giáo sư, lại có thể bị bọn họ đánh thảm như vậy, bất kể nói thế nào, trong lòng vẫn là rất tự hào."),t("br"),t("br"),n._v("Vốn tưởng rằng giúp lão sư hả giận, lại triển lộ này hơn nửa tháng sở học, có thể đạt được vài câu khích lệ, đã thấy trước mắt Trương lão sư, sắc mặt âm trầm đáng sợ, một đạo lửa giận tại trong mắt liên tục xoay quanh."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Thấy hắn dáng vẻ ấy, Trịnh Dương mấy người tràn đầy nghi hoặc."),t("br"),t("br"),n._v("“Có phải là cảm thấy ta nên khích lệ các ngươi?”"),t("br"),t("br"),n._v("Trương Huyền nhìn sang, khác nào tụ tập bão táp."),t("br"),t("br"),n._v("“Chúng ta...”"),t("br"),t("br"),n._v("Năm người vò đầu."),t("br"),t("br"),n._v("“Vốn tưởng rằng cố ý gạt ta, sẽ luyện tập cái gì lợi hại hợp kích trận pháp, đến, đều lại đây nói cho ta, đây là thứ đồ gì?” Trương Huyền gào thét, đầy mặt chỉ tiếc mài sắt không nên kim."),t("br"),t("br"),n._v("“Trận pháp này không tốt?”"),t("br"),t("br"),n._v("“Này tính được là Thiên Huyền Vương quốc đứng đầu nhất hợp kích trận pháp có được hay không?”"),t("br"),t("br"),n._v("Nghe được hắn tức giận lý do, tất cả mọi người đều đờ ra."),t("br"),t("br"),n._v("Cái này hợp kích trận pháp coi như tại Thiên Huyền Vương quốc đều xếp hàng lên khá cao, dù sao, mượn trận pháp này, một cái võ giả ngũ trọng, bốn cái nhị trọng, đem một vị Ích Huyệt cảnh đỉnh phong cường giả đánh bại, chiến tích tuyệt đối nghịch thiên."),t("br"),t("br"),n._v("Như vậy lại vẫn không hài lòng?"),t("br"),t("br"),n._v("Nói là “Thứ đồ gì” ?"),t("br"),t("br"),n._v("Một bên thật vất vả thanh tĩnh lại Lục Tầm, càng là ngực khó chịu, suýt chút nữa không khống chế lại thương thế, một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Có lầm hay không?"),t("br"),n._v("Cái này hợp kích trận pháp, để hắn suýt chút nữa tắt thở, lại còn không hài lòng?"),t("br"),t("br"),n._v("“Làm sao? Không phục? Tốt lắm, ta liền theo chiêu thứ nhất bắt đầu nói, Viên Đào nhằm phía Lục lão sư, giờ khắc này Trịnh Dương nếu như tại vị trí này ra tay, dùng ta dạy thương pháp của ngươi tiến hành giam giữ, uy lực có thể so với trực tiếp tiến công, tăng lên dữ dội gấp đôi; Lưu Dương, ở đây ra quyền, cả người hạ ngồi xổm ba tấc, vừa vặn là né tránh thiếu sót, muốn tránh đều không tránh thoát; Còn có Triệu Nhã, Vương Dĩnh, các ngươi ngủ sao? Nếu như phân biệt đứng ở đó hai cái vị trí, các ngươi cảm thấy, hắn còn có thể chạy thoát?”"),t("br"),t("br"),n._v("Không để ý tới mọi người giật mình, Trương Huyền quăng một cái ống tay áo, tiện tay trên đất đem cái này hợp kích trận pháp vẽ đi ra, chỉ mấy chỗ."),t("br"),t("br"),n._v("Lục Tầm vốn đang đang nghi ngờ, nhìn thấy hắn chỉ vài điểm, sắc mặt đột nhiên biến đổi, trong nháy mắt trở nên trắng bệch."),t("br"),t("br"),n._v("Đối phương nói không sai, nếu như vừa nãy mấy người này dựa theo hắn nói đứng vị, đồng thời đồng thời ra tay mà nói, coi như là hắn, cũng căn bản né tránh không ra, chí ít sẽ bị đồng thời đánh trúng ba chiêu."),t("br"),t("br"),n._v("Này mấy tên tiểu tử, một chiêu liền để hắn thổ huyết, liên tiếp là ba chiêu, coi như toàn thịnh kỳ, cũng khẳng định đối phó không được."),t("br"),t("br"),n._v("Không để ý tới của hắn run rẩy, Trương Huyền ngoài miệng liên tục, ngón tay lần thứ hai điểm qua."),t("br"),t("br"),n._v("“Chiêu thứ hai, Viên Đào ôm lấy Lục lão sư, vốn là cơ hội tốt nhất, nếu như Lưu Dương ở đây công kích, Vương Dĩnh, chậm ba cái hô hấp ra tay, Triệu Nhã cũng đừng do dự, cái thứ bảy hô hấp đóng kín đường lui, các ngươi còn có thể bị thương? Một cái bị thương đều không có, hắn sẽ chịu thua!”"),t("br"),t("br"),n._v("Càng nói Trương Huyền càng tức giận."),t("br"),t("br"),n._v("Này đám tiểu tử thực sự là quá xằng bậy."),t("br"),t("br"),n._v("Vốn tưởng rằng học được cái gì lợi hại hợp kích trận pháp, để hắn yên tâm, ai biết nhưng là trăm ngàn chỗ hở."),t("br"),t("br"),n._v("Đều cái gì ngoạn ý mà!"),t("br"),t("br"),n._v("Điều này cũng kêu hợp kích trận pháp?"),t("br"),t("br"),n._v("Chỉ cần những thứ này sớm tìm bản thân chỉ điểm vài câu, ngày hôm nay thì sẽ không có một người bị thương, hơn nữa, còn có thể đem Lục Tầm đánh giống như chó chết."),t("br"),t("br"),n._v("Nào giống bây giờ còn có tinh lực phản kháng, chết sống không chịu thua!"),t("br"),t("br"),n._v("Càng không thể bị thương vài cái, như thế chật vật!"),t("br"),t("br"),n._v("“Chuyện này... Chuyện này...”"),t("br"),t("br"),n._v("Nghe được lời giải thích của hắn, Lục Tầm toàn thân rét run."),t("br"),t("br"),n._v("Người khác có lẽ rất khó coi hắn chỉ điểm những cái này, nhưng lấy tư cách người có liên quan, tự mình cùng này mấy học sinh đối chiến người, biết hắn thuận miệng nói đáng sợ."),t("br"),t("br"),n._v("Nếu như vừa nãy mấy người này thật làm như vậy, sẽ cùng hắn nói như thế kết quả, một cái bị thương không có, mà bản thân, sẽ trở nên cùng đầu heo như thế."),t("br"),t("br"),n._v("Lẽ nào vị này Trương Huyền, còn hiểu đến trận pháp?"),t("br"),t("br"),n._v("Chiến đấu thay đổi trong nháy mắt, tốc độ nhanh như vậy, liếc mắt là đã nhìn ra bọn họ hợp kích trận pháp thiếu hụt, cũng làm ra tối ưu phân phối... Thật hay giả?"),t("br"),t("br"),n._v("Làm thế nào đến?"),t("br"),t("br"),n._v("Môi phát khô, Lục Tầm thân thể run rẩy."),t("br"),t("br"),n._v("Vốn là cùng mấy người này đối chiến, hắn đã cảm thấy được đối phương phối hợp rất tốt, làm sao đều không nghĩ tới, nhiều như vậy thiếu sót, nhiều như vậy vấn đề."),t("br"),t("br"),n._v("Mà một khi đem những vấn đề này bổ sung, hắn coi như chỉ có một thân Ích Huyệt cảnh tu vi, cũng không cách nào chống lại!"),t("br"),t("br"),n._v("Lợi hại hợp kích trận pháp, không phải một cộng một bằng hai, mà là bằng ba, bằng bốn."),t("br"),t("br"),n._v("Viên Đào am hiểu phòng ngự, là kiên cố nhất tấm khiên, Trịnh Dương trường thương công kích, là sắc bén nhất mũi tên, Triệu Nhã theo bên trong trấn thủ, đủ loại thủ đoạn tầng tầng lớp lớp, Vương Dĩnh thân pháp ảo diệu, xuyên tới xuyên lui, Lưu Dương quyền lực vô địch, thẳng thắn thoải mái..."),t("br"),t("br"),n._v("Dựa theo trước hợp kích trận pháp, năm người nhiều nhất chỉ có thể phát huy bảy hoặc là tám uy lực, mà nếu như dựa theo vị này Trương lão sư chỉ điểm đi làm, tuyệt đối có thể phát huy ra mười mấy, thậm chí hai mươi sức mạnh!"),t("br"),t("br"),n._v("Loại uy lực này coi như Thông Huyền cảnh sơ kỳ, cũng chưa chắc có thể chống đỡ được, huống chi hắn Ích Huyệt cảnh đỉnh phong!"),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("“Diệu a! Tuy rằng chỉ là ngăn ngắn vài câu, lại nói hết trận pháp tinh túy, lẽ nào vị này Trương đan sư, đối với trận pháp cũng nghe ngóng rất nhiều?”"),t("br"),t("br"),n._v("Đem Trương Huyền chỉ điểm tất cả nhìn ở trong mắt, Lưu Lăng tràn đầy không thể tin được."),t("br"),t("br"),n._v("Trận pháp sư, luyện đan sư đều thuộc về lên cửu lưu nghề nghiệp bên trong cao nhất tồn tại, mỗi một cái đều cao thâm huyền bí, cái tuổi này trở thành chính thức luyện đan sư liền rất lợi hại, trận pháp cũng hiểu?"),t("br"),t("br"),n._v("“Ta nói những cái này, đều hiểu?”"),t("br"),t("br"),n._v("Không để ý tới xung quanh khiếp sợ yên lặng như tờ, Trương Huyền quát lớn xong năm vị học viên, nhìn quanh một vòng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Năm người cúi đầu, đầy mặt xấu hổ."),t("br"),t("br"),n._v("Cái này hợp kích trận pháp là Triệu Nhã đem ra, thuộc về Bạch Ngọc thành đứng đầu nhất, vốn tưởng rằng hoàn mỹ không một tì vết, uy lực cực mạnh, nằm mơ đều không nghĩ tới, nhiều như vậy thiếu sót."),t("br"),t("br"),n._v("Bất quá, đi qua Trương lão sư chỉ điểm, cùng vừa nãy tự mình cùng người chiến đấu, đã nghe ngóng cực sâu, lại triển khai mà nói, cũng là thuận buồm xuôi gió hơn nhiều."),t("br"),t("br"),n._v("“Rõ ràng là tốt rồi!”"),t("br"),t("br"),n._v("Giáo huấn xong mấy người, Trương Huyền hai tay một gánh, chậm rãi quay trở về: “Nếu học được, lại đi qua đánh một lần, Lục lão sư không phải không nhận thua sao? Đánh hắn chịu thua mới thôi!”"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("“Lại đánh một lần?”"),t("br"),t("br"),n._v("Năm học sinh đồng thời loáng một cái, suýt chút nữa không phun máu."),t("br"),t("br"),n._v("Hắn cảm giác mình liền đủ tàn nhẫn, không nghĩ tới Trương lão sư càng ác hơn..."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Lục Tầm càng là mắt tối sầm lại."),t("br"),t("br"),n._v("Giời ạ, ngươi chạy tới, ngăn cản đối phương, chỉ điểm trận pháp, náo loạn cả buổi, không phải cảm thấy cho bọn họ làm không được, mà là muốn đùa chết ta đi..."),t("br"),t("br"),n._v("Đứng dậy, đang muốn nói chuyện, liền nghe đến một cái thanh âm dồn dập từ đằng xa truyền đến."),t("br"),t("br"),n._v("“Kính xin Trương đại sư nể tình ta bỏ qua cho tiểu tử này một lần đi!”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, liền thấy Lục Trầm đại sư đi nhanh tới."),t("br"),t("br"),n._v("“Cha...”"),t("br"),t("br"),n._v("Thấy luôn luôn không tranh với đời phụ thân, sẽ lúc này đi tới thay hắn mở miệng, Lục Tầm nắm đấm căng thẳng, sắc mặt đỏ lên."),t("br"),t("br"),n._v("Rời nhà trốn đi, liền muốn chứng minh cho phụ thân xem, không cần hắn, cũng có thể kiếm ra một mảnh mới thiên địa, nằm mơ đều không nghĩ tới, bị nhìn thấy như thế chật vật một khắc."),t("br"),t("br"),n._v("“Nghiệt súc, quỳ xuống!”"),t("br"),t("br"),n._v("Lục Trầm đại sư một mặt chỉ tiếc mài sắt không nên kim, bàn tay lớn vẫy một cái, chỉ về Trương Huyền: “Còn không gọi thúc!”"),t("br"),t("br"),n._v("(Chưa xong còn tiếp.,, ủng hộ của ngài, chính là ta động lực lớn nhất.) 17-01-22 1017"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-232"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-234"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);