(window.webpackJsonp=window.webpackJsonp||[]).push([[1448],{1805:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-225-đai-su-huynh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-225-đai-su-huynh"}},[n._v("#")]),n._v(" Chương 225: Đại sư huynh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Gọi thúc?”"),t("br"),t("br"),n._v("Bất luận lão sư vẫn là học sinh, cũng giống như bị đồng thời bóp lấy cái cổ."),t("br"),t("br"),n._v("Để Lục Tầm gọi Trương Huyền thúc?"),t("br"),t("br"),n._v("Đây là từ nơi nào luận?"),t("br"),t("br"),n._v("Ta không nghe lầm chứ?"),t("br"),t("br"),n._v("Lục Trầm đại sư là đường đường Đế sư, thân phận cao quý, để Lục Tầm gọi thúc, há không nói rõ, Thẩm Truy bệ hạ cũng phải gọi hắn thúc?"),t("br"),t("br"),n._v("Có muốn hay không như thế kích động?"),t("br"),t("br"),n._v("Nghĩ đến điểm này, tất cả mọi người đều nhìn về chủ chỗ ngồi bệ hạ, quả nhiên xem sắc mặt hắn cũng có chút không dễ nhìn."),t("br"),t("br"),n._v("Đường đường quốc vương bệ hạ, hơn bốn mươi tuổi người trung niên, so một cái không đủ hai mươi tiểu tử thấp hơn một đời... Đổi làm ai, đều sẽ không cao hứng."),t("br"),t("br"),n._v("“Gọi hắn... Thúc?”"),t("br"),t("br"),n._v("Lục Tầm suýt chút nữa khóc."),t("br"),t("br"),n._v("Này đều cái nào cùng cái nào a?"),t("br"),t("br"),n._v("Hắn đều không ta lớn, còn chỉ là cái đê cấp giáo sư, gọi thúc?"),t("br"),t("br"),n._v("Trong lòng bị đè nén muốn thổ huyết."),t("br"),t("br"),n._v("“Làm sao? Cảm thấy không gọi được? Trương đại sư cùng ta ngang hàng luận giao, ta xưng hô hắn huynh đệ, ngươi không gọi thúc, tên gì?”"),t("br"),t("br"),n._v("Một cái tát đánh ở trên đầu con trai, Lục Trầm đại sư gầm lên."),t("br"),t("br"),n._v("Này nuôi cái thứ đồ gì, dám đối với thúc thúc động thủ, thực sự là liều lĩnh, may mà Trương đại sư rộng lượng, không phải vậy, ngày hôm nay cũng không cần về nhà."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Vùng vẫy mấy lần, Lục Tầm khóc không ra nước mắt, thực sự không cách nào mở miệng."),t("br"),t("br"),n._v("“Ngươi cái súc sinh...”"),t("br"),t("br"),n._v("Lục Trầm đại sư tức giận râu dựng thẳng lên, đang muốn tiếp tục quát lớn liền bị một bên Trương lão sư ngăn cản."),t("br"),t("br"),n._v("“Được rồi, Lục huynh, hài tử nhất thời không nghĩ ra, không hiểu nổi, không có gì, sau đó cố gắng giáo dục là được rồi, trước công chúng lão đánh, cũng không tốt lắm!”"),t("br"),t("br"),n._v("Trương Huyền khuyên can."),t("br"),t("br"),n._v("Nói thật, cái gọi là thế hệ, hắn cũng không để ý."),t("br"),t("br"),n._v("Bất quá, Lục Trầm đại sư mặt mũi hay là muốn cho."),t("br"),t("br"),n._v("Từ khi xuyên qua đến hiện tại, vị này Lục đại sư cho hắn giúp đỡ rất lớn, nếu không là như vậy, Lục Tầm như vậy khiêu khích, khẳng định sớm đã đem hắn đánh thành đầu heo."),t("br"),t("br"),n._v("“Vâng, Trương huynh đệ nói rất đúng!”"),t("br"),t("br"),n._v("Lục đại sư gật gù, lần thứ hai đánh con trai một cái tát: “Ngươi cái súc sinh, còn không mau một chút cảm ơn Trương thúc thúc khoan hồng rộng lượng!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Lục Tầm điên rồi, lần thứ hai nhìn về phía Trương Huyền, có loại cảm giác sắp phát điên."),t("br"),t("br"),n._v("Đại ca, ngươi tiến vào thân phận tốc độ cũng quá nhanh đi!"),t("br"),t("br"),n._v("“Hài tử” nhất thời không nghĩ ra..."),t("br"),t("br"),n._v("Ta con mẹ nó là ai hài tử?"),t("br"),t("br"),n._v("Ngươi còn nhỏ hơn ta, nói như thế chuyện đương nhiên, từ đâu tới tự tin..."),t("br"),t("br"),n._v("Trong lòng vô hạn ưu thương, nhưng cha trước mặt, lại không dám phí lời."),t("br"),t("br"),n._v("Hắn chỉ là ưu thương, nhưng lại không biết đã có người đã té xỉu."),t("br"),t("br"),n._v("Chính là bằng hữu tốt nhất của hắn, Vương Siêu."),t("br"),t("br"),n._v("Vị này Trương lão sư, đối với Vương Sùng có nửa sư tình nghĩa, nói cách khác... Cha hắn đều là vãn bối."),t("br"),t("br"),n._v("Như vậy bàn về đến, hắn muốn xưng hô Trịnh Dương mấy người là thúc..."),t("br"),t("br"),n._v("“Trương... Thúc thúc!”"),t("br"),t("br"),n._v("Băn khoăn rất lâu, Lục Tầm cắn răng đứng dậy, hít sâu một hơi, trong ánh mắt mang theo ý lạnh: “Ngươi sư giả bình trắc thắng ta, muốn trở thành ba sư học đồ, e sợ cũng không làm được!”"),t("br"),t("br"),n._v("Ngày hôm nay như thế mất mặt, đều bởi vì cái tên này, dù cho xem ở cha mặt mũi lên, gọi hắn là Trương thúc thúc, kỳ thực sâu trong nội tâm, không có nửa điểm tôn kính."),t("br"),t("br"),n._v("“Ồ? Hiền chất sao lại nói lời ấy?” Trương Huyền nghi hoặc nhìn sang."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Biết đối phương xưng hô “Hiền chất” là cố ý, Lục Tầm tức thiếu chút nữa lần thứ hai thổ huyết, cả người lắc lắc, cắn răng một cái: “Muốn trở thành danh sư học đồ, đầu tiên muốn nắm giữ giáo sư công hội chứng thực minh tinh giáo sư thân phận, hoặc là cao cấp giáo sư thân phận, ngươi chỉ là cái đê cấp giáo sư, về mặt thân phận liền không phù hợp yêu cầu!”"),t("br"),t("br"),n._v("Giáo sư chia làm: Đê cấp giáo sư, cao cấp giáo sư cùng minh tinh giáo sư."),t("br"),t("br"),n._v("Muốn trở thành danh sư học đồ, cơ bản đều là minh tinh giáo sư, kém cỏi nhất cũng đạt đến cao cấp, một cái nho nhỏ đê cấp giáo sư, về mặt thân phận liền không phù hợp yêu cầu."),t("br"),t("br"),n._v("Coi như tam đại danh sư thưởng thức ngươi thì lại làm sao?"),t("br"),t("br"),n._v("Bọn họ lấy tư cách danh sư, nhất cử nhất động lại nên tuân thủ quy tắc, không thể làm trái."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Tiền thân vẫn luôn là đê cấp giáo sư thân phận, hắn sau khi sống lại, thời gian quá mức khẩn trương, cũng chưa kịp sát hạch cao cấp, xem ra sau này nhất định phải tìm một cơ hội thi một lần, không phải vậy muốn trở thành danh sư, ánh sáng về mặt thân phận liền có thể đem người kẹt chết."),t("br"),t("br"),n._v("Danh sư, làm là thế giới này cao nhất, chói mắt nhất nghề nghiệp, mỗi một bước đều cần vững chắc đi tới, bằng không, một khi bị người tra được, mất mặt chuyện nhỏ, hết thảy vinh dự đều sẽ lụi tàn theo lửa."),t("br"),t("br"),n._v("“Trương lão sư, chúng ta lại gặp mặt!”"),t("br"),t("br"),n._v("Ngay ở trong lòng hắn cân nhắc khi nào đi thi hạch một lần, liền nghe đến một cái thanh âm quen thuộc vang lên, quay đầu nhìn lại, liền chỉ bảo sư tổ sẽ Mạc trưởng lão chẳng biết lúc nào đã đi tới."),t("br"),t("br"),n._v("“Hóa ra là Mạc trưởng lão!”"),t("br"),t("br"),n._v("Đối với cái này Mạc trưởng lão, hắn vẫn có rất lớn hảo cảm."),t("br"),t("br"),n._v("Biết sai liền đổi, không giúp bằng hữu, không che chở tình thân, công chúng vô tư, lần trước học tâm tra hỏi, nếu không là hắn, muốn chơi Thượng Thần, Thượng Bân á khẩu không trả lời được, vẫn đúng là không làm được."),t("br"),t("br"),n._v("“Trương lão sư, thực sự thật không tiện, giáo sư bên trong ra Thượng Thần thứ bại hoại như vậy, mang cho ngươi đến không ít phiền phức, ngày đó đi rồi, ta liền báo lên tổng hội, mãi đến tận hiện tại mới có kết quả, để ngươi đợi lâu!”"),t("br"),t("br"),n._v("Mạc trưởng lão khom người."),t("br"),n._v("“Ồ? Có kết quả? Được!”"),t("br"),t("br"),n._v("Trương Huyền gật gù."),t("br"),t("br"),n._v("Lần trước học tâm tra hỏi, Mạc trưởng lão chỉ là tạm thời phế bỏ Thượng Thần phòng giáo dục chủ nhiệm thân phận cùng thủ tiêu Thượng Bân lão sư chương trình học, cũng không làm ra phán quyết, giáo sư công hội xử phạt nửa tháng chậm chạp chưa tới."),t("br"),t("br"),n._v("Vốn tưởng rằng là không dự định xử phạt, không nghĩ tới là đem chuyện này đưa tin tổng hội."),t("br"),t("br"),n._v("Giáo sư tổng hội, cách nơi này không biết bao xa, coi như có nhanh nhất đưa tin công cụ, thông bẩm phong hào Vương quốc, sau đó sẽ thông qua hắn truyền tống trận, một cái qua lại, cũng cần ít nhất thời gian nửa tháng."),t("br"),t("br"),n._v("“Ta hiện tại liền tuyên đọc tổng hội kết quả!”"),t("br"),t("br"),n._v("Mạc lão sư gật đầu một lần, từ trong lồng ngực lấy ra một cái quyển trục, nhẹ nhàng mở ra."),t("br"),t("br"),n._v("Rào rào!"),t("br"),t("br"),n._v("Một ánh hào quang theo tỷ thí giữa đài ở giữa xông thẳng lên trời, soi sáng tứ phương."),t("br"),t("br"),n._v("“Là giáo sư công hội !”"),t("br"),t("br"),n._v("“Công hội hàm? Cái kia là cái gì?”"),t("br"),t("br"),n._v("“Vương quốc có thánh chỉ, đủ loại khẩu dụ, chiếu lệnh... Giáo sư công hội là toàn bộ đại lục cấp bậc cao nhất thế lực một trong, cũng có vật này, chính là cái này công hội hàm! Dùng để xử trí một ít làm trái quy tắc giáo sư, hoặc là bổ nhiệm phân hội hội trưởng loại hình sự tình, một khi tuyên đọc, sẽ tự động ghi lại ở giáo sư công hội sách bên trong, bất luận người nào phân hội, đều có thể tuần tra!”"),t("br"),t("br"),n._v("“Lại có thể để công hội dùng ra công hội hàm, nói rõ đối với chuyện này vô cùng coi trọng!”"),t("br"),t("br"),n._v("“Cái kia là tự nhiên, tận lực áp bức một thiên tài, thậm chí là có thể trở thành danh sư tồn tại, công hội không trọng thị mới là lạ.”"),t("br"),t("br"),n._v("“Đúng đấy, muốn trách thì trách cái này là Thượng Thần tìm lộn người, đáng đời!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy vật này, hiểu rõ sự tình lão sư, tất cả đều âm thầm khiếp sợ."),t("br"),t("br"),n._v("Giáo sư công hội hàm giống nhau là không dễ dàng dùng ra, hiện tại lấy ra, đủ thấy đối với chuyện này coi trọng."),t("br"),t("br"),n._v("Ào ào ào!"),t("br"),t("br"),n._v("Công hội hàm xuất hiện, hết thảy lão sư, học sinh, thậm chí bao gồm tam đại danh sư, tất cả đều đồng loạt đứng dậy, thần thái cung kính."),t("br"),t("br"),n._v("“Thiên Huyền Vương quốc Hồng Thiên học viện giáo sư Thượng Thần, thân là thầy chủ nhiệm, mượn chức vụ chi tiện, đối với các lão sư khác tận lực chèn ép, làm trái công hội tôn chỉ, hiện từ bỏ giáo sư tư cách, vĩnh viễn không tuyển nhận!”"),t("br"),t("br"),n._v("Hào quang lấp loé, một cái âm thanh uy nghiêm chậm rãi vang lên."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Trong đám người ngồi Thượng Thần trưởng lão, biến sắc mặt, ngã xuống đất, mặt xám như tro tàn."),t("br"),t("br"),n._v("Hắn nằm mơ đều không nghĩ tới, liền giúp tôn tử hả giận, đã biến thành kết quả này."),t("br"),t("br"),n._v("Từ bỏ giáo sư tư cách, vĩnh viễn không tuyển nhận, cũng là đại biểu thân phận của hắn bây giờ cùng địa vị, trực tiếp đổ nát, vương tử biến thứ dân."),t("br"),t("br"),n._v("“Thiên Huyền Vương quốc Hồng Thiên học viện giáo sư Thượng Bân, không thể đoàn kết đồng sự, làm người gương sáng, thu hồi giáo sư tư cách ba năm; Giáo sư Tào Hùng, vì đố sinh tàn nhẫn bốc lên sự cố, từ bỏ giáo sư tư cách, bất luận cái gì học viện không thể mướn người!”"),t("br"),t("br"),n._v("Nói tiếp ra đối với Thượng Bân, Tào Hùng phán phạt."),t("br"),t("br"),n._v("Thu hồi giáo sư tư cách ba năm, cũng chẳng khác nào ba năm không cách nào dạy học."),t("br"),t("br"),n._v("Vậy cũng là tốt, chí ít ba năm sau, có thể lần thứ hai tiến vào học viện, mà gia gia hắn Thượng Thần cùng Tào Hùng, lại bằng cả đời không có cơ hội."),t("br"),t("br"),n._v("Đặc biệt là Tào Hùng, bất luận cái gì học viện không thể mướn người, bày tỏ coi như đi làm cái công nhân làm vệ sinh, đều không cho phép, một khi phát giác, toàn bộ học viện đều phải bị công hội trừng phạt."),t("br"),t("br"),n._v("“Thiên Huyền Vương quốc Hồng Thiên học viện giáo sư Trương Huyền, làm người thành khẩn, lấy đại cục làm trọng, chịu nhục, mà giáo thư dục nhân có phần chiến tích, quả thật lão sư bên trong điển phạm, đặc biệt tấn là minh tinh giáo sư.”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Công hội hàm âm thanh kết thúc, hào quang từ từ biến mất."),t("br"),t("br"),n._v("“Ta đây chính là minh tinh giáo sư?”"),t("br"),t("br"),n._v("Trương Huyền chớp con mắt."),t("br"),t("br"),n._v("Vốn đang cho rằng cần sát hạch cao cấp, sau đó lại nghĩ cách thăng cấp, bây giờ nhìn lại, căn bản không cần, giáo sư công hội một trang công hội hàm, liền đem vấn đề giải quyết triệt để."),t("br"),t("br"),n._v("“Trương lão sư, chúc mừng!”"),t("br"),t("br"),n._v("Mạc trưởng lão hưng phấn nhìn sang, một bên vuốt râu, một vừa cười nói."),t("br"),t("br"),n._v("“Làm phiền Mạc trưởng lão nhọc lòng!” Trương Huyền khom người."),t("br"),t("br"),n._v("Hắn biết cái này xử quyết khẳng định có Mạc trưởng lão công lao, trong lòng khá là cảm kích."),t("br"),t("br"),n._v("“Khách khí, ta liền đến tuyên bố công hội quyết định, còn lại liền không quấy rầy, cáo từ!” Mạc trưởng lão nói xong liền ôm quyền, cười rời đi."),t("br"),t("br"),n._v("Hắn vừa rời đi, xung quanh một trận yên tĩnh, mọi người cùng xoạt xoạt nhìn về phía bên trong Lục Tầm."),t("br"),t("br"),n._v("Ngươi không phải mới vừa rêu rao lên người ta không tư cách sao?"),t("br"),t("br"),n._v("Mạc trưởng lão một lần liền đến đến trước mặt, tuyên bố giáo sư công hội quyết định, để Trương lão sư trở thành minh tinh giáo sư, này mặt đánh... Nói thật, tất cả mọi người cảm thấy đau."),t("br"),t("br"),n._v("Nhìn về phía Lục Tầm, quả nhiên thấy sắc mặt trở nên trắng, một bộ muốn chết dáng dấp."),t("br"),t("br"),n._v("“Trương đại sư, tiểu tử này từ nhỏ bị ta thói quen hỏng rồi, ngươi đại nhân có đại lượng...”"),t("br"),t("br"),n._v("Đem tình cảnh này nhìn ở trong mắt, Lục Trầm đại sư lắc đầu một cái, muốn không thèm quan tâm, cuối cùng nhịn không được, đối với Trương Huyền liền ôm quyền."),t("br"),t("br"),n._v("“Lục đại sư nơi nào lời nói...” Trương Huyền cười cợt: “Hắn cũng chỉ là một lòng muốn trở thành danh sư học đồ mà rối loạn thân phận tâm trí mà thôi, ta sẽ không chú ý!”"),t("br"),t("br"),n._v("“Vậy thì đa tạ...” Thấy hắn không truy cứu, Lục Trầm vội vã cảm kích ôm quyền."),t("br"),t("br"),n._v("“Không cần khách khí, đúng rồi!” Trương Huyền xoay đầu lại nhìn về phía, chủ chỗ ngồi ba sư: “Lưu sư, Trang sư, Trịnh sư, Lục Tầm tuy rằng gần nhất biểu hiện không tốt lắm, nhưng đang dạy dỗ học sinh còn có tài nghệ thật sự lên, không kém chút nào, các ngươi xem ai muốn thu học đồ, có thể suy tính một chút hắn!”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Một bên Lục Tầm đang phiền muộn phải chết, nghe nói như thế, hơi đỏ mặt, con mắt đột nhiên trợn tròn."),t("br"),t("br"),n._v("Hắn sở dĩ muốn cùng Trương Huyền cạnh tranh, liền bởi vì muốn cạnh tranh bái vào ba sư môn hạ cơ hội, làm sao... Trước mắt cái này Trương lão sư rõ ràng thắng, ngược lại muốn thành toàn mình..."),t("br"),t("br"),n._v("Hơn nữa, ngươi đây là thái độ gì?"),t("br"),t("br"),n._v("Đối phương thế nhưng danh sư, nói như ngươi vậy, liền không sợ bị đánh chết?"),t("br"),t("br"),n._v("Trong lòng chấn động còn không kết thúc, liền thấy ba sư đi ra, đứng ở Trương Huyền trước mặt đồng thời ôm quyền."),t("br"),t("br"),n._v("“Đại sư huynh!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-233"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-235"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);