(window.webpackJsonp=window.webpackJsonp||[]).push([[1450],{1803:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-227-minh-ly-canh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-227-minh-ly-canh"}},[n._v("#")]),n._v(" Chương 227: Minh Lý cảnh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Đương nhiên, Trương Huyền cũng biết đây là không thể nào."),t("br"),t("br"),n._v("Trở thành luyện đan sư, hắn cũng biết lợi hại hơn nữa dược vật, ăn nhiều cũng sẽ có kháng dược tính, càng chưa nói loại này trực tiếp đề thăng tu vi đan dược."),t("br"),t("br"),n._v("Giải quyết xong mấy người tai hoạ ngầm, triệt để hóa giải cùng Lục Tầm mâu thuẫn, đều đại vui mừng, đang muốn tuyên bố tân sinh đại bỉ chính thức bắt đầu, chỉ thấy vô số học viên dâng lên, từng cái một kích động hai mắt thấu hồng, nhìn chằm chằm Trương Huyền, như là sói đói gặp được đồ ăn."),t("br"),t("br"),n._v("“Trương lão sư, ta muốn bái nhập của ngươi môn hạ, có thể hay không thu ta làm học sinh?”"),t("br"),t("br"),n._v("“Van cầu ngươi nhận lấy ta đi, ta không chỉ thiên phú tốt, còn có thể trải giường chiếu gấp mền làm việc nhà...”"),t("br"),t("br"),n._v("“Ta kỳ thực vẫn sùng bái ngươi, dù cho không phải là chính thức học sinh, làm bàng thính cũng tốt...”"),t("br"),t("br"),n._v("“Lão sư, nhà ta là có tiền, chỉ cần thu ta làm học sinh, muốn bao nhiêu cũng không có vấn đề gì!”"),t("br"),t("br"),n._v("“Tiễn tiễn, tiễn em gái ngươi a! Cút sang một bên, Trương lão sư là tham tiền người sao? Nếu như là... Nhà ta cũng có rất nhiều tiền, đem ta thu làm học sinh đi...”"),t("br"),t("br"),n._v("Sư giả bình trắc kinh diễm biểu hiện, tiện tay giải quyết tam sư đều không giải quyết được vấn đề, để ở đây sở hữu học sinh tất cả đều điên rồi, cũng ngăn chặn không được, hận không thể tại chỗ bái sư."),t("br"),t("br"),n._v("Trong nháy mắt, toàn bộ tỷ thí đài đưa tới bái sư triều dâng."),t("br"),t("br"),n._v("Trước chất vấn, nghi ngờ tất cả đều tiêu tan thành mây khói, vô số học sinh phía sau tiếp trước, rất sợ bỏ lỡ cơ hội."),t("br"),t("br"),n._v("Cũng không ít hối hận muốn nhảy lầu, nghĩ trước đây thế nào như thế mắt mù, tin vào lời đồn, không có sớm bái sư."),t("br"),t("br"),n._v("Nếu như khai giảng tựu lạy, sợ rằng hiện tại ở trên đài phong cảnh vô hạn không phải là Trịnh Dương mấy người, mà là bọn hắn!"),t("br"),t("br"),n._v("Phải biết rằng, nhập học trắc thí, Trịnh Dương đám người cũng không hiển sơn lộ thủy, đặc biệt là Viên Đào càng được một tên sau cùng."),t("br"),t("br"),n._v("Nhưng bây giờ, Viên Đào một chút đụng phải Lục lão sư lui về phía sau hai bước, và hắn ngạnh kháng tràng cảnh còn sở sờ ở trước mắt, để vô số người hơi bị kích động."),t("br"),t("br"),n._v("Từ thứ nhất đếm ngược, miễn cưỡng nhập học tên, đến ngạnh hám Ích Huyệt cảnh cường giả... Chỉ trải qua ngắn ngủi nửa tháng sau, không phải là tận mắt nhìn thấy, đều không thể tin được."),t("br"),t("br"),n._v("“Này...” Trương Huyền cũng lại càng hoảng sợ."),t("br"),t("br"),n._v("Rất hiển nhiên hắn cũng không có nghĩ tới những người này hội kích động như thế."),t("br"),t("br"),n._v("“Lão sư... Là thật mở mày mở mặt!”"),t("br"),t("br"),n._v("Một màn này rơi vào Triệu Nhã đám người trong mắt, từng cái một nắm tay xiết chặt."),t("br"),t("br"),n._v("Kể từ khi biết Lục Tầm muốn khiêu chiến Trương lão sư sau, bọn họ ngực vẫn nín một hơi thở, đó chính là vì sư giành vinh quang, vì sư thắng được vinh dự, mà bây giờ... Rốt cục làm xong rồi!"),t("br"),t("br"),n._v("Trương lão sư ưu tú, cũng rốt cục được người lý giải."),t("br"),t("br"),n._v("Một loại tự hào thản nhiên mà sinh."),t("br"),t("br"),n._v("Phảng phất trong nháy mắt tưởng nói cho mọi người, thậm chí ở trước mặt bọn họ, kiêu ngạo nói một câu..."),t("br"),t("br"),n._v("Nhìn, này tựu là sư phụ của ta ——"),t("br"),t("br"),n._v("Trương Huyền!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Ta đã có năm vị học viên, cũng không tính trong khoảng thời gian ngắn lại thu học sinh. Bất quá, các ngươi nếu như vậy nhiệt tình, như vậy đi, ta có thể cấp các ngươi giảng một đường tu luyện cơ sở tri thức khóa, có thể đối với các ngươi có chút trợ giúp!”"),t("br"),t("br"),n._v("Biết thật muốn thu nhiều như vậy học sinh, sớm muộn gì cũng sẽ tươi sống mệt chết, Trương Huyền chần chờ một chút nghĩ đến một chủ ý, mở miệng nói."),t("br"),t("br"),n._v("Năm học sinh cũng nhanh dạy hắn táng gia bại sản, có chút phát điên, nhiều như vậy... Đi ra ngoài xin cơm cũng nuôi không nổi a!"),t("br"),t("br"),n._v("“Không thu?”"),t("br"),t("br"),n._v("“Có thể nghe một lớp cũng tốt...”"),t("br"),t("br"),n._v("Thấy hắn không nữa thu học sinh, mọi người tất cả đều lộ ra vẻ thất vọng, bất quá nghe hắn phải nói một lớp, hựu toàn bộ lộ ra vẻ kích động."),t("br"),t("br"),n._v("Có thể để cho Trịnh Dương đám người, trong khoảng thời gian ngắn tựu đột nhiên tăng mạnh, giảng bài làm sao có thể giản đơn?"),t("br"),t("br"),n._v("“Cái này cơ sở khóa, tựu an bài ở trưa mai, địa điểm còn là ở đây, muốn nghe, có thể sớm nhiều, ngày hôm nay đại gia còn là an tâm tiến hành tân sinh đại bỉ!”"),t("br"),t("br"),n._v("Ăn nói một tiếng, trấn an ở mọi người, Trương Huyền ly khai tỷ thí đài."),t("br"),t("br"),n._v("Không đi nữa, chân sợ bị những điên cuồng học ăn sống rồi."),t("br"),t("br"),n._v("Hơn nữa, hôm nay kịch có giọng hát và điệu bộ rất nặng là tân sinh đại bỉ, sư giả bình trắc chỉ là món ăn khai vị mà thôi, cũng không thể tiếng động lớn tân đoạt chủ, cưu chiêm thước sào đi."),t("br"),t("br"),n._v("Ly khai tỷ thí đài, cương trở lại phòng học của mình, liền thấy tam đại danh sư đi đến."),t("br"),t("br"),n._v("“Đại sư huynh!”"),t("br"),t("br"),n._v("Ba người đồng loạt ôm quyền."),t("br"),t("br"),n._v("“Ừ, mời ngồi, vừa vặn ta có việc muốn phiền phức các ngươi!” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Ngươi là Dương sư thân truyền học viên, bọn ta chỉ là giảng bài, làm là sư huynh, có chuyện gì xin cứ việc phân phó chính là, nói phiền phức, thực sự không dám nhận...”"),t("br"),t("br"),n._v("Lưu Lăng lúng túng cười."),t("br"),t("br"),n._v("Đều là một lão sư học sinh, là có thân phận cao thấp, đệ tử thân truyền bối phận tự động thăng cấp, dù cho tu vi thấp, tuổi còn nhỏ, giảng bài học viên cũng muốn xưng hô sư huynh."),t("br"),n._v(" Sư huynh dặn chuyện, tự nhiên muốn chiếu bạn."),t("br"),t("br"),n._v("“Là như vậy!” Thấy bọn họ đáp ứng nhanh như vậy, Trương Huyền gật đầu, nói: “Các ngươi cũng biết Dương sư vân du tứ phương, không hỏi thế sự, tuy rằng thu ta làm đệ tử thân truyền, nhưng chỉ là để ta nhiều hơn học tập, cũng không có cho ta một thân phận thích hợp... Sở dĩ, ta muốn khảo hạch danh sư, khả năng còn cần phiền phức mấy vị!”"),t("br"),t("br"),n._v("Thu mấy người này làm học sinh, chính là vì mục đích này, “Dương sư” nói không có phương tiện, chỉ có thể hắn nói."),t("br"),t("br"),n._v("“Nga, ta còn tưởng rằng là chuyện gì, cái này giản đơn.” Lưu Lăng cười: “Muốn khảo hạch danh sư, phải có danh sư học đồ thân phận, Dương sư quy ẩn, không có phương tiện triển lộ thân phận, ba người chúng ta có thể làm thay.”"),t("br"),t("br"),n._v("Nói cổ tay khẽ lật, lấy ra một ngọc bài, đưa tới."),t("br"),t("br"),n._v("“Đây là có ba người chúng ta khí tức ẩn chứa ở bên trong thân phận ngọc phù, chỉ cần cầm ở trên người, tựu đại biểu là ba người chúng ta đã khảo nghiệm đồng thời công nhận danh sư học đồ. Ngươi chỉ cần đem máu tích đi vào nhận chủ, vô luận đi đến nơi nào danh sư đường, cũng sẽ được tán thành, do đó báo danh tham gia danh sư khảo hạch.”"),t("br"),t("br"),n._v("“Nga? Đơn giản như vậy?”"),t("br"),t("br"),n._v("Tiện tay mượn qua ngọc bài, Trương Huyền có chút không dám tin tưởng."),t("br"),t("br"),n._v("Vốn tưởng rằng rất phức tạp, không nghĩ tới dễ dàng như vậy."),t("br"),t("br"),n._v("“Trở thành học đồ trợ giáo dễ, chỉ cần danh sư tán thành, vậy minh tinh giáo sư, cao cấp giáo sư đều có thể, nhưng muốn khảo hạch danh sư, tựu khó khăn!” Lưu Lăng nói."),t("br"),t("br"),n._v("“Nga?... Thế nào mới có thể khảo hạch danh sư?”"),t("br"),t("br"),n._v("Lấy máu nhận chủ, Trương Huyền cất xong ngọc bài, nhịn không được hỏi."),t("br"),t("br"),n._v("Thiên huyền vương quốc không có danh sư, tàng thư kho cũng không có tương tự ghi chép, tuy rằng mấy ngày này giả mạo danh sư, nói thật đi, hắn liên làm sao khảo hạch, ở địa phương nào khảo hạch, hoàn toàn chẳng biết, dốt đặc cán mai."),t("br"),t("br"),n._v("“Khảo hạch danh sư, muốn cụ có đầy đủ ngoại bộ điều kiện và nội bộ điều kiện, ngoại bộ điều kiện làm: Danh sư đề cử, học đồ trợ giáo thân phận, chính mình chí ít một hạng phụ trợ chức nghiệp thân phận!”"),t("br"),t("br"),n._v("Lưu Lăng giải thích."),t("br"),t("br"),n._v("“Ừ!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cái này trước hắn tựu nghe nói, đồng thời không kỳ quái."),t("br"),t("br"),n._v("“Nội bộ điều kiện chỉ có hai cái!” Lưu Lăng nói tiếp: “Theo thứ tự là đạt được Minh Lý cảnh tâm như chỉ thủy, đạt được Thông Huyền cảnh tu vi!”"),t("br"),t("br"),n._v("“Minh Lý cảnh?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Tâm cảnh đệ nhị trọng là tâm như chỉ thủy, hắn đã sớm biết, nhưng Minh Lý cảnh là cái gì?"),t("br"),t("br"),n._v("Lưu Lăng nói: “Tâm cảnh là mỗi một tu luyện giả thậm chí mỗi một loại chức nghiệp, đều phải không ngừng tôi luyện. Tâm như chỉ thủy, chỉ là một gọi chung, biểu thị một người có thể khống chế tâm cảnh của mình, để tâm thần chuyên chú nào đó một việc, không vì ngoại giới hoàn cảnh ảnh hưởng.”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn nói không sai, trần tiêu đan sư, Mạnh Nham đan sư đám người, cũng bởi vì không có đạt được loại cảnh giới này, luyện đan thời gian, đem mình mặt trái tâm tình thêm ở Tĩnh Tâm đan trên, để dược vật trở nên chỉ tốt ở bề ngoài, mất đi vốn có công hiệu."),t("br"),t("br"),n._v("“Nếu như kể lại phân chia nói, cái này tâm như chỉ thủy cảnh, có thể chia làm hai cái, một là danh sư phương hướng, một là võ giả và những nghề nghiệp khác phương hướng.”"),t("br"),t("br"),n._v("“Vì sao phân chia như vậy, rất đơn giản, danh sư muốn đạt tới tâm như chỉ thủy, và những nghề nghiệp khác tất cả đều bất đồng, đây cũng là vì sao danh sư như vậy đặc thù, tôn quý nguyên nhân.”"),t("br"),t("br"),n._v("“Những nghề nghiệp khác và võ giả tâm như chỉ thủy, chỉ cần làm được tâm cảnh như một, bất động không dao động là được, mà danh sư tâm như chỉ thủy, lại muốn làm đến minh lý biện vật, phân biệt thật giả!”"),t("br"),t("br"),n._v("“Minh lý biện vật, phân biệt thật giả?” Trương Huyền đầu đầy vụ thủy."),t("br"),t("br"),n._v("“Không sai, nếu như bất hảo lý giải, trực tiếp chia làm lưỡng chủng mà nói tựu đơn giản, người thường tâm như chỉ thủy, gọi Chuyên Nhất cảnh, chỉ cần làm được chuyên chú làm nào đó sự kiện là được; Mà danh sư tâm như chỉ thủy, gọi Minh Lý cảnh, có thể nhận sự vật, tuần tra chuyện thứ tự, tìm được hạch tâm vấn đề chỗ ở.”"),t("br"),t("br"),n._v("Lưu Lăng nói: “Nói cách khác, đồng dạng đạt được tâm như chỉ thủy, danh sư và người thường là bất đồng, chỉ có chính mình Minh Lý cảnh tâm như chỉ thủy, mới có tư cách khảo hạch danh sư, đây cũng là vì sao một ít cao đẳng học viện, có luyện đan lão sư, trà đạo lão sư, trận pháp lão sư, luyện khí lão sư... Mà những người này cũng danh sư như nhau.”"),t("br"),t("br"),n._v("Chính mình phụ trợ chức nghiệp, có thể khảo hạch danh sư, Điền lão là trà đạo đại sư, vừa bắc võ học viện lão sư, trước liền suy nghĩ, vì sao không khảo hạch danh sư, nguyên lai căn do tại đây."),t("br"),t("br"),n._v("Không có Minh Lý cảnh tâm như chỉ thủy, là không có tư cách."),t("br"),t("br"),n._v("“... Tu luyện như thế nào mới có thể chính mình Minh Lý cảnh tâm như chỉ thủy?” Minh bạch nhiều như vậy huyền bí, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“Làm sao đạt được?” Lưu Lăng, trang hiền đám người liếc mắt nhìn nhau, đồng thời nở nụ cười: “Đây là không có biện pháp tu luyện, là thiên phú! Chính mình danh sư tiềm lực nhân, chỉ cần đạt được tâm như chỉ thủy cảnh giới, tự nhiên sẽ minh lý biện vật, phân biệt thật giả!”"),t("br"),t("br"),n._v("“Cái đó và luyện đan sư phải có mộc thuộc tính thể chất như nhau, không có cái này, dù cho tu luyện lại ngoan, cũng không dùng! Nói giản đơn điểm, nam nhân lợi hại hơn nữa, dù cho hội trên đời sở hữu chức nghiệp, cũng sẽ không sanh con, không có thiên phú, tu luyện nhiều hơn nữa, cũng là giỏ trúc múc nước công dã tràng!”"),t("br"),t("br"),n._v("“Đạt được tâm như chỉ thủy... Tự nhiên sẽ minh lý biện vật? Phân biệt thật giả?” Trương Huyền trong lòng “Lộp bộp!” Một chút."),t("br"),t("br"),n._v("Hắn đã sớm đạt được tâm như chỉ thủy, thế nào không có loại cảm giác này?"),t("br"),t("br"),n._v("“Minh Lý cảnh... Và Chuyên Nhất cảnh có cái gì bất đồng?”"),t("br"),t("br"),n._v("“Dùng đơn giản nhất ví dụ mà nói, đạt được Minh Lý cảnh tâm như chỉ thủy, người khác tu luyện vũ kỹ, sẽ tự động phân tích ra chiêu số thứ tự, trình tự, sau đó sẽ cân nhắc tu luyện như thế nào mới có thể đạt được hiệu quả tốt nhất, mà thông thường tâm như chỉ thủy, tựu làm không được.”"),t("br"),t("br"),n._v("Lưu Lăng nói: “Danh sư đều thích tìm minh tinh giáo sư làm học đồ, bởi vì có thể bằng vào mình có thể lực để học sinh cam tâm tình nguyện đi theo, nói rõ khẳng định có thiên phú như thế.”"),t("br"),t("br"),n._v("“Tự động phân tích vũ kỹ thứ tự trình tự?”"),t("br"),t("br"),n._v("Trương Huyền thân thể run lên."),t("br"),t("br"),n._v("Ni mã, đồng dạng là tâm như chỉ thủy? Hắn tại sao không có loại cảm giác này?"),t("br"),t("br"),n._v("Chẳng lẽ... Hắn không thích hợp làm danh sư?"),t("br"),t("br"),n._v("Thật muốn như vậy, tựu thao đản."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-235"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-237"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);