(window.webpackJsonp=window.webpackJsonp||[]).push([[1453],{1806:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-230-thien-đao-chi-sach-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-230-thien-đao-chi-sach-thuong"}},[n._v("#")]),n._v(" Chương 230: Thiên Đạo chi sách (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Chúng ta Triệu gia công pháp, thẳng thắn thoải mái, to lớn hùng vĩ, điểm ấy chắc hẳn bệ hạ cũng biết!”"),t("br"),t("br"),n._v("Thấy đối phương mê hoặc, Triệu Phong thành chủ cũng không ẩn giấu, mở miệng nói."),t("br"),t("br"),n._v("“Đúng đấy!” Thẩm Truy bệ hạ gật đầu."),t("br"),t("br"),n._v("Kỳ thực rất nhiều năm trước, Thẩm, Triệu hai nhà vốn là bằng hữu, cùng đánh xuống Thiên Huyền Vương quốc, một cái trấn thủ giang sơn, một cái trấn thủ của cải. Nguyên nhân chính là như vậy, hai nhà đời đời giao hảo, thậm chí công pháp đều không có ẩn giấu."),t("br"),t("br"),n._v("Đối với Triệu gia Bạch Ngọc Công, Thẩm Truy bệ hạ từ nhỏ đã tiếp xúc, hết sức quen thuộc, sức mạnh cương mãnh, khí tức mười phần, không thích hợp Triệu Nhã loại này nữ hài tu luyện."),t("br"),t("br"),n._v("Triệu Phong thành chủ tiếp tục nói: “Từ khi Thiên Huyền Vương quốc thành lập, ta Triệu gia vẫn trấn thủ Ngọc thành, thậm chí đem hắn cải danh ‘Bạch ngọc’, đời đời cùng ngọc tiếp xúc. Dựa theo đạo lý, ngọc nuôi người, người nuôi ngọc, có thể để cho tu luyện càng nhanh nhẹn hơn, tốc độ càng hơn! Bất quá, đáng tiếc chính là, ngọc tính thuộc về lạnh, tiếp xúc một hai khối cũng còn tốt, tiếp xúc hơn nhiều, đối với chúng ta nhiệt tính Bạch Ngọc Công, có rất lớn xung kích cùng tổn thương.”"),t("br"),t("br"),n._v("“Vừa mới bắt đầu còn không nhìn thấy, nhưng nóng lạnh xung đột lẫn nhau lâu, sẽ ở trong người hình thành một loại đặc thù mầm họa, nương theo huyết dịch, đồng lứa thế hệ truyền thừa xuống. Cái này cũng là ta nhiều năm như vậy, vẫn vây ở Thông Huyền cảnh hậu kỳ, không cách nào đột phá nguyên nhân.”"),t("br"),t("br"),n._v("Triệu Phong thành chủ năm đó thiên phú, không thể so Triệu Nhã thua kém, được gọi là thiếu niên thiên tài, chừng hai mươi tuổi liền đạt đến Thông Huyền cảnh, danh chấn nhất thời."),t("br"),t("br"),n._v("Đáng tiếc, đạt đến Thông Huyền cảnh hậu kỳ lại không cách nào tinh tiến, mười mấy năm qua đi, không có một chút nào tiến triển, vốn tưởng rằng là tiềm lực đào cố gắng, không nghĩ tới là cả ngày cùng ngọc cùng nhau, tổ tông lưu truyền tới nay mầm họa."),t("br"),t("br"),n._v("“Trương lão sư nhìn ra vấn đề này, cố ý đổi sai Bạch Ngọc Công, để cho nóng lạnh tương giao, sức mạnh hỗn tạp, như vậy tới nay, trong cơ thể ta lưu giữ mầm họa liền bị triệt để tiêu trừ. Không còn mầm họa, lại thêm lên tích lũy nhiều năm như vậy, lúc này mới một lần đột phá, xung kích Thông Huyền cảnh đỉnh phong thành công!” Triệu Phong vừa nói, trong mắt một bên lộ ra khâm phục vẻ."),t("br"),t("br"),n._v("Chỉ nhìn một lần công pháp, biết mình vị trí Bạch Ngọc thành, liền suy tính ra những cái này, vị này Trương lão sư đối với công pháp, tu luyện lý giải, coi như không phải Tông Sư, cũng khẳng định cách biệt không xa."),t("br"),t("br"),n._v("“Nếu giải quyết mầm họa, cái kia công pháp...” Thẩm Truy bệ hạ còn hơi nghi hoặc một chút."),t("br"),t("br"),n._v("“Bệ hạ có phải là muốn hỏi, vì sao phải thiêu hủy công pháp?”"),t("br"),t("br"),n._v("Triệu Phong thành chủ cười cợt: “Cái kia công pháp vốn là sai, mục đích là vì giúp ta giải trừ mầm họa, hiện tại mầm họa tiêu trừ, tiếp tục tu luyện, e sợ sẽ thật sự tẩu hỏa nhập ma. Vì lẽ đó Trương lão sư trực tiếp thiêu hủy, ý tứ là... Đã có thể mang vật này quên mất, không cần lại tiếp tục tu luyện.”"),t("br"),t("br"),n._v("“Thì ra là như vậy...” Thẩm Truy bệ hạ giờ mới hiểu được lại đây, trong lòng kinh hãi."),t("br"),t("br"),n._v("Đối phương nói đơn giản, kỳ thực hắn biết, khẳng định không dễ dàng."),t("br"),t("br"),n._v("Một môn công pháp truyền thừa mấy trăm năm, vô số tiền bối đều cân nhắc qua, chỉ bằng vào cải biến một ít vận chuyển chân khí pháp môn, liền đem lắng đọng đếm thế hệ mầm họa thanh trừ, loại năng lực này, đã không thể dùng đáng sợ để hình dung, tuyệt đối là khủng bố cấp bậc."),t("br"),t("br"),n._v("E sợ ba sư, đều không làm được điểm ấy."),t("br"),t("br"),n._v("“Cha, bệ hạ, các ngươi sao lại ở đây?”"),t("br"),t("br"),n._v("Đang đang khiếp sợ, liền nghe đến Triệu Nhã âm thanh âm vang lên, chỉ thấy ngày hôm nay làm náo động lớn mấy cái thiếu nam, thiếu nữ đi tới."),t("br"),t("br"),n._v("“Há, cha bái phỏng một hồi các ngươi Trương lão sư!” Triệu Phong gật đầu: “Các ngươi không phải tham gia tân sinh thi đấu sao? Làm sao...”"),t("br"),t("br"),n._v("Trước tỷ thí là sư giả bình trắc, muốn xác định đang tái sinh bên trong thứ tự, chỉ có thi đấu, làm sao thi đấu còn chưa bắt đầu, bọn họ sẽ trở lại?"),t("br"),t("br"),n._v("“Chúng ta như bây giờ, coi như muốn đánh, ai dám so?”"),t("br"),t("br"),n._v("Triệu Nhã hai tay mở ra."),t("br"),t("br"),n._v("“Ây... Cũng đúng!” Triệu Phong hai người cười khổ."),t("br"),t("br"),n._v("Bất luận Triệu Nhã, Trịnh Dương vẫn là Viên Đào, liền Ích Huyệt cảnh cường giả đều có thể gắng chống đỡ, so với bình thường lão sư đều mạnh, còn ai dám cùng với tỷ thí?"),t("br"),t("br"),n._v("E sợ vừa nhìn thấy liền chịu thua."),t("br"),t("br"),n._v("Lại nói, đều có thực lực như vậy, cái gọi là tân sinh thi đấu, coi như đạt được quán quân, cũng không bất luận cái gì vinh dự có thể nói, còn không bằng lùi lại từ đây."),t("br"),t("br"),n._v("“Đúng rồi, cha, ngươi nhìn thấy Trương lão sư sao? Có cần hay không ta giới thiệu một chút?” Thấy hai người đứng ở trước cửa, Triệu Nhã không nhịn được nói."),t("br"),t("br"),n._v("“Ta đã gặp các ngươi Trương lão sư.” Nói đến đây, Triệu Phong nhìn về phía con gái: “Tiểu nhã, có thể bái vào Trương lão sư môn hạ, là của ngươi may mắn, sau đó nhất định phải cố gắng nghe lời, không thể cùng trong nhà như thế đùa nghịch tính khí!”"),t("br"),t("br"),n._v("“Ừm!” Triệu Nhã gật đầu."),t("br"),t("br"),n._v("“Còn có, Trương lão sư là trên trời Giao Long, e sợ Thiên Huyền Vương quốc lưu lại không được bao lâu, nếu như hắn muốn ngươi theo, nhất định nắm chắc cơ hội, không thể buông tay, không phải vậy, tuyệt đối sẽ hối hận cả đời...” Những câu nói này, Triệu Phong đổi dùng truyền âm, trong mắt lộ ra nồng đậm chờ đợi: “Có lẽ... Cứu ra mẹ ngươi, chỉ có hắn... Mới có thể giúp ngươi làm được!”"),t("br"),t("br"),n._v("“Vâng!” Triệu Nhã nắm đấm xiết chặt."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Xem một cái cái này Minh Lý Ngọc Tinh làm sao sử dụng!”"),t("br"),t("br"),n._v("Chỉ điểm Triệu Phong, chỉ là nể mặt Triệu Nhã, không tính là gì, hai người vừa rời đi, Trương Huyền liền không nghĩ nhiều nữa, cúi đầu nhìn về phía lòng bàn tay ngọc thạch."),t("br"),t("br"),n._v("Theo lý thuyết, hắn có Thiên Đạo thư viện, có thể nhẹ nhõm nhìn ra người khác tu luyện tới vấn đề cùng thiếu hụt, coi như không phải Minh Lý cảnh tâm như chỉ thủy, cũng không sao cả."),t("br"),t("br"),n._v("Nhưng có thiên phú như thế, đồng thời đạt đến 3, mới là có sát hạch danh sư tư cách."),t("br"),t("br"),n._v("Không có danh sư thân phận, chỉ dựa vào ngụy trang, đều là không có thể dài lâu."),t("br"),t("br"),n._v("Vậy thì cùng kiếp trước lái xe như thế, không có giấy phép lái xe, dù cho là tay đua xe, ra đi cũng phải nơm nớp lo sợ, sợ bị bắt lấy."),t("br"),t("br"),n._v("Hơn nữa, mấu chốt nhất chính là, danh sư đường thư tịch đông đảo, muốn vào quan sát, nhanh chóng tăng lên, cái này quan trên mặt thân phận nhất định phải có."),t("br"),t("br"),n._v("“Hấp thu!”"),t("br"),t("br"),n._v("Tinh thần hơi động, tâm cảnh lại lần nữa tiến vào cảnh giới thứ hai, quả nhiên cảm thấy lòng bàn tay một luồng mát mẻ tâm ý tràn vào toàn thân, trước hao tổn tâm cảnh mức độ, chầm chậm khôi phục."),t("br"),t("br"),n._v("“Thật sự có hiệu quả!”"),t("br"),t("br"),n._v("Cảm nhận được thân thể biến hóa, Trương Huyền ánh mắt sáng lên, tiếp tục chìm đắm trong đó, tham lam hấp thu Minh Lý Ngọc Tinh bên trong năng lượng."),t("br"),t("br"),n._v("Không biết qua bao lâu, rốt cục cảm thấy trước hao tổn tâm cảnh mức độ, triệt để khôi phục, một lần nữa đạt đến 0. 1 xung quanh."),t("br"),t("br"),n._v("Bất quá, khôi phục lại 0. 1, liền lại không cách nào hấp thu, thật giống đã no đủ, lại không cách nào chứa đựng."),t("br"),n._v(" “Xem ra không có công pháp tu luyện, có vật này cũng không kéo, rồi cùng không có công pháp tu luyện, không cách nào để cho tu vi thăng cấp như thế!”"),t("br"),t("br"),n._v("Không cách nào hấp thu, Trương Huyền đành phải ngừng lại."),t("br"),t("br"),n._v("Trong tu luyện linh khí cùng chân khí quan hệ, liền như là nước chảy, từ cao hướng thấp, muốn cho tình huống nghịch chuyển, liền cần phải mượn mỗi bên loại phương pháp, công pháp tu luyện chính là một người trong đó."),t("br"),t("br"),n._v("Minh Lý Ngọc Tinh tuy rằng có thể trợ giúp người tăng trưởng tâm cảnh mức độ, cũng không có xứng đôi tu luyện pháp quyết, đối với hắn bây giờ tới nói, như thế vô bổ."),t("br"),t("br"),n._v("“Quên đi, trước không nghiên cứu, xem một cái kim sắc thư tịch.”"),t("br"),t("br"),n._v("Biết băn khoăn cũng vô dụng, Trương Huyền không nghĩ nhiều nữa, nhìn về phía trong thư viện cái kia bản kim sắc thư tịch."),t("br"),t("br"),n._v("Mấy tên tiểu tử kia vì hắn chính danh thời điểm xuất hiện, vẫn chưa kịp quan sát, giờ khắc này vừa vặn rảnh rỗi, có thể lại lần nữa nghiên cứu một chút."),t("br"),t("br"),n._v("Trong lòng hơi động, thư tịch đi tới trước mắt, cùng trước như thế, chỉ có một tờ trống tờ."),t("br"),t("br"),n._v("“Đến cùng có phải là để người cảm kích mới phải xuất hiện, còn phải chờ nghiên cứu, chỉ là... Vật này, đến cùng cái gì dùng?”"),t("br"),t("br"),n._v("Nhìn chằm chằm trước mắt thư tịch, Trương Huyền lòng tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Lần trước lúc nghiên cứu, thư tịch biến mất, hắn rơi vào hôn mê, sau khi tỉnh lại, trong thư viện tri thức liền tiến vào đầu óc, đã biến thành đồ vật của chính mình."),t("br"),t("br"),n._v("Chẳng lẽ cái này xem ra trâu bò lập lòe đồ vật, liền này một chút tác dụng?"),t("br"),t("br"),n._v("Nhưng nếu như không phải, một chữ dấu vết đều không có, lại không có đề kỳ... Coi như là hắn, cũng càng xem càng hồ đồ."),t("br"),t("br"),n._v("“Dùng Thiên Đạo thư viện thử xem!”"),t("br"),t("br"),n._v("Nghiên cứu thật lâu, trước sau không làm rõ được, trong lòng hơi động, một ý nghĩ xông ra."),t("br"),t("br"),n._v("Hắn nghiên cứu không ra, nhưng thư viện đâu?"),t("br"),t("br"),n._v("Rồi cùng trước độc khí như thế, cho đến hiện nay, tựa hồ vẫn không có cái này thần khí nghịch thiên phân tích không ra thiếu hụt đồ vật."),t("br"),t("br"),n._v("Bất quá, này kim sắc thư tịch, tại bản thân đầu óc, mặc dù coi như cầm ở trong tay, trên thực tế nhưng là linh hồn cảm ứng, không phải thực thể chạm đến, chính là không biết tình huống như thế, có thể hay không hình thành thư tịch."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Nắm chặt thư tịch, tinh thần cảm ứng."),t("br"),t("br"),n._v("Nhìn chung quanh một chút, quả nhiên cùng đoán như thế, ý thức thượng chạm đến, là không cách nào kiểm tra thiếu hụt."),t("br"),t("br"),n._v("“Chỉ có thể dùng đần biện pháp đoán...”"),t("br"),t("br"),n._v("Thử cả buổi không tiến triển chút nào, Trương Huyền đành phải cười khổ."),t("br"),t("br"),n._v("Cái kia phương pháp vô dụng, cũng chỉ có thể dựa vào chính mình đoán."),t("br"),t("br"),n._v("Lấy ra một cái trống không thư tịch, cầm lấy giấy bút ở phía trên viết: “Kim sắc thư tịch tác dụng, chỉ có thể đem thư viện tri thức, chuyển hóa thành kí chủ.”"),t("br"),t("br"),n._v("Ong!"),t("br"),t("br"),n._v("Đầu óc hình thành một quyển tương đồng thư tịch, mở ra nhìn chút, quả nhiên viết rõ thiếu hụt: “Chuyển hóa tri thức chỉ là năng lực một trong, cũng không phải là duy nhất.”"),t("br"),t("br"),n._v("“Thật còn có cái khác tác dụng!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Liền nói như thế lợi hại thư tịch, làm sao có khả năng cũng chỉ có một chuyển hóa tri thức tác dụng, quả nhiên còn có cái khác tác dụng."),t("br"),t("br"),n._v("Chỉ có điều, đến cùng chỗ ích lợi gì, cũng chỉ có thể dựa vào chính mình suy đoán."),t("br"),t("br"),n._v("“Quyển sách này tên gọi... Kim sắc thư tịch!”"),t("br"),t("br"),n._v("Chần chờ một hồi, Trương Huyền tiếp tục viết."),t("br"),t("br"),n._v("Rất nhanh, thiếu sót xuất hiện: “Kim sắc thư tịch tên sai lầm, quyển sách là .”"),t("br"),t("br"),n._v("“Thiên Đạo chi sách? Thật bá đạo tên? Nhưng mà... Chỉ thô bạo vô dụng, phải biết có tác dụng gì mới được!”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Đã biết một người trong đó tác dụng là chuyển hóa tri thức, cái kia tác dụng thứ hai là cái gì?"),t("br"),t("br"),n._v("“Có thể hay không chuyển hóa nhượng lại tâm cảnh mức độ tăng lên công pháp tu luyện?”"),t("br"),t("br"),n._v("Đang suy nghĩ chắc là làm sao thí nghiệm, một ý nghĩ xông ra."),t("br"),t("br"),n._v("Này cả buổi vẫn băn khoăn tâm cảnh mức độ, nếu ngày hôm nay đạo chi sách như vậy trâu bò, có thể hay không sinh thành một bộ tăng lên mức độ công pháp?"),t("br"),t("br"),n._v("Đang đang miên man suy nghĩ, còn chưa kịp có động tác, liền thấy trước mắt thư tịch một trận kịch liệt lay động, biến thành một đạo sáng lạn ánh sáng, bao phủ toàn bộ thư viện."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lần này không có hôn mê, thời gian không lâu, liền khôi phục như cũ, vội vàng nhìn lại, cùng trước như thế, kim sắc thư tịch vẫn còn, chính là trong đó cái kia tờ trống tờ lần nữa biến mất không thấy."),t("br"),t("br"),n._v("“Chẳng lẽ... Thật hình thành để tâm cảnh mức độ tăng lên công pháp?”"),t("br"),t("br"),n._v("Vừa nãy chỉ muốn như thế nào để tâm cảnh tăng lên, trang sách vàng óng liền tiêu hao mất, có thể hay không thật cùng hắn chờ đợi như thế, thư viện đã có tu luyện tâm cảnh mức độ bí kỹ?"),t("br"),t("br"),n._v("Nghĩ tới đây, trong lòng một trận kịch liệt, vội vàng hướng bốn phía tìm đi."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-238"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-240"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);