(window.webpackJsonp=window.webpackJsonp||[]).push([[1455],{1809:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-231-thien-đao-chi-sach-ha"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-231-thien-đao-chi-sach-ha"}},[n._v("#")]),n._v(" Chương 231: Thiên Đạo chi sách (hạ)")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Tìm cả buổi, bất đắc dĩ phát giác, trong thư viện vẫn như cũ là những cái kia thư tịch, căn bản không thêm ra đến đồ vật, cũng không có cái gọi là công pháp."),g("br"),g("br"),n._v("“Lẽ nào uổng phí tiêu hao?”"),g("br"),g("br"),n._v("Trương Huyền tràn đầy lúng túng."),g("br"),g("br"),n._v("Thật muốn là như vậy, cũng chỉ có thể khóc."),g("br"),g("br"),n._v("Không nói cái khác tác dụng, liền nói để trong thư viện tri thức biến thành bản thân, cũng rất lợi hại, dù sao, tích góp toàn bộ Vương quốc tàng thư khố thư tịch, nếu như toàn năng biến thành bản thân tri thức, coi như không có quá cao thâm lý luận, rất nhiều nghề nghiệp, cũng sẽ nghe ngóng không ít."),g("br"),g("br"),n._v("Kết quả... Cái gì đều vô dụng, liền như thế không còn?"),g("br"),g("br"),n._v("Có muốn hay không như thế hố?"),g("br"),g("br"),n._v("Tràn đầy chưa từ bỏ ý định, lại tìm thật lâu, trước sau không tìm được cái gọi là công pháp bí tịch, Trương Huyền đành phải lòng tràn đầy phiền muộn lui ra thư viện."),g("br"),g("br"),n._v("Ý thức mới vừa vừa rời đi, liền nghe đến một trận nghị luận truyền vào lỗ tai."),g("br"),g("br"),n._v("“Ta cảm thấy Trương lão sư không phải tu luyện!”"),g("br"),g("br"),n._v("“Không phải tu luyện đang làm gì? Con mắt đóng chặt, một chút động tĩnh đều không có? Chẳng lẽ ngủ?”"),g("br"),g("br"),n._v("“Nếu như là tu luyện mà nói, xung quanh chắc là có linh khí hội tụ, có thể hiện tại một điểm đều không có!”"),g("br"),g("br"),n._v("“Không phải tu luyện, không phải ngủ, chẳng lẽ Trương lão sư... Trương lão sư, ngươi đừng đi, ta luyến tiếc ngươi...”"),g("br"),g("br"),n._v("Còn chưa kịp nói chuyện, liền bị một tên béo ôm lấy, tiếp theo bị lắc choáng váng đầu hoa mắt."),g("br"),g("br"),n._v("Đừng đi, đi ngươi muội a, ta chỉ là ý thức tiến vào thư viện, quá mức chuyên tâm, không chú ý bên ngoài mà thôi, làm sao đến trong miệng ngươi cùng qua đời như thế?"),g("br"),g("br"),n._v("Trương Huyền xạm mặt lại, suýt chút nữa nổi khùng."),g("br"),g("br"),n._v("“Ngậm miệng!”"),g("br"),g("br"),n._v("Một tiếng quát lạnh, Trương Huyền mở mắt ra."),g("br"),g("br"),n._v("“A, Trương lão sư, ngươi không chết? Quá tốt rồi, ta còn tưởng rằng ngươi trong mộng Tiên Du...”"),g("br"),g("br"),n._v("Nhìn thấy sắc mặt từ từ biến thanh Trương lão sư, Viên Đào âm thanh càng ngày càng nhỏ."),g("br"),g("br"),n._v("Vừa nãy mấy người tiến vào lớp học, liền thấy Trương lão sư ngồi ở trên cái băng con mắt đóng chặt, không nhúc nhích, vốn đang cho rằng là nghỉ ngơi không làm sao lưu ý, có thể đợi hơn mười phút, dĩ nhiên không có bất kỳ phản ứng nào, thậm chí hô vài tiếng đều không có động tĩnh, lúc này mới hoảng sợ thần."),g("br"),g("br"),n._v("Từng cái từng cái sốt ruột vây lại đây, lần này xuất hiện vừa nãy tình cảnh đó."),g("br"),g("br"),n._v("Đang muốn dạy dỗ cái này không giữ mồm giữ miệng ngốc tử Viên Đào, liền thấy Trịnh Dương đi tới: “Lão sư, Vương Siêu lão sư cùng Vương Sùng đại sư ở ngoài cửa chờ đợi hơn nửa ngày rồi, có muốn hay không gặp gỡ?”"),g("br"),g("br"),n._v("“Vương Siêu? Vương Sùng? Để bọn họ vào đi!” Trương Huyền khoát tay áo một cái."),g("br"),g("br"),n._v("“Vương Sùng mang theo con bất hiếu Vương Siêu bái kiến Trương lão sư...”"),g("br"),g("br"),n._v("Vừa vào cửa, Vương Sùng liền hạ thấp tư thái."),g("br"),g("br"),n._v("Trương Huyền biết hắn nhất định là vì con trai xin lỗi, vẫn chưa nói thêm cái gì."),g("br"),g("br"),n._v("Vị này Vương Siêu tuy rằng nhiều lần nhằm vào bản thân, nhưng không có tạo thành tính thực chất ảnh hưởng, kỳ thực nói trắng ra, của hắn bản chất không hỏng, sở dĩ khắp nơi nhằm vào, là vì thay bạn tốt ra mặt."),g("br"),g("br"),n._v("  Liền Lục Tầm cũng có thể tha thứ, cái tên này liền đương nhiên không tính là gì."),g("br"),g("br"),n._v("Cho tới Vương Siêu, vốn đang ôm ấp địch ý, nghe được Trương Huyền cho phép Vương Sùng đem Thiên Đạo thương pháp truyền thụ bản thân, lập tức kích động toàn thân run rẩy."),g("br"),g("br"),n._v("Thiên Đạo thương pháp mạnh mẽ, trước tại chủ tịch vị đã xem rõ rõ ràng ràng, lòng ngứa ngáy khó nhịn, đạt được cho phép học tập hứa hẹn, đối với vị này Trương lão sư oán khí, cũng tất cả đều hóa thành khâm phục cùng cảm kích."),g("br"),g("br"),n._v("Hai người rời đi, Vương Hoằng tộc trưởng, Bạch Minh tộc trưởng mấy người lại nhao nhao bái phỏng, tiếp đó, Triệu Nhã mấy người trợn mắt ngoác mồm bên dưới, viện trưởng, Điền lão, Âu Dương hội trưởng, Thẩm Bích Như lão sư, Hàn Quỳnh lão sư..."),g("br"),g("br"),n._v("Trước hẻo lánh, liền cái Quỷ Ảnh đều không người đến chật hẹp phòng học, phảng phất đã biến thành toàn bộ học viện trung tâm, từng cái từng cái thân phận cao hơn, tại Thiên Huyền học viện nhân vật hô phong hoán vũ, tất cả đều chạy tới bái kiến."),g("br"),g("br"),n._v("Trương Huyền chán ghét ứng phó những cái này, bất quá người khác chuyên lại đây, lại không thể đuổi đi, đành phải nhắm mắt."),g("br"),g("br"),n._v("Bất quá, may là những người này cũng tựa hồ biết hắn có chút không thích, tất cả đều không lưu lại bao lâu liền cáo từ."),g("br"),g("br"),n._v("“Trương Huyền, ngươi có phải là muốn rời khỏi Hồng Thiên học viện?”"),g("br"),g("br"),n._v("Cuối cùng tiến vào Thẩm Bích Như, lưu lại một hồi, rốt cục không nhịn được, mở miệng hỏi."),g("br"),g("br"),n._v("Vốn là, nàng đảm nhiệm học viện đệ nhất mỹ nữ, giảng bài tốt, hào quang vô hạn, cùng loại này mất hết tên tuổi đê cấp giáo sư, là không hề có quen biết gì, ai biết Trương Huyền lực lượng mới xuất hiện, ngăn ngắn hai mươi ngày không tới, liền xông ra lớn như vậy danh tiếng, lại thành tam đại danh sư sư huynh."),g("br"),g("br"),n._v("Như vậy tiền đồ vô lượng, Hồng Thiên học viện loại địa phương nhỏ này, khẳng định cũng lại không chứa được, sớm muộn muốn càng chạy càng xa."),g("br"),g("br"),n._v("Rõ ràng những cái này, trong lòng hơi chua xót."),g("br"),g("br"),n._v("Hai người tuy rằng chỉ là quan hệ đồng nghiệp, nhưng trước mắt người thanh niên này nhưng là cái thứ nhất dẫn tới nàng hiếu kỳ cùng chú ý người, tựu cái này dạng rời đi mà nói, ít nhiều có chút không muốn."),g("br"),g("br"),n._v("“Ừm!”"),g("br"),g("br"),n._v("Không biết nữ hài trong lòng hỗn độn ý nghĩ, Trương Huyền gật gật đầu."),g("br"),g("br"),n._v("Sở dĩ ở lại học viện, là bởi vì này mấy cái học viên, hiện ở tại bọn hắn đều trưởng thành, bản thân cũng bắt được minh tinh giáo sư tư cách cùng học đồ thân phận, thực lực càng là đạt đến Thông Huyền cảnh đỉnh phong, cũng sẽ ra đi vòng vòng, khắp nơi xem một cái."),g("br"),g("br"),n._v("Hơn nữa, quan trọng nhất chính là hắn muốn mau sớm giải quyết trong cơ thể độc khí."),g("br"),g("br"),n._v("Không phải vậy, vẫn mang theo viên bom hẹn giờ, đổi làm ai cũng sẽ khó chịu, cũng không muốn mới vừa sống lại không bao lâu, còn chưa kịp hưởng thụ cuộc sống mới, liền lại lần nữa bỏ mình."),g("br"),g("br"),n._v("“Cái kia... Ngươi muốn đi đâu? Bắc Vũ Vương quốc?”"),g("br"),n._v(" Nghe được xác nhận, Thẩm Bích Như không nhịn được hỏi."),g("br"),g("br"),n._v("Tuy rằng cảm thấy không muốn, nhưng cũng biết, trước mắt người thanh niên này thiên tài như thế, sau đó giữa hai người chênh lệch sẽ càng lúc càng lớn, gặp nhau càng ngày càng nhỏ, lại không có gặp nhau khả năng."),g("br"),g("br"),n._v("Chung quy là người của hai thế giới."),g("br"),g("br"),n._v("“Không phải, ta nghĩ đi Thiên Vũ Vương quốc!” Trương Huyền nói."),g("br"),g("br"),n._v("Thiên Vũ Vương quốc Hồng Liên sơn mạch, rất có thể có thể nắm giữ Độc điện, nếu rời đi Thiên Huyền, đương nhiên phải cái thứ nhất đi tìm một chút xem một cái."),g("br"),g("br"),n._v("“Thiên Vũ nhất đẳng Vương quốc? Xa như vậy?” Thẩm Bích Như sững sờ: “Hai cái Vương quốc tuy nói sát bên, trên thực tế lại cách xa nhau không dưới mấy vạn km, ngươi làm sao vượt qua?”"),g("br"),g("br"),n._v("“Ta cũng đang suy nghĩ...” Trương Huyền chần chờ."),g("br"),g("br"),n._v("Thông qua xem tàng thư khố đất lý đồ sách, biết hắn vị trí đại lục, rộng lớn vô ngần, không biết đến cùng bao la đến mức nào."),g("br"),g("br"),n._v("Đối phương nói không sai, Thiên Vũ Vương quốc tuy rằng nghe tới cùng Thiên Huyền sát bên, trên thực tế lại cách xa nhau không dưới mấy vạn km, coi như nhanh nhất tuấn mã, không có thời gian mấy tháng, đều đừng hòng đến."),g("br"),g("br"),n._v("Nếu như không phải xa như vậy, Thiên Huyền loại địa phương nhỏ này, khẳng định sớm đã bị mạnh mẽ quốc gia nhét vào bản đồ, làm sao có khả năng mặc cho nó độc lập tồn tại."),g("br"),g("br"),n._v("“Này có cái gì cân nhắc, đi Thú đường cưỡi  không phải là được!” Một bên Hàn Quỳnh chậm rãi xoay người, cười khanh khách nói chen vào."),g("br"),g("br"),n._v("“Thú đường? Thú Thừa?”"),g("br"),g("br"),n._v("“Thú đường là cùng luyện đan sư công hội, luyện khí sư công hội như thế tồn tại, cũng gọi là thuần thú sư công hội, là do thuần thú sư liên hợp hình thành tổ chức, bọn họ thuần phục vô số phi hành man thú làm cho người ta xem như công cụ thay đi bộ, cũng chính là cái gọi là Thú Thừa, tốc độ nhanh, có thể ngày đi vạn dặm. Nơi này khoảng cách Thiên Vũ Vương quốc tuy rằng xa, nhưng đổi làm thú vật thừa, cũng là thời gian vài ngày mà thôi.”"),g("br"),g("br"),n._v("Hàn Quỳnh nói."),g("br"),g("br"),n._v("“Ồ?” Trương Huyền ánh mắt sáng lên."),g("br"),g("br"),n._v("Này chẳng phải cùng kiếp trước máy bay như thế?"),g("br"),g("br"),n._v("“Hàn Quỳnh, không biết không nên nói bậy!” Thẩm Bích Như liếc bạn tốt một chút: “Ngươi nói loại này thiểm điện Thú Thừa, là tốc độ nhanh nhất, mỗi một đầu, có người nói đều nắm giữ cấp bậc tông sư thực lực, toàn bộ Thú đường cũng chỉ có một đầu, chỉ có rất cao thân phận danh sư, hoặc là chính thức thuần thú sư mới có tư cách cưỡi, chúng ta bình thường cưỡi những cái kia, tốc độ rất chậm, một ngày có thể bay cái hai, ba ngàn km, thế là tốt rồi.”"),g("br"),g("br"),n._v("“Ân, Thẩm lão sư nói không sai, thiểm điện Thú Thừa xác thực không phải cái gì đều có thể cưỡi, coi như là danh sư học đồ, đều không có tư cách!” Hàn Quỳnh gật gù."),g("br"),g("br"),n._v("Trương Huyền gật đầu đồng ý, nếu như tốc độ như thế này nhanh, người nào đều có thể cưỡi, cũng là lộ ra không ra đặc quyền, không dây dưa vấn đề này tiếp tục hỏi."),g("br"),g("br"),n._v("“Thiên Huyền Vương quốc có Thú đường sao?”"),g("br"),g("br"),n._v("Vương thành gặp qua luyện đan sư công hội, luyện khí sư công hội, giáo sư công hội, còn chưa từng nghe nói Thú đường."),g("br"),g("br"),n._v("Mặc dù nặng sinh phía sau, tại Vương thành loanh quanh thời gian không lâu, nhưng tiền thân thế nhưng ở đây sinh trưởng ở địa phương, nếu như thật sự có, khẳng định cũng đã sớm nghe nói, làm sao có khả năng kiến thức nông cạn, tin tức gì đều chưa từng nghe tới?"),g("br"),g("br"),n._v("“Là có Thú đường, bất quá, không ở Vương thành, mà là tại cách đó không xa bên trong dãy núi!”"),g("br"),g("br"),n._v("Thẩm Bích Như nhẹ nhàng nở nụ cười: “Ngươi cũng biết, man thú yêu thích không bị ràng buộc sinh hoạt, núi rừng mới là chúng nó hoàn cảnh sinh tồn, Thú đường nếu như xây ở Vương thành, cả ngày có man thú bay tới bay lui, không ít người e sợ đều sẽ dọa sợ!”"),g("br"),g("br"),n._v("“Cũng đúng!” Trương Huyền gật đầu."),g("br"),g("br"),n._v("Man thú hung mãnh, nếu như không có đủ lớn hoàn cảnh, vẫn nuôi nhốt ở trong lồng, e sợ đều không sống nổi mấy ngày."),g("br"),g("br"),n._v("Vậy thì cùng thỏ rừng, lợn rừng loại hình rất khó nuôi trong nhà như thế."),g("br"),g("br"),n._v("“Làm sao, muốn đi tìm Thú đường? Có muốn hay không ta mang ngươi tới? Ta thế nhưng đi qua nhiều lần, đối với Thú đường vị trí biết đến rất rõ ràng, cũng có người quen tại cái kia, mang ngươi tới mà nói, có lẽ có thể giúp ngươi tìm tới rất tốt phi hành man thú.”"),g("br"),g("br"),n._v("Hàn Quỳnh lão sư khẽ mỉm cười."),g("br"),g("br"),n._v("“Hừ!” Nhìn thấy bạn tốt như vậy trần trụi lời nói, Thẩm Bích Như hơi nhướng mày: “Trương lão sư, đừng nghe nàng nói bậy, ngươi thật muốn đi Thú đường, ta ngược lại thật ra có thể mang ngươi tới!”"),g("br"),g("br"),n._v("“Khả năng còn muốn phiền phức Thẩm lão sư!” Đối với vị này giống như yêu tinh bình thường Hàn lão sư, Trương Huyền cũng có chút đau đầu, không nhịn được ôm quyền."),g("br"),g("br"),n._v("Đối lập về phía nàng, vẫn cảm thấy cùng Thẩm lão sư cùng nhau so sánh thoải mái."),g("br"),g("br"),n._v("Lại nói, bất kể nói thế nào, hai người đều tính cả tương đối quen thuộc."),g("br"),g("br"),n._v("“Được!” Thẩm Bích Như gật gù: “Vậy chúng ta lúc nào xuất phát? Ta tốt sớm chuẩn bị một chút?”"),g("br"),g("br"),n._v("“Ta ngày mai có một trận công khai giờ học, lên xong liền đi!”"),g("br"),g("br"),n._v("Trầm tư một chút, Trương Huyền nói."),g("br"),g("br"),n._v("Trong cơ thể độc khí vấn đề, càng sớm giải quyết càng tốt, nếu quyết định muốn rời khỏi, cũng không cần phải băn khoăn."),g("br"),g("br"),n._v("“Tốt lắm, ta trước hết đi chuẩn bị!”"),g("br"),g("br"),n._v("Thẩm lão sư gật gù, cùng Hàn Quỳnh hai người chuyển thân rời đi."),g("br"),g("br"),n._v("Vừa rời đi lớp học, Thẩm Bích Như không nhịn được nhìn về phía bên cạnh Hàn Quỳnh: “Hàn lão sư, ngươi sẽ không thật sự đối với Trương lão sư có ý tứ chứ?”"),g("br"),g("br"),n._v("“Ta?” Hàn Quỳnh cười khanh khách nhìn sang: “Lời này chắc là ta hỏi ngươi chứ? Vừa nãy ta chỉ là thử xem, xem ngươi đến cùng làm sao phản ứng, không nghĩ tới, trực tiếp đáp ứng muốn dẫn hắn đi... Hì hì, này không phải là chúng ta vị này Thẩm đại hoa khôi của trường, băng sơn mỹ nhân chắc là làm ra sự tình đi!”"),g("br"),g("br"),n._v("“Ta...” Thẩm Bích Như khuôn mặt đỏ lên."),g("br"),g("br"),n._v("“Ta biết ngươi khả năng đối với hắn có hảo cảm, nói thật, đàn ông ưu tú như vậy, ai cũng yêu thích, bất quá...”"),g("br"),g("br"),n._v("Hàn Quỳnh lắc lắc đầu, không còn trước ngả ngớn, mà là trong đôi mắt đẹp mang theo nghiêm nghị."),g("br"),g("br"),n._v("“Nắm giữ thực lực và thiên phú, còn có thể không tính toán được mất, ẩn nhẫn lâu như vậy... Chỉ cần không ngã xuống, sớm muộn muốn một tiếng hót lên làm kinh người, náo động thiên hạ!”"),g("br"),g("br"),n._v("“Người như thế...”"),g("br"),g("br"),n._v("“Chúng ta không xứng với!”"),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-239"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-241"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);