(window.webpackJsonp=window.webpackJsonp||[]).push([[1459],{1814:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-235-huyen-lac-son-mach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-235-huyen-lac-son-mach"}},[n._v("#")]),n._v(" Chương 235: Huyền Lạc sơn mạch")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Làm sao?”"),t("br"),t("br"),n._v("Thấy ba người như là choáng váng như thế, Trương Huyền không nhịn được cúi đầu nhìn lại, nhìn chút bên dưới cũng là sững sờ."),t("br"),t("br"),n._v("Trước ngọc thạch bên trên xuất hiện mấy cái rõ ràng con số 5. 1."),t("br"),t("br"),n._v("“5. 1?”"),t("br"),t("br"),n._v("Lần này, không những những người khác loại vẻ mặt này, liền ngay cả Trương Huyền cũng sắp hôn mê."),t("br"),t("br"),n._v("Hắn ngày hôm qua đo rất nhiều lần, cao nhất thời điểm 0. 1, làm sao một hồi biến thành 5. 1?"),t("br"),t("br"),n._v("Chẳng lẽ..."),t("br"),t("br"),n._v("“Cái kia trang sách vàng óng, một hồi để tâm cảnh của ta mức độ gia tăng rồi... 5. 0?”"),t("br"),t("br"),n._v("Khóe miệng kéo."),t("br"),t("br"),n._v("Con ngươi sắp rơi trên mặt đất."),t("br"),t("br"),n._v("Sớm biết Thiên Đạo chi sách rất trâu bò, chỉ là nằm mơ đều không nghĩ tới, như thế trâu bò!"),t("br"),t("br"),n._v("Cái gì tâm cảnh công pháp tu luyện, cái gì Minh Lý Ngọc Tinh, tại đồ chơi này trước mặt đều là cặn bã."),t("br"),t("br"),n._v("Một hồi liền để tâm cảnh gia tăng rồi 5. 0..."),t("br"),t("br"),n._v("Truyền đi tuyệt đối có thể đem người dọa chết tươi."),t("br"),t("br"),n._v("“Mức độ là 3 có tư cách sát hạch nhất tinh danh sư, mức độ đạt đến 6, có thể sát hạch nhị tinh... Vừa lĩnh ngộ tâm như chỉ thủy liền đạt đến 5. 1, e sợ chỉ muốn học phương pháp tu luyện, dùng không được mấy năm, liền nhị tinh danh sư đều có thể thi!”"),t("br"),t("br"),n._v("Lưu Lăng mấy người từ trong khiếp sợ khôi phục như cũ, từng cái từng cái kích động sắc mặt đỏ lên."),t("br"),t("br"),n._v("Bọn họ mấy người, mới vừa lĩnh ngộ tâm như chỉ thủy thời điểm, mức độ bất quá 1. 5 khoảng chừng, trước mắt cái tên này nhưng là 5. 1, thiên phú như thế, chỉ phải cố gắng tu luyện, dùng không được mấy năm, sẽ đem bọn họ xa xa bỏ lại đằng sau, liền nhị tinh danh sư cũng có thể sát hạch."),t("br"),t("br"),n._v("Quả nhiên, thiên tài cùng phàm nhân là có khoảng cách."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Đang đang khiếp sợ, liền thấy Lục Tầm tách ra đoàn người, đi tới trước mặt."),t("br"),t("br"),n._v("Trước Trương Huyền giảng bài, hắn cũng không tới nghe, cũng không phải là không tôn trọng, mà là đến đột phá bước ngoặt, không cho phân tâm."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Nhìn hắn xuất hiện, Lưu Lăng mấy người tất cả đều nhìn sang."),t("br"),t("br"),n._v("“Lão sư ngày hôm qua cùng ta nói, ta chính là quá tốt mạnh, quá để ý phụ thân cho ta áp lực, mới không cách nào đột phá tâm như chỉ thủy. Suy đi nghĩ lại, bỗng nhiên tỉnh ngộ, cùng phụ thân hòa hảo như lúc ban đầu, quả nhiên phủ đầy bụi tâm cảnh bắt đầu buông lỏng, cuối cùng... Thành công đột phá!”"),t("br"),t("br"),n._v("Nói đến đây, Lục Tầm trong mắt lại lần nữa khôi phục tự tin."),t("br"),t("br"),n._v("Cho tới nay, hắn tâm cảnh đều không có cách nào tiến bộ, không đạt tới tâm như chỉ thủy, vốn tưởng rằng là còn không tích lũy đến, trải qua Lưu sư chỉ điểm mới rõ ràng, là hắn quá nhiều chấp nhất."),t("br"),t("br"),n._v("Không nghĩ tới cải thiện phụ tử quan hệ sau đó, quả nhiên được toại nguyện liền đột phá!"),t("br"),t("br"),n._v("Hai mươi mấy tuổi liền đạt đến tâm như chỉ thủy... Toàn bộ Thiên Huyền Vương quốc, e sợ đều được xem là phá kỷ lục đi!"),t("br"),t("br"),n._v("Vẫn bị trước mắt cái này Trương Huyền đả kích không thở nổi, hiện tại trước tiên đột phá tâm như chỉ thủy, tổng lẽ ra có thể mở mày mở mặt một phen!"),t("br"),t("br"),n._v("Thân thể ưỡn lên thẳng tắp, Lục Tầm trên mặt mang theo ôn hoà nụ cười."),t("br"),t("br"),n._v("“Đo qua sao? Mức độ bao nhiêu?”"),t("br"),t("br"),n._v("Nghe được hắn cũng đột phá, Lưu Lăng tràn đầy cao hứng, không nhịn được hỏi."),t("br"),t("br"),n._v("“Đã đo qua!” Lục Tầm nhẹ nhàng nở nụ cười, trong mắt hưng phấn khó có thể che lấp: “Lão sư đã nói, mới vừa đạt đến tâm như chỉ thủy, liền có thể đạt đến 1. 0 trở lên, biểu thị sau đó có 50% cơ sẽ trở thành danh sư, đạt đến 1. 5 trở lên, có 70% cơ sẽ thành công, mà ta là...”"),t("br"),t("br"),n._v("Dừng lại một chút, nhìn quanh một vòng: “Là 1. 8!”"),t("br"),t("br"),n._v("Hai tay chắp ở sau lưng, Lục Tầm vốn tưởng rằng nói xong thành tích của chính mình, sẽ làm ba sư doạ giật mình, hưng phấn khó tự kiềm chế, dù sao hắn số này đáng giá, thế nhưng so ba sư khi còn trẻ còn cao hơn."),t("br"),t("br"),n._v("Ai biết bọn họ sau khi nghe xong, đồng thời “Ồ!” Một tiếng, liền không tiếp tục để ý, lại lần nữa nhìn về phía Trương Huyền, đầy mặt kịch liệt."),t("br"),t("br"),n._v("Thấy mình như thế thành tích cao, lại có thể cũng bị gạt sang một bên, Lục Tầm tràn đầy phiền muộn."),t("br"),t("br"),n._v("Theo lý thuyết, không phải là nghe được thành tích sau, hưng phấn khó có thể tự chế, vây lại đây cố gắng khích lệ một phen sao?"),t("br"),t("br"),n._v("Làm sao vây nhốt Trương lão sư?"),t("br"),t("br"),n._v("Trong lòng kỳ quái, không nhịn được nhìn về phía trước, đúng dịp thấy Trương Huyền trong tay Văn Lý Thạch, nhìn chút bên dưới, toàn thân cứng đờ."),t("br"),t("br"),n._v("“5. 1?”"),t("br"),t("br"),n._v("Thân thể kéo, Lục Tầm khóc."),t("br"),t("br"),n._v("Giời ạ, này không phải đưa mặt đi tới chịu đòn ư..."),t("br"),t("br"),n._v("Thương xót ta còn dào dạt đắc ý thời gian dài như vậy, vốn tưởng rằng có thể cố gắng biểu hiện..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" bao la vô biên, mọc đầy cao to cây cối cùng thảm thực vật, lại thêm lên man thú hoành hành, không thực lực, rất khó đi đi."),t("br"),t("br"),n._v("“Con đường này, ta đã tới một lần, phi thường bí ẩn, không tìm được mà nói, một khi lạc lối tại núi rừng, lại muốn đi ra liền khó khăn!”"),t("br"),t("br"),n._v("Thẩm Bích Như cười đi ở phía trước dẫn đường."),t("br"),t("br"),n._v("Trương Huyền theo sát ở phía sau."),t("br"),t("br"),n._v("Công khai giờ học nói, đem Triệu Nhã, Trịnh Dương mấy người bàn giao cho tam đại danh sư, để bọn họ hỗ trợ mang tới Thiên Vũ Vương thành, Trương Huyền lại không có quá nhiều do dự, rồi cùng Thẩm Bích Như tiến vào Thú đường vị trí Huyền Lạc sơn mạch."),t("br"),n._v("Trong ngọn núi man thú hoành hành, cây cỏ rất nhiều, nếu không là Thẩm lão sư trước đã tới, hắn thật hoài nghi có thể hay không lạc đường, triệt để rơi vào trong đó, lại không cách nào rời đi."),t("br"),t("br"),n._v("Liên tục đi rồi ba ngày, cả người đều có chút hư thoát."),t("br"),t("br"),n._v("“Thú đường vị trí cũng ẩn giấu quá sâu đi, chẳng lẽ bọn họ không cần bình thường sinh hoạt?” Trương Huyền tràn đầy kỳ quái."),t("br"),t("br"),n._v("Chỉ cần là người, khẳng định cần gặp nhau, cần muốn mua đủ loại vật tư, đồ ăn, Thú đường giấu đến nơi núi rừng sâu xa, mua cái đồ vật cũng không tiện a!"),t("br"),t("br"),n._v("“Thú đường người có phi hành man thú, chúng ta đi ba ngày, đối với bọn họ tới nói, liền nửa canh giờ đều dùng không được, không tính là gì.” Thẩm Bích Như lắc đầu."),t("br"),t("br"),n._v("Nàng lần thứ nhất cùng sau lưng người khác đi Thú đường thời điểm, cũng có cái cảm giác này, chân chính sau khi đến, mới biết, loại ý nghĩ này buồn cười dường nào."),t("br"),t("br"),n._v("“Hơn nữa, đến địa phương, ngươi mới sẽ biết, Thú đường vị trí, khoảng cách chung quanh Bắc Vũ Vương quốc, Hàn Vũ Vương quốc chờ Vương thành, cũng không tính là quá xa, mỗi ngày đều có không ít người đến đây, vật tư loại hình, không hề thiếu.” Thẩm Bích Như nói tiếp."),t("br"),t("br"),n._v("Vương quốc tàng thư khố có liên quan với chung quanh quốc gia đất đồ, mấy ngày nay Trương Huyền cũng chuyên môn nghiên cứu một lần, phát giác xác thực cách xa nhau đều không xa, nếu không là cái này tự nhiên Huyền Lạc sơn mạch ngăn trở, Thiên Huyền Vương thành những năm này cũng sẽ không yên tĩnh như thế."),t("br"),t("br"),n._v("“Huyền Lạc sơn mạch có nhiều như vậy man thú, vì sao người tu luyện không thu phục một hai đầu, nhất định phải đi Thú đường?”"),t("br"),t("br"),n._v("Sơn mạch có không ít man thú, bọn họ ba ngày nay liền gặp phải vài nhóm, may mà thực lực đều không kém, mới không quá đáng lo."),t("br"),t("br"),n._v("Nếu có nhiều như vậy man thú, hoàn toàn có thể thuần phục một hai đầu, làm gì còn muốn đi Thú đường phiền toái như vậy?"),t("br"),t("br"),n._v("“Trước ta cũng có tương tự ý nghĩ, trước tiên không nói man thú dã tính lớn như vậy, tốt không dễ thuần phục, chỉ nói riêng có thể chở người phi hành man thú, chúng ta này một đường cũng chưa từng thấy đi!” Thẩm Bích Như cười khẽ."),t("br"),t("br"),n._v("Trương Huyền chần chờ một chút, không nhịn được gật đầu."),t("br"),t("br"),n._v("Có thể chở người phi hành man thú, ít nhất cũng phải so với người lớn hơn nhiều mới được, bọn họ mấy ngày nay gặp phải, đều là một ít có thể chạy trốn thú vật, hơn nữa cái đầu cũng không tính là quá to lớn."),t("br"),t("br"),n._v("“Loại này phi hành man thú ít ỏi, hơn nữa đánh không lại có thể bay đi, muốn tóm lấy rất khó! Đương nhiên, này còn không phải trọng yếu nhất, nếu như ngươi dùng võ lực để cho thần phục, vạn nhất nó giả vờ nghe theo, trên không trung đổi ý, há không xui xẻo?”"),t("br"),t("br"),n._v("Thẩm Bích Như nói."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Trương Huyền rụt cổ một cái."),t("br"),t("br"),n._v("Phi hành man thú không giống với cái khác, mang theo ngươi bay trên không trung, nếu như không phải thật tâm thực lòng thần phục, chỉ cần trên không trung nghiêng người, cơ bản chẳng khác nào chết chắc rồi."),t("br"),t("br"),n._v("“Thuần thú sư đảm nhiệm đặc thù nghề nghiệp, có thể bảo đảm để man thú trăm phần trăm nghe lời, sẽ không xuất hiện tình huống như thế, lúc này mới chịu đến tôn sùng, trở thành thượng cửu lưu nghề nghiệp bên trong khá cao một trong. Không phải vậy, man thú phản bội, thế nhưng vô cùng nguy hiểm.”"),t("br"),t("br"),n._v("Người tu luyện coi như thực lực đạt đến Tông Sư cảnh, trên không rớt xuống cũng là một con đường chết."),t("br"),t("br"),n._v("Vì lẽ đó, không thuần thú sư thủ đoạn, tốt nhất không muốn tìm phi hành man thú phiền phức, coi như dùng cường lực thủ đoạn thu phục một đầu, đối phương thật muốn hại người, trốn đều tránh không khỏi."),t("br"),t("br"),n._v("“Thuần thú sư cùng danh sư như thế, cũng chia làm rất nhiều Tinh cấp, cấp bậc càng cao, có thể thuần phục man thú cũng là càng cường đại! Trở thành chính thức thuần thú sư trước, tương tự cần sát hạch thuần phục thú học đồ!”"),t("br"),t("br"),n._v("Thấy hắn đối thuần thú sư biết đến không nhiều, Thẩm Bích Như giải thích: “Trước ngươi nhìn thấy Hàn Quỳnh, nàng kỳ thực chính là cái thuần phục thú học đồ, cho nên mới nói cho ngươi, Thú đường có người quen, ta lần trước tới đây, chính là cùng với nàng cùng một chỗ.”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền đáp một tiếng."),t("br"),t("br"),n._v("Vương quốc tàng thư khố liên quan với thuần thú sư ghi chép không hề nhiều, bất quá, những cơ sở này vẫn là biết đến, không tính là bí mật."),t("br"),t("br"),n._v("Thuần phục thú học đồ cùng luyện đan học đồ như thế, người sau học tập thuốc chủng loại dược tính dược lý, người trước thì cần muốn học tập man thú chủng loại, lực công kích, đủ loại tập tính..."),t("br"),t("br"),n._v("Chỉ có đem những cái này tất cả thuộc nằm lòng, gặp phải man thú mới có thể biết gốc biết rễ, có cơ hội thuần phục."),t("br"),t("br"),n._v("Bằng không, bay ra ngoài một đầu, biết cũng không biết, đừng nói thuần phục, làm không cẩn thận, còn có thể bởi vì không biết thực lực, bị đối phương chém giết."),t("br"),t("br"),n._v("Một ít hung mãnh man thú, cơ bản rất căm thù nhân loại."),t("br"),t("br"),n._v("“Chúng ta đi nhanh một chút đi, nơi này khoảng cách Thú đường, cũng sẽ không đến một ngày lộ trình, đã không xa!”"),t("br"),t("br"),n._v("Còn nói vài câu, Thẩm Bích Như cười cợt, hai người đang muốn tiếp tục hướng phía trước, liền nghe đến ngay phía trước một trận thanh âm huyên náo vang lên."),t("br"),t("br"),n._v("Ngẩng đầu nhìn lại, liền thấy cách đó không xa xuất hiện một đám nhân ảnh."),t("br"),t("br"),n._v("Tổng cộng bảy, tám cái khoảng chừng, ngồi xổm cùng nhau, tựa hồ đang thương nghị chuyện gì, phần lớn đều tại Ích Huyệt cảnh, chỉ có một cái hai mươi bảy, tám thanh niên là Đỉnh Lực cảnh đỉnh phong."),t("br"),t("br"),n._v("Tuy rằng thực lực thấp, nhưng tựa hồ là mấy người đầu lĩnh, nhất cử nhất động tự dẫn dắt phong độ."),t("br"),t("br"),n._v("“Người nào?”"),t("br"),t("br"),n._v("Hai người bọn họ không che lấp hành tung, phát giác đối phương, đối phương đương nhiên cũng là phát giác bọn họ, quát to một tiếng, đồng loạt rút ra binh khí."),t("br"),t("br"),n._v("Biết khoảng cách Thú đường tiếp cận, gặp phải người thuộc về bình thường, Thẩm Bích Như không hoang mang, bước lên trước: “Hai người chúng ta đến từ Thiên Huyền Vương thành, muốn đi Thú đường, đi ngang qua nơi đây.”"),t("br"),t("br"),n._v("“Muốn đi Thú đường?”"),t("br"),t("br"),n._v("Thanh niên liếc mắt nhìn vị mỹ nữ này giáo sư, con mắt không nhịn được sáng ngời."),t("br"),t("br"),n._v("Thẩm Bích Như có thể xưng là Hồng Thiên học viện đệ nhất mỹ nữ giáo sư, dung mạo không cần phải nói, tuy rằng ba ngày nay ăn gió nằm sương, có chút chật vật, vẫn như cũ khó nén phong thái."),t("br"),t("br"),n._v("“Chúng ta cũng phải trở về Thú đường, bất quá trước đó, muốn làm một chuyện, chỉ cần không quấy rầy chúng ta, đúng là có thể mang bọn ngươi cùng một chỗ trở lại!”"),t("br"),t("br"),n._v("Thanh niên khoát tay áo một cái."),t("br"),t("br"),n._v("“Không cần, Thú đường ở nơi nào, tự chúng ta đi qua là được!”"),t("br"),t("br"),n._v("Thẩm Bích Như lắc đầu, cùng Trương Huyền hai người tiếp tục đi đến phía trước."),t("br"),t("br"),n._v("“Đứng lại, phía trước các ngươi không thể đi!”"),t("br"),t("br"),n._v("Mới đi rồi hai bước, còn lại mấy người đồng thời hét lớn, ào ào ào, binh khí ra khỏi vỏ âm thanh lại vang lên."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-243"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-245"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);