(window.webpackJsonp=window.webpackJsonp||[]).push([[1460],{1815:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-236-tran-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-236-tran-phap"}},[n._v("#")]),n._v(" Chương 236: Trận pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Không thể đi? Đường này là của các ngươi?” Thẩm Bích Như sầm mặt lại."),t("br"),t("br"),n._v("Trương Huyền cũng quay đầu nhìn sang."),t("br"),t("br"),n._v("Chân khí của hắn tinh khiết, ẩn nấp thực lực liền ngay cả Lưu sư bọn người phát giác không được, mấy người này đương nhiên cũng nhìn không ra đến."),t("br"),t("br"),n._v("Đạt đến Thông Huyền cảnh đỉnh phong, Tông Sư không ra, đã không cái gì đáng sợ sợ, nếu như đám người kia thật muốn gây phiền phức, không ngại ra tay giáo huấn, giáo huấn."),t("br"),t("br"),n._v("Bất quá, còn không nói gì, liền thấy thanh niên bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Làm gì? Thả xuống! Không phải hết thảy sự tình đều có thể dùng võ lực giải quyết!”"),t("br"),t("br"),n._v("“Vâng!” Mọi người đem binh khí thu hồi."),t("br"),t("br"),n._v("“Hai vị không nên kinh hoảng, chúng ta không có ác ý! Thực không dám giấu giếm, tại hạ là là Hàn Vũ Vương quốc thất vương tử, Vân Đào. Đang tiến hành nhất tinh thuần thú sư sát hạch, hiện tại đã thông qua cửa thứ nhất, chỉ cần có thể thuần phục một đầu Ích Huyệt cảnh man thú, coi như hợp lệ!”"),t("br"),t("br"),n._v("Bước lên trước, thanh niên lộ ra nụ cười nhàn nhạt."),t("br"),t("br"),n._v("“Bọn họ sở dĩ ngăn cản hai vị, là bởi vì ta muốn thuần phục một đầu , cái tên này tốc độ cực nhanh, chúng ta đã liên tục lần theo hơn mười ngày, thật vất vả phát hiện tung tích, liền ở mặt trước không xa, vì lẽ đó, đặc biệt ở đây bố trí cạm bẫy, chờ nó tự chui đầu vào lưới. Không cho hai vị tiến lên, là sợ bởi vậy đánh rắn động cỏ, để ta hơn mười ngày nỗ lực giao cho hàng ngũ nước, vẫn xin xem xét! Như có mạo phạm, chờ bắt lấy con này thiết tiền báo, ta tự mình bồi tội!”"),t("br"),t("br"),n._v("“Cái kia... Chúng ta trước hết chờ một lát!”"),t("br"),t("br"),n._v("Thấy đối phương nói chuyện khách khí, nhìn Trương Huyền một chút, thấy hắn không phản đối, Thẩm Bích Như gật đầu đáp ứng."),t("br"),t("br"),n._v("Trương Huyền là người khác dễ nói chuyện, hắn cũng dễ nói."),t("br"),t("br"),n._v("Đối phương nói rõ lý do, thái độ thành khẩn, cũng là không sao cả."),t("br"),t("br"),n._v("Vừa vặn liên tục chạy đi, đi được mệt mỏi, nghỉ ngơi một chút cũng không có cái gì, còn có thể nhiều hỏi thăm một chút Thú đường tình huống."),t("br"),t("br"),n._v("“Đa tạ thông cảm!”"),t("br"),t("br"),n._v("Thấy bọn họ dừng lại, Vân Đào thoả mãn gật đầu, sắp xếp một tiếng, để mọi người tiếp tục bố trí cạm bẫy, bản thân thì đi tới Thẩm Bích Như trước mặt."),t("br"),t("br"),n._v("“Không biết hai vị xưng hô như thế nào?”"),t("br"),t("br"),n._v("“Tại hạ Thẩm Bích Như, đây là Trương Huyền!” Thẩm lão sư gật đầu."),t("br"),t("br"),n._v("“Hóa ra là Thẩm tiểu thư, hạnh ngộ hạnh ngộ!” Vân Đào gật gật đầu, tự động đem Trương Huyền xem nhẹ."),t("br"),t("br"),n._v("Cũng không trách hắn xem thường, Trương Huyền y phục chỉ là quán vỉa hè hàng, tu luyện Thiên Đạo Kim thân sau, da dẻ bóng loáng, xem ra cùng mười bảy, tám tuổi thiếu niên giống như vậy, mới nhìn, còn tưởng rằng là cái tuỳ tùng, không có chút đáng chú ý nào."),t("br"),t("br"),n._v("“Không biết tiểu thư đi Thú đường có chuyện gì? Không có gì bất ngờ xảy ra, tại hạ nhất định sát hạch thuần thú sư thành công, tại Thú đường, nói chuyện vẫn có chút phân lượng!”"),t("br"),t("br"),n._v("Cười nhạt nói, Vân Đào trong lời nói mang theo nồng đậm tự tin."),t("br"),t("br"),n._v("“Thất vương tử hảo ý chân thành ghi nhớ, chúng ta chỉ là thuê phi hành man thú, không tính phiền phức!” Thẩm Bích Như lắc đầu."),t("br"),t("br"),n._v("Đảm nhiệm Hồng Thiên học viện băng sơn nữ thần, đối với loại này cố ý đến gần, từ lâu rất quen thuộc, tùy tiện liền có thể lấp liếm cho qua."),t("br"),t("br"),n._v("“Tương phùng chính là duyên, Thẩm tiểu thư cũng không cần khách khí với ta, nếu như không có thuần thú sư chỉ điểm, chọn không tới tốt phi hành man thú, thế nhưng rất nguy hiểm. Không bằng như vậy, chờ ta bắt lấy Kim Thân Thiết Tiễn Báo trở lại Thú đường, tự mình là ngươi lựa chọn!”"),t("br"),t("br"),n._v("Vân Đào nói."),t("br"),t("br"),n._v("“Không cần!” Thẩm Bích Như xua tay."),t("br"),t("br"),n._v("“Thú đường phi hành man thú tuy rằng đều là thuần phục tốt, bình thường sẽ không xảy ra vấn đề, nhưng tương tự man thú, sự chịu đựng, ứng biến lực đều không giống nhau, nếu như không cố gắng chọn, trên không trung một khi gặp phải cái khác man thú công kích, rất dễ dàng xuất hiện sự cố, Thẩm tiểu thư liền không cần khách khí!”"),t("br"),t("br"),n._v("Khẽ vuốt ống tay áo, Vân Đào làm cho người ta một loại ngọc thụ lâm phong cảm giác, trong giọng nói mang theo quan tâm, thái độ thành khẩn."),t("br"),t("br"),n._v("Trước mắt cô bé này, bất luận khí chất, dung mạo đều là hắn gặp qua xinh đẹp nhất, đương nhiên phải cố gắng bày ra bản thân ưu tú, đánh động mỹ nhân tâm."),t("br"),t("br"),n._v("“Chuyện này...” Vốn còn muốn từ chối, vừa nghĩ tới là Trương lão sư muốn ngồi phi hành man thú, Thẩm Bích Như vẫn là không nhịn được gật gù: “Khả năng này liền phiền phức ngươi!”"),t("br"),t("br"),n._v("“Không kêu phiền phức, ta học tập thuần phục thú mười hai năm, Thú đường hầu như hết thảy man thú đều rõ ràng, chúng nó tập tính, sự chịu đựng, tuổi, tốc độ phản ứng... Đều rõ như lòng bàn tay. Yên tâm đi, ta giúp tiểu thư tuyển, khẳng định không thành vấn đề! Nếu như không phải xem ở chúng ta hữu duyên, coi như có người tiêu tốn của cải khổng lồ, cũng chưa chắc có thể mời được ta tự mình động thủ...”"),t("br"),t("br"),n._v("Thấy nàng đồng ý, Vân Đào ánh mắt sáng lên, nói càng ra sức: “Lần trước các ngươi Thiên Huyền thành một vị đại thần lại đây, muốn cho ta hỗ trợ, liền bị ta khéo léo cự tuyệt...”"),t("br"),t("br"),n._v("Thấy cái tên này vẫn trang bức, muốn lộ ra bản thân nhiều có năng lực, nhiều có tài cán, Trương Huyền thực sự nghe không vô, bất đắc dĩ lắc đầu một cái, hướng trước mấy người nhìn lại."),t("br"),t("br"),n._v("Bọn họ cái gọi là bố trí cạm bẫy, cũng không phải là đào hố, bên trên xếp đặt chút lá khô rơm rạ."),t("br"),t("br"),n._v("Loại này sơ cấp nhất cạm bẫy đối phó một ít dã thú còn có thể, man thú đã nắm giữ trí tuệ, làm như vậy, không những không bắt được, làm không cẩn thận còn có thể bị cho rằng đang làm nhục sự thông minh của nó, cùng với không chết không thôi."),t("br"),t("br"),n._v("Giương mắt nhìn lại, cùng Vân Đào cùng một chỗ này mấy cái Ích Huyệt cảnh cường giả, cầm trong tay từng cái từng cái màu xám tro lá cờ, dựa theo tính toán tốt phương vị, từng cây từng cây cắm vào đi."),t("br"),t("br"),n._v("“Lẽ nào là... Trận pháp?”"),t("br"),t("br"),n._v("Nhìn thấy hành động của bọn họ, một cái tên bốc lên đầu óc."),t("br"),t("br"),n._v("Trận pháp sư là thượng cửu lưu nghề nghiệp bên trong, khá cao, cùng luyện đan sư so ra đều không phân cao thấp, được xem là đứng đầu nghề nghiệp."),t("br"),t("br"),n._v("Tuy rằng Vương quốc tàng thư khố có liên quan với trận pháp ghi chép, lại đều nói chi không rõ, chỉ nói rõ là lợi hại, cụ thể làm sao bố trí, thế nào kích hoạt... Đều không có."),t("br"),t("br"),n._v("Dù sao Thiên Huyền Vương quốc chỉ là bất nhập lưu địa phương nhỏ, liền trận pháp sư công hội đều không có, bản thân biết tin tức, cũng là có hạn."),t("br"),n._v(" Cứ việc ghi chép thiếu, nhưng mấy người động tác cùng trong đó miêu tả bày trận hoàn toàn tương đồng, Trương Huyền vẫn là một chút nhận ra được."),t("br"),t("br"),n._v("Truyền thuyết, trận pháp một khi bố trí thành công, sẽ hình thành đặc thù khí tràng, xúc động gợi ra, liền sẽ lập tức xuất hiện đủ loại hiệu quả đặc biệt."),t("br"),t("br"),n._v("Tỷ như: Khốn trận có thể đem người nhốt lại, nhận biết không ra phương hướng không cách nào chạy trốn; Ảo trận có thể khiến người ta xuất hiện mê huyễn, không hiểu ở nơi nào; Sát trận càng là có thể đem người trực tiếp xoá bỏ, uy lực đáng sợ."),t("br"),t("br"),n._v("Trương Huyền lần thứ nhất gặp người bày trận, tràn đầy hiếu kỳ."),t("br"),t("br"),n._v("“Chỉ bằng những thứ đồ này, liền có thể nhốt lại cái kia cái gì Kim Thân Thiết Tiễn Báo?”"),t("br"),t("br"),n._v("Nhìn một hồi, phát giác cái gì đều xem không hiểu, bất đắc dĩ lắc đầu một cái."),t("br"),t("br"),n._v("Trận pháp cùng luyện đan, thư họa như thế, là một môn học cao thâm, không đi qua chuyên môn học tập, đừng nói có thể nhìn ra, e sợ những thứ đồ này đến cùng đặt tại này có ích lợi gì, đều đầu óc mơ hồ."),t("br"),t("br"),n._v("“Trương lão sư, làm sao?”"),t("br"),t("br"),n._v("Đang đang quan sát, liền nghe đến Thẩm Bích Như âm thanh âm vang lên, quay đầu lúc này mới phát giác, nàng tựa hồ cũng không chịu được đối phương tự yêu mình lời nói, đi tới bên cạnh."),t("br"),t("br"),n._v("“Há, xem bọn họ tại bố trí cái gì cạm bẫy!” Trương Huyền nói."),t("br"),t("br"),n._v("“Này thật giống là trận pháp, chúng ta Thiên Huyền Vương quốc không có trận pháp sư công hội, cũng không có tương quan thư tịch, đối cái này nghe ngóng không nhiều!” Thẩm Bích Như cũng nhận ra được."),t("br"),t("br"),n._v("“Đúng đấy, những cái này lá cờ nhỏ liền có thể hình thành trận pháp, xuất hiện mạnh mẽ uy lực, xác thực lợi hại!” Trương Huyền cảm khái."),t("br"),t("br"),n._v("“Có thể trở thành là thượng cửu lưu nghề nghiệp một trong, không có một cái đơn giản, truyền thuyết chính thức trận pháp sư bố trí ra trận pháp, liền nửa bước Tông Sư đều rất khó thoát thoát, làm không cẩn thận đều sẽ bị chém giết, bọn họ xem ra tùy tiện xuyên kỳ, trên thực tế khẳng định có đặc biệt kết cấu!” Thẩm Bích Như gật đầu."),t("br"),t("br"),n._v("“Ừm! Ngược lại cũng không hiểu, liền tùy tiện xem một cái!” Trương Huyền cười cợt."),t("br"),t("br"),n._v("Trận pháp ảo diệu, không có mấy năm nghiên cứu rất khó có thành quả, ngược lại cũng không hiểu, coi như xem cái mới mẻ."),t("br"),t("br"),n._v("“Tùy tiện xem một cái?” Vân Đào đi tới, đầu vung lên, mí mắt nghiêng nhìn sang, nhẹ nhàng nở nụ cười: “Trận pháp Hạo Nhiên đại khí, ảo diệu vô tận, ngươi nhìn hiểu sao?”"),t("br"),t("br"),n._v("“Ta là tay mơ này, chưa bao giờ nghiên cứu qua, xác thực không hiểu lắm!” Trương Huyền cũng không ngụy trang, một mặt thành khẩn: “Nghe khẩu khí, thất vương tử đối với trận pháp hiểu rất rõ?”"),t("br"),t("br"),n._v("“Cái kia là đương nhiên!”"),t("br"),t("br"),n._v("Vân Đào khóe miệng vung lên, lộ ra một cái ngạo nghễ vẻ mặt: “Nói thật cho ngươi biết đi, ta không những là thuần thú sư, còn làm việc hơn thời gian học tập trận pháp, là một vị thật trận pháp học đồ, đối không ít trận pháp đều có rất sâu nghiên cứu.”"),t("br"),t("br"),n._v("“Trận pháp học đồ?”"),t("br"),t("br"),n._v("Cùng Thẩm Bích Như liếc nhau một cái, hai người hơi kinh ngạc."),t("br"),t("br"),n._v("Sát hạch thuần thú sư, lại là trận pháp học đồ, hai loại thượng cửu lưu khá cao nghề nghiệp đều đang biết, không thể không nói, vị này Vân Đào tuy rằng người rắm thối chút, yêu thích khoe khoang, cũng xác thực có khoe khoang tiền vốn."),t("br"),t("br"),n._v("“Nếu không hiểu, ta liền giới thiệu cho các ngươi một chút!”"),t("br"),t("br"),n._v("Thấy hai người kinh ngạc, Vân Đào tràn đầy đắc ý, cười cợt đầu vung lên, rất nhiều chỉ điểm giang sơn tâm ý: “Bọn họ hiện tại bố trí cái này, kêu , lấy Thiên Cương ba mươi sáu tinh là trận cơ, Địa Sát bảy mươi hai tinh là trận kỳ, ẩn chứa Thanh Mộc thuộc tính, hội tụ tám pháp linh khí... Do đó hình thành đặc thù trận pháp, sức mạnh mười phần, uy thế vô tận.”"),t("br"),t("br"),n._v("“Thanh Mộc khốn trận? Liền những thứ này lá cờ nhỏ?”"),t("br"),t("br"),n._v("“Không sai! Trận kỳ chỉ là phụ trợ, kỳ thực, bố trí trận pháp, không những phương vị muốn làm tốt, không thể có chút sai lầm, quan trọng nhất chính là kích hoạt, chờ bọn hắn bố trí kỹ càng, ta sẽ xuất thủ dẫn động, một khi thành công, Kim Thân Thiết Tiễn Báo rơi vào trong đó, tuy không nguy hiểm đến tình mạng, nhưng cũng có thể để nó lên trời không đường, nhập địa không cửa!”"),t("br"),t("br"),n._v("Đang khi nói chuyện, mấy người đã đem rất nhiều trận kỳ dựa theo đặc biệt phương vị dọn xong."),t("br"),t("br"),n._v("Vân Đào nhẹ nhàng nở nụ cười, nhanh chân đi lên phía trước, bàn tay một trảo, lấy ra một cái kim sắc cờ nhỏ, nhẹ nhàng run lên, cắm vào hết thảy trận kỳ bên trong."),t("br"),t("br"),n._v("Ong!"),t("br"),t("br"),n._v("Một tiếng kêu khẽ, trận pháp như là bị trong nháy mắt khởi động, trước có thể thấy rõ ràng trận kỳ ẩn giấu ở xanh tươi cỏ xanh ở giữa, khó hơn nữa phát giác."),t("br"),t("br"),n._v("Nếu như không phải tận mắt đến, đều không thể tin được nơi này ẩn giấu một cái trận pháp."),t("br"),t("br"),n._v("“Lợi hại...”"),t("br"),t("br"),n._v("Đang cảm khái, Trương Huyền muốn nói cái gì, đột nhiên đầu óc chấn động, thư viện xuất hiện một quyển sách."),t("br"),t("br"),n._v("Đầu tiên là nghi hoặc, lập tức hiểu được."),t("br"),t("br"),n._v("Vân Đào là trận pháp sư học đồ, bố trí kích hoạt trận pháp, rồi cùng luyện đan sư luyện đan, võ giả tu luyện võ kỹ là một cái ý tứ, tương tự có thể dẫn tới thư viện biến hóa."),t("br"),t("br"),n._v("Tinh thần hơi động, thư tịch xuất hiện ở trước mắt, chỉ thấy bên trên viết cái bốn chữ lớn Thanh Mộc khốn trận."),t("br"),t("br"),n._v("Tiện tay mở ra, bên trên ghi chép trong đó thiếu sót."),t("br"),t("br"),n._v("“Thanh Mộc khốn trận, mộc thuộc tính trận pháp, lấy Thanh Mộc là trận kỳ bày trận, nắm giữ phong cấm, vây nhốt công hiệu, 300 năm trước, nhị tinh trận pháp Tông Sư Đào Nguyên sáng chế, thiếu sót mười bảy chỗ, phân biệt là...”"),t("br"),t("br"),n._v("“Trước mắt là giản lược bản, thiếu sót bảy mươi hai nơi, phân biệt là...”"),t("br"),t("br"),n._v("Đem thư tịch lật lên xem xong, Trương Huyền hơi kinh ngạc, một mặt quái lạ nhìn về phía bên cạnh một bộ dương dương tự đắc, đệ nhất thiên hạ dáng dấp thất vương tử."),t("br"),t("br"),n._v("“Ngươi xác định... Trận pháp này thật có thể nhốt lại Kim Thân Thiết Tiễn Báo?”"),t("br"),t("br"),n._v("(Thực sự quá bận, phỏng chừng ngày mai bạo phát phải hủy bỏ. Ô u Ô u, muốn lo lắng tang. Cầu an ủi!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-244"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-246"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);