(window.webpackJsonp=window.webpackJsonp||[]).push([[1461],{1816:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-237-sang-mot-goc-choi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-237-sang-mot-goc-choi"}},[n._v("#")]),n._v(" Chương 237: Sang một góc chơi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Cái kia là đương nhiên! Trận pháp này là ta từ trận pháp sư công hội, tiêu tốn to lớn đánh đổi mua mà đến, liền Thông Huyền cảnh sơ kỳ cường giả đều có thể nhốt lại, đừng nói một cái nho nhỏ Ích Huyệt cảnh man thú!”"),t("br"),t("br"),n._v("Vân Đào bàn tay lớn vẫy một cái, nhàn nhạt nhìn sang: “Làm sao, ngươi hoài nghi Thanh Mộc khốn trận uy lực?”"),t("br"),t("br"),n._v("“Này ngược lại là không phải!” Trương Huyền lắc đầu: “Chẳng qua là cảm thấy, trận pháp này bố trí có gì đó không đúng, khả năng không cách nào phát huy uy lực lớn nhất...”"),t("br"),t("br"),n._v("“Nói khoác không biết ngượng! Ngươi biết cái gì gọi là trận pháp?”"),t("br"),t("br"),n._v("“Không biết từ nơi nào nhô ra nhà quê, lại dám nghi vấn vương tử điện hạ bố trí trận pháp?”"),t("br"),t("br"),n._v("“Không biết trời cao đất rộng...”"),t("br"),t("br"),n._v("Trương Huyền lời còn chưa nói hết, liền bị một đám thanh âm phẫn nộ đánh gãy."),t("br"),t("br"),n._v("Chính là Vân Đào một đám thuộc hạ."),t("br"),t("br"),n._v("Vừa nãy tiểu tử này cùng bọn họ vương tử đối thoại, mấy người cũng cũng nghe được, một cái liền trận pháp là món đồ gì cũng không biết tiểu tử, lại còn nói bố trí không đúng..."),t("br"),t("br"),n._v("Ngươi mặt sao lớn như vậy chứ?"),t("br"),t("br"),n._v("Ngươi biết những cái này trận kỳ bố trí xuống đi có cái gì dùng sao? Biết trận pháp này kích hoạt sau, lớn bao nhiêu uy lực sao?"),t("br"),t("br"),n._v("Cái gì cũng không biết, liền ăn nói bừa bãi, thực sự là gan lớn đến cực hạn."),t("br"),t("br"),n._v("“Khặc khặc, Trương lão sư, không hiểu liền không cần nói...”"),t("br"),t("br"),n._v("Nghe được mọi người quát lớn, Thẩm Bích Như khuôn mặt đỏ lên."),t("br"),t("br"),n._v("Nàng đối cái này Trương lão sư cũng có chút không nói gì."),t("br"),t("br"),n._v("Ngươi nói thẳng bố trí không đúng, không phải bằng ngay mặt cười nhạo vị này Vân Đào vương tử sao?"),t("br"),t("br"),n._v("Tuy rằng cái tên này có chút rắm thối, bản lĩnh nhưng là thật có, nếu đối với trận pháp không biết, cũng đừng nhiều lời, đi Thú đường trước, đắc tội một vị chính là sắp trở thành thuần thú sư gia hỏa, thực sự không tính sáng suốt."),t("br"),t("br"),n._v("Thấy mọi người căm phẫn sục sôi, Trương Huyền bất đắc dĩ lắc đầu một cái."),t("br"),t("br"),n._v("Quả nhiên lời thật thì khó nghe, vốn còn muốn nhiều lời vài câu, nếu bọn họ loại thái độ này, cũng là chẳng muốn phí lời."),t("br"),t("br"),n._v("“Trận pháp một đạo, mênh mông bể sở, nếu một chữ cũng không biết, liền không muốn hồ mở miệng lung tung, may mà ta rộng lượng, không phải vậy, chỉ bằng vào vừa nãy câu nói đó, là có thể cố gắng giáo huấn ngươi một trận, tùy tiện đánh chết đều là nhẹ!”"),t("br"),t("br"),n._v("Đánh gãy lời của mọi người, Vân Đào nhìn Trương Huyền một chút, hừ một tiếng, tiếp theo chỉ về phía trước, mang theo tự tin cùng kiêu ngạo: “Cái này Thanh Mộc khốn trận, dựa vào mộc mà uy lực tăng mạnh, nơi này khắp nơi đều là thảm thực vật, lại có cỏ dại che lấp, thích hợp nhất bố trí. Một lúc nữa liền để ngươi xem một chút, trận pháp làm sao đại triển hùng vĩ, bắt lấy Kim Thân Thiết Tiễn Báo!”"),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Thấy đối phương một mặt tự tin, tràn đầy muốn tại Thẩm Bích Như trước mặt biểu hiện dục vọng, Trương Huyền không nói thêm nữa, yên tĩnh đứng ở một bên."),t("br"),t("br"),n._v("Vân Đào lúc này mới thoả mãn gật gù, đang muốn tiếp tục nói khoác vài câu trận pháp lợi hại, liền nghe đến ngay phía trước một tiếng gào thét."),t("br"),t("br"),n._v("Tiếp theo liền nhìn thấy một đầu thân ảnh khổng lồ sốt ruột thoán mà tới."),t("br"),t("br"),n._v("Kim Thân Thiết Tiễn Báo!"),t("br"),t("br"),n._v("Con này Ích Huyệt cảnh thực lực man thú, thêm vào đuôi có tới dài hơn bốn mét, màu vàng óng trên thân thể, che kín xanh xám sắc lấm tấm, khoảng cách mọi người đủ có mấy trăm mét, liền làm cho người ta một loại cảm giác áp bách mạnh mẽ."),t("br"),t("br"),n._v("“Đến rồi...”"),t("br"),t("br"),n._v("Vân Đào ánh mắt sáng lên, lại không để ý tới cùng Trương Huyền nét mực, khẩn trương nhìn về phía trước."),t("br"),t("br"),n._v("Chỉ thấy Kim Thân Thiết Tiễn Báo thảnh thơi thảnh thơi đi về phía trước mấy bước, lập tức nhìn thấy mọi người, bản năng lùi lại mấy bước, liền muốn rời khỏi."),t("br"),t("br"),n._v("“Nhanh nhen lửa đồ vật!”"),t("br"),t("br"),n._v("Thấy đối phương động tác, Vân Đào vội vàng dặn dò một vị thuộc hạ."),t("br"),t("br"),n._v("Đuổi mười mấy ngày mới tìm được, một khi đào tẩu, lại muốn tìm tới liền khó khăn."),t("br"),t("br"),n._v("“Vâng!” Cái này thuộc hạ này mới phản ứng được, lấy ra một bao đồ vật, lập tức dùng hộp quẹt nhen lửa."),t("br"),t("br"),n._v("Nhất thời, một luồng nồng nặc mùi hôi thối truyền tới, kích động đầu người ngất hoa mắt, có chút muốn nhổ."),t("br"),t("br"),n._v("“Đây là giống cái Kim Thân Thiết Tiễn Báo phân và nước tiểu, thiêu đốt có thể thả ra cực sự mãnh liệt mùi vị, đối với con người mà nói rất khó ngửi, nhưng đối với nam tính thiết tiền báo tới nói, nhưng có khó có thể chống cự sức hấp dẫn.”"),t("br"),t("br"),n._v("Đối với thuần phục thú, Thẩm Bích Như tựa hồ biết một ít, nhỏ giọng giới thiệu."),t("br"),t("br"),n._v("Cố nén mê muội, Trương Huyền thật không biết cái tên này làm sao sẽ thích như thế ghê tởm đồ vật, không nhịn được hướng về phía trước nhìn lại, quả nhiên cái này kích động mùi bên dưới, vốn định rời đi Kim Thân Thiết Tiễn Báo, chần chờ một chút, hướng bên này đi tới."),t("br"),t("br"),n._v("Nương theo cái tên này càng ngày càng gần, mọi người lúc này mới cảm thấy trên người nó toả ra cảm giác ngột ngạt là cỡ nào mãnh liệt."),t("br"),t("br"),n._v("Mặt mũi dữ tợn, lưu tuyến hình thân thể, mặc dù là Ích Huyệt cảnh thực lực man thú, chân chính chiến đấu với nhau, e sợ bảy, tám cái cùng cấp những nhân loại khác đều không phải là đối thủ."),t("br"),t("br"),n._v("Man thú trời sinh tốc độ cực nhanh, lại thêm lên hình thể to lớn, da lông đảm nhiệm tấm chắn, nghe tới là Ích Huyệt cảnh thực lực, trên thực tế hoàn toàn có thể làm được vượt cấp khiêu chiến!"),t("br"),t("br"),n._v("Nói cách khác, trước mắt con này Kim Thân Thiết Tiễn Báo, coi như gặp phải Thông Huyền cảnh sơ kỳ cường giả, đều có thể một trận chiến."),t("br"),t("br"),n._v("“Chuẩn bị dẫn động trận pháp!”"),t("br"),t("br"),n._v("Thấy Kim Thân Thiết Tiễn Báo bị tức ý vị hấp dẫn, đi tới, khoảng cách Thanh Mộc khốn trận càng ngày càng gần, Vân Đào hưng phấn nắm đấm xiết chặt."),t("br"),t("br"),n._v("Chỉ phải bắt được cái tên này, nghĩ biện pháp thuần phục, hắn thuần thú sư thân phận liền ván đã đóng thuyền!"),t("br"),t("br"),n._v("Loại thân phận này trở lại Vương quốc, tất nhiên có thể từ mấy vị vương tử bên trong bộc lộ tài năng, coi như không cách nào kế thừa đại bảo, cũng tất nhiên gấp bội chịu đến ưu ái."),t("br"),t("br"),n._v("“Hả?”"),t("br"),t("br"),n._v("Cao hứng còn không kết thúc, liền nhìn thấy con này tên to xác tại khoảng cách trận pháp còn có mười mét khoảng cách ngừng lại."),t("br"),t("br"),n._v("Đạt đến Ích Huyệt cảnh man thú, đã có trí tuệ của chính mình, biểu hiện như vậy đã phát hiện Vân Đào mấy người không có ý tốt, nếu không là thiêu đốt phân và nước tiểu mùi vị hấp dẫn, sợ là sớm đã xa xa đào tẩu."),t("br"),t("br"),n._v("“Đối với nó công kích, nghĩ biện pháp đem hắn làm tức giận!”"),t("br"),t("br"),n._v("Không hổ là chính là sắp trở thành thuần thú sư tồn tại, thấy nó dừng lại, Vân Đào tuy rằng phiền muộn, lại đối y theo đối man thú thuộc tính nghe ngóng, thấp giọng dặn dò."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mấy cái thuộc hạ đã sớm chuẩn bị, đồng thời gật gù, từ trên lưng gỡ xuống cung tên, cài tên lên dây cung đối thiết tiền báo bắn lại đây."),t("br"),t("br"),n._v("Ô u Ô u Ô u Ô u!"),t("br"),t("br"),n._v("Ích Huyệt cảnh cường giả chân khí phối hợp đặc thù mũi tên, mang theo không khí tiếng nghẹn ngào thẳng tắp phóng tới, còn chưa tới đến trước mặt, Kim Thân Thiết Tiễn Báo liền cảm thấy uy hiếp, toàn thân lông tóc một hồi nổ lên, thân thể nhảy lên né tránh ra đến."),t("br"),t("br"),n._v("Nó cái đầu tuy rằng rất lớn, thân thể lại dị thường linh hoạt, tốc độ nhanh chóng, e sợ đã có thể cùng Lục Tầm triển khai huyễn ảnh Cửu Cung Bộ sánh ngang."),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),n._v("Vân Đào lên tiếng hét lớn."),t("br"),t("br"),n._v("Nghe được dặn dò, mọi người tăng nhanh tốc độ, mũi tên như mưa."),t("br"),t("br"),n._v("“Hống!”"),t("br"),t("br"),n._v("Thấy tiễn càng ngày càng nhiều, một tiếng phẫn nộ gào thét, Kim Thân Thiết Tiễn Báo tựa hồ bị triệt để chọc giận, to lớn tiếng gầm gừ bên trong, thẳng tắp hướng mọi người vọt tới."),t("br"),t("br"),n._v("Ong!"),t("br"),t("br"),n._v("Còn không xông lên vài bước, liền một chân bước vào trước trận kỳ bày ra phạm vi, một tiếng kêu khẽ vang lên, Thanh Mộc khốn trận trong nháy mắt khởi động, Kim Thân Thiết Tiễn Báo như là lập tức rơi vào đầm lầy, lại thật giống bị một cái thiết bao phủ lại, bên trái thụi phía trên va nhưng thủy chung không cách nào tiến lên."),t("br"),t("br"),n._v("“Đây chính là trận pháp? Thật là lợi hại!”"),t("br"),t("br"),n._v("Thẩm Bích Như sắc mặt ngưng lại."),t("br"),t("br"),n._v("Vừa nãy Kim Thân Thiết Tiễn Báo mạnh mẽ, nàng xem như là tận mắt thấy, như xử lý lợi hại man thú, rơi vào mấy cây trận kỳ bố trí đồ vật bên trong, lại có thể bị gắt gao nhốt lại, muốn chạy trốn đều không làm được, không phải tận mắt nhìn thấy, đều không thể tin được."),t("br"),t("br"),n._v("Nếu như đổi làm nàng vô ý xông vào, khẳng định cũng khó có thể chạy trốn."),t("br"),t("br"),n._v("“Xem ra sau này đi ra ngoài, nhất định phải phòng bị trận pháp sư!” Trong lòng âm thầm cảnh giác."),t("br"),t("br"),n._v("Trận pháp sư sở dĩ tại rất nhiều nghề nghiệp bên trong để người kính trọng, nguyên nhân lớn nhất chính là có vượt cấp giết địch năng lực."),t("br"),t("br"),n._v("Chỉ cần đưa ra thời gian sớm bố trí kỹ càng trận pháp, coi như thực lực vượt qua đối phương, cũng biết bị đơn giản chém giết."),t("br"),t("br"),n._v("“Xem ngươi lần này trốn chỗ nào!”"),t("br"),t("br"),n._v("Không biết trong lòng nàng sinh ra kiêng kỵ, Vân Đào cười ha ha, nhìn Trương Huyền một chút, tràn đầy đắc ý."),t("br"),t("br"),n._v("Ngươi không phải nói bố trí không đúng sao?"),t("br"),t("br"),n._v("Này không phải lập tức nhốt lại?"),t("br"),t("br"),n._v("Hai tay chắp ở sau lưng, Vân Đào nhìn về phía thuộc hạ, tiếp tục dặn dò: “Mọi người không cần phải gấp, để trận pháp nhiều nhốt lại cái tên này một hồi, triệt để tiêu hao một hồi nó thể lực!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người gật gù, vây ở xung quanh."),t("br"),t("br"),n._v("“Hống!”"),t("br"),t("br"),n._v("Ngay ở Vân Đào mấy người, dự định Kim Thân Thiết Tiễn Báo bị nhốt trận tiêu hao hết thể lực, tại một lần thu phục thời điểm, lại lần nữa nghe được một tiếng phẫn nộ gào thét, người sau như phát điên mạnh mẽ quay về trận pháp đánh tới."),t("br"),t("br"),n._v("Rào rào!"),t("br"),t("br"),n._v("Trận pháp một trận lay động, trước ẩn nấp ở trận kỳ xuất hiện lần nữa tung tích."),t("br"),t("br"),n._v("“Vương tử điện hạ, làm sao bây giờ...”"),t("br"),t("br"),n._v("Nhìn thấy trận kỳ bị một hồi va ra hiện tung tích, một cái Ích Huyệt cảnh cường giả không nhịn được nhìn sang."),t("br"),t("br"),n._v("“Hoảng sợ cái gì? Yên tâm đi! Thanh Mộc trận pháp mượn địa lợi xu thế, có thể tự chủ hấp thu xung quanh mộc thuộc tính sức mạnh, tăng cường phòng ngự, dựa vào nó là không cách nào phá tan...”"),t("br"),t("br"),n._v("Vân Đào tự tin nở nụ cười, bàn tay lớn vẫy một cái, đang muốn tiếp tục nói cái gì, liền nghe mặt đất lại lần nữa nổ vang, mấy cây trận kỳ lại cũng không chịu nổi va chạm, “Vèo!” Bay lên."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trận kỳ bay ra, Thanh Mộc khốn trận bằng trong nháy mắt bị phá, nổi giận Kim Thân Thiết Tiễn Báo thân thể to lớn loáng một cái, dĩ nhiên đi tới Vân Đào trước mặt, vung lên móng trảo liền đập tới."),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Vân Đào đang nói bốc nói phét, tự nhận là của hắn Thanh Mộc khốn trận khẳng định không thành vấn đề, liền thấy cái tên này dĩ nhiên đi tới trước mặt, sợ đến rít lên một tiếng, suýt chút nữa xụi lơ trên đất."),t("br"),t("br"),n._v("“Súc sinh chớ cuồng!”"),t("br"),t("br"),n._v("“Điện hạ cẩn thận!”"),t("br"),t("br"),n._v("Mấy cái Ích Huyệt cảnh thuộc hạ, giờ khắc này cũng phản ứng lại, đồng thời vọt tới, đủ loại binh khí ra khỏi vỏ, trong nháy mắt đao khí, kiếm khí, gào thét hướng Kim Thân Thiết Tiễn Báo bổ tới."),t("br"),t("br"),n._v("Hống!"),t("br"),t("br"),n._v("Không để ý tới những công kích này, Kim Thân Thiết Tiễn Báo thô to đuôi đột nhiên cuốn một cái, tiếng gió gầm rú thẳng tắp hướng một người trong đó quất tới, cùng thời khắc đó, thô to móng trảo khoảng chừng đập ngang, uy lực mười phần."),t("br"),t("br"),n._v("Ba ba ba ba!"),t("br"),t("br"),n._v("Không hổ là Ích Huyệt cảnh man thú, Vân Đào này mấy cái thuộc hạ, chỉ cảm thấy chấn động toàn thân, từng cái từng cái không nhịn được sắc mặt đỏ lên, đồng loạt lui về phía sau, đồng thời yết hầu ngòn ngọt, máu tươi phun mạnh."),t("br"),t("br"),n._v("Mấy người liên thủ tuy rằng lợi hại, nhưng gặp phải đã phát rồ Ích Huyệt cảnh man thú, vẫn là lực không có đến."),t("br"),t("br"),n._v("“Chạy mau!”"),t("br"),t("br"),n._v("Thấy thuộc hạ liên thủ cũng không ngăn nổi, Vân Đào sợ đến suýt chút nữa ngất đi, hô hô một tiếng, đang muốn chạy trốn, liền thấy đẩy lùi mọi người Kim Thân Thiết Tiễn Báo, thân thể to lớn xoay một cái, hướng Trương Huyền cùng Thẩm Bích Như vọt tới."),t("br"),t("br"),n._v("“Xong...”"),t("br"),t("br"),n._v("Thân thể cứng đờ."),t("br"),t("br"),n._v("Hắn nhiều như vậy thuộc hạ đều không chống đỡ được, người mỹ nữ này bất quá Đỉnh Lực cảnh đỉnh phong, cái kia vô tri tiểu tử, khẳng định càng yếu hơn, phỏng chừng lành ít dữ nhiều."),t("br"),t("br"),n._v("Chính giác đến hai người chắc chắn phải chết, liền nghe đến cái kia vô tri tiểu tử tràn đầy bất đắc dĩ âm thanh âm vang lên."),t("br"),t("br"),n._v("“Ngươi ngốc a, là bọn họ muốn bắt ngươi, tìm ta làm gì?”"),t("br"),t("br"),n._v("Nghe nói như thế, chúng thân thể người tất cả đều loáng một cái."),t("br"),t("br"),n._v("Tiểu tử này có phải là não tàn?"),t("br"),t("br"),n._v("Hiện tại nó bị làm tức giận, còn nói chuyện như vậy, này không phải muốn chết là làm gì?"),t("br"),t("br"),n._v("Ngay ở tất cả mọi người đều cho rằng đầu óc có bệnh gia hỏa, chắc chắn phải chết thời điểm, liền nghe đối phương bất đắc dĩ âm thanh lại vang lên."),t("br"),t("br"),n._v("“Ngươi không đi, ta nhưng muốn ra tay rồi... Quên đi, nhìn dáng dấp giảng đạo lý giảng không thông, sang một góc chơi!”"),t("br"),t("br"),n._v("Âm thanh kết thúc, tiếp theo “Ba” một tiếng."),t("br"),t("br"),n._v("Vèo!"),t("br"),t("br"),n._v("Kim Thân Thiết Tiễn Báo thân ảnh khổng lồ, trong nháy mắt bay ngược mà ra, va đầu vào trên một cây đại thụ, miệng sùi bọt mép, liên tục co giật."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-245"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-247"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);