(window.webpackJsonp=window.webpackJsonp||[]).push([[1466],{1820:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-241-ta-thuan-phuc-con-chim"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-241-ta-thuan-phuc-con-chim"}},[n._v("#")]),n._v(" Chương 241: Ta thuần phục con chim")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đừng ầm ĩ, đi Hàn Vũ Vương quốc Sáp Bắc thành, chỉ có mười cái danh ngạch, hôm nay đã đầy, muốn đi, ngày mai lại tới đây xếp hàng đi!”"),t("br"),t("br"),n._v("“Đây là đi An Viễn Vương quốc Tể Bắc thành, còn kém ba người, muốn đi dành thời gian, chậm liền không vị trí!”"),t("br"),t("br"),n._v("“Nơi này bán ra Thông Huyền cảnh man thú Hồng Vĩ Hồ con non, có đồng ý mua, dành thời gian lại đây...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đoàn người phía trước, đủ loại âm thanh ầm ĩ, lộ ra tùm la tùm lum."),t("br"),t("br"),n._v("“Huyền Lạc sơn mạch dựa lưng mười quốc, Bắc Vũ Vương quốc, Hàn Vũ Vương quốc, Thiên Huyền Vương quốc... Đều sẽ tới này giao dịch, mỗi ngày đều sẽ có rất nhiều người, đặc biệt là đi xa nhà, toàn bộ Thú đường tổng cộng liền nhiều như vậy man thú, đến nhiều người, không thể đều ngồi bên trên, cơ bản cần sớm xếp hàng, có mấy người ở đây liền chờ nửa tháng, đều rất khó đứng hàng!”"),t("br"),t("br"),n._v("Nhìn trước mắt ầm ĩ đám người, Vân Đào giải thích."),t("br"),t("br"),n._v("“Nửa tháng đều không xếp hàng tới?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Thú đường không thể có đầy đủ man thú thỏa mãn nhu cầu, điểm ấy hắn đã sớm đoán được, nhưng không nghĩ tới cần xếp hàng thời gian lâu như vậy."),t("br"),t("br"),n._v("Nếu như ở chỗ này chờ nửa tháng, còn không bằng trực tiếp ngồi Kim Thân Thiết Tiễn Báo đi Thiên Vũ Vương quốc."),t("br"),t("br"),n._v("“Tiền bối yên tâm!” Xem vẻ mặt của hắn, Vân Đào cười cợt: “Chỉ cần ta hoàn thành sát hạch, trở thành chính thức thuần thú sư, liền có tư cách giúp tiền bối tuyển lựa phi hành man thú, không dùng như những người khác như thế ở đây dính dáng chờ.”"),t("br"),t("br"),n._v("Nghe hắn nói như vậy, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Bất kỳ nghề nghiệp nào, đều là có đặc quyền."),t("br"),t("br"),n._v("Thật giống như luyện đan sư tại luyện đan sư công hội mua đan dược, không chỉ ưu tiên, còn có chiết khấu có thể đánh."),t("br"),t("br"),n._v("Thuần thú sư như thế."),t("br"),t("br"),n._v("Người khác muốn cưỡi phi hành man thú, nhất định phải xếp hàng chờ đợi, mỗi ngày phân phát cố định danh ngạch, chính thức thuần thú sư không giống, có thể trực tiếp cưỡi, không cần cùng những người khác như thế xếp hàng làm lỡ thời gian."),t("br"),t("br"),n._v("http://ngantruyen.com"),t("br"),n._v("“Cái kia... Đi hoàn thành sát hạch đi!”"),t("br"),t("br"),n._v("Nhìn thấy xếp hàng mọi người giống như hàng dài, Trương Huyền tê cả da đầu, tùy ý khoát tay áo một cái."),t("br"),t("br"),n._v("“Ân, thuần thú sư sát hạch địa phương ở nơi đó!” Vân Đào đi đầu dẫn đường."),t("br"),t("br"),n._v("Mấy người nhanh chân đi đi, người khác nhìn thấy Trương Huyền bên cạnh cao to hung mãnh Kim Thân Thiết Tiễn Báo, nhao nhao tránh ra."),t("br"),t("br"),n._v("Lúc này Kim Thân Thiết Tiễn Báo đã không còn mới vừa đột phá lúc sắc bén khí tức, ngoại trừ hình thể to lớn, cùng trước không quá to lớn khác nhau, để người nhìn chút bên dưới, còn tưởng rằng chỉ có Ích Huyệt cảnh."),t("br"),t("br"),n._v("Bất quá, mặc dù là Ích Huyệt cảnh man thú, cũng không ai nguyện trêu chọc, không phải là bởi vì thực lực của nó, mà là nơi này là Thú đường, có thể mang theo như thế to lớn một đầu, khẳng định có rất nhiều quan hệ, không phải học đồ chính là thuần thú sư, bất luận bên nào, đều không phải bọn họ đều có thể đắc tội."),t("br"),t("br"),n._v("Không có ai trở ngại, đi cũng là phi thường cấp tốc, thời gian không lâu mọi người liền đến đến một cái to lớn gian phòng trước mặt, trước cửa năm cái đại tự “Thuần thú sư sát hạch”."),t("br"),t("br"),n._v("Cùng sát hạch luyện đan sư địa phương không giống, lộ ra lại rộng rãi."),t("br"),t("br"),n._v("“Khoảng cách sát hạch kết thúc, còn có một quãng thời gian, thời gian không muộn, chúng ta vào đi thôi!”"),t("br"),t("br"),n._v("Nghĩ đến lập tức liền có thể thông qua sát hạch trở thành chính thức thuần thú sư, Vân Đào hưng phấn con mắt tỏa ánh sáng, nở nụ cười một tiếng, liền muốn đi vào."),t("br"),t("br"),n._v("“Này không phải Vân Đào sao? Làm sao nhanh như vậy sẽ trở lại? Có phải là cảm thấy sát hạch không cách nào thông qua, ảo não trở về?”"),t("br"),t("br"),n._v("Mọi người còn chưa vào cửa, liền nghe dẫn dắt một cái mang theo cười nhạo âm thanh âm vang lên."),t("br"),t("br"),n._v("Quay đầu nhìn lại, liền thấy một người thanh niên mang theo rất nhiều thuộc hạ cùng một đầu to lớn hổ đầu thú, nghênh ngang đi tới."),t("br"),t("br"),n._v("Cái này hổ đầu thú cái đầu so Kim Thân Thiết Tiễn Báo càng thêm to lớn, khí thế cũng càng thêm kinh người, làm cho người ta một loại có thể xé nát bầu trời cảm giác."),t("br"),t("br"),n._v("Bất quá, bàn về thực lực, còn kém một chút, chỉ là Ích Huyệt cảnh sơ kỳ thôi."),t("br"),t("br"),n._v("“Cái này là Bắc Đẩu Vương quốc lục vương tử Chu Cẩm Hoàng, cùng chúng ta điện hạ luôn luôn không hợp nhau...”"),t("br"),t("br"),n._v("Xem Trương Huyền nghi hoặc, Vân Đào một cái thuộc hạ, truyền âm lại đây."),t("br"),t("br"),n._v("“Bắc Đẩu Vương quốc?”"),t("br"),t("br"),n._v("Trương Huyền trong đầu xuất hiện chung quanh địa đồ thư tịch."),t("br"),t("br"),n._v("Cùng Hàn Vũ Vương quốc như thế, Bắc Đẩu cũng là cái nhị đẳng Vương quốc, bởi vì bản đồ cùng Hàn Vũ lân cận, ma sát không ngừng, xem như là đối địch Vương quốc một trong, làm hai đại Vương quốc ở giữa vương thất thành viên, lẫn nhau căm thù, cũng là rất bình thường."),t("br"),t("br"),n._v("“Hai người bọn họ mâu thuẫn, kỳ thực... Không phải là bởi vì bản đồ, mà là...”"),t("br"),t("br"),n._v("Vân Đào thuộc hạ nói còn chưa dứt lời, liền nghe đến tiếng bước chân vang lên, tiếp theo lại một đám người đi tới."),t("br"),t("br"),n._v("“Cẩm Hoàng, ngươi tại sao có thể nói như vậy đâu? Không thấy phía sau hắn con này Kim Thân Thiết Tiễn Báo à? Có con này tên to xác, đương nhiên là có thể thông qua sát hạch!” Cầm đầu một cái thanh niên mặc áo trắng, khẽ cười một tiếng, lập tức hơi nhướng mày: “Chỉ là... Ta thấy thế nào con này Kim Thân Thiết Tiễn Báo, tựa hồ cùng hắn không thân đâu? Sẽ không phải không phải của hắn thú sủng đi!”"),t("br"),t("br"),n._v("Nghe tới là giúp Vân Đào giải vây, trên thực tế hơi vung lên môi, mang theo trào phúng tâm ý."),t("br"),t("br"),n._v("“Đây là Càn Viễn Vương quốc Cửu vương tử, Chu Tuyên, cùng điện hạ cũng không quá hòa hợp...” Vân Đào thuộc hạ truyền âm tiếp tục vang lên."),t("br"),t("br"),n._v("Càn Viễn Vương quốc đồng dạng là cấp hai Vương quốc, cách nơi này cũng không tính quá xa, cùng Thiên Huyền không hề sát bên, lại cùng Thiên Vũ Vương quốc gấp chịu."),t("br"),t("br"),n._v("“Há, ngươi vừa nói như vậy, ta mới nhìn thấy!”"),t("br"),t("br"),n._v("Liếc mắt nhìn Kim Thân Thiết Tiễn Báo, cũng không tới gần Vân Đào, ngược lại cùng một cái không biết tên thanh niên gấp chịu, Chu Cẩm Hoàng cười cợt: “Vân Đào, ngươi sẽ không thật sự không thuần phục man thú, cố ý tìm người mượn một cái đến đây đi? Thật muốn như vậy, ta khuyên ngươi vẫn là cẩn thận một ít, một khi bị Thú đường tra được, sẽ cả đời cấm thi, đến thời điểm, cũng chỉ có thể cong đuôi ngoan ngoãn rời đi!”"),t("br"),t("br"),n._v("Thú đường có sáng tỏ quy định, sát hạch thuần thú sư nếu như là dùng người khác man thú, một khi tra ra, cả đời cấm thi, cũng là đại biểu đời này sẽ cùng thuần thú sư nghề nghiệp này vô duyên."),t("br"),t("br"),n._v("Chu Tuyên, Chu Cẩm Hoàng làm thuần phục thú học đồ, nhãn lực đương nhiên không kém, liếc mắt là đã nhìn ra Kim Thân Thiết Tiễn Báo cùng Trương Huyền thân cận, cùng Vân Đào vô cùng xa lạ, rất nổi bật không phải của hắn thú sủng."),t("br"),t("br"),n._v("“Hì hì, chỉ cần lần này không cách nào thông qua, nhìn hắn còn mặt mũi nào, theo đuổi Mạc Vũ tiểu thư!” Chu Tuyên nói."),t("br"),t("br"),n._v("“Còn theo đuổi, ta xem là có thể lăn bao xa lăn bao xa, Mạc Vũ tiểu thư loại này thiên tài siêu cấp, không phải là hắn loại này gà mờ có thể khinh nhờn!” Chu Cẩm Hoàng hừ lạnh."),t("br"),t("br"),n._v("“Ta không tư cách? Các ngươi cũng chưa chắc tốt hơn ta chạy đi đâu, bớt ở chỗ này nói lời châm chọc, có bản lĩnh thành thuần thú sư lại nói!” Thấy hai người giáp công, Vân Đào một người có chút một cây làm chẳng lên non, quăng một cái ống tay áo."),t("br"),t("br"),n._v("“Thật sao? Xem ra rất tự tin a! Bất quá, một lúc nữa có ngươi khóc thời điểm, thuần thú sư sát hạch, không những thông qua, còn muốn bình cấp, ta xem ngươi một lúc nữa có thể bình được với cấp mấy!”"),t("br"),t("br"),n._v("Chu Tuyên nói."),t("br"),t("br"),n._v("“Ngươi vẫn là quản tốt bản thân đi, ta sợ đến thời điểm liền cái cấp ba đều bình không lên, ảo não rời đi!” Vân Đào nói."),t("br"),t("br"),n._v("“Ngươi...” Chu Tuyên hai mắt nhắm lại: “Hi vọng sát hạch xong sau đó, ngươi còn có thể tự tin như vậy.”"),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Ba người đối chọi gay gắt, không ai nhường ai, một đám thuộc hạ cũng đều lẫn nhau căm thù, không có sắc mặt tốt."),t("br"),t("br"),n._v("Đem mấy người đối thoại, nghe vào trong tai, Trương Huyền cũng hiểu được, mấy người mâu thuẫn, xác thực không phải là bởi vì Vương quốc bản đồ, thật giống là một vị kêu Mạc Vũ nữ tử."),t("br"),t("br"),n._v("“Mạc Vũ tiểu thư, là thuần phục thú học đồ bên trong rất nổi danh thiên tài, cùng điện hạ bọn họ cùng một chỗ sát hạch thuần thú sư, hào quang chói mắt, những cái này Vương quốc điện hạ, đều đáy lòng thầm mến, lén lút cạnh tranh, hy vọng có thể đạt được nàng ưu ái...”"),t("br"),t("br"),n._v("Vân Đào thuộc hạ tiếp tục truyền âm."),t("br"),t("br"),n._v("Biết là nguyên nhân này, Trương Huyền lắc đầu một cái."),t("br"),t("br"),n._v("Còn tưởng rằng làm vương tử, bởi vì bản đồ chi tranh, mà lẫn nhau tranh đấu, không nghĩ tới bởi vì một người phụ nữ."),t("br"),t("br"),n._v("“Là Mạc Vũ tiểu thư!”"),t("br"),t("br"),n._v("“Mau nhìn phía sau nàng man thú...”"),t("br"),t("br"),n._v("“Thật là lợi hại!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ba người chính đang đối đầu, liền nghe đến xung quanh một trận ồ lên, một cái thân ảnh yểu điệu chậm rãi đi tới."),t("br"),t("br"),n._v("Cái thân ảnh này sau lưng theo một đầu cao to hùng ưng, thiết lưng cánh vàng, hai mắt như điện, khiến lòng người sinh cảnh giác."),t("br"),t("br"),n._v("“Thông Huyền cảnh man thú?” Trương Huyền ánh mắt ngưng lại."),t("br"),t("br"),n._v("Con này hùng ưng, lại nhưng đã đạt đến Thông Huyền cảnh hậu kỳ, so bên cạnh hắn Kim Thân Thiết Tiễn Báo đều mạnh mẽ không ít."),t("br"),t("br"),n._v("Hơn nữa, khí tức mơ hồ, làm cho người ta một loại phong lôi cảm giác, e sợ tại trên tốc độ, so với trước quái điểu, đều chắc chắn mạnh hơn."),t("br"),t("br"),n._v("“Mạc Vũ tiểu thư!”"),t("br"),t("br"),n._v("Thấy này bóng người xuất hiện, Vân Đào, Chu Cẩm Hoàng, Chu Tuyên bọn người là ánh mắt sáng lên, vội vàng nghênh đón."),t("br"),t("br"),n._v("“Nàng chính là Mạc Vũ?”"),t("br"),t("br"),n._v("Xem xong hùng ưng, Trương Huyền lúc này mới đem sự chú ý di động ở cái kia thân ảnh yểu điệu bên trên."),t("br"),t("br"),n._v("Là cái hai mươi ba, bốn tuổi nữ tử, một thân trường bào màu xanh nhạt, đem duyên dáng vóc người hoàn mỹ phác hoạ ra đến, thiến eo dịu dàng nắm chặt, ba ngàn tóc đen chảy xuôi xuống, cùng da thịt trắng như tuyết tương xứng, làm cho người ta một loại dị dạng vẻ đẹp."),t("br"),t("br"),n._v("Nói riêng về dung mạo, so với Thẩm Bích Như đều không kém chút nào, thậm chí còn vượt qua, có thể nói đều theo kịp kích hoạt mười phần trăm Thuần Âm thể chất Triệu Nhã."),t("br"),t("br"),n._v("Đẹp đẽ là rất đẹp, bất quá xem ra vô cùng lành lạnh, đối với Vân Đào mấy người thái độ, càng là không để ý chút nào."),t("br"),t("br"),n._v("“Không hổ là Mạc Vũ tiểu thư, quả nhiên ra tay bất phàm!” Chu Cẩm Hoàng mặt tươi cười chào đón: “Thông Huyền cảnh thanh ưng thú, e sợ cũng chỉ có ngươi mới có thể thuần phục, lợi hại, lợi hại!”"),t("br"),t("br"),n._v("“Cái kia là đương nhiên, một đầu nho nhỏ thanh ưng thú mà thôi, Mạc Vũ tiểu thư ra tay, còn không bắt giữ đến?”"),t("br"),t("br"),n._v("Chu Tuyên cũng một mặt tươi cười."),t("br"),t("br"),n._v("Đối với hai người khen tặng, vị này Mạc Vũ tiểu thư liếc mắt nhìn, lạnh lẽo trên mặt không có bất kỳ biểu lộ gì, con mắt thoáng nhìn, nhìn thấy trong đám người Thẩm Bích Như, dừng lại một chút, trực tiếp đi tới."),t("br"),t("br"),n._v("“Ngươi cũng tới sát hạch thuần thú sư? Hàn Quỳnh đâu? Nàng không có tới?”"),t("br"),t("br"),n._v("Âm thanh tuy rằng lanh lảnh rung động lòng người, lại mang theo chống người bên ngoài ngàn dặm cảm giác."),t("br"),t("br"),n._v("“Nàng không có tới, ta là cùng bằng hữu lại đây.” Thẩm Bích Như chỉ tay Trương Huyền."),t("br"),t("br"),n._v("Mạc Vũ tiểu thư liếc mắt nhìn Trương Huyền, lập tức quét qua, giống như không khí, tiếp theo thân thể mềm mại xoay một cái, cũng không phí lời, hướng gian phòng đi đến."),t("br"),t("br"),n._v("Chu Cẩm Hoàng mấy người bị đối phương lơ là, cũng không có một chút nào không vui, cũng theo sát đi vào."),t("br"),t("br"),n._v("“Các ngươi nhận thức?” Đối với cái này Mạc Vũ tiểu thư không đếm xỉa, Trương Huyền cũng không để ý, mà là nghi hoặc nhìn về phía bên cạnh Thẩm lão sư."),t("br"),t("br"),n._v("“Lần trước cùng Hàn Quỳnh tới đây, gặp qua một lần.” Thẩm Bích Như gật gù."),t("br"),t("br"),n._v("“Nàng thật sự chỉ là một cái có thiên tư thuần phục thú học đồ?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("Coi như cô bé này rất đẹp, đối thuần phục thú rất có thiên phú, cũng không đến mức để tam đại Vương quốc vương tử, một bộ thái độ cung kính đi!"),t("br"),t("br"),n._v("Bất luận Hàn Vũ, Bắc Đẩu vẫn là Càn Viễn, làm cấp hai Vương quốc, thuần thú sư coi như không nhiều, cũng khẳng định có không ít, một cái học đồ mà thôi, còn không đến mức để đường đường vương tử điện hạ như vậy ăn nói khép nép, cam tâm tình nguyện."),t("br"),t("br"),n._v("Tại nhị đẳng Vương quốc, đừng nói thuần thú sư học đồ, coi như danh sư học đồ, e sợ đều không thứ địa vị này."),t("br"),t("br"),n._v("Nương theo Vương quốc đẳng cấp càng cao, vương quyền cũng là vượt qua cao quý, trừ phi là đẳng cấp cao chức nghiệp giả, bằng không, là sẽ không gây cho người chú ý, không thể như Thiên Huyền Vương quốc như thế, một cái nho nhỏ danh sư học đồ, liền có thể làm cho Thẩm Truy bệ hạ cung kính đối xử."),t("br"),t("br"),n._v("“Nàng xác thực không chỉ là thuần phục thú học đồ, ta nghe Hàn Quỳnh đã nói một lần, còn có một cái thân phận khác... Thiên Vũ Vương quốc tam công chúa!” Thẩm Bích Như suy nghĩ một chút nói."),t("br"),t("br"),n._v("“Thiên Vũ Vương quốc Công chúa?” Trương Huyền bừng tỉnh."),t("br"),t("br"),n._v("Này còn tạm được."),t("br"),t("br"),n._v("Thiên Vũ Vương quốc là nhất đẳng Vương quốc, chung quanh mười hai quốc không hề hoài nghi bá chủ."),t("br"),t("br"),n._v("Cái này Vương quốc Công chúa điện hạ, thân phận so với một chút ít Vương quốc quốc vương đều muốn cao quý, nếu có thể cưới đến tay, Phò mã thân phận, nhất định sẽ đối sau đó vương trữ tranh cướp, có rất lớn giúp ích."),t("br"),n._v("Xem ra Chu Cẩm Hoàng, Chu Tuyên thậm chí Vân Đào đều đánh ý đồ này."),t("br"),t("br"),n._v("Bất quá, những cái này đối Trương Huyền tới nói, cái gì công chúa hay không công chúa, cũng không đáng kể."),t("br"),t("br"),n._v("Theo dựa vào người khác đều vô dụng, chủ yếu nhất vẫn là bản thân."),t("br"),t("br"),n._v("Hai người nói chuyện công phu, đáp ứng đi vào gian phòng."),t("br"),t("br"),n._v("Trong phòng cùng bên ngoài không giống, rất là quạnh quẽ, chỉ có mới vừa tiến vào mấy bầy người."),t("br"),t("br"),n._v("“Đám này sát hạch tổng cộng bốn cái, đều trở về, ngươi nói sẽ là ai cho chúng ta sát hạch?”"),t("br"),t("br"),n._v("“Đương nhiên là đường chủ đại nhân! Hắn là Thú đường cấp bậc cao nhất nhị tinh thuần thú sư, mỗi lần sát hạch hầu như đều là hắn kiểm định.”"),t("br"),t("br"),n._v("“Đúng đấy, nghe nói đường chủ đại nhân sát hạch vô cùng nghiêm khắc, không biết là không đúng giả.”"),t("br"),t("br"),n._v("“Quản hắn thật giả, chúng ta điện hạ đã thuần phục man thú, phù hợp yêu cầu, thông qua khẳng định không không có vấn đề.”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đi vào gian phòng, liền nghe đến xung quanh một trận nhỏ giọng nói thầm."),t("br"),t("br"),n._v("Ba vị vương tử điện hạ đều dẫn dắt không ít tùy tùng, trái lại vị kia Mạc Vũ tiểu thư, một thân một mình, thân thể mềm mại thẳng tắp đứng ở một bên, lạnh lẽo khí tràng toả ra, dĩ nhiên không một người dám qua đi quấy rối."),t("br"),t("br"),n._v("“Nghe vừa nãy ngữ khí, nàng cùng Hàn lão sư có mâu thuẫn?”"),t("br"),t("br"),n._v("Nhìn thấy vị này Thiên Vũ Vương quốc tam công chúa, lại có thể một cái tùy tùng đều không mang theo, một bộ người sống chớ quấy rầy dáng dấp, nhớ tới trước cùng Thẩm Bích Như đối thoại, không nhịn được nhìn sang."),t("br"),t("br"),n._v("“Không thể nói là mâu thuẫn đi, Hàn lão sư cũng là thuần phục thú học đồ, hai người đều tại Thú đường lưu lại qua một quãng thời gian, bởi vì đều là mỹ nữ, đáy lòng đương nhiên cũng là có lẫn nhau so sánh tâm tư. Chỉ là không nghĩ tới nàng lại có thể nhanh như vậy liền sát hạch chính thức thuần thú sư, thiên phú quả nhiên lợi hại.”"),t("br"),t("br"),n._v("Thẩm Bích Như tú mục mang theo kinh ngạc."),t("br"),t("br"),n._v("“Thiên phú?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Sát hạch cái thuần thú sư liền thiên phú cao?"),t("br"),t("br"),n._v("“Xem ra ngươi không biết, vị này Mạc Vũ Công chúa, học tập thuần thú sư nghề nghiệp, còn chưa đủ thời gian một năm!” Thẩm Bích Như nói."),t("br"),t("br"),n._v("“Không đủ một năm? Cái kia xác thực có thể xưng tụng thiên tài!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tuy rằng hắn không biết thuần thú sư, nhưng đối với luyện đan sư biết rất nhiều, nếu hai loại nghề nghiệp tại thượng cửu lưu bên trong, xếp hạng không phân cao thấp, độ khó khẳng định không nhỏ."),t("br"),t("br"),n._v("Không đủ một năm, không những trở thành học đồ, còn sát hạch chính thức thuần thú sư, phần này thiên phú, quả nhiên mạnh lớn đến đáng sợ."),t("br"),t("br"),n._v("“Mấu chốt nhất chính là... Nàng không chỉ có học tập thuần phục thú nghề nghiệp, còn một vị nhất tinh luyện đan sư, khoảng cách nhị tinh đều không xa! Mấu chốt nhất chính là... Càng là một vị danh sư học đồ, có người nói tới nơi này sát hạch thuần thú sư, là vì mài giũa bản thân tâm cảnh mức độ, một khi tư cách đầy đủ, trở lại là có thể sát hạch nhất tinh danh sư.”"),t("br"),t("br"),n._v("Thẩm Bích Như khuôn mặt nghiêm nghị."),t("br"),t("br"),n._v("“Nhất tinh luyện đan sư? Danh sư học đồ?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Tuy rằng cùng hắn có chút tương tự, nhưng hắn biết, đối phương thiên phú xác thực cao hơn chính mình hơn nhiều."),t("br"),t("br"),n._v("Hắn sát hạch nhất tinh luyện đan sư, dựa cả vào đầu cơ trục lợi, dựa dẫm Thiên Đạo thư viện uy lực, thật nếu để cho hắn luyện đan, khả năng nhiều nhất chế biến ra dược tản mạn, liền phổ thông viên thuốc phối chế không ra."),t("br"),t("br"),n._v("Có thể dựa vào bản lãnh thật sự bằng chừng ấy tuổi sát hạch nhất tinh luyện đan sư thành công, đồng thời vẫn là danh sư học đồ, khó trách được gọi là thiên tài, xác thực rất lợi hại."),t("br"),t("br"),n._v("Hai người trò chuyện công phu, một ông già đi vào."),t("br"),t("br"),n._v("“Yên tĩnh, ngày hôm nay do ta phụ trách chư vị sát hạch!”"),t("br"),t("br"),n._v("Nhìn thấy người này xuất hiện, tất cả mọi người là sững sờ."),t("br"),t("br"),n._v("“Là Thú đường Hồng thú sư? Làm sao sẽ là hắn đến xét duyệt?” Vân Đào cũng tràn đầy kỳ quái: “Dựa theo đạo lý, xét duyệt chính thức thuần thú sư, đường chủ mấy người nhất định phải đích thân tới, còn muốn có nhị tinh thuần thú sư trấn thủ, vị này Hồng thú sư tuy rằng địa vị cũng không thấp, lại chỉ là nhất tinh đỉnh phong, phụ trách xét duyệt... Vẫn không có tư cách.”"),t("br"),t("br"),n._v("Tựa hồ nhìn ra mọi người nghi hoặc, Hồng thú sư mở miệng: “Bây giờ Thiên đường chủ bọn người có chuyện quan trọng cần phải xử lý, ta liền thay bọn họ xét duyệt! Chư vị yên tâm, ta sẽ nghiêm ngặt dựa theo bước đi, sẽ không vì tình riêng mà làm rối kỷ cương, vì lẽ đó, mọi người không cần lo lắng, lại nói, xung quanh có ghi chép ngọc tinh thực lúc ghi chép, chỉ cần phù hợp yêu cầu, sẽ thông qua sát hạch, trở thành Thú đường một thành viên.”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người đồng thời gật đầu, không nói thêm nữa."),t("br"),t("br"),n._v("Bất luận luyện đan sư vẫn là thuần thú sư, bất luận cái nào nghề nghiệp sát hạch, đều cần tại chỗ ghi chép, để phía sau tìm đọc."),t("br"),t("br"),n._v("Có vật này tồn tại, coi như muốn gian dối, cũng không dám."),t("br"),t("br"),n._v("“Chư vị đều là thuần phục thú học đồ, hơn nữa đều thông qua cửa thứ nhất sát hạch, cửa ải thứ hai nội dung cùng sát hạch hình thức, chắc hẳn đã sớm biết, bất quá, hay là muốn sớm nói một chút.”"),t("br"),t("br"),n._v("Hồng thú sư nhìn quanh một vòng: “Cửa ải này yêu cầu là thuần phục một đầu so thực lực mình mạnh man thú, tuy sơ bộ thuần phục liền có thể, nhưng cũng sẽ phân ra đẳng cấp.”"),t("br"),t("br"),n._v("“Man thú so bản thân tu vi cao càng nhiều, trung thành hơn cao, cấp bậc cũng sẽ càng cao.”"),t("br"),t("br"),n._v("“Dựa theo tình huống bình thường, chia làm một, hai, ba, ba đẳng cấp, đẳng cấp càng cao, sát hạch thành tích cũng là vượt qua khá cao. Đối với những cái này, chư vị không sao dị nghị đi!”"),t("br"),t("br"),n._v("“Không có!” Vân Đào mấy người sốt ruột vội vàng gật đầu."),t("br"),t("br"),n._v("“Tốt lắm, hiện tại bắt đầu xét duyệt, cái thứ nhất, Chu Tuyên!” Hồng thú sư nhìn lại."),t("br"),t("br"),n._v("“Vâng!” Chu Tuyên đi lên phía trước."),t("br"),t("br"),n._v("“Ngươi thuần phục chính là cái gì?” Hồng thú sư hỏi."),t("br"),t("br"),n._v("“Hồi bẩm Hồng thú sư, ta thuần phục là đầu kia Khiếu Xỉ Lang! Ích Huyệt cảnh sơ kỳ man thú.” Chu Tuyên chỉ tay, thuộc hạ của hắn lập tức đem một đầu dài hơn ba mét cự lang mang theo lại đây."),t("br"),t("br"),n._v("Cự lang toàn thân than chì, khóe miệng lộ ra sắc bén hàm răng, làm cho người ta một loại dị thường hung mãnh cảm giác."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Gật gật đầu, Hồng thú sư lấy ra một thủy tinh cầu đưa tới."),t("br"),t("br"),n._v("“Đây là kiểm tra man thú trung thành độ Vấn Tâm thạch anh, có thể biểu hiện trị số, sơ bộ thuần phục, trung thành độ tại mười lăm đến ba mươi ở giữa, trị số càng cao, bình cấp cũng sẽ càng cao.”"),t("br"),t("br"),n._v("“Vấn Tâm thạch anh?”"),t("br"),t("br"),n._v("Nhìn thấy vật này, Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Lúc trước học tâm tra hỏi, kiểm tra Lưu Dương độ tín nhiệm cũng là vật này."),t("br"),t("br"),n._v("Xem ra không những có thể kiểm tra người độ tín nhiệm, liền man thú trung thành độ cũng có thể kiểm tra."),t("br"),t("br"),n._v("Ngẫm lại liền có thể hiểu được, vật này có thể làm cho chạm đến người trong đầu xuất hiện ảo cảnh, lợi dụng giả lập cảnh tượng phán đoán tín nhiệm cùng trung thành trình độ, hẳn là phi thường chuẩn xác."),t("br"),t("br"),n._v("Cùng kiểm tra độ tín nhiệm như thế, Chu Tuyên trước nhỏ một giọt huyết dịch vào, sau đó để man thú câu thông."),t("br"),t("br"),n._v("Thời gian không lâu, thạch anh cái trước trị số chậm rãi hiện lên ——20!"),t("br"),t("br"),n._v("“Không tới thời gian nửa tháng, liền để trung thành độ đạt đến 20, phù hợp sơ bộ thuần phục tiêu chuẩn, không tồi không tồi, đánh giá cấp hai!”"),t("br"),t("br"),n._v("Nhìn thấy bên trên trị số, Hồng thú sư vuốt râu thoả mãn gật gù."),t("br"),t("br"),n._v("Tìm tới man thú, sau đó thuần phục, ngăn ngắn nửa tháng không tới, liền đạt đến loại này trung thành độ, tuyệt đối có tư cách trở thành một tinh thuần thú sư."),t("br"),t("br"),n._v("“Điện hạ quả nhiên lợi hại!”"),t("br"),t("br"),n._v("“Đánh giá là cấp hai, đây là Thú đường rất nhiều năm cũng không có xuất hiện đi!”"),t("br"),t("br"),n._v("“Đúng đấy, quá mạnh mẽ!”"),t("br"),t("br"),n._v("Nghe được đánh giá ra thành tích, phía dưới nghị luận sôi nổi."),t("br"),t("br"),n._v("Sát hạch thuần thú sư, cấp ba coi như thông qua, cấp hai, tại mới vừa trở thành thuần thú sư loại này, tuyệt đối tính được là thiên tài."),t("br"),t("br"),n._v("“Cấp hai, không sai, bất quá, ta cũng không kém gì ngươi!”"),t("br"),t("br"),n._v("Không nghĩ tới Chu Tuyên thành tích cao như thế, Chu Cẩm Hoàng sửng sốt một chút, lập tức cười nhạt, cũng đi tới."),t("br"),t("br"),n._v("Rất nhanh kết quả xuất hiện, của hắn đầu kia hổ đầu thú, trung thành độ cũng đạt đến 20 khoảng chừng, cấp hai."),t("br"),t("br"),n._v("“Luân Mạc Vũ tiểu thư!”"),t("br"),t("br"),n._v("“Mạc Vũ tiểu thư đây là phi hành man thú, thuần phục lên bản thân liền khó, lại thêm lên là Thông Huyền cảnh man thú, bổ trợ khẳng định càng to lớn hơn!”"),t("br"),t("br"),n._v("“Đúng đấy, thành tích của nàng e sợ vượt qua cấp hai.”"),t("br"),t("br"),n._v("“Thú đường sát hạch thuần thú sư, đã hơn ba mươi năm không xuất hiện cấp một thành tích, chẳng lẽ nàng biết đánh nhau phá quy củ này?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy Mạc Vũ tiểu thư đi lên phía trước, tất cả mọi người đều lộ ra vẻ sốt sắng, đồng loạt nhìn sang."),t("br"),t("br"),n._v("Sát hạch thuần thú sư đánh giá đẳng cấp đạt đến nhất đẳng, cùng luyện đan sư sát hạch bên trong luyện chế ra hoàn mỹ đan dược như thế."),t("br"),t("br"),n._v("Một khi thành công, tất nhiên chịu đến Thú đường coi trọng, đủ loại tài nguyên cũng sẽ vì thế nghiêng."),t("br"),t("br"),n._v("Chu Tuyên, Chu Cẩm Hoàng có thể sát hạch nhị đẳng thành tích liền rất chói mắt, nhưng mà Mạc Vũ tiểu thư nổi bật càng mạnh hơn, tất cả mọi người đều muốn nhìn một chút, có thể hay không sáng tạo mới lịch sử."),t("br"),t("br"),n._v("Không đếm xỉa xung quanh nghị luận, Mạc Vũ tiểu thư nhấc chân đi lên phía trước, tay ngọc một chiêu, đầu kia to lớn thanh ưng thú liền chậm rãi đi tới trước mặt."),t("br"),t("br"),n._v("Và những người khác bước đi như thế, rất nhanh thi kiểm tra xong."),t("br"),t("br"),n._v("Trung thành độ, 40!"),t("br"),t("br"),n._v("“Nhất đẳng! Là nhất đẳng!”"),t("br"),t("br"),n._v("Nghe xong đánh giá, gian phòng trong nháy mắt sôi trào."),t("br"),t("br"),n._v("Tất cả mọi người đều biết Mạc Vũ tiểu thư thiên tài vô song, nhất định sẽ đạt đến nhất đẳng, chân chính nghe được, vẫn là khiếp sợ không thôi."),t("br"),t("br"),n._v("“Hì hì, Vân Đào đến lượt ngươi, chúng ta đều là nhị đẳng, Mạc Vũ tiểu thư càng là nhất đẳng, vừa nãy ngữ khí lớn lối như vậy, sẽ không chơi cái tam đẳng đi ra đi, thật muốn như vậy, ta cảm thấy đều không mặt mũi gặp người!”"),t("br"),t("br"),n._v("Xem Vân Đào muốn lên sân khấu, Chu Cẩm Hoàng cười nói."),t("br"),t("br"),n._v("“Ta cảm thấy Chu huynh vẫn là quá đánh giá cao hắn, đừng nói tam đẳng, ta cảm thấy... Hắn có thể hay không sát hạch thông qua, đều là cái vấn đề!” Chu Tuyên hừ lạnh: “Con này Kim Thân Thiết Tiễn Báo khẳng định không là của hắn, vậy hắn thuần phục man thú ở đâu?”"),t("br"),t("br"),n._v("“Đúng vậy, sẽ không không có thuần phục man thú chứ?”"),t("br"),t("br"),n._v("“Nếu như không có man thú, liền nói rõ thất bại...”"),t("br"),t("br"),n._v("Hai người âm thanh không có che lấp, lập tức dẫn tới xung quanh một trận ồ lên."),t("br"),t("br"),n._v("Liền ngay cả Mạc Vũ tiểu thư cũng không nhịn được đôi mi thanh tú một nhăn, xem ra lại đây."),t("br"),t("br"),n._v("“Yên tĩnh!”"),t("br"),t("br"),n._v("Khoát tay chặn lại, đánh gãy mọi người cãi vã, Hồng thú sư nhìn về phía đi qua Vân Đào, trong mắt cũng tràn ngập nghi hoặc, không nhịn được hỏi: “Ngươi thuần phục chính là cái gì? Là đầu kia Kim Thân Thiết Tiễn Báo?”"),t("br"),t("br"),n._v("“Không phải, cái kia là Trương Huyền tiền bối thuần phục...”"),t("br"),t("br"),n._v("Vân Đào gãi đầu một cái, có chút lúng túng: “Ta... Thuần phục con chim!”"),t("br"),t("br"),n._v("(Cuối cùng một chương sáu ngàn chữ, ngày hôm nay 0 giờ bạo phát, xin mọi người đem vé tháng cho lão nhai chuẩn bị kỹ càng a!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-249"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-251"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);