(window.webpackJsonp=window.webpackJsonp||[]).push([[1469],{1826:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-244-xu-phat"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-244-xu-phat"}},[n._v("#")]),n._v(" Chương 244: Xử phạt")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thượng Cổ di thú?”"),t("br"),t("br"),n._v("Mọi người sắc mặt đồng thời biến đổi."),t("br"),t("br"),n._v("Man thú chia làm rất nhiều chủng loại, Thượng Cổ di thú, tuyệt đối là huyết mạch mạnh mẽ nhất một trong."),t("br"),t("br"),n._v("Loại này man thú nương theo tuổi tăng trưởng, trong cơ thể huyết mạch kích hoạt, thực lực sẽ có tiến bộ nhảy vọt."),t("br"),t("br"),n._v("Chỉ cần là thuần thú sư, hoàn toàn lấy thuần phục Thượng Cổ di thú làm vinh, bởi vì, thú sủng thực lực tăng cường, thông qua khế ước nối liền, đối thân là chủ nhân hắn cũng có ích lợi rất lớn."),t("br"),t("br"),n._v("Đáng tiếc, loại này man thú ít ỏi không gì sánh được, trên căn bản đã tuyệt tích, không ít thuần thú sư, đi khắp núi sông lâm nhạc, tra khắp nơi thượng cổ thần thoại, đủ loại truyền thuyết, đều tìm tìm không được bất kỳ tung tích nào."),t("br"),t("br"),n._v("Lại có thể xuất hiện ở đây, còn bị Vân Đào... Thuần phục?"),t("br"),t("br"),n._v("“Cái này không thể nào! Coi như cái tên này không phải Cốt Chi Điểu, cũng không thể là Thượng Cổ di thú, vật này, cơ bản đã tuyệt tích...”"),t("br"),t("br"),n._v("Hồng thú sư cái thứ nhất nghi vấn."),t("br"),t("br"),n._v("Những người khác tuy rằng không nói, rất rõ ràng cũng cùng ý nghĩ của hắn như thế, không thể tin được."),t("br"),t("br"),n._v("“Thượng Cổ huyết mạch, sớm đã đoạn tuyệt, coi như có, cũng e sợ sớm đạt đến Linh thú cấp bậc, làm sao có khả năng chỉ là cái phổ thông man thú?”"),t("br"),t("br"),n._v("Chu Cẩm Hoàng không nhịn được hừ lạnh: “Coi như ngươi muốn chạy trốn chịu tội, cũng không dùng chuyện giật gân đi.”"),t("br"),t("br"),n._v("“Liền con quái điều này cũng Thượng Cổ di thú? Đùa gì thế! Ta còn nói ta con này hổ đầu thú là Hồng Hoang dị chủng đây!” Chu Tuyên liên tục cười lạnh."),t("br"),t("br"),n._v("Hồng Hoang dị chủng so với thượng cổ di thú còn lợi hại hơn, số lượng càng thêm ít ỏi."),t("br"),t("br"),n._v("Không những bọn họ như vậy, liền ngay cả Vân Đào cũng khóe miệng kéo, suýt chút nữa không ngất đi."),t("br"),t("br"),n._v("Liền một cái tham ăn, vô sỉ quái điểu thôi, như thế nào cùng Thượng Cổ di thú dính líu quan hệ?"),t("br"),t("br"),n._v("Phải biết, nơi này thế nhưng Thú đường, một khi nói sai, phiền phức liền lớn."),t("br"),t("br"),n._v("“Ha ha!” Trương Huyền cười cợt: “Nếu như ta không đoán sai, Thú đường chắc là có đo lường huyết mạch phương pháp chứ? Không tin cũng không sao, kiểm thử xem sẽ biết được.”"),t("br"),t("br"),n._v("Nếu là Thượng Cổ di thú, huyết mạch đương nhiên từ Thượng Cổ truyền thừa xuống, có chứa đặc thù sức mạnh, thật muốn điều tra, cũng rất dễ dàng làm được."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Phong đường chủ, Hồng thú sư mấy người liếc mắt nhìn nhau, đồng thời gật gật đầu."),t("br"),t("br"),n._v("Nếu như con quái điều này thực sự là Thượng Cổ di thú, tuyệt đối là khiếp sợ toàn bộ Thú đường một chuyện."),t("br"),t("br"),n._v("Bởi vì từ khi cái này phân bộ thành lập tới nay, truyền thừa không dưới ngàn năm, còn chưa bao giờ có thuần thú sư thuần phục một đầu Thượng Cổ huyết mạch man thú."),t("br"),t("br"),n._v("“Truyền thuyết, thời kỳ thượng cổ linh khí không giống hiện tại như vậy ôn hòa, cuồng bạo mãnh liệt, tuy rằng tăng lên tu luyện đến làm ít mà hiệu quả nhiều, nhưng này thời điểm man thú, huyết dịch lại như đốt tan nước như thế, bất cứ lúc nào cũng sẽ sôi trào, mang theo tàn phá sức mạnh. Nếu như nó thực sự là di thú, chỉ cần lấy ra một giọt máu cho hắn man thú dùng, nhất định sẽ không chịu nổi...”"),t("br"),t("br"),n._v("Dừng lại chốc lát, Phong đường chủ chậm rãi nói."),t("br"),t("br"),n._v("Thượng Cổ di thú huyết mạch tinh khiết, cực nóng, cùng hiện tại man thú, là hai loại khái niệm, có lúc một giọt máu cũng có thể không chịu nổi, tại chỗ thiêu đốt hầu như không còn."),t("br"),t("br"),n._v("Thật muốn đo lường, không tính quá khó."),t("br"),t("br"),n._v("Dặn dò một tiếng, một cái học đồ đi ra ngoài, thời gian không lâu ôm một con man thú đi tới."),t("br"),t("br"),n._v("Thỏ rừng kích cỡ tương đương, toàn thân trắng bệch, cùng luyện đan sư công hội thử đan thú tương tự, bất quá, hình thể càng thêm to lớn."),t("br"),t("br"),n._v("Loại này man thú cùng thử đan thú như thế, là Thú đường dùng để thí nghiệm đủ loại thuốc, đồ ăn."),t("br"),t("br"),n._v("“Cho!”"),t("br"),t("br"),n._v("Trương Huyền ngón tay một điểm, một giọt máu liền từ quái điểu trên người nổi lên, nhẹ nhàng bắn ra, hướng Phong đường chủ bay đi."),t("br"),t("br"),n._v("Bàn tay một trảo, huyết dịch bị chân khí bao vây, chậm rãi đưa vào man thú trong miệng."),t("br"),t("br"),n._v("Xì xì xì tư!"),t("br"),t("br"),n._v("Huyết dịch vừa tiến vào trong cơ thể, lập tức như là nồi chảo ném vào giọt nước, phát ra chi “Tư lạp!” Âm thanh, rất nhanh này con man thú liền trở nên toàn thân ửng hồng, liên tục lăn lộn trên mặt đất, thời gian không lâu liền ngừng lại, cắt đứt hô hấp."),t("br"),t("br"),n._v("Rõ ràng là bị dòng máu mạnh mẽ thiêu đốt mà chết."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Đem tình cảnh này nhìn ở trong mắt, Phong đường chủ mấy người con ngươi co rụt lại."),t("br"),t("br"),n._v("Cũng chỉ có loại này huyết mạch sức mạnh, mới có uy lực như thế."),t("br"),t("br"),n._v("Trầm tĩnh!"),t("br"),t("br"),n._v("Bên trong gian phòng yênn tĩnh giống như chết."),t("br"),t("br"),n._v("Giờ khắc này kẻ ngu đến đâu cũng biết, vị thanh niên này nói không sai, trước mắt con này xem ra không đáng chú ý quái điểu, xác thực là Thượng Cổ di thú."),t("br"),t("br"),n._v("Dù cho cái tên này cái đầu tiểu, chỉ bằng vào trong cơ thể huyết mạch, liền so Chu Tuyên, Chu Cẩm Hoàng hai người thuần phục man thú cao quý mấy chục lần."),t("br"),t("br"),n._v("Hai người căn bản không phải cùng một cấp bậc, thật giống như kim cương cùng bùn nhão khác nhau, không thể giống nhau."),t("br"),t("br"),n._v("“Ta vừa nãy cười nhạo Thượng Cổ di thú?”"),t("br"),t("br"),n._v("Chu Cẩm Hoàng, Chu Tuyên khóe miệng co giật, đều cảm thấy da mặt bị mạnh mẽ đánh một cái tát, sắp khóc."),t("br"),t("br"),n._v("Đây chính là Thượng Cổ di thú, tại Thú đường tuyệt đối có thể dẫn tới náo động tồn tại, lại bị bọn họ nói thành phá chim, không đáng giá một đồng..."),t("br"),t("br"),n._v("Loại này nhãn lực, lại vẫn chẳng biết xấu hổ nói mình là thuần thú sư?"),t("br"),t("br"),n._v("Truyền đi tuyệt đối sẽ bị người xem là chuyện cười, coi như trở thành chính thức thuần thú sư, cũng sắp trở thành nhân sinh một đại nét bút hỏng, cọ rửa không xong."),t("br"),n._v("Người khác sự chú ý đều tập trung tại đây đầu Vân Điện Cổ Tước trên người, mà Mạc Vũ tiểu thư sắc mặt ngưng lại, không nhịn được nhìn về phía cách đó không xa người thanh niên này."),t("br"),t("br"),n._v("Liền Phong đường chủ mấy người cũng chưa nhận ra được, cái tên này lại chậm rãi mà nói, không uý kỵ tí nào, hắn... Đến cùng từ nơi nào nhô ra?"),t("br"),t("br"),n._v("Thiên Huyền Vương quốc loại này địa phương nhỏ, có thể xuất hiện nhân vật lợi hại như thế?"),t("br"),t("br"),n._v("“Ta... Dĩ nhiên phán định thuần phục Thượng Cổ di thú Vân Đào thất bại?”"),t("br"),t("br"),n._v("Hồng thú sư sắc mặt trở nên trắng bệch, phảng phất một hồi già đi mười tuổi."),t("br"),t("br"),n._v("Thượng Cổ di thú, vậy cũng là vô số thuần thú sư muốn muốn thuần phục lại không làm được!"),t("br"),t("br"),n._v("Không những là khó có thể tìm kiếm, chủ yếu nhất chính là, loại này man thú trí tuệ cực cao, bình thường sẽ không thần phục với người... Coi như là hắn loại này thuần phục thú thủ đoạn, gặp gỡ đều khó mà thành công, bị một cái chỉ là thuần phục thú học đồ gia hỏa thuần phục, bản thân lại vẫn phán người ta thất bại, để hắn cút ra ngoài..."),t("br"),t("br"),n._v("Không để ý tới mọi người giật mình, Trương Huyền bước lên trước, thanh âm nhàn nhạt tiếp tục vang lên."),t("br"),t("br"),n._v("“Vân Điện Cổ Tước, có thể hấp thu lôi điện chi lực, kích hoạt cổ mạch, xông thẳng lên trời! Loại này cổ tước, cùng Cốt Chi Điểu xác thực tương tự, nhưng cũng có không ít không giống địa phương, đầu tiên là ánh mắt. Cốt Chi Điểu bởi vì ăn thịt cùng ăn cỏ hỗn tạp, ánh mắt vẩn đục không ánh sáng, mà cổ tước mang theo thấu hồn phách người chói mắt, tuy rằng không quá rõ ràng, hơi hơi chú ý, vẫn là rất dễ dàng có thể nhìn ra.”"),t("br"),t("br"),n._v("“Tiếp theo là móng trảo, không những lòng bàn tay có điểm đỏ, chủ yếu nhất chính là, cổ tước móng vuốt càng cứng rắn hơn sắc bén, bình thường binh khí đều có thể nhẹ nhõm xé nát, chớ nói chi là người da thịt. Hồng thú sư nói nó không sao sức chiến đấu, đó là bởi vì nó còn tuổi nhỏ, sẽ không vận dụng thôi, một khi phát huy được, đừng nói Ích Huyệt cảnh, coi như Thông Huyền cảnh cường giả, e sợ đều sẽ bị một trảo xé thành hai nửa.”"),t("br"),t("br"),n._v("“Đương nhiên, hai thứ này, xem như là không tốt lắm nhận biết đặc thù một trong, bình thường thuần thú sư đều khó mà nhận ra.”"),t("br"),t("br"),n._v("Vẻ mặt hờ hững, Trương Huyền dừng lại một chút, chỉ tay Vân Điện Cổ Tước trên đầu: “Rõ ràng nhất chính là trên đầu nó mào gà, tuy rằng không lớn, lại hiện màu đỏ xanh, có ‘Vương’ tượng trưng, nắm giữ loại này hồng mang thêm, coi như không hiểu thuần phục thú người, đều biết này chim tuyệt không đơn giản, không phải Cốt Chi Điểu loại này cấp bậc thấp dã thú có thể so với! Như vậy biểu hiện đặc thù đều không nhìn thấy, vừa mở miệng liền nhận định sai lầm, không qua ải...”"),t("br"),t("br"),n._v("Nói đến đây, Trương Huyền lạnh lùng nhìn sang: “Hồng thú sư, làm một cái thuần thú sư, ngươi chính là như vậy kiểm tra man thú?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Hồng thú sư sắc mặt trắng nhợt, tại ánh mắt của hắn tiêm vào hạ, không nhịn được lùi về sau một bước."),t("br"),t("br"),n._v("“Ai cho sự tự tin của ngươi, để ngươi trực tiếp phán định Vân Đào không hợp cách, để hắn cút ngay?”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục hừ lạnh."),t("br"),t("br"),n._v("Thịch thịch thịch!"),t("br"),t("br"),n._v("Bị hắn ngữ khí bức bách, Hồng thú sư lại không vừa nãy khí thế, lại lần nữa lùi về sau."),t("br"),t("br"),n._v("Vân Điện Cổ Tước tại Trương Huyền đem hắn đánh bất tỉnh, liền hình thành thư tịch, trong đó có tỉ mỉ giới thiệu, vì lẽ đó đã sớm biết tên gọi."),t("br"),t("br"),n._v("Vốn là cái tên này xét duyệt sát hạch, hơi hơi nghiêm túc, khiêm tốn thỉnh giáo, chắc chắn sẽ không ẩn giấu, nói thẳng ra."),t("br"),t("br"),n._v("Có thể... Đều vạch ra hắn sai lầm, lại vẫn chân khí phun ra, thậm chí gọi ra thú sủng, muốn ra tay!"),t("br"),t("br"),n._v("Bản thân là Thông Huyền cảnh đỉnh phong, có thể không úy kỵ khí thế cùng áp bức, một khi tu vi thấp, e sợ vừa nãy cái kia một lần, cũng đã miệng mũi ra máu, bị thương nặng."),t("br"),t("br"),n._v("Nếu đều không nể mặt mũi, hắn đương nhiên cũng sẽ không cho đối phương sắc mặt tốt."),t("br"),t("br"),n._v("“Là ta sơ sẩy...”"),t("br"),t("br"),n._v("Hồng thú sư sắc mặt khó coi."),t("br"),t("br"),n._v("Đối phương không có một chút nào sai lầm, coi như hắn muốn phản bác cũng phản bác không được."),t("br"),t("br"),n._v("“Thân gồm cả sát hạch thuần thú sư chi trách, lại dựa theo bản thân hỉ nộ quyết định, không làm được công bằng công chúng, suýt nữa để một cái có thể thuần phục Thượng Cổ di thú thuần thú sư minh châu phủ bụi!”"),t("br"),t("br"),n._v("Trương Huyền quăng một cái ống tay áo: “Đây chính là Thú đường hành động? Nếu như đúng là như vậy, ta nói là giá áo túi cơm, có thể có sai lầm?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Nghe được đối diện, sắc bén nghi vấn lời nói, Hồng thú sư thân thể run rẩy, lại mảy may phản bác không ra."),t("br"),t("br"),n._v("Trước nghe được lời của đối phương, còn tức giận sắp nổ tung, biết mình suýt chút nữa đem thuần phục Thượng Cổ di thú thuần thú sư đuổi đi, liền hiểu được, coi như bị người như vậy mắng, cũng không cách nào phản bác."),t("br"),t("br"),n._v("Sát hạch thuần thú sư, bản thân liền là Thú đường tuyển chọn nhân tài, nhân tài như vậy bị đuổi đi, dĩ nhiên xúc phạm Thú đường quy tắc."),t("br"),t("br"),n._v("“Vị tiểu huynh đệ này không nên tức giận, Thượng Cổ di thú, chủng loại nhiều, hơn nữa tám chín phần mười cũng đã chôn vùi tại lịch sử bên trong, coi như là Thú đường cũng không thể đem hết thảy chủng loại tất cả thống kê chỉnh tề, không may xuất hiện, cũng coi như bình thường!”"),t("br"),t("br"),n._v("Phong đường chủ cười cợt, đi lên phía trước: “Như vậy đi, nếu là chúng ta Thú đường sơ sẩy, chúng ta cũng không từ chối, hiện tại liền cho Vân Đào một lần nữa xét duyệt, sau đó một lần nữa đánh giá! Cho tới Hồng thú sư, sát hạch không chăm chú, lơ là nhiệm vụ, bắt đầu từ hôm nay, liền đi thú lâm nuôi dưỡng man thú, trong vòng ba năm, không thể ra ngoài một bước!”"),t("br"),t("br"),n._v("Nói đến đây, nhìn sang: “Hồng thú sư, ngươi có đồng ý hay không của ta xử phạt?”"),t("br"),t("br"),n._v("“Tại hạ không hề lời oán hận!” Hồng thú sư khom người lại, vẻ mặt chán nản."),t("br"),t("br"),n._v("“Một câu nói để Hồng thú sư đi thú lâm ba năm, không thể ra ngoài?”"),t("br"),t("br"),n._v("“Chuyện này... Quá ác chứ?”"),t("br"),t("br"),n._v("“Thú lâm là nuôi nhốt man thú địa phương, cùng cái gọi là chuồng lợn tương tự, bình thường đều là học đồ đi tới, đường đường nhất tinh đỉnh phong Hồng thú sư ở nơi đó ba năm?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được quyết định này, tất cả mọi người tất cả đều sửng sốt, tiếp theo lại lần nữa nhìn về phía Trương Huyền, trong mắt tràn đầy sợ hãi."),t("br"),t("br"),n._v("Đây chính là Hồng thú sư, Thú đường ngoại trừ ba vị nhị tinh thuần thú sư ở ngoài, địa vị cao nhất thuần thú sư một trong, bị hắn một câu nói, liền lưu đày đến cấp thấp nhất thú lâm làm khổ sai sự tình, ba năm không thể ra ngoài, loại năng lực này cùng thủ đoạn cũng thật đáng sợ!"),t("br"),t("br"),n._v("Mọi người mang theo sợ hãi, mà lấy tư cách người có liên quan Trương Huyền, thì lặng lẽ cho Thẩm Bích Như đưa tin."),t("br"),t("br"),n._v("“Cái này... Thú lâm là nơi nào? Cái tên này trừng phạt có nặng hay không?”"),t("br"),t("br"),n._v("“...” Thẩm Bích Như tú mục trợn tròn, đầy mặt rung mạnh."),t("br"),t("br"),n._v("(Canh thứ ba đến, lão nhai tiếp tục viết chương thứ tư, vé tháng liền xin nhờ các ngươi!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-252"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-254"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);