(window.webpackJsonp=window.webpackJsonp||[]).push([[1471],{1825:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-246-nguoi-thi-thuan-thu-su-sao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-246-nguoi-thi-thuan-thu-su-sao"}},[n._v("#")]),n._v(" Chương 246: Ngươi thi thuần thú sư sao?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đánh... Một trận?”"),t("br"),t("br"),n._v("Tất cả mọi người ngẩn ngơ."),t("br"),t("br"),n._v("Phong đường chủ cũng khóe miệng kéo, suýt chút nữa không ngất đi."),t("br"),t("br"),n._v("Đây chính là Thượng Cổ di thú, toàn bộ Huyền Lạc sơn mạch phạm vi cũng chưa chắc có thể vượt qua con thứ hai tồn tại, tốt bảo bối tốt, cố gắng che chở cũng không kịp... Đánh một trận?"),t("br"),t("br"),n._v("Lại nói, thuần phục thú cần bồi dưỡng cảm tình, tăng cường độ khớp, đánh bên trên một trận, có lẽ có thể để cho giả vờ khuất phục, chỉ khi nào thời gian lâu dài, sẽ xuất hiện đủ loại tai hại."),t("br"),t("br"),n._v("Bất quá... Nếu như nói là dùng cứng rắn thủ đoạn bức bách con này Vân Điện Cổ Tước thuần phục mà nói, trung thành độ bốn mươi lại là cái gì quỷ?"),t("br"),t("br"),n._v("Ép buộc xác thực có thể thuần phục man thú, trước đây cũng từng có tiền lệ, nhưng ép buộc hạ man thú, coi như ký kết khế ước, trung thành độ cũng chỉ là triệt để thuần phục bên trong cấp thấp nhất, đại khái tại 31 khoảng chừng."),t("br"),t("br"),n._v("Bốn mươi điểm trung thành độ, có thể nói thành thật thực lòng khuất phục, không có nửa điểm trái lương tâm mới có thể làm đến!"),t("br"),t("br"),n._v("“Lẽ nào... Thượng Cổ di thú cần phương pháp này thuần phục?”"),t("br"),t("br"),n._v("Phong đường chủ, Lư thú sư, Vương thú sư liếc nhau một cái, từng người suy tư."),t("br"),t("br"),n._v("Thượng Cổ di thú, chưa bao giờ có thuần phục tiền lệ, tất cả mọi người cũng không biết làm sao thuần phục, vốn tưởng rằng cũng cùng cái khác man thú như thế, cần đủ loại quan tâm, đủ loại che chở, cùng với giao hữu mới có thể thành công, không nghĩ tới... Chẳng lẽ là đơn giản nhất bạo lực?"),t("br"),t("br"),n._v("“Cái này... Trong miệng ngươi nói vị này Trương Huyền tiền bối không biết ở nơi nào? Chúng ta có thể không bái phỏng một lần?”"),t("br"),t("br"),n._v("Nghĩ đến một hồi, thực sự không nghĩ ra, Phong đường chủ không nhịn được nói."),t("br"),t("br"),n._v("Dưới cái nhìn của hắn, vị này đối với thượng cổ di thú có nghiên cứu tiền bối, tất nhiên đức cao vọng trọng, đối thuần phục thú lý giải, không so với bọn họ thấp."),t("br"),t("br"),n._v("“Bái phỏng? Không cần, Trương Huyền tiền bối liền ở ngay đây...”"),t("br"),t("br"),n._v("Sửng sốt một chút, Vân Đào chỉa sang."),t("br"),t("br"),n._v("“Hắn chính là Trương Huyền... Tiền bối?”"),t("br"),t("br"),n._v("Phong đường chủ sững sờ, trước mắt chóng mặt."),t("br"),t("br"),n._v("Vân Đào bất kể nói thế nào đều là Hàn Vũ Vương quốc vương tử điện hạ, hai mươi bảy, tám tuổi, để hắn xưng hô tiền bối, tại mấy người xem ra, làm sao cũng muốn năm, sáu mươi tuổi, kết quả... Là cái này mới vừa nhận ra Vân Điện Cổ Tước, không đủ hai mươi tiểu tử..."),t("br"),t("br"),n._v("“Đúng đấy, đừng xem tiền bối tuổi trẻ, thuần phục thú thực lực, nhưng là thật, thấy không, bên cạnh hắn đầu kia Kim Thân Thiết Tiễn Báo, cũng là mới vừa thuần phục, từ gặp phải đến thuần phục, tất cả gộp lại, liền mười phút cũng chưa tới.”"),t("br"),t("br"),n._v("Chỉ lo Phong đường chủ mấy người không trọng thị, Vân Đào vội vàng đối Trương Huyền tán dương một câu."),t("br"),t("br"),n._v("“Không tới mười phút... Thuần phục Kim Thân Thiết Tiễn Báo?”"),t("br"),t("br"),n._v("“Này không phải nằm mơ chứ?”"),t("br"),t("br"),n._v("“Nói bậy cũng phải có cái mức độ, coi như đơn giản nhất sơ cấp thuần phục, không có ba, năm ngày đều rất khó hoàn thành, không tới mười phút?”"),t("br"),t("br"),n._v("“Tại sao ta cảm giác đang nghe chuyện cười đâu?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Vân Đào âm thanh không có che lấp, gian phòng người đều nghe được, lại lần nữa hai mặt nhìn nhau, ngốc tại chỗ."),t("br"),t("br"),n._v("Không nói Mạc Vũ, Chu Tuyên mấy người, liền nói phía sau bọn họ những hộ vệ kia, đều tham dự thuần phục thú quá trình, biết muốn muốn thuần phục một con man thú là cỡ nào khó khăn."),t("br"),t("br"),n._v("Trước cần phải thấu hiểu đối phương tình huống, tập tính, có hay không bạn ngẫu, yêu ghét..."),t("br"),t("br"),n._v("Đem những cái này tất cả quen thuộc, sau đó sẽ nhằm vào làm ra phương án, vẫn chưa thể đường đột... Nói chung, vì thuần phục một con man thú, thêm vào chuẩn bị công tác mà nói, không có mấy tháng thời gian, không thể thành công, mười phút... Lừa gạt quỷ chứ?"),t("br"),t("br"),n._v("Đừng nói thuần phục Ích Huyệt cảnh man thú, cho ngươi con chó, mười phút, có thể làm cho nó ngoan ngoãn cùng ngươi về nhà không?"),t("br"),t("br"),n._v("Mạc Vũ tiểu thư cũng không nhịn được nhìn về phía cách đó không xa từ chối nàng thanh niên, tràn đầy không thể tin được."),t("br"),t("br"),n._v("Mười phút thuần phục một con man thú?"),t("br"),t("br"),n._v("Nếu như là thật sự, bản thân còn với hắn khiêu chiến... Này không phải tìm ngược sao?"),t("br"),t("br"),n._v("Vì thuần phục con này Thanh Ưng Thú, nàng thế nhưng tiêu tốn thời gian rất lâu, nhấc lên chuẩn bị trước ròng rã hai tháng..."),t("br"),t("br"),n._v("“Như thế trong thời gian ngắn thuần phục Kim Thân Thiết Tiễn Báo? Hắn... Dùng phương pháp gì? Bạn ngẫu hấp dẫn pháp? Ăn uống hấp dẫn pháp? Sức mạnh hấp dẫn pháp... Vẫn là cái gì thủ đoạn khác?”"),t("br"),t("br"),n._v("Lư thú sư không nhịn được nói, vừa mở miệng nói ra mười mấy loại thuần phục thú trung bình thấy thủ đoạn cùng phương pháp."),t("br"),t("br"),n._v("“Không đúng, những phương pháp này, so sánh cơ sở, thuần phục lên đem so sánh chậm, ta cảm thấy hẳn là nhiều thủ đoạn dung hợp phương pháp, kéo tơ bóc kén tầng tầng tiến dần lên, trong thời gian ngắn bắt lấy đối phương hứng thú, khó có thể dứt bỏ...”"),t("br"),t("br"),n._v("Phong đường chủ mở miệng."),t("br"),t("br"),n._v("“Những phương pháp này quá nhiều, trong thời gian ngắn khẳng định không thể hoàn thành, ta suy đoán, khẳng định là một loại thủ đoạn...” Lư thú sư lắc đầu."),t("br"),t("br"),n._v("Hai một đời người say mê thuần phục thú, sớm đem nghề nghiệp này hòa vào cốt tủy, nghe được ngắn như vậy thời gian thuần phục man thú, đầu tiên nghĩ đến chính là dùng thủ đoạn gì."),t("br"),t("br"),n._v("“Khặc khặc, Phong đường chủ, Lư thú sư, Trương Huyền tiền bối... Những phương pháp này đều vô dụng!”"),t("br"),t("br"),n._v("Nghe được bọn họ nghị luận càng ngày càng nhiều, càng ngày càng phức tạp, thậm chí rất nhiều học thuật nghiên cứu xu thế, Vân Đào tại cũng không nhịn được cản bọn họ lại nghị luận."),t("br"),t("br"),n._v("“Đều vô dụng? Lẽ nào là... Cái gì mới thuần phục thú phương pháp?”"),t("br"),t("br"),n._v("Phong đường chủ mấy người ánh mắt sáng lên."),t("br"),t("br"),n._v("Mới thuần phục thú phương pháp, cùng luyện đan sư thủ pháp luyện đan như thế, một khi xuất hiện, tất nhiên dẫn tới Hạo Nhiên sóng lớn."),t("br"),t("br"),n._v("Có thể mười mấy phút thuần phục một đầu Ích Huyệt cảnh mạnh mẽ man thú, phương pháp này thật muốn truyền đi, khẳng định khiếp sợ tất cả mọi người."),t("br"),t("br"),n._v("“Không phải mới thuần phục thú phương pháp, mà là...” Vân Đào vò đầu, tựa hồ nhớ tới trước sự tình, vẫn như cũ cảm thấy khó mà tin nổi: “Trương Huyền tiền bối cũng đem con này Kim Thân Thiết Tiễn Báo đánh một trận, sau đó... Nó liền thần phục!”"),t("br"),n._v(" “Dát?”"),t("br"),t("br"),n._v("“Cũng đánh một trận?”"),t("br"),t("br"),n._v("Phong đường chủ mấy người trợn mắt ngoác mồm."),t("br"),t("br"),n._v("Thuần phục Thượng Cổ di thú, đánh một trận, có thể nói là giống không giống, dùng phương pháp này có lẽ đi đến thông, có thể... Kim Thân Thiết Tiễn Báo cái tên này Thú đường có thuần phục tiền lệ, tính cách kiêu ngạo, tốc độ cực nhanh, luôn luôn không cùng người giao lưu, muốn muốn thuần phục, tốt nhất phương pháp là bạn ngẫu hấp dẫn pháp..."),t("br"),t("br"),n._v("Đánh một trận liền thần phục..."),t("br"),t("br"),n._v("“Đúng đấy, kỳ thực ta vốn là là dự định thuần phục con này Kim Thân Thiết Tiễn Báo, kết quả đánh giá thấp thực lực của nó, suýt chút nữa bị giết, là Trương Huyền tiền bối xuất hiện, đã cứu chúng ta. Tiền bối đem cái tên này đánh xong, nó liền hoàn toàn thần phục, chủ động yêu cầu ký kết khế ước, nói thật, tiền bối còn có chút không tình nguyện, nếu không là ta khuyên can, hắn e sợ đều không đáp ứng.”"),t("br"),t("br"),n._v("Nhớ tới trước trải qua, Vân Đào không nhịn được nói."),t("br"),t("br"),n._v("“Đánh xong liền thần phục? Vẫn là hoàn toàn thần phục, yêu cầu ký kết khế ước?”"),t("br"),t("br"),n._v("“Kim Thân Thiết Tiễn Báo muốn thần phục, còn không tình nguyện...”"),t("br"),t("br"),n._v("“Người khác thuần phục man thú, đều cùng trải qua sinh tử như thế, tiền tài tiêu hao hết, lột một lớp da, hắn đánh một trận liền thần phục...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe nói như thế, tất cả mọi người lại lần nữa loáng một cái, suýt chút nữa máu tươi phun ra."),t("br"),t("br"),n._v("Đặc biệt là Chu Tuyên, Chu Cẩm Hoàng hai người, chỉ cảm thấy toàn thân run rẩy, sắp ngất đi."),t("br"),t("br"),n._v("Bọn họ vì thuần phục bên cạnh man thú, từ từng người Vương quốc điều khiển nhân lực vật lực, đủ loại tiền tài đưa vào càng là đếm không hết, dù vậy, còn chỉ là sơ cấp thuần phục, đối phương bất cứ lúc nào cũng có thể bỏ gánh không liên quan, cái tên này ngược lại tốt, tùy tiện đánh một trận, liền triệt để thuần phục..."),t("br"),t("br"),n._v("Thuần phục thú khi nào đơn giản như vậy?"),t("br"),t("br"),n._v("“Đánh một trận, liền có thể thuần phục, có thể nhận ra Thượng Cổ di thú cũng thuần phục, không có tác dụng phương pháp gì tại bên trong, đều là thuần phục thú thiên tài!”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, Lư thú sư không nhịn được hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Đúng rồi, hắn có phải là... Thuần thú sư?”"),t("br"),t("br"),n._v("Đột nhiên nhớ tới cái gì, Vương thú sư không nhịn được hỏi."),t("br"),t("br"),n._v("Có thể đơn giản thuần phục man thú, nên đã là thuần thú sư đi, bất quá, nếu như đúng, vì sao Thú đường không có ghi chép?"),t("br"),t("br"),n._v("“Hắn còn không phải, vẫn chưa đã tham gia sát hạch! Chỉ là nghiệp dư học tập thuần phục thú bản lĩnh!” Vân Đào nói."),t("br"),t("br"),n._v("Trên đường tới, đã hỏi dò rõ ràng, Trương Huyền tiền bối, không đã tham gia bất luận cái gì thuần phục thú sát hạch."),t("br"),t("br"),n._v("Dân gian là có rất lớn một nhóm thiên tài, không đã tham gia bất luận cái gì sát hạch, lại nắm giữ đặc thù thiên phú."),t("br"),t("br"),n._v("“Còn không sát hạch? Quá tốt rồi, ngươi giúp ta hỏi một chút, hắn có nguyện ý hay không sát hạch thuần thú sư, chỉ cần đồng ý, ta đồng ý đơn độc vì hắn sát hạch!”"),t("br"),t("br"),n._v("Phong đường chủ vội nói."),t("br"),t("br"),n._v("Có thể đơn giản như vậy thuần phục man thú, thủ đoạn kinh người, một khi trở thành thuần thú sư, tất nhiên quang mang vạn trượng, cái này Thú đường phân bộ, làm không cẩn thận... Cũng sẽ nhờ đó danh dương thiên hạ, mấu chốt nhất chính là, nếu như người này thật cùng trong truyền thuyết như thế, nắm giữ đánh một trận liền để man thú thuần phục bản lĩnh, tham gia cái kia đại hội... Tuyệt đối có thể một lần đoạt được thứ nhất!"),t("br"),t("br"),n._v("Đương nhiên, hắn còn cần tự mình sát hạch một lần, tận mắt chứng kiến một, hai."),t("br"),t("br"),n._v("“Sát hạch thuần thú sư? Ta giúp hỏi một chút...”"),t("br"),t("br"),n._v("Vân Đào gật gù, vài bước đi tới Trương Huyền trước mặt: “Tiền bối, Phong đường chủ nói, chỉ cần ngươi đồng ý sát hạch thuần thú sư, hắn đồng ý đơn độc vì ngươi sát hạch...”"),t("br"),t("br"),n._v("“Vẫn là quên đi...”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền từ chối."),t("br"),t("br"),n._v("Hắn có thể thuần phục thú, là bởi vì thư viện, thật muốn sát hạch mà nói, một chữ cũng không biết, khẳng định lập tức lòi."),t("br"),t("br"),n._v("Bất quá, chỉ cần cho đầy đủ thư xem, như thế có thể giải quyết, này không hề là mấu chốt nhất, quan trọng nhất chính là... Thi cái thuần phục thú muốn hơn nửa tháng, phía trước còn không biết cần trải qua bao nhiêu cửa ải."),t("br"),t("br"),n._v("Trong cơ thể kịch độc sự tình, nhất định phải cố gắng nhanh giải quyết, thật không quá nhiều thời gian lãng phí ở đây."),t("br"),t("br"),n._v("“Đây chính là cơ hội tốt, Phong đường chủ tự mình sát hạch...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn trực tiếp từ chối, Vân Đào không nhịn được mở miệng."),t("br"),t("br"),n._v("Phong đường chủ chuyên môn thêm thử, đây chính là ngàn năm một thuở cơ hội tốt, không ít người tha thiết ước mơ cũng không chiếm được."),t("br"),t("br"),n._v("“Ta có chuyện quan trọng muốn chạy đi, ngược lại ngươi thành thuần thú sư, giúp ta tuyển một đầu khá một chút phi hành man thú là được, thi không thi cũng không đáng kể!” Trương Huyền nói."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Thấy hắn xác thực không có quá bất cẩn hướng muốn sát hạch thuần thú sư, Vân Đào lắc đầu một cái, có chút tiếc hận: “Kỳ thực sát hạch thuần thú sư, chỉ cần tìm được thích hợp man thú thuần phục liền có thể, cũng không nhất định nhất định phải thời gian nửa tháng. Hơn nữa, thành chính thức thuần thú sư, không những có thể mình lựa chọn man thú, cưỡi Thú đường man thú, còn không cần giao nộp bất luận cái gì phí dụng, thậm chí có thể tại bất luận cái gì Thú đường, tùy ý thay đổi vật cưỡi...”"),t("br"),t("br"),n._v("“Không cần giao nộp phí dụng?”"),t("br"),t("br"),n._v("Lời còn chưa dứt, liền nhìn thấy cách đó không xa bình tĩnh không gì sánh được Trương Huyền, chẳng biết lúc nào đi tới trước mặt, hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Đúng đấy, chính thức thuần thú sư, có cái đặc quyền này, nhất tinh thuần thú sư, dùng Tông Sư cảnh trở xuống man thú miễn phí. Nhị tinh thuần thú sư, dùng Tông Sư cảnh man thú miễn phí, cứ thế mà suy ra, đẳng cấp càng cao, địa vị cũng là càng cao...”"),t("br"),t("br"),n._v("Vân Đào giải thích, lời còn chưa nói hết, liền thấy Trương Huyền tiền bối, một mặt nghiêm túc nhìn lại, trong giọng nói mang theo kiên định."),t("br"),t("br"),n._v("“Miễn phí cái gì, đều là việc nhỏ, không cần nhiều lời! Thuần thú sư làm sao thi...”"),t("br"),t("br"),n._v("“Nếu không... Ta thi một lần thử xem!”"),t("br"),t("br"),n._v("(Thật không tiện, chậm chút, đầu tháng ngày thứ hai, tiếp tục cầu vé tháng, phía sau đại thần thế tới hung hăng, mọi người thêm đem dầu, lão nhai sẽ cố lên gõ chữ!!! Cảm tạ!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-254"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-256"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);