(window.webpackJsonp=window.webpackJsonp||[]).push([[1472],{1827:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-247-muc-đo-10-tro-len"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-247-muc-đo-10-tro-len"}},[n._v("#")]),n._v(" Chương 247: Mức độ 10 trở lên?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Vân Đào không phản ứng lại."),t("br"),t("br"),n._v("Mới vừa không trả thái độ kiên quyết nói không thi sao? Làm sao một lần liền chuyển biến họa phong?"),t("br"),t("br"),n._v("Nói chuyện cưỡi phi hành man thú miễn phí, lập tức dáng dấp này..."),t("br"),t("br"),n._v("Vân Đào chỉ cảm thấy nước bọt sặc tại yết hầu, suýt chút nữa nghẹn chết."),t("br"),t("br"),n._v("Ở trong mắt hắn, vị này Trương Huyền tiền bối là thiếu niên Tông Sư, tu vi sâu không lường được, thực lực cao cao không thể với tới, người như thế chắc là coi tiền tài như cặn bã mới là, vừa nghe thấy miễn phí liền hai mắt tỏa ánh sáng, khặc khặc, cũng quá thực tế đi!"),t("br"),t("br"),n._v("“Tốt lắm...”"),t("br"),t("br"),n._v("Thật vất vả đem nước bọt nuốt xuống, chính muốn nói chuyện, liền thấy Mạc Vũ tiểu thư nhìn lại, kiêu ngạo cổ vung lên, trong ánh mắt lộ ra cười gằn."),t("br"),t("br"),n._v("“Thi một lần thử xem? Hừ, ngươi cho rằng thuần thú sư là trò đùa, tùy tiện liền có thể thi? Đúng dịp thuần phục hai con man thú, liền cảm thấy thuần thú sư đơn giản! Nói thật cho ngươi biết, chỉ đại lục thông thường mấy vạn loại man thú tên, thân thể đặc thù, tính tình, sinh hoạt tập tính mấy người rất nhiều đặc thù, muốn thuộc xuống, không có mấy năm liền không làm được!”"),t("br"),t("br"),n._v("Mới vừa khiêu chiến cái tên này, lại có thể từ chối, để Mạc Vũ trong lòng tức sôi ruột, giờ khắc này thấy hắn dĩ nhiên nói khoác không biết ngượng, lập tức hừ lạnh phản kích."),t("br"),t("br"),n._v("Còn thi một lần thử xem..."),t("br"),t("br"),n._v("Thuần thú sư là thượng cửu lưu nghề nghiệp bên trong, khá cao nghề nghiệp một trong, độ khó lớn, không thấp hơn luyện đan sư, ngươi cho rằng là chơi nhà chòi, đúng dịp thuần phục một hai con man thú, là có thể sát hạch?"),t("br"),t("br"),n._v("Đây giống như là mơ mơ hồ hồ xứng đáng cái dược, chữa khỏi chứng bệnh, lẽ nào liền cảm giác mình có thể thi y sư?"),t("br"),t("br"),n._v("Nằm mơ đi!"),t("br"),t("br"),n._v("Mỗi một cái nghề nghiệp, đều có thuộc về mình học vấn hệ thống, ảo diệu tinh thâm, không phải một ngày hai ngày, liền có thể học được."),t("br"),t("br"),n._v("Đặc biệt là thuần phục thú, tu vi thấp man thú, có thể dùng man lực chinh phục, tu vi cao thâm đâu?"),t("br"),t("br"),n._v("Thật muốn dám vô lễ, e sợ đối phương trước đem ngươi diệt."),t("br"),t("br"),n._v("Không biết đối phương tập tính, không biết hồng nhạn nam trở về, con cá bơi lội, liền dám nói sát hạch thuần thú sư, không phải nói khoác không biết ngượng là cái gì?"),t("br"),t("br"),n._v("“Mạc Vũ tiểu thư, không cần cùng loại này tự đại, cái gì cũng không hiểu người tức giận! May mắn đã biết tên Vân Điện Cổ Tước, không biết làm sao thuần phục Kim Thân Thiết Tiễn Báo, liền cảm thấy trời đất bao la, Duy Ngã Độc Tôn. Trên thực tế đối với thuần phục thú một đạo, cái gì cũng không biết, cái gì cũng không hiểu, một khi sát hạch, khẳng định lập tức lòi!”"),t("br"),t("br"),n._v("Chu Cẩm Hoàng bước lên trước."),t("br"),t("br"),n._v("Thuần phục thú là một môn học vấn, coi như có thiên phú, không có thời gian mấy năm, cũng chỉ có thể học được da lông, cái tên này cũng không là thuần phục thú học đồ, nhìn dáng dấp lại không tiếp xúc qua, sẽ hai chiêu mèo quào thủ đoạn liền muốn sát hạch?"),t("br"),t("br"),n._v("“Không biết trời cao đất rộng gia hỏa mà thôi, thuần thú sư không những là thuần phục man thú, còn muốn đối man thú từ trong tới ngoài cũng giải mới được, dựa vào man lực đánh thắng man thú, bức bách khuất phục, liền nói mình là thuần thú sư, đùa gì thế! Nếu như đúng là như vậy, chẳng phải người người đều biến thành thuần thú sư?”"),t("br"),t("br"),n._v("Chu Tuyên cũng cười gằn."),t("br"),t("br"),n._v("Hai người cừu thị Vân Đào, đương nhiên không hy vọng người đứng bên cạnh hắn cũng sát hạch thông qua, lại nói, cái tên này vừa mới bác Mạc Vũ tiểu thư mặt mũi, vừa vặn tiến lên giúp nàng nói chuyện, thu được mỹ nữ ưu ái."),t("br"),t("br"),n._v("“Ta là đúng dịp thuần phục hai con man thú, hơn nữa cái gì cũng không hiểu, nếu như vậy... Vị này Mạc Vũ tiểu thư, ngươi làm gì thế còn muốn so với ta thử, thậm chí còn muốn cùng ta học tập làm sao thu được Vân Điện Cổ Tước hảo cảm?”"),t("br"),t("br"),n._v("Không thèm để ý Chu Cẩm Hoàng cùng Chu Tuyên hai vị cùng gió gia hỏa, ngẩng đầu nhìn hướng trước mắt bóng người xinh đẹp, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thân thể cứng đờ, Mạc Vũ tức giận sắc mặt đỏ lên."),t("br"),t("br"),n._v("Thân phận nàng cao quý, cùng người ở chung, cũng đều là người khác khen tặng, nào có người dám nói như vậy, trong lúc nhất thời bị phản bác dấu ở ngực, không biết trả lời như thế nào."),t("br"),t("br"),n._v("“Tiểu tử, ngươi biết tại cùng ai nói chuyện sao? Liều lĩnh gia hỏa...”"),t("br"),t("br"),n._v("“Mau nhanh xin lỗi, Mạc Vũ tiểu thư chính miệng nói ngươi, là ngươi tam sinh vinh hạnh, lại còn dám phản bác, thật là to gan...”"),t("br"),t("br"),n._v("Thấy cái tên này lại dám nói nữ thần, Chu Cẩm Hoàng cùng Chu Tuyên nhất thời không vui, đồng thời hét lớn, bất quá lời còn chưa nói hết, liền thấy Mạc Vũ tiểu thư sầm mặt lại."),t("br"),t("br"),n._v("“Ngậm miệng! Không nói lời nào không ai coi các ngươi là thành người câm!”"),t("br"),t("br"),n._v("“Tiểu thư...” Hai người sững sờ, đồng thời khàn giọng dừng."),t("br"),t("br"),n._v("Giúp nàng nói chuyện, làm sao ngược lại bị mắng?"),t("br"),t("br"),n._v("Bất quá, thời khắc này, có ngốc cũng biết, nịnh hót vỗ tới vó ngựa lên."),t("br"),t("br"),n._v("“Đều là cái tên này, một lúc nữa ngược lại muốn xem xem ngươi làm sao xấu mặt!”"),t("br"),t("br"),n._v("Càng nghĩ càng giận, lại không dám đem lửa giận phát đến nữ thần trên người, đành phải đem một giọng tức giận chuyển đến Trương Huyền nơi nào, càng xem hắn vượt qua cảm thấy không hợp mắt."),t("br"),t("br"),n._v("Không thèm để ý ba tên này, Trương Huyền cùng sau lưng Vân Đào đi tới Phong đường chủ trước mặt."),t("br"),t("br"),n._v("“Phong đường chủ, Trương Huyền tiền bối đáp ứng muốn sát hạch thuần thú sư, ngươi xem... Khi nào thì bắt đầu đi!” Vân Đào một mặt cao hứng, nói."),t("br"),t("br"),n._v("Đối với vị này Trương Huyền tiền bối, hắn là rất có tự tin."),t("br"),t("br"),n._v("Hắn thuần phục cái man thú giãy giụa chết giãy giụa sống, người ta đánh một trận là được, hơn nữa nhẹ nhõm giúp đối phương thăng cấp, chỉ bằng vào này thủ đoạn, liền ngay cả Phong đường chủ đều không làm được!"),t("br"),t("br"),n._v("Nắm giữ như vậy năng lực người, làm sao có khả năng sát hạch cái thuần thú sư cũng không thành công?"),t("br"),t("br"),n._v("“Hiện tại là có thể bắt đầu, ta nghe nói, ngươi hiện tại còn không đã tham gia sát hạch, liền thuần phục thú học đồ đều không phải?” Phong đường chủ nhìn sang."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ngươi có thể thuần phục Ích Huyệt cảnh man thú, nhận ra Vân Điện Cổ Tước, nói rõ sát hạch thuần phục thú học đồ đối với ngươi mà nói không tính là gì, có thể miễn trừ.”"),t("br"),t("br"),n._v("Phong đường chủ nói."),t("br"),t("br"),n._v("Học đồ chỉ là nghề nghiệp cơ sở, liền bọn họ đều không nhận ra Vân Điện Cổ Tước đều biết, nói rõ vị này Trương Huyền, đối thuần phục thú một đạo không xa lạ gì, một cái nho nhỏ học đồ mà thôi, sát hạch lên rất phiền phức, hoàn toàn là lãng phí thời gian."),t("br"),n._v(" Đương nhiên, nếu như cho hắn biết, Trương Huyền đối thuần phục thú cái gì cũng không hiểu, thậm chí mới nghe nói không bao lâu, phỏng chừng liền sẽ không nghĩ như vậy rồi."),t("br"),t("br"),n._v("“Bất quá, không sát hạch học đồ, khó có thể thử thách có hay không đối thuần phục thú hiểu rõ, đã như vậy, ta liền miễn trừ thuần phục thú cửa thứ nhất sát hạch, đem học đồ sát hạch cửa thứ nhất thêm đi vào.”"),t("br"),t("br"),n._v("Học đồ sát hạch, chia làm lý luận kiểm tra, phân biệt man thú."),t("br"),t("br"),n._v("Chính thức thuần thú sư sát hạch vì: Thu được man thú tán thành, thuần phục man thú."),t("br"),t("br"),n._v("Trước mắt vị này Trương Huyền, liền Vân Điện Cổ Tước loại này lạ Thượng Cổ di thú đều có thể nhận ra, nói rõ kiến thức cực rộng, sát hạch phân biệt man thú cũng là lãng phí thời gian. Nếu như có thể làm được thuần phục một đầu chân chính man thú, thu được man thú tán thành, cũng không có gì ý nghĩa."),t("br"),t("br"),n._v("Vì lẽ đó, Phong đường chủ suy nghĩ một chút, trực tiếp đem sát hạch thay đổi một lần, tương tự hai quan, lại cùng cái khác thuần phục thú học đồ không giống."),t("br"),t("br"),n._v("“Phân biệt là: Số một, lý luận sát hạch, cũng chính là trả lời bài thi; Thứ hai, thuần phục một đầu so thực lực mình mạnh man thú! Ta cũng không vì tình riêng mà làm rối kỷ cương, chỉ cần ngươi có thể qua này hai quan, chính là nhất tinh thuần thú sư.”"),t("br"),t("br"),n._v("“Híc, được!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Làm sao sát hạch thuần thú sư, trước cũng nghe Vân Đào đã nói, tựa hồ chính là những cái này bước đi, đối phương cũng không cố ý gây chuyện."),t("br"),t("br"),n._v("Thấy hắn đáp ứng, Phong đường chủ cười nói: “Tốt lắm, ta hiện tại đi chuẩn bị một tờ bài thi, ngươi qua lại đáp, nếu như có thể trả lời không có vấn đề, tất cả chính xác, coi như thông qua!”"),t("br"),t("br"),n._v("“Trước không vội, các ngươi nơi này có liên quan với thuần phục thú thư tịch không? Có thể mượn trước ta xem một chút à?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Đọc sách?”"),t("br"),t("br"),n._v("Phong đường chủ, Lư thú sư mấy người nhìn nhau, tất cả đều tràn đầy mê hoặc."),t("br"),t("br"),n._v("Lúc này đọc sách làm gì?"),t("br"),t("br"),n._v("“Bên kia thư khố tất cả đều là liên quan với thuần phục thú thư tịch, vốn là chỉ có thuần phục thú học đồ mới có thể quan sát, ngươi muốn xem, ta có thể đặc biệt phê, chỉ là bây giờ nhìn thư...”"),t("br"),t("br"),n._v("Tuy rằng nghi hoặc Phong đường chủ vẫn là mở miệng, hắn câu nói sau cùng không nói ra, người người lại đều nghe ra ý tứ."),t("br"),t("br"),n._v("Liên quan với thuần phục thú thư, chí ít mấy trăm ngàn bản, coi như lâm trận mới mài gươm, ngươi cũng tìm điểm chuyện quan trọng đi làm, hiện tại đến xem... Vậy thì cùng muốn ôm hài tử, mới nghĩ tìm vợ, có chút quá chậm đi!"),t("br"),t("br"),n._v("“Há, ta có cái thói quen, gặp phải đại sự trước, tâm tình khẩn trương, cần đọc sách điều tiết một lần trạng thái, khả năng muốn phiền phức đường chủ!”"),t("br"),t("br"),n._v("Trương Huyền thuận miệng nói."),t("br"),t("br"),n._v("Lý do này lần trước tại Lục Trầm đại sư phủ đệ dùng qua, cảm giác rất tốt, lại lần nữa chuyển đi ra."),t("br"),t("br"),n._v("“Như vậy... Tốt, thư khố liền ở bên kia, ngươi trực tiếp đi qua là được!”"),t("br"),t("br"),n._v("Phong đường chủ cũng không nghi ngờ, chỉ về phía trước."),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu, nhanh chân đi tới."),t("br"),t("br"),n._v("Không đọc sách liền muốn sát hạch thuần thú sư, hắn khẳng định không làm được, bất quá, chỉ cần có thư tại, để thư viện thu nhận, còn lại liền đơn giản."),t("br"),t("br"),n._v("“Cái tên này...”"),t("br"),t("br"),n._v("Một bên Thẩm Bích Như thấy vị này Trương lão sư, thật đáp ứng sát hạch thuần thú sư, chỉ cảm thấy lông mày co giật, một ngụm máu ngậm trong miệng sắp phun ra."),t("br"),t("br"),n._v("Lá gan cũng không tránh khỏi lớn quá rồi đó!"),t("br"),t("br"),n._v("Không phải nàng không tin đối phương, mà là... Cái tên này cùng bản thân cùng nhau đi tới, đối thuần phục thú một đạo, xa lạ không thể lại xa lạ, liền ngay cả vừa nãy, còn hỏi mình “Thú lâm” là cái gì..."),t("br"),t("br"),n._v("Một mình ngươi liền thú lâm cũng không biết gia hỏa, lại có thể không uý kỵ tí nào đáp ứng sát hạch thuần thú sư?"),t("br"),t("br"),n._v("Mấu chốt nhất chính là..."),t("br"),t("br"),n._v("Đáp ứng đáp đáp ứng, người ta lập tức nắm bài thi, ngươi muốn xem thư?"),t("br"),t("br"),n._v("Làm cái gì?"),t("br"),t("br"),n._v("“Hả? Không đúng.. Lần trước đi học viện Tàng Thư các, một mình hắn lật qua, chẳng lẽ...”"),t("br"),t("br"),n._v("Chính không làm rõ ràng được đối phương làm gì, trong lòng hơi động, nhớ tới hai người tại Tàng Thư các gặp mặt cảnh tượng."),t("br"),t("br"),n._v("Vị này Trương lão sư tại bên cạnh mình vẫn lật qua, liền cho rằng là cố ý làm như thế, là muốn hấp dẫn sự chú ý của nàng, có ý kiến gì. Sau đó mới biết, là nàng cả nghĩ quá rồi, đối phương cũng không phải là lật qua, mà là đã đem sách vở nội dung tất cả đều nhớ kỹ."),t("br"),t("br"),n._v("Lật qua liền nhớ kỹ thư tịch nội dung..."),t("br"),t("br"),n._v("Chẳng lẽ, vị này Trương lão sư, là thật sự sẽ không thuần phục thú tri thức, hiện tại đến xem thư mục đích là... Học tập?"),t("br"),t("br"),n._v("“Chuyện này... Làm sao có khả năng? Giáo sư Tàng Thư các thư, vốn là không nhiều, lại nói hắn thành là lão sư sau, thường thường đi qua, khẳng định lật xem không chỉ một ngày, đừng nói danh sư học đồ, coi như nắm giữ đã gặp qua là không quên được tam tinh danh sư, cũng không làm được, trong thời gian ngắn học được chứ?”"),t("br"),t("br"),n._v("Kinh ngạc trong lòng, vẫn như cũ không thể tin được."),t("br"),t("br"),n._v("Truyền thuyết, Minh Lý cảnh tâm như chỉ thủy, mức độ đạt đến 10 trở lên, liền nắm giữ đã gặp qua là không quên được bản lĩnh, chỉ cần xem qua thư tịch bên trong nội dung, là có thể vững chắc ghi lại ở đầu óc, Vĩnh Sinh sẽ không quên."),t("br"),t("br"),n._v("Chính vì như thế, lợi hại danh sư mới có thể học tập rất nhiều nghề nghiệp, nhớ kỹ trên đời này nhiều như vậy tri thức."),t("br"),t("br"),n._v("Có thể... Coi như vị này Trương lão sư nghịch thiên, mức độ đạt đến 10 trở lên, đem mấy trăm ngàn bản thư tịch lật hết, một tờ tờ xem xong, không có mấy tháng cũng không làm được đi!"),t("br"),t("br"),n._v("“Lẽ nào... Lẽ nào?”"),t("br"),t("br"),n._v("Đột nhiên nghĩ tới điều gì, Thẩm Bích Như thân thể mềm mại run lên."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-255"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-257"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);