(window.webpackJsonp=window.webpackJsonp||[]).push([[1473],{1828:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-248-han-tai-chay-bang-bang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-248-han-tai-chay-bang-bang"}},[n._v("#")]),n._v(" Chương 248: Hắn tại chạy băng băng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Cái này không thể nào...”"),t("br"),t("br"),n._v("Ý nghĩ này tại đầu óc xoay một cái, Thẩm Bích Như liên tục lắc đầu."),t("br"),t("br"),n._v("Nàng từng xem qua một cái sách cổ, bên trên ghi chép, thượng cửu lưu nghề nghiệp ngoại trừ luyện khí sư, luyện đan sư, trận pháp sư những cái này thông thường nghề nghiệp bên ngoài, còn có một loại thần bí nhất nghề nghiệp Thiên Cơ sư."),t("br"),t("br"),n._v("Thiên Cơ sư, am hiểu Thiên Cơ thần toán, có thể lợi dụng đặc thù kỹ xảo, sớm báo trước trong thời gian ngắn sắp phát sinh sự tình, sau đó làm ra chuẩn bị."),t("br"),t("br"),n._v("Nếu như là người như thế mà nói, hoàn toàn có thể sớm biết đề thi, lại lật xem đối ứng với nhau thư tịch, chỉ cần nhớ kỹ đáp án liền có thể, căn bản không cần đem hết thảy sách vở đều xem xong."),t("br"),t("br"),n._v("Chẳng lẽ vị này Trương lão sư có loại nghề nghiệp này thiên phú? Vì lẽ đó, mới có thể biết mình muốn đưa ra câu hỏi vấn đề, sớm tại Tàng Thư các xem xong, trả lời mười phân vẹn mười!"),t("br"),t("br"),n._v("Mới có thể sớm biết Vân Đào trận pháp không được nhốt lại Kim Thân Thiết Tiễn Báo tác dụng... Vừa nghĩ như thế, cũng thật sự có thể!"),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("Loại nghề nghiệp này chỉ ở trong truyền thuyết, dựa theo thư tịch bên trên ghi chép tỉ lệ, ngàn tỉ người tu luyện bên trong, đều không nhất định có thể xuất hiện một cái, hầu như chỉ tồn tại mịt mờ bên trong, căn bản không sao người gặp qua."),t("br"),t("br"),n._v("Nếu như thật sự có loại năng lực này, giáo viên sát hạch vì không điểm sự tình, khẳng định có thể sớm tránh khỏi, không đến mức phía sau dẫn ra nhiều như vậy khó khăn."),t("br"),t("br"),n._v("Lại nói, coi như Thiên Cơ thần toán, cũng chỉ là tính xảy ra chuyện đại thể mạch lạc, không thể liền đề thi là cái gì, ở đâu một quyển sách bên trên, đều có thể rõ ràng đi!"),t("br"),t("br"),n._v("Trong lòng suy nghĩ lung tung, nhíu mày thành mụn nhọt, Thẩm Bích Như càng nghĩ càng hồ đồ, càng nghĩ càng không hiểu."),t("br"),t("br"),n._v("Tự cho là đã hiểu rất rõ vị này Trương lão sư, lại phát hiện, chỉ là của hắn một điểm nhỏ của tảng băng chìm."),t("br"),t("br"),n._v("Suy nghĩ hồi lâu, không có một chút nào kết quả, ngược lại đối vị này Trương lão sư, càng thêm hiếu kỳ."),t("br"),t("br"),n._v("Cho tới giờ khắc này, nàng mới cuối cùng đã rõ ràng rồi, vì sao Hàn Quỳnh nói nàng không xứng với đối phương, xác thực, coi như nàng rất đẹp, thiên phú cũng không tính quá kém, người như thế, cũng là không xứng với."),t("br"),t("br"),n._v("Nàng suy nghĩ lung tung, Chu Cẩm Hoàng, Chu Tuyên mấy người thì trợn to hai mắt."),t("br"),t("br"),n._v("“Tiểu tử này... Có bị bệnh không? Muốn sát hạch, mới đến xem thư?”"),t("br"),t("br"),n._v("“Khẳng định là đầu óc có vấn đề, không phải vậy, lúc này đọc sách có ích lợi gì?”"),t("br"),t("br"),n._v("Hai người một bên nói thầm, một bên lặng lẽ nhìn về phía cách đó không xa Mạc Vũ, chỉ thấy nàng cũng lông mày nhăn thành mụn nhọt, không biết nghĩ cái gì."),t("br"),t("br"),n._v("Đang muốn lấy lòng giống như đến nói cái gì, liền thấy nữ thần thân thể xoay một cái, nhấc chân hướng thư khố phương hướng đi đến."),t("br"),t("br"),n._v("“Mạc Vũ tiểu thư...” Hai người liền muốn chạy tới."),t("br"),t("br"),n._v("“Cho ta đợi ở chỗ này, ai đuổi tới đến, ta đánh gãy chân hắn!”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Mạc Vũ thân thể mềm mại xoay một cái, thẳng tắp hướng Tàng Thư các đi đến."),t("br"),t("br"),n._v("“Đáng ghét, đáng ghét!”"),t("br"),t("br"),n._v("Nghe được không Vũ công chúa dĩ nhiên vì một cái mới quen gia hỏa, quát lớn hai người, Chu Cẩm Hoàng, Chu Tuyên tức giận sắc mặt trắng bệch, nghiến răng nghiến lợi."),t("br"),t("br"),n._v("Bất quá, lửa giận của bọn họ còn không thiêu đốt thời gian bao lâu, liền thấy Mạc Vũ tiểu thư lại lần nữa đi trở về, trong miệng không biết nói thầm cái gì, sắc mặt cũng không biết là thanh vẫn là hồng, tràn đầy quái lạ."),t("br"),t("br"),n._v("Hai người liếc mắt nhìn nhau, cảm thấy nghi hoặc, lại chỉ lo lại lần nữa làm tức giận đối phương, đành phải đứng tại chỗ, sốt ruột vò đầu bứt tai."),t("br"),t("br"),n._v("Một lát sau, thực sự không nhịn được, Chu Cẩm Hoàng vẫy tay, một tên hộ vệ đi tới, nhỏ giọng bàn giao vài câu."),t("br"),t("br"),n._v("Này tên hộ vệ đi vòng một vòng lớn, đi tới Mạc Vũ tiểu thư phía sau, nghe xong một hồi, cũng là một mặt quái lạ đi tới."),t("br"),t("br"),n._v("“Mạc Vũ Công chúa nói cái gì?”"),t("br"),t("br"),n._v("Thấy hắn đi tới, hai người vội vàng nhìn sang."),t("br"),t("br"),n._v("“Nàng... Liên tục đang nói, lật sách, lật sách, lật sách... Cụ thể còn có cái gì ta cũng không có nghe rõ!”"),t("br"),t("br"),n._v("Hộ vệ vò đầu."),t("br"),t("br"),n._v("“Lật sách?”"),t("br"),t("br"),n._v("Chu Cẩm Hoàng, Chu Tuyên lại lần nữa nhìn nhau, từng người từ đối phương trên mặt, nhìn ra mộng bức, đồng thời trong lòng bốc lên một ý nghĩ..."),t("br"),t("br"),n._v("Lẽ nào... Nữ thần điên rồi?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không biết đừng trong lòng người hai vị nữ thần đều đang vì hắn rung mạnh, giờ khắc này Trương Huyền cùng Mạc Vũ nói như thế, đang điên cuồng lật sách."),t("br"),t("br"),n._v("Vốn tưởng rằng liên quan với thuần phục thú thư, mấy vạn bản liền không ít, đến thư khố mới biết ý nghĩ này cỡ nào ấu trĩ, trước mắt thư tịch mênh mông bể sở, số lượng tuyệt đối không dưới mấy trăm ngàn."),t("br"),t("br"),n._v("Tuy rằng không sánh được Thiên Huyền Vương quốc tàng thư khố, nhưng cũng không kém nhiều lắm."),t("br"),t("br"),n._v("Nếu như dựa theo trước đây phương pháp nhìn xuống, không có ba ngày ba đêm, căn bản không nhìn xong."),t("br"),t("br"),n._v("Vốn là cảm thấy sát hạch cái thuần thú sư rất đơn giản, một hồi liền chơi xong, thật muốn đọc sách thời gian dài như vậy, e sợ bản thân không điên, bên ngoài chờ Phong đường chủ mấy người cũng sẽ trước điên mất."),t("br"),t("br"),n._v("Người khác điều chỉnh trạng thái, một hai canh giờ ngược lại cũng thôi, hắn nếu như một điều chỉnh tốt mấy ngày, sau đó đầy mắt tơ máu đi ra, phỏng chừng kẻ ngu đến đâu cũng sẽ hoài nghi không đúng."),t("br"),t("br"),n._v("“Không lật hết những sách này, thư viện liền không thể thu nhận, vạn nhất thi đến trong đó tri thức, liền không thể qua ải...”"),t("br"),t("br"),n._v("Trương Huyền nhíu mày thành mụn nhọt: “Như vậy liền không hưởng thụ được thuần thú sư mang đến miễn phí...”"),t("br"),t("br"),n._v("Tuy rằng hắn trữ vật giới chỉ bên trong còn có mấy ngàn vạn kim tệ, nhưng kiếp trước lấy tư cách trạch ở nhà người bình thường một viên, từ lâu nuôi thành thói quen... Có thể không dùng tiền, tuyệt đối không hoa!"),t("br"),t("br"),n._v("Này không phải nhiều tiền tiền ít vấn đề, mà là... Nguyên tắc!"),t("br"),t("br"),n._v("“Thư viện đụng vào một lần vật phẩm, liền có thể xuất hiện thiếu hụt, ta đụng vào một lần giá sách... Sẽ xuất hiện hay không bên trên thư tịch hết thảy nội dung?”"),t("br"),t("br"),n._v("Dựa theo bình thường tốc độ, khẳng định là không nhìn xong nhiều như vậy thư tịch, có thể hay không không có thể nghĩ một biện pháp? Đang cau mày, một ý nghĩ đột nhiên xông ra."),t("br"),t("br"),n._v("Ngón tay hắn chỉ cần đụng vào, mặc kệ vô ý thức người, man thú, vẫn là đồ vật, đều sẽ ở đầu óc hiện lên thư tịch, xuất hiện đủ loại thiếu hụt."),t("br"),t("br"),n._v("Những sách này đều do giá sách nở rộ, có thể hay không ngón tay đụng vào, thư viện sẽ xuất hiện hết thảy thư tịch nội dung?"),t("br"),n._v(" “Thử một chút!”"),t("br"),t("br"),n._v("Nghĩ đến liền làm, Trương Huyền hai bước đi tới một cái giá sách trước mặt, bàn tay duỗi một cái, tóm tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thư viện chấn động, quả nhiên xuất hiện một quyển sách, mở ra nhìn chút, Trương Huyền thân thể loáng một cái, suýt chút nữa một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Chỉ thấy bên trên viết: Huyền Lạc sơn mạch Thú đường phân bộ thư khố hàng thứ ba giá sách, chất liệu nhãn thơm mộc, làm một vệt thợ thủ công chỗ làm, năm ba mươi bảy..."),t("br"),t("br"),n._v("“Ta muốn không phải cái này...”"),t("br"),t("br"),n._v("Nước mắt kém gảy đi ra đến."),t("br"),t("br"),n._v("Rất rõ ràng, ngón tay đụng tới cái gì, mới phải xuất hiện cái gì sai lầm, đầu cơ trục lợi khẳng định là không được."),t("br"),t("br"),n._v("“Hả? Không đúng, ngón tay đụng tới là được, nói cách khác... Những sách này, ta coi như không ngã, chỉ cần đụng tới, có phải là cũng có thể sinh thành thư tịch?”"),t("br"),t("br"),n._v("Ý nghĩ đầu tiên thất bại, trong lòng lại lần nữa linh quang nhất thiểm."),t("br"),t("br"),n._v("Ngón tay đụng vào, mặc kệ thời gian dài ngắn, chỉ cần tinh thần tập trung la lên “Thiếu hụt”, sẽ hình thành thư tịch, đã như vậy, không cần lật tờ, chỉ cần ngón tay cùng thư tịch đụng vào, có phải là cũng được?"),t("br"),t("br"),n._v("“Thử xem...”"),t("br"),t("br"),n._v("Ánh mắt ngưng lại, đi tới một đống thư tịch trước mặt, trong lòng la lên thiếu hụt, bàn tay đột nhiên tìm tới."),t("br"),t("br"),n._v("Ào ào ào!"),t("br"),t("br"),n._v("Đang suy nghĩ đến cùng có không lúc hữu dụng, thư viện chấn động, một loạt thư tịch xuất hiện tại thức hải."),t("br"),t("br"),n._v("“Có thể đi?”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Trương Huyền vội vàng lấy ra một quyển mở ra nhìn chút, quả nhiên phát giác nội dung trong sách hoàn mỹ bị ấn vào trong đó, còn ghi chép đủ loại thiếu sót."),t("br"),t("br"),n._v("“Quả nhiên có thể được!”"),t("br"),t("br"),n._v("Trương Huyền nước mắt giàn giụa, lại thí nghiệm mấy lần, xem như là triệt để làm rõ."),t("br"),t("br"),n._v("Chỉ cần trong lòng la lên “Thiếu hụt” điều động Thiên Đạo thư viện, ngón tay xẹt qua chỗ, đều có thể đem thư tịch thu nhận trong đó."),t("br"),t("br"),n._v("Cứ như vậy, so lật xem nhanh hơn trăm lần không chỉ!"),t("br"),t("br"),n._v("Lật xem, dù cho một lần lật mười vài cuốn sách, cũng đầu tiên muốn đem thư lấy ra, này liền cần tiêu hao không ít thời gian. Mà làm như vậy căn bản không cần, một đường chạy chậm, ngón tay xẹt qua chỗ, thư tịch đương nhiên tiến vào đầu óc, một phút không cần, liền có thể thu nhận một loạt giá sách thư tịch."),t("br"),t("br"),n._v("Bất quá, bởi vì thu nhận thư tịch quá nhanh, thư viện sẽ chấn động, đầu óc của chính mình cũng sẽ xuất hiện sóng lớn, không thể một hơi thu xong, thông thường thu lấy mấy hàng giá sách, liền cần nghỉ ngơi một hồi."),t("br"),t("br"),n._v("Dù vậy, cũng so với phía trước nhanh nhiều lắm."),t("br"),t("br"),n._v("Trước đại khái ba ngày ba đêm mới có thể xem xong thư tịch, đổi làm hiện tại, phỏng chừng một canh giờ liền có thể."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Tìm ra càng tốt hơn thu nhận thư tịch phương pháp, Trương Huyền tâm tình vô cùng vui vẻ, trong lòng la lên, bàn tay gác lên trên giá sách, một đường về phía trước chạy đi."),t("br"),t("br"),n._v("Mỗi chạy xong một loạt, kiểm tra một lần, như có thiếu hụt, lại trở về quét tu bổ."),t("br"),t("br"),n._v("Thư tịch thu nhận rất nhanh, liên quan với thuần phục thú tri thức, Trương Huyền cũng vượt qua nắm giữ càng nhiều."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Phong đường chủ, bài thi chuẩn bị kỹ càng?”"),t("br"),t("br"),n._v("Bên trong gian phòng, Lư thú sư, Vương thú sư, thấy Phong đường chủ lại lần nữa đi tới, không nhịn được nhìn lại."),t("br"),t("br"),n._v("“Ừm!” Phong đường chủ gật gù: “Trong đó bao dung mấy trăm loại man thú nội dung, không có một chút nào nhường, nếu như hắn liền những cái này đều có thể trả lời đi ra, thuần phục thú trình độ, xác thực không tầm thường.”"),t("br"),t("br"),n._v("“Có thể nhận ra Vân Điện Cổ Tước, kiến thức phương diện, hẳn là sẽ không quá kém, có lẽ liền có thể thành công.” Lư thú sư đáp một tiếng."),t("br"),t("br"),n._v("“Đúng đấy, chúng ta Thú đường mỗi lần thi đấu, đều đếm ngược, liền bởi vì quá mức hẻo lánh, không có thiên tài, nếu như thật có thể xuất hiện một vị, đạt được khen thưởng, có lẽ liền có thể làm cho chúng ta thuận lợi thăng cấp tam tinh!”"),t("br"),t("br"),n._v("Phong đường chủ ánh mắt hưng phấn lóe lên."),t("br"),t("br"),n._v("“Đúng đấy, không vội vã, xem sát hạch kết quả đi!” Vương thú sư cũng đáp một tiếng: “Chúng ta cũng là nghe Vân Đào nói tài năng như thần, mười phút thuần phục Ích Huyệt cảnh man thú, nói thật, ta vẫn còn có chút không quá tin tưởng!”"),t("br"),t("br"),n._v("“Ta cũng không quá tin tưởng, bất quá, vừa nãy ta không những hỏi dò Vân Đào, còn đem thuộc hạ của hắn đều hỏi một lần, tất cả đều nói giống như đúc, không có vấn đề chút nào, xem ra coi như không trong truyền thuyết thần kỳ như vậy, lại cũng gần như!”"),t("br"),t("br"),n._v("Lư thú sư nói tiếp."),t("br"),t("br"),n._v("“Đúng đấy, bất kể nói thế nào, đều là chúng ta một cơ hội, nếu như không loại này bản lĩnh, quá mức lại tiếp tục tìm kiếm chính là. Này, đúng rồi, vị này Trương Huyền đi thư khố đọc sách, còn chưa có đi ra?”"),t("br"),t("br"),n._v("Cảm khái vài câu, Phong đường chủ lúc này mới phát giác, muốn sát hạch đến người có liên quan còn không xuất hiện, không nhịn được hơi nhướng mày."),t("br"),t("br"),n._v("“Còn chưa có đi ra!” Lư thú sư gật đầu."),t("br"),t("br"),n._v("“Tiểu chu, ngươi đi xem xem!”"),t("br"),t("br"),n._v("Phong đường chủ dặn dò một tiếng."),t("br"),t("br"),n._v("Tiểu chu là hắn thu học đồ, đang theo hắn học tập thuần phục thú, nghe được dặn dò đáp một tiếng, vội vàng chạy tới, một lát sau đi tới trước mặt, sắc mặt đỏ bừng lên, muốn nói lại thôi."),t("br"),t("br"),n._v("“Làm sao? Hắn còn đang đọc sách?”"),t("br"),t("br"),n._v("Nhìn thấy học đồ dáng dấp này, Phong đường chủ hơi nhướng mày, có chút kỳ quái, chính hắn một học đồ luôn luôn trầm ổn, lẽ nào xảy ra chuyện gì?"),t("br"),t("br"),n._v("“Không phải...”"),t("br"),t("br"),n._v("Tiểu chu đầy mặt lúng túng, bực bội khuôn mặt xanh xám: “Là hắn tại... Hắn tại tàng thư quán... Chạy băng băng!”"),t("br"),t("br"),n._v("(Vé tháng đây! Để ta cũng bắt đầu chạy đi!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-256"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-258"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);