(window.webpackJsonp=window.webpackJsonp||[]).push([[1474],{1829:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-249-van-đap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-249-van-đap"}},[n._v("#")]),n._v(" Chương 249: Vấn đáp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Chạy băng băng?”"),t("br"),t("br"),n._v("“Tại tàng thư khố chạy băng băng?”"),t("br"),t("br"),n._v("Phong đường chủ, Vương thú sư mấy người cảm giác mình có phải là nghe lầm, một mặt mê man nhìn sang."),t("br"),t("br"),n._v("Ngươi nói tại tàng thư khố đọc sách, thậm chí có cảm giác ngồi tu luyện, ta đều có thể nhịn xuống đến, cảm thấy có thể lý giải, có thể chạy băng băng..."),t("br"),t("br"),n._v("Phong đường chủ chớp con mắt: “Ngươi có phải là nhìn lầm?”"),t("br"),t("br"),n._v("Tàng thư khố tất cả đều là giá sách, coi như muốn chạy cũng không tiện a!"),t("br"),t("br"),n._v("Chủ yếu nhất chính là, có thể thuần phục Ích Huyệt cảnh man thú, thực lực khẳng định không kém, muốn chạy băng băng bao nhanh tốc độ, mới đối với tu luyện hữu dụng?"),t("br"),t("br"),n._v("“Có thể hay không cùng của hắn thuần phục thú phương pháp có quan hệ? Chạy nhanh có thể đuổi theo man thú?”"),t("br"),t("br"),n._v("Vương thú sư nghi hoặc."),t("br"),t("br"),n._v("“Hắn... Chạy không nhanh, cùng người bình thường chạy băng băng gần như, hơn nữa một bên chạy còn một bên nói cái gì, cách đến quá xa, có chút nghe không rõ.”"),t("br"),t("br"),n._v("Tiểu chu đầy mặt xấu hổ."),t("br"),t("br"),n._v("Nhìn thấy vừa nãy tình cảnh đó, hắn đều có chút thật không tiện nói ra, chỉ lo sẽ bị người xem là bệnh thần kinh đánh chết."),t("br"),t("br"),n._v("Đi điều chỉnh trạng thái đọc sách người, lại có thể tại tàng thư khố một bên chạy băng băng, một bên nói, đổi làm bất luận người nào đều sẽ tâm lý không chịu nổi."),t("br"),t("br"),n._v("“Chạy băng băng, nói?”"),t("br"),t("br"),n._v("Tam đại thuần thú sư, lần này là thật hôn mê."),t("br"),t("br"),n._v("Bọn họ nhanh cào nát đầu, đều không nghĩ ra, chuyện này... Mẹ nó cùng điều chỉnh trạng thái có cái lông quan hệ!"),t("br"),t("br"),n._v("Nói thật, bất luận người nào nghe được hành động của đối phương, trong lòng đều sẽ bốc lên một ý nghĩ... Đồ phá hoại!"),t("br"),t("br"),n._v("Không đồ phá hoại mà nói, nào có như vậy điều chỉnh trạng thái?"),t("br"),t("br"),n._v("Còn nhất định phải tại tàng thư khố, trước mắt lớn như vậy gian phòng chạy không dưới ngươi? Nếu như cảm thấy nơi này tiểu, bên ngoài còn có man thú hoạt động khu, dài rộng vài km, đừng nói chạy bộ, lật bổ nhào nửa giờ cũng đến không được đầu..."),t("br"),t("br"),n._v("“Không được, ta qua xem một chút, tàng thư trong kho có quá nhiều quý giá bí tịch, không thể bởi vậy bị hao tổn!”"),t("br"),t("br"),n._v("Nghĩ đến một hồi, Phong đường chủ thực sự cảm thấy không an toàn."),t("br"),t("br"),n._v("Một cái tại tàng thư khố chạy băng băng người, vạn nhất đầu óc động kinh, đem tàng thư khố điểm, e sợ muốn khóc cũng không kịp."),t("br"),t("br"),n._v("Quyết không cho phép xảy ra chuyện như vậy!"),t("br"),t("br"),n._v("Sắc mặt nghiêm túc, đang muốn hướng tàng thư khố đi đến, liền thấy một người thanh niên mang theo mệt mỏi đi tới."),t("br"),t("br"),n._v("Chính là Trương Huyền."),t("br"),t("br"),n._v("Giờ khắc này Trương Huyền cùng một canh giờ phía trước, hoàn toàn khác nhau, bất luận trạng thái tinh thần vẫn là thân thể, đều có vẻ hơi chán chường, xem ra thật giống trải qua một trận cực kỳ tàn ác huấn luyện, đem toàn thân thể lực đều tiêu hao cạn sạch."),t("br"),t("br"),n._v("“Ngươi không sao chứ?”"),t("br"),t("br"),n._v("Phong đường chủ không nhịn được đi lên phía trước."),t("br"),t("br"),n._v("Người khác điều chỉnh trạng thái, điều chỉnh xong đều tinh thần run run, tinh lực mười phần, cái tên này ngược lại tốt, điều chỉnh xong cùng làm một đêm lớn bảo vệ sức khoẻ như thế, ngươi xác định đây là điều chỉnh trạng thái, mà không phải tiêu hao trạng thái?"),t("br"),t("br"),n._v("“Há, ta không có chuyện gì, bài thi chuẩn bị kỹ càng? Chúng ta bắt đầu đi!”"),t("br"),t("br"),n._v("Đối diện Trương Huyền vung vung tay."),t("br"),t("br"),n._v("Tuy rằng dùng phương pháp mới, rất nhanh sẽ đem tàng thư khố hết thảy thư tịch đều thu nhận đến Thiên Đạo thư viện, tốc độ là nhanh, nhưng bởi vì trong thời gian ngắn thu nhận quá nhiều tri thức, trong đầu chấn động quá lợi hại, cả người đều có chút đầu óc choáng váng."),t("br"),t("br"),n._v("Cùng trước năm ngày năm đêm không ngủ có chút tương tự, khắp toàn thân từ trên xuống dưới tràn ngập mệt mỏi."),t("br"),t("br"),n._v("“Chuyện này... Liền bắt đầu?”"),t("br"),t("br"),n._v("Nhìn thấy đối phương con mắt đều có chút không mở ra được, Phong đường chủ không nhịn được lại lần nữa hỏi một câu: “Ngươi chắc chắn chứ?”"),t("br"),t("br"),n._v("“Xác định!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Tốt lắm, đến bên kia đi, ghi chép ngọc tinh ghi chép không tới đây.” Phong đường chủ gật gù."),t("br"),t("br"),n._v("Bất luận thuần phục thú hay là luyện đan sát hạch, để cho công bằng, đều cần dùng ghi chép ngọc tinh ghi chép tất cả, sau đó gửi lấy lưu lại có nghi vấn người lại đây tuần tra."),t("br"),t("br"),n._v("Rất nhanh đi tới nhà chính giữa, có ghi chép ngọc tinh địa phương, Phong đường chủ cùng cái khác hai vị thuần thú sư ngồi xuống."),t("br"),t("br"),n._v("Nhìn thấy bọn họ ngồi xong, phía dưới tất cả mọi người cũng biết sắp xảy ra chuyện gì, lại lần nữa bắt đầu nghị luận."),t("br"),t("br"),n._v("“Sát hạch muốn bắt đầu rồi!”"),t("br"),t("br"),n._v("“Đúng đấy, không trải qua học đồ, trực tiếp sát hạch thuần thú sư, đây chính là Thú đường cho thiên tài siêu cấp đãi ngộ, liền ngay cả Mạc Vũ tiểu thư đều không hưởng thụ đến, ta ngược lại muốn xem xem cái tên này, một khi thất bại, còn mặt mũi nào tiếp tục đợi ở chỗ này!”"),t("br"),t("br"),n._v("“May mắn thuần phục man thú mà thôi, ngươi còn thật sự cho rằng có bản lãnh gì? Ta cảm thấy khẳng định thất bại!”"),t("br"),t("br"),n._v("“Cửa thứ nhất lý luận tri thức, không có mấy năm học tập, không thể nhớ kỹ trên thế giới rất nhiều man thú, cùng với rất nhiều đặc thù. Lúc trước nhà chúng ta điện hạ, vì đọc thuộc lòng, mỗi ngày đều kiên trì, cái tên này liền hai mươi cũng chưa tới, coi như từ mẹ trong bụng học tập, có thể nhớ kỹ bao nhiêu?”"),t("br"),t("br"),n._v("“Hắn chính là lấy lòng mọi người, có thể có bản lãnh gì...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thấy vị này Trương Huyền muốn sát hạch, tất cả mọi người vây quanh, không ít đều mang theo cười trên sự đau khổ của người khác biểu cảm."),t("br"),t("br"),n._v("Không trải qua học đồ, trực tiếp sát hạch thuần thú sư, tại Thú đường lâu như vậy rồi, vẫn là lần thứ nhất thấy, những người này đều muốn nhìn cái tên này vấp váp."),t("br"),t("br"),n._v("Đặc biệt là Chu Tuyên, Chu Cẩm Hoàng, bởi vì Vân Đào cùng Mạc Vũ tiểu thư quan hệ, đã sớm đối với cái tên này chán ghét chí cực, ước gì hắn xấu mặt."),t("br"),t("br"),n._v("Nếu như ngay cả lý luận đều không qua được, còn thuần thú sư... Cũng chỉ có thể nằm mơ!"),t("br"),t("br"),n._v("Mấy người nghị luận, Mạc Vũ tiểu thư lúc này cũng khôi phục tỉnh táo, đi tới trước mặt, một mặt lành lạnh hướng cách đó không xa thanh niên nhìn lại."),t("br"),t("br"),n._v("Chỉ liếc mắt nhìn, đôi mi thanh tú nhíu lên."),t("br"),t("br"),n._v("Cái tên này xảy ra chuyện gì?"),t("br"),t("br"),n._v("Mới vừa rồi còn tinh thần chấn hưng ở bên trong lật sách, làm sao... Thời gian không lâu, cùng bị người luộc như thế, phờ phạc?"),t("br"),n._v(" Không những nàng nhìn ra, những người khác cũng nhìn ra, Thẩm Bích Như cùng Vân Đào liếc mắt nhìn nhau, trên mặt nhăn thành mụn nhọt, không nhịn được về phía trước."),t("br"),t("br"),n._v("“Trương lão sư, ngươi không sao chứ?”"),t("br"),t("br"),n._v("“Không có chuyện gì!” Trương Huyền lắc đầu: “Chỉ là có chút mệt mỏi!”"),t("br"),t("br"),n._v("Nhiều như vậy thư tịch, ngăn ngắn chưa tới một canh giờ, toàn bộ thu nhận đến Thiên Đạo thư viện, tinh thần độ cao vận chuyển, liên tục la lên “Thiếu hụt”, không mệt mới là lạ."),t("br"),t("br"),n._v("Không tại chỗ mệt mỏi ngã xuống, liền rất tốt."),t("br"),t("br"),n._v("“Tiền bối, nếu như thực sự cảm thấy không thoải mái mà nói, nếu không chúng ta nghỉ ngơi một chút, ngày mai thi lại!” Vân Đào không nhịn được nói."),t("br"),t("br"),n._v("Trước mắt vị này rất có thể có thể là Tông Sư cảnh cường giả, để người như thế mệt mỏi thành như vậy, đến cùng trải qua cái gì?"),t("br"),t("br"),n._v("“Không sao...”"),t("br"),t("br"),n._v("Nghe nói như thế, biết hai người là hảo ý, Trương Huyền đang muốn xua tay từ chối, liền nghe đến cách đó không xa cười gằn vang lên."),t("br"),t("br"),n._v("“Ta xem là cố ý trang, đi thư khố điều chỉnh trạng thái, sẽ không phải là nhìn thấy thuần phục thú thư, mới biết sát hạch độ khó, cố ý giả bộ bệnh, muốn tránh né sát hạch đi!”"),t("br"),t("br"),n._v("Nói chuyện chính là Chu Cẩm Hoàng."),t("br"),t("br"),n._v("Trong mắt cười gằn nồng nặc, giống như là muốn vạch trần tên lừa đảo ngụy trang."),t("br"),t("br"),n._v("“Ta đoán khẳng định là như vậy, không phải vậy, đọc sách có thể xem tinh lực uể oải suy sụp, trạng thái uể oải, liền tay chân đều không nhấc lên nổi? Tha ta kiến thức nông cạn, cũng thật là lần đầu tiên nghe nói!” Chu Tuyên cũng nhân cơ hội bỏ đá xuống giếng."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("“Bọn họ vừa nói như vậy, cũng thật sự có thể.”"),t("br"),t("br"),n._v("“Phỏng chừng trước cảm giác mình có thể sát hạch, nhìn thư tịch mới biết, thuần phục thú bác đại tinh thâm, cảm thấy sợ hãi, lại không muốn mất mặt xấu hổ, mới cố ý kiếm cớ...”"),t("br"),t("br"),n._v("“Nhất định là như vậy...”"),t("br"),t("br"),n._v("Hai người mà nói... Vang vọng bốn phía, mọi người nhất thời tin tưởng bảy, tám phần."),t("br"),t("br"),n._v("Nếu như không phải như vậy, làm sao chuyên môn chạy đi điều chỉnh trạng thái, trở về lại cùng sắp chết rồi như thế?"),t("br"),t("br"),n._v("Một mặt mệt mỏi, nhìn dáng dấp bất cứ lúc nào cũng sẽ ngã chổng vó..."),t("br"),t("br"),n._v("Nếu như là thật sự rất mệt, xem cái ghi chép lên như vậy, căn bản giải thích không thông... Nếu không cách nào giải thích, vậy cũng chỉ có một khả năng, là hắn cố ý trang."),t("br"),t("br"),n._v("“Không cần, hiện tại bắt đầu!”"),t("br"),t("br"),n._v("Không thèm để ý xung quanh nghi vấn cùng nghị luận, Trương Huyền bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Đây là ta mới ra bài thi, ngươi hiện tại là có thể trả lời câu hỏi, trong vòng một canh giờ hoàn thành, hơn nữa bảo đảm chính xác tỷ suất đạt đến trăm phần trăm, coi như thông qua sát hạch!”"),t("br"),t("br"),n._v("Thấy hắn kiên trì, Phong đường chủ không nói thêm nữa, lấy ra một chồng bài thi đưa tới."),t("br"),t("br"),n._v("Cùng lúc trước sát hạch luyện đan học đồ như thế, có tới mấy chục tờ dày, chỉ viết chỉ sợ cũng phải hao phí thời gian rất lâu, chớ nói chi là còn phải chăm chỉ suy nghĩ, cẩn thận cân nhắc."),t("br"),t("br"),n._v("Cúi đầu nhìn lướt qua, chỉ thấy bên trên lít nha lít nhít tràn ngập đủ loại thuần phục thú kiến thức căn bản, bao dung chư nhiều phương diện, không có đầy đủ tri thức, e sợ xem đều xem không hiểu."),t("br"),t("br"),n._v("“Được rồi, hiện tại có thể trả lời câu hỏi, tính giờ bắt đầu!”"),t("br"),t("br"),n._v("Thấy hắn tiếp nhận bài thi, Phong đường chủ bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Ừm!” Đem bài thi thả xuống, Trương Huyền cầm lấy một cái bút lông, đang muốn trả lời câu hỏi, lúc này mới cảm thấy trong đầu mê muội, toàn bộ tay đều có chút run rẩy."),t("br"),t("br"),n._v("Vừa nãy thu lấy thư tịch, cứ việc tốc độ vượt qua dĩ vãng, nhưng thực lực bây giờ của hắn quá yếu, vẫn còn có chút không chịu nổi, không những tinh lực không đủ, liền thân thể đều xuất hiện phản ứng."),t("br"),t("br"),n._v("E sợ muốn điều chỉnh chí ít nửa canh giờ mới có thể khôi phục."),t("br"),t("br"),n._v("Mà hiện tại, rất rõ ràng đã không có thời gian."),t("br"),t("br"),n._v("Thật muốn điều chỉnh nửa canh giờ, bài thi như thế dày, muốn viết xong, coi như có Thiên Đạo thư viện chống đỡ, cũng không làm được."),t("br"),t("br"),n._v("Chỉ cần sai một đề, liền không cách nào thông qua thuần phục thú sát hạch, thất bại mất mặt chuyện nhỏ, mấu chốt nhất chính là... Không cách nào hưởng thụ miễn phí, đây là để hắn không cách nào khoan dung."),t("br"),t("br"),n._v("“Mau nhìn, hắn thật giống mệt mỏi liền bút đều bắt không được!”"),t("br"),t("br"),n._v("“Khà khà, đường đường Ích Huyệt cảnh trở lên cường giả, mệt mỏi liền bút lông đều bắt không được, ngươi sẽ tin tưởng? Khẳng định là trang!”"),t("br"),t("br"),n._v("“Đúng đấy, tuyệt đối là nhìn thấy đề mục, cảm thấy cái gì đều sẽ không, cố ý nguỵ trang đến mức uể oải... Nói thật, người như thế vô liêm sỉ nhất!”"),t("br"),t("br"),n._v("“Ai nói không phải!”"),t("br"),t("br"),n._v("Thấy ngón tay hắn run rẩy, liền bút đều không cầm được, mọi người lại lần nữa hừ lạnh."),t("br"),t("br"),n._v("Không những là bọn họ, liền ngay cả Phong đường chủ, Lư thú sư mấy người, cũng liếc nhau một cái, từng người trong lòng nghi hoặc."),t("br"),t("br"),n._v("Chẳng lẽ, thật và những người khác nói như thế?"),t("br"),t("br"),n._v("Nếu như không phải, coi như tại tàng thư khố chạy băng băng, cũng không đến mức mệt mỏi thành như vậy đi!"),t("br"),t("br"),n._v("“Trương Huyền, nếu như ngươi cảm thấy thân thể có bệnh, thực sự không cách nào đáp đề, có thể cho phép ngươi nghỉ ngơi một ngày, ngày mai tiếp tục.”"),t("br"),t("br"),n._v("Nhìn một hồi, thấy cái tên này xác thực uể oải, liền bút đều không cầm được, Phong đường chủ cũng không nhịn được nữa, mở miệng nói."),t("br"),t("br"),n._v("Thở dài một tiếng, Trương Huyền rung cổ tay, đem bút lông để xuống."),t("br"),t("br"),n._v("Đối phương nói không sai, hắn hiện tại tình trạng cơ thể, coi như cưỡng ép viết chữ, cũng sẽ viết xiêu xiêu vẹo vẹo, khó coi chí cực."),t("br"),t("br"),n._v("Hơn nữa mấu chốt nhất chính là... Tốc độ theo không kịp, khả năng viết vài chữ liền muốn nghỉ ngơi một chút, cưỡng ép kiên trì, ngược lại sẽ để đáp đề tốc độ biến chậm."),t("br"),t("br"),n._v("Muốn thông qua sát hạch, lại không muốn tiếp tục làm lỡ xuống... Hiện tại cũng chỉ có một cái biện pháp!"),t("br"),t("br"),n._v("Cảm thán một tiếng, Trương Huyền ngẩng đầu, xem hướng về phía trước."),t("br"),t("br"),n._v("“Phong đường chủ, hai vị thú sư, ta thực sự vô lực cầm bút, có thể hay không lựa chọn vấn đáp?”"),t("br"),t("br"),n._v("“Vấn đáp?”"),t("br"),t("br"),n._v("Tam đại thuần thú sư đồng thời đột nhiên đứng dậy, tất cả đều trong mắt tràn đầy không thể tin được."),t("br"),t("br"),n._v("“Ngươi... Ngươi nói cái gì? Ngươi muốn tuyển chọn lý luận sát hạch bên trong khó nhất... Vấn đáp?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-257"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-259"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);