(window.webpackJsonp=window.webpackJsonp||[]).push([[1480],{1834:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-254-co-thu-benh-nang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-254-co-thu-benh-nang"}},[n._v("#")]),n._v(" Chương 254: Có thú bệnh nặng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Danh sư chịu đến giáo sư công hội cùng danh sư đường bảo vệ, đừng nói ám sát, coi như có người xúc phạm, đều rất có thể có thể bị ngăn chặn."),t("br"),t("br"),n._v("Bọn họ Phong Sát lệnh, uy lực cực lớn, vô số thế lực, cao thủ đều sẽ tôn sùng, đừng nói một cái nho nhỏ nhị đẳng vương quốc, coi như Thiên Vũ vương quốc loại này nhất đẳng vương quốc gặp gỡ, e sợ đều sẽ kiên trì không được bao lâu, liền sụp đổ."),t("br"),t("br"),n._v("Có thể nói, thật muốn đánh lén một vị danh sư, sau đó bị danh sư đường tra ra, không những bọn họ sẽ chết, liền ngay cả sau lưng vương quốc, cũng sẽ gặp phải dính dáng!"),t("br"),t("br"),n._v("Này chính là danh sư đáng sợ!"),t("br"),t("br"),n._v("Đệ nhất thiên hạ nghề nghiệp, không những không cho sỉ nhục, lại không cho phép kẻ khác khinh nhờn."),t("br"),t("br"),n._v("Dám khinh nhờn danh sư, quản ngươi cái gì quốc vương, cái gì tông chủ, dù cho thực lực mạnh đến đâu, địa vị cao đến đâu, danh sư đường một lời bên dưới, cũng có thể nhẹ nhõm xóa đi."),t("br"),t("br"),n._v("Truyền thuyết một cái phong hào vương quốc quốc vương, đùa giỡn một vị mỹ nữ danh sư, trêu đến nàng giận dữ, liền danh sư đường đều không vận dụng, tại chỗ giảng bài bảy ngày, đưa tới vô số cao thủ, ra lệnh một tiếng, liền đem vị này quốc vương bắt sống, cuồng ẩu chết."),t("br"),t("br"),n._v("Cái này vương quốc lão tổ, thực lực mạnh mẽ, rõ ràng có thể ngăn cản, cũng không dám ra tay."),t("br"),t("br"),n._v("Điều này cũng hết cách rồi, quốc vương chết rồi, có thể một lần nữa tuyển, vương quốc một khi diệt, cái gì đều không có."),t("br"),t("br"),n._v("Danh sư nắm giữ không biết bao nhiêu đệ tử, chỉ điểm một lần, thì có nửa sư tình nghĩa, cùng tổ ong vò vẽ như thế, một khi đụng vào, không chết cũng tàn phế."),t("br"),t("br"),n._v("Ám sát danh sư... Nói thật, này cùng chán sống muốn chết không khác nhau chút nào."),t("br"),t("br"),n._v("Chu Cẩm Hoàng mấy người sợ đến run cầm cập, cái khác vốn là có ý nghĩ thế này người, cũng lập tức đem ý nghĩ ép xuống."),t("br"),t("br"),n._v("“Danh sư?” Không nghĩ tới vị này Mạc Vũ tiểu thư sẽ như vậy nghĩ, Trương Huyền lắc lắc đầu: “Ta hiện tại còn không phải!”"),t("br"),t("br"),n._v("Hắn bây giờ có được ba sư liên hợp tán thành thân phận, chỉ là lợi hại một điểm học đồ thôi, còn không sát hạch, đương nhiên cũng sẽ không là danh sư."),t("br"),t("br"),n._v("“Không phải?” Mạc Vũ tiểu thư ngẩn ngơ."),t("br"),t("br"),n._v("Vốn tưởng rằng đối phương là danh sư, mới có lợi hại như vậy, đủ loại thủ đoạn tầng tầng lớp lớp, dĩ nhiên không phải..."),t("br"),t("br"),n._v("“Không phải? Lần này quá tốt rồi...”"),t("br"),t("br"),n._v("“Chỉ cần còn không phải, giết chết không thương lượng!”"),t("br"),t("br"),n._v("Vốn đang lo lắng không thôi Chu Tuyên, Chu Cẩm Hoàng, nghe được không phải danh sư, thở phào nhẹ nhõm, tâm tư lại lần nữa sinh động lên."),t("br"),t("br"),n._v("Bất quá, còn không sinh động thời gian bao lâu, liền nghe đến Thẩm Bích Như âm thanh âm vang lên: “Trương lão sư tuy rằng không phải danh sư, nhưng cũng là Bắc Vũ vương quốc Lưu sư, Trang sư cùng với Hàn Vũ vương quốc Trịnh sư đại sư huynh!”"),t("br"),t("br"),n._v("“Danh sư sư huynh?”"),t("br"),t("br"),n._v("Hai người nhìn nhau, lần này là thật khóc."),t("br"),t("br"),n._v("Đều là cấp hai vương quốc, ba vị này danh sư tiếng tăm rất lớn, xem như là nhất đỉnh cấp một nhóm người, bọn họ làm sao không biết."),t("br"),t("br"),n._v("Sư huynh của bọn họ..."),t("br"),t("br"),n._v("Coi như không phải danh sư, địa vị so danh sư cao hơn nữa a!"),t("br"),t("br"),n._v("Còn giết chết không thương lượng... May là không có động thủ, không phải vậy, đối phương giết chết hai người bọn họ, đúng là không thương lượng..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Bắc Vũ vương quốc Lưu sư, ta biết, cũng gặp qua một lần, từng tại Khương Thư nhị tinh danh sư môn hạ nghe giảng bài, thuộc về nửa cái môn sinh. Ngươi là sư huynh của hắn, lẽ nào...”"),t("br"),t("br"),n._v("Nghe được Thẩm Bích Như mà nói, Mạc Vũ cũng ý thức được cái gì, con ngươi co rụt lại."),t("br"),t("br"),n._v("Không phải danh sư nhưng là sư huynh, nói rõ bọn họ thuộc về đồng nhất cái lão sư, mà có thể làm cho danh sư cam tâm tình nguyện bái sư, e sợ ít nhất đều đạt đến nhị tinh cấp bậc, thậm chí... Càng cao hơn!"),t("br"),t("br"),n._v("Thấp nhất nhị tinh danh sư học sinh thân truyền..."),t("br"),t("br"),n._v("Loại thân phận này, so với nàng vị công chúa này, đều chỉ cao chớ không thấp hơn."),t("br"),t("br"),n._v("Hơn nữa, có thể được loại này thu làm thân truyền, nói rõ tiềm lực rất lớn, sau đó thành tựu, khả năng liền nàng, đều rất khó so được với."),t("br"),t("br"),n._v("Dù sao, nàng tuy là Khương Thư nhị tinh danh sư học đồ, nhưng không phải thân truyền."),t("br"),t("br"),n._v("Biết những cái này, lại lần nữa nhìn về phía thanh niên trước mắt, ánh mắt tràn đầy kính trọng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Hóa ra là danh sư thân truyền, khó trách có thể đối với thuần phục thú có như thế lý giải!”"),t("br"),t("br"),n._v("Phong đường chủ mấy người cũng hiểu được, đồng thời gật gù."),t("br"),t("br"),n._v("Trước bọn họ cũng dự định cố gắng hỏi dò, học một ít Trương Huyền loại này đánh đập thuần phục thú pháp, biết đối phương thân phận thực sự, cái ý niệm này bỏ đi."),t("br"),t("br"),n._v("Thuần thú sư tuy rằng cũng là thượng cửu lưu nghề nghiệp, nhưng cùng danh sư loại này cao nhất nghề nghiệp, vẫn có nhất định chênh lệch."),t("br"),t("br"),n._v("Thậm chí... Vị này Trương Huyền triển khai đánh đập thuần phục thú pháp, chính là đến từ vị danh sư kia... Một khi cho đối với mới biết,"),t("br"),t("br"),n._v("Nhóm người mình muốn còn muốn hỏi thủ đoạn của hắn, khẳng định phiền phức không ít."),t("br"),t("br"),n._v("Cùng với như vậy, còn không bằng nghĩ biện pháp giao hảo."),t("br"),t("br"),n._v("“Đây là ngươi nhất tinh thuần thú sư huy chương!”"),t("br"),t("br"),n._v("Biết của hắn “Thân phận”, tam đại thuần thú sư thái độ cũng biến thành tốt hơn rất nhiều, rất nhanh sẽ đem đại biểu thuần thú sư thân phận huy chương lấy lại đây."),t("br"),t("br"),n._v("Tiếp nhận huy chương, Trương Huyền tiện tay thả vào trữ vật giới chỉ, nhìn lại."),t("br"),t("br"),n._v("“Ta muốn đi Hồng Liên sơn mạch, cần một đầu phi hành man thú, các ngươi nhìn cái gì dạng thích hợp?”"),t("br"),t("br"),n._v("Tuy rằng thành thuần thú sư, từng đọc không ít sách vở, nhưng không cưỡi qua phi hành man thú, có thể bay bao xa, mấy ngày có thể đến, vẫn là không cách nào xác nhận."),t("br"),t("br"),n._v("“Hồng Liên sơn mạch?”"),t("br"),t("br"),n._v("Phong đường chủ vuốt râu tính toán một chút, nói: “Thiên Vũ vương quốc cách nơi này, đủ có mấy vạn km, cưỡi ngươi mới vừa thuần phục con này Thanh Ưng Thú, đại khái muốn nửa tháng khoảng chừng!”"),t("br"),n._v(" “Nửa tháng?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Vốn tưởng rằng Thanh Ưng Thú đạt đến nửa bước Tông Sư, đi Hồng Liên sơn mạch cũng là mấy ngày, không nghĩ tới vẫn như cũ cần nửa tháng lâu dài."),t("br"),t("br"),n._v("“Đúng đấy, con này Thanh Ưng Thú vừa mới đột phá, tu vi không vững chắc, một ngày đại khái cũng là bốn, năm ngàn km, thời gian còn cần nghỉ ngơi, không thể ngày đêm phi hành, nửa tháng đã xem như là tốc độ nhanh nhất!” Phong đường chủ nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tuy rằng không biết Thanh Ưng Thú có thể bay nhiều khối, nhưng nó vừa mới đột phá, cước lực tất nhiên bị hạn chế, nếu như lại phối hợp củng cố tu vi, nghỉ ngơi, xác thực muốn tiêu hao không ít."),t("br"),t("br"),n._v("Chần chờ một chút, nhớ tới Hàn Quỳnh trước nói mà nói, không nhịn được hỏi."),t("br"),t("br"),n._v("“Ta nghe nói Thú đường có loại , tốc độ cực nhanh, coi như mấy vạn km, mấy ngày liền có thể bay đến, không biết đúng hay không?”"),t("br"),t("br"),n._v("“Thú đường là có Thiểm Điện Thú Thừa, cũng chính là Tông Sư cảnh cường giả man thú, có thể chở người phi hành, tốc độ cực nhanh, có thiểm điện danh xưng, bất quá...” Phong đường chủ chần chờ một chút: “Loại này cấp Thú Thừa khác, toàn bộ Thú đường, tổng cộng chỉ có hai con, trong đó một đầu, đã bay đi Càn Huyết vương quốc, một đầu khác...”"),t("br"),t("br"),n._v("Nói đến đây lắc lắc đầu, cái khác hai vị thuần thú sư, cũng lộ ra một tia vẻ uể oải."),t("br"),t("br"),n._v("“Làm sao?” Thấy bọn họ dáng dấp này, Trương Huyền tràn đầy kỳ quái."),t("br"),t("br"),n._v("“Con này bệnh nặng, ngày hôm nay liền đứng đều không đứng lên nổi, chớ nói chi là phi hành, ta cùng Lư thú sư bọn họ, vừa nãy đang chẩn đoán bệnh, nghĩ biện pháp trị liệu, mới không có thời gian lại đây sát hạch, để Hồng thú sư làm giúp!”"),t("br"),t("br"),n._v("Phong đường chủ nói."),t("br"),t("br"),n._v("“Bệnh nặng?” Trương Huyền lúc này mới nhớ tới, Vân Đào vừa tới sát hạch thời điểm, Hồng thú sư từng nói Phong đường chủ mấy người có việc, tạm thời đến không tới."),t("br"),t("br"),n._v("Trước liền đang nghĩ, đến cùng chuyện gì, có thể so sánh sát hạch thuần thú sư còn trọng yếu hơn, thì ra là như vậy."),t("br"),t("br"),n._v("Tông Sư cảnh man thú, xem như là cái này Thú đường chỗ dựa lớn nhất cùng nội tình, bệnh nặng liền đứng dậy cũng khó khăn, khó trách bọn hắn lo lắng."),t("br"),t("br"),n._v("“Bệnh gì?” Trương Huyền hỏi."),t("br"),t("br"),n._v("Đối phương bệnh nặng không bệnh nặng không sao cả, then chốt là... Có cái này cưỡi, có thể giảm thiểu không ít thời gian."),t("br"),t("br"),n._v("Nghe được của hắn hỏi dò, Phong đường chủ không nhịn được lắc đầu: “Liền bởi vì không nhìn ra bệnh gì chứng, mới không có cách nào!”"),t("br"),t("br"),n._v("“Ba người chúng ta đều chẩn đoán bệnh qua, thuần thú sư đủ loại thủ đoạn, đều dùng qua một lần, trước sau không làm rõ!” Lư thú sư cười khổ."),t("br"),t("br"),n._v("Chứng bệnh cũng không thấy, làm sao trị liệu?"),t("br"),t("br"),n._v("“Có thể có phương pháp giải quyết?” Một bên Mạc Vũ tiểu thư không nhịn được hỏi."),t("br"),t("br"),n._v("Làm thuần thú sư, nghe được man thú bệnh nặng, trong mắt lộ ra quan tâm."),t("br"),t("br"),n._v("“Chúng ta đã phái người đi Bắc Vũ vương quốc mời Thanh Dương Tông Sư, hắn đi tới, có lẽ liền có thể tra ra nguyên nhân sinh bệnh!” Vương thú sư nói."),t("br"),t("br"),n._v("“Thanh Dương Tông Sư? Vương thú sư nói thế nhưng Bắc Vũ vương quốc, một vị duy nhất nhị tinh y đạo Tông Sư?” Thẩm Bích Như nhớ tới cái gì."),t("br"),t("br"),n._v("“Không sai, chính là hắn! Chúng ta thuần thú sư, tuy rằng cũng có một chút giúp man thú xem bệnh thủ đoạn, nhưng cùng chân chính y đạo Tông Sư so, vẫn là kém không ít, trước đã phái người mời, chắc là không tốn thời gian dài sẽ đi tới!” Vương thú sư cười nói."),t("br"),t("br"),n._v("Thuần thú sư tuy rằng cũng có trị liệu man thú chứng bệnh phương pháp, nhưng đều là một ít thông thường bệnh trạng, gặp phải nghi nan tạp chứng, cũng là bó tay toàn tập, giống hiện tại bộ dáng này, tìm kiếm y đạo Tông Sư ra tay mới là biện pháp tốt nhất."),t("br"),t("br"),n._v("Bắc Vũ Vương thành cách nơi này cũng không xa, cưỡi phi hành man thú mà nói, một cái qua lại một canh giờ đều dùng không được."),t("br"),t("br"),n._v("Ngược lại chờ cũng là chờ, bọn họ mới tới đây quan sát thuần thú sư sát hạch, gặp phải Trương Huyền cùng Hồng thú sư tranh chấp."),t("br"),t("br"),n._v("Nói xong y đạo Tông Sư, Vương thú sư tựa hồ đối với vị này Thanh Dương rất tin tưởng, nhìn về phía Trương Huyền cười cợt: “Chỉ cần chữa khỏi chứng bệnh, để nó chuyên môn đưa ngươi đi một chuyến Hồng Liên sơn mạch, cũng không sao! Chỉ có điều... Cưỡi Tông Sư cảnh Thú Thừa, giá cả không ít, ngươi có thể muốn sớm có chuẩn bị!”"),t("br"),t("br"),n._v("“Đại khái bao nhiêu?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("“Dựa theo Thú đường chào giá, đi Thiên Vũ vương quốc như thế địa phương xa, lại là Tông Sư cảnh man thú tự mình điều động, ít nhất phải hai mươi viên , Trương thú sư là chúng ta nội bộ nhân viên, có thể đánh gãy, cũng cần 16 khối khoảng chừng, đổi thành vương quốc thông dụng kim tệ, đại khái 800 vạn khoảng chừng!”"),t("br"),t("br"),n._v("“800 vạn kim tệ?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, con mắt trợn tròn."),t("br"),t("br"),n._v("Lúc trước tại Thiên Huyền vương quốc, vì giãy giụa mấy triệu, đủ loại ngụy trang, đủ loại khổ cực, bọn họ tùy tiện bay một chuyến, liền nhiều tiền như vậy... Quả thực chính là cướp đoạt!"),t("br"),t("br"),n._v("“Điều này cũng hết cách rồi, Tông Sư cảnh man thú, cần nhị tinh thuần thú sư tự mình điều động, lại là đơn độc phi hành, cái giá này, xem như là thấp nhất!”"),t("br"),t("br"),n._v("Vương thú sư cười khổ."),t("br"),t("br"),n._v("Tông Sư cảnh man thú, tuy rằng đã sớm bị người thuần phục, nhưng thực lực không đủ, muốn điều động hầu như không thể, cần nhị tinh thuần thú sư tự mình theo."),t("br"),t("br"),n._v("Có thể đạt đến loại này cấp bậc thuần thú sư, thực lực cũng đều đạt đến Tông Sư cảnh."),t("br"),t("br"),n._v("Nói cách khác, vì này một chuyến, cần Tông Sư cảnh cường giả cùng Tông Sư cảnh man thú toàn bộ điều động, giá cả làm sao có khả năng thấp?"),t("br"),t("br"),n._v("Phải biết Tông Sư cảnh cường giả, tại nhị đẳng vương quốc, đều được xem là lão tổ cấp bậc!"),t("br"),t("br"),n._v("Người như thế tự thân làm, 800 vạn kim tệ, xác thực không nhiều."),t("br"),t("br"),n._v("“Không phải nói... Chỉ cần thành thuần thú sư, có thể miễn phí cưỡi sao?” Cố nén phiền muộn, Trương Huyền không nhịn được nói."),t("br"),t("br"),n._v("Nghe Vân Đào nói, cưỡi miễn phí mới thi thuần thú sư, kết quả còn muốn 800 vạn... Ngẫm lại đều có chút buồn bực."),t("br"),t("br"),n._v("“Thành thuần thú sư là có thể miễn phí, bất quá, là cưỡi Tông Sư cảnh trở xuống man thú, muốn cưỡi loại này cấp bậc man thú miễn phí, nhất định phải đạt đến nhị tinh thuần thú sư mới được!” Thấy hắn không hiểu, Phong đường chủ giải thích."),t("br"),t("br"),n._v("“Nhị tinh thuần thú sư liền có thể miễn phí?”"),t("br"),t("br"),n._v("Trương Huyền chần chờ một chút, nói tiếp: “Nếu như vậy... Các ngươi xem cần muốn điều kiện gì, không được, ta thi một lần đi!”"),t("br"),t("br"),n._v("(Nàng dâu đi làm, ta ở nhà một mình xem hài tử, không có thời gian gõ chữ, dùng điện thoại di động cứng dằn vặt ra ba ngàn chữ, Ô u Ô u, lòng tốt nhét, xem sữa ba thương xót phần bên trên, cho vài tờ an ủi một chút đi!) (Chưa xong còn tiếp.,, ủng hộ của ngài, chính là ta động lực lớn nhất.) 17-02-0511:34:47"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-262"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-264"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);