(window.webpackJsonp=window.webpackJsonp||[]).push([[1481],{1835:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-255-thanh-duong-tong-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-255-thanh-duong-tong-su"}},[n._v("#")]),n._v(" Chương 255: Thanh Dương Tông Sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Không được, thi một lần?"),t("br"),t("br"),n._v("Phong đường chủ mấy người khóe miệng lại lần nữa kéo."),t("br"),t("br"),n._v("Tại Thú đường sát hạch qua vô số học đồ, đây là nghe qua nhất kỳ hoa mượn cớ."),t("br"),t("br"),n._v("Người khác thi thuần thú sư, đều là thực lực đạt đến, muốn thu được nghề nghiệp tán thành, cái tên này ngược lại tốt, chỉ là vì cưỡi Thú Thừa miễn phí..."),t("br"),t("br"),n._v("Ngươi thế nhưng danh sư sư huynh, lại có bản lĩnh như vậy, chỉ cần đồng ý, mấy triệu kim tệ mà thôi, nơi nào kiếm lời không tới, vì ít tiền như vậy... Mặt đâu? Tôn nghiêm đâu? Thân phận đâu?"),t("br"),t("br"),n._v("Quan trọng nhất chính là, đây chính là nhị tinh thuần thú sư, toàn bộ Huyền Lạc sơn mạch Thú đường, thành lập ngàn năm qua, cũng chỉ có ba vị."),t("br"),t("br"),n._v("Thi một lần... Khẩu khí cùng cắt dưa chuột, dùng bữa giống như vậy, không có bất luận cái gì khẩn trương cùng sợ sệt."),t("br"),t("br"),n._v("Ngươi biết độ khó sao?"),t("br"),t("br"),n._v("Biết liền ngay cả Hồng thú sư người như vậy, đều liên tục bảy lần đều không thành công sao?"),t("br"),t("br"),n._v("Há mồm liền đến, khen ngợi khen ngợi chuyện lạ, không khỏi quá không đem thuần phục thú nghề nghiệp này để ở trong mắt đi!"),t("br"),t("br"),n._v("Mạc Vũ tiểu thư càng là ngực chập trùng, bực bội một hơi không tới."),t("br"),t("br"),n._v("Nàng là thiên tài siêu cấp, tiêu tốn thời gian một năm, mới thật vất vả trở thành một tinh thuần thú sư, vốn tưởng rằng có thể mở mày mở mặt, làm cho tất cả mọi người đều tán dương, kết quả... Gặp phải cái tên này."),t("br"),t("br"),n._v("Nàng cầu gia gia cáo bà nội mới sơ cấp thuần phục một đầu Thanh Ưng Thú, đối phương đánh một trận, liền triệt để thuần phục... Sát hạch nhất tinh thành công, lại còn chưa biết thế nào là đủ, muốn sát hạch nhị tinh..."),t("br"),t("br"),n._v("Nếu như hậu tích bạc phát, một tiếng hót lên làm kinh người, cũng coi như, then chốt chính là... Rõ ràng trước không nghĩ muốn thi, nghe miễn phí sau, mới động tâm tư."),t("br"),t("br"),n._v("Người khác sắp mệt chết đều không làm được, hắn lại cùng đùa giỡn như thế, không mang theo như thế đả kích người đi..."),t("br"),t("br"),n._v("“Sát hạch nhị tinh thuần thú sư, cần...”"),t("br"),t("br"),n._v("Đè nén phiền muộn, Phong đường chủ đang muốn giải thích, liền thấy một ông già một mặt kích động đi tới."),t("br"),t("br"),n._v("“Đường chủ, đường chủ! Thanh Dương Tông Sư đến!”"),t("br"),t("br"),n._v("“Đến?”"),t("br"),t("br"),n._v("Tam đại thuần thú sư ánh mắt sáng lên, đột nhiên đứng dậy, lại không để ý tới giải thích, trực tiếp đi về phía trước."),t("br"),t("br"),n._v("Đi mấy bước Phong đường chủ dừng lại: “Mấy người các ngươi là mới sát hạch thuần thú sư, cũng có tư cách đi qua, vừa vặn nhìn y đạo Tông Sư làm sao cho cấp bậc tông sư man thú chữa bệnh, cũng coi như mở mang tầm mắt!”"),t("br"),t("br"),n._v("“Phải!”"),t("br"),t("br"),n._v("Mạc Vũ, Chu Cẩm Hoàng mấy người đồng thời gật đầu, đi theo."),t("br"),t("br"),n._v("“Tiền bối, chúng ta cũng qua xem một chút đi, loại này trị liệu man thú, thế nhưng ngàn năm không gặp cơ hội, ngắm nghía cẩn thận, có thể học không ít tri thức!”"),t("br"),t("br"),n._v("Thấy mấy người đi xa, Vân Đào đi tới Trương Huyền trước mặt."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn vẫn muốn cưỡi Thiểm Điện Thú Thừa, cái tên này có thể được chữa khỏi, đi Hồng Liên sơn mạch, sẽ bớt đi không ít thời gian cùng phiền phức."),t("br"),t("br"),n._v("Cùng ở sau lưng mọi người, đi rồi một hồi, đi tới một cái sân rất lớn."),t("br"),t("br"),n._v("Rộng rãi bao la, đủ loại kiến trúc xem ra đều so bình thường phủ đệ lớn hơn mấy số, làm cho người ta một loại tiến vào người khổng lồ quốc ảo giác."),t("br"),t("br"),n._v("Trong viện ghế đá, ghế đá, mỗi một cái đều có bảy, dài tám mét cởi, không nhìn kỹ, cũng hoài nghi là từng cái từng cái võ đài, cất bước hành lang phiến đá đường, cũng rất là rộng rãi, rộng mấy chục mét hẹp, trong ao nước ở giữa đứng sừng sững đình, càng là giống như cung điện, khí thế mười phần."),t("br"),t("br"),n._v("“Đây là chuyên môn cho  xây dựng phủ đệ, có bảy vị thuần phục thú học đồ chuyên môn hầu hạ sinh hoạt thường ngày sinh hoạt...”"),t("br"),t("br"),n._v("Thấy Trương Huyền xem mê hoặc, Vân Đào không nhịn được truyền âm giải thích: “Thiết Xỉ Khiếu Thiên Thú chính là đầu kia sinh bệnh Tông Sư cảnh man thú.”"),t("br"),t("br"),n._v("“Chuyên môn cho một con man thú xây dựng phủ đệ?”"),t("br"),t("br"),n._v("Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Có muốn hay không xa xỉ như vậy?"),t("br"),t("br"),n._v("Cái nhà này, bất luận chiếm diện tích vẫn là kiến trúc xa hoa trình độ, cùng Thiên Huyền Vương cung đều không phân cao thấp, không biết, còn tưởng rằng đại nhân vật gì trấn thủ trong đó, không nghĩ tới dĩ nhiên là chuyên môn làm một con man thú xây dựng..."),t("br"),t("br"),n._v("Thậm chí chuyên môn phân phối bảy vị thuần phục thú học đồ, chăm sóc sinh hoạt thường ngày... Thật so với bình thường cường giả hưởng thụ đãi ngộ đều cao."),t("br"),t("br"),n._v("Khó trách phi hành một chuyến Thiên Vũ vương quốc liền muốn 800 vạn kim tệ, không có cao như thế thu phí, vẫn đúng là không nuôi nổi."),t("br"),t("br"),n._v("“Thanh Dương Tông Sư, lần này lại phiền phức ngươi...”"),t("br"),t("br"),n._v("Trong lòng đang cảm khái người không bằng thú, liền nghe đến Phong đường chủ thanh âm hưng phấn vang lên."),t("br"),t("br"),n._v("Nhìn về phía trước, liền thấy một cái vóc người cao to ông lão đứng ở cách đó không xa, hai tay chắp ở sau lưng, mang theo người sống chớ quấy rầy khí tức. Hơn năm mươi tuổi dáng dấp, một thân áo xanh, không có bất kỳ biểu lộ gì, để người không nhìn ra sướng vui đau buồn."),t("br"),t("br"),n._v("Xem ra chính là trong truyền thuyết y đạo Tông Sư Thanh Dương!"),t("br"),t("br"),n._v("Giống như nghề nghiệp, nhất tinh xưng là đại sư, nhị tinh cực kỳ trở lên cũng có thể xưng là Tông Sư."),t("br"),t("br"),n._v("Vị này Thanh Dương, là nhị tinh y sư, rõ ràng muốn so với Nguyên Ngữ đại sư lợi hại không ít."),t("br"),t("br"),n._v("“Không sao cả! Khiếu Thiên thú ở đâu? Mang ta tới nhìn!”"),t("br"),t("br"),n._v("Đối với Phong đường chủ gật đầu một cái, Thanh Dương Tông Sư không thừa bao nhiêu phí lời, bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Tốt, mời tới bên này!”"),t("br"),t("br"),n._v("Phong đường chủ đi đầu dẫn đường."),t("br"),t("br"),n._v("Thời gian không lâu, mọi người tới đến một cá biệt viện, lập tức liền nhìn thấy rộng rãi trên đài đá, một đầu to lớn man thú nằm thẳng ở phía trên, hai mắt nhắm nghiền, liều lĩnh."),t("br"),t("br"),n._v("Này con man thú cùng Thanh Ưng Thú có chút tương tự, bất quá càng thêm to lớn, thân thể có tới dài mười mấy mét ngắn, từ xa nhìn lại, làm cho người ta một loại núi nhỏ cảm giác."),t("br"),t("br"),n._v("“Bất tỉnh?”"),t("br"),t("br"),n._v("Liếc mắt nhìn, Trương Huyền liền biết con này tên to xác đã rơi vào hôn mê."),t("br"),n._v("Khó trách Phong đường chủ mấy người vội vã như thế, nghe được Thanh Dương Tông Sư đi tới, liền nói hơn một câu tâm tư đều không có, xem ra con này Khiếu Thiên thú, quả nhiên bệnh rất nặng."),t("br"),t("br"),n._v("“Thanh Dương Tông Sư, Khiếu Thiên thú là ba ngày phía trước đột nhiên phát bệnh, trước không nghiêm trọng lắm, chỉ là không muốn ăn đồ vật, cũng không để ý, sáng sớm hôm nay lại đột nhiên hôn mê, mấy người chúng ta đủ loại thủ đoạn đều dùng qua, lại không có bất kỳ biện pháp nào...”"),t("br"),t("br"),n._v("Phong đường chủ giải thích."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Gật gù, Thanh Dương Tông Sư vài bước đi tới trước mặt, dọc theo Khiếu Thiên thú thân thể to lớn quay một vòng, càng chạy sắc mặt chìm đến càng lợi hại."),t("br"),t("br"),n._v("“Nói một chút nó gần nhất ẩm thực sinh hoạt thường ngày tình huống!”"),t("br"),t("br"),n._v("Phong đường chủ vẫy tay, một cái phụ trách Khiếu Thiên thú sinh hoạt thường ngày thuần phục thú học đồ đi tới, cung kính đi xong lễ tiết."),t("br"),t("br"),n._v("“Mười ngày phía trước, Khiếu Thiên thú liền không quá muốn ăn đồ ăn, có vẻ hơi nôn nóng bất an, bởi vì mùa thu, dễ dàng nội hỏa, chúng ta cho nó dùng an thần tiêu hỏa , tình huống giảm bớt một chút.”"),t("br"),t("br"),n._v("“Bất quá, vẫn như cũ có gì đó không đúng, mỗi ngày buổi tối đều ở trong sân xoay loạn, thỉnh thoảng ngửa mặt lên trời hí dài, đặc biệt là mấy ngày trước đây là đêm trăng tròn, nó hầu như một đêm không ngủ, qua lại chạy trốn, thật giống có phát tiết không xong tinh lực!”"),t("br"),t("br"),n._v("“Ngày thứ hai tỉnh lại, liền không muốn ăn cơm, cái gì đều dẫn không nổi hứng thú của nó, sáng sớm hôm nay càng là trực tiếp hôn mê!”"),t("br"),t("br"),n._v("“Mười ngày này lượng cơm ăn, còn không bằng dĩ vãng một ngày, trừ một chút luộc tốt thú thịt, canh cháo ở ngoài, vẫn chưa ăn qua những vật khác. Nha, cứ dựa theo đường chủ mà nói, hôm qua cho nó dùng hai viên , bất quá tựa hồ không có tác dụng gì, trái lại càng thêm nghiêm trọng.”"),t("br"),t("br"),n._v("Rất mau đem tình huống căn bản giới thiệu một lần."),t("br"),t("br"),n._v("Nghe xong, Thanh Dương Tông Sư gật gù, vung vung tay: “Đi xuống đi!”"),t("br"),t("br"),n._v("“Phải!” Vị này học đồ không dám phí lời, xoay người rời đi."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Phong đường chủ nhìn sang."),t("br"),t("br"),n._v("“Chờ một chút!”"),t("br"),t("br"),n._v("Thanh Dương Tông Sư cũng không giải thích, lại lần nữa dọc theo Thiết Xỉ Khiếu Thiên Thú quay một vòng, thời gian nhổ rút đối phương lông tóc cùng mí mắt, lông mày nhăn thành mụn nhọt, tựa hồ đang cẩn thận cân nhắc."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Y sư cũng có thể cho man thú chữa bệnh?”"),t("br"),t("br"),n._v("Nhìn thấy hắn nói thật dáng vẻ, Trương Huyền không nhịn được hỏi."),t("br"),t("br"),n._v("“Thanh Dương Tông Sư là Bắc Vũ vương quốc lợi hại nhất y đạo Tông Sư, có người nói đã đạt đến nhị tinh đỉnh phong, khoảng cách tam tinh cũng không xa, Thú đường không ít không cách nào giải quyết chứng bệnh đều là hắn ra tay giải quyết!”"),t("br"),t("br"),n._v("Vân Đào nhỏ giọng."),t("br"),t("br"),n._v("“Y sư xem bệnh cứu người, bất kể là người vẫn là thú, chỉ cần nhiễm bệnh, nguyên lý đều cơ bản tương đồng, có thể làm cho người ta xem, đương nhiên cũng có thể cho thú xem! Không phải vậy, y sư nghề nghiệp này, không phải chiến đấu hình, làm sao có khả năng tại thượng cửu lưu bên trong, đều xếp hạng cao?”"),t("br"),t("br"),n._v("Nghe được hai người nói chuyện, Mạc Vũ tiểu thư hừ một câu."),t("br"),t("br"),n._v("Thượng cửu lưu nghề nghiệp, danh sư xếp số một, cái này không thể nghi ngờ, cái khác xếp hạng so sánh mơ hồ, không có thống nhất kết luận, bất quá, luyện đan sư, y sư, trận pháp sư, thuần thú sư mấy người nghề nghiệp đều khá cao, được người ta tôn trọng."),t("br"),t("br"),n._v("Y sư cùng trận pháp sư, thuần thú sư so, hoàn toàn không có sức chiến đấu, lại có thể cùng cùng sánh vai, vẻn vẹn chỉ làm cho người ta xem bệnh, rõ ràng không làm được."),t("br"),t("br"),n._v("“Ngươi không phải thuần phục thú một đạo rất rất lợi hại sao? Còn tưởng rằng cái gì đều tinh thông, làm sao, đối với y đạo không hiểu?”"),t("br"),t("br"),n._v("Thấy Trương Huyền một mặt hồ đồ dáng vẻ, Mạc Vũ tiểu thư mặt cười giương lên."),t("br"),t("br"),n._v("Ngươi không phải rất kiêu ngạo sao? Đánh một trận liền có thể thuần phục man thú sao?"),t("br"),t("br"),n._v("Y đạo một đường, hoàn toàn không hiểu, ăn quả đắng đi!"),t("br"),t("br"),n._v("“Ây...” Nghe được đối phương chất vấn, Trương Huyền lúng túng nở nụ cười."),t("br"),t("br"),n._v("Thiên Huyền vương quốc liên quan với y đạo thư tịch, đã ít lại càng ít, có thể nói hầu như không có. Còn y sư, hắn tiếp xúc qua cũng chỉ có một cái Nguyên Ngữ đại sư, hai người gặp nhau, còn chỉ ở thư họa bên trên, cũng không phải là y thuật."),t("br"),t("br"),n._v("Vì lẽ đó, đối với nghề nghiệp này, vẫn đúng là không rõ ràng."),t("br"),t("br"),n._v("Cũng là lần đầu tiên nghe nói, y sư không những có thể làm cho người ta xem bệnh, còn có thể cho man thú xem."),t("br"),t("br"),n._v("“Mạc Vũ tiểu thư không những là nhất tinh thuần thú sư, nhất tinh luyện đan sư, danh sư học đồ, còn là một vị y sư học đồ!” Vân Đào nhỏ giọng, trong mắt tràn đầy ước ao."),t("br"),t("br"),n._v("“Y sư học đồ?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Cái này Mạc Vũ tiểu thư tuy rằng đẹp đẽ, có chút ngạo khí, nói thật, cũng quả thật có ngạo khí tiền vốn."),t("br"),t("br"),n._v("Dĩ nhiên một người tinh thông nhiều như vậy nghề nghiệp, xem như là hiện nay gặp qua nhiều như vậy người bên trong, thiên tài nhất một cái."),t("br"),t("br"),n._v("“Y sư cùng luyện đan sư có chỗ giống nhau, sẽ theo liền học học, đối với ta mà nói, cũng không khó! Lần khảo hạch này xong thuần thú sư, trở lại ta liền sát hạch chính thức y sư!” Mạc Vũ tiểu thư nhẹ nhàng nở nụ cười."),t("br"),t("br"),n._v("Đừng trong mắt người vô cùng phức tạp nghề nghiệp cấp cao, tại trong miệng nàng rồi cùng chơi nhà chòi giống như đơn giản."),t("br"),t("br"),n._v("Nàng luôn luôn thiên tài, hơn người một bậc, kết quả bị cái này kêu Trương Huyền vẫn làm mất mặt, giờ khắc này thấy đối phương đối với y sư nghề nghiệp không hiểu, vừa vặn có thể hòa nhau một ván, một lần nữa dấy lên tự tin."),t("br"),t("br"),n._v("Nói xong vốn tưởng rằng cái này kêu Trương Huyền sẽ khâm phục một lần, đã thấy cái tên này biến mất ở trước mắt, không biết đi nơi nào."),t("br"),t("br"),n._v("“Đáng ghét!”"),t("br"),t("br"),n._v("Giậm chân một cái, tức giận mặt cười phấn hồng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Có thể nhìn ra cái gì?” Thấy Thanh Dương Tông Sư cau mày, Phong đường chủ không nhịn được hỏi."),t("br"),t("br"),n._v("“Ân, trong lòng ta đã có một chút ý nghĩ, gần như có thể xác thực chẩn nó là xảy ra chuyện gì...”"),t("br"),t("br"),n._v("Thanh Dương Tông Sư gật gù, chính muốn nói chuyện, đột nhiên một lần sửng sốt, sắc mặt trở nên âm trầm, lớn tiếng quát lớn: “Ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Nghe hắn quát lớn, mọi người đồng loạt nhìn lại, liền thấy một người thanh niên chẳng biết lúc nào đứng ở Khiếu Thiên thú trước mặt, đối với đầu của nó... Sờ soạng một lần."),t("br"),t("br"),n._v("(Nàng dâu năm nay mang lớp 12, sớm đi làm, ngày hôm nay ở nhà mang theo một ngày hài tử, không có thời gian viết, chương mới chậm, thực sự thật không tiện! Này, mang một ngày hài tử so gõ chữ còn mệt mỏi.. Mồ hôi! Mặt khác, đề cử minh chủ đồng quẻ Tiên sư một quyển sách 《 á này lan truyền kỳ 》 thư hoang có thể đi nhìn!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-263"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-265"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);