(window.webpackJsonp=window.webpackJsonp||[]).push([[1482],{1837:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-256-ta-noi-nguoi-la-lang-bam"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-256-ta-noi-nguoi-la-lang-bam"}},[n._v("#")]),n._v(" Chương 256: Ta nói ngươi là lang băm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Nhìn thấy thanh niên động tác, mọi người tất cả đều từng cái từng cái trợn mắt ngoác mồm."),t("br"),t("br"),n._v("Cái tên này lá gan cũng không tránh khỏi lớn quá rồi đó!"),t("br"),t("br"),n._v("Không thấy y đạo Tông Sư tại trị liệu sao?"),t("br"),t("br"),n._v("Chạy tới làm gì? Hơn nữa..."),t("br"),t("br"),n._v("Đây chính là Thiết Xỉ Khiếu Thiên Thú, Tông Sư cảnh man thú, không phải nhà ngươi sủng vật, sờ một chút... Cùng gặp phải chó giữ nhà như thế, giở trò quỷ gì?"),t("br"),t("br"),n._v("“Ta cũng muốn nhìn một chút, con này Thiết Xỉ Khiếu Thiên Thú bị bệnh gì!”"),t("br"),t("br"),n._v("Nhìn ra mọi người nghi hoặc, vị thanh niên này xoay đầu lại, cười cợt."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Nghe nói như thế, Mạc Vũ tiểu thư hừ một tiếng."),t("br"),t("br"),n._v("Đối phương xoay một cái mặt, nàng liền nhận ra được, không phải người khác, chính là cái kia vẫn làm cho nàng phiền muộn Trương Huyền."),t("br"),t("br"),n._v("Khó trách mới vừa nói nói liền không thấy bóng người, nguyên lai chạy tới sờ Khiếu Thiên thú."),t("br"),t("br"),n._v("Phong đường chủ để ta sang đây xem Thanh Dương Tông Sư chữa bệnh, chỉ là vì học tập, an phận thủ thường, yên tĩnh nhìn là tốt rồi, dằn vặt lung tung cái gì a?"),t("br"),t("br"),n._v("Sờ cái gì sờ a!"),t("br"),t("br"),n._v("Này không phải là mình tìm mắng sao?"),t("br"),t("br"),n._v("Quả nhiên, cái ý niệm này mới vừa kết thúc, liền thấy một bên Thanh Dương Tông Sư sắc mặt dần dần xanh lại, hai mắt nhắm lại."),t("br"),t("br"),n._v("“Làm càn!”"),t("br"),t("br"),n._v("Bàn tay lớn quăng một cái, nhìn về phía Phong đường chủ, râu nhếch lên, ngữ khí ác liệt: “Này chính là các ngươi Thú đường người? Như thế không có quy củ? Còn nhìn một chút... Xem hiểu không? Cái gì cũng không hiểu, liền chạy tới sờ loạn, thực sự là không biết trời cao đất rộng!”"),t("br"),t("br"),n._v("“Tông Sư xin bớt giận, hắn không phải cố ý, khả năng cũng là lo lắng Khiếu Thiên thú...”"),t("br"),t("br"),n._v("Cũng không nghĩ tới Trương Huyền sẽ như vậy lỗ mãng, trực tiếp chạy tới sờ Khiếu Thiên thú, Phong đường chủ vội vã giải thích."),t("br"),t("br"),n._v("“Ta xem bệnh cần tuyệt đối yên tĩnh, không thích quấy rối, ở chung nhiều năm, ngươi không thể nào không biết đi!”"),t("br"),t("br"),n._v("Đánh gãy hắn, Thanh Dương Tông Sư ánh mắt như điện, mang theo kiêu ngạo không thể xâm phạm khí tức."),t("br"),t("br"),n._v("“Ta biết, chỉ là...”"),t("br"),t("br"),n._v("“Biết là tốt rồi, không có chỉ là. Bạn tốt nhiều năm, ta liền không tính đến, nơi này không phải cái gì lung ta lung tung người đều có thể đi vào, đem bọn họ đuổi ra ngoài, ta hiện tại chữa bệnh.” Nói xong khoát tay áo một cái, Thanh Dương Tông Sư một mặt ngạo nghễ."),t("br"),t("br"),n._v("Hắn người nào?"),t("br"),t("br"),n._v("Y đạo Tông Sư, Bắc Vũ vương quốc đều tiếng tăm lừng lẫy tồn tại, đang chữa bệnh, một cái không biết từ nơi nào nhô ra gia hỏa, cũng nói nhìn một chút, ngươi biết cái gì? Ngươi sẽ xem sao?"),t("br"),t("br"),n._v("“Thanh Dương Tông Sư còn xin bớt giận, này mấy người trẻ tuổi, đều là ngày hôm nay vừa mới thông qua sát hạch thuần thú sư, để bọn họ đi qua đến, chỉ là quan sát một hồi, tuyệt đối vô tâm mạo phạm!” Phong đường chủ nói."),t("br"),t("br"),n._v("“Đúng đấy, Trương thú sư cũng chỉ là nhất thời hiếu kỳ mới sờ soạng một hồi, bảo đảm lần sau tuyệt đối sẽ không tái phạm!”"),t("br"),t("br"),n._v("Lư thú sư cũng liền bận bịu đi tới khuyên can."),t("br"),t("br"),n._v("“Hừ, ngày hôm nay liền cho hai người các ngươi một bộ mặt, lần sau ta hi vọng Thú đường có thể có chút quy củ! Xem bệnh cần tuyệt đối yên tĩnh, không phải là người nào đều có tư cách lại đây quan sát, có thể nhìn hiểu! Nếu như lần sau lại có thêm loại này cái gì cũng không hiểu, liền táy máy tay chân, làm lỡ tốt nhất trị liệu thời gian, các ngươi ai có thể gánh nặng lên?”"),t("br"),t("br"),n._v("Thanh Dương Tông Sư hừ một tiếng, nhìn quanh một vòng, mang theo không cho phản bác uy nghiêm."),t("br"),t("br"),n._v("“Là, là!” Phong đường chủ liền vội vàng gật đầu."),t("br"),t("br"),n._v("Thuần thú sư cùng y sư địa vị gần như, nhưng mà hắn có việc cầu người, không có cách nào."),t("br"),t("br"),n._v("Ai bảo Khiếu Thiên thú bệnh nặng, lại chỉ có này người mới có thể trị liệu? Coi như đối phương quát lớn, cũng chỉ có thể nhẫn nhịn."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy mọi người thái độ cũng còn tốt, Thanh Dương Tông Sư không ở tính toán, tiếp tục nhìn về phía trước mắt đã rơi vào hôn mê Khiếu Thiên thú."),t("br"),t("br"),n._v("“Vừa nãy Tông Sư nói đã xem xảy ra vấn đề, thế nhưng có trị liệu biện pháp?” Phong đường chủ có chút sốt sắng."),t("br"),t("br"),n._v("“Đi qua của ta sơ bộ chẩn đoán bệnh, đã xác định chỗ mấu chốt.” Thanh Dương Tông Sư bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("Mọi người tất cả đều ánh mắt sáng lên."),t("br"),t("br"),n._v("Không hổ là y đạo Tông Sư, nhanh như vậy liền tìm đến vấn đề chỗ ở."),t("br"),t("br"),n._v("Phải biết, tam đại nhị tinh thuần thú sư, rất nhiều nhất tinh thuần thú sư, nghiên cứu ròng rã hai ngày, đều không có cách nào giải quyết."),t("br"),t("br"),n._v("Thấy mọi người một mặt ánh mắt mong chờ, Thanh Dương Tông Sư lúc này mới thoả mãn gật đầu, lạnh nhạt nói: “Con này Thiết Xỉ Khiếu Thiên Thú, trong cơ thể khô nóng, tinh lực dồi dào, lại thỉnh thoảng ngửa mặt lên trời hí dài, nếu như ta không nhìn lầm... Hẳn là muốn tìm bạn đời!”"),t("br"),t("br"),n._v("“Tìm bạn đời?”"),t("br"),t("br"),n._v("“Không sai, man thú cũng có giao phối **, đặc biệt là đến mùa thu, loại này ** càng sâu, thậm chí đạt đến khó có thể ngăn chặn mức độ. Vừa nãy ta nhìn, thân thể hắn khô nóng, cần gấp phát tiết, bởi vì không cách nào đạt tới nguyện vọng, lúc này mới càng ngày càng nhanh táo, nội hỏa trên xông, tiến vào phổi, cuối cùng rơi vào hôn mê.”"),t("br"),t("br"),n._v("Thanh Dương Tông Sư đầu vung lên, mang theo nhìn xa trông rộng ánh sáng."),t("br"),t("br"),n._v("“Ngươi nói những cái này, chúng ta trước cũng hoài nghi, chỉ là... Dựa theo man thú trưởng thành tuổi, chắc là còn chưa tới mới đúng...” Phong đường chủ hơi nghi hoặc một chút."),t("br"),t("br"),n._v("“Man thú không thể dựa theo người tuổi cân nhắc, nó chỉ cần thân thể bộ phận thành thục, tự nhiên sẽ có đặc thù **, đây là bản tính gây ra. Thành đêm không ngủ, ngửa mặt lên trời hí dài, những thứ này đều là muốn tìm bạn đời dấu hiệu, không có sai!” Thanh Dương Tông Sư nhìn sang, trong mắt tinh quang bắn ra bốn phía, mang theo làm cho người ta áp bức cảm giác: “Làm sao? Không tin của ta chẩn đoán bệnh?”"),t("br"),t("br"),n._v("“Này cũng không phải, chỉ là...” Phong đường chủ lắc đầu một cái, không tiếp tục băn khoăn vấn đề này, sốt ruột nhìn sang: “Cái kia tình huống như thế... Giải quyết như thế nào?”"),t("br"),t("br"),n._v("“Xem ra rất phức tạp, tìm tới căn do, giải quyết lên liền rất đơn giản, nó muốn tìm bạn đời, giúp nó tìm một cái là được!” Thanh Dương Tông Sư cười nói."),t("br"),t("br"),n._v("“Tìm một cái?”"),t("br"),n._v("Phong đường chủ đầy mặt phát khổ."),t("br"),t("br"),n._v("Cái khác hai vị nhị tinh Tông Sư cũng đồng thời lắc đầu."),t("br"),t("br"),n._v("“Không phải là không muốn tìm, mà là Thiết Xỉ Khiếu Thiên Thú bản thân liền ít ỏi, trong thời gian ngắn tìm một đầu, đồng thời tuổi xấp xỉ mang tới, hầu như không thể nào làm được!”"),t("br"),t("br"),n._v("Thiết Xỉ Khiếu Thiên Thú loại này còn vị thành niên liền đạt đến Tông Sư cảnh giới phi hành man thú, toàn bộ Huyền Lạc sơn mạch cũng sẽ không vượt qua hai cái."),t("br"),t("br"),n._v("Càng quan trọng chính là, phi hành nhanh như vậy, coi như tìm tới, lại làm sao có khả năng bắt lấy thuần phục?"),t("br"),t("br"),n._v("Lúc trước Thú đường thu dưỡng con này, cũng là nó tuổi nhỏ lúc nuôi nấng, thậm chí còn chuyên môn xây dựng phủ đệ, chuyên môn phái người hầu hạ, mới để cho ngoan ngoãn ở lại chỗ này, nói thật... Đến hiện tại đều không triệt để thuần phục, không cùng bất luận người nào ký kết qua khế ước."),t("br"),t("br"),n._v("Không phải là không muốn, mà là không làm được!"),t("br"),t("br"),n._v("Khiếu Thiên thú bản thân liền đối với nhân loại có thành kiến rất lớn, muốn triệt để thuần phục rất khó, nếu như đúng là triệt để thuần phục, đã có thể lấy hòa chủ nhân tiến hành câu thông, bọn họ khẳng định cũng sớm biết đối phương bị bệnh gì, có thể sớm một chút trị liệu."),t("br"),t("br"),n._v("Một đầu từ nhỏ nuôi đến đại Khiếu Thiên thú đều thuần phục không được, lại làm sao có khả năng trong thời gian ngắn cho nó tìm một đầu bầu bạn lại đây!"),t("br"),t("br"),n._v("Hiển nhiên là không thể nào làm được!"),t("br"),t("br"),n._v("“Không tìm được?”"),t("br"),t("br"),n._v("“Đúng đấy!” Phong đường chủ nhìn sang: “Khẩn cầu Thanh Dương Tông Sư lại muốn những biện pháp khác, chỉ cần có thể cứu Khiếu Thiên thú là tốt rồi!”"),t("br"),t("br"),n._v("“Biện pháp trái lại có, hơn nữa còn có thể một lần vất vả suốt đời nhàn nhã, vĩnh viễn giải quyết cái này mầm họa, chỉ là... Có thể sẽ phiền toái một chút.” Thanh Dương Tông Sư chần chờ một chút."),t("br"),t("br"),n._v("“Đã có, vậy thì phiền phức Tông Sư nhanh lên một chút ra tay.” Phong đường chủ mấy người ánh mắt sáng lên, vội vàng nói."),t("br"),t("br"),n._v("“Ân, được rồi!” Hai tay chắp ở sau lưng, Thanh Dương Tông Sư gật gù: “Không tìm được bầu bạn, kỳ thực muốn giải quyết nó loại này nhu cầu, cũng rất đơn giản, vậy thì là... Thiến đi! Chỉ cần thành công, sau đó thì sẽ không có chuyện như vậy quấy nhiễu, nó cũng sẽ hoàn toàn thần phục các ngươi, chuyên tâm vì Thú đường làm việc.”"),t("br"),t("br"),n._v("“Thiến đi?”"),t("br"),t("br"),n._v("Phong đường chủ mấy người đồng thời sửng sốt."),t("br"),t("br"),n._v("“Chỉ có thiến đi, mới có thể ổn định tâm thần, không đến mức mỗi ngày khô nóng khó nhịn, khó có thể tự chế, cũng sẽ không lại xuất hiện tình huống như thế.”"),t("br"),t("br"),n._v("Thanh Dương Tông Sư gật đầu nhìn sang: “Các ngươi Thú đường không phải cũng có rất nhiều man thú, vì để tránh cho phiền phức, chuyên môn nghiêm ngặt sao?”"),t("br"),t("br"),n._v("“Chuyện này...” Phong đường chủ gật đầu."),t("br"),t("br"),n._v("Thú đường xác thực có không ít man thú, vừa sinh ra liền bị thiến rơi mất, như vậy có thể không cần suy nghĩ nhiều những chuyện khác, triệt để là nhân loại hiệu lực."),t("br"),t("br"),n._v("“Được rồi, của ta chẩn đoán bệnh phương pháp chính là cái này, các ngươi suy tính một chút, nếu như muốn động thủ, liền cố gắng nhanh, gia đình này cùng nhau rất rõ ràng chống đỡ không được quá lâu, không giải quyết, ta sợ ngày mai đều không kiên trì được!”"),t("br"),t("br"),n._v("Thanh Dương Tông Sư không nói thêm nữa, yên tĩnh đứng tại chỗ, chờ đợi mọi người lựa chọn."),t("br"),t("br"),n._v("“Lẽ nào tại thật muốn như vậy?”"),t("br"),t("br"),n._v("Phong đường chủ mấy người nhìn nhau, từng cái từng cái đầy mặt băn khoăn."),t("br"),t("br"),n._v("Thiết Xỉ Khiếu Thiên Thú, từ nhỏ đã theo bọn họ, đã ở chung rất nhiều năm, sớm có người thân giống như cảm tình, trực tiếp đem nó thiến... Thực sự không xuống tay được."),t("br"),t("br"),n._v("Lại nói, Thú đường xác thực có thiến man thú tiền lệ, thế nhưng, không ít man thú đều không chịu đựng được, chết tại chỗ."),t("br"),t("br"),n._v("Hiện tại Khiếu Thiên thú rơi vào hôn mê, bản thân liền bệnh nặng, nếu như cử động nữa đao, không sống sót... Thật liền nguy rồi!"),t("br"),t("br"),n._v("Hơn nữa, Tông Sư cảnh man thú đã có bản thân ý thức, làm như vậy vạn nhất trêu đến không cao hứng, rất có thể có thể giương cánh rời đi, cũng sẽ không bao giờ trở về, thậm chí làm không cẩn thận còn có thể trả thù Thú đường, đưa tới lại phiền toái lớn."),t("br"),t("br"),n._v("Tình huống như thế, cũng không phải là không có tiền lệ, cái khác Thú đường liền từng gặp được tình huống như vậy, thiến rơi mất một đầu nửa bước Tông Sư cảnh man thú, dẫn đến đối phương tính tình đại biến, bội phản Thú đường không nói, càng là coi Thú đường người vì đại thù, cả ngày đến tìm phiền phức."),t("br"),t("br"),n._v("Cuối cùng đành phải phái ra cao thủ, đem nó đánh gục."),t("br"),t("br"),n._v("Nghe nói đánh gục sau đó, này con man thú chủ nhân, ròng rã khóc rống ba ngày, cũng bởi vì thương tâm quá độ, chân khí tan rã, sống không bao lâu liền qua đời."),t("br"),t("br"),n._v("Man thú cùng người như thế, cũng cần tôn trọng."),t("br"),t("br"),n._v("Không nói cho đối phương biết, liền đem hắn cắt, bản thân liền là một loại sỉ nhục."),t("br"),t("br"),n._v("“Thanh Dương Tông Sư, nhưng còn có những biện pháp khác, cái này... Thực sự không tốt lắm xử lý!”"),t("br"),t("br"),n._v("Chần chờ chốc lát, Phong đường chủ không nhịn được nói."),t("br"),t("br"),n._v("“Này đã là phương pháp giải quyết tốt nhất! Nếu như không muốn thì thôi, ta đưa ra chẩn đoán bệnh kết quả cùng phương pháp trị liệu, không đi làm là chuyện của các ngươi, sau đó có vấn đề gì, cũng không dùng tới tìm ta nữa!” Thanh Dương Tông Sư hừ nói, đầy mặt không vui."),t("br"),t("br"),n._v("Hắn đã đưa ra nguyên nhân sinh bệnh cùng phương pháp trị liệu, đối phương còn ra sức khước từ, lẽ nào là không tin hắn vị này y đạo Tông Sư?"),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Thấy đối phương nói như vậy, Phong đường chủ cắn răng một cái, vừa định đáp ứng, liền nghe đến một cái thanh âm nhàn nhạt tiếng vang lên."),t("br"),t("br"),n._v("“Đã sớm nghe nói lang băm hại người, còn chưa tin, bây giờ nhìn lại, quả thực như vậy...”"),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Thanh Dương Tông Sư lập tức nổ tung, đột nhiên quay đầu nhìn lại, liền thấy đám người bên trong một người thanh niên, vẻ mặt thản nhiên nhìn sang."),t("br"),t("br"),n._v("Chính là vừa nãy lặng lẽ chạy tới sờ Khiếu Thiên thú đầu thú gia hỏa."),t("br"),t("br"),n._v("Trương Huyền!"),t("br"),t("br"),n._v("“Ta nói ngươi là lang băm! Nếu như nghe không hiểu...”"),t("br"),t("br"),n._v("Trương Huyền đầy mặt nghiêm túc: “Chính là y thuật thấp kém ngu xuẩn, thường thường không nhìn ra chứng bệnh, liền tùy tiện chẩn đoán bệnh!”"),t("br"),t("br"),n._v("(Thứ hai, cầu phiếu đề cử!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-264"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-266"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);