(window.webpackJsonp=window.webpackJsonp||[]).push([[1490],{1844:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-263-xuat-phat-đoc-đien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-263-xuat-phat-đoc-đien"}},[n._v("#")]),n._v(" Chương 263: Xuất phát, Độc điện!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Mạc Vũ cái này lò luyện đan, là mới vừa thông qua nhất tinh luyện đan sư thời điểm, sư phụ tiễn đưa, cẩn thận tính ra, xác thực đã luyện đan mười tám lần, trong đó có muốn nổ lô dấu hiệu bảy lần, mỗi lần đều là sư phụ giúp áp chế."),t("br"),t("br"),n._v("Nàng cũng xác thực một tháng trước, luyện chế quá một viên Băng Phách đan."),t("br"),t("br"),n._v("Đối phương nói không có một chút nào sai lầm."),t("br"),t("br"),n._v("Liếc mắt nhìn lò luyện đan, liền nhìn ra những cái này? Chuyện này... Không khỏi quá nghịch thiên đi!"),t("br"),t("br"),n._v("Mạc Vũ chỉ cảm thấy toàn thân run cầm cập, khó có thể tin."),t("br"),t("br"),n._v("“Đương nhiên những cái này chỉ là trụ cột nhất đồ vật, chỉ có thể khiến người ta sơ bộ phán định luyện đan thời điểm có thể sẽ nổ tung, cũng không xác định lúc nào nổ, nổ tung sức mạnh lớn bao nhiêu, lô đỉnh cuối cùng có thể kiên trì bao lâu.”"),t("br"),t("br"),n._v("Thấy đối phương không lên tiếng, Trương Huyền cũng không hỏi nhiều, tiếp tục nói."),t("br"),t("br"),n._v("“Muốn xác định những cái này, đối với thuốc khống chế cũng có thể đạt đến cảnh giới nhất định, Chước Dương đan vì chín loại dương thuộc tính thuốc dung hợp mà thành, chín loại dược liệu chỉ có lẫn nhau trung hoà, lẫn nhau ước chế và cân đối quyền lực, mới có thể duy trì sức mạnh không xung đột, bằng không, một bước phạm sai lầm, từng bước phạm sai lầm, giống như nhen lửa thuốc nổ.”"),t("br"),t("br"),n._v("“Trước, ngươi nghiêm ngặt dựa theo yêu cầu của ta đưa lên dược liệu, không có bất cứ vấn đề gì, bất quá, thứ mười bảy bước thời điểm, ngươi đưa lên Căn Cực thảo thời điểm, vừa vặn sớm nửa cái hô hấp.”"),t("br"),t("br"),n._v("“Chớ xem thường này nửa cái hô hấp, ngươi thứ mười một bước thả dương hỏa mộc cùng thứ mười bốn bước thả Kim Dương hoa, sức mạnh xung đột lẫn nhau, lấy ngươi lúc đó lô nội tình tình hình, cần mười hai cái hô hấp mới có thể triệt để hòa tan, Kim Dương hoa cần bảy cái hô hấp mới có thể, mà tốt nhất đưa lên bên dưới một gốc dược liệu thời gian, chính là mới vừa dung hợp thời điểm, bao nhiêu cũng không được!”"),t("br"),t("br"),n._v("“Căn Cực thảo sớm nửa cái hô hấp, bằng này hai loại dược liệu còn không dung hợp hoàn thành, dược liệu bên trong dương thuộc tính còn có thể va chạm xung đột lẫn nhau, cùng nhen lửa hỏa dược như thế, lập tức xuất hiện sóng trùng kích. Nguồn sức mạnh này, còn chưa đủ làm cho của ngươi lò luyện đan phá nát, lại tăng lên vỡ vụn quá trình, sau đó, ta để ngươi tiếp tục đưa lên dược liệu, thậm chí gia tăng hỏa lực, chính là thời khắc quan sát, tính toán dược liệu dung hợp xuất hiện lực xung kích lớn bao nhiêu, đồng thời cũng tính toán cùng ngươi lô đỉnh sức chịu đựng mạnh bao nhiêu.”"),t("br"),t("br"),n._v("“Đi qua suy tính, phát giác đủ để kiên trì đến thành đan, lúc này mới để ngươi tiếp tục nữa.” Trương Huyền nói đến đây, ngừng lại, vẻ mặt hờ hững nhìn sang: “Còn có nghi vấn gì không?”"),t("br"),t("br"),n._v("“Ta... Không rồi!”"),t("br"),t("br"),n._v("Mạc Vũ trong lòng chấn động khó có thể lắng lại."),t("br"),t("br"),n._v("Vốn đang cho rằng, là cái gì kỹ xảo làm cho đối phương chính xác khống chế luyện đan, bây giờ mới biết, mặc dù là kỹ xảo, cũng không phải nàng có thể học được."),t("br"),t("br"),n._v(" Bên trong liên luỵ đồ vật thực sự quá nhiều."),t("br"),t("br"),n._v("Khí tài, thuốc, thủ pháp luyện đan, hoàn cảnh, thậm chí luyện đan lúc tâm cảnh... Tất cả mọi thứ, thiếu một thứ cũng không được, dù cho thiếu tính toán một điểm, cũng có thể dẫn đến sắp thành lại bại."),t("br"),t("br"),n._v("Trước còn đang suy nghĩ, nàng cùng đối phương luyện đan cũng là chênh lệch một điểm, hảo hảo tu luyện, rất nhanh là có thể đuổi kịp, hiện tại mới rõ ràng... Cách biệt đâu chỉ ngàn dặm vạn dặm, căn bản không ở cùng một đẳng cấp."),t("br"),t("br"),n._v("Khó trách có thể Luận đan thành công, đơn phần này đối với luyện đan hiểu rõ, liền để nàng lòng sinh tuyệt vọng."),t("br"),t("br"),n._v("“Nếu như không có nghi vấn, ta muốn nghỉ ngơi!”"),t("br"),t("br"),n._v("Thấy thật vất vả lừa gạt dừng đối phương, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Nói thật, vừa nãy những cái kia chỉ là thuận miệng nói một chút, nếu như không phải thư viện công lao, muốn y theo những cái kia nhỏ bé đồ vật phân tích ra nổ lô đồng thời thành đan, ngũ tinh luyện đan sư e sợ đều không làm được."),t("br"),t("br"),n._v("Nắm giữ loại năng lực này, kém cỏi nhất cũng muốn lục tinh, thậm chí càng tài cao đi."),t("br"),t("br"),n._v("“Trương thú sư, còn xin dừng bước...”"),t("br"),t("br"),n._v("Thấy hắn liền muốn đi vào sân, Mạc Vũ tiểu thư vội vã la lên: “Ta nghe nói, ngươi muốn đi Thiên Vũ vương quốc?”"),t("br"),t("br"),n._v("“Làm sao ngươi biết?”"),t("br"),t("br"),n._v("Lời nói mới hỏi ra, lập tức hiểu được, khẳng định là Vân Đào cái tên này lắm miệng."),t("br"),t("br"),n._v("Bất quá, vốn là cũng không có ý định ẩn giấu, gật gật đầu: “Ta là muốn đi Hồng Liên sơn mạch.”"),t("br"),t("br"),n._v("“Hồng Liên sơn mạch vô cùng hoang vu, trên căn bản không ai đi tới, ngươi muốn qua đi, không phải là muốn đi tìm... Độc điện chứ?” Mạc Vũ tiểu thư không nhịn được nói."),t("br"),t("br"),n._v("Huyền Lạc sơn mạch cây xanh tỏa bóng, còn có vô số man thú, Hồng Liên sơn mạch thì cùng hỏa diễm thiêu đốt quá giống như vậy, hầu như không sao đồ vật, chỉ cần là người tu luyện, hầu như đều không gặp qua đi."),t("br"),t("br"),n._v("Sở dĩ như vậy, bởi vì trong truyền thuyết có một toà Độc điện ẩn giấu ở sơn mạch nơi sâu xa, xung quanh bày xuống kịch độc, chỉ muốn qua đi, bất luận người vẫn là thú, đều sẽ phải chịu độc khí tập kích, chết oan chết uổng."),t("br"),t("br"),n._v("Nguyên nhân cụ thể, ai cũng không biết, bởi vì đi người, tám chín mươi phần trăm đều không trở về, lâu dần, mọi người đều nghe đến đã biến sắc, lại không ai dám đi tới."),t("br"),t("br"),n._v("Nơi này cơ bản là hoang tàn vắng vẻ chỗ, không người hỏi thăm."),t("br"),t("br"),n._v("Vị này Trương Huyền muốn đi... E sợ chính là vì cái kia Độc điện."),t("br"),t("br"),n._v("“Ngươi biết Độc điện?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Độc điện sự tình, hắn là theo Lưu sư mấy người trong miệng biết được, cái khác chưa bao giờ nhắc tới, giờ khắc này Mạc Vũ tiểu thư nói ra, lập tức kích động lên."),t("br"),t("br"),n._v("“Truyền thuyết Hồng Liên sơn mạch là có Độc điện, bất quá... Cụ thể ở nơi nào, ai cũng không biết!” Mạc Vũ lắc đầu một cái."),t("br"),t("br"),n._v("“Chỉ cần có là tốt rồi, tổng có thể tìm tới!” Trương Huyền nhẹ nhàng nở nụ cười."),t("br"),t("br"),n._v("Chỉ cần xác định Độc điện tại đây cái sơn mạch, muốn tìm được liền dễ dàng."),t("br"),t("br"),n._v("Quá mức tìm tấm bản đồ, một chỗ một chỗ thí nghiệm, Thiên Đạo thư viện có thể phân biệt thật giả, tuy rằng không thể vạch ra chính xác vị trí, nhưng có thể đem sai lầm vị trí tất cả giám định ra đến."),t("br"),t("br"),n._v("Chỉ cần không có sai lầm, vậy thì là chính xác."),t("br"),t("br"),n._v("“Nói thì nói như thế, có thể Hồng Liên sơn mạch diện tích mấy ngàn dặm, thật muốn từng tấc từng tấc tìm, đem người mệt chết cũng không làm được. Lại nói, coi như tìm tới, Độc điện xung quanh khả năng bố trí có kịch độc, không ai dẫn dắt, căn bản không vào được!”"),t("br"),t("br"),n._v("Nói đến đây Mạc Vũ tiểu thư dừng lại một chút, một đôi tú mục nhìn sang: “Nếu như ngươi thật muốn đi tìm, ta ngược lại thật ra có thể giúp.”"),t("br"),t("br"),n._v("“Ngươi?” Trương Huyền sững sờ: “Ngươi có thể tìm tới Độc điện, đồng thời có biện pháp đi vào?”"),t("br"),t("br"),n._v("“Ta không làm được, bất quá, ta biết có một người khẳng định có thể.” Mạc Vũ tiểu thư nở nụ cười."),t("br"),t("br"),n._v("“Ồ?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Không nghĩ tới vị này Mạc Vũ tiểu thư, vẫn còn có người như thế mạch, nếu có thể đem hắn mang tới, tuyệt đối bớt đi không ít phiền phức."),t("br"),t("br"),n._v("“Ngươi dạy ta luyện đan, ta dẫn ngươi đi tìm người kia, làm sao?” Mạc Vũ tiểu thư nhìn sang."),t("br"),t("br"),n._v("“Thành giao!” Trương Huyền gật đầu."),t("br"),n._v("“Vậy lúc nào thì xuất phát?”"),t("br"),t("br"),n._v("“Sáng mai liền đi, đúng rồi, ngươi có thể có phi hành man thú?” Trương Huyền nhìn sang."),t("br"),t("br"),n._v("Thiết Xỉ Khiếu Thiên Thú đạt đến nửa bước Chí Tôn, tốc độ cực nhanh, nếu như nàng phi hành man thú theo không kịp, sẽ cản trở."),t("br"),t("br"),n._v("“Ngươi!”"),t("br"),t("br"),n._v("Nghe đối phương như vậy hỏi dò, Mạc Vũ tiểu thư con mắt một hồi nheo lại, hàm răng cắn khanh khách vang vọng, hô hấp dồn dập."),t("br"),t("br"),n._v("Hết chuyện để nói, ngươi cố ý chứ?"),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Nhìn thấy nàng dáng dấp này, Trương Huyền mới nhớ tới đến, nàng xác thực có một con phi hành man thú, bất quá... Đã làm phản, hiện tại là bản thân!"),t("br"),t("br"),n._v("“Như vậy, ngươi cùng ta cùng một chỗ cưỡi Khiếu Thiên thú!”"),t("br"),t("br"),n._v("Thiết Xỉ Khiếu Thiên Thú hình thể to lớn, đừng nói ngồi hai người, coi như ngồi lên bảy, tám cái đều dư dả."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Nghe nói như thế, Mạc Vũ tiểu thư sắc mặt mới khá hơn một chút, bất quá còn chưa khỏe bao lâu, lời của đối phương tiếp tục vang lên."),t("br"),t("br"),n._v("“Khiếu Thiên thú là của ta thú sủng, ngươi muốn cưỡi mà nói, cần giao nộp phí dụng, trước nó vẫn là Tông sư cảnh thời điểm, đi một chuyến Thiên Vũ vương quốc đại khái 20 viên thú huyết thạch, thực lực bây giờ tăng lên, tốc độ càng nhanh hơn... Liền gia tăng gấp đôi đi, 40 viên thú huyết thạch.”"),t("br"),t("br"),n._v("Trương Huyền tính toán một chút: “Ngày mai chuẩn bị kỹ càng, sớm cho, không muốn quỵt nợ!”"),t("br"),t("br"),n._v("Xem xong Thú đường thư tịch, cũng biết thú huyết thạch là món đồ gì."),t("br"),t("br"),n._v("Cùng kim tệ như thế, là Thú đường chuyên dụng giao dịch tiền, đối với man thú huyết mạch tiến dời, có cực giúp đỡ lớn."),t("br"),t("br"),n._v("Tu sĩ tu luyện hấp thu linh khí, man thú dùng thú huyết thạch."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Mạc Vũ thân thể mềm mại loáng một cái."),t("br"),t("br"),n._v("Ngươi một người tinh thông nhiều như vậy nghề nghiệp, lại là như thế lợi hại luyện đan sư, thiên tư hơn người, có thể khỏi nói như thế dung tục đồ vật sao?"),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Biết cùng cái tên này nói chuyện, sớm muộn đều sẽ bị tươi sống tức chết, cũng lại lưu lại không được, xoay người rời đi."),t("br"),t("br"),n._v("Mới đi mấy bước, liền nghe đối phương lầm bầm lầu bầu âm thanh lại tiếng vang lên."),t("br"),t("br"),n._v("“Này, hiện tại người quá không cảm thấy, trước còn nói báo đáp ta, cho ít tiền, kết quả... Không có động tĩnh. Cưỡi của ta phi hành man thú, lại còn không muốn ra tiền... Không tiền sớm nói sao, làm hại ta trắng chờ mong một trận, còn Công chúa, Công chúa liền có thể quỵt nợ?”"),t("br"),t("br"),n._v("“Ngươi... Trương Huyền, ngươi nói rõ cho ta, ai không tiền...”"),t("br"),t("br"),n._v("Đột nhiên xoay người, không Vũ công chúa cũng lại ngăn chặn không được, đang muốn tìm cái tên này đem lời nói rõ ràng ra, liền thấy hắn một bên thở dài một bên lắc đầu, đi vào viện."),t("br"),t("br"),n._v("“Đáng ghét! Đáng ghét! Ghê tởm Trương Huyền, chết Trương Huyền, ngươi chờ ta!”"),t("br"),t("br"),n._v("Thấy hắn biến mất, Mạc Vũ tiểu thư giậm chân một cái, quá thật lâu, mới thở phì phò rời đi."),t("br"),t("br"),n._v("Nhìn nàng dáng dấp phẫn nộ, e sợ ngày hôm nay đều khó mà đi vào giấc ngủ."),t("br"),t("br"),n._v("Trở lại sân, Trương Huyền cũng xác thực mệt mỏi, nằm xuống liền ngủ."),t("br"),t("br"),n._v("Suốt đêm không nói chuyện, trời vừa sáng, nhị tinh thuần thú sư huy chương liền đưa tới."),t("br"),t("br"),n._v("Đem huy chương thu hồi, Trương Huyền sắp xếp một hồi."),t("br"),t("br"),n._v("Thẩm Bích Như chỉ là tiễn đưa hắn đến Thú đường, còn muốn hồi thiên huyền Vương thành, vốn là hắn định dùng Thanh Ưng Thú đưa về, bất quá đối phương kiên trì không thể, Trương Huyền liền đem Kim Thân Thiết Tiễn Báo đưa cho nàng."),t("br"),t("br"),n._v("Nắm giữ Thanh Ưng Thú cùng Khiếu Thiên thú hai đại phi hành man thú, thiết tiền báo đối với hắn đã không còn ý nghĩa, muốn cùng không muốn đều không quá quan trọng, đưa cho Thẩm Bích Như, đối với nàng mà nói trái lại một sự giúp đỡ lớn."),t("br"),t("br"),n._v("Chí ít tại Thiên Huyền Vương thành, lại không ai dám tìm nàng phiền phức."),t("br"),t("br"),n._v("Cũng coi như báo đáp đối phương này một đường chăm sóc tình nghĩa."),t("br"),t("br"),n._v("Sắp xếp xong Thẩm Bích Như, đi tới Khiếu Thiên thú sân, mới phát giác trên lưng của nó ngồi một cái tương tự gian phòng kiến trúc, toàn thân kim loại rèn đúc, cùng thân thể hoàn mỹ kết hợp với nhau, coi như phi hành bên trong cương phong gào thét, cũng sẽ không lay động."),t("br"),t("br"),n._v("Cưỡi Thú Thừa, không ít người thực lực cũng không mạnh, bay quá nhanh dễ dàng ngã xuống, có vật này, rồi cùng ngồi ở gian phòng không có gì khác nhau, cũng là Thú đường chuyên môn vì khách hàng chuẩn bị."),t("br"),t("br"),n._v("Khiếu Thiên thú vốn là to lớn, trên lưng nó bộ này đồ vật, lớn, rộng có tới năm, sáu mét, cùng gian phòng như thế, bên trong lại bày cái bàn, giường chiếu, mảy may không cảm thấy được phi hành trên không trung."),t("br"),t("br"),n._v("Đừng nói hai người, coi như đem Thanh Ưng Thú thêm vào, đều không thể không biết chen chúc."),t("br"),t("br"),n._v("“Cáo từ!”"),t("br"),t("br"),n._v("Đi vào trong đó, Trương Huyền hết sức hài lòng, lúc này mới cùng Phong đường chủ cáo từ."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Thiết Xỉ Khiếu Thiên Thú thô to cánh quạt một cái, hai người hai thú, thẳng tắp hướng thiên không bay đi."),t("br"),t("br"),n._v("Nhìn từ từ rời xa Thú đường cùng Huyền Lạc sơn mạch, Trương Huyền khẽ mỉm cười."),t("br"),t("br"),n._v("Độc điện, ta đến rồi."),t("br"),t("br"),n._v("(Đề cử đại thần áo gile sách mới 《 vô địch cường hóa hệ thống 》, vị này đại thần viết siêu cấp tinh vi tin, so phát cái tinh vi tin đi Thiên Đình còn muốn sớm, xem như là tinh vi tin lưu người khai sáng một trong, sách mới đáng để mong chờ.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-271"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-273"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);