(window.webpackJsonp=window.webpackJsonp||[]).push([[1499],{1852:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-271-khe-uoc-co-【-hai-hop-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-271-khe-uoc-co-【-hai-hop-nhat-】"}},[n._v("#")]),n._v(" Chương 271: Khế ước cổ 【 hai hợp nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Đem Đại Dược Vương một bàn tay đánh ngất, nói liên luỵ bí pháp, rất nhiều hộ vệ nhịn, làm như không nhìn thấy."),t("br"),t("br"),n._v("Lúc này dĩ nhiên nói như vậy, này rõ ràng là đang gây hấn với, làm sao có thể phải nhịn xuống!"),t("br"),t("br"),n._v("“Làm càn!”"),t("br"),t("br"),n._v("“Muốn chết!”"),t("br"),t("br"),n._v("Rất nhiều hộ vệ, từng cái từng cái trợn mắt nhìn, trong tay đao kiếm hàn quang bắn ra, chỉ cần ra lệnh một tiếng, ngay lập tức sẽ đem cái này giả vờ giả vịt, không giữ mồm giữ miệng gia hỏa, băm thành thịt vụn."),t("br"),t("br"),n._v("“Đầu óc không bệnh chứ?”"),t("br"),t("br"),n._v("“Ta trước đây cùng cái này Bạch Thiềm tiếp xúc qua, tuy rằng nhân phẩm không ra gì, đầu óc vẫn tính bình thường, làm sao... Đột nhiên trở nên có vấn đề?”"),t("br"),t("br"),n._v("Vốn cho là hắn có thể nói ra cái gì kinh thế lý luận, cái khác rất nhiều y sư nghe nói như thế, tất cả đều suýt chút nữa không một ngụm máu tươi phun ra, từng cái từng cái trợn mắt ngoác mồm, như là nhìn thấy yêu quái."),t("br"),t("br"),n._v("Chỉ cần đến Hồng Liên thành, người nào không biết Đại Dược Vương uy thế?"),t("br"),t("br"),n._v("Quả thực chính là vua một cõi, coi như Thiên Vũ vương quốc vương tử đi tới nơi này, đều muốn ngoan ngoãn đợi. Một cái tát đem người đánh ngất, đã đang gây hấn với, dĩ nhiên ngày càng táo tợn, nói thẳng “Tự gây nghiệp chướng, không thể sống”..."),t("br"),t("br"),n._v("Ngươi là cảm thấy mệnh lớn, sống đủ, muốn muốn tìm chết, vẫn cảm thấy Đại Dược Vương dễ tính?"),t("br"),t("br"),n._v("Thành Phong y sư cũng xem kẻ ngu si như thế nhìn sang."),t("br"),t("br"),n._v("Cái tên này... Đầu bị man thú đá?"),t("br"),t("br"),n._v("Không phải vậy, làm sao sẽ nói ra như thế khốn nạn mà nói...?"),t("br"),t("br"),n._v("“Xong, lần này... Thật xong...”"),t("br"),t("br"),n._v("Mạc Vũ vỗ trán một cái, khóc không ra nước mắt."),t("br"),t("br"),n._v("Trước còn có thể nói đặc thù bí pháp, cần Đại Dược Vương hôn mê, tùy tiện qua loa lấy lệ, này vừa nói, bằng triệt để đắc tội đối phương, không thể quay lại chỗ trống!"),t("br"),t("br"),n._v("Ngươi muốn chết sẽ chết, chớ liên lụy ta a..."),t("br"),t("br"),n._v("Ta chính là muốn mở mang kiến thức một chút, chiêu ai trêu chọc ai..."),t("br"),t("br"),n._v("  Quên đi, cùng một chỗ đến, coi như cái tên này tìm đường chết, cũng không thể đem hắn ném!"),t("br"),t("br"),n._v("Trong lòng phiền muộn, cắn răng, Mạc Vũ lặng lẽ truyền âm: “Trương Huyền, ngươi hiện tại thông báo Khiếu Thiên thú lại đây, trong tay ta có phụ vương cho ta lưu lại trận bàn, chỉ cần kích hoạt, lẽ ra có thể kiên trì một quãng thời gian, động tác nhanh lên một chút, không muốn chần chờ, có lẽ còn có một chút hi vọng sống.”"),t("br"),t("br"),n._v("Nơi này tuy là đầm rồng hang hổ, nhưng Khiếu Thiên thú nửa bước Chí Tôn tu vi, hơn nữa có thể phi hành, thật muốn đi qua, dẫn bọn họ đào tẩu vẫn có rất lớn hi vọng."),t("br"),t("br"),n._v("Bất quá, hiện tại quan trọng nhất chính là, làm sao kiên trì Khiếu Thiên thú tìm tới nơi này."),t("br"),t("br"),n._v("Bàn tay nắm trữ vật giới chỉ, Mạc Vũ tinh thần âm thầm cảnh giác, chỉ cần đối phương vừa động thủ, nàng liền sẽ lập tức đem trận bàn lấy ra, ném ra."),t("br"),t("br"),n._v("Làm Công chúa, một người chạy đến rèn luyện, thủ đoạn bảo mệnh vẫn có một ít."),t("br"),t("br"),n._v("Trận bàn, là thông qua phương pháp đặc thù, đem to lớn trận pháp khắc hoạ ở một cái có thể bên người mang theo vật phẩm bên trên, cần dùng lúc, chỉ cần chân khí truyền vào liền có thể, mặc dù không phải trận pháp sư, cũng có thể ung dung kích hoạt."),t("br"),t("br"),n._v("Tuy rằng uy lực so chân chính trận pháp yếu không ít, nhưng có thể trong nháy mắt kích phát, đồng thời thuận tiện mang theo, là không ít người dùng để thủ đoạn bảo mệnh."),t("br"),t("br"),n._v("Chỉ là vật này giá cả không ít, hơn nữa còn là một lần vật phẩm, cũng chỉ có nàng vị này Thiên Vũ vương quốc Công chúa, mới có như thế tiền vốn."),t("br"),t("br"),n._v("“Để nó qua tới làm chi? Không cần sốt sắng!”"),t("br"),t("br"),n._v("Thấy đối phương nói khẩn trương, Trương Huyền cười cợt."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Xem cái tên này đều nguy hiểm như vậy, còn một mặt liều lĩnh dáng vẻ, Mạc Vũ đều sắp tức giận nổ: “Ngươi triệt để đắc tội Đại Dược Vương, không nhanh đi, lẽ nào thật sự muốn chết ở chỗ này!”"),t("br"),t("br"),n._v("“Đắc tội hắn, ta tại nói cho hắn kể ra bệnh tình, làm sao sẽ đắc tội?” Trương Huyền vung vung tay, an ủi một câu: “Ngươi an tâm đợi, không nên suy nghĩ bậy bạ!”"),t("br"),t("br"),n._v("“Giảng giải bệnh tình?”"),t("br"),t("br"),n._v("Mạc Vũ phát rồ."),t("br"),t("br"),n._v("Mắng người ta tự mình làm bậy thì không thể sống được, đây là giảng giải bệnh tình?"),t("br"),t("br"),n._v("Ta sát hạch y sư học đồ lâu như vậy rồi, còn chưa từng nghe qua!"),t("br"),t("br"),n._v("Còn an tâm đợi, tình huống như thế, có thể lưu lại được mới là lạ!"),t("br"),t("br"),n._v("Trong lòng tức giận nghiến răng nghiến lợi, Mạc Vũ cũng biết hiện tại không phải bực bội thời điểm, con mắt nhìn quanh hai bên, lựa chọn tốt nhất đường chạy trốn."),t("br"),t("br"),n._v("Nếu như sớm biết cái tên này như vậy vô căn cứ, liền không với hắn cùng một chỗ mạo hiểm."),t("br"),t("br"),n._v("“Bạch Thiềm y sư, ta đưa ngươi hảo ý mời đi theo, cho lão gia nhà chúng ta chữa bệnh, ngươi lại nói chuyện như vậy. Ngày hôm nay không đem lời nói rõ ràng ra, thật cũng đừng muốn đi!”"),t("br"),t("br"),n._v("Không để ý tới mọi người khiếp sợ, Lộ quản gia cố nén tức giận, quăng một cái ống tay áo, híp mắt lại."),t("br"),t("br"),n._v("Nếu không là đối phương giúp hắn giải quyết mầm họa, chỉ bằng vào câu nói mới vừa rồi kia, cũng đã đao phủ gia thân, tại chỗ băm chết rồi, vậy còn cho cơ hội giải thích."),t("br"),t("br"),n._v("“Không nên gấp gáp, lời nói ta sẽ nói rõ ràng, chỉ là... Nơi này nhiều người như vậy, ngươi thật làm cho ta nói ra?” Đối với sự uy hiếp của hắn, Trương Huyền không cần thiết chút nào, trái lại cười nhạt."),t("br"),t("br"),n._v("“Thiếu giả vờ giả vịt, ngày hôm nay coi như ngươi nói phá thiên đi, cũng chỉ có một con đường chết!”"),t("br"),t("br"),n._v("“Dám khiêu khích Đại Dược Vương uy nghiêm, nhất định phải chết!”"),t("br"),t("br"),n._v("“Lộ quản gia, với hắn phí lời làm cái gì, chỉ cần ngươi ra lệnh một tiếng, chúng ta hiện tại liền đem cái này mạo phạm lão gia người, băm thành tám mảnh!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy hắn chết cũng không hối cải, còn cợt nhả, xung quanh hộ vệ tất cả đều cảm thấy sắp tức giận nổ, đồng thời rống to, từng cái từng cái đằng đằng sát khí."),t("br"),t("br"),n._v("Những hộ vệ này, đều là Thông Huyền cảnh trở lên tu vi, mấy chục nhân khí thế đồng thời toả ra, tập hợp thành một luồng, khác nào ở trong phòng hội tụ chất lỏng sềnh sệch, để người hô hấp không kìm lòng được gấp gáp, lòng bàn tay đổ mồ hôi."),t("br"),t("br"),n._v("Liền ngay cả rất nhiều Tông sư cảnh y sư, cũng sắc mặt trở nên trắng, từng cái từng cái không kìm lòng được lùi về sau."),t("br"),t("br"),n._v("Thông Huyền cảnh tuy rằng cùng Tông sư cảnh cách biệt rất xa, nhưng những hộ vệ này, mỗi ngày cùng nhau, lẫn nhau quen thuộc, tiến thối giống như một thể, lại thêm lên ủng hộ có kịch độc các thủ đoạn, một khi động thủ, coi như Tông sư cường giả, đều rất khó phòng bị."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Thấy những hộ vệ này bất cứ lúc nào cũng sẽ động thủ, Lộ quản gia bàn tay lớn vẫy một cái: “Tất cả dừng tay cho ta!”"),t("br"),t("br"),n._v("Nói xong lại lần nữa nhìn sang: “Bạch y sư, những người này đối với lão gia trung thành tuyệt đối, ngươi mở miệng xông tới, không nói cho rõ ràng, e là cho dù là ta, cũng không khống chế được.”"),t("br"),t("br"),n._v("Trong giọng nói đã mang theo sự uy hiếp mạnh mẽ."),t("br"),t("br"),n._v("Chỉ cần Trương Huyền không nói ra được thích hợp lý do, những hộ vệ này, chỉ sợ cũng thật sẽ xuất thủ."),t("br"),t("br"),n._v("“Nói rõ ràng rất đơn giản, bất quá, tuy rằng các ngươi không ngại, nhưng liên luỵ bí mật, của ta ý kiến, vẫn là không muốn làm nhiều người như vậy, nói ra được!”"),t("br"),t("br"),n._v("Mặc kệ bất cứ lúc nào cũng sẽ động thủ mọi người, Trương Huyền đi tới Đại Dược Vương trước mặt, trong ánh mắt mang theo hờ hững: “Như vậy đi, ta nói ba chữ, nếu như ngươi cảm thấy ta chẩn đoán bệnh đúng, để những người này đi ra ngoài, chúng ta nói tỉ mỉ, nếu như không đúng, đem ta giết chính là!”"),t("br"),t("br"),n._v("Cùng Đại Dược Vương giao lưu một hồi, Lộ quản gia gật đầu: “Được!”"),t("br"),t("br"),n._v("“Đáp ứng là tốt rồi!”"),t("br"),t("br"),n._v("Trương Huyền cười cợt: “Của ta ba chữ rất đơn giản... Khế ước cổ!”"),t("br"),t("br"),n._v("“Khế ước cổ? Cái kia là cái gì?”"),t("br"),t("br"),n._v("Còn tưởng rằng đối phương sẽ nói cái gì, Lộ quản gia hơi nhướng mày, đang suy nghĩ có muốn hay không đem băm chết, liền thấy bất cứ lúc nào cũng sẽ tắt thở Đại Dược Vương sắc mặt đột nhiên biến đổi, vẩn đục muốn chết con ngươi, đột nhiên bắn ra kích động ánh sáng."),t("br"),t("br"),n._v("“Để... Để... Những người khác đều đi ra ngoài...”"),t("br"),t("br"),n._v("Giẫy giụa lay động một chút, hắn thanh âm khàn khàn vang lên."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Thấy vị này Bạch Thiềm chỉ nói ba chữ, liền để uể oải uể oải suy sụp, không nhúc nhích Đại Dược Vương nói ra lời, tất cả mọi người cũng giống như xem quái vật nhìn sang."),t("br"),t("br"),n._v("Mới vừa rồi còn cho rằng hắn chết chắc rồi, ai cũng không nghĩ tới sẽ có loại này chuyển ngoặt."),t("br"),t("br"),n._v("Lẽ nào... Ba chữ này có cái gì đặc thù ma lực?"),t("br"),t("br"),n._v("“Lão gia!”"),t("br"),t("br"),n._v("Thấy lão gia nói chuyện, Lộ quản gia vội vàng đi tới Đại Dược Vương trước mặt, giao lưu một hồi, lúc này mới đứng dậy ngắm nhìn bốn phía."),t("br"),t("br"),n._v("“Chư vị, thực sự thật không tiện, ngày hôm nay chẩn đoán bệnh liền tới đây, kính xin rời đi đi! Các vị thù lao, ta sẽ để người cho các ngươi đưa đi.”"),t("br"),t("br"),n._v("Nói xong không nhịn được nhìn Trương Huyền chớp mắt."),t("br"),t("br"),n._v("Đại Dược Vương hiện tại thân thể quá yếu, không nói ra được nói cái gì, nhưng mà hắn hầu hạ đối phương nhiều năm, coi như chỉ nhìn một cách đơn thuần ánh mắt, cũng có thể hiểu được ý tứ."),t("br"),t("br"),n._v("Lão gia để lộ ra ý tứ rất đơn giản!"),t("br"),t("br"),n._v("Dựa theo vị này Bạch y sư dặn dò làm, chỉ có hắn, mới có thể cứu bản thân!"),t("br"),t("br"),n._v("Một cái trong lòng nói bậy, ăn nói linh tinh gia hỏa, có thể cứu?"),t("br"),t("br"),n._v("Chẳng lẽ, thật cùng đối phương nói như vậy, lão gia là tự mình làm bậy thì không thể sống được?"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("“Đại Dược Vương, Lộ quản gia, chúng ta liền cáo từ!”"),t("br"),t("br"),n._v("Nghe Lộ quản gia hạ lệnh trục khách, mọi người tuy rằng trong lòng nghi hoặc, vẫn như cũ lắc đầu một cái, xoay người rời đi, ra ngoài phía trước nhìn về phía cách đó không xa Bạch Thiềm y sư, tràn đầy mê hoặc."),t("br"),t("br"),n._v("Cái tên này nói rồi ba chữ, liền để Đại Dược Vương đuổi người, chẳng lẽ hắn chính xác tri thức đạo đối phương bị bệnh gì?"),t("br"),t("br"),n._v("Có thể có vẻ như... Cũng không có bệnh gì kêu  a?"),t("br"),t("br"),n._v("“Các ngươi cũng đi xuống đi!”"),t("br"),t("br"),n._v("Không để ý tới mọi người nghi hoặc, Lộ quản gia đối với rất nhiều hộ vệ khoát tay áo một cái."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Biết là lão gia ý tứ, rất nhiều hộ vệ đáp một tiếng, đồng thời lui xuống."),t("br"),t("br"),n._v("Mạc Vũ thấy mọi người rời đi, chần chờ một chút, cuối cùng vẫn là ngừng lại."),t("br"),t("br"),n._v("Mặc kệ cái tên này đến cùng nói rất đúng không đúng, một lúc nữa có thể hay không bị chém chết, nàng nếu cùng đối phương là cùng một chỗ đến, liền tự mình đi rồi."),t("br"),n._v(" Thấy cái này kiêu ngạo Công chúa, nói chuyện không êm tai, người còn rất trượng nghĩa, Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Rất nhanh, trong phòng chỉ còn dư lại mấy người bọn hắn."),t("br"),t("br"),n._v("“Bạch Thiềm y sư, hiện tại đã không ai, là không phải có thể nói rồi?”"),t("br"),t("br"),n._v("Biết Mạc Vũ là cùng vị này Bạch y sư cùng một chỗ, Lộ quản gia không tiếp tục cấm kỵ, cung kính ôm quyền."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gù, không ở che lấp: “Kỳ thực vừa nãy nhìn thấy Đại Dược Vương dáng dấp, trong lòng ta thì có phán đoán. Đi qua bí pháp thôi diễn, đã xác định ra...”"),t("br"),t("br"),n._v("Nói đến đây, trong ánh mắt mang theo thở dài: “Đại Dược Vương cái này xác thực không phải bệnh, cũng không phải trúng độc, mà là một loại kêu  đồ vật! Vật này là cùng người làm giao dịch nào đó lưu lại, hiện tại làm giao dịch người đã chết, chịu đến cổ trùng hạn chế, hắn đương nhiên cũng là sống không lâu!”"),t("br"),t("br"),n._v("“Khế ước cổ... Cái kia là cái gì?” Lộ quản gia vẫn như cũ không hiểu."),t("br"),t("br"),n._v("Coi như là hắn, mỗi ngày cùng y sư, dược liệu tiếp xúc, cũng chưa từng nghe tới danh tự này."),t("br"),t("br"),n._v("Đại Dược Vương rõ ràng biết vật này, chỉ bất quá hắn hiện tại không cách nào nói ra, coi như có thể giao lưu, cũng chỉ là đơn giản câu thông, không thể đem như thế phức tạp đồ vật biểu đạt ra đến."),t("br"),t("br"),n._v("“Khế ước cổ, là vì lấy được một ít người tín nhiệm, cùng đối phương ký kết đặc thù khế ước, thuộc về độc sư một loại thủ đoạn, nói trắng ra cùng thuần thú sư sinh tử khế ước tương tự. Một khi ký kết, chẳng khác nào đã biến thành đối phương trung thực thuộc hạ, một khi chủ nhân tử vong, cái này thuộc hạ, cũng sống không lâu, chẳng mấy chốc sẽ đi theo mà đi.”"),t("br"),t("br"),n._v("“Vật này là độc sư vì khống chế thuộc hạ, cố ý làm ra đến, chỉ cần ký kết, sinh tử không khỏi bản thân khống chế, chỉ cần làm trái chủ nhân ý niệm, sẽ gặp phải vạn cổ xót ruột chi đau!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt nghiêm nghị, chậm rãi nói."),t("br"),t("br"),n._v("“Trên thế giới... Còn có thứ này?”"),t("br"),t("br"),n._v("Lộ quản gia líu lưỡi, tràn đầy không thể tin được."),t("br"),t("br"),n._v("Đối phương không nói, liền hắn đều chưa từng nghe nói."),t("br"),t("br"),n._v("Một bên Mạc Vũ tiểu thư, cũng tú mục trợn tròn."),t("br"),t("br"),n._v("Khế ước cổ, nàng không biết, sinh tử khế ước nhưng là biết đến rất rõ ràng."),t("br"),t("br"),n._v("Loại này khế ước, chỉ cần chủ nhân tử vong, man thú mặc kệ rất mạnh thực lực, ở nơi nào, đều sẽ đồng dạng cắt đứt hô hấp."),t("br"),t("br"),n._v("Nghe ngữ khí, cái này khế ước cổ càng thêm lợi hại, nếu như đúng là như vậy, chẳng phải là đem người xem là man thú đến mệnh lệnh, thuần dưỡng?"),t("br"),t("br"),n._v("Đây cũng quá khủng bố đi!"),t("br"),t("br"),n._v("“Lẽ nào... Cái này khế ước cổ cùng lão gia bệnh có quan hệ?”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, Lộ quản gia sắc mặt trắng bệch."),t("br"),t("br"),n._v("“Đâu chỉ có quan hệ, chính là nguyên nhân này dẫn tới!”"),t("br"),t("br"),n._v("Nhìn về phía trước mắt Đại Dược Vương, Trương Huyền không nhịn được lắc đầu: “Nhà ngươi lão gia, đường đường Đại Dược Vương, Hồng Liên thành chúa tể một phương, một mực muốn làm người khác thuộc hạ, cam tâm dùng loại này độc cổ, không phải tự gây nghiệp chướng, không thể sống là cái gì?”"),t("br"),t("br"),n._v("Hồng Liên thành Đại Dược Vương, không ngừng Võ Vương quốc quốc vương bệ hạ cũng không muốn đơn giản đắc tội, tam tinh y sư đều tự nguyện cúi đầu, địa vị cao, so với một ít nhị đẳng vương quốc quốc vương cũng mạnh hơn mấy lần."),t("br"),t("br"),n._v("Người như thế dĩ nhiên cam tâm dùng khế ước cổ, trở thành người khác thuộc hạ, không phải là mình muốn chết là cái gì?"),t("br"),t("br"),n._v("Xuất hiện ngày hôm nay cục diện này, cũng có thể nói oán không được người khác."),t("br"),t("br"),n._v("Nghe nói như thế, Đại Dược Vương vốn là khô quắt mặt, hơi ửng hồng, trong mắt tràn đầy hối hận."),t("br"),t("br"),n._v("“Kính xin Bạch y sư mau cứu lão gia nhà ta!”"),t("br"),t("br"),n._v("Thế mới biết bản thân lão gia bị bệnh gì, Lộ quản gia thân thể run lên, đầu gối mềm nhũn, ngã quỵ ở mặt đất."),t("br"),t("br"),n._v("Những ngày qua thấy lão gia càng ngày càng yếu, trước sau không nghĩ ra, giờ khắc này rốt cuộc biết nguyên do, cũng không nhịn được nữa."),t("br"),t("br"),n._v("Hắn từ nhỏ tại Đại Dược Vương phủ lớn lên, trung thành không thể nghi ngờ."),t("br"),t("br"),n._v("Lão gia thật muốn chết rồi, chịu đến cái khác Dược Vương công kích, Dược Vương phủ nhất định sẽ suy yếu xuống, làm quản gia, đứng mũi chịu sào, hắn cũng rất khó sống lâu dài."),t("br"),t("br"),n._v("Bất luận về công về tư, đều không thể xảy ra chuyện gì."),t("br"),t("br"),n._v("Trước mắt này Bạch y sư, không những chớp mắt nhìn ra của hắn chứng bệnh, càng nhìn ra lão gia nguyên nhân sinh bệnh, có lẽ thì có phương pháp giải quyết."),t("br"),t("br"),n._v("“Trước đứng lên đi!” Đưa tay đem đối phương nâng dậy, Trương Huyền lắc đầu một cái: “Khế ước cổ, khế ước ký kết mà thành, đã tiến vào huyết dịch cốt tủy, muốn giải quyết, rất khó!”"),t("br"),t("br"),n._v("Ký kết khế ước sau, cổ trùng sẽ tiến vào trong cơ thể, nương theo huyết dịch di động, muốn tìm được, cơ bản không thể."),t("br"),t("br"),n._v("“Chỉ cần Bạch y sư ra tay, cứu trị lão gia nhà chúng ta, ta Đại Dược Vương phủ có thể đáp ứng bất kỳ yêu cầu gì...” Lộ quản gia cắn răng nói."),t("br"),t("br"),n._v("“Muốn cứu hắn, cần phải tìm được cổ trùng vị trí, đem lấy ra đến, hoặc là giết chết!”"),t("br"),t("br"),n._v("Trương Huyền không có đáp ứng, mà là giải thích: “Cổ trùng bản thân là sống, sẽ dọc theo huyết dịch di động, một khi gặp phải nguy hiểm, lập tức chạy trốn, làm sao bắt?”"),t("br"),t("br"),n._v("Khế ước cổ, quan trọng nhất chính là nuôi ở trong người cổ trùng, muốn giải quyết rất đơn giản, chỉ phải bắt được giết chết liền có thể, có thể bắt tay vào làm, thực sự quá khó khăn."),t("br"),t("br"),n._v("Vật này tại trong máu, không nhìn thấy sờ không được, lại sẽ khắp nơi di động, tránh né nguy hiểm, làm sao giết?"),t("br"),t("br"),n._v("E sợ cổ trùng không giết chết, người trước hết chết rồi."),t("br"),t("br"),n._v("“Y sư nếu có thể nhìn ra lão gia nhà ta vấn đề, liền nhất định có biện pháp giải quyết, kính xin ra tay!”"),t("br"),t("br"),n._v("Lộ quản gia lại lần nữa quỳ xuống."),t("br"),t("br"),n._v("Tìm nhiều như vậy y sư, liền chứng bệnh cũng không thấy, vị này Bạch Thiềm đã là bọn họ duy nhất hi vọng."),t("br"),t("br"),n._v("“Ta có thể xuất thủ cứu hắn, bất quá... Ta có một điều kiện!”"),t("br"),t("br"),n._v("Trương Huyền chần chờ một chút, nói."),t("br"),t("br"),n._v("Người khác tìm cổ trùng rất khó, hắn không giống nhau, có Thiên Đạo thư viện, có thể rõ ràng đánh dấu cổ trùng vị trí, thật muốn trị liệu, vẫn là có thể làm được."),t("br"),t("br"),n._v("“Y sư mời nói!”"),t("br"),t("br"),n._v("Nghe được hắn thật sự có thể cứu trị, Lộ quản gia kích động nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Ta muốn đi Độc điện, hi vọng chữa khỏi Đại Dược Vương sau, có thể mang ta tới!”"),t("br"),t("br"),n._v("Trương Huyền nhàn nhạt nhìn sang."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Không nghĩ tới là yêu cầu này, Lộ quản gia biến sắc mặt: “Độc điện hiện tại rất nguy hiểm, không tiếp kiến người ngoài, đặc biệt là y sư, đi qua... Vô cùng nguy hiểm!”"),t("br"),t("br"),n._v("Độc sư cùng y sư, tương đương với đối địch hai cái nghề nghiệp, một mình ngươi y sư muốn đi Độc điện, này không phải muốn chết sao?"),t("br"),t("br"),n._v("“Hiện tại rất nguy hiểm? Có ý gì?”"),t("br"),t("br"),n._v("Nghe ra trong lời nói của đối phương có chuyện, Trương Huyền nghi hoặc nhìn sang."),t("br"),t("br"),n._v("Nhìn lão gia chớp mắt, thấy hắn không phản đối, Lộ quản gia cắn răng một cái: “Độc điện hiện tại xuất hiện nội chiến, triệt để đóng kín, ai cũng không thấy, liền ngay cả lão gia cũng không vào được, hiện tại đi qua... Thực sự không phải thời cơ tốt nhất!”"),t("br"),t("br"),n._v("“Nội chiến?”"),t("br"),t("br"),n._v("“Vâng!” Lộ quản gia nói: “Nếu như y sư thật muốn đi qua, một năm sau nội chiến dẹp loạn lại đi, có lẽ sẽ tốt hơn một chút...”"),t("br"),t("br"),n._v("“Một năm sau? Quá chậm!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn phí hết tâm huyết chuyên môn đến tìm Độc điện chính là vì hiểu rõ quyết trong cơ thể độc khí, một tháng đều không muốn chờ, còn chờ một năm?"),t("br"),t("br"),n._v("Đùa gì thế!"),t("br"),t("br"),n._v("“Chỉ cần đem ta mang tới Độc điện, làm sao đi vào, lúc nào đi, là của ta sự tình, đáp ứng, ta liền xuất thủ cứu trị, không đáp ứng, liền như vậy coi như thôi!”"),t("br"),t("br"),n._v("Trương Huyền lạnh nhạt nói: “Bất quá, ngoại trừ ta, coi như ngươi tìm khắp nơi chung quanh mười ba quốc, e sợ cũng không ai có thể giải quyết!”"),t("br"),t("br"),n._v("Nghe được ngữ khí của hắn kiên quyết, Lộ quản gia nhìn về phía cách đó không xa lão gia, thấy đối phương nháy mắt một cái, lúc này mới nhìn sang."),t("br"),t("br"),n._v("“Tốt, chúng ta lão gia đáp ứng rồi!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền lúc này mới gật gù: “Đem bọn ngươi lão gia để nằm ngang, ta hiện tại liền bắt đầu trị liệu.”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Biết song phương đều là người có thân phận, không thể làm ra quỵt nợ sự tình, Lộ quản gia không nói thêm nữa, đem ghế nằm phóng tới, để lão gia nằm thẳng."),t("br"),t("br"),n._v("Cũng biết lập tức trị cho hắn khế ước cổ, Đại Dược Vương khô quắt trên mặt, cũng lộ ra vẻ sốt sắng."),t("br"),t("br"),n._v("Lộ quản gia, Mạc Vũ chưa từng nghe qua khế ước cổ, biết đến không nhiều, hắn từng trải qua, đương nhiên biết, vật này hầu như khó giải."),t("br"),t("br"),n._v("Người trước mắt này lại còn nói có thể trị liệu, tuy rằng trong lòng có chút không tin, cũng còn muốn thử một chút."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Quyết định trị liệu, Trương Huyền không ở phí lời, xoay cổ tay một cái, một hộp ngân châm xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Tinh thần hơi động, mở ra trong thư viện thư tịch đánh dấu, thiếu hụt vị trí (cũng chính là khế ước cổ cổ trùng vị trí)."),t("br"),t("br"),n._v("Chân khí trong cơ thể vận chuyển, nhẹ nhàng bắn ra, ngân châm thẳng tắp đối với thư tịch ghi chép địa phương, đâm tới."),t("br"),t("br"),n._v("Ẩn chứa hắn Thiên Đạo chân khí ngân châm, chỉ cần đâm trúng cổ trùng, tuyệt đối có thể đem chém giết."),t("br"),t("br"),n._v("“Hả?”"),t("br"),t("br"),n._v("Đâm vào sau đó, chân khí quay một vòng, Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("“Cổ trùng dĩ nhiên đi rồi...”"),t("br"),t("br"),n._v("Thư tịch là vừa nãy hình thành, này cả buổi lại là nói chuyện, lại là quan sát, Đại Dược Vương trong cơ thể cổ trùng lại nhưng đã không ở đánh dấu địa phương."),t("br"),t("br"),n._v("Xem ra còn cần hình thành mới thư tịch mới được."),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nhìn lại, có chút thật không tiện."),t("br"),t("br"),n._v("“Đại Dược Vương, thực sự thật không tiện, ngươi xem... Ngươi mới không tiện, lại hôn mê một hồi?”"),t("br"),t("br"),n._v("(Vì cố sự liên tục, không ngừng mọi người khó chịu, hai chương hợp nhất, tương đương với ngày hôm nay chương 2: Cùng chương 3: Yên tâm, ngày hôm nay còn có chương 4: Thứ hai, hi vọng các vị ném hai tấm phiếu đề cử cùng vé tháng, già nhai bái tạ!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-279"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-281"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);