(window.webpackJsonp=window.webpackJsonp||[]).push([[1501],{1855:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-273-tinh-huong-the-nao-【-hai-hop-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-273-tinh-huong-the-nao-【-hai-hop-nhat-】"}},[n._v("#")]),n._v(" Chương 273: Tình huống thế nào? 【 hai hợp nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Không nghĩ tới tên này như vậy dứt khoát, như vậy quyết đoán, nói ngất liền ngất, không chút nào dây dưa dài dòng, Trương Huyền không còn gì để nói."),t("br"),t("br"),n._v("“Khặc khặc, ý của ta là... Giải quyết là có thể giải quyết, còn cần thêm tiền, ngươi vậy thì hôn mê, ta tìm ai tính sổ?”"),t("br"),t("br"),n._v("Giúp cái tên này giải quyết khế ước cổ, điều kiện trao đổi là dẫn hắn trừ độc điện. Hiện tại đã hoàn thành ước định, đối phương còn muốn trị liệu bị thương thân thể, cũng không thể trắng để cho mình tiêu hao chân khí đi!"),t("br"),t("br"),n._v("Nói còn chưa dứt lời, ngươi liền té xỉu, là không phải là đối ta người y sư này, quá không tôn trọng?"),t("br"),t("br"),n._v("Nghe được của hắn lầm bầm lầu bầu, một bên Lộ quản gia ho khan một tiếng, vội vã đi lên phía trước: “Khặc khặc, lão gia nhà chúng ta cũng là muốn để ngươi nhanh lên một chút trị liệu, Bạch y sư có yêu cầu gì, trực tiếp nói với ta chính là, nhất định sẽ thỏa mãn yêu cầu!”"),t("br"),t("br"),n._v("“Này còn tạm được!”"),t("br"),t("br"),n._v("Trương Huyền gật gù: “Như vậy đi, các ngươi không phải bán dược liệu sao? Cái này  chắc là có không ít đi, chuẩn bị cho ta cái ba phần... Không, đến cái thập phần.”"),t("br"),t("br"),n._v("Nếu như đã sớm chuẩn bị, liền không cần đối với người khác động thủ, đem đập hôn mê."),t("br"),t("br"),n._v("Hiện tại nếu biết, đương nhiên phải nhiều đến vài phần, trước mắt cái này nhà giàu không làm thịt một hồi, giết ai?"),t("br"),t("br"),n._v("“Thập phần Ma Hồn tán?”"),t("br"),t("br"),n._v("Lộ quản gia sững sờ, con mắt chớp chớp."),t("br"),t("br"),n._v("“Đúng đấy, lẽ nào các ngươi đường đường Đại Dược Vương phủ, không bỏ ra nổi đến?” Trương Huyền lông mày vừa nhíu, đồng thời trong lòng lo sợ, có thể hay không một hồi mở miệng muốn quá nhiều, đối phương không bỏ ra nổi đến?"),t("br"),t("br"),n._v("“Chuyện này... Cái này...” Lộ quản gia đầy mặt băn khoăn, giống như táo bón: “Là vật này quá tiện nghi...”"),t("br"),t("br"),n._v("Ma Hồn tán chỉ là y sư đơn giản nhất mốc bào chế, một phần bố trí xuống, bất quá mấy trăm kim tệ, thập phần cộng lại đến vậy bất quá mấy ngàn, chút tiền này là có thể cứu trị lão gia mệnh..."),t("br"),t("br"),n._v("Đường đường Đại Dược Vương mệnh, cũng không tránh khỏi quá giá rẻ đi!"),t("br"),t("br"),n._v("“Tiện nghi?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt một chút, nhìn về phía một bên Mạc Vũ, thấy người sau một mặt không nói gì gật đầu, lúc này mới lúng túng nở nụ cười: “Cái này... Đúng rồi, các ngươi phủ đệ có hay không nửa bước Tông sư cùng Tông sư cảnh tu luyện bí tịch? Mặc kệ cấp bậc, càng nhiều càng tốt, nếu như các ngươi có thể sưu tập hơn một nghìn bản, cũng có thể xem như tiền thù lao.”"),t("br"),t("br"),n._v("Hắn tu vi bây giờ đã đạt đến Thông Huyền cảnh đỉnh phong, muốn đột phá, chỉ có thể sưu tập càng nhiều cấp bậc càng cao hơn bí tịch."),t("br"),t("br"),n._v("Thiên Huyền vương quốc cấp bậc quá thấp, cao nhất cũng là Thông Huyền cảnh, nửa bước Tông sư bí tịch số lượng thực sự quá ít, cái này Hồng Liên thành, là nhất đẳng vương quốc một cái thành thị, lại là thuốc trung tâm giao dịch, có lẽ liền có thể tìm tới đầy đủ cấp bậc công pháp."),t("br"),t("br"),n._v("“Nửa bước Tông sư, Tông sư cảnh bí tịch? Hơn một nghìn bản?”"),t("br"),t("br"),n._v("Không nghĩ tới vị này Bạch Thiềm y sư, muốn đồ vật như vậy quái lạ, Lộ quản gia suy nghĩ một chút, nói: “Chúng ta Đại Dược Vương khống chế Thiên Vũ vương quốc cùng với chung quanh mười hai quốc hết thảy dược liệu, không ít người tu luyện không tiền mua, đành phải dùng độc môn bí tịch trao đổi.”"),t("br"),t("br"),n._v("“Bởi vậy, nhiều năm qua, nửa bước Tông sư bí tịch, đại khái tích lũy mấy trăm bản, bất quá, thật nếu mà muốn, chúng ta Đại Dược Vương phủ ra lệnh một tiếng, trong thời gian ngắn sưu tập hơn một nghìn bản không thành vấn đề. Còn Tông sư cảnh công pháp bí tịch... Không muốn cầu cấp bậc mà nói, sưu tập đại mấy trăm quyển không thành vấn đề, hơn một nghìn bản e sợ có chút khó khăn.”"),t("br"),t("br"),n._v("“Tốt, ta liền muốn vật này!”"),t("br"),t("br"),n._v("Nghe được nửa bước Tông sư bí tịch có thể sưu tập hơn một nghìn bản, Tông sư bí tịch đều có thể sưu tập đại mấy trăm quyển, Trương Huyền ánh mắt sáng lên, liền vội vàng gật đầu."),t("br"),t("br"),n._v("Không hổ là Thiên Vũ vương quốc thành thị, quả nhiên cấp bậc muốn cao rất nhiều."),t("br"),t("br"),n._v("Chỉ cần những bí tịch này sưu tập đúng chỗ, là có thể ung dung đột phá nửa bước Tông sư, thậm chí ngay cả Tông sư cảnh đều có thể đột phá, đến thời điểm, coi như tại Độc điện có nguy hiểm gì, cũng coi như có năng lực ứng phó rồi."),t("br"),t("br"),n._v("“Tốt, ngươi đi sắp xếp đi, ta hiện tại liền cứu trị nhà các ngươi lão gia!”"),t("br"),t("br"),n._v("Có nhiều như vậy bí tịch làm thù lao, Trương Huyền tâm thoả mãn *** thế hệ một câu, ngón tay đè tại Đại Dược Vương kinh mạch, một đạo Thiên Đạo chân khí truyền vào đi vào."),t("br"),t("br"),n._v("Đại Dược Vương như vậy sinh cơ hao tổn, trên thực tế chính là kinh mạch bế tắc, khí không thông suốt, chỉ cần đem những cái này bế tắc chỗ dọn dẹp sạch sẽ, Tông sư cảnh hùng hậu chân khí, tự nhiên sẽ một lần nữa ôn dưỡng tinh, khí, thần, để hắn khôi phục khỏe mạnh."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Tinh khiết chân khí, dọc theo kinh mạch bế tắc đột nhiên vọt một cái, trước ràng buộc nhao nhao phá tan, khế ước cổ lưu lại di chứng về sau, khác nào tuyết trắng gặp phải nắng gắt, lại không có bất luận cái gì trốn chỗ trống."),t("br"),t("br"),n._v("Trong cơ thể mầm họa giải trừ, bên ngoài Đại Dược Vương khô quắt, nhão da thịt, lại lần nữa khôi phục co dãn cùng sức sống, hình như trong nháy mắt trẻ lại mấy chục tuổi."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Đứng ở một bên Mạc Vũ, liên tục chớp con mắt."),t("br"),t("br"),n._v("Trước nàng cho rằng Trương Huyền cho đối phương trị liệu, cũng cùng cái khác y sư như thế, cần mỗi bên loại dược liệu phối hợp, cần tháng ngày tích lũy thời gian chồng chất, làm sao đều không nghĩ tới, bắt lấy đối phương cổ tay, liền trực tiếp khôi phục..."),t("br"),t("br"),n._v("Đây thực sự là y sư thủ đoạn?"),t("br"),t("br"),n._v("Làm sao chưa từng nghe nói?"),t("br"),t("br"),n._v("Cùng cái tên này lưu lại không mấy ngày, trước đây chỗ nhận thức vô số sự tình, đều bị lật đổ, thế giới quan đều bị hoàn toàn lật đổ."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Chân khí rất nhanh tại đối phương trong cơ thể chảy xuôi một lần, toàn thân bế tắc chỗ đều bị dọn dẹp sạch sẽ, Trương Huyền đứng dậy, thở ra một hơi."),t("br"),t("br"),n._v("So với phía trước tính toán còn muốn đơn giản một ít."),t("br"),t("br"),n._v("Giải quyết mầm họa, Đại Dược Vương cũng chậm rãi tỉnh lại, nhìn thấy đã khôi phục mềm mại da dẻ, cùng toàn thân dâng trào sức mạnh, tuy rằng nội tâm cảm khái, nhưng không có Mạc Vũ khiếp sợ."),t("br"),t("br"),n._v("Trước mắt vị này, liền vô số y sư, độc sư đều không thể làm gì khế ước cổ, đều có thể trị hết, khôi phục tổn thương loại chuyện nhỏ này, không trị hết mới thật kêu quái đản."),t("br"),t("br"),n._v("“Đa tạ Bạch Thiềm y sư!”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Đại Dược Vương lại lần nữa ngã quỵ ở mặt đất."),t("br"),t("br"),n._v("Nếu như không phải trước mắt vị này, e sợ hiện tại đã chết rồi."),t("br"),t("br"),n._v("Loại này ân tình, đã không phải tiền tài có thể cân nhắc."),t("br"),t("br"),n._v("“Giao dịch mà thôi! Ta chỉ là kỳ quái, ngươi thứ địa vị này, tại sao phải ký kết cái này khế ước cổ, để cho mình thụ người chế trụ?”"),t("br"),t("br"),n._v("Trương Huyền vung vung tay, nghi hoặc nhìn sang."),t("br"),t("br"),n._v("Liền ngay cả Mạc Vũ cũng nghĩ không thông."),t("br"),t("br"),n._v("Đại Dược Vương tại Hồng Liên thành, liền ngay cả Thiên Vũ vương thất cũng không dám xuất binh thảo phạt, đủ thấy đáng sợ, người như thế lại có thể ký kết khế ước cổ, không phải tận mắt nhìn thấy, nghĩ cũng không dám nghĩ tới."),t("br"),t("br"),n._v("“Này, chuyện này nói rất dài dòng, nếu như các ngươi biết, để ta ký kết khế ước chính là ai, liền sẽ không như thế nghĩ đến!”"),t("br"),t("br"),n._v("Nhìn ra hai trong mắt người nghi hoặc, Đại Dược Vương cười khổ."),t("br"),t("br"),n._v("Đối phương cứu tính mạng của hắn, trải qua sinh tử, cũng biết không sao không được nói, không có ý định ẩn giấu."),t("br"),t("br"),n._v("“Cùng ta ký kết khế ước chính là... Độc điện Điện chủ!”"),t("br"),t("br"),n._v("“Độc điện Điện chủ?”"),t("br"),t("br"),n._v("“Ừm!” Đại Dược Vương gật gù, thần sắc phức tạp: “Đại Dược Vương xem ra uy vũ, mạnh mẽ, tại Hồng Liên thành không ai dám trêu chọc, trên thực tế, chỉ là Độc điện phát ngôn viên thôi! Không những là ta, chúng ta mỗi một đời Đại Dược Vương, đều muốn cùng Độc điện Điện chủ ký kết khế ước, trở thành bọn họ thả ở bên ngoài con rối.”"),t("br"),t("br"),n._v("“Mỗi một đời?”"),t("br"),t("br"),n._v("Trương Huyền, Mạc Vũ hai người tặc lưỡi, lập tức cũng là bừng tỉnh."),t("br"),t("br"),n._v("Hồng Liên thành là tiến vào Độc điện duy nhất môn hộ, nếu như sau lưng không có Độc điện giúp đỡ, làm sao có khả năng từng đời một làm Đại Dược Vương, khống chế lợi nhuận khổng lồ như thế dược liệu thị trường? Lại làm sao có khả năng đối mặt vương quốc đều không uý kỵ tí nào, liền vương tử, Công chúa cũng dám chém giết?"),t("br"),t("br"),n._v("Cũng chỉ có Độc điện loại này quỷ dị khó lường thế lực ở sau lưng giúp đỡ, mới có như thế nội tình."),t("br"),t("br"),n._v("Không phải vậy, một cái suy yếu cổ xưa thành thị mà thôi, coi như khống chế thuốc lưu thông, cũng khẳng định không dám cùng Thiên Vũ như vậy đại quốc đối kháng!"),t("br"),t("br"),n._v("Thiên Vũ vương quốc thế nhưng có nhị tinh danh sư, thậm chí còn vượt xa quá một vị."),t("br"),t("br"),n._v("Có loại này danh sư giáo hóa, tuy không phải người người Như Long, nhưng toàn thể tu vi tất nhiên vượt qua Thiên Huyền vương quốc quá nhiều, đại quân vừa ra, trời long đất lở, bài sơn đảo hải. Nếu không có Độc điện ở sau lưng giúp đỡ, một cái nho nhỏ Hồng Liên thành, làm sao có khả năng biến thành hiện tại loại này pháp bên ngoài chi địa?"),t("br"),t("br"),n._v("Phỏng chừng sớm đã bị san bằng."),t("br"),t("br"),n._v("“Khế ước cổ chỉ có triển khai này cổ chủ nhân tử vong, mới sẽ phát tác, lẽ nào...”"),t("br"),t("br"),n._v("Trương Huyền nghĩ đến cái gì, không nhịn được nhìn sang."),t("br"),t("br"),n._v("“Không sai!” Đại Dược Vương gật đầu: “Khóa này Điện chủ chết rồi!”"),t("br"),t("br"),n._v("“Trước Lộ quản gia đã nói rồi, Độc điện gần nhất nội chiến, rất nguy hiểm, không tiếp kiến người ngoài, cũng không phải là chuyện giật gân! Khóa này Độc điện Điện chủ đột nhiên chết bất đắc kỳ tử, của hắn ba cái truyền nhân đều muốn tranh đoạt Điện chủ vị trí, lúc này mới gợi ra hỗn loạn. Coi như là ta, cũng không dám đơn giản đi qua, bằng không, chết cũng không biết chết như thế nào.”"),t("br"),t("br"),n._v("Đại Dược Vương cười khổ: “Đối với bọn họ tới nói, ta chính là cái con rối mà thôi, chỉ cần Độc điện tồn tại, sau đó có thể một lần nữa lập một cái mới.”"),t("br"),t("br"),n._v("Tuy không muốn thừa nhận, Trương Huyền cũng biết đây là sự thực."),t("br"),t("br"),n._v("Chỉ cần có Độc điện ở sau lưng giúp đỡ, phù trợ ai làm Đại Dược Vương, còn không phải chuyện một câu nói?"),t("br"),t("br"),n._v("Người ngoài trước mặt uy phong lẫm lẫm Đại Dược Vương, chỉ là có cũng được mà không có cũng được con rối, không phải chính tai nghe, đều khó có thể tin tưởng được."),t("br"),t("br"),n._v("Đại Dược Vương nhìn về phía Trương Huyền, trong mắt mang theo nghiêm nghị: “Bạch Thiềm y sư, ngươi cứu ta, làm điều kiện trao đổi, dẫn ngươi đi Độc điện là ta chuyện nên làm. Bất quá... Hiện tại ba đồ tranh vị, toàn bộ Độc điện khắp nơi hung hiểm, ai đi qua đều là một con đường chết, nếu như ngươi có thể chờ đợi, không ngại liền ở chỗ này của ta ở lại, ta cũng bất cứ lúc nào cho ngươi tìm hiểu tin tức. Một khi xác lập mới Điện chủ, chúng ta sẽ đi qua, có thể miễn trừ không ít nguy hiểm.”"),t("br"),t("br"),n._v("Độc điện tranh cướp, cùng vương tử đoạt vị không thập khác nhau, đầy rẫy âm mưu cùng mưu sát."),t("br"),t("br"),n._v("Người ngoài đi vào, làm không cẩn thận sẽ bị xem là quân cờ, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("Có thể nói, hiện tại Độc điện, tuyệt đối là nhất náo loạn, nguy hiểm nhất."),t("br"),t("br"),n._v("Bất quá, một khi Điện chủ xác định, cần lôi kéo người tâm, chèn ép người thất bại, ngược lại sẽ ổn định cục diện, an toàn không ít."),t("br"),t("br"),n._v("“Ta không chờ được nữa!”"),t("br"),t("br"),n._v("Biết đối phương nói rất chính xác, không có lừa dối ý tứ, nhưng Trương Huyền vẫn là không kịp đợi."),t("br"),t("br"),n._v("Độc trong người khí tuy rằng bị Thiên Đạo chân khí áp chế, nhưng ai cũng không dám bảo đảm khi nào sẽ phản phệ."),t("br"),t("br"),n._v("Để bọn hắn ba ngày, năm ngày, mười ngày nửa tháng cũng có thể, các cái một hai năm, khẳng định kiên trì không tới."),t("br"),t("br"),n._v("“Kỳ thực... Bạch y sư nếu như thật sốt ruột, cũng không phải là không thể được.”"),t("br"),t("br"),n._v("Thấy vị này ân nhân cứu mạng có chút nóng nảy, muốn phải nhanh một chút trừ độc điện, Đại Dược Vương trầm tư một chút, nói: “Lão Điện chủ ba đại đệ tử, đều khống chế Độc điện một phần giúp đỡ, thế lực ngang nhau, dựa theo tình huống bình thường, không có cái một hai năm, xác thực rất khó phán quyết xuống, bất quá, cũng không nhất định!”"),t("br"),t("br"),n._v("“Nói nghe một chút!” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đại Dược Vương nói: “Độc điện cùng luyện đan sư công hội, luyện khí sư công hội như thế, cũng có tổng bộ cùng rất nhiều phân bộ. Chúng ta Hồng Liên sơn mạch cái này, chỉ là một người trong đó nho nhỏ phân bộ thôi!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Thông qua quan sát các loại thư tịch, hắn cũng biết, hiện tại vị trí, chỉ là thế giới này muối bỏ biển, căn bản không tính là gì."),t("br"),t("br"),n._v("Hồng Liên sơn mạch Độc điện, tiếng tăm tuy rằng rất lớn, cũng chỉ ở Thiên Vũ vương quốc phạm vi quản hạt, mới lộ ra lợi hại."),t("br"),t("br"),n._v("Đến càng cao hơn quốc gia, đương nhiên có càng lợi hại phân bộ tồn tại."),t("br"),t("br"),n._v("“Theo ta được biết, lão Điện chủ đột nhiên chết bất đắc kỳ tử, không đem vị trí truyền cho bất luận người nào, điện bên trong tất cả trưởng lão, đã đem tin tức này phái người đưa đi tổng bộ! Nếu như tổng bộ đồng ý quản lý, phái đặc sứ lại đây, bình định nội loạn, xác định Điện chủ, nên đơn giản không ít, thời gian cũng sẽ rút ngắn!”"),t("br"),t("br"),n._v("Đại Dược Vương đạo."),t("br"),t("br"),n._v("“Ồ? Còn có việc này?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Chỉ cần là phân bộ, liền muốn nghe theo tổng bộ sắp xếp."),t("br"),t("br"),n._v("Phân bộ Điện chủ tử vong, tổng bộ phái người lại đây, coi như lại loạn, cũng nhất định có thể trong thời gian ngắn giải quyết."),t("br"),t("br"),n._v("“Giống như tình huống như thế, Độc điện tổng bộ sẽ thời gian bao lâu phái người lại đây? Lại bao lâu có thể bình định nội chiến? Trước đây có hay không qua tiền lệ?”"),t("br"),t("br"),n._v("Trương Huyền không nhịn được hỏi."),t("br"),t("br"),n._v("“Căn cứ Độc điện dĩ vãng lịch sử, xác thực từng có tiền lệ, bất quá...”"),t("br"),t("br"),n._v("Đại Dược Vương chần chờ một chút, đầy mặt cười khổ: “Thời gian này, vẫn đúng là không thể xác định. Hồng Liên sơn mạch Độc điện, nghe tới uy phong, trên thực tế đối với tổng bộ tới nói, chỉ là cái xó xỉnh, không được coi trọng địa phương. Thật phái đặc sứ lại đây, dùng không được nửa tháng liền có thể triệt để bình định, mà... Đối phương nếu như cảm thấy không sao cả, không phái người lại đây, sẽ mặc cho phát triển, không có một hai năm giải quyết không được!”"),t("br"),t("br"),n._v("Tuy rằng phái cái đặc sứ liền có thể giải quyết, nhưng đối với Độc điện tới nói, một cái có cũng được mà không có cũng được địa phương nhỏ, không cần thiết lãng phí tinh lực, không phái cũng rất bình thường."),t("br"),t("br"),n._v("Trước đây cũng từng có tình huống như thế."),t("br"),t("br"),n._v("Thật muốn như vậy, thì có chút nói linh tinh."),t("br"),t("br"),n._v("Vì lẽ đó, coi như là hắn, cũng không cách nào nội chiến thời gian."),t("br"),n._v("“Không cách nào xác định, cũng không thể vẫn chờ xem!”"),t("br"),t("br"),n._v("Nghe xong giải thích, Trương Huyền càng thêm phiền muộn."),t("br"),t("br"),n._v("Hắn muốn tìm đầy đủ độc sư thư tịch học tập, giải quyết trong cơ thể độc khí mầm họa, luôn không khả năng vẫn các một cái tin tức không chắc chắn, chờ thêm một, hai năm đi!"),t("br"),t("br"),n._v("“Biện pháp tốt nhất là vẫn chờ, bất quá, Bạch y sư nếu như thật muốn đi qua, ta có thể hiện tại dẫn ngươi đi, chỉ là... Sẽ xuất hiện chuyện gì, ta cũng không cách nào bảo đảm!”"),t("br"),t("br"),n._v("Đại Dược Vương chần chờ một chút, đạo."),t("br"),t("br"),n._v("“Ta suy tính một chút!”"),t("br"),t("br"),n._v("Hắn trừ độc điện chỉ là vì đọc sách, chỉ cần không tiếp xúc bọn họ tranh cướp Điện chủ vị trí, sẽ không có tưởng tượng nguy hiểm."),t("br"),t("br"),n._v("Đương nhiên, hắn đối với độc sư cũng không biết, có hay không tình huống như thế, cũng khó nói."),t("br"),t("br"),n._v("Chỉ có thể đi một bước xem một bước."),t("br"),t("br"),n._v("“Chờ ngươi sưu tập hết thư tịch, chúng ta liền xuất phát!”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền xác định được."),t("br"),t("br"),n._v("Lao lực thiên tân vạn khổ đi tới Hồng Liên thành, không thể vẫn chờ đợi, đã như vậy, nên đến liền đến, bất kể hắn là cái gì nội chiến."),t("br"),t("br"),n._v("Bất quá, trước đó, thực lực có thể tăng lên một điểm là một chút."),t("br"),t("br"),n._v("Chỉ cần đạt đến Tông sư đỉnh phong, dựa vào Thiên Đạo thư viện cùng tinh khiết Thiên Đạo chân khí, nửa bước Chí Tôn đều có thể một trận chiến, coi như Độc điện người muốn giết hắn, e sợ cũng rất khó làm được."),t("br"),t("br"),n._v("“Sưu tập thư tịch?”"),t("br"),t("br"),n._v("Đại Dược Vương hơi nghi hoặc một chút."),t("br"),t("br"),n._v("Mới vừa rồi cùng Lộ quản gia bàn điều kiện thời điểm, hắn đã rơi vào hôn mê, không hề biết chuyện."),t("br"),t("br"),n._v("“Là như vậy...” Trương Huyền giải thích một hồi."),t("br"),t("br"),n._v("“Nguyên lai Bạch y sư muốn những sách này tịch, cái này đơn giản, ta tự mình đi giúp ngươi đốc xúc, tốc độ có thể mau một chút, nhiều nhất ba ngày, liền có thể đem toàn bộ Hồng Liên thành hết thảy nửa bước Tông sư, Tông sư cảnh thư tịch sưu tập lại đây!”"),t("br"),t("br"),n._v("Nghe được vị này lợi hại Bạch Thiềm y sư, yêu cầu thù lao dĩ nhiên là cái này, Đại Dược Vương cũng là sững sờ, lập tức cười cợt, trong mắt lộ ra nồng đậm tự tin."),t("br"),t("br"),n._v("Hồng Liên thành đi qua hắn nhiều năm kinh doanh, đã sớm thùng sắt một khối, những sách này tịch tuy rằng quý giá, nhưng chỉ cần hắn tự thân xuất mã, còn là vô cùng đơn giản."),t("br"),t("br"),n._v("“Vậy thì tốt, xin nhờ Đại Dược Vương!”"),t("br"),t("br"),n._v("Nghe được ba ngày liền có thể, Trương Huyền đưa khẩu khí, gật gù."),t("br"),t("br"),n._v("“Kính xin Bạch y sư tạm thời ở nơi này, ta vậy thì đi đốc xúc một hồi.”"),t("br"),t("br"),n._v("Đại Dược Vương đứng dậy, hô một tên hộ vệ, sắp xếp hai người ở lại, vội vội vàng vàng đi ra ngoài."),t("br"),t("br"),n._v("Ân nhân cứu mạng yêu cầu, hắn có thể không dám thất lễ."),t("br"),t("br"),n._v("“Trước ở lại đi!”"),t("br"),t("br"),n._v("Trương Huyền biết sốt ruột cũng vô dụng, mang theo Mạc Vũ, an tâm được xuống."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Các ngươi nói tên kia đến cùng chẩn đoán bệnh đúng rồi, vẫn là sai rồi?”"),t("br"),t("br"),n._v("Đại Dược Vương phủ đệ bên ngoài, mới vừa mới rời khỏi rất nhiều y sư, chất thành một đống, từng cái từng cái cau mày."),t("br"),t("br"),n._v("Theo phủ đệ đi ra, bọn họ vẫn chưa rời đi, mà là canh giữ ở nơi này."),t("br"),t("br"),n._v("Đường đường nhị tinh, tam tinh y sư, danh chấn vương quốc tồn tại, lại có thể bị một cái tên chưa từng có ai biết đến nhất tinh y sư đoạt danh tiếng, đổi làm ai, đều sẽ buồn bực không thôi."),t("br"),t("br"),n._v("“Một cái nhất tinh y sư, có thể chẩn đoán bệnh đúng? Đùa gì thế, phỏng chừng là ăn nói bừa bãi!”"),t("br"),t("br"),n._v("“Này có thể không nhất định, một câu nói để Đại Dược Vương có phản ứng lớn như vậy, nếu như không phải nói đúng rồi, làm sao có khả năng?”"),t("br"),t("br"),n._v("“Thật nói đúng, cái kia  là món đồ gì? Ta trước làm sao chưa từng nghe qua?”"),t("br"),t("br"),n._v("Càng thảo luận, mọi người càng mê hoặc."),t("br"),t("br"),n._v("Nếu như nói cái tên này là nói bậy đi, không thể để Đại Dược Vương phản ứng như thế, thậm chí không ngại đem bọn họ đều đuổi đi."),t("br"),t("br"),n._v("Nếu như thật mà nói, khế ước cổ lại là cái gì? Làm sao trước chưa từng nghe qua?"),t("br"),t("br"),n._v("Trên thế giới còn có loại bệnh này?"),t("br"),t("br"),n._v("Hơn nữa... Nhất tinh y sư, làm sao có khả năng so với bọn họ còn lợi hại hơn, làm thế nào nhìn ra được đến?"),t("br"),t("br"),n._v("“Cái này Bạch Thiềm, ta cùng hắn ở chung không biết bao nhiêu năm, khi nào có bản lĩnh như thế này!”"),t("br"),t("br"),n._v("Trong đám người Thành Phong y sư phiền muộn muốn muốn điên."),t("br"),t("br"),n._v("Hắn cùng Bạch Thiềm y sư quen thuộc nhất, vốn là trào phúng đối phương một hồi, để hắn xấu mặt, nằm mơ đều không nghĩ tới, người ta thật xem xảy ra vấn đề, mạnh mẽ làm mất mặt."),t("br"),t("br"),n._v("“Đừng băn khoăn, đang không chính xác, ở chỗ này chờ, chắc là rất nhanh sẽ có kết quả!”"),t("br"),t("br"),n._v("“Nếu như hắn thật chẩn đoán được Đại Dược Vương chứng bệnh, có thể nghênh ngang đi ra, bằng không, chỉ có thể biến thành thi thể!”"),t("br"),t("br"),n._v("Mộc Hoành các hai vị lợi hại nhất tam tinh y sư đánh gãy mọi người nghị luận."),t("br"),t("br"),n._v("“Đúng đấy!”"),t("br"),t("br"),n._v("Mọi người đồng thời gật đầu."),t("br"),t("br"),n._v("Cái tên này không biết trời cao đất rộng, một quyền đánh ngất Đại Dược Vương, đã phạm vào kiêng kỵ, có thể trị hết chứng bệnh còn nói được, không trị hết, nhất định sẽ bị giết chết."),t("br"),t("br"),n._v("Biết điểm ấy, mọi người không tiếp tục băn khoăn, chỉ cần cái tên này là dựng thẳng đi ra, liền cho thấy thật nhìn ra bệnh. Nằm ngang đi ra, liền nói rõ ăn nói bừa bãi, trang bức tìm đường chết."),t("br"),t("br"),n._v("“Không cần nghĩ, khẳng định là nằm ngang đi ra...”"),t("br"),t("br"),n._v("Thành Phong y sư hàm răng cắn chặt, bất quá lời nói còn tịch thu hết, liền nghe đến phía trước “Kẹt kẹt!” Một tiếng, phủ đệ đại môn một hồi mở ra."),t("br"),t("br"),n._v("“Mau nhìn, có người đi ra...”"),t("br"),t("br"),n._v("Nghe được âm thanh, rất nhiều y sư, đồng loạt nhìn sang, chỉ liếc mắt nhìn, tất cả đều con mắt một hồi trợn tròn, không tại chỗ ngất đi."),t("br"),t("br"),n._v("Đi ra chính là một ông già, Lộ quản gia theo sau lưng, một mặt cung kính."),t("br"),t("br"),n._v("“Đây là... Đại Dược Vương?”"),t("br"),t("br"),n._v("Thấy rõ mặt của đối phương tướng mạo, hết thảy y sư, đồng thời nuốt ngụm nước bọt."),t("br"),t("br"),n._v("Mới vừa cho Đại Dược Vương trị liệu thời gian dài như vậy, làm sao có khả năng không quen biết!"),t("br"),t("br"),n._v("Tuy rằng cái này so với phía trước cái kia trẻ lại không ít, nhưng có thể nhường đường quản gia cung kính như thế, ngoại trừ hắn còn ai vào đây?"),t("br"),t("br"),n._v("Mới vừa rồi còn nằm tại trên ghế nằm, không nhúc nhích, khác nào sắp chết rồi... Không tới hai mười phút, nghênh ngang đi ra, khí tức mười phần, trẻ lại mấy chục tuổi..."),t("br"),t("br"),n._v("Ai có thể nói cho ta phát sinh cái gì?"),t("br"),t("br"),n._v("Coi như thật nhìn ra chứng bệnh, trị liệu cũng cần tiêu tốn thời gian nhất định chứ?"),t("br"),t("br"),n._v("Hai mươi phút chữa khỏi... Để hắn tự mình đi ra."),t("br"),t("br"),n._v("Mẹ nó, thật sự không nhìn lầm?"),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Thành Phong y sư càng là thân thể loáng một cái, suýt chút nữa thổ huyết."),t("br"),t("br"),n._v("Mới vừa nói xong đối phương khẳng định nằm đi ra, kết quả... Đại Dược Vương đứng yên đi ra."),t("br"),t("br"),n._v("Ai có thể nói cho ta, xảy ra chuyện gì?"),t("br"),t("br"),n._v("“Chư vị y sư còn chưa đi?”"),t("br"),t("br"),n._v("Đi ra viện, nhìn thấy mọi người dĩ nhiên không rời đi, Đại Dược Vương cười cợt."),t("br"),t("br"),n._v("“Đại Dược Vương, ngươi đây là...”"),t("br"),t("br"),n._v("Mộc Hoành y sư cũng không nhịn được nữa, đi lên phía trước, đầy mặt kỳ quái."),t("br"),t("br"),n._v("“Là Bạch Thiềm y sư giúp ta chữa khỏi chứng bệnh, y thuật của hắn thật cao!”"),t("br"),t("br"),n._v("Đại Dược Vương trong mắt lộ ra sùng bái tâm ý, cảm khái một câu, liền ôm quyền: “Được rồi, chư vị, ta còn có việc, liền không làm lỡ, cáo từ!”"),t("br"),t("br"),n._v("Nói xong nhanh chân rời đi."),t("br"),t("br"),n._v("“Thực sự là hắn chữa khỏi?”"),t("br"),t("br"),n._v("“Ngăn ngắn hai mười phút, để một cái xem ra hơn chín mươi tuổi người, biến thành không đủ năm mươi tuổi?”"),t("br"),t("br"),n._v("Tuy rằng sớm có suy đoán, nhưng nghe đến hắn chính miệng xác nhận, mọi người vẫn cảm thấy choáng váng đầu hoa mắt, đầu choáng váng."),t("br"),t("br"),n._v("Bọn họ nhiều người như vậy, nghiên cứu rất lâu đều nghiên cứu không hiểu chứng bệnh, đối phương như thế trong thời gian ngắn chữa khỏi... Đây là cái gì trình độ?"),t("br"),t("br"),n._v("Thực sự là nhất tinh y sư?"),t("br"),t("br"),n._v("“Nhìn tới... Chúng ta đều nhìn sai, vị này Bạch Thiềm y sư, e sợ đã sớm không phải nhất tinh y sư!”"),t("br"),t("br"),n._v("Nặng nề cả buổi, một vị tam tinh y sư, không nhịn được nói."),t("br"),t("br"),n._v("Huy chương không có nghĩa là thực lực."),t("br"),t("br"),n._v("“Đúng đấy, nhất tinh y sư làm sao có khả năng đơn giản chữa khỏi Đại Dược Vương... Loại năng lực này, sợ là đạt đến tứ tinh!” Người thứ hai tam tinh y sư cũng nói."),t("br"),t("br"),n._v("Tuy không muốn thừa nhận, Mộc Hoành y sư cũng gật gật đầu."),t("br"),t("br"),n._v("“Tứ tinh y sư... Chờ hắn đi ra, ta nhất định phải hảo hảo bái kiến, bù đắp trước đường đột!”"),t("br"),t("br"),n._v("“Đúng đấy, ta cũng phải cố gắng thỉnh giáo...”"),t("br"),t("br"),n._v("Nghe được ba vị tam tinh y sư như vậy phán đoán, những người khác sắc mặt đồng thời ngưng lại."),t("br"),t("br"),n._v("Trước tại đại điện thời điểm, bọn họ không ít đối với vị này Bạch y sư tiến hành cười nhạo."),t("br"),t("br"),n._v("Hiện tại biết hắn khả năng là một vị đạt đến tứ tinh y sư siêu cường tồn tại, đều cảm thấy hoảng hốt."),t("br"),t("br"),n._v("“Đáng ghét, đừng cho ta thấy ngươi, không phải vậy, không đánh chết ngươi không được...”"),t("br"),t("br"),n._v("Mọi người đang tại ngầm hạ quyết định, liền nghe đến một tiếng phẫn nộ gào thét, đồng thời xoay mặt nhìn lại, liền thấy một cái chỉ liền áo khoác cũng không mặc gia hỏa, một mặt lửa giận đứng ở cách đó không xa, bàn tay lớn vung vẩy."),t("br"),t("br"),n._v("“Bạch Thiềm y sư?”"),t("br"),t("br"),n._v("Nhìn rõ ràng người này dáng dấp, hết thảy y sư tất cả đều sững sờ, lập tức hai mắt tỏa ánh sáng, vọt tới."),t("br"),t("br"),n._v("Thương xót Bạch y sư theo hôn mê tỉnh lại, phát giác không những y sư huy chương bị cướp, áo khoác cũng bị bới, tức giận oa oa kêu loạn, đang muốn tìm kiếm “Hung thủ” liền nhìn thấy một đám nhị tinh, tam tinh y sư giống nhìn thấy thần tượng giống như xông lại."),t("br"),t("br"),n._v("Sợ đến hoa cúc siết, toàn thân mềm nhũn..."),t("br"),t("br"),n._v("Những thứ này, bình thường nhìn thấy, đều là cao cao tại thượng, xem thường, làm sao ta ngất một hồi, đều cùng nhìn thấy tiền bối giống như?"),t("br"),t("br"),n._v("Ai có thể nói cho ta..."),t("br"),t("br"),n._v("Này mẹ nó là tình huống thế nào?"),t("br"),t("br"),n._v("(Lại là hai hợp một chương tiết, buổi chiều còn có chương mới! Mọi người không cần lo lắng lão nhai uể oải. Nếu như có vé tháng, mời đưa tới, để lão nhai tiếp tục kiên trì. Đồng thời chúc mọi người lễ tình nhân hạnh phúc!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-281"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-283"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);