(window.webpackJsonp=window.webpackJsonp||[]).push([[1502],{1856:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-274-nua-buoc-tong-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-274-nua-buoc-tong-su"}},[n._v("#")]),n._v(" Chương 274: Nửa bước Tông sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thành Phong y sư, đây là... Xảy ra chuyện gì?”"),t("br"),t("br"),n._v("Thật vất vả tỉnh táo lại, nhìn thấy một người quen cũ, Bạch Thiềm y sư vội vàng hỏi."),t("br"),t("br"),n._v("“Bạch y sư, vừa nãy là ta có mắt mà không thấy núi thái sơn, không biết mấy năm không thấy, y thuật của ngươi đã tăng trưởng đến trình độ loại này, có bao nhiêu mạo phạm, vẫn xin xem xét!”"),t("br"),t("br"),n._v("Bị điểm tên Thành Phong y sư hơi đỏ mặt, vội vã ôm quyền."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Bạch Thiềm y sư trên đầu bốc lên dấu chấm hỏi."),t("br"),t("br"),n._v("Đám người kia, đều uống nhầm thuốc hay là làm sao?"),t("br"),t("br"),n._v("Ta liền bất tỉnh một hồi, làm sao thái độ của bọn họ đều như vậy?"),t("br"),t("br"),n._v("Nghi hoặc một hồi, vị này một mặt mộng bức Bạch y sư, cũng coi như hiểu được, cướp giật hắn huy chương gia hỏa, hình như giả mạo hắn chữa khỏi Đại Dược Vương bệnh!"),t("br"),t("br"),n._v("Này đánh ngất bản thân, không đoạt tiền, không cướp đồ vật, còn mua cho mình mấy vạn kim tệ dược liệu... Chỉ vì giúp người khác chữa bệnh?"),t("br"),t("br"),n._v("Làm sao cảm giác như thế vô căn cứ đâu?"),t("br"),t("br"),n._v("Trên đời này vẫn còn có chuyện tốt như thế..."),t("br"),t("br"),n._v("Thật như vậy, có phải là muốn suy tính một chút, lại đi không ai ngõ nhỏ chờ?"),t("br"),t("br"),n._v("Muốn không van cầu ông trời, thương xót thương xót, lại cho ta ban tặng hắn một hai cướp phỉ..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên ngoài Bạch Thiềm y sư thành khẩn tâm nguyện, Trương Huyền cũng không rõ ràng, ba ngày nay, vẫn lưu lại tại Đại Dược Vương phủ, ngoại trừ lật xem trong thư viện thư tịch, nhanh chóng học tập, chính là chỉ điểm Mạc Vũ."),t("br"),t("br"),n._v("Đây là trước đáp ứng đối phương, bây giờ tìm đến Đại Dược Vương, cũng sẽ thực hiện lời hứa."),t("br"),t("br"),n._v("Trải qua qua mấy ngày học tập, Mạc Vũ với trước mắt vị này so với nàng tuổi còn nhỏ thanh niên, phục sát đất."),t("br"),t("br"),n._v("Nàng đan dược lão sư, là Thiên Vũ vương quốc đều nổi tiếng tồn tại, có thể tại chỉ điểm luyện đan bên trên, kém xa đối phương nói trúng tim đen, thẳng bên trong chỗ yếu."),t("br"),t("br"),n._v("Đi qua ba ngày học tập, cải chính, nàng thuật luyện đan, tăng nhanh như gió, lại nhưng đã có thể tự mình có thể luyện ra nhị phẩm đan dược."),t("br"),t("br"),n._v("Luyện chế nhị phẩm đan dược, là có thể sát hạch nhị tinh luyện đan sư, chỉ cùng học ba ngày, liền đạt đến trình độ loại này, liền ngay cả nàng tự mình trải qua, đều có gan ở trong mơ ảo giác."),t("br"),t("br"),n._v("Phải biết ở tình huống bình thường, theo nhất tinh đến nhị tinh, không có năm, sáu năm mài giũa, là không thể nào làm được."),t("br"),t("br"),n._v("“Bạch y sư, ngươi muốn thư tịch chúng ta đã tất cả tìm đến!”"),t("br"),t("br"),n._v("Ngày thứ tư sáng sớm, vẫn như cũ một cái dạy, một cái học, liền thấy Đại Dược Vương đi vào."),t("br"),t("br"),n._v("“Qua xem một chút!”"),t("br"),t("br"),n._v("Trương Huyền đứng dậy."),t("br"),t("br"),n._v("Chờ ba ngày, sẽ chờ cái này, quả nhiên không để cho mình thất vọng."),t("br"),t("br"),n._v("“Mời tới bên này!”"),t("br"),t("br"),n._v("Đại Dược Vương phía trước dẫn đường, rất nhanh đi tới một cái rộng rãi gian phòng, bên trong xếp đầy đủ loại thư tịch, giương mắt nhìn lại, phía ngoài cùng một loạt, quả nhiên là nửa bước Tông sư cùng Tông sư cảnh bí tịch."),t("br"),t("br"),n._v("“Ta xem một hồi, các ngươi đi ra ngoài trước đi!”"),t("br"),t("br"),n._v("Biết hắn đọc sách phương pháp, quá mức kinh thế hãi tục, Trương Huyền khoát tay áo một cái."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Đại Dược Vương cùng Lộ quản gia đi ra ngoài, mới vừa đi ra ngoài cửa liền nghe đến một trận thanh âm dồn dập vang lên, hình như có người ở trong phòng chạy bộ."),t("br"),t("br"),n._v("Chuyên môn tìm những sách này lại đây, không phải muốn xem sao?"),t("br"),t("br"),n._v("Chạy bộ?"),t("br"),t("br"),n._v("Liếc mắt nhìn nhau, hai người tất cả đều vẻ mặt vô cùng nghi hoặc."),t("br"),t("br"),n._v("“Bạch y sư là cao nhân, làm việc ngoài dự đoán mọi người cũng rất bình thường, chúng ta hay là... Trước đừng động!”"),t("br"),t("br"),n._v("Cố nén muốn muốn vào xem một chút kích động, Đại Dược Vương lắc lắc đầu."),t("br"),t("br"),n._v("Cái khác y sư, xem bệnh cần còn muốn hỏi, quan sát... Cái tên này ngược lại tốt, đem người đập ngất, mới có thể chẩn đoán chính xác!"),t("br"),t("br"),n._v("Trị bệnh cứu người, thù lao đều là dược liệu, kim tệ, hắn muốn lại có thể là mặc kệ cấp bậc công pháp..."),t("br"),t("br"),n._v("Ham nhiều không tiêu hóa, đây là ai cũng biết nguyên lý, người tu luyện thông thường chỉ tu luyện một loại công pháp liền có thể, tìm nhiều như vậy, làm gì?"),t("br"),t("br"),n._v("Ba ngày qua, vấn đề này, vẫn vây quanh trong lòng, hai người nghĩ ra mười mấy lý do, đều cảm thấy vô căn cứ."),t("br"),t("br"),n._v("Vốn tưởng rằng thư tịch tìm đến, có thể theo của hắn cử động, biết một ít, kết quả lại nghe được chạy bộ âm thanh..."),t("br"),t("br"),n._v("Tìm bí tịch lại đây, hoặc là xem, hoặc là học tập, kém cỏi nhất cũng muốn lật nhìn một chút đi!"),t("br"),t("br"),n._v("Kết quả nhưng là chạy bộ..."),t("br"),t("br"),n._v("Sớm biết cao nhân làm việc không tầm thường, có thể... Điều này cũng hắn mẹ quá không người thường!"),t("br"),t("br"),n._v("“Lão gia, chúng ta hiện đang làm gì?”"),t("br"),t("br"),n._v("Đứng giữa trời, nghe được ngoại trừ chạy bộ, thực sự nghe không ra những thanh âm khác, Lộ quản gia không nhịn được nói: “Có muốn hay không cũng bồi tiếp... Chạy một hồi?”"),t("br"),t("br"),n._v("“Không cần, chờ ở bên ngoài đi, hắn chạy xong... Nhất định sẽ đi ra!”"),t("br"),t("br"),n._v("Lắc đầu một cái Đại Dược Vương đang muốn nói tiếp, liền nghe đến thư phòng “Kẹt kẹt!” Một tiếng mở ra, Bạch y sư đi ra."),t("br"),t("br"),n._v("“Chuyện này... Bạch y sư, lẽ nào những sách này tịch không đúng?”"),t("br"),t("br"),n._v("Thấy rõ đối phương, Đại Dược Vương sững sờ, có chút không hiểu rõ nổi."),t("br"),t("br"),n._v("Chuyên môn muốn những bí tịch này, vốn tưởng rằng sẽ ở bên trong coi trọng một ngày, hai ngày, chạy thế nào một hồi bước... Liền đi ra?"),t("br"),n._v(" Thời gian ngắn như vậy, liền có bao nhiêu ghi chép đều không thấy rõ chứ?"),t("br"),t("br"),n._v("“Đã xem xong!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Nếu như không đúng, ta lại nghĩ cách... A? Ngươi nói cái gì, xem, xem, xem xong?”"),t("br"),t("br"),n._v("Thân thể loáng một cái, Đại Dược Vương cùng Lộ quản gia, suýt chút nữa không thổ huyết té xỉu."),t("br"),t("br"),n._v("Chúng ta lao lực thiên tân vạn khổ, tìm hơn một ngàn quyển sách, đi vào mấy phút... Xem xong?"),t("br"),t("br"),n._v("Chỉ nghe thấy đến chạy bộ, khi nào xem?"),t("br"),t("br"),n._v("“Ân, của ngươi thư tịch đối với ta trợ giúp rất lớn, đa tạ!”"),t("br"),t("br"),n._v("Không để ý tới đối phương khiếp sợ, Trương Huyền gật gù."),t("br"),t("br"),n._v("Tổng cộng chừng một ngàn quyển sách, tùy ý chạy chạy liền sờ xong, nói thật, mấy phút mới đi ra, hay là hắn vì đột phá nửa bước Tông sư, lúc này mới làm lỡ một ít thời gian, không phải vậy, đối phương khả năng còn chưa đi ra đại môn, chỉ sợ cũng đã “Xem” hết."),t("br"),t("br"),n._v("Đại Dược Vương sưu tập những sách này tịch, nửa bước Tông sư so sánh đầy đủ hết, hình thành hoàn chỉnh Thiên Đạo công pháp, tại chỗ liền tu luyện."),t("br"),t("br"),n._v("Nửa bước Tông sư chỉ là thăng cấp Tông sư một cái chuyển tiếp, có chính xác công pháp, tiến bộ cực nhanh, mấy phút, liền thuận lợi đột phá, đồng thời củng cố tu vi."),t("br"),t("br"),n._v("Cho tới Tông sư cảnh công pháp, chỉ sưu tập bảy, tám trăm bản, khoảng cách hình thành hoàn chỉnh công pháp, còn kém một chút, cũng là không tiếp tục tu luyện."),t("br"),t("br"),n._v("Tu vi đạt đến nửa bước Tông sư, sức mạnh của hắn lại lần nữa tăng lên dữ dội, dĩ nhiên vượt qua một ngàn đỉnh, đạt đến 1500 đỉnh."),t("br"),t("br"),n._v("Loại sức mạnh này, đã có thể cùng Tông sư cảnh sơ kỳ sánh ngang."),t("br"),t("br"),n._v("Tông sư cảnh sơ kỳ 1000 đỉnh, trung kỳ 2000 đỉnh, hậu kỳ 3000 đỉnh, đỉnh phong 4000 đỉnh!"),t("br"),t("br"),n._v("Nửa bước Tông sư liền đạt đến 1500 đỉnh, coi như tại Tông sư sơ kỳ bên trong, đều được xem là trung lưu."),t("br"),t("br"),n._v("Mấu chốt nhất chính là... Người khác đột phá nửa bước Tông sư, không có ba, năm năm, thậm chí mười mấy năm đều không làm được, hắn... Mấy phút!"),t("br"),t("br"),n._v("May là, Thiên Đạo chân khí tinh khiết không gì sánh được, không ai có thể nhìn ra tu vi, bằng không, cho Đại Dược Vương biết, liền mới vừa vào đi này chút thời gian, tu vi lại tăng trưởng cấp một, nhất định sẽ tại chỗ khóc ra thành tiếng."),t("br"),t("br"),n._v("Phải biết, hắn lúc trước vì đột phá nửa bước Tông sư, thế nhưng ăn không biết bao nhiêu dược liệu, dùng tiền tài cân nhắc mà nói, cũng có thể mua một tòa thành trì."),t("br"),t("br"),n._v("Nhiều như vậy đồ vật mới đột phá, “Bạch y sư” đi vào chạy một sẽ ra tới, liền thành công..."),t("br"),t("br"),n._v("Trên thế giới lợi hại nhất thiên tài, đều sẽ tại chỗ đả kích thương tích đầy mình."),t("br"),t("br"),n._v("“Không có gì, nên làm!”"),t("br"),t("br"),n._v("Cố nén muốn muốn kích động đến mức phát điên, Đại Dược Vương không nhịn được hỏi: “Bạch y sư, cái kia... Chúng ta phía dưới làm gì? Còn có đi hay không Độc điện?”"),t("br"),t("br"),n._v("“Đương nhiên muốn đi!”"),t("br"),t("br"),n._v("Trương Huyền cười cợt: “Không những muốn đi, còn muốn quang minh chính đại đi!”"),t("br"),t("br"),n._v("“Quang minh chính đại?”"),t("br"),t("br"),n._v("“Không sai!” Trương Huyền gật gù: “Trước ngươi không phải nói, lão Điện chủ tử vong sự tình, đã thông báo tổng bộ sao?”"),t("br"),t("br"),n._v("“Ừm! Chính là không biết tổng bộ phái không phái đặc sứ lại đây, nếu như phái lại đây, lẽ ra có thể rất nhanh bình định phản loạn, chúng ta cũng có thể an toàn đi qua!” Đại Dược Vương nói: “Nếu như không phái... Có thể sẽ có chút phiền phức.”"),t("br"),t("br"),n._v("“Ta đã nghĩ kỹ biện pháp! Cùng với ở chỗ này chờ mịt mờ, có thể sẽ không đến đặc sứ, còn không bằng... Làm ra cái đặc sứ đi ra!”"),t("br"),t("br"),n._v("Trương Huyền đạo."),t("br"),t("br"),n._v("“Làm ra cái đặc sứ?” Đại Dược Vương có chút không rõ."),t("br"),t("br"),n._v("“Rất đơn giản, ngươi dẫn ta đi Độc điện, liền nói ta là đặc sứ! Ngược lại bọn họ người người đều chưa từng thấy, chắc hẳn cũng phát giác không được.”"),t("br"),t("br"),n._v("Ba ngày nay, hắn cũng muốn được rồi, cùng với dính dáng chờ, còn không bằng chủ động xuất kích."),t("br"),t("br"),n._v("Nếu Độc điện nội chiến, hiện tại đi vào không an toàn, vậy thì chơi một cái... Đối phương tranh cãi nữa đấu cũng không dám động thân phận."),t("br"),t("br"),n._v("Những người này không phải không biết đặc sứ là ai, có đến hay không à?"),t("br"),t("br"),n._v("Cái kia liền trực tiếp giả mạo đặc sứ!"),t("br"),t("br"),n._v("Ngược lại mục đích của chính mình là đọc sách, xem xong là có thể rời đi, coi như sau đó bị phát hiện, người đã chạy trốn, không quá quan trọng."),t("br"),t("br"),n._v("Trước liền danh sư đều giả mạo qua, một cái Độc điện đặc sứ mà thôi, chỉ cần không có gì bất ngờ xảy ra, sẽ không có quá nhiều vấn đề."),t("br"),t("br"),n._v("“Giả mạo... Đặc sứ?”"),t("br"),t("br"),n._v("Đại Dược Vương sắc mặt một hồi trở nên trắng bệch, thân thể liên tục run cầm cập."),t("br"),t("br"),n._v("Đại ca, ta lớn tuổi, trái tim không tốt... Ngươi xác định không có nói đùa?"),t("br"),t("br"),n._v("Giả mạo không phải là chơi đùa, vạn nhất xuất hiện vấn đề, có thể tại chỗ sẽ bị đánh chết!"),t("br"),t("br"),n._v("“Ừm! Chuẩn bị một chút, chúng ta liền lên đường đi, ngươi thế nhưng trước đáp ứng, muốn mang ta đi Độc điện, sẽ không muốn đổi ý đi!”"),t("br"),t("br"),n._v("Trương Huyền nhìn sang, hơi nhướng mày: “Nếu như ngươi sợ sệt, đem ta mang tới Độc điện liền có thể, không cần theo tới!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Đây căn bản không phải sợ không sợ sự tình, cũng không phải đi không đi Độc điện vấn đề..."),t("br"),t("br"),n._v("Mà là giả mạo đặc sứ, một khi bị phát giác, chết một vạn lần cũng không đủ!"),t("br"),t("br"),n._v("Vốn đang cho rằng đối phương chữa khỏi của hắn chứng bệnh, chỉ yêu cầu một ít thư tịch thì thôi, yêu cầu không cao, nằm mơ đều không nghĩ tới, khó nhất ở đây..."),t("br"),t("br"),n._v("Sớm biết ngươi đánh ý đồ này, tình nguyện chết rồi, cũng không đáp ứng a!"),t("br"),t("br"),n._v("Hiện tại đổi ý... Vẫn tới kịp không?"),t("br"),t("br"),n._v("Đại Dược Vương da mặt co giật, lần này là thật sự khóc."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-282"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-284"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);