(window.webpackJsonp=window.webpackJsonp||[]).push([[1503],{1857:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-275-tien-vao-đoc-đien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-275-tien-vao-đoc-đien"}},[n._v("#")]),n._v(" Chương 275: Tiến vào Độc điện")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Ô u Ô u Ô u Ô u!"),t("br"),t("br"),n._v("Hồng Liên sơn mạch cương phong như đao, đâm da dẻ đau đớn. Đơn điệu màu hồng cảnh sắc, xem có thêm để người con mắt cay cay, dị thường mệt mỏi."),t("br"),t("br"),n._v("Thỉnh thoảng một cái đầu ngón tay to nhỏ sâu, theo trước mắt bò qua, ẩm ướt tanh hôi, khiến cho người buồn nôn."),t("br"),t("br"),n._v("“Đây là Hồng Liên sơn mạch đặc hữu ẩm ướt trùng, lấy dược liệu mà sống, là thuốc thiên địch. Bất quá, kỳ quái chính là, thi thể nhưng là dược liệu đại bổ vật phẩm, đương phân trồng trọt đi ra thuốc, linh khí bức người, tươi đẹp loá mắt. Rất nhiều không cách nào trồng trọt dược liệu, có vật này, chẳng những có thể ung dung trồng trọt, còn tình hình sinh trưởng khả quan. Chính vì như thế, Hồng Liên thành mới có thể trở thành là xung quanh mười ba cái vương quốc, to lớn nhất dược liệu sàn giao dịch chỗ!”"),t("br"),t("br"),n._v("Khác nào hình hoa sen hình dáng sơn mạch nơi sâu xa, hai bóng người đi bộ về phía trước, một người trong đó nhìn dưới mặt đất mọc đầy sâu, giải thích."),t("br"),t("br"),n._v("“Khó trách, ta vẫn đang nghĩ, như thế hẻo lánh địa phương, không dài thảm thực vật, Hồng Liên thành làm sao sẽ trở thành dược liệu tập trung chi địa, thì ra là như vậy.”"),t("br"),t("br"),n._v("Người thứ hai ảnh gật gật đầu, đi về phía trước mấy bước, nhìn về phía trên đầu che khuất ánh mặt trời sương mù, hỏi tiếp: “Đây chính là ngươi nói khí độc?”"),t("br"),t("br"),n._v("Hai người chính là rời đi Hồng Liên thành đi tới Độc điện Trương Huyền cùng Đại Dược Vương."),t("br"),t("br"),n._v("Bởi vì trừ độc điện quá mức nguy hiểm, liền không mang Mạc Vũ, để bản thân nàng trở về Thiên Vũ vương thành."),t("br"),t("br"),n._v("Đại Dược Vương băn khoăn một quãng thời gian, hay là quyết định mang hắn tới."),t("br"),t("br"),n._v("Cái mạng này là nhặt, nếu đã đáp ứng đối phương, liền không cách nào từ chối."),t("br"),t("br"),n._v("Một đường tiến lên, hai người đã đi rồi ròng rã năm ngày."),t("br"),t("br"),n._v("“Đúng đấy! Hồng Liên sơn mạch bên dưới là cái núi lửa lớn, mặt đất nóng bức, lúc này mới không có một ngọn cỏ, nham thạch đều hiện màu đỏ rực. Nhiệt khí bốc hơi bên dưới, khí độc trôi nổi tại cách mặt đất mấy chục mét địa phương, dày đến mấy trăm mét. Cưỡi phi hành man thú từ trời cao bay lượn có thể, nhưng muốn xuống, liền rất khó khăn. Một khi đi qua tầng này độc khí, sẽ bị thương tổn được, coi như là Tông sư cảnh phi hành man thú, đều kiên trì không được một khắc, sẽ thất khiếu chảy máu tử vong!”"),t("br"),t("br"),n._v("Đại Dược Vương giải thích."),t("br"),t("br"),n._v("“Vì lẽ đó, đoạn này đường chỉ có thể đi bộ, không cách nào cưỡi phi hành man thú! Cũng bởi vì như thế, Độc điện có thể sừng sững tại đây Hồng Liên núi nhiều năm như vậy, không bị những cái được gọi là chính nghĩa chi sĩ đồ diệt.”"),t("br"),t("br"),n._v("Độc điện, phối chế độc dược bán ra, âm u khủng bố, khó lòng phòng bị, đều sẽ đắc tội không ít người, sừng sững mấy ngàn năm mà không ngã, sau lưng có mạnh mẽ tổng bộ giúp đỡ chỉ là một phần, càng quan trọng chính là những cái này khí độc cùng với vị trí bí ẩn."),t("br"),t("br"),n._v("Tìm cũng không tìm tới, coi như tìm tới, không cách nào cưỡi phi hành man thú, tiến quân thần tốc, liền không tạo thành được uy hiếp."),t("br"),t("br"),n._v(" “Xác thực lợi hại.” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Khó trách Độc điện rất nhiều người không tìm được, thậm chí nghe đều chưa từng nghe tới, có những cái này khí độc làm yểm hộ, coi như biết, cũng e sợ không ai tiến đến."),t("br"),t("br"),n._v("Những thứ đồ này, không những trôi nổi ở trên trời, còn có một phần cùng mặt đất gấp quấn quýt, nếu không là Đại Dược Vương biết con đường, sớm lẩn tránh, e sợ hai người từ lâu thân trúng kịch độc."),t("br"),t("br"),n._v("Có thể nói, không ai dẫn đường, muốn tìm được, hầu như không thể."),t("br"),t("br"),n._v("Trước liền nghe nói, đơn độc tìm kiếm Độc điện người, cơ bản đều có hay không đi gặp, bây giờ nhìn lại, quả thực như vậy."),t("br"),t("br"),n._v("May mà có Mạc Vũ dẫn đường tìm tới Đại Dược Vương, không có mình chạy tới."),t("br"),t("br"),n._v("Không phải vậy, coi như Thiên Đạo chân khí có thể xua đuổi một ít độc khí, bên trong hơn nhiều, cũng rất là phiền phức."),t("br"),t("br"),n._v("“Khoảng cách Độc điện có còn xa lắm không?”"),t("br"),t("br"),n._v("Nhìn về phía trước, chỉ thấy sơn mạch vẫn như cũ liên tiếp, không nhìn thấy phần cuối, liền một cái kiến trúc cái bóng đều không có, Trương Huyền không nhịn được hỏi."),t("br"),t("br"),n._v("Đã đi rồi năm ngày, vẫn còn chưa đi đến, chất độc này điện không khỏi quá xa đi!"),t("br"),t("br"),n._v("“Lập tức đến, liền ở mặt trước!” Đại Dược Vương chỉ về phía trước."),t("br"),t("br"),n._v("“Phía trước?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Phía trước ngoại trừ trơ trụi nham thạch, cái gì đều không có, từ đâu tới Độc điện?"),t("br"),t("br"),n._v("“Đi theo ta!”"),t("br"),t("br"),n._v("Cười cợt, Đại Dược Vương đi về phía trước, vòng qua một cái to lớn nham thạch, cùng một cái núi nhỏ sườn dốc, một cái thung lũng xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Cúi đầu hướng thung lũng nhìn chút, Trương Huyền lập tức gương mặt kinh ngạc."),t("br"),t("br"),n._v("Chỉ thấy một tòa thật to thành trì sừng sững trong đó."),t("br"),t("br"),n._v("Thành trì căn cứ địa thế xây dựng, trong đó kiến trúc cổ xưa, cùng Hồng Liên thành có chút tương tự, nhìn chút liền biết tồn tại không biết bao nhiêu năm."),t("br"),t("br"),n._v("“Đây chính là Độc điện?”"),t("br"),t("br"),n._v("Cái này Độc điện thành trấn cùng trước Thú đường so, đều không hề yếu, màu xanh nhạt đủ loại kiến trúc liên tiếp, khảm nạm tại màu đỏ thắm bên trong dãy núi, khác nào một bức duyên dáng bức tranh."),t("br"),t("br"),n._v("“Có người nói xây dựng Độc điện thời điểm, chuyên môn mời trận pháp Đại sư, lấy sơn mạch vì trận kỳ, bố trí cái ẩn nấp trận pháp, không tìm được con đường mà nói, coi như đứng ở trước mặt, cũng không nhìn thấy.”"),t("br"),t("br"),n._v("Đại Dược Vương đạo."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Xác thực như vậy."),t("br"),t("br"),n._v("Vừa nãy hắn đứng ở nơi đó nhìn hồi lâu, chỉ nhìn thấy liên miên sơn mạch, Quỷ Ảnh đều không nhìn thấy, có thể tha qua mấy cái tảng đá cùng một cái núi nhỏ sườn dốc, như thế thành phố khổng lồ đang ở trước mắt, nếu như nói không có trận pháp ẩn giấu, hắn đều không tin."),t("br"),t("br"),n._v("“Bạch y sư, phía trước chính là Độc điện, chúng ta lẽ nào tựu cái này dạng trực tiếp đi vào?”"),t("br"),t("br"),n._v("Đại Dược Vương hỏi lần nữa."),t("br"),t("br"),n._v("Nhớ lại ý nghĩ của đối phương, hắn liền cảm thấy trong lòng run sợ."),t("br"),t("br"),n._v("Nói thật, Độc điện đặc sứ xác thực ai cũng chưa từng thấy, có thể... Một khi bị phát giác không phải, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("“Ân, thân phận của ta bây giờ là y sư, có lẽ liền có người từng thấy... Nhất định phải thay đổi một hồi!”"),t("br"),t("br"),n._v("Trương Huyền hiện tại dáng dấp hay là Bạch Thiềm, Độc điện độc sư cũng không có thiếu khắp nơi du lịch, có lẽ liền từng thấy, hay là cẩn thận một ít cho thỏa đáng."),t("br"),t("br"),n._v("Tinh thần hơi động, thuật ngụy trang triển khai, bắp thịt toàn thân xương cốt “Khanh khách!” Vang vọng, cả người bất luận dung mạo hay là thân cao đều có biến hóa, lại lần nữa xem ra, là cái chừng bốn mươi tuổi người trung niên."),t("br"),t("br"),n._v("Thay đổi đã sớm chuẩn bị kỹ càng một cái màu đen trang phục màu xám xỏ vào, Trương Huyền giờ khắc này như là biến thành người khác, cùng vừa nãy Bạch Thiềm, thậm chí bản thân, không hề có một chút tương đồng, đừng nói Đại Dược Vương, coi như Triệu Nhã mấy người lại đây, cũng khẳng định đoán không được, trước mắt vị này chính là giáo viên của bọn họ."),t("br"),t("br"),n._v("Ngụy trang gặp qua người không quá dễ dàng, mà ngụy trang một cái chưa bao giờ từng xuất hiện người, hay là rất đơn giản."),t("br"),t("br"),n._v("“Đây là... Thuật ngụy trang?”"),t("br"),t("br"),n._v("Đại Dược Vương thân thể run lên."),t("br"),t("br"),n._v("Trương Huyền này một tay, liền ngay cả hắn đều không nghĩ tới."),t("br"),t("br"),n._v("Khó trách trước mắt vị này Bạch y sư, dám giả mạo đặc sứ, nguyên lai có này dựa dẫm."),t("br"),t("br"),n._v("Chỉ là... Ngươi giả bộ một chút, người khác không nhận ra, không tìm được, ta làm sao bây giờ?"),t("br"),t("br"),n._v("Ta thế nhưng Hồng Liên thành Đại Dược Vương, chạy trời không khỏi nắng."),t("br"),t("br"),n._v("Mang một cái giả đặc sứ lại đây, một khi bị phát giác, coi như đào tẩu, cũng nhất định sẽ bị nắm về, băm thành tám mảnh, tại chỗ băm thành thịt vụn."),t("br"),t("br"),n._v("Vốn là, còn tưởng rằng hai người cùng tiến cùng lui, thế mới biết, đối phương liền bộ mặt thật đều không lộ ra, coi như tướng tra, cũng không tra được..."),t("br"),t("br"),n._v("Bất kể nói thế nào, hố đều là chính hắn."),t("br"),t("br"),n._v("Rõ ràng những cái này, Đại Dược Vương một mặt vặn vẹo, khóc không ra nước mắt."),t("br"),n._v("“Yên tâm đi, một lúc nữa đến Độc điện, ngươi có thể sớm thanh minh, liền nói là ta tìm được ngươi, tự xưng đặc sứ, ngươi không xác nhận bên dưới, mang theo lại đây. Như vậy rồi cùng ngươi không có quan hệ gì, còn trên người ngươi khế ước cổ, cũng có thể nói là ta vì để cho ngươi dẫn đường, ra tay giải quyết đi.”"),t("br"),t("br"),n._v("Nhìn ra ý nghĩ của hắn, Trương Huyền đạo."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Đại Dược Vương hơi đỏ mặt."),t("br"),t("br"),n._v("Không nghĩ tới đối phương đã thay hắn nghĩ kỹ đường lui, buồn cười hắn còn tiểu nhân chi tâm đo lòng quân tử!"),t("br"),t("br"),n._v("Không thể không nói, đề nghị này, xác thực là tốt nhất chối từ."),t("br"),t("br"),n._v("Hắn không phải Độc điện người, không nhận ra đặc sứ bình thường, nếu như các ngươi cũng mắt vụng về, không nhận ra, coi như sau đó muốn trách tội cũng trách tội không được a."),t("br"),t("br"),n._v("Lại nói, Đại Dược Vương cùng Điện chủ ký kết khế ước cổ sự tình, tuy là cơ mật, Độc điện nội bộ, hay là có người biết đến, hiện tại Điện chủ chết rồi, hắn còn nhảy nhót tưng bừng, không có lời giải thích, ai cũng sẽ hoài nghi."),t("br"),t("br"),n._v("Một câu nói, giải quyết của hắn mầm họa, có thể để cho người khác không nghi ngờ, không tiếp tục dám động hắn, thực sự là một mũi tên trúng ba đích."),t("br"),t("br"),n._v("Chỉ có điều..."),t("br"),t("br"),n._v("“Nếu như ta nói như vậy, bọn họ nhất định đối với ngươi xuất hiện hoài nghi...” Đại Dược Vương chần chờ một chút, hỏi: “Không biết Bạch y sư, đối với độc sư biết bao nhiêu?”"),t("br"),t("br"),n._v("Nói như vậy, bản thân là an toàn, nhưng vị này Bạch y sư sẽ rơi vào tình cảnh càng nguy hiểm."),t("br"),t("br"),n._v("Độc điện người tất nhiên sẽ nghĩ biện pháp sát hạch, nếu như hắn đối với độc sư hiểu rõ rất nhiều cũng còn tốt, hơi có sai lầm, cũng rất dễ dàng lòi."),t("br"),t("br"),n._v("“Nửa tháng phía trước mới nghe nói nghề nghiệp này, trước chưa từng nghe qua!” Trương Huyền đạo."),t("br"),t("br"),n._v("“Nửa tháng phía trước... Mới nghe nói?”"),t("br"),t("br"),n._v("Đại Dược Vương mí mắt lật một cái, suýt chút nữa không máu tươi phun ra."),t("br"),t("br"),n._v("Đại ca, ngươi lừa ta chơi đùa chứ?"),t("br"),t("br"),n._v("Nửa tháng phía trước mới nghe nói, liền độc sư là cái gì ngoạn ý cũng không biết, liền dám giả mạo đặc sứ..."),t("br"),t("br"),n._v("“Đúng đấy!”"),t("br"),t("br"),n._v("“Giả mạo đặc sứ, liền đại biểu thân phận huy chương, tổng bộ thân phận lệnh bài đều không có, muốn muốn lấy được đối phương tín nhiệm, nhất định phải bày ra dùng độc kỹ xảo, cái gì đều sẽ không, cái gì cũng không hiểu...”"),t("br"),t("br"),n._v("Thấy đối phương đáp ứng chuyện đương nhiên, không có một chút nào sợ hãi, Đại Dược Vương chỉ cảm thấy một ngụm máu tươi nén tại ngực, bất cứ lúc nào cũng sẽ phun ra ngoài."),t("br"),t("br"),n._v("Nếu như ngươi có tổng bộ thân phận lệnh bài, cấp bậc cao độc sư huy chương, giả mạo đặc sứ, người khác sẽ tin tưởng!"),t("br"),t("br"),n._v("Không có những cái này, có vững chắc dùng độc thủ đoạn, cũng có thể làm cho người tin phục..."),t("br"),t("br"),n._v("Nửa tháng phía trước mới nghe nói độc sư, cái gì ngoạn ý đều sẽ không, cũng không hiểu, liền dám giả mạo, đến cùng là tâm đại, hay là không biết nguy hiểm?"),t("br"),t("br"),n._v("Này không phải chơi nhà chòi, một khi cho đối phương phát giác ngươi là giả, thật sẽ giết chết!"),t("br"),t("br"),n._v("“Không sao, đến thời điểm tùy cơ ứng biến là được!”"),t("br"),t("br"),n._v("Biết đối phương lo lắng cái gì, Trương Huyền khoát tay áo một cái: “Được rồi, đều đi tới nơi này, nghĩ quá nhiều cũng vô dụng, đi xuống đi!”"),t("br"),t("br"),n._v("Tuy rằng hắn đối với độc sư không biết, nhưng có Thiên Đạo thư viện tại, liền danh sư đều có thể ngụy trang, trang cái lợi hại một chút độc sư, chắc là không thành vấn đề."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nhìn thấy đối phương một mặt không quan tâm, Đại Dược Vương đầy mặt ủ rũ, vừa mới sinh ra một chút lòng tin, lại lần nữa tan thành mây khói."),t("br"),t("br"),n._v("Thật không biết trước mắt cái tên này đến cùng từ đâu tới tự tin."),t("br"),t("br"),n._v("Bất quá, việc đã đến nước này, đã không có cách nào, đành phải đi được tới đâu hay tới đó."),t("br"),t("br"),n._v("Dọc theo thung lũng thông đạo tiếp tục đi, thời gian không lâu đi tới thành trì trước mặt."),t("br"),t("br"),n._v("“Người nào, dám lén xông vào Độc điện?”"),t("br"),t("br"),n._v("Còn không đi vào cửa thành, liền nghe đến quát to một tiếng, mấy cái thân mặc áo bào đen người trung niên trốn ra, từng cái từng cái ánh mắt lạnh lùng, giống như ưng kiêu."),t("br"),t("br"),n._v("“Chu độc sư, Liễu độc sư, chư vị, là ta!”"),t("br"),t("br"),n._v("Bước lên trước, Đại Dược Vương vội vàng nói."),t("br"),t("br"),n._v("“Ồ? Hóa ra là Đại Dược Vương? Ngươi không ở Hồng Liên thành đợi, chạy nơi này làm cái gì?” Kêu Chu độc sư người trung niên híp mắt lại."),t("br"),t("br"),n._v("“Hiện tại tình huống như thế, người khác trốn cũng không kịp, chính ngươi lại muốn vọt qua đến, có phải là chán sống rồi?” Liễu độc sư cũng hừ nói."),t("br"),t("br"),n._v("Độc điện náo loạn, người người tự nguy, lúc này lại đây, tuyệt đối là muốn chết."),t("br"),t("br"),n._v("Hai người này trước rồi cùng Đại Dược Vương nhận thức, xưa nay bên trong không ít thu thứ tốt, cũng đương nhiên giúp hắn nói chuyện."),t("br"),t("br"),n._v("“Ta tìm đến ba vị Phó điện chủ...”"),t("br"),t("br"),n._v("Biết đối phương là hảo ý, Đại Dược Vương đang muốn nói rõ ý đồ đến, liền thấy một cái độc sư đi tới Trương Huyền trước mặt."),t("br"),t("br"),n._v("“Người này là ai? Chẳng lẽ không biết không được đồng ý, liền tùy ý dẫn người lại đây, là tội chết sao?”"),t("br"),t("br"),n._v("Nhìn về phía Trương Huyền, vị này độc sư trong mắt mang theo nồng nặc sát cơ: “Người ngoài, muốn trách thì trách ngươi mệnh không được, bất quá, có thể chết ở ta mới nghiên cứu chế tạo độc dược bên trên, cũng coi như vinh hạnh!”"),t("br"),t("br"),n._v("“Chậm đã, vị này chính là...”"),t("br"),t("br"),n._v("Thấy hắn lập tức động thủ, Đại Dược Vương biến sắc mặt, đang muốn nói ra hắn là đặc sứ, liền thấy trước mắt “Bạch y sư”, bàn chân nhấc lên, một chân đạp tới."),t("br"),t("br"),n._v("Oành!"),t("br"),t("br"),n._v("Muốn giết hắn độc sư, còn không phản ứng lại, liền đổ bay mà ra, thân thể tầng tầng đánh vào trên tường thành, giống như một tấm bánh thịt, cái cổ lệch đi, cắt đứt hô hấp."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("“Làm càn!”"),t("br"),t("br"),n._v("Không nghĩ tới cái này lén xông vào Độc điện gia hỏa, dám đối với bọn họ động thủ, Chu độc sư, Liễu độc sư mấy người tất cả đều sững sờ, lập tức một hồi nổ tung, đồng loạt đem hai người vây nhốt, đằng đằng sát khí."),t("br"),t("br"),n._v("“Xong...”"),t("br"),t("br"),n._v("Không có đại biểu thân phận huy chương, không hiểu độc sư, Đại Dược Vương nghĩ, tốt nhất có thể điệu thấp chút, lấy được đừng người tin phục sau đó, lại muốn những biện pháp khác."),t("br"),t("br"),n._v("Cái tên này đến tốt, vừa đến liền giết người..."),t("br"),t("br"),n._v("Đại ca, ngươi thực sự là chê chúng ta chết không đủ nhanh?"),t("br"),t("br"),n._v("Thấy cảnh này, thân thể loáng một cái, Đại Dược Vương chỉ cảm thấy nước mắt cũng lại ngăn chặn không được."),t("br"),t("br"),n._v("Mẹ nó, người khác có như thần đội hữu."),t("br"),t("br"),n._v("Ta cái này... Là hố người chết không đền mạng a!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-283"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-285"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);