(window.webpackJsonp=window.webpackJsonp||[]).push([[1505],{1859:function(n,h,c){"use strict";c.r(h);var i=c(44),t=Object(i.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-277-lai-toi-mot-cai"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-277-lai-toi-mot-cai"}},[n._v("#")]),n._v(" Chương 277: Lại tới một cái")]),n._v(" "),c("hr"),n._v(" "),c("p",[n._v("Đang muốn vị này Bạch y sư đối mặt ba vị Phó điện chủ, tất cả trưởng lão, ứng đối ra sao, liền nghe được câu này, Đại Dược Vương chỉ cảm thấy mắt tối sầm lại."),c("br"),c("br"),n._v("Độc Tâm Kim Lệnh đại biểu thân phận, đặc sứ lại tới đây, đối phương xem xét, là mười phần bình thường yêu cầu, không có bất cứ vấn đề gì."),c("br"),c("br"),n._v("Đến một câu đúng quy cách à..."),c("br"),c("br"),n._v("Hiện tại điện chủ chết bất đắc kỳ tử, chỉ có ba vị Phó điện chủ cùng tất cả trưởng lão, bọn hắn không đủ tư cách, ai đủ"),c("br"),c("br"),n._v("Coi như đặc sứ thân phân cao quý, phách lối cũng phải có cái hạn độ, lớn lối như vậy... Rất dễ dàng bị đánh chết!"),c("br"),c("br"),n._v("“Đặc sứ nói đùa!”"),c("br"),c("br"),n._v("Quả nhiên, nghe nói như thế, mới vừa rồi còn khuôn mặt tươi cười yêu kiều Liêu Huân Phó điện chủ sắc mặt khó coi: “Ta là Hồng Liên phân bộ đệ nhất Phó điện chủ, hiện tại điện chủ không ở, chức vị cao nhất, tự nhiên có tư cách!”"),c("br"),c("br"),n._v("“Các ngươi điện chủ bỏ mình, chuyện này ta đã biết, hôm nay đứng ở chỗ này, chính là xử lý tân nhiệm điện chủ công việc, nếu không loại này thâm sơn cùng cốc địa phương, ta sao lại tới”"),c("br"),c("br"),n._v("“Không có xác lập mới điện chủ trước đó, tất cả mọi người thân phận đều như thế, nếu quả thật muốn có tư cách, không cần nói với ta các ngươi đảm nhiệm cái gì, mà là... Xuất ra các ngươi đối với độc khống chế, đối với độc lý giải.”"),c("br"),c("br"),n._v("Ống tay áo Trương Huyền hất lên, ánh mắt lộ ra uy nghiêm: “Ta Độc điện, cần chính là có bản lĩnh Độc Sư, không phải tầm thường!”"),c("br"),c("br"),n._v("“Đối với độc khống chế” Liêu Huân sững sờ: “Đặc sứ đây là muốn khảo hạch chúng ta”"),c("br"),c("br"),n._v("“Không sai, ta không có thời gian cùng các ngươi hao phí, cũng lười bút tích. Độc Sư, vẫn là muốn lấy độc mạnh yếu, đến kết luận thực lực. Các ngươi phối trí độc dược, ta tại vừa quan sát, ai có thể hợp với ta chưa từng thấy độc, hoặc có lẽ là ra ta chưa từng nghe qua lý luận, thì có tư cách nhìn Độc Tâm Kim Lệnh của ta, trở thành đời tiếp theo điện chủ!”"),c("br"),c("br"),n._v("Trương Huyền mặt không thay đổi nhìn qua: “Nếu không, cùng đem Độc điện giao cho công tử bột, còn không bằng để nó tự sinh tự diệt được rồi!”"),c("br"),c("br"),n._v("“Là...”"),c("br"),c("br"),n._v("Nghe hắn nói trực tiếp như vậy, đám người riêng phần mình nhìn nhau, tràn đầy ngưng trọng."),c("br"),c("br"),n._v("Trước kia đặc sứ tới, cơ bản đều là muốn ăn hối lộ, chỉ cần phục vụ thư thái, yêu cầu cũng liền có thể thỏa mãn."),c("br"),c("br"),n._v("Hiện tại xem ra, những thủ đoạn này, ở cái này đặc sứ trước mặt khó dùng."),c("br"),c("br"),n._v("Trước mắt vị này, vừa đến thì cho ra oai phủ đầu, cho phạm vi, muốn lên chức, dựa vào thực lực!"),c("br"),c("br"),n._v("Không có thực lực, cũng đừng kéo chút vô dụng."),c("br"),c("br"),n._v("..."),c("br"),c("br"),n._v("“Không hổ là có thể giải quyết Khế Ước Cổ người, lợi hại!”"),c("br"),c("br"),n._v("Nghe được “Bạch y sư” một câu liền hóa giải nguy cơ, Đại Dược Vương nhẹ nhàng thở ra, phục sát đất."),c("br"),c("br"),n._v("Vị này Bạch y sư không riêng y thuật cao siêu, đối với lòng người khống chế, cũng là ngoài dự đoán của mọi người tinh chuẩn."),c("br"),c("br"),n._v("Biết ba vị điện chủ đều ở ngấp nghé điện chủ chi vị, lợi dụng này làm mồi dụ."),c("br"),c("br"),n._v("Bọn hắn vốn là tranh khó bỏ khó phân, ai cũng không phục ai, hiện tại quy định ra điều lệ, tự nhiên lập tức tuân thủ."),c("br"),c("br"),n._v("Coi như đối với hắn thân phận có chỗ hoài nghi, cũng không khả năng hiện tại kiểm nghiệm, trừ phi... Ngươi không có bản sự, không cách nào phục chúng!"),c("br"),c("br"),n._v("Một câu liền đem rơi xuống trên người mình đầu mâu chuyển dời đến ba vị điện chủ trên người, để bọn hắn tranh đến ngươi chết ta sống."),c("br"),c("br"),n._v("Hoàn toàn chính xác lợi hại!"),c("br"),c("br"),n._v("Chỉ bất quá..."),c("br"),c("br"),n._v("Muốn khảo hạch ba người, mình cũng phải hiểu dùng độc mới được, ngươi ngay cả Độc Sư là cái gì đều không biết rõ ràng, khảo hạch thế nào"),c("br"),c("br"),n._v("Nếu là ba vị điện chủ thực phối trí độc dược, một dạng đều nhận không ra, coi như không cần kiểm nghiệm Độc Tâm Kim Lệnh, cũng phải lập tức để lộ a!"),c("br"),c("br"),n._v("Nghĩ vậy, mặt của Đại Dược Vương lần nữa biến thành mướp đắng."),c("br"),c("br"),n._v("Người khác chơi là nhịp tim, hắn đây là liều mạng a!"),c("br"),c("br"),n._v("..."),c("br"),c("br"),n._v("Quy định ra điều lệ, đám người liền không ở ngoài thành đợi, mấy vị Phó điện chủ dưới sự hướng dẫn, rất mau tới đến khí thế nhất rộng lớn một tòa đại điện."),c("br"),c("br"),n._v("“Đặc sứ, như thế nào khảo hạch, còn mời lập cái điều lệ!”"),c("br"),c("br"),n._v("Đám người vào chỗ, Liêu Huân Phó điện chủ nói."),c("br"),c("br"),n._v("“Rất đơn giản, chỉ có hai ải. Đệ nhất, riêng phần mình thi triển bản lĩnh phối dược, độc dược cấp bậc cao thắng được! Đệ nhị, phối chế bản thân nghiên chế dược vật, ai phối độc tính cao nhất, ngay cả ta đều không thể nhận ra, đồng dạng chiến thắng!”"),c("br"),c("br"),n._v("Trương Huyền thản nhiên nói."),c("br"),c("br"),n._v("Nghe được nội dung, tam đại Phó điện chủ đồng thời gật đầu."),c("br"),c("br"),n._v("Độc Sư giống như luyện đan sư, chế biến độc dược cấp bậc cao, tự thân cấp bậc tự nhiên cũng liền cao, tự thân nghiên chế dược vật lợi hại, là nói rõ về sau có thể đi đường càng xa."),c("br"),c("br"),n._v("Hai cái khảo hạch, một cái kiểm trắc thực lực, một cái kiểm trắc thiên phú, nếu như đều có thể siêu quần bạt tụy, lên làm điện chủ, cũng liền không có dị nghị."),c("br"),c("br"),n._v("“Được, cho các ngươi hai cái canh giờ thời gian chuẩn bị!”"),c("br"),c("br"),n._v("Tùy ý khoát tay áo, Trương Huyền đứng dậy: “Các ngươi Tàng thư khố ở nơi nào lần này tới, ta còn dâng tổng bộ mệnh lệnh, muốn tìm kiếm một quyển sách.”"),c("br"),c("br"),n._v("“Tìm kiếm thư tịch”"),c("br"),c("br"),n._v("Đám người mặt mũi tràn đầy nghi hoặc, bất quá nghe được là tổng bộ mệnh lệnh, chần chờ một chút, vẫn là không có hỏi ra."),c("br"),c("br"),n._v("“Đặc sứ, Tàng thư khố chính ở đằng kia, ta đây liền phái người mang ngươi tới!” Liêu Huân vội nói."),c("br"),c("br"),n._v("“Không cần, ta tự mình đi là được, các ngươi đều đi chuẩn bị một chút tiếp xuống khảo hạch, dính dấp điện chủ chi vị, hy vọng có thể cho ta một cái câu trả lời hài lòng, cũng tốt trở về bàn giao!”"),c("br"),c("br"),n._v("Trương Huyền bàn tay một lưng."),c("br"),c("br"),n._v("“Đúng!”"),c("br"),c("br"),n._v("Liêu Huân đám người gật đầu."),c("br"),c("br"),n._v("Không tiếp tục để ý đám người, Trương Huyền nhanh chân hướng Tàng thư khố đi đến."),c("br"),c("br"),n._v("Hồng Liên sơn mạch Độc điện sừng sững mấy ngàn năm, trong đó sách chứa đựng lượng, mười phần phong phú, mặc dù không bằng Thiên Huyền vương quốc Tàng thư khố, cùng phía trước Thú đường so sánh, cũng không thua bao nhiêu."),c("br"),c("br"),n._v("Tiến vào bên trong, lập tức nhìn thấy vô số thư tịch bày ra ở trên giá sách, một chút nhìn không thấy cuối cùng."),c("br"),c("br"),n._v("Có thẻ tre, đóng chỉ, thậm chí các loại gốm sứ, thú cốt bên trên điêu khắc."),c("br"),c("br"),n._v("Bởi vì không có trải qua chiến hỏa, cái này thư tịch cũng đều vô cùng hoàn chỉnh."),c("br"),c("br"),n._v("“Không nghĩ tới đơn giản như vậy liền vào được...”"),c("br"),c("br"),n._v("Gặp không ai theo ở phía sau, Trương Huyền nhẹ nhàng thở ra."),c("br"),c("br"),n._v("Đến Độc điện mục đích đúng là vì nhìn những sách vở này, vốn cho rằng biết có rất nhiều phiền phức, không nghĩ tới đơn giản như vậy."),c("br"),c("br"),n._v("Kỳ thật ngẫm lại cũng liền biết, Độc điện người mặc dù đối với hắn cái này đặc sứ thân phận có chỗ hoài nghi, nhưng cũng không dám quá mức minh mục trương đảm nghi vấn."),c("br"),c("br"),n._v("Dù sao, nếu như là thực sự, đại biểu tổng bộ ý tứ, vạch mặt, toàn bộ phân bộ đều có thể bởi vậy xong đời."),c("br"),c("br"),n._v("Nếu như là giả, chỉ cần không trốn đi, sớm muộn đều có biện pháp vạch trần."),c("br"),c("br"),n._v("“Trước không cần quan tâm nhiều, xem hết thư, giải quyết xong độc trong người khí lại nói!”"),c("br"),c("br"),n._v("Trong cơ thể hắn cái kia đạo khí độc, mặc dù bị Thiên Đạo chân khí áp chế, tạm thời không cách nào động đậy, nhưng hắn biết, một khi phản phệ, nhất định xuất hiện vấn đề lớn, nếu không phải là bởi vì loại này sinh tồn áp bách, cũng không khả năng nhất định phải mạo hiểm tới nơi này."),c("br"),c("br"),n._v("Hiện tại thuận lợi đi tới Độc điện, đồng thời quang minh chính đại tiến nhập Tàng thư khố, liền dành thời gian."),c("br"),c("br"),n._v("Mặc dù mấy cái Phó điện chủ bị bản thân hù dọa, ai cũng không dám cam đoan, không biết kịp phản ứng, tùy thời trở mặt."),c("br"),n._v("Thư tịch tuy nhiều, nhưng hắn có có mới “Chạy đọc sách pháp”, một canh giờ không sai biệt lắm liền có thể xem hết, thời gian đầy đủ."),c("br"),c("br"),n._v("“Bắt đầu đi!”"),c("br"),c("br"),n._v("Thở ra một hơi, Trương Huyền tập trung tinh thần, bàn tay tại hàng thứ nhất trên giá sách một dựng, lần nữa chạy ra."),c("br"),c("br"),n._v("..."),c("br"),c("br"),n._v("“Phó điện chủ, ngươi nói cái này đặc sứ... Có phải thật vậy hay không”"),c("br"),c("br"),n._v("Gian phòng bên trong, Liêu Huân ngồi tại vị trí trước, đối diện Hắc Đồng trưởng lão, nhịn không được nói."),c("br"),c("br"),n._v("Người người đều biết Hắc Đồng trưởng lão thiết diện vô tư, trên thực tế nhưng lại không biết, hắn nhưng thật ra là Liêu Huân Phó điện chủ người."),c("br"),c("br"),n._v("“Không thấy được Độc Tâm Kim Lệnh, ta cũng vô pháp xác nhận!” Liêu Huân chần chờ một chút, nói: “Bất quá, Độc điện mặc dù lâu không xuất thế, uy danh vẫn còn, chắc hẳn còn không người dám cả gan làm loạn chạy tới muốn chết!”"),c("br"),c("br"),n._v("“Như thế...” Hắc Đồng trưởng lão gật đầu."),c("br"),c("br"),n._v("Mặc dù Độc điện ẩn cư thế ngoại, không có nghĩa là có thể để người ta tùy tiện khi dễ, giả mạo đặc sứ tới... Trừ phi thực không muốn sống!"),c("br"),c("br"),n._v("“Hơn nữa, vô luận làm chuyện gì, cũng phải có mục đích, nếu thật là giả, cũng phải có có thể làm cho hắn bỏ qua tánh mạng mục đích mới được...” Liêu Huân nói tiếp: “Chúng ta Độc điện, trừ độc vật, cũng không có quá nhiều trân quý vật phẩm, chắc hẳn còn sẽ không để cho người ta cam mạo nó hiểm!”"),c("br"),c("br"),n._v("Làm một chuyện gì cũng phải có mục đích, có lý do."),c("br"),c("br"),n._v("Độc điện liền một chút độc dược, trân quý nhất cũng liền một chút dược liệu, giả mạo đặc sứ, một khi bị phát hiện, thập tử vô sinh, bốc lên nguy hiểm lớn như vậy, luôn không khả năng chỉ vì những cái này đi!"),c("br"),c("br"),n._v("Hắn cũng không cho rằng, những cái này có thể dùng tiền mua độc dược cùng dược liệu, có thể để cho người ta làm như vậy."),c("br"),c("br"),n._v("Đương nhiên, nếu như cho hắn biết đối phương đích xác không phải là vì độc dược mà đến, mà chỉ vì đọc sách... Đoán chừng biết tại chỗ thổ huyết điên mất."),c("br"),c("br"),n._v("Độc dược chí ít còn có giá trị... Đọc sách có cái gì dùng"),c("br"),c("br"),n._v("Mấu chốt nhất là, vẫn là chạy tới chạy lui..."),c("br"),c("br"),n._v("“Phó điện chủ chẳng lẽ đã quên... Vật kia” Hắc Đồng trưởng lão chần chờ một chút, nói."),c("br"),c("br"),n._v("“Ngươi nói là dưới núi lửa chính là cái kia”"),c("br"),c("br"),n._v("Liêu Huân sắc mặt trở nên ngưng trọng."),c("br"),c("br"),n._v("“Đúng vậy a!” Hắc Đồng trưởng lão gật gật đầu: “Chúng ta Hồng Liên chi nhánh, canh giữ ở địa phương khỉ ho cò gáy này, mục đích chủ yếu chính là vì vật kia, sẽ có hay không có người biết, cái này cái gì đã thành thục, lão điện chủ cũng là bởi vì cái này tử vong”"),c("br"),c("br"),n._v("“Không phải là không có khả năng!”"),c("br"),c("br"),n._v("Nghe nói như thế, Liêu Huân lại không có trước đó bình tĩnh như vậy: “Đi đem Đại Dược Vương mang vào, ta phải hỏi rõ ràng!”"),c("br"),c("br"),n._v("“Đúng!” Hắc Đồng trưởng lão lui ra ngoài, chỉ chốc lát, Đại Dược Vương bị mang theo tới."),c("br"),c("br"),n._v("“Gặp qua Liêu phó điện chủ, gặp qua Hắc Đồng trưởng lão!” Đại Dược Vương ôm quyền."),c("br"),c("br"),n._v("“Nói một chút, ngươi làm sao gặp được vị này đặc sứ, còn nữa, nếu như ta đoán không sai, ngươi nên hiện tại đã chết đi, tại sao sẽ không sao”"),c("br"),c("br"),n._v("Liêu Huân tròng mắt hơi híp."),c("br"),c("br"),n._v("“Ta trúng Khế Ước Cổ, hoàn toàn chính xác sắp tử vong, là vị này đặc sứ xuất thủ thay ta giải quyết. Sau đó để cho ta dẫn hắn đến Độc điện, về phần cái khác... Hoàn toàn không biết!”"),c("br"),c("br"),n._v("Đại Dược Vương liền tranh thủ trước đó thương nghị tốt thuyết pháp nói ra."),c("br"),c("br"),n._v("“Thật không biết” Liêu Huân lạnh rên một tiếng: “Ngươi cũng đã biết giấu diếm hậu quả của ta”"),c("br"),c("br"),n._v("“Ta biết!”"),c("br"),c("br"),n._v("Đại Dược Vương quỳ rạp xuống đất, một mặt sợ hãi: “Ta nói câu câu là thật, Phó điện chủ cũng biết, ta chính là Độc điện một cái khôi lỗi, người phát ngôn. Vị tiền bối này nói là đặc sứ, ta cũng không dám ngăn cản, đành phải mang tới, về phần cái khác, thật không rõ ràng!”"),c("br"),c("br"),n._v("“Ừ, đi xuống đi!”"),c("br"),c("br"),n._v("Lại hỏi vài câu, gặp cái gì đều không hỏi được, Liêu Huân khoát khoát tay."),c("br"),c("br"),n._v("“Đúng!”"),c("br"),c("br"),n._v("Đại Dược Vương như được đại xá, vội vàng đi ra ngoài."),c("br"),c("br"),n._v("“Khế Ước Cổ dung nhập huyết dịch, coi như lão sư của ta, đều không thể giải quyết, có thể làm cho gia hỏa này một điểm tai hoạ ngầm đều không có, chỉ sợ chí ít đạt đến tứ tinh Độc Sư trình độ!”"),c("br"),c("br"),n._v("Gặp hắn rời đi, Liêu Huân ngón tay gõ cái bàn, một chút xíu phân tích: “Tứ tinh Độc Sư, cũng chỉ có tổng bộ bên kia mới có!”"),c("br"),c("br"),n._v("Hồng Liên Độc điện lợi hại nhất Độc Sư, cũng liền tam tinh."),c("br"),c("br"),n._v("Tứ tinh, cũng chỉ có tổng bộ mới có thể phái đi ra."),c("br"),c("br"),n._v("Nói như vậy, cái này đặc sứ hẳn là thực sự."),c("br"),c("br"),n._v("“Bất quá, vẫn cẩn thận một chút cho thỏa đáng, Hắc Đồng trưởng lão, làm phiền ngươi lặng lẽ đi Tàng thư khố nhìn xem, vị này đặc sứ đến cùng đang tìm cái gì thư tịch, lại muốn làm gì!”"),c("br"),c("br"),n._v("Chần chờ một chút, Liêu Huân nói."),c("br"),c("br"),n._v("“Được!”"),c("br"),c("br"),n._v("Hắc Đồng trưởng lão lên tiếng, lui ra ngoài."),c("br"),c("br"),n._v("Qua ước chừng một canh giờ, rồi mới trở về, khắp khuôn mặt là mê hoặc cùng cổ quái."),c("br"),c("br"),n._v("“Thế nào có cái gì không đúng kình hắn nhưng có muốn tìm thư tịch”"),c("br"),c("br"),n._v("Gặp hắn bộ dáng này, Liêu Huân nhịn không được hỏi."),c("br"),c("br"),n._v("“Không có gì vấn đề... Cũng không có đặc biệt tưởng nhớ tìm thư tịch!”"),c("br"),c("br"),n._v("Nhớ tới trước đó thấy một màn, mắt đen dùng sức lắc đầu."),c("br"),c("br"),n._v("Trước đó nhìn thấy lãnh khốc vô cùng đặc sứ, tại Tàng thư khố vậy mà giống như tên điên chạy loạn, không có ý dừng lại chút nào, nói muốn tìm thư tịch, lại không đem một quyển sách rút ra..."),c("br"),c("br"),n._v("Ngay cả hắn loại này kiến thức rộng trưởng lão, cũng nhìn tràn đầy mộng bức, không biết đến cùng muốn làm gì."),c("br"),c("br"),n._v("Nếu không phải xác nhận, đối phương chính là cái kia đặc sứ, thực hoài nghi có phải điên rồi hay không."),c("br"),c("br"),n._v("“Không có liền tốt!”"),c("br"),c("br"),n._v("Liêu Huân nhẹ nhàng thở ra: “Xem ra vị này đặc sứ hẳn là thực sự, không cần hoài nghi! Vẫn là dành thời gian muốn phối chế loại thuốc nào, có thể làm cho hắn hai mắt tỏa sáng đi!”"),c("br"),c("br"),n._v("Hắc Đồng trưởng lão gật gật đầu, đang muốn nói chuyện, chỉ thấy phía trước Liễu độc sư, vội vã đi tới, mang trên mặt bối rối."),c("br"),c("br"),n._v("“Phó điện chủ...”"),c("br"),c("br"),n._v("“Thế nào” Liêu Huân nhướng mày."),c("br"),c("br"),n._v("“Bên ngoài đến rồi một người, muốn ngươi và cái khác hai vị Phó điện chủ gặp hắn... Còn nói hắn là... Đặc sứ!”"),c("br"),c("br"),n._v("Liễu độc sư thanh âm có chút cà lăm."),c("br"),c("br"),n._v("“Đặc sứ”"),c("br"),c("br"),n._v("Liêu Huân cùng Hắc Đồng trưởng lão nhìn nhau, đồng thời con mắt trợn tròn, vẻ mặt vô cùng nghi hoặc."),c("br"),c("br"),n._v("Đặc sứ không phải đã tới sao làm sao..."),c("br"),c("br"),n._v("Lại toát ra một cái"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-285"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-287"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);