(window.webpackJsonp=window.webpackJsonp||[]).push([[1510],{1864:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-281-xich-diem-lien-tu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-281-xich-diem-lien-tu"}},[n._v("#")]),n._v(" Chương 281: Xích diễm liên tử")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Yên tĩnh."),t("br"),t("br"),n._v("Xung quanh một trận yên lặng như tờ."),t("br"),t("br"),n._v("Cổ Mục sắc mặt lúc đỏ lúc trắng."),t("br"),t("br"),n._v("Vị này “Sư thúc tổ” vừa mới triển khai tốc độ, hắn cẩn thận quan sát, nếu như thật dùng tốc độ như thế này động thủ mà nói, hắn e sợ liền một cái hô hấp đều không kiên trì được!"),t("br"),t("br"),n._v("Nói cách khác... Thật muốn giết hắn, xác thực không thể so với giết gà khó khăn!"),t("br"),t("br"),n._v("Nhớ tới vừa nãy lại đối với người như thế động thủ, mồ hôi lạnh liền ướt đẫm lưng y phục."),t("br"),t("br"),n._v("May mà nhận ra bản thân, có chút giao tình, bằng không, coi như cái gọi là đặc sứ, chỉ sợ cũng phải lập tức biến thành thi thể, không còn cách xoay chuyển đất trời."),t("br"),t("br"),n._v("Loại cường giả cấp bậc này, vừa nãy đều không có động thủ, lại làm sao có khả năng cố ý lừa gạt ngất bản thân, lại hạ sát thủ, do đó tự rơi thân phận?"),t("br"),t("br"),n._v("“Là ta tiểu nhân chi tâm, kính xin sư thúc tổ thứ lỗi!”"),t("br"),t("br"),n._v("Rõ ràng điểm ấy, Cổ Mục khom người."),t("br"),t("br"),n._v("Giết ngươi so giết gà dễ dàng, thật muốn động thủ, còn không đến mức cùng gà chơi đùa tâm nhãn."),t("br"),t("br"),n._v("“Ta nhàn vân dã hạc, đi qua Hồng Liên thành, thấy Đại Dược Vương thân bên trong khế ước cổ, lúc này mới xuất thủ cứu giúp. Bởi vậy, có thể nghe đường đường Độc điện phân bộ, vì một cái nho nhỏ Điện chủ vị trí, tranh chấp một mất một còn, không ngại huynh đệ tương tàn. Không đành lòng ta độc sư hậu bối liền như vậy tổn thương mất, lúc này mới lại đây, hi vọng hóa giải can qua!”"),t("br"),t("br"),n._v("Ngước nhìn trời cao, Trương Huyền trong mắt lộ ra trách trời thương người tâm ý."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Nghe được đối phương dĩ nhiên vì vì cái này đi tới nơi này, Liêu Huân mấy người âm thầm xấu hổ."),t("br"),t("br"),n._v("Nguyên lai vị tiền bối này mục đích là cái này, nhóm người mình còn tưởng rằng hắn là tên lừa đảo, tham đồ bảo bối gì... Bây giờ suy nghĩ một chút thực sự là buồn cười."),t("br"),t("br"),n._v("Khó trách hắn vừa đến liền trực tiếp nói muốn sát hạch, thực lực mạnh làm Điện chủ, làm như vậy, chính là tránh khỏi phiền phức cùng tranh đấu."),t("br"),t("br"),n._v("Cổ Mục cũng bỗng nhiên tỉnh ngộ, trước liền đang nghĩ, vị này nếu là bản thân sư thúc tổ, địa vị khẳng định rất cao, vì sao phải giả mạo đặc sứ, hóa ra là không đành lòng độc sư đệ tử bị hao tổn."),t("br"),t("br"),n._v("Rõ ràng điểm ấy, trong lòng khâm phục không thôi, e sợ cũng chỉ có chân chính độc sư, chân chính đối với nghề nghiệp này yêu thích đến mức tận cùng người, mới có lòng dạ như vậy."),t("br"),t("br"),n._v("“Được rồi, nếu ngươi vị này chân chính đặc sứ đến rồi, ta tiếp tục ở lại chỗ này, cũng không có ý nghĩa, vẫn là liền như vậy cáo từ!”"),t("br"),t("br"),n._v("Lắc đầu một cái, Trương Huyền không nói thêm nữa, thân thể loáng một cái, đi ra ngoài."),t("br"),t("br"),n._v("Hắn nói những cái này, dựa dẫm vừa nãy Thiên Đạo thân pháp uy hiếp, mọi người không dám suy nghĩ nhiều, sau một quãng thời gian, liền sẽ phát hiện trăm ngàn chỗ hở."),t("br"),t("br"),n._v("Chẳng qua, đối với hắn mà nói, đến Độc điện mục đích là vì đọc sách, hiện tại thư tịch xem xong, chân chính đặc sứ cũng tới, cùng với tiếp tục ở lại chỗ này chờ làm lộ, còn không bằng lập tức rời đi."),t("br"),t("br"),n._v("Chỉ muốn rời khỏi độc khí phạm vi, là có thể triệu hoán Khiếu Thiên thú, đến lúc đó còn không trời cao mặc chim bay, biển rộng mặc cá nhảy."),t("br"),t("br"),n._v("“Kính xin sư thúc tổ cứu ta...”"),t("br"),t("br"),n._v("Thấy hắn xoay người rời đi, không tiếp tục nhấc lên cứu mạng việc, Cổ Mục sắc mặt trắng nhợt, biết vừa nãy nghi vấn, đã đắc tội đối phương, lúc này hai bước về phía trước, lại lần nữa ngã quỵ ở mặt đất."),t("br"),t("br"),n._v("Phàm là cường giả đều có tôn nghiêm."),t("br"),t("br"),n._v("Vừa đến liền động thủ với hắn, đối phương nói cần giúp đỡ giải quyết, còn chưa tin, đầy mặt nghi vấn, có thể cao hứng mới là lạ."),t("br"),t("br"),n._v("Không ra tay giáo huấn, đã xem như là dễ tính."),t("br"),t("br"),n._v("“Ta ra tay cần bị cứu người hôn mê, ngươi kiêng kỵ bản thân an toàn, không tin cũng bình thường!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thấy đối phương ngữ khí rõ ràng tức giận, không có ý định ra tay, Cổ Mục chần chờ một chút, cắn răng một cái truyền âm lại đây: “Sư thúc tổ, xin chậm! Lần này tổng bộ để ta lại đây, trên danh nghĩa là phù trợ tân Điện chủ, ổn định phân bộ, trên thực tế là vì vì cái này phân bộ  thành thục!”"),t("br"),t("br"),n._v("“Xích Diễm liên tử?”"),t("br"),t("br"),n._v("“Vâng, Xích Diễm liên tử sinh sống ở núi lửa nơi sâu xa, hấp thu địa mạch lực lượng, người tu luyện dùng, có thể tăng cường 20% cơ hội, đột phá Chí Tôn ràng buộc, xung kích cảnh giới cao hơn! Ta chính là kẹt ở bình cảnh không cách nào đột phá, mới chủ động thân mời đi theo.”"),t("br"),t("br"),n._v("Nói ra, Cổ Mục tại không bảo lưu: “Ta biết lấy sư thúc tổ thực lực, vật này không dùng, chẳng qua, nếu như có thể đạt được, đưa cho đệ tử, quần áo học sinh dùng, cũng là một ân tình lớn.”"),t("br"),t("br"),n._v("“Có thể khiến người ta đột phá Chí Tôn ràng buộc tỷ lệ tăng cường 20%?” Trương Huyền tặc lưỡi."),t("br"),t("br"),n._v("Tu vi đột phá, dựa vào chính là ý niệm, tự tin, tích lũy, công pháp... Đan dược trợ giúp, chỉ có thể xếp tới phía sau, cơ bản chỉ tính phụ trợ, bởi vậy, giống như thuốc, đối với người tu luyện tới nói, công hiệu không hề là rất lớn."),t("br"),t("br"),n._v("Cái này dĩ nhiên có 20%, không phải chính tai nghe nói, đều không thể tin được."),t("br"),t("br"),n._v("Đừng xem 20% không tính là quá cao, trong tu luyện, có thể có loại này tỷ lệ, tuyệt đối xem như là rất nghịch thiên rồi."),t("br"),t("br"),n._v("Chí Tôn cảnh, là võ giả cảnh giới tối cao, chẳng qua, cũng không phải tu luyện điểm cuối, đại lục bao la vô biên, khẳng định còn có vô số thực lực vượt qua Chí Tôn cường giả siêu cấp."),t("br"),t("br"),n._v("Một viên Xích Diễm liên tử, có thể làm cho đột phá tỷ lệ tăng cường nhiều như vậy, tuyệt đối có thể được xưng là bảo vật vô giá."),t("br"),t("br"),n._v("Chất độc này sư phân bộ, vẫn còn có thứ đồ tốt này, liền ngay cả Trương Huyền đều có chút bất ngờ."),t("br"),t("br"),n._v("“Tốt, nói một chút yêu cầu của ngươi!”"),t("br"),t("br"),n._v("Biết đối phương nói ra vật này, khẳng định có mục đích của chính mình, Trương Huyền nhìn sang."),t("br"),t("br"),n._v("“Ta chỉ cầu sư thúc tổ ra tay giải quyết trên người ta mầm họa, còn hạt sen, ta muốn một viên có thể đột phá cảnh giới là được! Có thêm không dám đòi hỏi, còn lại có thể tất cả đóng góp cho sư thúc tổ.”"),t("br"),t("br"),n._v("Cổ Mục nói."),t("br"),t("br"),n._v("Nghe được lời của đối phương, hắn biết, lấy hiện tại tình huống thân thể, coi như tìm tới hạt sen, cũng không cách nào đột phá, cùng với như vậy còn không bằng đổi đối phương xuất thủ cứu trị."),t("br"),t("br"),n._v("tＲuy cập http://truyen"),t("br"),n._v("cuaｔui.net/ để đọc truyện  Lại nói, Xích Diễm liên tử mặc dù là thứ tốt, cũng không phải như vậy dễ dàng đạt được, bằng không, lão Điện chủ cũng không đến mức đột nhiên chết bất đắc kỳ tử."),t("br"),t("br"),n._v("Lão Điện chủ tử vong, người khác không biết nguyên nhân, hắn biết rất rõ ràng, cái này phân bộ chuyên môn đem hắn nguyên nhân cái chết, viết thành sách, đưa tới, mà hắn vừa vặn nhìn thấy nội dung."),t("br"),t("br"),n._v("Cũng không phải là ngoại giới nghe đồn bị bệnh chứng, mà là Xích Diễm liên tử thành thục, muốn đi hái, bị địa tâm hỏa tại chỗ thiêu thành tro tàn."),t("br"),t("br"),n._v("Đường đường cường giả chí tôn, đều không chống đỡ được, không có gì bất ngờ xảy ra, khả năng còn cần dựa dẫm trước mắt vị này, mới có thể đạt được cái gọi là hạt sen."),t("br"),t("br"),n._v("Vì lẽ đó, nghĩ phía trước lo lắng sau, còn không bằng đem tin tức này nói ra, lấy này hối đoái."),t("br"),n._v(" Hắn cũng biết, cường giả loại này, biết tin tức sau, tự tin thân phận, chắc chắn sẽ không bạc đãi bản thân."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vốn là hắn liền dự định giúp trị liệu, hiện tại thấy lại còn có đồ vật có thể nắm, sao lại không làm."),t("br"),t("br"),n._v("“Cái kia... Khi nào bắt đầu?”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, Cổ Mục sắc mặt vui vẻ: “Có thể cần chuẩn bị món đồ gì?”"),t("br"),t("br"),n._v("“Không cần chuẩn bị, hiện tại liền bắt đầu đi!”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("Làm lỡ thời gian càng dài, càng đêm dài lắm mộng, còn không bằng cố gắng nhanh."),t("br"),t("br"),n._v("“Tốt!” Nghe được có thể trực tiếp trị liệu, Cổ Mục sắc mặt vui vẻ: “Liền xin nhờ sư thúc tổ ra tay rồi!”"),t("br"),t("br"),n._v("Nói xong cổ tay giương lên."),t("br"),t("br"),n._v("Bốp cộc!"),t("br"),t("br"),n._v("Vỗ vào trên đầu, thân thể mềm nhũn, té xỉu trên đất."),t("br"),t("br"),n._v("“Ế?”"),t("br"),t("br"),n._v("Thấy hắn trực tiếp đem mình đập ngất, cúi đầu nhìn một chút trong tay mới vừa lấy ra , Trương Huyền bất đắc dĩ đành phải lại lần nữa thu hồi trữ vật giới chỉ."),t("br"),t("br"),n._v("“Còn muốn để ngươi dùng cái này, không nghĩ tới so Đại Dược Vương còn lỗ mãng...”"),t("br"),t("br"),n._v(" có thể khiến người ta hôn mê, đối với hắn “Đặc thù y thuật” tới nói, tác dụng rất lớn, gần xuất phát phía trước, chuyên môn muốn không ít."),t("br"),t("br"),n._v("Ngược lại vật này không đáng giá, Đại Dược Vương cũng vui vẻ ân tình, đưa không dưới hơn hai mươi bao."),t("br"),t("br"),n._v("Vốn tưởng rằng lần này, có thể thử xem hiệu quả, không nghĩ tới mới đưa đồ vật lấy ra, cái tên này liền đem mình đập bất tỉnh."),t("br"),t("br"),n._v("Quá nhanh đi!"),t("br"),t("br"),n._v("Chẳng qua, như vậy cũng tốt, ngược lại đều là hôn mê, tiết kiệm nhiều phí một lần công phu."),t("br"),t("br"),n._v("Bước lên trước, đi tới hôn mê Cổ Mục trước mặt, lật bàn tay một cái, mấy cây ngân châm xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Trước thư viện liền sinh thành liên quan với đối phương tỉ mỉ miêu tả, lúc này cũng không cần lại lần nữa chẩn đoán bệnh."),t("br"),t("br"),n._v("Ngón tay búng một cái, ngân châm bay qua, bàn tay tung bay, cả người ung dung linh động, khác nào hồ điệp."),t("br"),t("br"),n._v("Cổ Mục thân thể cùng trước Đại Dược Vương không giống."),t("br"),t("br"),n._v("Người sau là bị khế ước cổ tổn thương căn cơ, lúc này mới xuất hiện suy nhược, mà trước mắt vị này, là bởi vì lợi dùng độc dược kích thích huyệt vị, tiến lên tăng cao thực lực, độc khí tiến vào thân thể, đem trong cơ thể sức sống phá hoại."),t("br"),t("br"),n._v("Người trước cần Thiên Đạo chân khí ôn dưỡng, người sau chỉ cần tìm tới kịch độc tập trung địa phương, lợi dụng Thiên Đạo chân khí đem bức ra ngoài thân thể, liền có thể giải quyết triệt để."),t("br"),t("br"),n._v("Cẩn thận nói đến, so Đại Dược Vương trị liệu, còn muốn dễ dàng không ít."),t("br"),t("br"),n._v("Xì xì xì tư!"),t("br"),t("br"),n._v("Ngân châm phối hợp Thiên Đạo chân khí, đâm vào độc khí hội tụ chỗ, rất nhanh Cổ Mục trên người, liền phát ra một trận nước chảy kêu vang, ngón tay, ngón chân, chảy ra Hắc Thủy."),t("br"),t("br"),n._v("“Đây là... Ẩn giấu ở trong cơ thể độc khí?”"),t("br"),t("br"),n._v("“Tựu cái này dạng bức ra đến?”"),t("br"),t("br"),n._v("Thấy cảnh này, Liêu Huân mấy người đồng thời trợn to hai mắt, từng cái từng cái khó có thể tin tưởng được."),t("br"),t("br"),n._v("Suốt ngày cùng kịch độc tiếp xúc, biết trừ độc khó khăn."),t("br"),t("br"),n._v("Một ít độc khí tiến vào trong cơ thể, coi như lợi dụng vô số thuốc, tiêu tốn một thời gian cả đời, đều không thể hóa giải."),t("br"),t("br"),n._v("Cũng chính vì như thế, độc sư tuy rằng nghe tới đáng sợ, tuổi thọ lại giống như đều không dài, năm mươi, sáu mươi chính là một cái cửa ải."),t("br"),t("br"),n._v("Bọn họ những người này, ở bề ngoài không quá to lớn bệnh trạng, trên thực tế trong cơ thể cũng hoặc nhiều hoặc ít như vậy vấn đề như vậy, suốt ngày cùng độc tiếp xúc, đều có rất lớn tổn thương."),t("br"),t("br"),n._v("Vốn tưởng rằng, lại không cách nào trị liệu, không nghĩ tới trước mắt vị tiền bối này, ung dung liền có thể giải quyết."),t("br"),t("br"),n._v("Liền đặc sứ trong cơ thể như thế lợi hại độc đều có thể bức ra đến, bọn họ có phải là cũng có thể làm được dễ dàng?"),t("br"),t("br"),n._v("Mọi người lại lần nữa nhìn về phía Trương Huyền, từng cái từng cái ánh mắt tràn đầy kịch liệt."),t("br"),t("br"),n._v("Không để ý tới vẻ mặt của mọi người, Trương Huyền tinh thần tập trung, từng cây từng cây ngân châm chuẩn xác cắm ở Cổ Mục trong cơ thể độc khí ẩn giấu sâu nhất địa phương, một lát sau, bàn tay run lên, ngân châm tất cả thu lại rồi."),t("br"),t("br"),n._v("Vung tay lên."),t("br"),t("br"),n._v("Cổ Mục chậm rãi tỉnh lại."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Mở mắt ra, Cổ Mục lập tức cảm nhận được trên người tình huống."),t("br"),t("br"),n._v("Trước, trong cơ thể độc khí vờn quanh, cả người như là cõng lấy một cái to lớn gông xiềng, bất luận hành động, vẫn là cử chỉ đều có chút khó khăn, mà hiện tại, có thể rõ ràng cảm nhận được gông xiềng bị tháo ngoại trừ, toàn thân ung dung."),t("br"),t("br"),n._v("Coi như không dùng chân khí thăm dò, cũng biết, trước để hắn bất cứ lúc nào cũng sẽ tử vong bóng mờ, đã bị đối phương giải quyết."),t("br"),t("br"),n._v("“Đa tạ sư thúc tổ xuất thủ cứu giúp!”"),t("br"),t("br"),n._v("Lại lần nữa ngã quỵ ở mặt đất, lần này tràn đầy thành khẩn, khâm phục xuất phát từ nội tâm, không giống vừa nãy, chỉ là kiêng kỵ thực lực của đối phương."),t("br"),t("br"),n._v("Trương Huyền khoát tay áo một cái: “Ngươi tình huống trong cơ thể, so ta thấy còn nghiêm trọng hơn, khả năng cần liên tục trị liệu ba lần mới có thể giải quyết triệt để. Vì lẽ đó, ngươi cũng không cần cảm kích, cùng triệt để chữa khỏi lại nói cũng không muộn!”"),t("br"),t("br"),n._v("Xích Diễm liên tử còn chưa thấy, ở đây lại không phải tuyệt đối an toàn, tự nhiên cũng muốn chừa chút hậu chiêu, không thể một hồi chữa khỏi đối phương."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-289"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-291"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);