(window.webpackJsonp=window.webpackJsonp||[]).push([[1511],{1865:function(n,h,c){"use strict";c.r(h);var t=c(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-282-thien-đao-đoc-cong"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-282-thien-đao-đoc-cong"}},[n._v("#")]),n._v(" Chương 282: Thiên Đạo độc công")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("br"),c("br"),n._v("“Vâng!”"),c("br"),c("br"),n._v("Cổ Mục biết thân thể tình huống, đã đến ác liệt nhất mức độ, vốn tưởng rằng ít nhất phải duy trì mấy tháng mới có thể trị tốt, không nghĩ tới ba lần liền có thể, với trước mắt “Sư thúc tổ” thực lực không kìm lòng được, lại lần nữa khâm phục mấy phần."),c("br"),c("br"),n._v("“Ngươi tình huống bây giờ, trong thời gian ngắn sẽ không có vấn đề gì, còn lại hai lần, ta sẽ từ từ giúp ngươi trị liệu. Cái này Độc điện phân bộ, còn có Xích Diễm liên tử sự tình, ngươi liền nhìn sắp xếp đi! Dù sao ngươi là đặc sứ, ta cũng không tiện nhúng tay!”"),c("br"),c("br"),n._v("Nên lập uy đã lập uy, nên cho mứt táo đã cho mứt táo, biết trước mắt vị này Cổ Mục đặc sứ đã triệt để đối với mình bái phục, Trương Huyền không đang lo lắng, nhàn nhạt nói một câu, ngồi ở đây đại điện nhất vị trí giữa."),c("br"),c("br"),n._v("Cổ Mục gật gù, quay đầu nhìn về phía Liêu Huân mấy người: “Ngoại trừ trưởng lão cùng phó điện chủ, những người khác đều đi ra ngoài!”"),c("br"),c("br"),n._v("“Vâng!”"),c("br"),c("br"),n._v("Đặc sứ lên tiếng, không ai dám làm trái, rất nhanh trong phòng liền còn lại bảy người, bốn vị trưởng lão cùng ba cái phó điện chủ."),c("br"),c("br"),n._v("“Bản sứ hôm nay tới đây, chỉ vì hai việc!”"),c("br"),c("br"),n._v("Quăng một cái ống tay áo, Cổ Mục ngắm nhìn bốn phía."),c("br"),c("br"),n._v("Tại Trương Huyền trước mặt, hắn cùng vãn bối như thế, không dám có chút vượt qua, nhưng trên thực tế thân là Chí Tôn đỉnh phong, tam tinh độc sư đỉnh phong, một người tiêu diệt nơi này phân điện, cũng không tính là cái gì."),c("br"),c("br"),n._v("Thực lực tuyệt đối quyết định hắn có vô thượng uy nghiêm, để người không dám phản bác."),c("br"),c("br"),n._v("“Đặc sứ mời nói!”"),c("br"),c("br"),n._v("Liêu Huân mấy người liền vội vàng khom người."),c("br"),c("br"),n._v("“Số một, xác lập Điện chủ ứng cử viên. Thứ hai, Xích Diễm liên tử thành thục, chuyên tới để hái.” Cổ Mục nói."),c("br"),c("br"),n._v("“Xích Diễm liên tử...”"),c("br"),c("br"),n._v("Liêu Huân mấy người đồng thời run cầm cập một hồi, sắc mặt trắng bệch."),c("br"),c("br"),n._v("“Xích diễm liên, sinh trưởng trên đất tâm hoả thiêu đốt chỗ, lấy vật kịch độc là dinh dưỡng sinh trưởng, bất luận cành cây, lá sen vẫn là hoa sen, đều mang có kịch độc, là phối chế độc dược tốt nhất vật liệu. Chỉ có hạt sen, ra độc mà không nhiễm, tuy rằng tại hoàn cảnh này bên trong sinh trưởng, lại không hề có một chút độc tính, trái lại là đột phá Chí Tôn đỉnh phong vô thượng bảo vật. Hồng Liên phân điện, sở dĩ xây dựng ở nơi này, sừng sững mấy ngàn năm, chính là vì chăm sóc này đóa hoa sen.”"),c("br"),c("br"),n._v("Cổ Mục trong thanh âm mang theo uy nghiêm: “Hiện tại, hạt sen thành thục, hoa sen, lá sen đều lưu cho các ngươi, hạt sen ta mang đi, có lẽ không có vấn đề gì chứ!”"),c("br"),c("br"),n._v("“Không... Vấn đề!”"),c("br"),c("br"),n._v("Liêu Huân mấy người cười khổ, đồng thời gật đầu."),c("br"),c("br"),n._v("Đặc sứ lên tiếng, đặc sứ “Sư thúc tổ” còn ở bên cạnh nhìn, coi như có vấn đề, cũng không dám a!"),c("br"),c("br"),n._v("“Vậy thì tốt, hạt sen còn bao lâu mới có thể triệt để thành thục?”"),c("br"),c("br"),n._v("Thấy đối phương không dám phản kháng, Cổ Mục lúc này mới thoả mãn gật gù, hỏi tiếp."),c("br"),c("br"),n._v("Hắn có thể vào lúc này lại đây, tự nhiên tính được rồi hạt sen thành thục ngày, ngay ở mấy ngày nay phụ cận, chỉ có điều, cụ thể ngày nào đó liền không rõ ràng."),c("br"),c("br"),n._v("“Hồi bẩm đặc sứ, đại khái còn cần 10 ngày khoảng chừng!”"),c("br"),c("br"),n._v("Liêu Huân nói."),c("br"),c("br"),n._v("“Mười ngày? Tốt lắm, đi thu thập một cái tốt nhất gian phòng, để ta sư thúc tổ ở lại, mười ngày này bên trong, ta sẽ xác định tân một đời Điện chủ ứng cử viên!” Cổ Mục nói."),c("br"),c("br"),n._v("“Vâng!”"),c("br"),c("br"),n._v("Thấy hắn làm ra quyết định, mọi người gật đầu."),c("br"),c("br"),n._v("Biết sau mười ngày Xích Diễm liên tử mới có thể thành thục, Trương Huyền cũng không vội vã, vừa vặn có thể nhân cơ hội tiêu hóa một hồi mới vừa học được tri thức, cẩn thận nghiên cứu trong cơ thể đạo kia độc khí."),c("br"),c("br"),n._v("Ngược lại còn không giúp đối phương triệt để đi trừ mầm họa, coi như ý thức được chính hắn một “Sư thúc tổ” là giả, cũng không dám làm gì mình."),c("br"),c("br"),n._v("Lại nói, bày ra nhiều như vậy lợi hại thủ đoạn, giống như ngũ tinh độc sư đều không làm được, e sợ cũng không dám hoài nghi."),c("br"),c("br"),n._v("..."),c("br"),c("br"),n._v("Không để ý tới Độc điện tân Điện chủ làm sao tuyển chọn, lúc này Trương Huyền đang ngồi ở trong phòng, để Đại Dược Vương cùng Lộ quản gia giữ ở ngoài cửa, nhìn trong đầu sưu tập đến rất nhiều thư tịch, tinh thần độ cao tập trung."),c("br"),c("br"),n._v("“Chính xác!”"),c("br"),c("br"),n._v("Trong lòng hô khẽ, giá sách bên trong liên quan với độc sư vô số thư tịch, cấp tốc hội tụ, hình thành một cái dày đặc bí tịch."),c("br"),c("br"),n._v("Biết chính là hắn muốn , Trương Huyền cũng không do dự, bàn tay một trảo, nhẹ nhàng cầm tới, tiện tay mở ra."),c("br"),c("br"),n._v("“Độc, dược một loại, có thể hại người, cũng có thể cứu người...”"),c("br"),c("br"),n._v("Bên trên lít nha lít nhít viết liên quan với độc dược giải thích."),c("br"),c("br"),n._v("Đồng dạng là sưu tập độc dược thư tịch hình thành Thiên Đạo độc công, bản này rất rõ ràng muốn so với tại Thiên Huyền vương quốc tàng thư khố, tỉ mỉ, cao thâm nhiều lắm, không ngừng xem, Trương Huyền đối với độc hiểu rõ, cũng là càng ngày càng nhiều."),c("br"),c("br"),n._v("Không biết qua bao lâu, con mắt mở, ánh mắt từ trước mê man, đã biến thành trong suốt."),c("br"),c("br"),n._v("“Không nghĩ tới độc, dĩ nhiên cũng bao la như vậy tinh thâm!”"),c("br"),c("br"),n._v("Nhớ lại vừa mới học được độc công, Trương Huyền trong lòng cảm khái."),c("br"),c("br"),n._v("Khó trách độc sư có thể làm cho vô số người vì đó sợ hãi, xác thực đáng sợ, bàn về mức độ phức tạp, không kém chút nào luyện đan sư, luyện khí sư cùng thượng cửu lưu nghề nghiệp, lực công kích, thậm chí còn vượt qua."),c("br"),c("br"),n._v("Làm một ví dụ, thực lực bây giờ của hắn, gặp phải Tông sư trung kỳ, khẳng định không phải là đối thủ, khó có thể chống lại. Nhưng nếu như có đầy đủ độc dược, hoàn toàn có thể đơn giản độc giết Tông sư đỉnh phong cường giả!"),c("br"),c("br"),n._v("Thậm chí độc chết Chí Tôn cảnh người."),c("br"),c("br"),n._v("Đương nhiên, độc dược cũng không phải vạn năng, tu vi không đủ, không cách nào áp chế trong cơ thể sát cơ, rất dễ dàng bị cường giả phát giác, e sợ còn chưa kịp ném độc, liền bị chém giết."),c("br"),n._v(" Hơn nữa, một ít lợi hại độc dược, cần độc sư tinh huyết cùng chân khí thôi thúc, mới có thể phát huy dược lực. Bản thân không phải độc sư, hoặc là đẳng cấp qua thấp, coi như cho lợi hại thuốc, cũng không cách nào sử dụng, làm không cẩn thận ngược lại sẽ đem chính mình độc chết."),c("br"),c("br"),n._v("Đây giống như là người người đều biết bom nguyên tử rất lợi hại, có thể không có đầy đủ sức mạnh, khống chế không được, coi như cho ngươi một viên, cũng không tạo được uy hiếp, làm không cẩn thận còn có thể mang đến họa sát thân."),c("br"),c("br"),n._v("Lợi hại độc dược, giống như lợi hại binh khí, thực lực không đủ, đừng nói đụng vào, khoảng cách mấy trăm mét, thậm chí mấy ngàn mét bên trong, sẽ bị độc giết!"),c("br"),c("br"),n._v("Nguy hiểm cực kỳ!"),c("br"),c("br"),n._v("Vốn còn muốn, nếu như cho mình bào chế làm ra một bộ lợi hại độc dược, có phải là liền Chí Tôn đỉnh phong cường giả cũng không cần sợ hãi, bây giờ nhìn lại, vẫn còn có chút ý nghĩ kỳ lạ."),c("br"),c("br"),n._v("“Ta không muốn dùng độc vật hại người, nhưng cũng có thể phòng bị bị người khác làm hại!”"),c("br"),c("br"),n._v("Biết độc sư nghề nghiệp bác đại tinh thâm, Trương Huyền trong lòng cũng sinh ra cảnh giác tâm ý."),c("br"),c("br"),n._v("Hắn không dùng độc giết người, nhưng cũng muốn phòng ngừa bị người hạ độc."),c("br"),c("br"),n._v("Thật giống như trong cơ thể đạo kia độc khí, nếu như sớm chính là độc sư, liền có thể sớm dự phòng, không về phần mình hiện tại chật vật như vậy."),c("br"),c("br"),n._v("“Căn cứ thư tịch ghi chép, độc sư tu luyện tới cảnh giới tối cao, là nắm giữ độc thể, ta hoàn toàn có thể tu luyện một phen!”"),c("br"),c("br"),n._v("Khẽ mỉm cười."),c("br"),c("br"),n._v("Độc sư cùng những nghề nghiệp khác như thế, cũng phải để ý thiên phú."),c("br"),c("br"),n._v("Có thiên phú độc sư, được gọi là Tiên Thiên độc thể, không những đối với độc có cực mạnh nhạy cảm lực, thân thể càng nắm giữ vượt qua người thường đỡ độc tính. Nói cách khác, người nắm giữ loại thể chất này, coi như cái khác độc sư đem kịch độc thả vào trong miệng hắn, cũng cũng sẽ cùng ăn cơm uống nước như thế, không chịu đến tổn thương chút nào."),c("br"),c("br"),n._v("Thậm chí, truyền thuyết, người như thế còn có thể hấp thu độc dược bên trong sức mạnh, để tu vi tăng nhiều."),c("br"),c("br"),n._v("Đương nhiên, loại thể chất này ít ỏi cực kỳ, ngàn tỉ độc sư bên trong cũng chưa chắc có thể xuất hiện một cái."),c("br"),c("br"),n._v("Loại năng lực này, cùng Triệu Nhã Thuần Âm thể chất như thế, thuộc về Tiên Thiên, hắn khẳng định là không làm được. Chẳng qua, Độc điện thư tịch bên trong có không ít liên quan với độc sư tu luyện bí kỹ, vừa nãy cũng thuận hình thành Thiên Đạo công pháp."),c("br"),c("br"),n._v("Dựa  theo cái này tu luyện, coi như không phải Tiên Thiên độc thể, nhưng cũng có thể để thân thể đối với độc sức đề kháng tăng cường, so chân chính độc sư chuyên môn rèn luyện thân thể, đều cường đại hơn mấy lần."),c("br"),c("br"),n._v("“Thử xem!”"),c("br"),c("br"),n._v("Nghĩ đến liền làm, Trương Huyền không có quá nhiều chần chờ, tinh thần hơi động, độc sư tu luyện hình thành bí tịch liền xuất hiện ở trước mắt, mở ra nhìn sang."),c("br"),c("br"),n._v("Không biết qua bao lâu, một ngụm trọc khí phun ra, đột nhiên đứng dậy."),c("br"),c("br"),n._v("Kẽo kẹt! Kẽo kẹt! Kẽo kẹt!"),c("br"),c("br"),n._v("Xương cốt toàn thân giống như rang đậu giống như vang lên, cả người khí tức đại thịnh."),c("br"),c("br"),n._v("“Tuy rằng không bằng Tiên Thiên độc thể, nhưng cũng không úy kỵ giống như kịch độc, coi như tam tinh độc sư bố trí độc dược, ăn vào cũng cho ta không tạo được bất cứ thương tổn gì!”"),c("br"),c("br"),n._v("Cảm nhận được tu luyện qua hậu thân thể biến hóa, Trương Huyền nở nụ cười."),c("br"),c("br"),n._v("Tu luyện xong Thiên Đạo độc công, thân thể có rõ ràng biến hóa, đối với độc dược đỡ lực tăng cường."),c("br"),c("br"),n._v("Cứ việc không phải Tiên Thiên độc thể, dựa vào hắn tình huống bây giờ, coi như đem tam tinh độc sư bố trí thuốc ăn, đều sẽ không có bất cứ thương tổn gì."),c("br"),c("br"),n._v("“Mấu chốt nhất chính là... Của ta Thiên Đạo chân khí, đi qua tu luyện độc công, có thể trị bệnh cứu người, giúp người đột phá, cũng có thể trong một ý nghĩ hóa thành kịch độc, để người khoảnh khắc tử vong!”"),c("br"),c("br"),n._v("Thân thể phát sinh biến hóa, càng biến hóa lớn vẫn là chân khí."),c("br"),c("br"),n._v("Thiên Đạo chân khí tinh khiết không một hạt bụi, có thể trị thương thế, khôi phục thể lực, tăng cường sinh cơ, giúp người đột phá tu vi..."),c("br"),c("br"),n._v("Lúc này tu luyện độc công, cũng có thể trong một ý nghĩ chuyển hóa thành kịch độc chi khí, để người không thể chịu đựng, một khi tiến vào kinh mạch, coi như cường giả chí tôn muốn hóa giải, e sợ đều rất khó hoàn thành."),c("br"),c("br"),n._v("Khác nào nước trong, một khi tiến vào trong cơ thể, có thể tiến vào bất luận cái gì khe hở, bất luận cái gì một chỗ huyệt đạo, biết rõ ràng ở đâu, chân khí trong cơ thể không rất tinh khiết, cũng không cách nào loại bỏ."),c("br"),c("br"),n._v("Vậy thì cùng bệnh đến giai đoạn cuối không khác nhau gì cả, biết nguyên nhân của bệnh và giải quyết thủ đoạn, lại không có cách nào trị liệu."),c("br"),c("br"),n._v("Không hổ là Thiên Đạo chân khí, nhất niệm mà sống, nhất niệm là chết."),c("br"),c("br"),n._v("Trước căn bản không biết còn có lợi hại như vậy năng lực."),c("br"),c("br"),n._v("“Tiên Thiên độc công tu luyện xong, nhìn đạo kia độc khí!”"),c("br"),c("br"),n._v("Tu luyện xong Tiên Thiên độc công, Trương Huyền phun ra một hơi, lại lần nữa nhìn về phía ẩn giấu ở trong cơ thể đạo kia độc khí."),c("br"),c("br"),n._v("Trước đối với độc không hiểu nhiều, không cách nào xác nhận vật này đến cùng là cái gì, giờ khắc này bàn về tri thức đo, có thể so với tam tinh độc sư, bàn về dùng độc, Cổ Mục khả năng đều không phải là đối thủ, đã có năng lực nghiên cứu này nói độc khí nguồn, thậm chí nghĩ biện pháp đem loại bỏ."),c("br"),c("br"),n._v("Tinh thần cực nhanh vận chuyển, tâm cảnh trong nháy mắt tiến vào tâm như chỉ thủy cảnh giới."),c("br"),c("br"),n._v("“Này cỗ độc khí, có thể ẩn giấu ở trong máu, có thể để trốn của ta Thiên Đạo chân khí...”"),c("br"),c("br"),n._v("Nhìn đạo kia ẩn giấu độc khí, trong thư viện từng quyển từng quyển liên quan với độc thư tịch xuất hiện ở trước mắt, từng cái từng cái tri thức tại trong đầu chảy xuôi."),c("br"),c("br"),n._v("Độc chủng loại, không dưới trăm vạn, mỗi một loại đều có sự khác biệt phối chế phương pháp và giải quyết thủ đoạn, chỉ cần một chút không may xuất hiện, liền có thể có thể làm cho kết quả xuất hiện sai biệt."),c("br"),c("br"),n._v("Trương Huyền cũng không vội vã, thông qua trong thư viện thư tịch, từng cái từng cái sàng lọc."),c("br"),c("br"),n._v("“Thanh Linh độc chủng, không đúng! Mặc Hư độc chủng, cũng không đúng! Xà Thanh độc chủng, cũng không giống...”"),c("br"),c("br"),n._v("Căn cứ trong cơ thể độc khí màu sắc, đặc thù, từng cái xếp hàng tra, không biết qua bao lâu, Trương Huyền đột nhiên ngừng lại, sắc mặt một hồi trở nên trở nên trắng, trong mắt tràn đầy không thể tin được."),c("br"),c("br"),n._v("“Dĩ nhiên là loại độc chất này, chuyện này... Sao có thể có chuyện đó?”"),c("br"),c("br"),n._v("(Ta nói một câu kẹt, cầu an ủi, ý tứ là vé tháng, phiếu đề cử loại hình cho điểm, để lão nhai nhấc lên nhấc lên tinh thần, có lẽ liền có thể tìm tới trạng thái. Kết quả... Mọi người dĩ nhiên để ta úy, an, mồ hôi, các ngươi quá dơ, người khác đều là thật độc giả, của ta nhất định là giả... Đừng kéo ta, để ta khóc một hồi!)"),c("br"),c("br"),n._v("Convert by: ThấtDạ"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-290"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-292"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);