(window.webpackJsonp=window.webpackJsonp||[]).push([[1512],{1866:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-283-dung-nham-thu-【-hai-hop-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-283-dung-nham-thu-【-hai-hop-nhat-】"}},[n._v("#")]),n._v(" Chương 283: Dung nham thú 【 hai hợp nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Đây là... Tiên Thiên thai độc!"),t("br"),t("br"),n._v("Cũng chính là Tiên Thiên độc chủng!"),t("br"),t("br"),n._v("Nhận ra độc chủng loại, Trương Huyền đều sắp điên rồi."),t("br"),t("br"),n._v("Tiên Thiên thai độc, là chỉ ra tại còn không sinh ra, tại mẫu bào thai bên trong ở giữa độc, hòa vào huyết dịch cùng linh hồn, hầu như khó giải."),t("br"),t("br"),n._v("Ngươi xem nhân gia Triệu Nhã, Tiên Thiên Thuần Âm thân thể, sau khi mở ra, oai phong lẫm liệt, khác nào nữ hoàng. Viên Đào Tiên Thiên Long Tê huyết mạch, sau khi mở ra, phòng ngự vô địch, khác nào hình người khiên thịt..."),t("br"),t("br"),n._v("Vốn là vẫn ước ao, hiện tại phát giác, hắn cũng có cái Tiên Thiên, chỉ có điều..."),t("br"),t("br"),n._v("Đồng dạng là Tiên Thiên, khác biệt làm sao lớn như vậy chứ?"),t("br"),t("br"),n._v("Người khác Tiên Thiên cũng là muốn người tu luyện Nhất Phi Trùng Thiên, mà của hắn Tiên Thiên nhưng là đòi mạng..."),t("br"),t("br"),n._v("“Tiên Thiên thai độc, chưa sinh ra liền loại ở trong người đặc thù độc chủng, nương theo cơ thể mẹ, cùng sinh mệnh triệt để dung hợp, thuộc về Tiên Thiên thai khí không đủ. Bên trong loại độc chất này người, tuổi thọ thông thường không sống hơn ba mươi, là chết trẻ thân thể... Khó giải.”"),t("br"),t("br"),n._v("Trương Huyền khóc không ra nước mắt."),t("br"),t("br"),n._v("Người khác đều là Tiên Thiên trâu bò thân thể, ta nhưng là Tiên Thiên chết trẻ thân thể..."),t("br"),t("br"),n._v("Chết trẻ ngươi muội a!"),t("br"),t("br"),n._v("Còn có thể hay không thể vui vẻ chơi đùa?"),t("br"),t("br"),n._v("Không tới ba mươi, há không biểu hiện, cũng chỉ có thời gian mười năm có thể sống? Để cho cuộc sống của hắn đã không hơn nhiều..."),t("br"),t("br"),n._v("Cố nén phiền muộn, lại sẽ tàng thư trong kho hết thảy thư tịch đều tìm tòi một lần, tìm ra bảy, tám bản ghi chép Tiên Thiên thai độc, kết quả... Đều cùng trong cơ thể hắn đạo kia độc khí, giống như đúc."),t("br"),t("br"),n._v("Nói cách khác, không cần tồn tại may mắn, trúng loại kịch độc này không thể nghi ngờ."),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("“Có thể bên trong tại cơ thể mẹ, truyền tới hậu bối thân thể, còn không bị phát hiện, loại này dùng độc thủ đoạn, ít nhất cũng phải đạt được bảy sao trở lên, ta liền một đứa cô nhi, không đến mức đi...”"),t("br"),t("br"),n._v("Xác định độc trong người là cái gì, phiền muộn chốc lát, Trương Huyền cũng tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Nhìn Độc điện nhiều như vậy thư tịch, đối với dùng độc cũng có cực sâu hiểu rõ, cho cơ thể mẹ bên trong hạ Tiên Thiên thai độc, còn không bị phát hiện, chất độc này sư, ít nhất cũng phải đạt được thất tinh trở lên."),t("br"),t("br"),n._v("Nói cách khác, chí ít là vị bát tinh thậm chí cửu tinh."),t("br"),t("br"),n._v("Loại này cấp bậc độc sư, quang tu vi cũng đã Thông Thiên, đạt được người chỗ không thể mức độ, làm sao sẽ tiêu hao tâm huyết, đối với hắn cái này cô nhi ra tay?"),t("br"),t("br"),n._v("Của hắn tiền thân, chỉ là sinh sống ở Thiên Huyền Vương thành loại địa phương nhỏ này tiểu nhân vật, không cha không mẹ, nghèo một đồng tiền đều không đáng, cũng là mười tám tuổi phía trước, thiên phú hơi hơi cao hơn một chút, sau đó bình thường chí cực, càng được khen là Hồng Thiên học viện từ trước tới nay kém cỏi nhất giáo sư."),t("br"),t("br"),n._v("Nhân vật như vậy, dĩ nhiên để một vị đứng ở cao nhất độc sư, tự mình động thủ đối phó... Khó tránh khỏi có chút khó mà tin nổi chứ?"),t("br"),t("br"),n._v("“Loại độc chất này, cùng toàn thân dung hợp, tuy hai mà một, tình huống bình thường, mãi đến tận sắp chết, đều phát giác không được. Ta tu luyện Thiên Đạo chân khí, quá mức tinh khiết, mới đem bức hiển lộ hình thái, bất quá, coi như hiển lộ, cũng không có cách nào trị liệu...”"),t("br"),t("br"),n._v("Trong lòng một hồi bất đắc dĩ."),t("br"),t("br"),n._v("Lần thứ nhất phát giác này độc thời điểm, còn tưởng rằng có người muốn mưu sát, tiền thân chính là bị giết chết, bây giờ nhìn lại, lo xa rồi."),t("br"),t("br"),n._v("Này độc nương theo vóc người này khu vừa sinh ra thì có, ẩn giấu ở trong cơ thể, cửu tinh độc sư đều phát giác không được, có thể nói giết người trong vô hình, coi như chết rồi, cũng không biết ai ra tay. Vốn là coi như mình là độc sư, cũng rất khó phát giác, nhưng hắn tu luyện Thiên Đạo chân khí, quá mức tinh khiết, chảy qua kinh mạch, huyết dịch, bất luận cái gì dơ bẩn đều không thể ẩn giấu, lúc này mới để cho hiển lộ hình thái."),t("br"),t("br"),n._v("Có thể hiển lộ ra cũng vô dụng, đối phương đi qua cơ thể mẹ thai nghén, cùng người như thế, cũng có linh trí, coi như Thiên Đạo chân khí, đều không thể thanh trừ, trái lại nương theo thời gian chuyển dời, chỉ biết trở nên càng ngày càng lớn mạnh, tốt bảo đảm bản thân ba mươi tuổi phía trước hẳn phải chết."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Chuyện này là sao, người khác xuyên qua, cõng lấy ngón tay vàng, vênh váo hò hét, tự mình cõng cái bom hẹn giờ, hơn nữa bảo vệ ngươi hẳn phải chết loại này..."),t("br"),t("br"),n._v("Xem ra tiền thân tuy rằng không phải độc phát mà chết, cũng gần như, tâm tình phiền muộn bên dưới, một lòng muốn chết, này nói độc khí liền sớm phát tác, chỉ là không nghĩ tới bản thân xuyên việt tới."),t("br"),t("br"),n._v("Cho tới trước thiên tài, sau đó hạng xoàng xĩnh, tu vi không tiếp tục tăng trưởng, cũng cùng đồ chơi này có quan hệ."),t("br"),t("br"),n._v("Tiền thân tại Chân Khí cảnh đỉnh phong phía trước tốc độ tu luyện cực nhanh, được gọi là trăm năm hiếm có, xung kích Bì Cốt cảnh nhưng thủy chung không cách nào thành công, chính là bởi vì này độc cùng toàn thân dung hợp, đối với hắn vẩn đục chân khí xuất hiện cực mạnh áp chế."),t("br"),t("br"),n._v("Trừ phi đạt được Thiên Đạo chân khí loại này độ tinh khiết, bằng không, lấy Thiên Huyền vương quốc loại công pháp kia tu luyện ra chân khí vẩn đục đo, không thể mở ra ràng buộc, đạt được càng cao hơn thực lực."),t("br"),t("br"),n._v("“Là độc, khẳng định có giải, bằng không, loại này Tiên Thiên độc chủng, thì lại làm sao bị người ta biết? Khả năng là cái này Độc điện đẳng cấp quá thấp, chỉ cần đi vào cấp bậc càng cao hơn địa phương, có lẽ liền có biện pháp...”"),t("br"),t("br"),n._v("Tuy rằng phiền muộn, Trương Huyền vẫn chưa bị hù ngã."),t("br"),t("br"),n._v("Lĩnh ngộ Thiên Đạo độc công, đối với độc đã có rất sâu kiến giải."),t("br"),t("br"),n._v("Có nhân có quả, có độc liền có giải dược, Tiên Thiên thai độc, tuy rằng nơi này thư tịch đánh dấu khó giải, không có nghĩa là thật sự không có cách nào giải quyết, có lẽ cấp bậc càng cao hơn Độc điện, sẽ có phương pháp giải quyết."),t("br"),t("br"),n._v("“Loại này Tiên Thiên thai độc, quy định ba mươi tuổi hẳn phải chết, trước thì sẽ không phát tác, nói cách khác... Nhất định phải tại mười năm này bên trong, tìm tới phương pháp giải quyết!”"),t("br"),t("br"),n._v("Tâm như chỉ thủy, giống như đao mang, đem tâm tình tiêu cực chém sạch sẽ, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Sống lại lại đây bất quá hơn một tháng, liền theo Chân Khí cảnh đỉnh phong đạt được nửa bước Tông sư, mười năm sau đó, còn không biết có thể đi đến mức nào. Đến lúc đó, chất độc này, cũng chưa chắc giải quyết không được."),t("br"),t("br"),n._v("Không cần thiết khẩn trương."),t("br"),t("br"),n._v("Bất quá, tuy rằng không sốt sắng, cũng là một loại áp lực."),t("br"),t("br"),n._v("Lý luận cùng thực tế vẫn có chênh lệch, hắn cũng không muốn thật vất vả lại lần nữa sống nhân sinh, vô duyên vô cớ liền bỏ mình."),t("br"),t("br"),n._v("“Đi đem Cổ Mục kêu đến!”"),t("br"),t("br"),n._v("Đứng dậy, đối với ngoài cửa bảo vệ Đại Dược Vương mấy người bàn giao một tiếng."),t("br"),t("br"),n._v("“Sư thúc tổ, ngươi tìm ta?”"),t("br"),t("br"),n._v("Thời gian không lâu, Cổ Mục đi tới."),t("br"),t("br"),n._v("“Ngồi!” Trương Huyền vẫy tay, nhìn lại: “Tìm ngươi tới, là có chuyện muốn còn muốn hỏi!”"),t("br"),t("br"),n._v("Cổ Mục ngồi nghiêm chỉnh: “Sư thúc tổ mời nói!”"),t("br"),t("br"),n._v("Mấy ngày nay, trước mắt vị này rút không thay hắn làm lần thứ hai trị liệu, sau khi kết thúc, rõ ràng cảm thấy tinh lực tăng nhiều, tựa hồ một lần nữa trở lại mười năm trước thể lực cao nhất thời kì, nắm giữ dùng mãi không hết sức mạnh."),t("br"),t("br"),n._v("Liên tục hai lần trị liệu, hắn biết trước mắt vị sư thúc này tổ thủ đoạn, có thể nói Thiên nhân, trong lòng từ lâu phục sát đất, không dám suy nghĩ nhiều."),t("br"),t("br"),n._v("“Ngươi có thể nghe qua Tiên Thiên thai độc?” Trương Huyền mở miệng."),t("br"),t("br"),n._v("Cổ Mục đến từ Hiên Viên vương quốc độc sư phân bộ, xem qua càng cao thâm thư tịch, tại kiến thức bên trên, khẳng định so với hắn cái này mới vừa xem xong ghi chép nửa vời mạnh hơn nhiều."),t("br"),t("br"),n._v("“Tiên Thiên thai độc?”"),t("br"),t("br"),n._v("Cổ Mục chần chờ một chút: “Loại độc chất này ta nghe qua, là theo mẫu bào thai trung hạ kịch độc, nương theo đứa bé trưởng thành, cùng sinh mệnh triệt để dung hợp, không có cách nào giải quyết!”"),t("br"),t("br"),n._v("“Khó giải?”"),t("br"),t("br"),n._v("“Vâng!” Cổ Mục gật gù, đột nhiên nghĩ tới điều gì, trên mặt mang theo chần chờ: “Bất quá...”"),t("br"),t("br"),n._v("“Tuy nhiên làm sao?” Trương Huyền nhìn sang."),t("br"),t("br"),n._v("“Ta nghe qua một tin đồn, không biết là thật hay giả!”"),t("br"),t("br"),n._v("Do dự một chút, Cổ Mục nói: “Có người nói, trong lịch sử lợi hại nhất Thiên nhân Khổng sư, ở giữa Tiên Thiên thai độc, bất quá, cuối cùng làm sao giải không biết, ngược lại hắn đầy đủ sống một vạn tuổi, hai tay buông xuôi.”"),t("br"),t("br"),n._v("Thiên nhân Khổng sư, được gọi là danh sư đệ nhất nhân, Danh Sư đường sáng lập người, thụ vô số người kính ngưỡng, chỉ cần là danh sư, dù cho học đồ, không ai không biết."),t("br"),t("br"),n._v("“Ồ?”"),t("br"),t("br"),n._v("Trương Huyền trước cũng nghe Hoàng Ngữ đã nói một lần, lòng sinh kính ngưỡng."),t("br"),t("br"),n._v("Chỉ là không nghĩ tới, vị này tiền bối, lại giống như chính mình, cũng trúng qua Tiên Thiên thai độc."),t("br"),t("br"),n._v("Đã có quá có thể giải tiền lệ, liền nói rõ này độc cũng không phải là khó giải."),t("br"),t("br"),n._v("“Đây chỉ là truyền thuyết, muốn biết Khổng sư sự tình, e sợ cần phải đi Danh Sư đường, chỉ có chân chính danh sư, mới có thể biết. Chúng ta loại độc chất này sư, cũng chỉ nghe qua nghe đồn... Không biết sư thúc tổ hỏi cái này để làm gì?” Cổ Mục nghi ngờ nói."),t("br"),t("br"),n._v("“Chỉ là thuận miệng hỏi một chút!”"),t("br"),t("br"),n._v("Vung vung tay, Trương Huyền trong lòng có chủ ý, xem đến chuyện bên này kết thúc, muốn đi Danh Sư đường lượn một vòng."),t("br"),t("br"),n._v("Các chức nghiệp xuất sắc nhân vật cuộc đời, đều sẽ tùy theo chuyên gia sáng tác thành sách, ở lại mỗi cái công hội, lấy cung cấp hậu nhân chiêm ngưỡng học tập."),t("br"),t("br"),n._v("Vị này Khổng sư nếu là danh sư lão tổ, Danh Sư đường liền chắc là có của hắn sự tích."),t("br"),t("br"),n._v("Nếu như thật sự có Tiên Thiên độc bào thai, đồng thời cuối cùng hai tay buông xuôi, liền nói rõ xác thực giải quyết."),t("br"),t("br"),n._v("Có thành công tiền lệ, cũng là không cần sốt sắng."),t("br"),t("br"),n._v("“Độc điện tình huống thế nào rồi?”"),t("br"),t("br"),n._v("Có tính toán, không ở nhiều lời, Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Đi qua mấy vòng sát hạch, ta đã xác định tân Điện chủ ứng cử viên, chính là đệ nhất phó điện chủ Liêu Huân! Xích Diễm liên tử xấp xỉ cũng đã thành thục, ngày mai là có thể hái.”"),t("br"),t("br"),n._v("Cổ Mục trả lời."),t("br"),t("br"),n._v("“Ngày mai liền có thể? Tốt!” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Tri thức học xong, cũng biết độc chủng loại, không cần thiết tiếp tục đợi ở chỗ này, mau chóng rời khỏi tốt nhất."),t("br"),t("br"),n._v("Cổ Mục rời đi, Trương Huyền tiếp tục học tập trong thư viện thư tịch nội dung, đồng thời cũng củng cố sức mạnh trong cơ thể."),t("br"),t("br"),n._v("Hồng Liên thành sưu tập Tông sư cảnh công pháp, tuy rằng không đủ để hình thành Thiên Đạo bí tịch, lại cũng không có thiếu chính xác câu nói, có thể thử tu luyện."),t("br"),t("br"),n._v("Đi qua liên tục thử nghiệm, rõ ràng cảm nhận được trong cơ thể nửa bước Tông sư cảnh chân khí, càng thêm hùng hậu, khoảng cách chân chính Tông sư cũng chỉ có cách một tia, bất cứ lúc nào cũng sẽ phá cửa mà vào."),t("br"),t("br"),n._v("Tuy rằng có Thiên Đạo thư viện nhìn ra thiếu hụt, nhưng muốn đi càng xa hơn, hay là muốn cơ sở vững chắc, bởi vậy, chỉ cần nhàn rỗi, hắn đi học tập, ngăn ngắn thời gian mười ngày, tri thức đo so với phía trước gia tăng rồi gấp đôi không ngừng, thuần thú, độc sư, đều có rất sâu hiểu rõ, hiện tại coi như không nhờ vả thư viện, cũng được xem là bác học nhiều nhận thức."),t("br"),n._v(" Học tập đồng thời, cũng đem Thiên Đạo quyền pháp, thoái pháp, thương pháp một lần nữa tu luyện một lần."),t("br"),t("br"),n._v("Những cái này chiêu số cứ việc đều là trụ cột nhất, nhưng có thể đem trong cơ thể sức mạnh hoàn mỹ phát huy được, để lực công kích của hắn, tăng cường không ít, hắn bây giờ, nếu như dùng ra Thiên Đạo quyền pháp cùng chiêu số, coi như cùng một ít Tông sư cảnh trung kỳ đều có thể đối kháng, thậm chí đem chém giết."),t("br"),t("br"),n._v("Đương nhiên, nếu như mượn Thiên Đạo thư viện thiếu sót năng lực, tiến hành lợi dụng lại phối hợp Thiên Đạo thân pháp, liều mạng bị thương, Tông sư cảnh đỉnh phong đều có khả năng giết chết."),t("br"),t("br"),n._v("Nửa bước Tông sư, có thể chém giết Tông sư đỉnh phong, loại này vượt cấp năng lực chiến đấu, e là cho dù Danh Sư đường nổi danh nhất thiên tài, đều khó mà so với."),t("br"),t("br"),n._v("“Sư thúc tổ, Xích Diễm liên tử dĩ nhiên thành thục!”"),t("br"),t("br"),n._v("Thời gian đang không ngừng tu luyện, học tập bên trong vượt qua, ngày thứ hai trời vừa sáng, Cổ Mục liền đến đến trước mặt."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật gù, đứng dậy."),t("br"),t("br"),n._v("Để người đột phá Chí Tôn đỉnh phong tỷ lệ tăng cường 20% bảo vật, đợi mười ngày liền vì cái này, rốt cục... Có thể hái!"),t("br"),t("br"),n._v("“Hồng Liên sơn mạch là cái loại cỡ lớn núi lửa, chúng ta Độc điện phân bộ liền xây dựng ở miệng núi lửa bên trên. Lúc trước xây dựng phân bộ thời điểm, chuyên môn mời trận pháp Đại sư, đem núi lửa dùng trận pháp niêm phong lại, đem trong đó địa tâm hỏa, hoàn chỉnh tập trung đứng dậy, dùng để Xích Diễm liên sinh trưởng.”"),t("br"),t("br"),n._v("Liêu Huân tuy rằng trở thành chính thức Điện chủ, nhưng ở hai người trước mặt, vẫn như cũ không dám trang đại, một bên tẩu biên giới thiệu, thái độ cung kính: “Đặc sứ, sư thúc tổ, mời tới bên này!”"),t("br"),t("br"),n._v("Đối với Trương Huyền, hắn cũng theo Cổ Mục xưng hô, kêu một tiếng “Sư thúc tổ”."),t("br"),t("br"),n._v("Ba người một đường tiến lên, rất nhanh đi tới một cái tế đàn."),t("br"),t("br"),n._v("“Miệng núi lửa liền bị ẩn giấu ở phía dưới này!”"),t("br"),t("br"),n._v("Liêu Huân chỉ về phía trước."),t("br"),t("br"),n._v("Trương Huyền nhìn sang."),t("br"),t("br"),n._v("Trước mắt là cái to lớn tế đàn, đường kính có tới hơn trăm thước, từ xa nhìn lại, mang theo phong cách cổ xưa mùi vị, cùng cái gọi là miệng núi lửa, nửa điểm đều không móc nối, trái lại tượng một cái hoang phế rất lâu nơi."),t("br"),t("br"),n._v("Trong lòng kỳ quái, bất quá có trước đi tới Độc điện trải qua, biết nơi này khẳng định có trận pháp, cũng không nói nhiều."),t("br"),t("br"),n._v("Quả nhiên, Liêu Huân đi tới trong tế đàn ở giữa, xoay cổ tay một cái, đại biểu Điện chủ thân phận lệnh bài xuất hiện tại lòng bàn tay, quân lệnh bài tại chính giữa tế đàn rãnh một thả."),t("br"),t("br"),n._v("Ầm ầm ầm!"),t("br"),t("br"),n._v("Một hồi trời đất quay cuồng, cảnh tượng trước mắt lập tức xuất hiện biến hóa."),t("br"),t("br"),n._v("Trước bằng phẳng rộng rãi tế đàn xuất hiện một cái hướng phía dưới lan tràn cầu thang, không biết kéo dài tới nơi nào."),t("br"),t("br"),n._v("“Mời tới bên này!”"),t("br"),t("br"),n._v("Liêu Huân đi đầu dẫn đường, ba người dọc theo cầu thang đi rồi gần tới một canh giờ, một cái rộng rãi lòng đất cung điện xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Cung điện chính giữa, là cái to lớn miệng núi lửa, cực nóng hỏa diễm cháy hừng hực, sóng nhiệt tập kích người, để người khó có thể tiếp cận."),t("br"),t("br"),n._v("Cảm nhận được trong đó nhiệt lượng, Trương Huyền vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Còn không tiến vào miệng núi lửa, chỉ bằng vào trong đại điện nhiệt khí, Ích Huyệt cảnh cường giả đi vào, chỉ sợ cũng không chịu nổi."),t("br"),t("br"),n._v("“Đặc sứ, sư thúc tổ, cái kia chính là Xích Diễm liên!”"),t("br"),t("br"),n._v("Đi về phía trước mấy bước, đi tới miệng núi lửa, Liêu Huân lại lần nữa chỉ về phía trước."),t("br"),t("br"),n._v("Thuận ngón tay hắn phương hướng nhìn lại, lập tức nhìn thấy núi lửa bên trong, cực nóng dung nham bên trong, một đóa màu đỏ thắm hoa sen, trôi nổi trong đó, bên trên một cái đài sen, kết đầy hạt sen, tỏa ra nhàn nhạt mùi thơm."),t("br"),t("br"),n._v("“Thật là đẹp!”"),t("br"),t("br"),n._v("Trương Huyền con mắt không nhịn được sáng ngời."),t("br"),t("br"),n._v("Trước mắt Xích Diễm liên, tuy rằng chỉ có hai, cao ba mét, toàn thân đỏ đậm, khác nào bốc cháy hỏa diễm."),t("br"),t("br"),n._v("Cách bọn họ vị trí, cách xa nhau không dưới mấy trăm mét, vẫn như cũ có thể cảm nhận được trong đó hung mãnh cực nóng cảm giác, đừng nói hắn thực lực như vậy, e là cho dù Chí Tôn cảnh cường giả, e sợ đều khó có thể chịu đựng."),t("br"),t("br"),n._v("“Cái này làm sao hái?” Trương Huyền hỏi."),t("br"),t("br"),n._v("Không những Xích Diễm liên cực nóng khó nhịn, then chốt nó còn trôi nổi tại miệng núi lửa ngay phía trên, cắm rễ tại dung nham bên trong, khoảng cách bên bờ đủ có mấy trăm mét, làm sao đủ đến?"),t("br"),t("br"),n._v("Tiến vào dung nham hái?"),t("br"),t("br"),n._v("Coi như Thiên Đạo Kim thân không kém, vừa tiến vào trong, cũng sẽ bị khoảnh khắc đốt thành tro bụi!"),t("br"),t("br"),n._v("Chớ nói chi là những người khác."),t("br"),t("br"),n._v("“Ta cũng không biết, sư phụ ta chính là thấy hạt sen sắp thành thục, muốn hái, kết quả bị thiêu thành tro tàn...” Liêu Huân lắc đầu, trên mặt lộ ra cô đơn vẻ."),t("br"),t("br"),n._v("Lão Điện chủ đột nhiên chết bất đắc kỳ tử, chính là vì muốn hái Xích Diễm liên tử."),t("br"),t("br"),n._v("“Xích Diễm liên tử mỗi hơn trăm năm sẽ thành thục một lần, mỗi lần đều có người thành công, tổng bộ cũng có thể thu được các ngươi cống phẩm, làm sao sẽ bị thiêu chết?”"),t("br"),t("br"),n._v("Cổ Mục nghi hoặc nhìn sang."),t("br"),t("br"),n._v("Nếu cái này Độc điện phân bộ xây dựng ở này, bảo vệ gốc này hoa sen, liền khẳng định có biện pháp, bằng không, dùng vô số dược liệu cung dưỡng, không phải là để cho người khác sử dụng?"),t("br"),t("br"),n._v("Lại nói, cái này phân bộ trước cũng mỗi cách trăm năm hướng tổng bộ cung cấp số lượng nhất định hạt sen, không thể liền hái cũng không có phương pháp đi."),t("br"),t("br"),n._v("“Phương pháp là có, bất quá, đã không cách nào sử dụng, hai vị mời xem!”"),t("br"),t("br"),n._v("Liêu Huân cười khổ một tiếng, tiện tay nhặt lên một tảng đá, bàn tay run lên, thẳng tắp đối với Xích Diễm liên bắn tới."),t("br"),t("br"),n._v("Hắn là Tông sư cảnh đỉnh phong cường giả, chân khí gia trì xuống, tảng đá phát ra Ô u Ô u tiếng, thẳng tắp vọt tới trước, tựa hồ đem không khí đều xé nát."),t("br"),t("br"),n._v("“Ngươi muốn làm gì...”"),t("br"),t("br"),n._v("Thấy hắn trực tiếp đối với Xích Diễm liên ném ra tảng đá, hơn nữa như vậy sức mạnh khổng lồ, Cổ Mục biến sắc mặt."),t("br"),t("br"),n._v("Lúc này Xích Diễm liên tử đã thành thục, một khi bị đánh trúng, rất dễ dàng rơi vào phía dưới dung nham, thật muốn rơi vào đi, lại muốn tìm ra đến, liền hầu như không thể."),t("br"),t("br"),n._v("“Đặc sứ nhìn chính là!” Liêu Huân nói tiếp."),t("br"),t("br"),n._v("Hai người nói chuyện công phu, tảng đá đã đi tới Xích Diễm liên trước mặt, ngay lúc sắp đánh trúng hoa sen, đột nhiên phía dưới dung nham một hồi kịch liệt lay động, một đầu quái dị đồ vật bay lơ lửng lên trời, há mồm liền đem tảng đá nuốt xuống."),t("br"),t("br"),n._v("Con này quái dị đồ vật, hình thể như xà, đủ có dài mấy chục mét ngắn, toàn thân bị dày đặc vảy che đậy, đầy người đỏ tươi, dạo chơi tại dung nham bên trong, vô cùng quái dị."),t("br"),t("br"),n._v("“Đây là cái gì?”"),t("br"),t("br"),n._v("Không nghĩ tới như vậy cực nóng dung nham bên trong, dĩ nhiên có như thế quái lạ đồ vật dạo chơi, Cổ Mục nhớ tới cái gì, hơi thay đổi sắc mặt: “Lẽ nào chính là các ngươi trước theo tổng bộ nói dung nham thú?”"),t("br"),t("br"),n._v("Nhìn thấy vật này, hắn nghĩ tới."),t("br"),t("br"),n._v("Hồng Liên phân bộ báo lên trong tài liệu, không chỉ có lão Điện chủ chết bất đắc kỳ tử, còn có một loại man thú ghi chép, có người nói vật này sinh sống ở dung nham bên trong, được gọi là dung nham thú."),t("br"),t("br"),n._v("Vốn là hắn cho rằng là lời nói vô căn cứ, đối phương chỉ là không muốn nộp lên Xích Diễm liên tử mà tìm mượn cớ, không nghĩ tới, lại là thật sự."),t("br"),t("br"),n._v("“Vâng, đây chính là dung nham thú, bên ngoài thân đặc thù vảy có thể chịu nổi được nhiệt độ cao, sinh sống ở dung nham bên trong, thực lực có thể so với Chí Tôn cảnh đỉnh phong cường giả...” Liêu Huân cười khổ."),t("br"),t("br"),n._v("“Còn có thứ này?”"),t("br"),t("br"),n._v("Không những Cổ Mục kinh ngạc, liền ngay cả Trương Huyền cũng tràn đầy kỳ quái."),t("br"),t("br"),n._v("Phía dưới dung nham nhiệt lượng, tuyệt đối đạt được một hai ngàn đo, như thế cực nóng tình huống, vẫn còn có sinh mệnh tồn tại, hơn nữa còn khổng lồ như vậy, không tận mắt nhìn thấy, căn bản không thể tin được."),t("br"),t("br"),n._v("“Đúng đấy, vật này không biết từ nơi nào nhô ra, cũng chẳng biết lúc nào đến, những năm này vẫn bảo vệ Xích Diễm liên hoa, đem xem là đồ vật của chính mình, bất luận người nào một khi nghĩ biện pháp tiếp cận, đều phải bị công kích!”"),t("br"),t("br"),n._v("Liêu Huân giải thích: “Nếu như tại trên bờ cũng còn tốt chút, trong nham tương mặt vốn là thiên hạ của nó, hơn nữa... Nó còn đạt được Chí Tôn đỉnh phong, chúng ta thực sự không thể ra sức!”"),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("Cổ Mục cũng nặng nề xuống."),t("br"),t("br"),n._v("Hắn tuy rằng cũng là Chí Tôn đỉnh phong, nhưng muốn tại dung nham trung hoà như vậy một cái tên to xác giao đấu... Tám chín phần mười sẽ ngã xuống ở đây."),t("br"),t("br"),n._v("“Dùng độc không được sao?”"),t("br"),t("br"),n._v("Không nhịn được hỏi."),t("br"),t("br"),n._v("Độc điện tinh thông dùng độc, hoàn chỉnh có thể mang hắn độc giết."),t("br"),t("br"),n._v("“Dung nham thú liền như vậy cực nóng địa tâm hỏa cũng không sợ, một ít độc dược lại tại sao có thể có tác dụng...” Liêu Huân nói."),t("br"),t("br"),n._v("Một hai ngàn đo dung nham đều có thể bơi lội, một ít độc dược khả năng còn không tới gần, liền đốt thành tro bụi, chớ nói chi là đem độc chết."),t("br"),t("br"),n._v("“Các loại phương pháp đều từng thử, trừ phi... Tìm cái thuần thú sư, nghĩ biện pháp đem thuần phục, bằng không... Căn bản không những phương pháp khác!” Liêu Huân nói đến tiếp tục lắc đầu: “Bất quá... Nơi này là chúng ta to lớn nhất cơ mật, hơn nữa chúng ta Độc điện cũng cùng những nghề nghiệp khác không làm sao liên hệ, thật muốn tìm một cái thuần thú sư lại đây, rất dễ dàng xuất hiện phiền phức, mang đến không cần thiết tai hoạ!”"),t("br"),t("br"),n._v("Độc sư cùng những nghề nghiệp khác không móc nối, thậm chí còn có quan hệ thù địch."),t("br"),t("br"),n._v("Xin những nghề nghiệp khác lại đây, chẳng khác nào bại lộ sào huyệt vị trí, rất dễ dàng mang đến tính chất hủy diệt tai nạn, ẩn giấu ở Hồng Liên sơn mạch nhiều năm như vậy, cũng là lãng phí."),t("br"),t("br"),n._v("“Đúng đấy...”"),t("br"),t("br"),n._v("Cổ Mục cũng biết điểm ấy, không nhịn được lắc lắc đầu."),t("br"),t("br"),n._v("Độc dược không được, lại không xông tới được, thuần thú sư lại không thể tìm... Muốn hái Xích Diễm liên tử, xác thực là một vấn đề khó khăn không nhỏ."),t("br"),t("br"),n._v("“Thuần thú sư có thể thuần phục cái tên này?”"),t("br"),t("br"),n._v("Nghe được hai người đối thoại, Trương Huyền chần chờ một chút, nói."),t("br"),t("br"),n._v("“Có lẽ... Ta có thể thử xem!”"),t("br"),t("br"),n._v("(Hai hợp nhất đại chương tiết, buổi tối còn có chương mới, nhanh cuối tháng, còn có vé tháng sao?)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-291"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-293"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);