(window.webpackJsonp=window.webpackJsonp||[]).push([[1515],{1868:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-286-thien-vu-vuong-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-286-thien-vu-vuong-thanh"}},[n._v("#")]),n._v(" Chương 286: Thiên Vũ vương thành")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thiên Vũ vương quốc, quốc thổ đường kính mấy vạn km, diện tích lãnh thổ bao la."),t("br"),t("br"),n._v("Vì có nhị tinh danh sư trấn thủ, được bầu thành nhất đẳng vương quốc."),t("br"),t("br"),n._v("Thiên Huyền, Bắc Vũ, Hàn Vũ, Bắc Đẩu, Càn Viễn, An Viễn, Bách Lý... Mười hai cái vương quốc cùng với lân cận, để cho bên trong Thiên Vũ vương thành thành chu vi mấy trăm ngàn km tu luyện cùng mậu dịch trung tâm, người người ngóng trông Thánh địa."),t("br"),t("br"),n._v("Không Vũ công chúa cưỡi Thanh Ưng Thú từ lâu rời đi, Khiếu Thiên thú rộng rãi lưng bên trên chỉ có hắn một cái, Trương Huyền cũng vui vẻ thanh nhàn, một bên học tập trong thư viện tri thức, một bên củng cố tu vi, tổng kết tình huống bây giờ."),t("br"),t("br"),n._v("“Nương theo tu vi tăng cường, Thiên Đạo Kim thân hiệu quả đã không tính là gì...”"),t("br"),t("br"),n._v("Thiên Đạo Kim thân tại Thông Huyền cảnh lúc còn có tác dụng rất lớn, để hắn vượt qua cùng cấp bậc không ít người, nhưng đạt đến nửa bước Tông sư, liền không tính là gì, một khi bước vào Tông sư cảnh, hiệu quả sẽ càng thêm nhỏ bé."),t("br"),t("br"),n._v("Hầu như không tính là cái gì."),t("br"),t("br"),n._v("Xem ra rảnh rỗi cần phải tìm một ít liên quan với thân thể công pháp, tiếp tục tu luyện."),t("br"),t("br"),n._v("Cho tới Thiên Đạo quyền pháp, thoái pháp, thân pháp... Tuy rằng đơn giản, uy lực lại không coi là nhỏ, nhìn dáng dấp tại Chí Tôn trước, sẽ không lạc hậu, cũng không cần phải gấp tìm kiếm bí tịch."),t("br"),t("br"),n._v("“Lần này đi Thiên Vũ vương thành, đầu tiên muốn sát hạch danh sư, chỉ có trở thành danh sư, mới có địa vị, tiến vào Danh Sư đường, lật xem liên quan với Khổng sư ghi chép...”"),t("br"),t("br"),n._v("Trên đường tới, hắn chuyên môn tại trống không thư tịch dâng thư viết nội dung, dùng Thiên Đạo thư viện kiểm tra, vị này Thiên nhân Khổng sư, giống như hắn, cũng trúng Tiên Thiên thai độc, cuối cùng thành công giải quyết."),t("br"),t("br"),n._v("Nói cách khác, cái này cái gọi là Tiên Thiên thai độc cũng không phải là khó giải, chỉ có điều lấy hắn hiện tại trình độ, cũng không tìm được phương pháp chính xác thôi."),t("br"),t("br"),n._v("Kiểm tra ra kết quả này, an tâm không ít, đồng thời tràn đầy nghi hoặc, hắn cái này tiền thân, chỉ là cái cô nhi, dựa vào cái gì có thể làm cho đường đường bát tinh, cửu tinh độc sư tự mình ra tay?"),t("br"),t("br"),n._v("Chẳng lẽ có cái gì nghịch thiên bối cảnh?"),t("br"),t("br"),n._v("Tuy rằng kỳ quái, nhưng cũng không đi suy nghĩ nhiều, trước tiên không nói tiền thân chỉ là được vứt bỏ cô nhi, coi như chính hắn, làm người “xuyên việt”, cũng không hy vọng đột nhiên nhô ra cái cha mẹ, cô bảy dì tám loại hình."),t("br"),t("br"),n._v("Cùng với chỉ tăng thêm buồn phiền, còn không bằng không thèm để ý."),t("br"),t("br"),n._v("Ngược lại nắm giữ Thiên Đạo thư viện tại tay, nương theo tu vi tăng cường, trời đất bao la, nơi nào không đi được!"),t("br"),t("br"),n._v("Đi rồi tiếp cận hai ngày, mắt thấy mặt trời liền muốn hạ xuống phía tây, một toà nguy nga thành thị xuất hiện ở trước mắt, chiếu rọi tại hỏa hồng hào quang bên dưới, như cùng một mảnh bao la sơn mạch, tầng tầng lớp lớp không biết lan tràn bao xa, không nhìn thấy phần cuối."),t("br"),t("br"),n._v("Thành phố này so Thiên Huyền Vương thành lớn hơn mấy chục lần không ngừng, đủ loại kiến trúc quy mô, xa hoa, cùng cao tới mấy chục mét cổ xưa tường thành so sánh, làm cho người ta một loại thương hải tang điền cảm giác."),t("br"),t("br"),n._v("Người ở trên không, nhìn xuống dưới, trong thành phố đường phố bốn phương thông suốt, dòng người như thoi đưa, đủ loại xe cộ, ngựa nối liền không dứt ra ra vào vào, nghiễm nhiên một bộ tuyệt mỹ tranh vẽ."),t("br"),t("br"),n._v("Có thể trở thành là mười ba đại vương quốc trung tâm giao dịch, cái này Thiên Vũ vương thành, quả nhiên khí thế rộng rãi, không phải trước đây gặp qua thành thị có thể so với."),t("br"),t("br"),n._v("“Đi xuống đi!”"),t("br"),t("br"),n._v("Biết loại này thành phố lớn, khẳng định đối với Khiếu Thiên thú loại này loại cỡ lớn man thú có hạn chế, Trương Huyền cũng không làm chim đầu đàn, còn chưa tới đến trước mặt liền rơi xuống, để nó tìm cái sơn mạch tự do hoạt động, mà bản thân thì nhanh chân hướng trước mắt nguy nga thành thị đi đến."),t("br"),t("br"),n._v("“Xin dừng bước! Vào thành cần giao nộp lệ phí vào thành!”"),t("br"),t("br"),n._v("Đi tới trước cửa thành, liền được một đội binh sĩ ngăn cản."),t("br"),t("br"),n._v("“Lệ phí vào thành?”"),t("br"),t("br"),n._v("Khoảng chừng nhìn một vòng, lúc này mới phát giác, chỉ cần vào thành, đều cần giao nộp nhất định phí dụng, nếu như không có loại này hạn chế, Thiên Vũ vương thành nhất định sẽ lại lần nữa mở rộng, liền ngay cả Thiên Vũ vương thất cũng không có cách nào đã khống chế."),t("br"),t("br"),n._v("“Không sai, trừ phi ngươi là danh sư, hoặc là học đồ, bằng không, coi như có những nghề nghiệp khác Đại sư, Tông sư, cũng nhất định phải giao nộp!”"),t("br"),t("br"),n._v("Binh sĩ nói."),t("br"),t("br"),n._v("Danh sư là thế giới tiến bộ căn cơ, địa vị tôn sùng, tại bất kỳ địa phương nào, đều sẽ phải chịu đãi ngộ đặc biệt, đây là những nghề nghiệp khác không cách nào hưởng thụ đến."),t("br"),t("br"),n._v("“Được rồi, với hắn phí lời nhiều như vậy làm gì, liền hắn cái tuổi này, làm sao có khả năng là danh sư hoặc là học đồ?”"),t("br"),t("br"),n._v("“Không sai, động tác nhanh lên một chút, phía sau những người khác còn chờ lắm!”"),t("br"),t("br"),n._v("Thấy hắn ở đây làm lỡ công phu, mặt khác hai tên lính đầy mặt thiếu kiên nhẫn, xếp hàng ở phía sau chờ vào thành mọi người, cũng nhao nhao lộ ra thúc giục tâm ý."),t("br"),t("br"),n._v("Trang cái gì trang?"),t("br"),t("br"),n._v("//truyencuatui."),t("br"),n._v("Ｎet/ Thiên Vũ vương thành từ mấy ngàn năm trước, vào thành liền muốn tiền, điều này cũng không biết, nơi nào nhô ra nhà quê?"),t("br"),t("br"),n._v("“Bao nhiêu tiền?”"),t("br"),t("br"),n._v("Không để ý tới ánh mắt của mọi người, Trương Huyền nhìn sang."),t("br"),t("br"),n._v("“100 kim tệ!” Binh sĩ nói."),t("br"),t("br"),n._v("“100?” Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Tại Hồng Thiên học viện làm lão sư, một tháng cũng là một hai ngàn kim tệ, ở đây, tiến cái thành liền phải hao phí một trăm, thật là đen."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Thấy hắn cả kinh một mới không từng va chạm xã hội dáng dấp, binh sĩ trên mặt lộ ra vẻ khinh thường."),t("br"),t("br"),n._v("Liền một trăm kim tệ đều như thế ngạc nhiên, nhìn chút chính là cái không tiền quỷ nghèo."),t("br"),t("br"),n._v("“Tiền ta không có...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu một cái."),t("br"),t("br"),n._v("Nghe nói như thế, binh sĩ trên mặt lộ làm ra một bộ liền biết ngươi sẽ như vậy nói biểu cảm, quát to một tiếng: “Không tiền liền đừng ở chỗ này vướng bận, còn muốn vào thành, nằm mơ đi...”"),t("br"),t("br"),n._v("Bất quá nói còn chưa dứt lời, liền trước mắt cái này muốn ăn đòn gia hỏa, lòng bàn tay chẳng biết lúc nào thêm ra một cái lệnh bài, đồng thời một mặt cao hứng nhìn sang: “Có cái này liền không cần giao tiền đi...”"),t("br"),t("br"),n._v("“Có cái gì đều vô dụng... A? Danh sư học đồ thân phận lệnh bài...”"),t("br"),n._v(" Lời nói nói phân nửa, nhận ra đối phương lệnh bài trong tay, binh sĩ thân thể run lên, muốn nói cũng nghẹn ở trong miệng, sắp khóc."),t("br"),t("br"),n._v("“Danh sư học đồ?”"),t("br"),t("br"),n._v("Vừa nãy hùng hùng hổ hổ mấy cái binh sĩ cũng nhìn thấy lệnh bài này, cũng tất cả đều lông mày nhảy loạn, sắc mặt trở nên trắng."),t("br"),t("br"),n._v("Vốn tưởng rằng là cái nhà quê, không nghĩ tới dĩ nhiên là cái mãnh nhân."),t("br"),t("br"),n._v("Đại ca, ngươi đều là danh sư học đồ, trực tiếp đi vào liền có thể, còn hỏi vào thành phí bao nhiêu, giả vờ giả vịt, này không phải cố ý gây chuyện chúng ta ư..."),t("br"),t("br"),n._v("“Mời đến...”"),t("br"),t("br"),n._v("Cố nén phiền muộn, mấy cái binh sĩ, vội vã nhường đường, không dám tiếp tục ngăn cản."),t("br"),t("br"),n._v("Gây chuyện danh sư học đồ, một khi cho vương thất biết, đừng nói chức vị, liền ngay cả tính mệnh cũng khó khăn bảo vệ."),t("br"),t("br"),n._v("Thấy lấy ra Lưu sư mấy người cho lệnh bài của hắn, quả nhiên có thể đi vào, Trương Huyền thoả mãn gật gù: “Không nghĩ tới đồ chơi này còn dùng rất tốt, một hồi liền bớt đi một trăm kim tệ...”"),t("br"),t("br"),n._v("“...” Bọn binh lính."),t("br"),t("br"),n._v("Gặp qua keo kiệt, chưa từng thấy như thế keo kiệt."),t("br"),t("br"),n._v("Đường đường danh sư học đồ, đi đến bất kỳ địa phương nào đều là bị người kính trọng tồn tại, bớt đi một trăm kim tệ, liền đầy mặt cao hứng..."),t("br"),t("br"),n._v("Ngươi xứng đáng ngươi như thế cao quý thân phận sao?"),t("br"),t("br"),n._v("Không để ý tới phiền muộn binh lính, Trương Huyền đi vào cửa thành."),t("br"),t("br"),n._v("Tuy rằng hiện tại trữ vật giới chỉ bên trong có mấy ngàn vạn kim tệ, nhưng hắn trong xương vẫn là tiểu nông tư tưởng, có thể tiết kiệm một phần là một phần, còn người khác cái nhìn..."),t("br"),t("br"),n._v("Liên quan gì đến ta!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trong thành đường phố rộng rãi, khắp nơi đều là mua đi gọi mua tiểu thương, cửa hàng chung quanh san sát, Tịch Dương Dư Huy chiếu xuống, cao to kiến trúc phản xạ hào quang, làm cho người ta một loại ấm áp cảm giác."),t("br"),t("br"),n._v("“Không hổ là Thiên Vũ vương thành, tùy ý một cái quầy hàng bán đồ vật liền so Thiên Huyền vương quốc cao cấp nhiều lắm!”"),t("br"),t("br"),n._v("Dọc theo đường phố về phía trước đi dạo, nhìn xung quanh đủ loại thương phẩm, Trương Huyền không kìm lòng được tặc lưỡi."),t("br"),t("br"),n._v("Hắn bây giờ, rồi cùng lưu mỗ mỗ tiến đại quan viên như thế, đối với rất nhiều thứ đều tràn ngập mới mẻ."),t("br"),t("br"),n._v("Tuy rằng không ít vật phẩm tại thư tịch bên trong đều từng thấy, tận mắt nhìn thấy vẫn có không giống nhau cảm thụ."),t("br"),t("br"),n._v("“Ngươi cũng là danh sư học đồ?”"),t("br"),t("br"),n._v("Đang tại khắp nơi quan sát, một giọng nói vang lên, quay đầu nhìn lại, liền thấy một cái hai mươi mấy tuổi nữ hài đứng ở cách đó không xa, vẻ mặt vô cùng nghi hoặc nhìn sang."),t("br"),t("br"),n._v("Cô bé này, trên người mặc trường bào màu xanh nhạt, dáng dấp không tính đẹp đẽ, tuy rằng cùng Thẩm Bích Như mấy người so, kém xa tít tắp, nhưng cũng chịu nổi xem, được xem là mỹ nữ."),t("br"),t("br"),n._v("Bên người nàng theo một ông già, rõ ràng thị vệ trang phục, khí tức thâm trầm, trong ánh mắt thỉnh thoảng tia điện chợt hiện qua, lại là một vị Tông sư cảnh cường giả."),t("br"),t("br"),n._v("Tông sư cường giả làm thị vệ, xem ra cô bé này, thân phận không thấp."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Đáp một tiếng, Trương Huyền cũng nhận ra, hai người hẳn là vào thành cửa lúc, theo sau lưng, bản thân đưa ra học đồ lệnh bài, được nhìn thấy, lúc này mới đuổi theo hỏi dò."),t("br"),t("br"),n._v("“Ta cũng là!”"),t("br"),t("br"),n._v("Nở nụ cười một tiếng, nữ hài lật bàn tay một cái, một cái lệnh bài xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Dĩ nhiên cũng là vị danh sư học đồ."),t("br"),t("br"),n._v("“Lợi hại!” Trương Huyền cảm khái."),t("br"),t("br"),n._v("Thiên Huyền vương quốc, Lục Tầm như thế ưu tú đều rất khó thành là học đồ, ở đây tùy ý gặp bên trên một cô gái chính là, đủ thấy chênh lệch."),t("br"),t("br"),n._v("Nghe đối phương khích lệ, nữ hài lộ ra thoả mãn biểu cảm, lại lần nữa nhìn về phía thanh niên trước mắt, thái độ rõ ràng so với phía trước tốt hơn rất nhiều: “Tại hạ sư từ nhất tinh danh sư Trình Hiên, không biết ngươi sư từ người phương nào?”"),t("br"),t("br"),n._v("“Lão sư ta không có gì tiếng tăm, nói ra ngươi cũng không biết, vẫn là không nói!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn căn bản không có lão sư, cái này học đồ lệnh bài cũng là Lưu sư mấy người cho, đành phải giả bộ ngớ ngẩn."),t("br"),t("br"),n._v("Thấy hắn không nói, nữ hài không truy hỏi nữa, mà là mang theo nghi hoặc nhìn sang: “Vào lúc này đến Vương thành, lẽ nào... Cũng là tham gia ?”"),t("br"),t("br"),n._v("“Vấn Đáp hội?” Trương Huyền sững sờ: “Cái kia là cái gì?”"),t("br"),t("br"),n._v("“Ngươi không biết?” Nữ hài giống xem quái vật nhìn sang: “Vấn Đáp hội là sát hạch danh sư cái cuối cùng quy trình, chỉ có thông qua trả lời, mới có thể có thể trở thành là chân chính danh sư, ngươi nếu là học đồ, làm sao có khả năng ngay cả điều này cũng không biết?”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy mê man."),t("br"),t("br"),n._v("Hắn dựa vào giả mạo “Dương sư” học đồ, mới đạt được học đồ lệnh bài, đối với sát hạch danh sư quy trình một chữ cũng không biết. Trước Lưu sư mấy người cũng chỉ nói sát hạch tư cách, cái khác vẫn chưa nói tỉ mỉ, bởi vậy cũng không biết, còn có cái trả lời."),t("br"),t("br"),n._v("“Đúng đấy!” Gật gù, nữ hài chần chờ một chút, như là nhớ ra cái gì đó, bỗng nhiên tỉnh ngộ: “Ta biết rồi, ngươi là mới vừa trở thành học đồ không lâu đi! Mới vừa trở thành học đồ, khoảng cách sát hạch chân chính danh sư còn không biết muốn học tập bao lâu, tự nhiên đối với làm sao sát hạch, biết đến không nhiều!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đừng nói hắn trở thành học đồ thời gian không lâu, đi tới thế giới này thời gian cũng không tới hai tháng, lại nói Thiên Huyền vương quốc liền cái danh sư, thậm chí ngay cả liên quan với danh sư thư tịch đều không có, làm sao sát hạch, đều có cái nào quy trình, có thể biết mới là lạ."),t("br"),t("br"),n._v("“Mới vừa trở thành học đồ cũng không cần phải gấp, hảo hảo học tập, chỉ cần trong vòng mười năm sát hạch thành công, đủ để làm rạng rỡ tổ tông!”"),t("br"),t("br"),n._v("Nghe hắn mới vừa trở thành học đồ, nữ hài thái độ lại lần nữa gần gũi hơn khá nhiều: “Dù sao chúng ta không giống Mạc Vũ công chúa như thế có thiên phú, mới hai mươi hai tuổi liền sát hạch danh sư thành công!”"),t("br"),t("br"),n._v("Nói tới Mạc Vũ công chúa, ánh mắt của nàng lập tức thả ra hào quang, để lộ ra nồng đậm sùng bái tâm ý."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-294"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-296"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);