(window.webpackJsonp=window.webpackJsonp||[]).push([[1516],{1869:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-289-thu-hoa-su-cong-hoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-289-thu-hoa-su-cong-hoi"}},[n._v("#")]),n._v(" Chương 289: Thư họa sư công hội")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Sát hạch danh sư thành công?”"),t("br"),t("br"),n._v("Trương Huyền nghi hoặc nhìn sang, Hồng Liên thành thời điểm, nàng không phải là học đồ sao?"),t("br"),t("br"),n._v("Bản thân trừ độc điện làm lỡ hai mươi ngày thời gian, lẽ nào vị công chúa này vừa về tới Thiên Vũ vương thành, liền bắt đầu sát hạch, hơn nữa còn thành công?"),t("br"),t("br"),n._v("“Mạc Vũ công chúa là Thiên Vũ vương quốc nổi danh nhất thiên tài một trong, nửa tháng phía trước xuất ngoại rèn luyện trở về, đầu tiên là sát hạch nhị tinh luyện đan sư thành công, lại đi sát hạch danh sư, thừa thế xông lên hoàn thành rất nhiều kiểm tra, liền còn sót lại ngày mai Vấn Đáp hội, chỉ cần thông qua, chẳng khác nào thành là chân chính nhất tinh danh sư!”"),t("br"),t("br"),n._v("Nữ hài tràn đầy hưng phấn: “Hai mươi hai tuổi nhất tinh danh sư, toàn bộ Thiên Vũ vương quốc, e sợ cũng là được gọi là thiên tài số một Mạc Hoằng Nhất mới có thể hơn một chút.”"),t("br"),t("br"),n._v("“Mạc Hoằng Nhất?”"),t("br"),t("br"),n._v("Hai mươi hai tuổi nhị tinh luyện đan sư, nhất tinh thuần thú sư, nhất tinh danh sư, y sư học đồ, lại còn không phải thiên tài số một?"),t("br"),t("br"),n._v("“Ngươi không biết?”"),t("br"),t("br"),n._v("Thấy hắn một mặt mê hoặc, nữ hài có chút không nói gì."),t("br"),t("br"),n._v("Không phải tận mắt đến của hắn học đồ lệnh bài, thật hoài nghi cái tên này có phải là thật hay không."),t("br"),t("br"),n._v("Không biết sát hạch danh sư quy trình, không biết  ngược lại cũng thôi..."),t("br"),t("br"),n._v("Liền Mạc Hoằng Nhất cũng không biết... Ngươi là từ đâu cái xó xỉnh nhô ra?"),t("br"),t("br"),n._v("“Hắn rất nổi danh?” Trương Huyền hỏi."),t("br"),t("br"),n._v("Nghe nói như thế, nữ hài cố nén một mặt phiền muộn, nói: “Ngươi sẽ không là... Lần đầu tiên tới Thiên Vũ vương thành đi!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Thấy hắn như thế thoải mái thừa nhận, liền thật không tiện đều không có, nữ hài lườm một cái."),t("br"),t("br"),n._v("Đổi làm người khác, lần đầu tiên tới thành phố lớn, nhất định sẽ đầy mặt ngượng ngùng, có loại tự ti cảm giác, cái tên này đến tốt, đầy mặt cây ngay không sợ chết đứng."),t("br"),t("br"),n._v("Thật giống như lần đầu tiên tới Thiên Vũ vương thành rất quang vinh như thế..."),t("br"),t("br"),n._v("Thật không biết từ đâu tới tự tin."),t("br"),t("br"),n._v("“Nếu ngươi không biết, ta liền nói cho ngươi nói!”"),t("br"),t("br"),n._v("Cô bé nói: “Mạc Hoằng Nhất, được gọi là Thiên Vũ vương quốc ngàn năm qua thiên tài số một, danh chấn mười ba quốc. Năm nay bất quá hai mươi sáu tuổi, đã là một vị nhất tinh đỉnh phong danh sư, nhị tinh luyện đan sư, nhị tinh trận pháp sư cùng nhị tinh luyện khí sư!”"),t("br"),t("br"),n._v("Trương Huyền con mắt trợn tròn."),t("br"),t("br"),n._v("Nhất tinh đỉnh phong danh sư, nhị tinh luyện đan sư, nhị tinh trận pháp sư, nhị tinh luyện khí sư..."),t("br"),t("br"),n._v("Hai mươi sáu tuổi, thân kiêm tứ đại nghề nghiệp!"),t("br"),t("br"),n._v("Nhìn như vậy đến, trước cái gọi là thiên tài Lục Tầm cùng với so sánh, hoàn chỉnh không ở một cái mức độ lên, thậm chí Mạc Vũ, đều chênh lệch thật lớn một đoạn."),t("br"),t("br"),n._v("Thấy hắn biểu cảm biến hóa, nữ hài lúc này mới thoả mãn gật gù, nói tiếp: “Không chỉ có như vậy, vị này lớn lao thiên tài, thiên phú tu luyện lên, cũng vô cùng khủng bố. Ba năm trước, mới vào Tông sư, liền đánh bại Tông sư trung kỳ thiết thụ Tướng quân. Càng là một người một kiếm, sẽ vì họa một phương thương bắc ngũ hùng chém giết! Phải biết năm người này đều là Tông sư cường giả, càng có ba vị đạt đến Tông sư trung kỳ! Bị hắn một người chém giết, không phải chính tai nghe, đều không thể tin được.”"),t("br"),t("br"),n._v("“Năm nay đạt đến Tông sư hậu kỳ, tại Thiên Tuyền phong khiêu chiến Tông sư đỉnh phong cường giả Hồng Nhược đại sư, trăm chiêu bên trong đem hắn đánh bại. Cũng tại tỷ thí bên trong, thành công đột phá, cũng đạt đến Tông sư đỉnh phong. Liền ngay cả quốc vương bệ hạ, cũng nói hắn là ngàn năm không gặp thiên tài, tương lai thành tựu vô hạn!”"),t("br"),t("br"),n._v("“Hai mươi sáu tuổi, Tông sư đỉnh phong? Còn có thể vượt cấp khiêu chiến?” Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Khó trách có thể được gọi là ngàn năm qua thiên tài số một, cái này Mạc Hoằng Nhất xác thực rất đáng sợ."),t("br"),t("br"),n._v("Cùng hắn so sánh, trước nhìn thấy cái gọi là thiên tài, xác thực chỉ cùng đom đóm như thế, không đáng nhắc tới."),t("br"),t("br"),n._v("“Cái này Mạc Hoằng Nhất cũng họ Mạc, hẳn là cũng là Thiên Vũ vương thất người?”"),t("br"),t("br"),n._v("Cảm khái một câu, không nhịn được hỏi."),t("br"),t("br"),n._v("Mạc Vũ công chúa họ Mạc, nói rõ Thiên Vũ vương quốc vương thất cũng là cái họ này, cái này Mạc Hoằng Nhất, chẳng lẽ là vương thất thành viên."),t("br"),t("br"),n._v("“Đúng đấy!” Nữ hài gật gù: “Hắn là vương thất một cái Vương gia con trai, cũng không được sủng ái, tuy rằng bối phận lên là Mạc Vũ công chúa ca ca, địa vị lại kém không ít, bất quá, có thiên phú như thế, cũng không ai dám xem thường!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền tán đồng."),t("br"),t("br"),n._v("Vương hầu tướng lĩnh há cứ phải là con dòng cháu giống, trời sinh hậu đãi chỉ là một điều kiện, chỉ phải cố gắng nỗ lực, như thế có thể thu được tôn trọng của người khác."),t("br"),t("br"),n._v("Liền lấy cái này Mạc Hoằng Nhất làm thí dụ, hắn tuy rằng không phải Thiên Vũ vương quốc vương thất đích truyền, nhưng dựa vào loại này tình thế, thật muốn xung kích đại vị, chắc hẳn cái khác vương tử cũng không cách nào ngăn cản."),t("br"),t("br"),n._v("Dốc hết toàn lực, đương độ cao của ngươi thật đạt đến cảnh giới nhất định thời điểm, cái gọi là âm mưu quỷ kế, đều cùng chơi nhà chòi giống như buồn cười."),t("br"),t("br"),n._v("Thấy hắn đăm chiêu, nữ hài nói tiếp: “Bất quá, hắn tuy rằng lợi hại, ta lại càng khâm phục Mạc Vũ công chúa, không những người đẹp đẽ, thiên phú cũng cao, ta cũng muốn giống như nàng, khiêm tốn có lễ, khiêm tốn hiếu học, hảo hảo học tập, tranh thủ một tiếng hót lên làm kinh người, danh chấn thiên hạ.”"),t("br"),t("br"),n._v("“Khiêm tốn có lễ, khiêm tốn hiếu học?” Trương Huyền nhìn sang."),t("br"),t("br"),n._v("Hắn theo cái này Mạc Vũ tiểu thư lưu lại thật nhiều ngày, làm sao chưa bao giờ cảm thấy còn có cái này ưu điểm?"),t("br"),t("br"),n._v("Lắc đầu một cái, không nghĩ nữa vấn đề này, nhớ tới trước nàng nói những cái này, hỏi tiếp: “Ngươi nói Vấn Đáp hội... Ngoại trừ vị này Mạc Vũ công chúa còn có bao nhiêu người cũng tiến hành sát hạch?”"),t("br"),t("br"),n._v("Nghe nói như thế, nữ hài lại lần nữa mắt trợn trắng."),t("br"),t("br"),n._v("“Danh sư rất nhiều sát hạch, có thể thông qua, một năm không nhất định có một cái, ngươi cho rằng là rau cải trắng a, tùy ý liền có thể bốc lên thật nhiều? Ta từ Tuyền Hải thành chuyên môn tới nơi này, chính là vì tham gia công chúa thăng cấp danh sư nghi thức, làm sao còn có thể có những người khác!”"),t("br"),t("br"),n._v("“Tuyền Hải thành?” Trương Huyền hiện lên trong đầu một quyển sách, chính là Thiên Vũ vương quốc địa đồ."),t("br"),t("br"),n._v("Nếu như đem Thiên Vũ vương thành so sánh Thiên Huyền thành, cái này Tuyền Hải thành liền cùng Bạch Ngọc thành xấp xỉ, thuộc về Thiên Vũ cảnh nội thành phố lớn thứ hai, từ nơi nào lại đây, lại có Tông sư cường giả làm hộ vệ, cô bé này, xem ra ưa thích nói chuyện, không sợ người sống, e sợ địa vị không thấp."),t("br"),t("br"),n._v("Cũng đúng, danh sư học tập, không có nhân mạch, muốn tìm đến tốt lão sư chỉ điểm, hầu như không thể."),t("br"),n._v("Lại hàn huyên vài câu, biết cô bé này gọi Lăng Tiêu Tiêu, năm nay mới sát hạch thành công danh sư học đồ."),t("br"),t("br"),n._v("Đều là học đồ chắc là chăm sóc lẫn nhau, lúc này mới nói với hắn nhiều như vậy."),t("br"),t("br"),n._v("“Vấn Đáp hội tại Danh Sư đường, trưa mai bắt đầu, ngươi sau đó muốn sát hạch danh sư mà nói, có thể tới xem một chút, liền như vậy cáo từ!”"),t("br"),t("br"),n._v("Nói rồi một hồi, phát giác cái này gọi Trương Huyền học đồ, đối với sát hạch danh sư một chữ cũng không biết, cái gì cũng không biết, Lăng Tiêu Tiêu cũng là mất đi cùng hắn tán gẫu hứng thú."),t("br"),t("br"),n._v("Dưới cái nhìn của nàng, người như thế coi như sau đó có cơ hội sát hạch danh sư, cũng muốn mấy chục năm sau, cùng với nàng hoàn chỉnh không tiếp tục một cái trình độ lên."),t("br"),t("br"),n._v("“Trước tiên tìm một nơi ăn cơm, sau đó sẽ tìm chỗ mới ở lại!”"),t("br"),t("br"),n._v("Cùng nữ hài cáo từ, Trương Huyền chậm rãi xoay người, vẫn chưa tiếp tục hướng phía trước."),t("br"),t("br"),n._v("Ngược lại đã hỏi rõ ràng Danh Sư đường vị trí, không vội vã đi qua, từ Độc điện đi tới nơi này, liên tục sáu, bảy ngày chạy đi, đều thức ăn lương khô, trong miệng đã sớm nhạt."),t("br"),t("br"),n._v("Ăn hết, đang định tìm cái tửu lâu ở một buổi chiều, liền nhìn thấy một người cao lớn kiến trúc xuất hiện ở trước mắt, bên trên năm cái đại tự rồng bay phượng múa, khác nào có tinh khí thần, bất cứ lúc nào cũng sẽ bay lượn mà ra."),t("br"),t("br"),n._v("“Thư họa sư công hội?”"),t("br"),t("br"),n._v("Nhận ra bên trên chữ viết, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Thư họa sư cùng luyện đan sư như thế, thuộc về đặc thù nghề nghiệp, cũng có thống nhất quản lý công hội, chỉ là Thiên Huyền vương quốc địa phương quá nhỏ, không có thiết lập phân bộ tư cách, không nghĩ tới vừa tiến vào Thiên Vũ vương thành liền nhìn thấy."),t("br"),t("br"),n._v("“Vào xem xem!”"),t("br"),t("br"),n._v("Hắn hiện tại rất nhiều nghề nghiệp, ngoại trừ thuần thú sư, luyện đan sư, liền đối với thư họa hiểu rõ nhiều nhất, thậm chí còn có thể làm ra ngũ cảnh tác phẩm, nếu gặp gỡ công hội, đúng là có thể sát hạch một cái chính thức thư họa sư."),t("br"),t("br"),n._v("Dù sao hắn lúc này chính thức nghề nghiệp, cũng chỉ có nhị tinh thuần thú sư cùng nhất tinh luyện đan sư hai cái mà thôi, xem ra có chút trắng xám."),t("br"),t("br"),n._v("Cho tới độc sư, hắn tuy rằng trình độ cũng đạt đến nhị tinh trình độ, bởi vì ngụy trang “Sư thúc tổ” vẫn chưa sát hạch, tự nhiên cũng không có cái gọi là huy chương."),t("br"),t("br"),n._v("Đương nhiên coi như có, dựa theo những nghề nghiệp khác đối với độc sư bài xích, cũng không dám lấy ra."),t("br"),t("br"),n._v("Thầm nghĩ, nhấc chân đi vào."),t("br"),t("br"),n._v("Không hổ là Thư Họa sư công hội, tương tự là rộng rãi đại điện, nhưng bên trong bố trí ngay ngắn có thứ tự, làm cho người ta một loại người tại vẽ bên trong cảm giác."),t("br"),t("br"),n._v("Chậm rãi đi vào trong đó, phảng phất rong chơi tại trong sách vỡ, không nói ra được thoải mái."),t("br"),t("br"),n._v("Bốn phía vách tường, càng là treo đầy đủ loại tác phẩm, một vài bức trông rất sống động, lại đều đạt đến cảnh giới thứ nhất lục thực trình độ."),t("br"),t("br"),n._v("Tuy rằng chính thức thư họa sư cần nắm giữ lục thực, linh động, ý tồn, kinh hồng tứ cảnh, nghe tới cảnh giới thứ nhất rất đơn giản, lại cũng cần mấy năm học tập, không thể một lần mà thôi."),t("br"),t("br"),n._v("Đặc biệt là vẽ tranh, không có trải qua cùng thể ngộ, muốn đem nhìn thấy phong cảnh miêu tả đi ra, cũng không phải một chuyện dễ dàng."),t("br"),t("br"),n._v("Xem ra có thể làm ra những cái này bức tranh, coi như không phải thư họa học đồ, lại cũng gần như."),t("br"),t("br"),n._v("“Vị công tử này, không biết cần cái gì tác phẩm hội họa, tiểu nhân có thể mang ngươi xem một chút!”"),t("br"),t("br"),n._v("Chậm rãi tiến lên, một cái mặc áo xanh gã sai vặt liền đi tới, nhìn về phía Trương Huyền một mặt cung kính."),t("br"),t("br"),n._v("“Những cái này vẽ bán ra?”"),t("br"),t("br"),n._v("Trương Huyền chỉ tay."),t("br"),t("br"),n._v("“Đúng đấy! Công tử tới nơi này không phải muốn mua vẽ sao?” Gã sai vặt gật đầu."),t("br"),t("br"),n._v("Thư Họa sư công hội không bán thư họa, đi chỗ nào mua?"),t("br"),t("br"),n._v("“Ồ...” Suy nghĩ một chút cũng là bừng tỉnh, cái này luyện đan sư công hội bán đan dược một cái ý tứ, nếu như nơi này đều không bán thư họa, đi nơi nào mua?"),t("br"),t("br"),n._v("Gật gù, hỏi: “Những thứ này đều là nhất cảnh tác phẩm hội họa, có hay không cấp bậc cao?”"),t("br"),t("br"),n._v("“Trên lầu xin mời! Công hội tầng một đều là nhất cảnh tác phẩm hội họa, tầng hai là nhị cảnh, cứ thế mà suy ra, công tử muốn mua gì cảnh giới? Ta vậy thì mang ngài đi tới!” Gã sai vặt ánh mắt sáng lên."),t("br"),t("br"),n._v("Thư họa cảnh giới càng cao, giá cả cũng là càng cao, một khi có thể bán ra, làm người phục vụ, đạt được chia làm tự nhiên cũng là nhiều."),t("br"),t("br"),n._v("“Có...”"),t("br"),t("br"),n._v("Chính muốn mở miệng hỏi một chút, có hay không ngũ cảnh tác phẩm hội họa, vừa vặn cũng đi lên xem một chút, liền nhìn thấy dòng người cuồn cuộn, không ít người hướng ngay phía trước phóng đi."),t("br"),t("br"),n._v("“Nhanh lên một chút, Quý Mặc công tử đi ra!”"),t("br"),t("br"),n._v("“Hắn sát hạch nhất tinh thư họa sư thành công?”"),t("br"),t("br"),n._v("“Đúng đấy, vừa mới sát hạch xong, vì báo đáp mọi người, dự định trước mặt mọi người vẽ tranh, hơn nữa còn muốn công khai bán đấu giá!”"),t("br"),t("br"),n._v("“Trước mặt mọi người vẽ tranh? Quá tốt rồi, của hắn vẽ, ta nhất định phải mua đến...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người từng cái từng cái nghị luận sôi nổi."),t("br"),t("br"),n._v("Những cái này cơ bản đều là nữ tử, tuổi không lớn lắm, một bên đi về phía trước, một bên líu ra líu ríu nghị luận, trong đó cũng có một chút người trung niên, từ y phục trên người liền xem xuất thân giá trị không ít."),t("br"),t("br"),n._v("“Quý Mặc công tử, vẽ tranh?”"),t("br"),t("br"),n._v("Nghi hoặc liếc mắt nhìn."),t("br"),t("br"),n._v("“Quý Mặc là Vương thành Quý gia tiểu thiếu gia, năm nay không đủ hai mươi, cũng đã là một vị chính thức thư họa sư...” Nói Quý Mặc công tử, gã sai vặt cũng là một mặt sùng bái: “Công tử nếu như muốn mua vẽ, không ngại qua xem một chút, có thể mua lên Quý thiếu gia vẽ, tuyệt đối không uổng chuyến này.”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-295"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-297"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);