(window.webpackJsonp=window.webpackJsonp||[]).push([[1517],{1870:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-288-quy-mac-cong-tu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-288-quy-mac-cong-tu"}},[n._v("#")]),n._v(" Chương 288: Quý Mặc công tử")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ồ? Vậy thì qua xem một chút!”"),t("br"),t("br"),n._v("Vừa vặn cũng không sao sự tình, Trương Huyền cũng muốn biết một chút thế nào sát hạch thư họa sư, đi theo đồ đen gã sai vặt phía sau, liền hướng mọi người ồn ào phương hướng đi đến."),t("br"),t("br"),n._v("Đoàn người ngay phía trước, màu xanh sẫm cái bàn dựa vào tường bày ra, một cái mười tám, mười chín tuổi thiếu niên, một bộ bạch y, ngồi ngay ngắn tại trên ghế, hai mắt nhắm nghiền."),t("br"),t("br"),n._v("“Quý Mặc công tử ta yêu ngươi, ta muốn cho ngươi sinh con!”"),t("br"),t("br"),n._v("“Không biết xấu hổ, Quý công tử là của ta, ai theo ta cướp, ta liền xé nát y phục của nàng!”"),t("br"),t("br"),n._v("“Tiểu thư, bên kia có cái mỹ nữ nhất định phải cướp, ngươi nhanh đi xé nàng y phục, chúng ta giúp ngươi cố lên!”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Phía trước Quý Mặc công tử còn đang nhắm mắt, tiếp xuống rất nhiều nữ tử đã phát rồ."),t("br"),t("br"),n._v("“Quý công tử tuy rằng tuổi không lớn lắm, lại thương hương tiếc ngọc, mấu chốt nhất chính là, mặt như ngọc, phong thần tuấn lãng, có không ít cô gái sùng bái!” Đồ đen gã sai vặt nhỏ giọng."),t("br"),t("br"),n._v("Trương Huyền gật đầu, hướng vị này Quý Mặc công tử nhìn lại, cái tên này xác thực da dẻ trắng nõn, dung mạo khá là tuấn tú, tuy nhắm mắt lại, khóe miệng lại hơi giương lên, làm cho người ta một loại mỉm cười cảm giác, khá trêu chọc cô gái yêu thích."),t("br"),t("br"),n._v("Có thiên phú, có thực lực, có gia thế, quan trọng nhất lớn còn rất tốt, khó trách nhiều như vậy nữ tử vì đó điên cuồng."),t("br"),t("br"),n._v("“Hắn đây là... Đang làm gì?”"),t("br"),t("br"),n._v("Thấy đối phương ngồi ngay ngắn, không để ý tới xung quanh cãi vã, hai mắt nhắm nghiền, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Quý Mặc công tử muốn làm tranh, hẳn là muốn yên tĩnh tâm thần, điều chỉnh trạng thái!” Đồ đen gã sai vặt đạo."),t("br"),t("br"),n._v("“Điều chỉnh trạng thái? Ở đây?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Liền làm cái tranh mà thôi, bản thân thông thường bắt bút liền đến, đồ chơi này... Còn cần điều chỉnh trạng thái?"),t("br"),t("br"),n._v("Lại nói, coi như điều chỉnh, đến không ai địa phương yên tĩnh thật tốt, nơi này nhiều người như vậy, cãi nhau, điều chỉnh cái gì?"),t("br"),t("br"),n._v("“Quý công tử không đủ hai mươi liền trở thành thư họa Đại sư, thiên tài tuyệt đỉnh, hết thảy cử động tự có thâm ý, há lại là ngươi loại này nhà quê có thể lý giải?”"),t("br"),t("br"),n._v("Nghe được hắn giọng nghi ngờ, đồ đen gã sai vặt còn chưa kịp mở miệng, một cái nữ hài liền xoay đầu lại, trợn mắt nhìn."),t("br"),t("br"),n._v("“Không sai, không biết từ nơi nào nhô ra người ngoài nghề, cút sang một bên, đừng ở chỗ này làm bẩn Quý công tử thư họa!” Lại một cô gái hừ nói."),t("br"),t("br"),n._v("Ào ào ào!"),t("br"),t("br"),n._v("Chỗ trống một vùng, xung quanh nữ tử tất cả đều lạnh lùng nhìn chăm chú lại đây, đầy mặt căm ghét."),t("br"),t("br"),n._v("“Ế?”"),t("br"),t("br"),n._v("Không nghĩ đến không nói gì, liền bị nhiều như vậy người căm thù, Trương Huyền không nói gì."),t("br"),t("br"),n._v("“Thiên Vũ vương quốc có Thư Họa sư công hội, không ít người đều yêu thích thư họa, học đòi văn vẻ, bởi vậy thư họa sư địa vị rất cao. Quý Mặc công tử lại là trong đó người nổi bật, được vô số người tung hô, ở đây tốt nhất cái gì đều đừng nói, không phải vậy mắng đều có thể đem người mắng chết!”"),t("br"),t("br"),n._v("Đồ đen gã sai vặt tràn đầy cười khổ hạ thấp giọng."),t("br"),t("br"),n._v("Trương Huyền gật gù, chính muốn nói chuyện, liền thấy ngay phía trước nhắm chặt hai mắt Quý công tử, mở mắt ra, chậm rãi đứng lên."),t("br"),t("br"),n._v("“Mau nhìn, Quý công tử muốn vẽ tranh!”"),t("br"),t("br"),n._v("“Không biết biết làm ra cấp bậc gì tác phẩm hội họa!”"),t("br"),t("br"),n._v("“Đó còn cần phải nói, Quý công tử thế nhưng chính thức thư họa sư, nhất định có thể làm ra nhị cảnh tác phẩm.”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thấy hắn mở mắt, trước trợn mắt nhìn mọi người, cũng là không thèm để ý Trương Huyền, lại lần nữa xem hướng về phía trước, trong mắt bốc lên một cái lại một cái chấm nhỏ."),t("br"),t("br"),n._v("Chính thức thư họa sư, cũng được gọi là thư họa Đại sư, cùng Lục Trầm, Nguyên Ngữ như thế, có thể đơn giản làm ra nhị cảnh, thậm chí tam cảnh tác phẩm hội họa, còn có hay không có thể kinh hồng, liền xem vận khí cùng trạng thái."),t("br"),t("br"),n._v("Thư họa bốn vị trí đầu cảnh phân biệt là: Lục thực, linh động, ý tồn, kinh hồng."),t("br"),t("br"),n._v("“Tại hạ Quý Mặc, sắp bêu xấu!”"),t("br"),t("br"),n._v("Quý Mặc công tử đứng ở trước bàn đọc sách mặt, bàn tay run lên, một thanh quạt giấy xuất hiện tại trước mặt, đập hai lần, tóc đen tung bay, bạch y như gió: “Chỗ làm tác phẩm hội họa, tại chỗ bán đấu giá, bất quá, số tiền này, Quý mỗ không lấy một đồng tiền, dùng để xây dựng thư họa đường, mời thư họa học đồ, ra mặt dạy học, để càng nhiều người yêu thích thư họa, hiểu rõ thư họa!”"),t("br"),t("br"),n._v("“Không hổ là Quý công tử, coi tiền tài như rác rưởi...”"),t("br"),t("br"),n._v("“Văn nhân khí khái, khiến cho người khâm phục!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe nói như thế, mọi người lại lần nữa ồ lên, đặc biệt là rất nhiều nữ tử, từng cái từng cái nhọn kêu thành tiếng."),t("br"),t("br"),n._v("“Cái tên này còn rất biết mời mua nhân tâm!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cái này Quý Mặc công tử xem ra tuổi không lớn lắm, lại sâu quan trường ngũ vị, biết cái gì có thể hấp dẫn sự chú ý của người khác."),t("br"),t("br"),n._v("Này cùng kiếp trước những cái kia minh tinh cái gọi là từ thiện bán đấu giá như thế, nói trắng ra, chính là cái mánh lới."),t("br"),t("br"),n._v("Nếu như thật muốn xây dựng thư họa đường, làm cho tất cả mọi người cũng giải thư họa, yêu thích thư họa, lặng lẽ làm chính là, không cần thiết chơi động tĩnh lớn như vậy. Huống chi... Hiện tại đều cuối mùa thu, lại là buổi tối, đứng tại chỗ có bao nhiêu chút lạnh, còn đánh cây quạt..."),t("br"),t("br"),n._v("“Yên tĩnh!”"),t("br"),t("br"),n._v("Quạt giấy khép lại, Quý Mặc công tử bàn tay lớn vẫy một cái: “Được rồi, ta liền không phí lời, hiện tại liền bắt đầu vẽ tranh, còn hi vọng chư vị có thể giữ yên lặng!”"),t("br"),t("br"),n._v("Nghe được hắn, trước còn rít gào mọi người, nhất thời ngừng lại."),t("br"),t("br"),n._v("Nhẹ nhàng nở nụ cười, Quý Mặc công tử vút qua ống tay áo, bàn tay một trảo, một cọng lông bút xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Tờ giấy trải ra, tay trái tại trên bàn nhấn một cái, chân khí xung kích bên dưới, trong nghiên mực mực viết rơi vào ngòi bút, tay phải múa, bút rơi giấy mặt."),t("br"),t("br"),n._v("Rồng bay phượng múa, muôn hình vạn trạng."),t("br"),t("br"),n._v("Ào ào ào!"),t("br"),t("br"),n._v("Bút lông trên giấy di động, khác nào một con cá tại bể nước di động, nương theo thời gian chuyển dời, một bộ tác phẩm hội họa chậm rãi xuất hiện."),t("br"),t("br"),n._v("Là một bộ sơn thủy đồ."),t("br"),t("br"),n._v("Sơn mạch cao vót, hiếm thấy lâm quái thạch, nước chảy rong chơi trong đó, đánh đá mặt, khác nào phát ra “Leng keng” âm thanh, hết thảy lá cây cũng giống như một phương hướng múa, tựa hồ có gió nhẹ lướt qua."),t("br"),t("br"),n._v("“Là bức cảnh giới thứ hai Linh Động cảnh giới tác phẩm!”"),t("br"),t("br"),n._v("Liếc mắt nhìn, Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Tại Lục Trầm đại sư trong nhà học tập xong Thiên Đạo thư họa, đối với thư họa nghề nghiệp này thì có rất sâu hiểu rõ, coi như không sử dụng Thiên Đạo thư viện, cũng có thể đối với một ít sách tranh tiến hành giám thưởng."),t("br"),t("br"),n._v("Bộ này sơn thủy đồ, non xanh nước biếc, giống như để người tận mắt đến một dãy núi, khí tức linh động, làm cho người ta dị dạng vẻ đẹp, nói rõ dĩ nhiên đạt đến Linh Động cảnh."),t("br"),t("br"),n._v("Bất quá, tuy rằng vô cùng đặc sắc, núi trong nước nhưng không có ý cảnh, ý vị, cũng chính là không đạt đến cái gọi là ý tồn."),t("br"),t("br"),n._v("“Đáng tiếc, nếu như bất quá phân khoe khoang kỹ năng, an tâm chân thật vẽ tranh, có lẽ thật có thể đạt đến cảnh giới này!” Nhìn một hồi, Trương Huyền thở dài một tiếng."),t("br"),t("br"),n._v("Cái này Quý Mặc công tử mặc dù coi như có chút giả vờ giả vịt, phần ngoại lệ tranh bản lĩnh quả thật không tệ, dựa theo đạo lý, chỉ cần tĩnh tâm vẽ tranh, làm ra cảnh giới thứ ba ý tồn tác phẩm, không khó lắm."),t("br"),t("br"),n._v("Chỉ tiếc, vì động tác đẹp đẽ, không ít địa phương quá mức khuếch đại, xem nhẹ vẽ tranh chủ ý, cuối cùng chỉ để tác phẩm hội họa đạt đến linh động đỉnh phong, khoảng cách ý tồn chỉ kém một tia."),t("br"),n._v("Trong lòng cảm khái, lại không nói ra."),t("br"),t("br"),n._v("Hắn đến đây chỉ là muốn nhìn một chút thế nào sát hạch thư họa sư, theo cái này Quý Mặc công tử, không nửa mao tiền quan hệ. Lại nói, mới vừa rồi còn không nói liền bị mọi người quát lớn một trận, cũng không muốn bị trước mắt này chồng nữ nhân mắng chết."),t("br"),t("br"),n._v("Rất nhanh, tác phẩm hội họa hoàn thành."),t("br"),t("br"),n._v("Quý Mặc công tử đem bút lông sau đó vứt tại ống đựng bút, đem tranh treo ở một bên trên vách tường."),t("br"),t("br"),n._v("“Đây là một bức nhị cảnh đỉnh phong tác phẩm hội họa, thanh phong sơn thủy đồ, hiện đang đấu giá bắt đầu, mọi người có thể tự do báo giá, 100 vạn kim tệ đặt cơ sở, mỗi lần tăng giá không thể thiếu tại 10 vạn kim tệ.”"),t("br"),t("br"),n._v("Nói đến đây, nhếch miệng lên, Quý Mặc công tử lộ ra một cái mỉm cười mê người, cây quạt tại bàn tay vung vẩy, làm nổi bật lên hắn công tử văn nhã khí chất: “Mua đến bức họa này người, ta có thể miễn phí cùng nàng cùng đi ăn tối!”"),t("br"),t("br"),n._v("“Cùng đi ăn tối? Bức họa này ta mua!”"),t("br"),t("br"),n._v("“Chớ cùng ta cướp, ta ra 1 10 vạn!”"),t("br"),t("br"),n._v("“1 10 vạn liền muốn mua Quý Mặc công tử tranh, đùa gì thế, 13 0 vạn!”"),t("br"),t("br"),n._v("“Ta ra hai trăm vạn!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được bán đấu giá, còn biếu tặng phúc lợi, rất nhiều nữ tử nhất thời hưng phấn con mắt đỏ, từng cái từng cái điên cuồng báo giá, rất nhanh, giá cả tăng cao."),t("br"),t("br"),n._v("“240 vạn?”"),t("br"),t("br"),n._v("Thấy giá cả rất nhanh tăng cao đạo hơn 200 vạn, Trương Huyền trợn mắt lên."),t("br"),t("br"),n._v("Trước hắn đem một bộ ngũ cảnh tác phẩm hội họa bán cho Lục Trầm, Hoàng Ngữ mấy người, cũng mới hai trăm vạn kim tệ, này một cái nhị cảnh, rác rưởi không thể lại rác rưởi tác phẩm hội họa, dĩ nhiên vượt qua cái giá này..."),t("br"),t("br"),n._v("Quá hãm hại đi!"),t("br"),t("br"),n._v("“Cái này... Ngũ cảnh Hóa Linh cảnh tác phẩm hội họa, đại khái bao nhiêu tiền?”"),t("br"),t("br"),n._v("Một mặt phiền muộn, không nhịn được quay đầu hỏi hướng bên cạnh gã sai vặt."),t("br"),t("br"),n._v("“Ngũ cảnh? Đây chính là nhị tinh Tông sư cảnh thư họa Đại sư lưu lại Mặc Bảo, hơn nữa còn không phải nhiều lần đều có thể tranh thành, cần cơ duyên cùng trạng thái... Loại này tác phẩm hội họa, tính toán ở đây, đều là trấn biết chi bảo, có thể nói... Vô giá!”"),t("br"),t("br"),n._v("Đồ đen gã sai vặt chần chờ một chút, đạo."),t("br"),t("br"),n._v("“Vô giá?” Trương Huyền loáng một cái."),t("br"),t("br"),n._v("“Đúng đấy, nếu như nhất định phải định vị giá cả, giá khởi đầu chỉ sợ cũng muốn ngàn vạn kim tệ, cuối cùng có thể cao lên tới ra sao, ta cũng không biết, chỉ biết là nửa năm trước, nơi này bán đấu giá qua một bộ, cuối cùng giá sau cùng là 3,400 vạn kim tệ.”"),t("br"),t("br"),n._v("Đồ đen gã sai vặt nói."),t("br"),t("br"),n._v("Trương Huyền khóe miệng co vào, cảm thấy lập tức sẽ khóc."),t("br"),t("br"),n._v("Hơn 3,400 vạn kim tệ một bức hoạ, hắn hai trăm vạn bán đi bốn bức... Có muốn hay không như thế hố?"),t("br"),t("br"),n._v("“Thư Họa sư công hội, tại Vương thành xây dựng không tới ba mươi năm, tác phẩm hội họa giá cả, cũng là khoảng thời gian này xào lên. Đổi làm ba mươi năm trước, căn bản không ai coi trọng, coi như là ngũ cảnh tác phẩm hội họa, e sợ cũng là đáng giá cái một hai trăm vạn.” Gã sai vặt nói tiếp: “Cao đến đâu, không ai đi mua!”"),t("br"),t("br"),n._v("“Ba mươi năm trước, một hai trăm vạn?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Đúng đấy!” Gã sai vặt gật gù."),t("br"),t("br"),n._v("Thấy hắn xác nhận, Trương Huyền lúc này mới chậm lại, cảm thấy trong lòng thoải mái một chút."),t("br"),t("br"),n._v("Thiên Huyền vương quốc tin tức bế tắc, vừa không có Thư Họa sư công hội, xem ra lúc trước Hoàng Ngữ mấy người cho mình báo giá, là ba mươi năm trước, hai trăm vạn... Đã xem như là không thiếu."),t("br"),t("br"),n._v("“Công tử muốn mua ngũ cảnh tác phẩm hội họa?” Đồ đen gã sai vặt nghi hoặc nhìn sang: “Ngũ cảnh tác phẩm hội họa, chỉ có hội trưởng mới có thể tranh đi ra, bất quá hội trưởng nửa tháng phía trước liền ra đi tìm linh cảm, hiện tại còn chưa có trở lại...”"),t("br"),t("br"),n._v("“Chỉ là tùy tiện hỏi một chút...”"),t("br"),t("br"),n._v("Nhịn xuống phiền muộn, Trương Huyền lắc đầu một cái."),t("br"),t("br"),n._v("“Cái kia...”"),t("br"),t("br"),n._v("Gã sai vặt nhìn sang: “Nếu không công tử... Đem Quý Mặc công tử bộ này thanh phong sơn thủy đồ cũng mua lại đi! Nhị cảnh đỉnh phong, lại là Quý công tử xuất phẩm, sau đó tất nhiên có thể thăng đáng giá!”"),t("br"),t("br"),n._v("“Quên đi!” Trương Huyền lắc đầu một cái: “Ta tới đây không phải mua tranh... Lại nói, một cái nhị cảnh tác phẩm hội họa mà thôi, không có hứng thú!”"),t("br"),t("br"),n._v("“Nhị cảnh, còn mà thôi? Khẩu khí thật là lớn!”"),t("br"),t("br"),n._v("Vừa dứt lời, đồ đen gã sai vặt vẫn chưa trả lời, liền nghe đến một cái hừ lạnh vang lên, ngẩng đầu nhìn lên, liền thấy phía trước Quý Mặc công tử lạnh lùng nhìn lại."),t("br"),t("br"),n._v("“Ế?”"),t("br"),t("br"),n._v("Không nghĩ tới tự mình nói thanh âm không lớn, lại bị đối phương nghe được, Trương Huyền có chút lúng túng: “Thật không tiện, ta chỉ là thuận miệng nói một chút, đừng ngạc nhiên!”"),t("br"),t("br"),n._v("Đối phương vẽ tranh mệt mỏi đầu đầy mồ hôi, cũng không dễ dàng, bản thân mới vừa nói thật có chút thiếu thỏa đáng."),t("br"),t("br"),n._v("“Hừ, ngươi cho rằng một câu đừng ngạc nhiên liền xong?”"),t("br"),t("br"),n._v("Quý Mặc công tử quăng một cái ống tay áo, lông mày giương lên: “Đánh gãy buổi đấu giá, đối với ta tác phẩm hội họa xem thường, ngươi đây là đang làm nhục thư họa nghề nghiệp, xem thường một vị chính thức thư họa sư!”"),t("br"),t("br"),n._v("“Cái kia... Ngươi nói làm sao bây giờ?”"),t("br"),t("br"),n._v("Bản thân chỉ là thuận miệng nói, cũng xin lỗi, đối phương lại lên cao đến sỉ nhục thư họa nghề nghiệp, xem thường chính thức thư họa sư mức độ, Trương Huyền bất đắc dĩ lắc đầu một cái."),t("br"),t("br"),n._v("“Rất đơn giản, năm trăm vạn kim tệ, đem ta bức họa này mua!”"),t("br"),t("br"),n._v("Quý Mặc công tử hai tay chắp ở sau lưng, nhàn nhạt nhìn sang: “Ngươi yên tâm, số tiền này, ta một phần không nắm, biết tất cả quyên đi ra ngoài, dùng để xây dựng thư họa đường, cũng coi như để ngươi lấy công chuộc tội!”"),t("br"),t("br"),n._v("“Năm trăm vạn kim tệ?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hắn liền tới xem một chút, thuận miệng nói một câu, đối phương liền muốn ép mua ép bán, đùa gì thế?"),t("br"),t("br"),n._v("Nơi này nhiều như vậy sùng bái nữ nhân của ngươi, ngươi làm cho các nàng mua không phải là được..."),t("br"),t("br"),n._v("“Làm sao? Không muốn mua?”"),t("br"),t("br"),n._v("Quý Mặc công tử cười lạnh, bàn tay vẫy một cái: “Không mua cũng được, lập tức xin lỗi, sau đó lại đừng bước vào công hội nửa bước! Chúng ta Thư Họa sư công hội, không hoan nghênh loại người như ngươi!”"),t("br"),t("br"),n._v("“Đừng bước vào công hội nửa bước?” Trương Huyền không nói gì."),t("br"),t("br"),n._v("Thực sự là người hiền bị bắt nạt, ngựa hiền bị người ta cưỡi."),t("br"),t("br"),n._v("Bản thân liền thuận miệng nói, cũng xin lỗi, đối phương lại lả lướt không buông tha."),t("br"),t("br"),n._v("“Xin lỗi cũng được, không bước vào thư họa công hội cũng được, coi như năm trăm vạn kim tệ mua lại cũng được, tiền đề là... Tác phẩm đáng giá cái giá này!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu một cái: “Này tấm tùy ý tìm cái học đồ liền vẽ đi ra ngoạn ý, cũng xứng?”"),t("br"),t("br"),n._v("(Vé tháng lập tức rơi khỏi mười vị trí đầu, mọi người còn nữa không?)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-296"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-298"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);