(window.webpackJsonp=window.webpackJsonp||[]).push([[152],{505:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1113-mau-sac-that-đam"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1113-mau-sac-that-đam"}},[n._v("#")]),n._v(" Chương 1113: Màu sắc thật đậm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Độc sư cấp bậc càng cao, càng bị người kiêng kị, nguyên nhân chính là như vậy, một chút cấp thấp nhất độc sư học đồ, cho thấy thân phận, ngược lại sẽ không tạo thành quá lớn ảnh hưởng."),t("br"),t("br"),n._v("Mà có thể phối chế một nửa đi ra khỏi khiếu cảnh cường giả hữu dụng dược vật độc sư, cấp bậc có thể nói, tuyệt đối có chút đáng sợ!"),t("br"),t("br"),n._v("Ít nhất đều muốn đạt tới thất tinh đỉnh phong cấp bậc."),t("br"),t("br"),n._v("“Ta là một vị y sư, am hiểu sâu dược lý, có thể căn cứ dược tính, suy đoán ra phương thuốc!” Trương Huyền mặt không đỏ mặt không thay đổi."),t("br"),t("br"),n._v("Độc sư là đánh chết cũng không thể thừa nhận, nhất là đối diện vị này, ghét ác như cừu Chiến sư đường đường chủ."),t("br"),t("br"),n._v("“Vậy vẫn là được rồi...” Hình đường chủ lắc đầu."),t("br"),t("br"),n._v("Y sư cứ việc đối dược lý biết rất nhiều, nhưng cùng độc sư chênh lệch một cái chức nghiệp, độc, mỗi một loại dược liệu phối hợp, thậm chí hỏa hầu nắm giữ hơi không đúng, liền có thể dẫn đến thuộc tính đại biến, không phải đơn giản nắm giữ dược lý, liền có thể phỏng đoán cùng phối trí."),t("br"),t("br"),n._v("Nếu không mỗi một cái y sư đều có thể làm độc sư, độc sư chức nghiệp cũng liền không có ý nghĩa."),t("br"),t("br"),n._v("Hắn cũng không muốn dùng mạng của mình, lấy ra làm cho đối phương thí nghiệm."),t("br"),t("br"),n._v("Mấu chốt vẫn chỉ là trị ngọn không trị gốc."),t("br"),t("br"),n._v("“Loại trừ hai thứ này, cái khác không có biện pháp nào?” Liêu điện chủ nhịn không được nói."),t("br"),t("br"),n._v("Một loại thực hiện không được, một loại bất cứ lúc nào cũng sẽ muốn mạng... Vốn nghĩ đường chủ tìm đến cao minh Khải Linh sư, sẽ để cho thực lực bạo tăng, ai biết thực lực không những không lớn, còn nhanh phải bị chơi phế..."),t("br"),t("br"),n._v("Có muốn hay không như thế hố?"),t("br"),t("br"),n._v("“Thật ra thì... Còn có một cái phương pháp, chính là... Các ngươi chưa hẳn có thể đáp ứng!”"),t("br"),t("br"),n._v("Dừng lại một hồi, Trương Huyền nói."),t("br"),t("br"),n._v("“Nói nghe một chút!”"),t("br"),t("br"),n._v("“Rất đơn giản, mở rộng nguyên thần của ngươi, để cho ta đi vào dò xét, tìm tới dung hợp thực vật linh tính, sau đó gạt bỏ!” Trương Huyền nói."),t("br"),t("br"),n._v("Ba đại Chiến sư liếc mắt nhìn nhau, trầm mặc xuống."),t("br"),t("br"),n._v("Mở rộng nguyên thần, chẳng khác nào hoàn toàn mất đi phòng ngự, đối phương một khi có cái gì ý khác, hoàn toàn có thể nhất niệm gạt bỏ."),t("br"),t("br"),n._v("“Cái này... Làm không được!” Hình đường chủ lắc đầu."),t("br"),t("br"),n._v("Liền xem như Liêu điện chủ, đều khó có khả năng hoàn toàn mở rộng nguyên thần, huống chi một cái không đáng tin cậy người xa lạ!"),t("br"),t("br"),n._v("“Vậy liền không có biện pháp nào khác, trừ phi...” Trong lòng lần nữa khẽ động, Trương Huyền lại nghĩ tới một loại khả năng, chẳng qua còn chưa nói ra miệng, liền lắc đầu."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("“Trừ phi, ta phối trí ra một chút độc chết thực vật linh tính độc dược, ngươi nghĩ biện pháp hút vào nguyên thần, đương nhiên... Có thể thành công hay không, chỉ có thể nhiều lần thí nghiệm!”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp."),t("br"),t("br"),n._v("Nhờ vào hắn đối độc lý giải, cũng có thể thực hiện, chỉ là... Đối phương trên đầu lại Trường Tiên người chưởng, lại kết quả, cái rắm rãnh bên trên còn nở hoa... Cái này thuộc về loại nào thực vật linh tính, còn không tốt xác nhận, nhất định phải rất nhiều loại dược vật cẩn thận thí nghiệm mới được."),t("br"),t("br"),n._v("“Càng không được...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, hình đường chủ nhoáng một cái."),t("br"),t("br"),n._v("Có thể độc chết thực vật linh tính, làm không cẩn thận cũng có thể độc chết nguyên thần của hắn, ngươi còn muốn thí nghiệm... Cái này đây là sợ chơi bất tử ta đi!"),t("br"),t("br"),n._v("“Chỉ có những biện pháp này, cái khác không có...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thành tâm không trách hắn, nếu không phải Ngụy điện chủ xông tới, đánh gãy hắn gạt bỏ linh tính bên trong ý niệm, chắc chắn sẽ không xuất hiện loại tình huống này."),t("br"),t("br"),n._v("Chỉ có thể nói trùng hợp gây ra."),t("br"),t("br"),n._v("Hình đường chủ trầm mặc."),t("br"),t("br"),n._v("Hắn cũng biết, đối phương là tại tận tâm giúp hắn, xuất hiện đủ loại biến cố, khẳng định là ai cũng không nguyện ý nhìn thấy, nhưng... Cái này mấy loại phương pháp giải quyết, thật không cách nào làm được."),t("br"),t("br"),n._v("“Tôn sư, để ta suy nghĩ một chút, đúng, ngươi không phải là muốn thử một chút tâm ma thông đạo, thuận tiện rèn luyện một chút tâm cảnh của mình ư? Không bằng hiện tại đi qua...”"),t("br"),t("br"),n._v("Chần chờ một chút, hình đường chủ đột nhiên mở miệng."),t("br"),t("br"),n._v("“Ừm?” Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("Trước mắt vị này suy nghĩ quá nhún nhảy đi."),t("br"),t("br"),n._v("Chính đang thương nghị làm sao cứu hắn, liền trực tiếp mở miệng, muốn bản thân thử một chút tâm ma thông đạo..."),t("br"),t("br"),n._v("“Yên tâm đi, Tâm điện tâm ma mục đích chủ yếu là vì khảo hạch, rèn luyện, nếu như cảm thấy không chịu nổi, nhận thua liền có thể chạy trốn, sẽ không đưa người vào chỗ chết!”"),t("br"),t("br"),n._v("Còn tưởng rằng hắn sợ hãi, hình đường chủ cười cười."),t("br"),t("br"),n._v("Nụ cười này, trên đầu cây xương rồng cảnh run rẩy, trái cây bất cứ lúc nào cũng sẽ ngã xuống, không nhìn thấy mảy may thành ý, ngược lại để cho người ta rùng mình."),t("br"),t("br"),n._v("“Ta đi thử xem!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đối phương tình huống hiện tại, cần bình tĩnh suy nghĩ một chút, nên lựa chọn như thế nào, không phải mình có thể hỗ trợ, đã nói như vậy, không bằng đi thử xem tâm ma thông đạo, rèn luyện một chút tâm cảnh của mình, có lẽ bởi vậy có thể có được dẫn dắt, tìm tới tốt hơn phương pháp giải quyết."),t("br"),t("br"),n._v("“Ừm! Liêu điện chủ, hỗ trợ mở ra tâm ma thông đạo!” Hình đường chủ gật đầu."),t("br"),t("br"),n._v("“Cái này...” Liêu điện chủ cau mày, một lát sau, vẫn gật đầu: “Tốt!”"),t("br"),t("br"),n._v("Nói hết, đi tới cách đó không xa thông đạo trước mặt, bàn tay nhấn một cái, thông đạo xuất hiện lần nữa."),t("br"),t("br"),n._v("Trương Huyền cùng mọi người nhẹ gật đầu, đi vào, thời gian qua một lát, biến mất tại mấy người trước mặt."),t("br"),t("br"),n._v("“Đường chủ, vị này Tôn sư, không phải chúng ta Chiến sư đường người, vì sao phải để hắn đi vào tâm ma thông đạo? Mấu chốt nhất là... Trong này còn dính dáng Tâm điện cao thâm nhất truyền thừa...”"),t("br"),t("br"),n._v("Trương Huyền vừa biến mất, Liêu điện chủ liền không nhịn được nói."),t("br"),n._v("Cái tâm ma này thông đạo, cũng không phải là bình thường Chiến sư rèn luyện tâm cảnh dùng, mà là điện chủ, đường chủ mới có tư chất tiến vào, ẩn chứa trong đó Tâm điện cao thâm nhất truyền thừa, đường chủ trực tiếp để một ngoại nhân đi vào... Rốt cuộc muốn làm gì?"),t("br"),t("br"),n._v("“Yên tâm đi, tâm ma điện cao nhất truyền thừa, chỉ có thông qua tất cả tâm ma khảo hạch mới có thể có đến, ta loại thực lực này, còn không cách nào hoàn thành, hắn một cái Thánh vực nhất trọng, làm sao có thể thành công?”"),t("br"),t("br"),n._v("Hình đường chủ lắc đầu."),t("br"),t("br"),n._v("“Cái này... Ngược lại là!”"),t("br"),t("br"),n._v("Liêu điện chủ vò đầu."),t("br"),t("br"),n._v("Quan tâm sẽ bị loạn, quên chuyện này."),t("br"),t("br"),n._v("Tâm điện cao nhất truyền thừa, lịch đại Chiến sư đường đường chủ, trăm chẳng qua năm, một cái nho nhỏ Khải Linh sư, muốn thông qua, tự nhiên càng không có thể."),t("br"),t("br"),n._v("“Cao nhất truyền thừa sự tình không cần lo lắng nhiều, tình huống hiện tại, các ngươi cũng rõ ràng, ta nhất định phải nhanh giải quyết trên người tai hoạ ngầm, mới có thể đối phó cái kia cái gọi là Huyền Huyền hội, nghênh đón cái khác Chiến sư đường khiêu chiến! Cho nên... Ta tính toán, để ý niệm của hắn đi vào nguyên thần của ta!”"),t("br"),t("br"),n._v("Không đi xoắn xuýt vấn đề này, hình đường chủ vẻ mặt nghiêm túc mà nói."),t("br"),t("br"),n._v("“Đi vào nguyên thần? Đường chủ, ngươi phải nghĩ lại mà làm sau...” Liêu, ngụy hai vị điện chủ đồng thời giật nảy mình, vội vàng khuyên can."),t("br"),t("br"),n._v("Mở rộng nguyên thần, để ngoại nhân ý niệm đi vào, cũng không phải đùa giỡn, hơi không cẩn thận, liền có thể hồn phi phách tán, loại sự tình này ngàn vạn không thể mạo hiểm."),t("br"),t("br"),n._v("“Không cần khuyên, ta cũng biết nguy hiểm, bởi vậy, nhận lời hắn đi vào thông đạo! Một khi lòng mang ý đồ xấu, tâm ma liền sẽ sinh sôi, càng ngày càng cường đại, từ đó rơi vào nguy hiểm! Đến lúc đó, không cần ta ra tay, cũng sẽ nhận trừng phạt, thậm chí... Tử vong. Nếu là tâm hắn hoài bằng phẳng, tâm ma không cách nào tổn thương, để ý nghĩa đọc đi vào nguyên thần của ta tự nhiên cũng liền không nguy hiểm, sao không mạo hiểm thử một lần?”"),t("br"),t("br"),n._v("Hình đường chủ nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hai người dừng lại."),t("br"),t("br"),n._v("Tâm ma, là người nội tâm xấu xí huyễn hóa, cái tên này thật muốn mưu đồ quấy rối, muốn hại người, tâm ma thông đạo thật giống như kính chiếu yêu, có thể mạnh nhẹ nhõm soi sáng ra, cũng để hắn chịu nhiều đau khổ."),t("br"),t("br"),n._v("Nếu như lòng mang nhân từ, để hắn nhìn trộm nguyên thần của mình, cũng liền không tính là gì."),t("br"),t("br"),n._v("“Thành việc lớn, phải có lấy hay bỏ! Thật ra thì Trần Trúc Thanh, Diêm Thanh Hải, Lục Vương Thu bọn họ mong muốn gia nhập Huyền Huyền hội, ta cũng không trách bọn họ, ngược lại có chút bội phục.”"),t("br"),t("br"),n._v("Thấy hai người do dự, hai tay chắp sau lưng, hình đường chủ như là một cái trong sa mạc đều ngoan cường sinh tồn cây xương rồng cảnh."),t("br"),t("br"),n._v("“Bội phục?”"),t("br"),t("br"),n._v("Hai vị điện chủ không hiểu."),t("br"),t("br"),n._v("Đổi lại bọn họ là đường chủ, những điện chủ này không để ý Chiến sư đường uy nghiêm, gia nhập một cái cái gì thâm sơn cùng cốc tới hệ thống, khẳng định đã sớm tức điên."),t("br"),t("br"),n._v("“Là, Chiến sư là Danh Sư đường bên trong một cái chi nhánh, tương đương với quân đội của đế quốc! Quân đội cần chính là cái gì? Tự nhiên là sức chiến đấu! Chỉ cần có thể để sức chiến đấu tăng lên, đang cùng Dị Linh tộc nhân chiến đấu bên trong, tốt hơn phát huy tác dụng, cái khác bất kể làm cái gì, đều chỉ là phụ trợ thủ đoạn, không sao... Đây là năm đó khai phái tổ sư lưu lại tiếng nói, cũng là chúng ta Chiến sư tuân thủ thiết luật!”"),t("br"),t("br"),n._v("Hình đường chủ nói."),t("br"),t("br"),n._v("Liêu điện chủ, Ngụy điện chủ gật đầu."),t("br"),t("br"),n._v("Đại lục muốn chiến sư ý nghĩa... Là chiến đấu."),t("br"),t("br"),n._v("Chỉ cần có đầy đủ sức chiến đấu, hơn nữa không vi phạm danh sư chuẩn tắc, sự tình khác, phụ trợ mà thôi, không tính là gì."),t("br"),t("br"),n._v("Huyền Huyền hội những người kia, mặc dù chỉ là từ nhỏ địa phương tới, nhưng đối với chiến đấu lý giải, quả thực vượt qua bọn họ, đã đạt tới hóa phức tạp thành đơn giản tình trạng, cùng bọn hắn học tập, có thể để cho chiến lực tăng nhiều, sao lại không làm?"),t("br"),t("br"),n._v("Khổng sư còn không ngại học hỏi kẻ dưới, lưu truyền Thiên Cổ!"),t("br"),t("br"),n._v("Hướng tu vì so với chính mình thấp, tuổi tác so với chính mình nhỏ bé người học tập, chẳng những không mất mặt, ngược lại còn có thể nhận được mẫn mà hiếu học thanh danh, đổi lại ai, đều sẽ làm ra lựa chọn."),t("br"),t("br"),n._v("“Bọn họ không để ý Nguyên Thần cảnh cường giả tôn nghiêm, trực tiếp thỉnh giáo, nói rõ bọn họ đối thực lực khát vọng, nhưng thật ra là chuyện tốt! Ta sở dĩ khuyên can, là không muốn để cho Chiến sư đường, biến thành Huyền Huyền hội, trở thành ngoại giới chế giễu đối tượng thôi.”"),t("br"),t("br"),n._v("Hình đường chủ nói tiếp."),t("br"),t("br"),n._v("“Ừm!” Hai người hiểu được."),t("br"),t("br"),n._v("“Được rồi, nhìn thấy vị này Tôn Cường, đến cùng phải hay không lòng dạ khó lường đi! Nếu quả thật có sát ý, hẳn là chẳng mấy chốc sẽ hiển lộ...”"),t("br"),t("br"),n._v("Giải thích xong, hình đường chủ nhìn về phía cách đó không xa thông đạo."),t("br"),t("br"),n._v("“Tâm ma thông đạo, có thể tốt hơn điều tra nội tâm, nếu như cái tên này, thật có sát ý, đừng có mưu đồ, ở đây quả thực có thể nhìn ra...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Liêu điện chủ bàn tay nhẹ nhàng tại trên mặt tường vuốt ve."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Mặt tường lập tức biến thành trong suốt chi sắc, tựa như ngọc chất."),t("br"),t("br"),n._v("“Cái này ngọc tường, có thể đo lường ra bên trong tâm ma số lượng, tâm tư càng nhiều, bản chất càng phá hư, đi vào thông đạo, tâm ma diễn sinh số lượng cũng càng nhiều. Chúng ta Chiến sư, vì chiến đấu mà sinh, tâm cảnh đơn thuần, dưới tình huống bình thường, sẽ chỉ sinh sôi hàng đơn vị đếm được tâm ma. Tựa như vừa rồi đường chủ đi vào, tâm cảnh mượt mà, chỉ xuất hiện ba đầu, bức tường hiện lên màu sáng...”"),t("br"),t("br"),n._v("Nhìn về phía trước mắt bức tường, Liêu đường chủ cười giải thích."),t("br"),t("br"),n._v("“Bình thường danh sư, cũng liền hơn mười, bức tường vẫn như cũ là màu sáng. Chỉ có những cái kia gian ác chi đồ, nắm giữ mấy chục cái tâm ma, màu sắc mới có thể biến sâu!”"),t("br"),t("br"),n._v("“Loại người này, mỗi một cái đều trên tay chạm phải không biết bao nhiêu người tính mạng, chết chưa hết tội!”"),t("br"),t("br"),n._v("“Hiện tại vị này Tôn Cường đi vào lối đi, bức tường nếu như vẫn là màu sáng, nói rõ thái độ làm người ngay thẳng, không cần lo lắng, đường chủ hoàn toàn có thể đối với hắn mở rộng nguyên thần... Nếu là biến thành màu đậm, khẳng định mưu đồ quấy rối, vừa ra tới liền có thể chém giết!”"),t("br"),t("br"),n._v("Liêu điện chủ mắt sáng lên."),t("br"),t("br"),n._v("“Ừm...”"),t("br"),t("br"),n._v("Hình đường chủ cùng Ngụy điện chủ cũng biết những này, đồng thời nhẹ gật đầu, đang nghĩ nói chuyện, đột nhiên đồng thời biến sắc."),t("br"),t("br"),n._v("Chỉ thấy trước mắt bức tường một hồi ánh sáng lóe lên, thời gian nháy mắt trở nên đen như mực."),t("br"),t("br"),n._v("“Màu sắc thật đậm, cái này... Vượt qua một trăm đầu tâm ma?”"),t("br"),t("br"),n._v("Liêu điện chủ ánh mắt phát lạnh."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1121"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1123"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);