(window.webpackJsonp=window.webpackJsonp||[]).push([[1521],{1876:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-21-huyen-khong-hanh-cham"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-21-huyen-khong-hanh-cham"}},[n._v("#")]),n._v(" Chương 21: Huyền không hành châm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Cái này chân vết thương tuy nhưng bình thường bước đi không thành vấn đề, lại làm cho nàng tu luyện có thụ dày vò, dường như xương mu bàn chân chi độc, nửa năm qua, cơ hồ không có một ngày vui cười."),t("br"),t("br"),n._v(" Chính vì như thế, mới khiến cho nàng đã biến thành bây giờ đối với người sống có chút sợ hãi, không muốn nói lời tính cách."),t("br"),t("br"),n._v(" Vốn tưởng rằng, đời này đều không có biện pháp chữa khỏi, không nghĩ tới vị này người người đều cảm thấy là rác rưởi Trương Huyền lão sư, trực tiếp mở miệng nói “Việc nhỏ!”, làm cho nàng khó có thể tin tưởng được lỗ tai của chính mình."),t("br"),t("br"),n._v(" Nếu như đổi lại trước, khẳng định cảm thấy là khoác lác, nhưng đối phương một cái nói ra bị thương nguyên nhân, làm cho nàng nội tâm không nhịn được sinh ra chờ đợi."),t("br"),t("br"),n._v(" “Để ngươi đơn độc đến gian phòng, tự nhiên là vì ngươi trị liệu! Tốt, toàn thân thả lỏng!” "),t("br"),t("br"),n._v(" Trương Huyền từ gian phòng lấy ra một cái không lớn hộp ngọc, nhẹ nhàng mở ra, bên trong bày đầy dài ngắn bất nhất châm bạc."),t("br"),t("br"),n._v(" Võ giả tu luyện, bị thương là chuyện thường xảy ra, bởi vậy, học viện ở mỗi cái phòng học đều trang bị vật này, có thể giúp người khơi thông huyết mạch, tạm thời giảm bớt thương thế."),t("br"),t("br"),n._v(" “Rõ!” Nhìn thấy thiếu niên ngữ khí bình tĩnh thận trọng, tự tin mà không kiêu căng, Vương Dĩnh chẳng biết vì sao, sâu trong nội tâm không tự chủ được tin tưởng lên, tiếp theo toàn thân thanh tĩnh lại."),t("br"),t("br"),n._v(" Vèo!"),t("br"),t("br"),n._v(" Trương Huyền ngồi ở tại chỗ vẫn chưa nhúc nhích, cầm lấy một cây châm bạc, thẳng tắp bắn tới."),t("br"),t("br"),n._v(" Một tiếng kêu khẽ, ẩn chứa hắn một nói chân khí châm bạc, liền thẳng tắp hướng về Vương Dĩnh bay qua, cắm trên chân."),t("br"),t("br"),n._v(" “Đây là... Huyền không hành châm?” Vương Dĩnh con mắt trừng lớn, thân thể mềm mại lần thứ hai run rẩy."),t("br"),t("br"),n._v(" Huyền không hành châm, là không tiếp xúc người bệnh thân thể, lợi dụng võ giả đối với sức mạnh tinh thuần lực chưởng khống, khống chế châm bạc phi hành, chuẩn xác đâm vị!"),t("br"),t("br"),n._v(" Làm như vậy, nam thầy thuốc không cần tiếp xúc người nữ mắc bệnh, là có thể hành châm, có thể tránh khỏi không ít không cần thiết lúng túng."),t("br"),t("br"),n._v(" Này loại hành châm, nói đến đơn giản, trên thực tế hết sức phức tạp, đầu tiên yêu cầu võ giả đối với sức mạnh tuyệt đối khống chế, hơi có sai lệch, liền có thể dẫn đến thất bại! Thứ yếu, đôi mắt lực cũng phải có rất lớn yêu cầu, cách quần áo, cũng không dễ dàng nhận huyệt, một khi nhận sai, sẽ xuất hiện các loại phiền phức!"),t("br"),t("br"),n._v(" Nguyên ngữ đại sư từng nói, muốn hư không hành châm, tu vi ít nhất phải đạt đến võ giả năm tầng Đỉnh Lực cảnh mới có thể làm được!"),t("br"),t("br"),n._v(" Lẽ nào cái này học viện được gọi là rác rưởi lão sư, đã đạt đến loại cảnh giới này rồi?"),t("br"),t("br"),n._v(" Cái này không thể nào a!"),t("br"),t("br"),n._v(" Đạt đến Đỉnh Lực cảnh, mỗi một cái đều tiếng tăm lừng lẫy, giống như Lục Tầm danh sư, căn cứ Lưu lão từng nói, cái này Trương Huyền lão sư, bất quá võ giả ba tầng Chân Khí cảnh, cách năm tầng Đỉnh Lực cảnh, còn có rất lớn một khoảng cách!"),t("br"),t("br"),n._v(" Võ giả ba tầng Chân Khí cảnh, liền có thể triển khai huyền không hành châm?"),t("br"),t("br"),n._v(" Nàng không thể tin được!"),t("br"),t("br"),n._v(" Sưu sưu sưu sưu!"),t("br"),t("br"),n._v(" Chính cảm thấy khó có thể tin, thiếu niên lại bắn ra mấy viên châm bạc."),t("br"),t("br"),n._v(" Liên tiếp phong thanh, châm bạc rơi trên đùi của nàng, vừa vặn tương đồng sâu cạn, đủ thấy đối phương đối với sức mạnh khống chế dĩ nhiên đạt đến mức lô hỏa thuần thanh."),t("br"),t("br"),n._v(" Châm bạc lạc trên chân, Vương Dĩnh đang muốn đối phương làm sao trị liệu, chỉ thấy thiếu niên bàn tay vạch một cái, trên ngân châm ẩn chứa chân khí đồng thời xúc động, xuyên nói."),t("br"),t("br"),n._v(" “A...” "),t("br"),t("br"),n._v(" Chân khí tiến vào trong cơ thể, tê dại một hồi, trước bị phong chắn huyệt đạo, lập tức xông ra."),t("br"),t("br"),n._v(" “Chuyện này... Chuyện này... Đây là trên tam phẩm chân khí độ tinh khiết?” "),t("br"),t("br"),n._v(" Toàn thân tê dại một hồi, Vương Dĩnh “Nhìn” rõ tiến vào trong cơ thể chân khí dáng dấp!"),t("br"),t("br"),n._v(" Trong suốt như nước, không có một chút nào tạp chất."),t("br"),t("br"),t("span",[t("br"),n._v("//ngantruyen.com/"),t("br")]),n._v(" Lại là... Trên tam phẩm chân khí mới có phẩm chất!"),t("br"),t("br"),n._v(" Trên tam phẩm chân khí?"),t("br"),t("br"),n._v(" Đây chính là thần, Thánh Cấp Công Pháp mới có thể tu luyện ra được, toàn bộ Thiên Huyền vương quốc, chưa từng nghe nói ai có thể tu luyện đi ra, vị này rác rưởi nhất lão sư, lại có? Ta không biết nhìn lầm đi..."),t("br"),t("br"),n._v(" “Tốt!” "),t("br"),t("br"),n._v(" Chính đang chấn động, tiếp theo toàn thân buông lỏng, châm bạc đã bị đối phương lấy đi."),t("br"),t("br"),n._v(" Tiếp theo Vương Dĩnh liền cảm thấy, hai chân một trận ung dung, trước nay chưa có thoải mái."),t("br"),n._v(" Sau khi bị thương, hai chân của nàng tuy rằng có thể bình thường bước đi, nhưng cùng bó thạch cao như thế, không giây phút nào không cảm thấy cứng ngắc, mà bây giờ, từng trận thoải mái cảm giác kéo tới, như phá vỡ một loại nào đó gông xiềng, trở nên cực kỳ linh hoạt."),t("br"),t("br"),n._v(" “Chân của ta...” "),t("br"),t("br"),n._v(" Có ngốc, cũng biết, chân của nàng đã hoàn toàn khỏi rồi!"),t("br"),t("br"),n._v(" “Lão sư, đa tạ...” "),t("br"),t("br"),n._v(" Đầu gối mềm nhũn, Vương Dĩnh trực tiếp ngã quỵ ở mặt đất, viền mắt một đỏ."),t("br"),t("br"),n._v(" Vì ngày đó, bọn nàng cần phải thời gian quá dài, thậm chí một lần tràn đầy tuyệt vọng, nằm mơ đều không có nghĩ đến... Như vậy ngoan cố bệnh tật, bị học viện kém nhất một cái lão sư chữa khỏi!"),t("br"),t("br"),n._v(" “Kỳ thực... Hắn là có lớn bản lãnh người, chỉ là làm người biết điều...” "),t("br"),t("br"),n._v(" Trong đầu bốc lên ý nghĩ này."),t("br"),t("br"),n._v(" Nếu như không phải biết điều, không nói giảng bài làm sao, đơn có thể giúp tự mình trị liệu chân nhanh này một hạng, ở trong học viện liền nhất định có thể lưu lại rất đại danh khí, làm sao sẽ rơi vào thầy giáo sát hạch thứ nhất đếm ngược kết cục?"),t("br"),t("br"),n._v(" Có ý nghĩ này, Vương Dĩnh ngã quỵ ở mặt đất, cam tâm tình nguyện bái Trương Huyền vi sư, cũng chưa muốn thôi học."),t("br"),t("br"),n._v(" “Hừm, nếu chân thương lành, đi ra ngoài cố gắng tu luyện đi, có vấn đề gì, lại tới tìm ta!” Trương Huyền vung vung tay, dặn dò một tiếng: “Thuận tiện đem Lưu Dương kêu đến!” "),t("br"),t("br"),n._v(" “Rõ!” Vương Dĩnh đầy mặt hưng phấn lùi ra."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Một lúc nữa ta dự định thôi học, các ngươi có ai cùng ta đồng thời sao?” "),t("br"),t("br"),n._v(" Trong lớp học, Lưu Dương, Trịnh Dương, Triệu Nhã, Viên Đào bốn người biết nhau về sau, Lưu Dương mở miệng."),t("br"),t("br"),n._v(" Nói thật hắn mười phần phiền muộn."),t("br"),t("br"),n._v(" Nhập học sát hạch 100 người đứng đầu người, rất nhiều cao cấp lão sư, đều sẽ muốn cướp, kết quả nhưng bởi vì hay vị lão sư ở giữa đánh cược, bại bởi vị này toàn bộ học viện kém nhất gia hỏa!"),t("br"),t("br"),n._v(" Mãnh liệt cảm giác nhục nhã, để hắn có chút phát điên."),t("br"),t("br"),n._v(" Bất kể nói thế nào, ngày hôm nay đều phải thôi học, dù cho đuổi học... Bất luận người nào đều không thể ngăn cản bước chân của hắn!"),t("br"),t("br"),n._v(" “Ta... Cũng nghĩ lùi, bất quá, cẩn thận lão sư tức giận, giáo huấn ngươi một trận!” Tên béo Viên Đào không nhịn được nói."),t("br"),t("br"),n._v(" “Tức giận? Hừ, hắn tức giận có thể làm gì ta? Ta cũng không muốn bị hắn chỉ điểm tẩu hỏa nhập ma!” Lưu Dương hừ lạnh."),t("br"),t("br"),n._v(" “Tẩu hỏa nhập ma?” Trịnh Dương nghi ngờ nhìn sang."),t("br"),t("br"),n._v(" Hắn chỉ biết mình vị lão sư này, thầy giáo sát hạch thứ tự rất kém cỏi, thứ nhất đếm ngược, còn chưa từng nghe nói có thể đem người chỉ điểm tẩu hỏa nhập ma."),t("br"),t("br"),n._v(" “Ừm!” Lưu Dương hừ nói: “Không tin các ngươi hãy chờ xem, một lúc nữa Vương Dĩnh bạn học đi ra, nhất định sẽ sắc mặt rất khó nhìn! Thậm chí cảm thấy được bị lừa! Cái này Trương Huyền lão sư trình độ ra sao, ta đã sớm hỏi thăm rõ ràng, nếu như có thể cho người ta cố gắng chỉ điểm, tuyệt đối không thể rơi vào kết quả như thế, bị sở hữu lão sư xem thường...” "),t("br"),t("br"),n._v(" Kẹt kẹt!"),t("br"),t("br"),n._v(" Chính đang nói chuyện, cửa phòng mở ra, Vương Dĩnh một mặt hưng phấn đi ra."),t("br"),t("br"),n._v(" “Ừm?” "),t("br"),t("br"),n._v(" Vốn tưởng rằng nàng sẽ đầy mặt ủ rũ, ai biết một mặt hưng phấn, Lưu Dương không nhịn được sững sờ."),t("br"),t("br"),n._v(" Cái khác mấy cái bạn học cũng rất là kỳ quái."),t("br"),t("br"),n._v(" Phần này cao hứng xuất phát từ nội tâm, khẳng định là có thu hoạch lớn, mới cái bộ dáng này."),t("br"),t("br"),n._v(" Lẽ nào vị này tiếng tăm kém nhất lão sư, thật có thể cố gắng chỉ điểm không được"),t("br"),t("br"),n._v(" Một lần chỉ điểm, liền có thể để học viên hưng phấn như thế, như nhặt được trân bảo, coi như học viện tiếng tăm rất lớn Lục Tầm lão sư cũng không làm được đi!"),t("br"),t("br"),n._v(" “Lưu Dương, lão sư để ngươi đi vào!” Không biết những người khác nghĩ cái gì, Vương Dĩnh bàn giao một câu, liền đi một bên tu luyện."),t("br"),t("br"),n._v(" “Ta?” Lưu Dương cắn răng một cái: “Ta ngược lại muốn xem xem ngươi có thể chỉ điểm cái gì! Chỉ điểm không ra, ta nhất định phải thôi học, thôi học!” "),t("br"),t("br"),n._v(" Thầm nhủ trong lòng, đi vào gian phòng."),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-29"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-31"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);