(window.webpackJsonp=window.webpackJsonp||[]).push([[1524],{1879:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-293-han-goi-truong-huyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-293-han-goi-truong-huyen"}},[n._v("#")]),n._v(" Chương 293: Hắn gọi Trương Huyền")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Hô!”"),t("br"),t("br"),n._v("Một hơi phun ra, Trương Huyền xoa xoa mi tâm."),t("br"),t("br"),n._v("Thư họa sư công hội tàng thư khố thư tịch tuy nhiều, với hắn mà nói, cũng liền nhiều chạy một hồi sự tình, không đến nửa canh giờ, liền đã thu sạch tiến vào trong óc."),t("br"),t("br"),n._v("“Trước đó mặc dù có thể làm ra ngũ cảnh thư hoạ, nhưng đối thư hoạ lý giải, còn có chút thiếu, có những sách vở này bổ sung, tri thức mở rộng gấp bội, xứng đáng tam tinh thư hoạ sư cái thân phận này!”"),t("br"),t("br"),n._v("Nhìn xem Thiên Đạo thư viện trong quán chỉnh chỉnh tề tề rất nhiều thư tịch, Trương Huyền duỗi lưng một cái."),t("br"),t("br"),n._v("Trước đó mượn nhờ Lục Trầm đại sư chỗ nào sưu tập rất nhiều kỹ xảo, mặc dù có thể vẽ ra ngũ cảnh tác phẩm, nhưng trên thực tế, đối cái gọi là cơ sở, lịch sử, phong cách, lưu phái... Biết cũng không nhiều."),t("br"),t("br"),n._v("Thật giống như Đoàn Dự, có thể thi triển ra đứng đầu nhất Lục mạch thần kiếm, đánh Cô Tô Mộ Dung Phục tè ra quần, thô thiển võ công lại một chút cũng không biết, cùng côn đồ lưu manh chiến đấu, làm không cẩn thận còn không phải là đối thủ."),t("br"),t("br"),n._v("Trương Huyền cũng là như thế này, để hắn tranh ngũ cảnh tác phẩm, nhẹ nhõm như ý, nhưng để hắn tranh lục thực cảnh, thật đúng là làm không được."),t("br"),t("br"),n._v("Bất quá, có nhiều như vậy thư tịch, vấn đề này liền không coi vào đâu."),t("br"),t("br"),n._v("Chỉ cần toàn bộ xem hết, bàn về tri thức lượng dự trữ, coi như lợi hại hơn nữa tam tinh thư hoạ sư, đều kém xa tít tắp, thậm chí những cái kia tứ tinh thư hoạ sư có thể hay không có hắn loại này kiến thức, đều còn khó nói."),t("br"),t("br"),n._v("“Chỉ là... Thiên Huyền vương quốc tàng thư khố, Thú đường, Độc điện, thư hoạ sư công lại... Đã sưu tập mấy ngàn vạn bản bí tịch, nếu như chỉ dựa vào đọc sách, năm nào tháng nào mới có thể xem hết?”"),t("br"),t("br"),n._v("Sưu tập xong sách, vấn đề mới lại đi ra, để hắn có chút đau đầu."),t("br"),t("br"),n._v("Từ Thiên Huyền vương quốc đi tới, hắn ghi vào Thiên Đạo thư viện quán thư tịch, đã vượt qua ngàn vạn bản. Mặc dù những ngày gần đây, chỉ cần nhàn hạ ngay tại thức hải đọc sách, tốc độ cũng rất nhanh, nhưng một ngày có thể nhìn cái một, hai ngàn bản, cũng chính là cực hạn, đối mặt ngàn vạn bản cự lấy... Trên cơ bản hạt cát trong sa mạc."),t("br"),t("br"),n._v("Hơn nữa, nương theo hắn học được chức nghiệp càng nhiều, địa vị càng cao, thư tịch sẽ càng nhiều, muốn đem những vật này toàn bộ chuyển hóa thành kiến thức của mình, không có mấy năm công phu, căn bản không có khả năng hoàn thành."),t("br"),t("br"),n._v("“Nếu như còn có kim sắc trang sách liền tốt...”"),t("br"),t("br"),n._v("Trong lòng hơi động."),t("br"),t("br"),n._v("Kim sắc trang sách, Thiên Đạo chi sách bên trong hình thành đồ vật."),t("br"),t("br"),n._v("Có thể để cho trong thư viện thư tịch tri thức, trong nháy mắt chuyển hóa thành bản thân, nếu có thứ này, liền có thể tuỳ tiện nhớ kỹ nhiều như vậy nội dung, không cần vất vả từng quyển từng quyển lật nhìn."),t("br"),t("br"),n._v("“Vốn cho rằng là bị người cảm kích liền sẽ hình thành, hiện tại xem ra, hẳn không phải là! Ngụy trang Dương sư thời điểm, nhận qua không ít cảm kích, đều không có hình thành, nói rõ không chỉ yếu tố này!”"),t("br"),t("br"),n._v("Nhớ tới kim sắc trang sách Thiên Đạo chi sách, bắt đầu hồi ức thứ này khi nào xuất hiện."),t("br"),t("br"),n._v("Thứ nhất bản khi nào xuất hiện, hắn không biết, nhưng cuốn thứ hai xuất hiện thời điểm, là Triệu Nhã bọn người ở tại tỷ thí đài, muốn khiêu chiến Lục Tầm, vì hắn rửa sạch sỉ nhục."),t("br"),t("br"),n._v("Khi đó mấy người đối với hắn cảm kích, mang theo nồng đậm thầy trò chi tình."),t("br"),t("br"),n._v("Chẳng lẽ lại... Chỉ có thầy trò ở giữa cảm kích, mới có thể để thứ này xuất hiện?"),t("br"),t("br"),n._v("Ngụy trang Dương sư, những cái kia cảm kích càng nhiều hơn chính là kính sợ, cũng không xen lẫn loại cảm tình này."),t("br"),t("br"),n._v("“Có thể thử một chút!”"),t("br"),t("br"),n._v("Nghĩ đến nơi này, nhãn tình sáng lên."),t("br"),t("br"),n._v("Đối với không cách nào xác nhận sự tình, người khác không có cách, hắn vẫn phải có."),t("br"),t("br"),n._v("Tiện tay lấy ra một bản trống không sách vở, đem trong lòng suy đoán viết lên đi, bàn tay nhẹ nhàng nhấn một cái."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một trận lắc lư, trong thư viện xuất hiện một bản giống nhau là thư tịch."),t("br"),t("br"),n._v("Tiện tay lật ra."),t("br"),t("br"),n._v("“Chân thành tha thiết thầy trò cảm kích, có thể hình thành kim sắc trang sách... Không sai! Quả là thế!”"),t("br"),t("br"),n._v("Nhìn thấy thư tịch tốt nhất kết quả, Trương Huyền mặt mũi tràn đầy kích động."),t("br"),t("br"),n._v("Sớm biết cái này kim sắc thư tịch là như thế này hình thành, đoạn đường này nhìn cọng lông sách, mệt chết đi sống lại, cũng không có nhớ kỹ bao nhiêu."),t("br"),t("br"),n._v("“Xem ra muốn thu hai cái học sinh, nghĩ biện pháp... Để bọn hắn cảm kích một cái!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Chỉ cần kim sắc trang sách hình thành, thư viện sưu tập những sách vở này, liền có thể tuỳ tiện chuyển hóa thành kiến thức của mình."),t("br"),t("br"),n._v("Hắn hiện tại thiếu hụt liền là tri thức tích lũy."),t("br"),t("br"),n._v("Chỉ cần có đầy đủ tri thức, coi như về sau không cần mượn nhờ Thiên Đạo thư viện quán, cũng có thể nhẹ nhõm nhìn ra người khác thiếu hụt cùng vấn đề."),t("br"),t("br"),n._v("“Triệu Nhã bọn hắn cũng không biết đã tới chưa, chỉ có ngày mai đi Danh Sư đường hỏi một chút. Hiện tại vừa vặn không có việc gì, bốn phía đi dạo, có lẽ liền có thể tìm người chỉ điểm một chút, thuận tiện thu cái học sinh loại hình...”"),t("br"),t("br"),n._v("Nghĩ đến nơi này, Trương Huyền không tại tàng thư khố xoắn xuýt, nhấc chân đi ra ngoài."),t("br"),t("br"),n._v("Rời đi tàng thư khố lần nữa gặp được hai vị phó hội trưởng, hàn huyên vài câu, liền không nhiều lắm đợi, đi ra thư họa sư công hội."),t("br"),t("br"),n._v("Thời gian quý giá, nghĩ biện pháp hình thành kim sắc trang sách trọng yếu nhất."),t("br"),t("br"),n._v("Hắn vừa đi ra công hội, một thanh niên, liền từ mặt khác một bên, đi đến."),t("br"),t("br"),n._v("Chính là vừa tới Thiên Vũ vương thành Lục Tầm."),t("br"),t("br"),n._v("Vừa rồi ở 【 Thiên Vũ lữ quán ] cách nơi này chỉ có mấy khoảng trăm thước, thấy Lưu sư an bài tốt đám người, hắn lại tới."),t("br"),t("br"),n._v("“Muốn thi danh sư, nhất định phải có một hạng phụ trợ chức nghiệp, ta từ nhỏ liền cùng phụ thân học tập vẽ tranh, sớm có thể làm ra lục thực tác phẩm, vừa vặn trước tiên đem thư hoạ học đồ thi, về sau lại nghĩ biện pháp khảo hạch chính thức thư hoạ sư!”"),t("br"),t("br"),n._v("Nhìn thấy thư họa sư công hội trước cửa rồng bay phượng múa chữ lớn, Lục Tầm tâm thần khuấy động."),t("br"),t("br"),n._v("Năng lực hiện tại của hắn, khảo hạch chính thức thư hoạ sư, còn có chút miễn cưỡng, bất quá, khảo hạch học đồ, nên vấn đề không lớn."),t("br"),t("br"),n._v("Đi vào công hội, vừa tiến đến, liền thấy trên vách tường treo một vài bức tác phẩm, sơn thủy, chim tước, gió xuân, mặt trời rực rỡ... Thế mà tất cả đều đạt đến lục thực cảnh, vừa nhìn liền biết, vẽ ra những này tác phẩm người, đối thư hoạ lý giải, tất cả đều không yếu hơn hắn."),t("br"),t("br"),n._v("“Không hổ là thư họa sư công hội, lợi hại...”"),t("br"),t("br"),n._v("Nhịn không được cảm khái, Lục Tầm hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Cái này công hội, thành lập thời gian không dài, Lục Trầm đại sư đều chưa từng tới, hắn tự nhiên cũng cũng không biết bên trong có thứ gì, không nghĩ tới mới vừa vào cửa nhìn thấy nhiều như vậy lợi hại tác phẩm, nhịn không được cảm giác mới mẻ."),t("br"),t("br"),n._v("Trong lòng âm thầm bội phục."),t("br"),n._v("“Vị công tử này, không biết cần gì tác phẩm hội họa, tiểu nhân có thể mang ngươi xem một chút!”"),t("br"),t("br"),n._v("Chính tại tới trước, vừa rồi nghênh đón Trương Huyền vị kia áo xanh gã sai vặt đi tới, một mặt cung kính."),t("br"),t("br"),n._v("“Ta không phải tới mua đồ, mà là... Muốn khảo hạch thư hoạ sư học đồ! Không biết ở nơi nào?” Lục Tầm cười nói."),t("br"),t("br"),n._v("“Khảo hạch học đồ?”"),t("br"),t("br"),n._v("Áo xanh gã sai vặt sững sờ."),t("br"),t("br"),n._v("Bình thường khảo hạch thư hoạ sư, học đồ, mấy tháng không xuất hiện một cái, hôm nay đến cùng làm sao vậy, thành đàn tới!"),t("br"),t("br"),n._v("Vừa thi xong một cái Quý Mặc công tử, tới một cái Trương tông sư, Trương tông sư vừa rời đi, cái này lại toát ra một cái muốn khảo học đồ..."),t("br"),t("br"),n._v("Quá náo nhiệt đi!"),t("br"),t("br"),n._v("“Mời tới bên này!”"),t("br"),t("br"),n._v("Trong lòng nghi hoặc, lại không dám thất lễ."),t("br"),t("br"),n._v("Hắn chỉ là công hội nhân viên phục vụ, học đồ thế nhưng là có cơ hội trở thành chính thức thư hoạ sư tồn tại, cả hai địa vị có chênh lệch rất lớn."),t("br"),t("br"),n._v("“Ừm!” Lục Tầm đi theo."),t("br"),t("br"),n._v("“Thật là lợi hại, vậy thì thật là ngũ cảnh tác phẩm!”"),t("br"),t("br"),n._v("“Đúng vậy a, lần thứ nhất khảo hạch liền vẽ ra ngũ cảnh tác phẩm, hơn nữa ngay cả hai mươi cũng chưa tới, thật sự là thiên tài!”"),t("br"),t("br"),n._v("“Ngay cả Quý Mặc công tử đều bại, thậm chí bị đuổi ra công hội, loại thiên phú này, thật khiến cho người ta bội phục!”"),t("br"),t("br"),n._v("“Vừa rồi vẽ tranh cảnh tượng, bây giờ còn đang trong óc xoay quanh, thật sự là quá đặc sắc!”"),t("br"),t("br"),n._v("“Không nghĩ tới man thú đều có thể vẽ tranh, đây là ta trước đó nghĩ cũng không dám nghĩ...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đi tại rộng rãi trong đại điện, nguyên một đám đè thấp tiếng nghị luận, từ bốn phương tám hướng truyền đến."),t("br"),t("br"),n._v("Nửa canh giờ trước giao đấu, mặc dù trong đám người có không ít Quý Mặc công tử ủng độn, cũng không ít chân chính yêu quý thư hoạ quý tộc hiển hách."),t("br"),t("br"),n._v("Cái kia Trương tông sư, lần thứ nhất tại công hội biểu diễn, liền cơ hồ đem toàn bộ công người biết đều sợ ngây người."),t("br"),t("br"),n._v("“Bọn hắn nói... Lần thứ nhất khảo hạch, liền vẽ ra ngũ cảnh tác phẩm, là có ý gì?”"),t("br"),t("br"),n._v("Nghe được chung quanh nghị luận càng ngày càng nhiều, Lục Tầm cũng nhịn không được nữa hiếu kỳ, nhìn về phía bên người áo xanh gã sai vặt."),t("br"),t("br"),n._v("“A, bọn hắn nói là vừa rồi giao đấu!” Nghe được tra hỏi, gã sai vặt cũng hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Vừa rồi giao đấu hắn nhưng là ở một bên nhìn rõ ràng, thậm chí vị tông sư kia đều là hắn tự mình tiếp đãi, có cực lớn tự hào."),t("br"),t("br"),n._v("“Giao đấu?”"),t("br"),t("br"),n._v("“Đúng vậy a, ngay tại vừa rồi...” Áo xanh gã sai vặt rất mau đem vừa rồi chuyện phát sinh, kỹ càng nói một lần."),t("br"),t("br"),n._v("“Ngươi nói... Vị tông sư kia, để man thú dính mực, tại trên giấy tuyên đi một vòng, sau đó cầm lên tùy tiện lắc một cái, bức họa kia liền biến thành tam cảnh tác phẩm?”"),t("br"),t("br"),n._v("Lục Tầm trừng to mắt."),t("br"),t("br"),n._v("Lâu dài cùng phụ thân học tập thư hoạ, hắn biết tam cảnh tác phẩm độ khó."),t("br"),t("br"),n._v("Coi như phụ thân, cũng không thể cam đoan mỗi lần đều vẽ ra đến, người này để man thú đi loạn, tùy tiện lắc một cái liền làm đến... Đây không phải đang khoác lác a?"),t("br"),t("br"),n._v("Bất quá, giật mình còn ở phía sau."),t("br"),t("br"),n._v("“Bó đuốc một sấy... Biến thành tứ cảnh?”"),t("br"),t("br"),n._v("“Phun ra ngụm nước, biến thành ngũ cảnh?”"),t("br"),t("br"),n._v("Càng về sau nghe, Lục Tầm càng cảm thấy đầu óc chuyển không đến, cả người đều có chút phát điên."),t("br"),t("br"),n._v("Làm sao nghe giống là nói sách kể chuyện xưa?"),t("br"),t("br"),n._v("Nếu như là thật, vị tông sư này, đối thư hoạ năng lực chưởng khống, cũng thật là đáng sợ đi!"),t("br"),t("br"),n._v("“Đương nhiên là thật, ta tự mình tiếp đãi vị tông sư kia, toàn bộ tỷ thí quá trình, tận mắt nhìn thấy, tuyệt sẽ không sai!”"),t("br"),t("br"),n._v("Áo xanh gã sai vặt nhìn ra hắn không tin, lời thề son sắt đường."),t("br"),t("br"),n._v("“Quá lợi hại... Nếu như hắn nói là sự thật, ta có thể trở thành vị tông sư này học sinh, thư hoạ bên trên nhất định có thể đột nhiên tăng mạnh, thành là chân chính thư hoạ sư cũng liền đơn giản!”"),t("br"),t("br"),n._v("Nghe đối phương nói như thế xác định, Lục Tầm trong lòng hơi động, một cái ý nghĩ xông ra, lại khó ngăn chặn."),t("br"),t("br"),n._v("Khảo hạch danh sư, chỉ riêng trở thành học đồ không được, nhất định phải có được chính thức phụ trợ chức nghiệp."),t("br"),t("br"),n._v("Hắn mặc dù từ tiểu học tranh, nhưng cũng biết, từ lục thực cảnh đến Linh Động cảnh, có chênh lệch rất lớn, nếu như chỉ là bản thân học tập, không có mấy năm, thậm chí mười mấy năm công phu căn bản không thể nào làm được."),t("br"),t("br"),n._v("Bất quá... Phải có cái lợi hại lão sư chỉ điểm, liền đơn giản."),t("br"),t("br"),n._v("Cái khác Tông sư hắn không biết, gã sai vặt mới vừa nói những cái kia, giản làm cho người ta nhìn mà than thở."),t("br"),t("br"),n._v("Có thể làm cho tác phẩm hội họa tùy ý tấn cấp, đồng thời đạt tới ngũ cảnh, nên đối thư hoạ sâu bao nhiêu hiểu rõ mới có thể làm đến điểm ấy?"),t("br"),t("br"),n._v("Bái hắn làm thầy, tuyệt đối có thể giải quyết nghi hoặc, làm không cẩn thận tiến về thư hoạ sư con đường, liền sẽ đơn giản không ít."),t("br"),t("br"),n._v("“Người tông sư này... Tên gọi là gì? Không biết bây giờ người ở chỗ nào? Có thu hay không học sinh?”"),t("br"),t("br"),n._v("Nghĩ đến nơi này, cố nén kích động trong lòng, Lục Tầm sắc mặt nắm đỏ, nhịn không được hỏi."),t("br"),t("br"),n._v("“Vị tông sư này, hắn giống như gọi... Trương Huyền!” Áo xanh gã sai vặt suy nghĩ một chút, nhịn không được nói."),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("Lục Tầm thân thể run lên, con mắt một cái trợn tròn."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-301"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-303"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);