(window.webpackJsonp=window.webpackJsonp||[]).push([[1526],{1881:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-295-bai-ta-lam-thay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-295-bai-ta-lam-thay"}},[n._v("#")]),n._v(" Chương 295: Bái ta làm thầy")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Nói, có phải hay không Quý Mặc phái các ngươi tới?”"),t("br"),t("br"),n._v("Quát lớn xong Khiếu Thiên thú, Trương Huyền đi tới người dẫn đầu trước mặt."),t("br"),t("br"),n._v("Bọn gia hỏa này vừa mới bắt đầu còn có chút cường ngạnh, thấy hắn làm ra độc dược, buộc ăn vào, thế mới biết trước mắt vị này, không riêng gì một vị thư hoạ sư, võ giả, càng là một vị độc sư!"),t("br"),t("br"),n._v("Không thể kiên trì được nữa, một năm một mười đem sự tình toàn bộ nói."),t("br"),t("br"),n._v("“Quý Mặc công tử cũng không để các ngươi giết ta, mà là cởi sạch y phục của ta, treo ở trên cây, thuận tiện dùng ghi chép ngọc tinh ghi chép lại?”"),t("br"),t("br"),n._v("Nghe được mục đích của đối phương, Trương Huyền không còn gì để nói."),t("br"),t("br"),n._v("Bất quá, suy nghĩ một chút cũng liền giật mình."),t("br"),t("br"),n._v("Mình bây giờ là tam tinh thư hoạ sư, bị người khác nhục nhã, khẳng định không mặt mũi nói ra, tự giác đem sự tình đè xuống."),t("br"),t("br"),n._v("Mà nếu là giết, thư họa sư công hội khẳng định phải kỹ càng truy tra. Vừa náo ra mâu thuẫn liền bị giết, tra ra hắn đến, không tính quá khó."),t("br"),t("br"),n._v("Đổi lại nhất tinh, nhị tinh bị giết, kiểu gì cũng sẽ khả năng không quá quan tâm, hắn một cái không đủ hai mươi tuổi tam tinh thư hoạ sư thật muốn bị giết, đến lúc đó Quý gia chỉ sợ đều không chịu nổi lửa giận."),t("br"),t("br"),n._v("Về phần dùng ghi chép ngọc tinh ghi chép, vậy liền càng đơn giản hơn, chỉ muốn nắm giữ cái này, chẳng khác nào nắm giữ át chủ bài cùng quyền chủ động. Về sau bản thân biết rõ bị nhục nhã, vì phòng ngừa tin tức tiết ra ngoài, cũng tuyệt không dám đối Quý gia làm ra cái gì quá phận cử động."),t("br"),t("br"),n._v("Không thể không nói, vị này Quý Mặc công tử nhìn lỗ mãng, trên thực tế suy tính, rất là chu đáo."),t("br"),t("br"),n._v("Chỉ tiếc, đánh giá thấp thực lực của mình, cuối cùng để năm người này rơi thê thảm như thế bộ dáng."),t("br"),t("br"),n._v("Biết mục đích của đối phương, Trương Huyền nhãn châu xoay động, tại trên thân mọi người riêng phần mình đá một cước."),t("br"),t("br"),n._v("“Không quản các ngươi dùng biện pháp gì, đem vị này Quý Mặc công tử cởi sạch quần áo treo ở trên cây, sau đó dùng ghi chép ngọc tinh quay xuống, đổi lấy giải dược. Nếu không, trong năm ngày, chờ lấy ruột xuyên bụng nát, độc phát thân vong đi! Không cần kỳ vọng tìm mặt khác độc sư giải quyết, lưu trên người các ngươi độc, là ta độc nhất vô nhị bí chế, coi như tam tinh độc sư tới đều giải không được!”"),t("br"),t("br"),n._v("Lấy đạo của người, trả lại cho người."),t("br"),t("br"),n._v("Ngươi không là muốn cho ta xấu mặt sao?"),t("br"),t("br"),n._v("Vậy thì tốt, liền để của ngươi mấy cái này thuộc hạ, đem biện pháp của ngươi dùng ở trên thân thể ngươi, nhìn nhìn hiệu quả gì."),t("br"),t("br"),n._v("Về phần cái gọi là độc môn bí chế độc dược chỉ là đem Thiên Đạo chân khí rót vào đối phương trong cơ thể. Bất quá, thứ này ngoại trừ hắn, không người có thể giải, ngược lại cũng là sự thật, chỉ cần hơi chuyển động ý nghĩ một chút, liền sẽ tại huyệt đạo chỗ sâu nổ tung, để bọn hắn làm trận tử vong."),t("br"),t("br"),n._v("Cũng không nói dối."),t("br"),t("br"),n._v("“Vâng, vâng!”"),t("br"),t("br"),n._v("Cảm nhận được trên người bị gieo xuống kịch độc, mấy người khóe miệng co giật."),t("br"),t("br"),n._v("Độc sư luôn luôn kinh khủng, giết người ở vô hình, đắc tội bọn hắn, cũng chỉ có thể chờ lấy tử vong."),t("br"),t("br"),n._v("Sớm biết thiếu gia muốn bọn hắn tìm người này khủng bố như thế, trực tiếp chống lại mệnh lệnh đi đường, cũng không trở thành tìm đường chết tới a."),t("br"),t("br"),n._v("“Cút đi!”"),t("br"),t("br"),n._v("An bài xong, Trương Huyền không tiếp tục để ý đối phương."),t("br"),t("br"),n._v("“Rõ!”"),t("br"),t("br"),n._v("Năm người giãy dụa lấy đứng dậy, dắt nhau đỡ, nhanh nhanh rời đi rừng cây."),t("br"),t("br"),n._v("Vừa về tới vương thành, hai mặt nhìn nhau."),t("br"),t("br"),n._v("“Chúng ta làm sao bây giờ?”"),t("br"),t("br"),n._v("Thương thế cuối cùng Chương Khánh hữu khí vô lực nhìn qua."),t("br"),t("br"),n._v("“Còn có thể làm sao? Chỉ có thể theo hắn phân phó làm, cùng lắm thì làm xong... Vĩnh viễn rời đi Thiên Vũ vương quốc, không về nữa!” Người dẫn đầu cắn răng."),t("br"),t("br"),n._v("Bọn hắn mặc dù thụ Quý gia thúc đẩy, cũng chỉ là xem ở tiền cùng tài nguyên trên mặt mũi, cùng tính mệnh so sánh, đương nhiên là người sau quan trọng hơn."),t("br"),t("br"),n._v("Lại nói, nếu không phải cái này Quý Mặc công tử tùy hứng, lại làm sao có thể đắc tội nhân vật lợi hại như thế? Để bọn hắn rơi vào thê thảm như thế bộ dáng?"),t("br"),t("br"),n._v("Đã sớm đối gia hỏa này hận thấu xương."),t("br"),t("br"),n._v("“Chuyện này cần bàn bạc kỹ hơn, đuổi trong vòng năm ngày làm xong...”"),t("br"),t("br"),n._v("Có quyết định, năm người uống máu ăn thề, kéo lấy khập khễnh thân thể, trước tìm địa phương chữa thương đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không còn quan tâm bị đánh tàn phế năm người, Trương Huyền đang muốn về thành, tìm kiếm Thiên Vũ học viện, liền nghe đến cách đó không xa trong dãy núi, vang lên một trận thanh âm huyên náo, tựa hồ có người tại chiến đấu."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Thân thể nhảy lên, đạp vào Khiếu Thiên thú lưng, gào thét một tiếng, man thú ngửa mặt lên trời mà lên, thừa dịp bóng đêm hướng âm thanh âm vang lên địa phương bay đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thiên Vũ lữ quán."),t("br"),t("br"),n._v("“Ngươi nói... Lão sư buổi tối hôm nay mới khảo hạch xong thư hoạ sư?”"),t("br"),t("br"),n._v("Triệu Nhã bọn người nhìn qua, nguyên một đám mặt mũi tràn đầy kích động."),t("br"),t("br"),n._v("Vốn cho rằng lão sư còn phải cần một khoảng thời gian mới có thể đi tới vương thành, không nghĩ tới vậy mà so với bọn hắn còn sớm đến."),t("br"),t("br"),n._v("“Ta cũng là nghe thư hoạ sư công hội người nói có cái gọi Trương Huyền người, khảo hạch tam tinh thư hoạ sư thành công, về phần có phải hay không... Sư bá, ta cũng không rõ lắm!”"),t("br"),t("br"),n._v("Lục Tầm nói."),t("br"),t("br"),n._v("Thi xong học đồ hắn liền lập tức trở về đến đem tin tức nói cho đám người."),t("br"),t("br"),n._v("“Khẳng định đúng vậy, có thể làm ra ngũ cảnh tác phẩm hội họa, lại gọi Trương Huyền, nào có trùng hợp như vậy sự tình?” Một bên Hoàng Ngữ nói."),t("br"),t("br"),n._v("Nàng thế nhưng là thấy tận mắt Trương Huyền vẽ tranh, tốc độ lại nhanh lại lợi hại, trình độ chi cao, làm người ta nhìn mà than thở, làm ra cái man thú vẽ tranh mánh lới, cũng không phải là không được."),t("br"),t("br"),n._v("“Đã lão sư thi xong thư hoạ sư chưa tới một canh giờ, khẳng định không đi xa, chúng ta lân cận tìm xem, có lẽ liền có thể đụng tới!”"),t("br"),t("br"),n._v("“Đúng vậy a, ta cũng muốn đi tìm tìm lão sư!”"),t("br"),t("br"),n._v("Nghe đến lão sư liền ở phụ cận đây, Triệu Nhã bọn người cũng không ngồi yên nữa, nhao nhao đứng dậy."),t("br"),t("br"),n._v("“Vậy thì tốt, các ngươi ra ngoài đi dạo, hai canh giờ bên trong, tìm không thấy tất cả đều trở về!”"),t("br"),n._v("Lưu sư không tiện cự tuyệt, bàn giao một câu."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Triệu Nhã bọn người rốt cuộc kìm nén không được, nguyên một đám vội vã đi ra ngoài."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Rầm rầm rầm!"),t("br"),t("br"),n._v("Chân khí lăng không, khí kình tung hoành, một người một thú ngay tại nơi núi rừng sâu xa chiến đấu."),t("br"),t("br"),n._v("“Tông Sư cảnh cường giả cùng Tông Sư cảnh man thú?”"),t("br"),t("br"),n._v("Đứng tại Khiếu Thiên thú trên lưng, nhìn xem chiến đấu song phương, Trương Huyền có chút kinh ngạc."),t("br"),t("br"),n._v("Phía dưới chiến đấu lại là một cái Tông sư cường giả đỉnh phong, tới giao chiến, thì là một đầu Tông sư hậu kỳ Xích Thân hổ."),t("br"),t("br"),n._v("Xích Thân hổ, cùng hắn lúc trước thu phục Kim Thân Thiết Tiễn Báo hình thể không sai biệt lắm, da lông kiên cố, lực lượng mười phần."),t("br"),t("br"),n._v("Đầu này rõ ràng so Thiết Tiễn báo cường đại, một móng một trảo, đều giống như muốn xé rách không khí."),t("br"),t("br"),n._v("Địa phương chiến đấu, cây cối vỡ vụn, nham thạch biến thành bụi phấn, tựa hồ cũng không chịu nổi lực lượng của bọn hắn trùng kích."),t("br"),t("br"),n._v("Xích Thân hổ tuy chỉ là Tông sư hậu kỳ, lại có thể vượt cấp chiến đấu, cái kia Tông sư đỉnh phong thủ đoạn tuy nhiều, cũng không thể tránh được, một người một thú đánh kịch liệt, ai cũng không gây thương tổn được ai."),t("br"),t("br"),n._v("Bành bành bành bành!"),t("br"),t("br"),n._v("Lại đấu một hồi, biết không kết quả, Xích Thân hổ thô to vẫy đuôi một cái, quay người rời đi."),t("br"),t("br"),n._v("Thấy nó đi xa, vị này Tông sư đỉnh phong cũng nhẹ nhàng thở ra, cũng đang muốn rời đi, đột nhiên con ngươi co rụt lại, ngẩng đầu nhìn đến không trung bay múa Khiếu Thiên thú."),t("br"),t("br"),n._v("Mặc dù là đêm tối, nhưng Khiếu Thiên thú bay không cao, vừa rồi thời điểm chiến đấu, khả năng không phát hiện được, chiến đấu kết thúc, cường đại như thế sinh mệnh xuất hiện ở trên không, nếu là không biết, thật liền có thể tự sát."),t("br"),t("br"),n._v("“Không biết vị tiền bối nào giá lâm, không thể nghênh đón, mong được tha thứ!”"),t("br"),t("br"),n._v("Vội vàng ôm quyền."),t("br"),t("br"),n._v("Có thể thuần phục nửa bước Chí Tôn trải qua man thú, thực lực chỉ sợ muốn đạt tới Chí Tôn cảnh giới, mặc dù hắn là Tông sư đỉnh phong, cũng không dám tự đại."),t("br"),t("br"),n._v("“Tiền bối?”"),t("br"),t("br"),n._v("Vốn là xem bọn hắn đánh xong, Trương Huyền liền muốn rời đi, không nghĩ tới bị phát hiện, trong lòng hơi động, một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("“Đúng a, ta không phải muốn tìm người cảm kích sao? Gia hỏa này đã đem ta nhận thành tiền bối, vừa dễ dàng lắc lư một cái, có lẽ liền có thu hoạch!”"),t("br"),t("br"),n._v("Đang rầu đi nơi nào tìm người, gia hỏa này liền xuất hiện, vừa dễ dàng thử một chút."),t("br"),t("br"),n._v("“Bất quá, ta hiện tại bộ dáng, quá trẻ tuổi, bị hắn phát hiện, khẳng định sẽ hoài nghi... Vẫn là Dương sư dáng vẻ đi!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, ngụy trang năng lực vận chuyển, Trương Huyền bắp thịt toàn thân xương cốt phát thành biến hóa, lần nữa biến thành “Dương Huyền” bộ dáng."),t("br"),t("br"),n._v("Dù sao hắn bây giờ tại Khiếu Thiên thú trên lưng, lại là đêm tối, coi như phát giác có người, cũng thấy không rõ dung mạo."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Mệnh lệnh một tiếng, Khiếu Thiên thú thân thể nhoáng một cái, chậm rãi rơi xuống."),t("br"),t("br"),n._v("Đến tới mặt đất, thấy rõ vị tông sư này đỉnh phong, cũng là cái trung niên người, một thân quần áo bó, lộ ra tinh khí mười phần."),t("br"),t("br"),n._v("Có trước đó Dương sư ngụy trang kinh lịch, lúc này dễ như trở bàn tay, đứng tại Khiếu Thiên thú trên lưng, hai tay chắp sau lưng, thần sắc không vui không buồn, không hề bận tâm: “Không sao, ta chỉ là đi ngang qua nơi đây, gặp ngươi cùng đầu kia Xích Thân hổ chiến đấu, ngừng chân nhìn một chút, ngươi tới chiến đấu, thế nhưng là muốn đem nó thu phục?”"),t("br"),t("br"),n._v("Thu phục man thú có rất nhiều loại phương pháp, hợp ý chỉ là nó một, như loại này chiến đấu, cũng là một loại."),t("br"),t("br"),n._v("Vừa rồi một người một thú chiến đấu mặc dù hung ác, thật muốn liều mạng, khẳng định đã sớm trên người đổ máu, mặc cho đối phương trực tiếp rời đi, chỉ sợ là đánh muốn thu phục tâm tư."),t("br"),t("br"),n._v("Hơn nữa nhìn bọn hắn chiến đấu, lẫn nhau đều có hiểu biết, loại này chiến đấu, sợ có phải hay không một lần, hai lần."),t("br"),t("br"),n._v("“Tiền bối minh giám, vãn bối hoàn toàn chính xác có ý thu phục nó tâm tư.”"),t("br"),t("br"),n._v("Trung niên nhân ôm quyền."),t("br"),t("br"),n._v("Vừa rồi hắn cố ý dò xét, phát hiện đối phương trầm tĩnh như nước, mảy may không cảm thấy được tu vi, làm đến điểm này, ngoại trừ chân khí so với hắn tinh thuần bên ngoài, chỉ có một cái khả năng... Liền là tu vi vượt xa hắn!"),t("br"),t("br"),n._v("Có một đầu nửa bước Tông sư Khiếu Thiên thú làm thú vật sủng, tu vi hắn lại nhìn không thấu, thực lực chân chính, xem ra trước mắt vị này, thật sự là một vị Chí Tôn cường giả!"),t("br"),t("br"),n._v("Muốn đến nơi này, sắc mặt càng thêm cẩn thận, nhìn về phía Trương Huyền, thái độ cũng càng thêm cung kính."),t("br"),t("br"),n._v("“Xích Thân hổ, là sơn lâm Vương Giả, tính cách cao ngạo, muốn muốn thuần phục sao mà khó khăn, bằng vào mấy cuộc chiến đấu, liền muốn làm đến, không có khả năng!” Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Thân là nhị tinh tuần thú sư, dung nham thú loại kia hiếm thấy man thú không biết, Xích Thân hổ loại này, vẫn là biết biết không ít."),t("br"),t("br"),n._v("Xích Thân hổ, núi rừng bên trong Vương Giả, trưởng thành có được Tông sư đỉnh phong sức chiến đấu, đầu này chỉ là hậu kỳ, nói rõ cùng trưởng thành còn cách một đoạn."),t("br"),t("br"),n._v("Loại này trời sinh liền cường đại man thú, cơ hồ rất ít hàng phục tại người, đánh mấy trận liền muốn phục tùng, khả năng rất thấp."),t("br"),t("br"),n._v("“Ta cũng biết, chỉ là... Có chút lòng tham!”"),t("br"),t("br"),n._v("Trung niên nhân cười khổ."),t("br"),t("br"),n._v("Hắn cũng biết cái đồ chơi này muốn muốn thuần phục, thực sự quá khó, nhưng nghĩ đến có thể thuần phục, đem đối với hắn mang đến bao lớn giúp ích, trong lòng liền khó mà ngăn chặn khát vọng."),t("br"),t("br"),n._v("“Ta có thể truyền thụ cho ngươi thuần phục phương pháp!” Trương Huyền nói."),t("br"),t("br"),n._v("“Tiền bối có thể truyền thụ?”"),t("br"),t("br"),n._v("Đang rầu không biết như thế nào mới có thể thuần phục, nghe nói như thế, trung niên nhân con ngươi co rụt lại, kích động sắc mặt có chút phiếm hồng: “Không biết tiền bối, có gì cần tại hạ hỗ trợ? Chỉ cần có thể làm đến, không chối từ!”"),t("br"),t("br"),n._v("Biết trên đời không có uổng phí được cơm trưa, mặc dù cao hứng, lại cũng không có mất đi phân tấc."),t("br"),t("br"),n._v("“Ta không có chuyện cần ngươi hỗ trợ, hơn nữa ngươi loại thực lực này, cũng khinh thường ta lợi dụng. Bất quá... Phương pháp không được truyền qua tai, đã gặp được, cho dù có duyên, ta có thể truyền thụ phương pháp, nhưng ngươi muốn bái ta làm thầy!”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Bái sư?” Vốn cho rằng đối phương sẽ có yêu cầu gì, nghe nói như thế, trung niên nhân sững sờ tại nguyên chỗ, một mặt mộng bức."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-303"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-305"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);