(window.webpackJsonp=window.webpackJsonp||[]).push([[1527],{1883:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-296-hoc-sinh-khuong-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-296-hoc-sinh-khuong-thu"}},[n._v("#")]),n._v(" Chương 296: Học sinh Khương Thư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 296: Học sinh Khương Thư"),t("br"),t("br"),n._v("Gặp qua cao nhân một lời không hợp xuất thủ đả thương người; Một lời không hợp quay người rời đi, không để ý tới thế tục."),t("br"),t("br"),n._v("Một lời không hợp để cho người ta bái sư..."),t("br"),t("br"),n._v("Trung niên nhân nháy con mắt."),t("br"),t("br"),n._v("Có thể tu luyện tới Tông sư đỉnh phong, thiên phú của hắn tự nhiên không yếu, thế nhưng tự biết, khoảng cách thiên tài chân chính còn có một đoạn chênh lệch."),t("br"),t("br"),n._v("Không phải thiên tài, đối phương lại mới mở miệng thu vì đệ tử..."),t("br"),t("br"),n._v("Tình huống như thế nào?"),t("br"),t("br"),n._v("“Không sai! Ngươi thế nhưng là nghi hoặc, ta vì sao mới mở miệng liền muốn ngươi bái sư?” Trương Huyền khoát tay áo, ánh mắt mang theo thâm thúy cùng tỉnh táo: “Ngươi thiên phú không phải tuyệt hảo, thực lực cũng không tính là gì, ta là coi trọng ngươi điểm nào nhất?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Bị nói toạc tâm tư, trung niên nhân sắc mặt mang theo xấu hổ."),t("br"),t("br"),n._v("“Ngươi vừa rồi cùng Xích Thân hổ chiến đấu, 【 Thương Lãng thân pháp ] phối hợp 【 Hoằng Nghiên quyền ], mặc dù có chút tinh diệu, lại không cách nào phát huy toàn bộ uy lực, nếu như ta không nhìn lầm, ngươi bộ này Hoằng Nghiên quyền, chỉ học được tàn thiên đi!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Tiền bối, tiền bối... Như thế nào biết được?” Trung niên nhân thần sắc biến đổi, tràn đầy hãi nhiên."),t("br"),t("br"),n._v("Thương Lãng thân pháp là Thiên Vũ vương quốc tương đối nổi danh thân pháp, có thể nhìn ra chẳng có gì lạ, nhưng Hoằng Nghiên quyền, là hắn độc môn tuyệt chiêu, là lúc trước một lần lịch lúc luyện, tại một cái thần bí sơn cốc phát hiện Chí Tôn cường giả lưu lại di tích, từ đó học được."),t("br"),t("br"),n._v("Quyền pháp dùng người sáng tạo mệnh danh, là một vị gọi là Hoằng Nghiên tiền bối sáng tạo, khí thế cương mãnh, uy lực mười phần, có thể thi triển ra đối cứng man thú lực lượng cuồng mãnh."),t("br"),t("br"),n._v("Mặc dù vị cường giả này trong di tích lưu lại chỉ là tàn thiên, tu luyện nhiều năm như vậy, cũng đã thông hiểu đạo lí, một mực xem như bảo mệnh át chủ bài, rất ít người trước sử dụng. Vừa rồi cùng Xích Thân hổ thời điểm chiến đấu, có tôi luyện tâm tư, phát huy ra, nằm mơ đều không nghĩ tới, bị vị tiền bối này một chút nhận ra."),t("br"),t("br"),n._v("Trương Huyền không có trả lời câu hỏi của hắn, trên mặt bình tĩnh không lay động: “Hoằng Nghiên quyền đại khai đại hợp, lực lượng cương mãnh, nếu như đơn tu cái môn này, liền xem như tàn thiên, cũng không ảnh hưởng toàn cục, sẽ không cho thân thể mang đến gánh vác, ngươi lại nhất định phải kiêm tu 【 Tam Thốn chưởng ]. Lực lượng phát huy tại ba tấc trong lúc đó, là gấp rút đánh thẳng tới thốn kình. Thốn kình cùng đại khai đại hợp quyền pháp, chủ ý bên trên xung đột lẫn nhau. Nếu như ta không nhìn lầm, thân thể ngươi hẳn là có chút bệnh gì, không cách nào thanh trừ đi!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Trung niên nhân mặt mũi tràn đầy hãi nhiên, thân thể run lên, vội vàng ôm quyền, thái độ càng thêm cung kính: “Tiền bối... Nói một điểm không sai!”"),t("br"),t("br"),n._v("Hắn vừa mới bắt đầu tu luyện võ kỹ, liền là Tam Thốn quyền."),t("br"),t("br"),n._v("Về sau đạt được Hoằng Nghiên quyền, mặc dù biết cả hai có thể sẽ xung đột lẫn nhau, vẫn như cũ không bỏ được lợi hại như vậy quyền pháp, gượng ép tu luyện, vốn cho rằng dựa vào thân phận của hắn, có thể giải quyết, ai ngờ tu luyện càng sâu, đối thân thể tổn thương cũng lại càng lớn."),t("br"),t("br"),n._v("Nhất là mấy năm gần đây, đã cảm thấy thể lực không nhiều bằng lúc trước, chính vì vậy, mới một lòng muốn muốn thuần phục đầu này tuổi không lớn lắm Xích Thân hổ, một khi bản thân xảy ra chuyện, còn có thể bảo vệ thân nhân chu toàn."),t("br"),t("br"),n._v("“Thân là danh sư, biết rõ quyền pháp xung đột lẫn nhau, nhưng như cũ học tập, tổn hại tự thân mà không biết, là vì ngu xuẩn.” Trương Huyền thanh âm bên trong mang theo lạnh lùng."),t("br"),t("br"),n._v("“Tiền bối... Biết ta là danh sư?” Trung niên nhân lần nữa nhìn qua, tràn đầy không thể tin được."),t("br"),t("br"),n._v("Vừa rồi chiến đấu, thi triển Hoằng Nghiên quyền cùng Tam Thốn chưởng, đối phương có thể nhìn ra, chẳng có gì lạ, nhưng hắn chưa mang huy chương, chưa mặc danh sư trường bào, đối phương như thế nào biết được?"),t("br"),t("br"),n._v("Tựa hồ vừa rồi hắn cũng không có biểu hiện ra giống danh sư a!"),t("br"),t("br"),n._v("“Cùng Xích Thân hổ chiến đấu, có thể lấy tiến về phía trước tránh đối phương lợi hại công kích, mặc dù có chiến đấu nhiều lần đã lẫn nhau quen thuộc chi nghi ngờ. Nhưng mỗi một chiêu lại có thể nhắm ngay đối phương thiếu hụt làm ra phản kích, điểm ấy, không phải danh sư, chắc hẳn cũng làm không được đi!” Trương Huyền nói."),t("br"),t("br"),n._v("Danh sư có được Minh Lý cảnh tâm cảnh, đó có thể thấy được võ kỹ bên trong thiếu hụt, sai lầm, từ đó tìm ra vấn đề, chỉ điểm thêm."),t("br"),t("br"),n._v("Chính vì vậy, cùng cấp bậc võ giả, danh sư tuyệt đối là cường đại nhất."),t("br"),t("br"),n._v("Vừa rồi vị này, công thủ có độ, mặc dù cùng cuồng bạo man thú đối chiến, không chút nào không khẩn trương, mỗi một chiêu đều có thâm ý, chỉ cần là người tu luyện, cơ hồ đều có thể nhìn ra vấn đề."),t("br"),t("br"),n._v("Huống chi vừa rồi hắn cùng Xích Thân hổ đối chiến, song song thi triển võ kỹ, trong thư viện sớm đã tạo thành tương quan nội dung."),t("br"),t("br"),n._v("“Tiền bối thật là tinh mắt...”"),t("br"),t("br"),n._v("Trung niên nhân chần chờ phút chốc, sửng sốt một chút, con mắt đột nhiên sáng lên: “Chẳng lẽ... Tiền bối cũng là một vị danh sư?”"),t("br"),t("br"),n._v("Nổi danh sư, hắn thực sự nghĩ không ra, người nào có thể một chút nhìn ra hắn tu luyện võ kỹ, thân thể tồn tại vấn đề, thậm chí nhìn ra nghề nghiệp của hắn."),t("br"),t("br"),n._v("Trương Huyền hai tay chắp sau lưng, gió núi quét tới, ống tay áo tung bay, tựa như Tiên Nhân: “Ta lâu dài du lịch bên ngoài, đã rất nhiều năm chưa từng nghe qua người khác xưng hô như vậy!”"),t("br"),t("br"),n._v("“Quả nhiên là danh sư...”"),t("br"),t("br"),n._v("Nghe được đối phương nói như vậy, trung niên nhân con mắt một cái sáng lên, tràn đầy kích động."),t("br"),t("br"),n._v("Đối phương ít nhất là cái cường giả chí tôn, lại có vượt xa quá nhãn lực của hắn, nói rõ... Ít nhất là một vị tam tinh danh sư!"),t("br"),t("br"),n._v("Là danh sư, cũng liền có thể lý giải vì sao vừa thấy mặt liền để hắn bái sư."),t("br"),t("br"),n._v("Danh sư, thân gánh vác truyền đạo học nghề trách nhiệm, gặp được chững chạc người, có thể vì đó giải đáp, chính vì vậy, mới làm cho cả đại lục, thậm chí nhân tộc, như thế phồn thịnh."),t("br"),t("br"),n._v("Mới khiến cho danh sư cái nghề nghiệp này, nhận ức vạn người kính ngưỡng."),t("br"),t("br"),n._v("Danh sư đẳng cấp sâm nghiêm, cấp bậc thấp nhìn thấy cấp bậc cao, bản liền có thể xưng hô lão sư, cái gọi là bái sư, không tồn tại vấn đề gì."),t("br"),t("br"),n._v("Huống chi, đối phương một chút nhìn ra trên người hắn vấn đề, còn có thể giúp đỡ thu phục Xích Thân hổ, thật muốn bái sư, tuyệt đối là hắn kiếm lợi lớn!"),t("br"),n._v("“Tiền bối như nguyện ý thu ta là học sinh, là vinh hạnh của ta!”"),t("br"),t("br"),n._v("Hơi đỏ mặt, cố nén kích động, vội nói."),t("br"),t("br"),n._v("“Không nghi ngờ rồi?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vãn bối không dám!”"),t("br"),t("br"),n._v("Trung niên nhân liền vội vàng gật đầu."),t("br"),t("br"),n._v("Tu vi của đối phương hắn nhìn không ra, tuần phục một đầu nửa bước Chí Tôn cảnh man thú, càng là nhãn lực kinh người, coi như bây giờ đối phương nói mình không phải danh sư, hắn cũng không tin."),t("br"),t("br"),n._v("“Vừa rồi tới chiến đấu Xích Thân hổ, cũng chưa trưởng thành, thực lực cũng không đạt đến cực hạn, nếu như ngươi có biện pháp có thể làm cho nó nhanh chóng tăng trưởng, thực lực đạt tới Tông sư đỉnh phong, thần phục với ngươi, liền không khó!”"),t("br"),t("br"),n._v("Thấy hắn tin tưởng bản thân danh sư thân phận, biết chuyện còn lại liền dễ làm, Trương Huyền không nói thêm lời, cho ra chỗ tốt."),t("br"),t("br"),n._v("“Cái này... Ta cũng biết, chỉ là ta thử qua, thực sự nghĩ không ra loại nào vật phẩm có thể làm cho hắn thực lực tăng trưởng...” Trung niên nhân chần chờ một chút."),t("br"),t("br"),n._v("Làm danh sư, bác học nhiều biết, đối thuần thú loại hình tri thức, cũng đều có đọc lướt qua, biết muốn muốn thuần phục đối phương, nhất định phải hợp ý, đáng tiếc... Hắn thí nghiệm qua tốt nhiều lần, cũng không biết thứ gì có thể làm cho đầu này Xích Thân hổ đột phá."),t("br"),t("br"),n._v("Dù sao, đối phương cũng không phải là lực lượng giam cầm, mà là niên kỷ không đến, ăn bất luận cái gì dược vật, cũng không có khả năng để tuổi của nó đột ngột sinh trưởng a! Coi như thật sự có, đó cũng là đốt cháy giai đoạn, cho Xích Thân hổ biết được, sẽ chỉ oán hận, muốn thần phục, lại không thể có thể."),t("br"),t("br"),n._v("“Trên người ngươi nhưng có đan dược?”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Có... Mấy cái đan dược chữa thương!” Không biết vị tiền bối này, vì sao hỏi như vậy, trung niên nhân chần chờ một chút, nói."),t("br"),t("br"),n._v("“Cho ta!” Trương Huyền đưa tay."),t("br"),t("br"),n._v("“Vâng!” Mặc dù không biết đối phương vì sao muốn đan dược, bất quá trung niên nhân lại không có quá nhiều do dự, cổ tay khẽ đảo, một cái bình ngọc xuất hiện tại lòng bàn tay, nhẹ nhàng bắn ra, chân khí đẩy đưa xuống, từ từ đi tới Trương Huyền trước mặt."),t("br"),t("br"),n._v("Chiêu này mặc dù đơn giản, lại cho thấy đối chân khí cực mạnh khống chế."),t("br"),t("br"),n._v("Trong lòng kinh ngạc, trên mặt lại không biểu hiện ra ngoài, Trương Huyền tiếp nhận bình ngọc, tiện tay mở ra, quả nhiên thấy bên trong thịnh phóng tầm mười mai bóng loáng mượt mà đan dược, thế mà đều đạt đến Nhị phẩm cấp bậc."),t("br"),t("br"),n._v("Nhị phẩm đan dược, mỗi một mai đều có giá trị không nhỏ, mười mấy mai chung vào một chỗ, mức chỉ sợ đã vượt qua ngàn vạn, tùy tiện mang ở trên người, trực tiếp ném qua đến, chỉ sợ cũng chỉ có danh sư mới có phách lực như thế."),t("br"),t("br"),n._v("Đem những đan dược này đổ vào lòng bàn tay, tinh thần khẽ động, tinh thuần Thiên Đạo chân khí, lập tức tràn vào trong đó, đem tầm mười viên thuốc đè ép thành một cái cự đại viên thuốc."),t("br"),t("br"),n._v("Muốn trợ giúp một cái chưa trưởng thành man thú đột phá tự thân hạn chế, trừ phi huyết mạch tiến dời, không có những phương pháp khác. Loại nào dược vật có thể làm đến, Trương Huyền cũng không rõ ràng, bất quá hắn biết... Hắn Thiên Đạo chân khí có thể!"),t("br"),t("br"),n._v("Đối phương chỉ có Tông sư đỉnh phong, mà hắn cách Tông Sư cảnh cũng không xa, cả hai không kém nhiều, Thiên Đạo chân khí hiệu quả có lẽ còn là rất lớn."),t("br"),t("br"),n._v("Bất quá, Thiên Đạo chân khí quá mức cao cấp, đối phương lại là danh sư, nhãn lực kinh người, muốn giúp đối phương tấn cấp, làm không cẩn thận liền sẽ phát hiện vấn đề, để ngụy trang nửa ngày, hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("Hơn nữa, dùng Thiên Đạo chân khí trực tiếp rót vào kinh mạch tấn cấp, nhất định phải làm cho đối phương không phản kháng mới được, luôn không khả năng chạy tới đem Xích Thân hổ đánh bất tỉnh đi."),t("br"),t("br"),n._v("Coi như nghĩ, cũng làm không được!"),t("br"),t("br"),n._v("Đem chân khí rót vào bên trong đan dược, một khi đan dược hòa tan, chân khí liền sẽ tự động tiến nhập thể nội, nương theo huyết dịch tuần hoàn, từ đó tịnh hóa huyết mạch, đồng dạng có thể đạt tới để man thú tấn cấp huyết mạch công hiệu."),t("br"),t("br"),n._v("Chỉ là như vậy làm, tương đối lãng phí đan dược, cấp bậc càng cao đan dược, dung nạp chân khí cũng càng nhiều, liền lần này, số ngàn vạn kim tệ dược vật chẳng khác nào lãng phí."),t("br"),t("br"),n._v("Đương nhiên, đối Trương Huyền tới nói, không quan trọng, dù sao là của người phúc ta."),t("br"),t("br"),n._v("Dùng mấy ngàn vạn đổi lấy thuần phục một đầu Tông sư đỉnh phong man thú... Gia hỏa này tuyệt đối là kiếm lời."),t("br"),t("br"),n._v("“Tốt, ta đã luyện chế lại một lần một cái của ngươi đan dược, chỉ cần đem thứ này đưa cho Xích Thân hổ phục dụng, liền có thể lập tức đột phá, như thế nào để nó thần phục với ngươi, vậy liền nhìn phía sau ngươi thủ đoạn!”"),t("br"),t("br"),n._v("Ngón tay búng một cái, dược hoàn thẳng tắp đối trung niên nhân bắn đi qua."),t("br"),t("br"),n._v("Hắn không có đối phương cử trọng nhược khinh thủ pháp, đành phải dùng loại này đần biện pháp."),t("br"),t("br"),n._v("“Vâng!” Tiện tay tiếp nhận đan dược, trung niên nhân mặt mũi tràn đầy mê mang, có chút không quá tin tưởng."),t("br"),t("br"),n._v("Những này chữa thương đan dược, hắn từ luyện đan sư công hội mua mua được, không có bất kỳ cái gì đặc thù, tại vị tiền bối này trong tay thả một hồi, nhiều nhất đem mười mấy viên thuốc bóp thành một viên, như vậy... Liền có thể để Xích Thân hổ tấn cấp?"),t("br"),t("br"),n._v("Làm sao đều cảm thấy có chút không đáng tin cậy đâu?"),t("br"),t("br"),n._v("“Ngươi bây giờ cầm tới cho nó phục dụng!” Trương Huyền cũng không giải thích."),t("br"),t("br"),n._v("“Vâng!” Trong lòng nghi hoặc, nghĩ đến đối phương có thể là tam tinh danh sư, Chí Tôn cảnh cường giả, không có khả năng lừa gạt mình, trung niên nhân gật đầu một cái, đem dược hoàn cất kỹ, thân thể nhảy lên hướng vừa rồi Xích Thân hổ đào tẩu phương hướng đuổi theo."),t("br"),t("br"),n._v("Trương Huyền cũng không đi qua, yên tĩnh đứng tại Khiếu Thiên thú trên lưng chờ."),t("br"),t("br"),n._v("Ước chừng qua một canh giờ, một trận kịch liệt vang lên ầm ầm, trung niên nhân cưỡi tại Xích Thân hổ trên lưng đi tới trước mặt."),t("br"),t("br"),n._v("Lần này lại không có do dự, nhảy xuống lưng hổ, lập tức quỳ rạp xuống đất: “Học sinh Khương Thư, bái kiến lão sư!”"),t("br"),t("br"),n._v("(Tiếp tục cầu công chúng hào Fan hâm mộ. “Wechat lục soát h Sty Sd S” phía trên sẽ tiếp tục công bố một chút quyển sách tương quan nội dung, cùng một chút phiên ngoại. Mặt khác, quyển sách gần nhất muốn nâng làm một lần Fan hâm mộ hoạt động, đưa tặng Thiên Đạo thư viện quán kí tên sách cùng Qidian tiền, quyển sách chủ yếu diễn viên quần chúng (không dễ dàng chết cái chủng loại kia). Cụ thể trong hoạt động cho sẽ tại công chúng hào bên trên tuyên bố, mọi người có thể sớm chú ý, tích cực tham gia a!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-304"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-306"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);