(window.webpackJsonp=window.webpackJsonp||[]).push([[1528],{1885:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-297-trieu-nha-bi-đua-gion"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-297-trieu-nha-bi-đua-gion"}},[n._v("#")]),n._v(" Chương 297: Triệu Nhã bị đùa giỡn")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Nhìn thấy Xích Thân hổ tại trung niên người Khương Thư bên cạnh bồi hồi, tu vi quả nhiên tấn cấp đến Tông sư đỉnh phong, biết đã thuần phục, Trương Huyền hài lòng gật đầu, nhàn nhạt nhìn qua, thanh âm không có chút nào gợn sóng."),i("br"),i("br"),n._v("“Vi sư Dương Huyền, chỉ là đi ngang qua Thiên Vũ vương thành, ở chỗ này đợi một thời gian ngắn, trong lúc này, ngươi ta có sư đồ chi danh. Rời đi về sau, ngươi vẫn là ngươi, ta vẫn là ta, bất quá bèo nước gặp nhau duyên phận mà thôi. Không thể mượn dùng tên của ta, bốn phía giả danh lừa bịp, nếu không, giết chết bất luận tội!”"),i("br"),i("br"),n._v("Thu người học sinh này mục đích, là vì hình thành kim sắc trang sách, tự nhiên không có khả năng một mực mang theo, sớm đem lời nói rõ ràng ra."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Khương Thư gật đầu, hơi lộ ra vẻ thất vọng."),i("br"),i("br"),n._v("Vừa rồi nghe đối phương muốn khác bái sư, mặc dù không cự tuyệt, trong lòng vẫn còn có chút kháng cự."),i("br"),i("br"),n._v("Chờ khác thật đem viên đan dược kia đưa cho Xích Thân hổ phục dụng, người sau quả nhiên đột phá, thế mới biết, có thể bái nhập loại người này môn hạ, là may mắn dường nào."),i("br"),i("br"),n._v("Phổ thông chữa thương đan dược, tiện tay luyện chế một phen, biến thành để man thú đột phá bảo vật, loại thủ đoạn này, coi như kiến thức rộng rãi, đều chưa từng nghe nói qua."),i("br"),i("br"),n._v("Đơn chiêu này, trước mắt vị này, chỉ sợ hoàn toàn không phải tam tinh danh sư đơn giản như vậy."),i("br"),i("br"),n._v("“Không biết lão sư ở tại nơi nào, học sinh cũng tốt lúc nào cũng bái kiến!”"),i("br"),i("br"),n._v("Biết có thể trở thành đối phương một đoạn thời gian học sinh, cũng là kiếm lời, đem thất vọng chi ý áp chế, Khương Thư ôm quyền."),i("br"),i("br"),n._v("đăng nhậｐ http://ngantruyen.com/ để đọc truyện"),i("br"),n._v("“Ta tạm thời còn không có xác thực chỗ đặt chân, có, sẽ phái người nói cho ngươi!” Trương Huyền khoát khoát tay: “Tốt, ngươi đi về trước đi!”"),i("br"),i("br"),n._v("Để hắn cảm kích, hình thành kim sắc trang sách, không thể nào là một lần mà thôi sự tình, hôm nay thu làm học sinh, đồng thời để hắn có nhất định tín nhiệm, đã coi như là rất tiến bộ nhiều."),i("br"),i("br"),n._v("Nếu như lại thêm nhanh bước chân, chỉ sợ làm không cẩn thận sẽ hoàn toàn ngược lại."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Biết cao nhân có cao nhân diễn xuất, khương húc không dám nhiều lời, lần nữa nhảy lên Xích Thân hổ, quay người rời đi."),i("br"),i("br"),n._v("“Hô!”"),i("br"),i("br"),n._v("Đối phương rời đi, Trương Huyền nhẹ nhàng thở ra."),i("br"),i("br"),n._v("Tại danh sư trước mặt ngụy trang cao nhân, vẫn là rất phiền phức."),i("br"),i("br"),n._v("Dù sao đối phương nhãn lực kinh người, hơi có sai lệch, liền vô cùng có khả năng xảy ra vấn đề."),i("br"),i("br"),n._v("“Đúng rồi, quên hỏi Thiên Vũ học viện vị trí!”"),i("br"),i("br"),n._v("Vốn nghĩ tuân hỏi một chút Thiên Vũ học viện vị trí, sau đó lại thu mấy cái học sinh, một khối bồi dưỡng, đa tuyến thao tác, kết quả quên, chỉ sợ cũng chỉ có thể trở về thành lại tìm người."),i("br"),i("br"),n._v("Lần nữa trở lại vương thành."),i("br"),i("br"),n._v("Lần này không có người theo dõi, cũng không có chuyện gì khác, hỏi tới liền đơn giản không ít."),i("br"),i("br"),n._v("Thiên Vũ học viện, là vương thành lớn nhất nhân tài đất tập trung, cơ hồ người người đều biết."),i("br"),i("br"),n._v("Rất nhanh liền bị khác hỏi thăm ra vị trí cụ thể."),i("br"),i("br"),n._v("Vậy mà tại Danh Sư đường phụ cận, tới gần thành thị dải đất trung tâm, bước đi tới, ít nhất phải một, hai canh giờ."),i("br"),i("br"),n._v("Nghe được xa như vậy, vốn còn muốn trong đêm tìm người lừa dối Trương Huyền ngừng lại."),i("br"),i("br"),n._v("Đi tới Thiên Vũ vương thành, trời liền đã tối, ăn cơm, khảo hạch thư hoạ sư thêm đọc sách, giày vò hơn một canh giờ, hiện tại lại xử lý kẻ đánh lén, thu Khương Thư vì học sinh, lại qua hơn một canh giờ, dựa theo kiếp trước thời gian, đã không sai biệt lắm hơn mười một giờ khuya."),i("br"),i("br"),n._v("Lúc này lại chạy tới, tới chỗ cũng kém không nhiều đều ngủ, hơn nửa đêm đi thu người... Làm không cẩn thận sẽ bị xem như từ chỗ nào cái phần mộ bò ra tới, lần nữa vùi vào đi."),i("br"),i("br"),n._v("“Thôi được rồi, ngày mai lại nói, tìm một chỗ nghỉ ngơi một đêm đi!”"),i("br"),i("br"),n._v("Liên tục đi đường, lại thêm khảo hạch, chiến đấu, trang bức, đã mệt mỏi mệt mỏi."),i("br"),i("br"),n._v("“Ừm? 【 Thiên Vũ lữ quán ]? Liền ở cái này!”"),i("br"),i("br"),n._v("Tại trên đường phố đi một hồi, nhìn quanh hai bên một vòng, một cái quán trọ xuất hiện ở trước mắt, nhấc chân đi vào."),i("br"),i("br"),n._v("Lữ quán rộng rãi sạch sẽ, tiến vào đại sảnh hỏi một cái, vẫn còn phòng trống, liền ở đi vào."),i("br"),i("br"),n._v("Khác vừa đi vào nhà ở ở giữa, vài bóng người liền nóng nảy đến đến đại sảnh."),i("br"),i("br"),n._v("“Triệu Nhã còn chưa có trở lại?”"),i("br"),i("br"),n._v("“Đúng vậy, Lưu sư!” Vương Dĩnh rụt rè đường."),i("br"),i("br"),n._v("“Ta không phải để các ngươi vô luận tìm tới tìm không thấy, đều một canh giờ trở về sao? Hiện tại cũng đã lâu như vậy, làm sao còn chưa tới?” Lưu sư mày nhăn lại."),i("br"),i("br"),n._v('"Chúng ta... Vừa ra khỏi cửa liền tách ra, vừa mới trở về ta không thấy được, cho là nàng đã trở về phòng, không nghĩ tới,'),i("br"),i("br"),n._v('Chờ nửa ngày còn không có xuất hiện..." Vương Dĩnh nói.'),i("br"),i("br"),n._v("“Như vậy, Lục Tầm, ngươi mang theo Trịnh Dương đi phía đông tìm kiếm, Tôn Cường ngươi cùng Lưu Dương, Viên Đào đi phía tây tìm xem, ta cùng Vương Dĩnh, Hoàng Ngữ đi mặt phía bắc, mặt phía nam là cửa thành, liền không cần đi!”"),i("br"),i("br"),n._v("Lưu sư bàn giao: “Bất kể như thế nào, cùng lần trước quy củ đồng dạng, trong vòng một canh giờ nhất định phải trở về!”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Đám người gật đầu, nhấc chân đi ra ngoài."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Bạch Thiềm y sư gần nhất xuân phong đắc ý."),i("br"),i("br"),n._v("Từ khi Hồng Liên sơn mạch sau khi trở về, tất cả y sư nhìn thấy đều cung kính hữu lễ, cực kỳ tôn kính."),i("br"),i("br"),n._v("Thậm chí đến y sư công hội hội trưởng, đều thái độ không đồng dạng."),i("br"),i("br"),n._v("Trước kia một mực chịu đủ lạnh nhạt, hiện tại nhảy lên thành vì tất cả mọi người tiêu điểm, lấy trước kia chút không phải cái mũi không phải mặt nhị tinh y sư, bây giờ tại trước mặt cũng không dám nói chuyện lớn tiếng..."),i("br"),i("br"),n._v("Để trong lòng của hắn càng ngày càng cao hứng, càng ngày càng hài lòng cuộc sống bây giờ."),i("br"),i("br"),n._v("“Không nghĩ tới bị người đập bất tỉnh trong ngõ hẻm, lại đạt được loại này chỗ tốt...”"),i("br"),i("br"),n._v("Hôm nay cùng hội trưởng xưng huynh gọi đệ uống một hồi rượu, đi trên đường đã có chút choáng đầu hoa mắt."),i("br"),i("br"),n._v("Nhân sinh của hắn điểm xuất phát, liền là tại Hồng Liên thành bị người đập ngất."),i("br"),i("br"),n._v("Sau khi tỉnh lại, vô số y sư thái độ đối với hắn liền thay đổi."),i("br"),i("br"),n._v("Ngẫm lại đều cảm thấy có phải hay không đang nằm mơ."),i("br"),n._v(" Đương nhiên, đi qua những ngày gần đây, cũng coi là triệt để minh bạch."),i("br"),i("br"),n._v("Ngày đó cùng Mạc Vũ công chúa cùng lúc xuất hiện thanh niên, đem hắn đập ngất về sau, mượn dùng tên tuổi của hắn cùng bộ dáng, cứu được Đại Dược Vương."),i("br"),i("br"),n._v("Những y sư này cố ý cùng hắn lấy lòng, tiếp cận, vừa đến, là muốn thông qua chính mình quan hệ, từ Đại Dược Vương chỗ nào giá thấp mua được tốt hơn thuốc; Thứ hai, cũng muốn biết cái gì là cái gọi là khế ước cổ, lại là như thế nào trị tốt."),i("br"),i("br"),n._v("Khác lúc ấy chỉ là nằm tại băng lãnh trong ngõ nhỏ, quần áo đều bị lột, chỗ nào nhận biết cái gì Đại Dược Vương? Tự nhiên cũng càng không biết cái gọi là khế ước cổ..."),i("br"),i("br"),n._v("Nhưng thật vất vả xoay người, bị nhiều người như vậy coi trọng, cứ như vậy phủ nhận, há không mất mặt xấu hổ, rất dễ dàng bị người đánh chết?"),i("br"),i("br"),n._v("Suy đi nghĩ lại, một mực nói láo đến bây giờ."),i("br"),i("br"),n._v("Một khi gặp được người khác hỏi thăm Đại Dược Vương sự tình, liền cắn chặt răng, khoảng chừng nói khác."),i("br"),i("br"),n._v("Thấy hắn không nói, chúng người biết khả năng dính dáng bí mật, liền không hỏi thêm nữa, địa vị của hắn ngược lại càng cao hơn."),i("br"),i("br"),n._v("Kết quả này, để khác tràn đầy hưng phấn, vừa gặp phải cái hẻm nhỏ liền chui vào trong, chờ mong lần nữa bị đánh ngất xỉu, kiếm lấy càng đại danh hơn âm thanh."),i("br"),i("br"),n._v("Hôm nay cáo từ hội trưởng, một mình hồi phủ, nhìn thấy một cái ngõ nhỏ, chính muốn đi vào, liền thấy một thiếu nữ, đối diện đi tới."),i("br"),i("br"),n._v("Nữ hài tuổi không lớn lắm, chỉ có mười sáu mười bảy tuổi bộ dáng, dung mạo lại dị thường mỹ lệ, vượt qua thấy qua tất cả mỹ nữ, thậm chí số liền nhau xưng Thiên Vũ vương quốc đệ nhất mỹ nữ chớ Vũ công chúa tới so sánh, đều kém một mảng lớn."),i("br"),i("br"),n._v("Khác háo sắc là nổi danh, nguyên nhân chính là như thế, lúc trước Mạc Vũ mới không chào đón, chứa lâu như vậy cao nhân, đột nhiên nhìn thấy như thế không có người, lại thêm cồn tác dụng, lập tức hai mắt tỏa ánh sáng."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Biết Trương lão sư tại thư họa sư công hội xuất hiện, Triệu Nhã bọn người cách mở quán trọ khắp nơi tìm kiếm."),i("br"),i("br"),n._v("Vì hiệu suất cao hơn, đám người chia ra làm việc."),i("br"),i("br"),n._v("Chung quanh tất cả đường đi, quán rượu cơ hồ đều tìm khắp cả, vậy mà không có nửa điểm cái bóng."),i("br"),i("br"),n._v("Vừa đi tìm cái này hẻm nhỏ, Triệu Nhã xoa xoa mang theo mệt mỏi con mắt, dự định quay về chỗ ở lại nói, liền thấy một cái mặt mũi tràn đầy hèn mọn lão giả, tiến lên đón."),i("br"),i("br"),n._v("“Rượu một mình không thú vị, vị cô nương này, nếu không theo giúp ta uống vài chén?” Bạch Thiềm y sư cản ở phía trước."),i("br"),i("br"),n._v("“Tránh ra!”"),i("br"),i("br"),n._v("Triệu Nhã đôi mi thanh tú nhăn lại."),i("br"),i("br"),n._v("Thuần Âm thể chất mở ra, dung mạo của nàng, khí chất có chất cải biến, loại này quấy rối, gặp thường đến, chỉ bất quá hầu như đều là niên kỷ tương đương, một cái đều có thể làm gia gia của nàng lão đầu xuất hiện, để sắc mặt nàng khó coi."),i("br"),i("br"),n._v("“Nếu như không cho đâu?”"),i("br"),i("br"),n._v("Bạch Thiềm y sư hừ một tiếng."),i("br"),i("br"),n._v("Hiện đang đến gần nửa đêm, trước đó náo nhiệt đường tắt, hầu như đều không có người, hơn nữa cho dù có người, nhìn thấy khác lộng lẫy quần áo, chỉ sợ không dám xuất đầu."),i("br"),i("br"),n._v("“Muốn chết!”"),i("br"),i("br"),n._v("Thấy gia hỏa này một mặt lưu manh dáng vẻ, Triệu Nhã đôi mi thanh tú nhăn lại, cổ tay khẽ đảo, trường kiếm xuất hiện tại lòng bàn tay."),i("br"),i("br"),n._v("Thuần Âm thể chất vận chuyển tới cực hạn, âm lãnh kiếm khí, thẳng tắp đối kẻ trước mắt này liền đâm tới."),i("br"),i("br"),n._v("“Không biết điều!”"),i("br"),i("br"),n._v("Thấy Triệu Nhã cũng dám đối với hắn dùng kiếm, Bạch Thiềm y sư hừ lạnh, vồ tới."),i("br"),i("br"),n._v("Đinh!"),i("br"),i("br"),n._v("Trường kiếm rơi vào đối phương trong lòng bàn tay."),i("br"),i("br"),n._v("Bạch Thiềm y sư tại Trương Huyền trước mặt là bị một quyền đập bất tỉnh hàng, nhưng là thực sự Thông Huyền cảnh cường giả đỉnh phong, căn bản không phải Triệu Nhã loại này chỉ mở ra hai vị huyệt đạo người, có thể chống lại."),i("br"),i("br"),n._v("“Nguy rồi!”"),i("br"),i("br"),n._v("Trường kiếm bị đoạt, Triệu Nhã trong lòng run lên, biết kẻ trước mắt này thực lực căn bản mình có thể chống lại, thân thể nhảy lên, thẳng tắp hướng quán trọ phương hướng vọt tới."),i("br"),i("br"),n._v("Nàng Thuần Âm thể chất mở ra, tốc độ mặc dù không kịp Vương Dĩnh, nhưng cũng cực nhanh, nơi này khoảng cách chỗ ở đã không xa, chỉ cần đến tới chỗ, Lưu sư ra mặt, liền cam đoan có thể đánh cái này hai hàng, không dám nói nhảm."),i("br"),i("br"),n._v("“Còn muốn trốn? Trốn được sao?”"),i("br"),i("br"),n._v("Thật vất vả gặp được mỹ nữ như thế, Bạch Thiềm cái nào có thể để cho chạy thoát, theo sát lấy đuổi theo."),i("br"),i("br"),n._v("“Triệu Nhã?”"),i("br"),i("br"),n._v("Một trận phi nước đại, Triệu Nhã đi tới chỗ ở đại sảnh, vừa vặn gặp dự định đi ra ngoài Lưu sư bọn người."),i("br"),i("br"),n._v("“Thế nào?”"),i("br"),i("br"),n._v("“Đã xảy ra chuyện gì?”"),i("br"),i("br"),n._v("Gặp nàng vội vàng xông tới, đám người tràn đầy nghi hoặc."),i("br"),i("br"),n._v("“Lão nhân này đùa bỡn ta...”"),i("br"),i("br"),n._v("Triệu Nhã hướng (về) sau một chỉ."),i("br"),i("br"),n._v("Bạch Thiềm y sư lúc này cũng đi vào đại sảnh."),i("br"),i("br"),n._v("“Cái gì?”"),i("br"),i("br"),n._v("Nghe được nàng bị đùa giỡn, Trịnh Dương đám người nhất thời giận dữ, nhướng mày, liền muốn vọt qua tới."),i("br"),i("br"),n._v("“Chậm đã!”"),i("br"),i("br"),n._v("Lưu sư một chút nhìn ra thực lực của đối phương, đem mấy người ngăn lại."),i("br"),i("br"),n._v("Bạch Thiềm y sư nhìn thấy lại có người tại, mắt sáng lên: “Khó trách chạy nhanh như vậy, nguyên lai có người tại đây! Đã như vậy vậy thì dễ làm rồi!”"),i("br"),i("br"),n._v("Làm kẻ già đời, cảnh tượng như thế này đã sớm thấy qua nhiều lần, không có chút nào sợ hãi."),i("br"),i("br"),n._v("“Không biết vị bằng hữu này, đuổi học sinh của ta không biết có chuyện gì?”"),i("br"),i("br"),n._v("Biết đây là Thiên Vũ vương thành, ngư long hỗn tạp, Lưu sư mặc dù tức giận, nhưng cũng cưỡng ép ngăn chặn, tiến về phía trước một bước, ôm quyền nói."),i("br"),i("br"),n._v("“Tiểu cô nương này, trộm ta đồ vật, cho nên, ta đuổi tới đòi hỏi! Đã các ngươi nhận biết, vậy thì dễ làm rồi, hoặc là đem đồ vật giao ra, hoặc là, đem nàng cho ta xử lý!”"),i("br"),i("br"),n._v("Bạch Thiềm y sư khoát tay chặn lại, thản nhiên nói."),i("br"),i("br"),n._v("“Trộm ngươi đồ vật? Ngươi... Nói bậy!”"),i("br"),i("br"),n._v("Không có nghĩ tới tên này đùa giỡn nàng, thế mà còn trả đũa, nói nàng trộm đồ, Triệu Nhã chọc tức lập tức khuôn mặt đỏ lên, kém chút bạo tạc."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-305"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-307"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);