(window.webpackJsonp=window.webpackJsonp||[]).push([[1529],{1882:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-298-vu-ham"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-298-vu-ham"}},[n._v("#")]),n._v(" Chương 298: Vu hãm")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Không biết các hạ mất đi thứ gì?”"),i("br"),i("br"),n._v("Lưu sư như thế nào nhìn không ra đối phương là cố ý nói như vậy, lông mày nhíu một cái."),i("br"),i("br"),n._v("“Tại hạ mất đi một gốc mười phần dược liệu quý giá!”"),i("br"),i("br"),n._v("Bạch Thiềm y sư sớm đã thành thói quen loại này sáo lộ, thản nhiên nói: “Ngay lập tức đem dược liệu giao ra, nếu không, ta sẽ lập tức báo cáo, đem bọn ngươi nhốt vào đại lao!”"),i("br"),i("br"),n._v("“Lưu sư, ta không có bắt hắn bất kỳ vật gì, là hắn ngậm máu phun người!”"),i("br"),i("br"),n._v("Triệu Nhã sốt ruột giải thích."),i("br"),i("br"),n._v("“Đúng vậy a, Lưu sư, Triệu Nhã phụ thân liền là Bạch Ngọc thành thành chủ, muốn cái gì có cái đó, làm sao có thể trộm đồ!”"),i("br"),i("br"),n._v("“Trương lão sư bản thân liền là luyện đan sư, dược liệu gì mua không được, còn cần lấy trộm?”"),i("br"),i("br"),n._v("Trịnh Dương mấy người cũng tiến về phía trước một bước."),i("br"),i("br"),n._v("Cùng một chỗ ở chung lâu như vậy, tự nhiên tin tưởng Triệu Nhã làm người."),i("br"),i("br"),n._v("Làm sao có thể trộm cái này bẩn thỉu lão đầu đồ vật?"),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Lưu sư khoát tay đánh gãy lời của mọi người, ngẩng đầu nhìn tới: “Tại hạ Bắc Vũ vương quốc nhất tinh danh sư, Lưu Lăng! Mang mấy cái học sinh lại tới đây, khả năng trong lúc đó có chút lầm lại...”"),i("br"),i("br"),n._v("Nói xong cổ tay khẽ đảo, một viên đại biểu thân phận lệnh bài xuất hiện tại lòng bàn tay."),i("br"),i("br"),n._v("“Nhất tinh danh sư?”"),i("br"),i("br"),n._v("Bạch Thiềm y sư sửng sốt một chút."),i("br"),i("br"),n._v("Danh sư tại Thiên Vũ vương thành có cực cao địa vị, cùng cấp bậc dưới, hoàn toàn không phải y sư có thể so sánh được."),i("br"),i("br"),n._v("Bất quá, Thiên Vũ vương thành, chân chính chức cao chính là nhị tinh danh sư, nhất tinh còn kém một chút."),i("br"),i("br"),n._v("Lại nói, cường long không ép địa đầu xà, danh sư thì sao? Chỉ cần không phải Thiên Vũ vương thành, chí ít có một vạn loại phương pháp, để hắn nói không ra lời."),i("br"),i("br"),n._v("Nghĩ tới những thứ này, còn muốn lên những ngày gần đây, một chút nhất tinh y sư nhìn thấy hắn đều đồng dạng cung kính hữu lễ, khí thế lần nữa đi lên, sầm mặt lại: “Hiểu lầm? Lão phu tại y sư công hội, liền ngay cả hội trưởng đều kính sợ ba phần, ngươi nói ta vu hãm của ngươi tiểu học sinh tiểu học?”"),i("br"),i("br"),n._v("“Tại hạ không phải ý tứ này...”"),i("br"),i("br"),n._v("Thấy cho thấy thân phận, đối phương còn nói như vậy, Lưu sư vội vàng giải thích."),i("br"),i("br"),n._v("Nhất tinh danh sư tại Bắc Vũ vương quốc, Thiên Huyền vương quốc, địa vị tôn sùng, ai cũng không dám mạo phạm, nhưng đến Thiên Vũ loại này nhất đẳng vương quốc, tầm quan trọng liền lập tức giảm bớt."),i("br"),i("br"),n._v("Một chút lợi hại gia tộc thế lực, thậm chí đều sẽ cung phụng một chút."),i("br"),i("br"),n._v("Chính vì vậy, cái thân phận này mặc dù để không ít người kiêng kị, chân chính thế lực lớn, có bối cảnh người, cũng sẽ không quá mức quan tâm, trừ phi... Đạt tới nhị tinh cấp bậc!"),i("br"),i("br"),n._v("Phía sau có danh sư đường tại, hắn mặc dù không quá e ngại, cũng không ai dám đem hắn thế nào, Triệu Nhã bọn người liền không đồng dạng."),i("br"),i("br"),n._v("Nơi này chưa quen cuộc sống nơi đây, một khi không hảo hảo giải quyết, cãi cọ, phiền phức không ít."),i("br"),i("br"),n._v("Thậm chí thật muốn chơi xấu, còn sẽ ảnh hưởng bản thân danh sư danh dự."),i("br"),i("br"),n._v("“Không phải ý tứ này, vậy ngươi có ý tứ gì? Ngươi người học sinh này trộm ta đồ vật, ta đều đuổi tới đây, chẳng lẽ lại còn không thể đoạt về?”"),i("br"),i("br"),n._v("Bạch Thiềm y sư đại thủ chặn lại, khí thế như hồng."),i("br"),i("br"),n._v("Hắn biết đối phương kiêng kị cái gì, càng thêm không kiêng nể gì cả."),i("br"),i("br"),n._v("“Mấy vị gia, đây là thế nào?”"),i("br"),i("br"),n._v("Song phương cãi lộn thanh âm rất lớn, nhưng vào lúc này, Thiên Vũ lữ quán ông chủ vội vã chạy tới."),i("br"),i("br"),n._v("“Ngươi mấy vị này khách hàng, có người trộm ta đồ vật, ngươi nói thế nào?” Bạch Thiềm y sư tiện tay kéo qua một cái ghế, ngồi xuống."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Ông chủ mặt mũi tràn đầy sốt ruột."),i("br"),i("br"),n._v("Vừa rồi nghe tiểu nhị nói, một bên là nhất tinh danh sư, một bên là y sư công hội chạm tay có thể bỏng như mặt trời ban trưa Bạch Thiềm y sư, ai đều đắc tội không nổi."),i("br"),i("br"),n._v("“Ta cũng không làm khó ngươi, đã ngươi không giải quyết được, liền đi xin đội tuần tra đi, chắc hẳn bên ngoài liền có!”"),i("br"),i("br"),n._v("Mí mắt vừa nhấc, Bạch Thiềm y sư không có sợ hãi, Bắc Vũ vương quốc loại địa phương nhỏ này tới danh sư mà thôi... Căn bản không đủ gây sợ."),i("br"),i("br"),n._v("“Vâng, vâng!”"),i("br"),i("br"),n._v("Ông chủ nhẹ nhàng thở ra, vội vàng ra hiệu, tiểu nhị nghe được phân phó, vội vàng chạy ra ngoài."),i("br"),i("br"),n._v("Thiên Vũ vương thành như thế lớn, mỗi ngày đều có các loại tranh đấu, cần phải kịp thời xử lý, mới có thể giữ gìn trị an. Đội tuần tra, chính là vương thất điều động chuyên môn phụ trách giữ gìn trị an đội ngũ."),i("br"),i("br"),n._v("Một khi gặp được phiền phức, liền có thể thông tri, để hắn tới."),i("br"),i("br"),n._v("“Cái này... Không biết các hạ dược liệu gì mất đi, muộn như vậy kinh động đội tuần tra cũng không tiện lắm, không bằng tự mình giải quyết!”"),i("br"),i("br"),n._v("Nghe muốn tìm đội tuần tra, Lưu sư đành phải ôm quyền nói."),i("br"),i("br"),n._v("Tại người khác trên địa bàn, liền điểm ấy không tốt, đội tuần tra khẳng định nghe hắn, nếu quả thật kết luận Triệu Nhã trộm đối phương đồ vật, các loại hỏi thăm, thẩm vấn, phiền phức sẽ rất nhiều."),i("br"),i("br"),n._v("Nếu là một mình hắn, tự nhiên không sợ, không có có danh sư đường mệnh lệnh, ai cũng không dám bắt người."),i("br"),i("br"),n._v("Nhưng Triệu Nhã đợi người tới tự Thiên Huyền vương quốc loại kia địa phương nhỏ, lại không có cái gì lợi hại thân phận, một khi điều tra, không có một hai tháng khó mà kết thúc, lại nói, tiến nhập đại lao, còn không phải đối phương muốn nói cái gì nói cái đó?"),i("br"),i("br"),n._v("Hắn thụ “Đại sư huynh” ủy thác mang học sinh tới, vạn nhất đang bị giam, nhìn thấy “Đại sư huynh” như thế nào bàn giao?"),i("br"),i("br"),n._v("Cho nên, dự định dàn xếp ổn thỏa, miễn cho phiền phức."),i("br"),i("br"),n._v("“Mất đi dược liệu gì? Đã muốn biết, ta liền nói cho ngươi, ta mất đi một gốc ngàn năm 【 Thiết Thanh hoa ], bán ra lời nói... Giá trị chí ít một ngàn vạn kim tệ, tự mình giải quyết cũng tốt, hoặc là, bồi một ngàn vạn kim tệ, hoặc là, đem cô bé này giao cho ta xử lý.”"),i("br"),i("br"),n._v("Bạch Thiềm y sư dương dương đắc ý nhìn qua."),i("br"),i("br"),n._v("Tiểu tử, muốn giải quyết riêng? Nhìn ta chơi không chết các ngươi!"),i("br"),i("br"),n._v("“Ngươi... Nói bậy!”"),i("br"),i("br"),n._v("“Ngậm máu phun người!”"),i("br"),i("br"),n._v("Triệu Nhã không nghĩ tới đối phương vô sỉ như vậy, tức giận đến gương mặt xinh đẹp thấu đỏ, hô hấp dồn dập."),i("br"),i("br"),n._v("Trịnh Dương mấy người cũng nguyên một đám nắm đấm xiết chặt."),i("br"),i("br"),n._v("“Ngàn năm Thiết Thanh hoa?”"),i("br"),n._v("Lưu sư sắc mặt cũng có chút khó coi."),i("br"),i("br"),n._v("Làm danh sư, mặc dù không tinh thông y dược, Thiết Thanh hoa cũng đã được nghe nói, là một loại trân quý dược liệu, nhất là đạt tới ngàn năm, càng thêm trân quý, thật yếu xuất thụ, một ngàn vạn kim tệ, chỉ sợ đều chưa hẳn có thể mua được."),i("br"),i("br"),n._v("Một ngàn vạn kim tệ, hắn tích lũy nhiều năm như vậy mặc dù có, nhưng một cái liền bị người lừa bịp đi nhiều như vậy, coi như hắn, đều cảm giác được không thể chịu đựng được."),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Bạch Thiềm y sư ngón tay gõ cái bàn, mí mắt vừa nhấc: “Ta vừa mới mua được bụi dược liệu này, đụng phải ngươi người học sinh này, đã không thấy tăm hơi, không phải nàng trộm ai trộm? Hoặc là bồi thường tiền, không muốn bồi, liền để ta đem nàng bắt về hỏi thăm. Nếu không, cùng lắm thì ta liền đem chuyện này tuyên dương ra ngoài, nói ngươi dung túng học sinh ăn cắp, nhìn Danh Sư đường, có thể hay không xử lý.”"),i("br"),i("br"),n._v("Vừa nói vừa từ trên mặt bàn rót một chén nước, một mặt đắc ý nhìn qua."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Lưu sư sắc mặt tái xanh."),i("br"),i("br"),n._v("Đối phương xem ra nắm chắc danh sư nhược điểm."),i("br"),i("br"),n._v("Phàm là danh sư, đều yêu quý phe cánh, thật muốn bị hắn vu hãm dung túng học sinh ăn cắp, về sau còn như thế nào khảo hạch nhị tinh? Như thế nào gặp người?"),i("br"),i("br"),n._v("Rầm rầm!"),i("br"),i("br"),n._v("Gian phòng ngay tại ngột ngạt, một đám người đi tới, chính là tiểu nhị đi mời đội tuần tra."),i("br"),i("br"),n._v("Những người này từng cái đều có Ích Huyệt cảnh thực lực, phía trước nhất vị kia càng là Thông Huyền cảnh cường giả."),i("br"),i("br"),n._v("Toàn thân mũ sắt, cầm trong tay trường kiếm, cho người ta một loại băng lãnh thiết huyết hương vị."),i("br"),i("br"),n._v("“Gặp qua Bạch Thiềm y sư!”"),i("br"),i("br"),n._v("Đội tuần tra mọi người đi tới đại sảnh, đi đầu một cái nhìn thoáng qua, nhận ra Bạch Thiềm, tiến lên ôm quyền."),i("br"),i("br"),n._v("Muốn nói gần nhất toàn bộ vương thành danh tiếng thịnh nhất chính là ai, tự nhiên là Tam công chúa chớ Vũ điện hạ, về phần tiếp theo, chỉ sợ cũng phải kể tới trước mắt ngồi vị này."),i("br"),i("br"),n._v("Nghe nói giải quyết ngay cả tam tinh y sư đều nhìn không tốt chứng bệnh, thực lực chân chính, chỉ sợ đã vượt qua tam tinh, đạt tới cảnh giới cao hơn, chỉ là tu vi không đủ, mới không có đi khảo hạch."),i("br"),i("br"),n._v("“A, nguyên lai là Diêu đội trưởng!”"),i("br"),i("br"),n._v("Mí mắt vừa nhấc, Bạch Thiềm cũng nhận ra đối phương, đại thủ chặn lại: “Nếu là ngươi vậy thì dễ làm rồi, nữ hài kia trộm Đại Dược Vương vừa mới đưa cho ta dược liệu, ngàn năm Thiết Thanh hoa, ngươi xem chuyện này xử lý như thế nào đi!”"),i("br"),i("br"),n._v("“Đại Dược Vương?”"),i("br"),i("br"),n._v("Diêu đội trưởng sắc mặt biến hóa."),i("br"),i("br"),n._v("Đại Dược Vương danh khí cực lớn, đại biểu Độc điện, chính vì vậy, tại Hồng Liên thành hầu như đều thành chúa tể một phương, Thiên Vũ vương thất đều chưa hề xuất binh qua."),i("br"),i("br"),n._v("Đại Dược Vương tự mình tiễn hắn dược liệu, xem ra nghe đồn là thật, chuyện này nhất định phải cẩn thận xử lý."),i("br"),i("br"),n._v("“Ngay cả Bạch Thiềm y sư dược liệu cũng dám trộm? Thật to gan!”"),i("br"),i("br"),n._v("Hừ lạnh một tiếng, Diêu đội trưởng tiến về phía trước một bước, đi tới Lưu sư trước mặt: “Vị danh sư này, ta kính trọng thân phận của ngươi, không sẽ động thủ, mau đưa người học sinh này giao ra đi! Để cho ta mang về thẩm vấn, yên tâm đi, đội tuần tra công bằng công chính, nếu thật là trong sạch, tự nhiên sẽ cho cho các ngươi công chính xử quyết; Thật muốn trộm đồ vật, coi như ngươi là danh sư, chúng ta cũng không để ý, nháo đến Danh Sư đường!”"),i("br"),i("br"),n._v("“Cái này... Còn xin đừng có gấp, chúng ta nguyện ý giải quyết riêng!”"),i("br"),i("br"),n._v("Thấy song phương nhận biết, biết Triệu Nhã một khi bị bắt, khẳng định sẽ bị ngồi vững trộm đồ tội danh, Lưu sư hít sâu một hơi, nói."),i("br"),i("br"),n._v("“Lưu sư, không được!”"),i("br"),i("br"),n._v("“Triệu Nhã không có trộm qua đồ vật, vì sao muốn giải quyết riêng?”"),i("br"),i("br"),n._v("“Không tệ, Lưu sư, một khi giải quyết riêng, chẳng khác nào chấp nhận Triệu Nhã cầm bọn hắn đồ vật, đối lão sư danh dự của ngươi, có tổn thất cực kỳ lớn hại...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được Lưu sư muốn giải quyết riêng, Trịnh Dương, Lục Tầm bọn người sốt ruột la lên."),i("br"),i("br"),n._v("Một khi giải quyết riêng, liền cho thấy chột dạ, thật trộm đồ vật, Lưu sư danh dự đem sẽ cực kì bị hao tổn, truyền đến Danh Sư đường, thậm chí đều sẽ tiếp nhận điều tra."),i("br"),i("br"),n._v("Như thế thật sự trăm miệng khó cãi."),i("br"),i("br"),n._v("“Tốt! Tất cả câm miệng!”"),i("br"),i("br"),n._v("Lưu sư khoát khoát tay."),i("br"),i("br"),n._v("Không giải quyết riêng, Triệu Nhã tất nhiên muốn bị bắt về thẩm vấn, mang cái học sinh đều mang mất đi, để hắn có mặt mũi nào đi gặp “Đại sư huynh” ?"),i("br"),i("br"),n._v("“Giải quyết riêng?”"),i("br"),i("br"),n._v("Không nghĩ tới vị danh sư này vì học sinh, ngay cả một ngàn vạn kim tệ đều bỏ được xuất ra, Bạch Thiềm y sư tắc lưỡi, lập tức cười lạnh một tiếng: “Giải quyết riêng cũng được, ta vừa rồi suy nghĩ một chút, cái này gốc ngàn năm Thiết Thanh thảo, vô cùng trân quý, một ngàn vạn căn bản mua không được, đại khái cần năm ngàn vạn kim tệ. Ngươi đem kim tệ lấy tới, ta cái này liền rời đi, không bỏ ra nổi đến, thật không tiện, người ta hôm nay mang đi định!”"),i("br"),i("br"),n._v("“Năm ngàn vạn? Ngươi... Dược liệu gì có thể như thế đáng tiền?”"),i("br"),i("br"),n._v("“Vừa rồi mới nói một ngàn vạn, lập tức tăng giá, đáng giận...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe gia hỏa này một cái đem dược liệu tăng giá đạo năm ngàn vạn, tất cả mọi người điên rồi, nguyên một đám trợn mắt nhìn."),i("br"),i("br"),n._v("Lưu sư cũng nắm đấm xiết chặt."),i("br"),i("br"),n._v("Hắn trở thành danh sư nhiều năm như vậy, đâu chịu nổi cơn giận như thế!"),i("br"),i("br"),n._v("“Làm sao? Muốn giải quyết riêng cũng nhanh chút, đừng ở chỗ này vết mực, thời gian của ta quý giá, không có công phu cùng các ngươi tiêu hao!”"),i("br"),i("br"),n._v("Bạch Thiềm y sư nâng chung trà lên, một mặt đắc ý nhìn qua."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Lưu sư khuôn mặt xanh xám."),i("br"),i("br"),n._v("“Lưu sư, ta cùng bọn hắn đi, rõ ràng là hắn đùa bỡn ta, ngược lại vu hãm ta trộm lấy dược liệu, ta không tin, toàn bộ Thiên Vũ vương quốc không có vương pháp!”"),i("br"),i("br"),n._v("Triệu Nhã tiến về phía trước một bước, cất cao giọng nói."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Bên trong căn phòng Trương Huyền, rửa mặt xong chính muốn ngủ, đột nhiên sững sờ."),i("br"),i("br"),n._v("“Nghe có chút giống... Triệu Nhã? Chẳng lẽ bọn hắn tới?”"),i("br"),i("br"),n._v("(Thứ hai cầu phiếu đề cử, cảm tạ, chương sau sắp xếp cái thi đấu!)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-306"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-308"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);