(window.webpackJsonp=window.webpackJsonp||[]).push([[1531],{1886:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-300-doa-dam-bach-y-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-300-doa-dam-bach-y-su"}},[n._v("#")]),n._v(" Chương 300: Doạ dẫm Bạch y sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Mạc Vũ công chúa mặc dù so ra kém mệnh danh Thiên Vũ vương quốc ngàn năm đệ nhất Mạc Hoằng Nhất, nhưng cũng là siêu cấp thiên tài, địa vị vượt xa mặt khác vương tử, công chúa."),t("br"),t("br"),n._v("Hiện tại càng là lập tức khảo hạch danh sư thành công, thâm thụ bệ hạ ưu ái."),t("br"),t("br"),n._v("Thậm chí có nghe đồn, sẽ trọng điểm bồi dưỡng, trở thành đời tiếp theo quân chủ người ứng cử, tốt phòng ngừa đại vị rơi vào Mạc Hoằng Nhất trong tay."),t("br"),t("br"),n._v("Cùng hắn quan hệ không phải bình thường? Còn kết bạn mà đi?"),t("br"),t("br"),n._v("Chẳng lẽ lại..."),t("br"),t("br"),n._v("Bọn hắn chỉ là một đám tiểu binh sĩ, bốn phía tuần tra, thông qua cật nã tạp yếu, giãy chút dầu nước... Đắc tội công chúa “Bằng hữu”, đây không phải muốn chết sao?"),t("br"),t("br"),n._v("“Thật...?” Run lên trong lòng, Diêu đội trưởng còn có chút không dám tin tưởng."),t("br"),t("br"),n._v("Nào có trùng hợp như vậy, tùy tiện đụng tới công chúa bằng hữu?"),t("br"),t("br"),n._v("Lại nói, công chúa luôn luôn giữ mình trong sạch, vô số thiên tài thiếu niên liều mạng truy cầu, đều không chút nào để ý, chưa từng nghe qua, có cái “Bằng hữu” a?"),t("br"),t("br"),n._v("“Ta cũng hy vọng là giả...” Bạch Thiềm y sư tràn đầy nhanh muốn khóc lên biểu lộ."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hắn cái bộ dáng này, đám người mặc dù cảm thấy không may, nhưng cũng biết, khẳng định là sự thật."),t("br"),t("br"),n._v("Đây chính là gần nhất như mặt trời ban trưa Bạch Thiềm y sư, bị đánh thành như vậy, đều không dám phản kháng, đủ để chứng minh vấn đề."),t("br"),t("br"),n._v("“Đã như vậy, Bạch y sư hảo ý chúng ta tựu nhận, như vậy cáo từ!”"),t("br"),t("br"),n._v("Giãy dụa lấy đứng dậy, Diêu đội trưởng liền ôm quyền."),t("br"),t("br"),n._v("Bạch y sư đều đắc tội không nổi, bọn hắn tự nhiên lại không dám đắc tội, cùng hắn ở chỗ này tìm bị đánh, còn không bằng quay người rời đi."),t("br"),t("br"),n._v("Tại vương thành tuần tra, không hiểu ánh mắt, sớm đã bị người đánh chết, cái nào có thể sống đến bây giờ."),t("br"),t("br"),n._v("Vô luận từ Bạch y sư, vẫn là Mạc Vũ công chúa, gia hỏa này đều đắc tội không nổi, đã như vậy, sớm một chút thoát thân mới là lựa chọn tốt nhất."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Thời gian qua một lát, đội tuần tra người liền đi sạch sẽ."),t("br"),t("br"),n._v("“Như vậy liền đi?”"),t("br"),t("br"),n._v("Mới vừa rồi còn lo lắng vô cùng Lưu Lăng bọn người, nguyên một đám trợn mắt hốc mồm."),t("br"),t("br"),n._v("Nhất là Lục Tầm, vừa cảm thấy vị này Trương sư bá thực sự quá lỗ mãng, thấy cảnh này, sắc mặt run rẩy, nhanh muốn biến thành tảng đá."),t("br"),t("br"),n._v("Đây chính là vương quốc đội tuần tra, đại biểu vương quốc tôn nghiêm, nguyên một đám bị đánh đi theo đầu heo đồng dạng, liên tục một câu đều không có bàn giao liền đi?"),t("br"),t("br"),n._v("Đám người kinh ngạc còn không có kết thúc, tựu nhìn vừa rồi phách lối liên tục Lưu sư đều không để vào mắt Bạch y sư, cung kính đi tới Trương lão sư trước mặt, một mặt kính sợ: “Cái này... Tiền bối, ta đã xử lý xong, ngươi nhìn... Ta có thể đi không?”"),t("br"),t("br"),n._v("Chúng mắt người lần nữa rơi đầy đất."),t("br"),t("br"),n._v("Tiền bối?"),t("br"),t("br"),n._v("Ngươi cũng già bảy tám mươi tuổi sắp xuống mồ, đối phương liên tục hai mươi cũng chưa tới, hô người ta tiền bối?"),t("br"),t("br"),n._v("Muốn hay không không biết xấu hổ như vậy?"),t("br"),t("br"),n._v("“Chẳng lẽ... Thiên Vũ vương quốc người đều bị coi thường, nhất định phải đánh lên một chầu mới được?”"),t("br"),t("br"),n._v("Viên Đào không nhịn được cô."),t("br"),t("br"),n._v("Nghe nói như thế, đám người mặt ngoài mặc dù im lặng, nhưng cũng tràn đầy đồng ý."),t("br"),t("br"),n._v("Trương lão sư đến cùng làm sao làm?"),t("br"),t("br"),n._v("Dựa theo vừa rồi gia hỏa này nước tiểu tính, còn không có đánh, tựu xù lông, dự định làm cho toàn thành đều biết, thật như vậy dùng bình nước đem hắn mở, lại liên tục đánh mười cái cái tát, còn không đem đám người giết?"),t("br"),t("br"),n._v("Nhưng bây giờ, lại cùng chó xù đồng dạng, để cho người ta không rõ ràng cho lắm, chỉ có thể âm thầm đối vị này Trương lão sư bội phục không thôi."),t("br"),t("br"),n._v("Có vẻ như... Mặc kệ nhiều khó khăn sự tình, đến trong tay hắn, đều không gọi chuyện gì."),t("br"),t("br"),n._v("Lục Tầm trước đó vẫn tồn tại muốn cùng đối phương lòng so sánh, hiện tại xem ra, loại ý nghĩ này, thật sự là buồn cười!"),t("br"),t("br"),n._v("“Đi? Đừng có gấp, ta không phải không nói lý người, làm việc, khẳng định sẽ phụ trách, đưa ngươi đánh cho một trận, có chút xấu hổ.” Trương Huyền nhìn qua: “Ngươi vết thương trên người giải quyết như thế nào?”"),t("br"),t("br"),n._v("“Ta đây là ta gieo gió gặt bão, tùy tiện tìm một chút thuốc bột bôi một cái liền tốt...” Bạch Thiềm y sư gượng cười."),t("br"),t("br"),n._v("Để hắn chịu trách nhiệm?"),t("br"),t("br"),n._v("Hắn cũng không dám!"),t("br"),t("br"),n._v("Không nói công chúa quan hệ, đơn nói đối phương chữa khỏi liên tục tam tinh y sư đều không chữa khỏi khế ước cổ, y đạo thực lực chi cao, Thiên Vũ vương quốc không có người so ra mà vượt, tại tăng thêm cùng Đại Dược Vương quan hệ..."),t("br"),t("br"),n._v("Để hắn chịu trách nhiệm? Không là muốn chết sao?"),t("br"),t("br"),n._v("“Nếu là gieo gió gặt bão, vậy ta tựu mặc kệ, bất quá... Đệ tử ta bị ngươi vu hãm, trong lòng nhận lấy to lớn tổn thương, tính thế nào?” Biết hắn nghĩ cái gì, kiêng kị cái gì, Trương Huyền nhàn nhạt nhìn qua."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Bạch Thiềm y sư khóe miệng giật một cái."),t("br"),t("br"),n._v("“Không cần ngươi nha ta nha, không phải mới vừa nói gốc kia dược liệu giá trị năm ngàn vạn sao? Như vậy đi, học trò ta tâm lý thụ thương, liền lấy số tiền này đền bù đi! Tiền giao ra, xéo ngay cho ta, không giao ra được, có tin ta hay không gọi ngay bây giờ đoạn cho ngươi gây tai hoạ đồ vật!”"),t("br"),t("br"),n._v("Nói, Trương Huyền con mắt liếc tới, rơi vào một chỗ, dọa đến Bạch Thiềm y sư kìm lòng không được run run một cái."),t("br"),t("br"),n._v("“Nhiều tiền như vậy... Ta thật không có!”"),t("br"),t("br"),n._v("Một lát sau, vẻ mặt cầu xin, Bạch Thiềm y sư nhịn không được nói."),t("br"),t("br"),n._v("Hắn chỉ là cái nhất tinh y sư, những năm này mặc dù hãm hại lừa gạt, hỏng sự tình đã làm nhiều lần, trên thực tế lại không để dành được bao nhiêu tiền."),t("br"),t("br"),n._v("“Không có?”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền bàn tay bỗng nhiên duỗi ra, đâm thẳng đối phương cái trán."),t("br"),t("br"),n._v("Bạch Thiềm y sư giật nảy mình, cổ về sau ngửa mặt lên, lập tức miệng có chút mở ra."),t("br"),t("br"),n._v("Trương Huyền cong ngón búng ra, “Hô!” Một cái, một vật tựu thẳng tắp bắn vào cổ họng của hắn."),t("br"),t("br"),n._v("“Khụ khụ... Đây là cái gì...”"),t("br"),t("br"),n._v("Nắm vuốt cuống họng, Bạch Thiềm y sư sắc mặt trở nên trắng bệch."),t("br"),n._v("“Không có gì, chỉ là từ Đại Dược Vương nơi đó lấy được đồ tốt, cho ngươi nếm thử, ngươi không phải chữa khỏi Đại Dược Vương bệnh, cùng hắn rất quen thuộc sao? Có thể đi tìm hắn hỏi một chút!” Trương Huyền một mặt không quan trọng."),t("br"),t("br"),n._v("“Ta...” Hơi đỏ mặt, chỉ cảm thấy hai chân như nhũn ra, Bạch Thiềm y sư tại cũng quả thực không ở, quỳ rạp xuống đất: “Tiền bối, ngươi đại nhân có đại lượng, bỏ qua cho ta đi...”"),t("br"),t("br"),n._v("Đại Dược Vương là Độc điện người phát ngôn, chỗ của hắn lấy ra đồ vật là cái gì, không cần đầu óc cũng có thể minh bạch..."),t("br"),t("br"),n._v("“Muốn cho ta tha cho ngươi cũng được, vừa vặn chúng ta tới đến vương thành, không có chỗ ở, ngươi đi Danh Sư đường phụ cận, tìm một bộ phủ đệ! Chuẩn bị tốt, có thể cân nhắc giải quyết cho ngươi, chuẩn bị không tốt... Ta nhìn cũng sẽ không cần ta xuất thủ, Đại Dược Vương thuốc, tựu đủ tiễn ngươi một đoạn đường!”"),t("br"),t("br"),n._v("Trương Huyền tùy ý khoát tay."),t("br"),t("br"),n._v("Gia hỏa này mặc dù sắc đảm bao thiên, tiểu nhân vật một cái, nhưng cũng có thể dùng chỗ."),t("br"),t("br"),n._v("Nhóm người mình vừa đến nơi đây, chưa quen cuộc sống nơi đây, từ hắn từ đó chiếu ứng, không ít khó khăn sự tình, tựu sẽ trở nên rất đơn giản."),t("br"),t("br"),n._v("Tình huống hôm nay, có thể trực tiếp xuất thủ, là bởi vì gia hỏa này nhận biết mình, biết mình cùng Mạc Vũ công chúa cùng một chỗ, có chút cáo mượn oai hùm chi nghi ngờ."),t("br"),t("br"),n._v("Đổi lại lúc khác, thật muốn xuất thủ, ngược lại hoàn toàn ngược lại."),t("br"),t("br"),n._v("Cho nên, có cái người biết chuyện ở bên cạnh, dù sao cũng so tràn đầy hồ đồ mạnh hơn nhiều."),t("br"),t("br"),n._v("“Tiền bối...”"),t("br"),t("br"),n._v("Mặt mũi tràn đầy phát khổ, Bạch Thiềm y sư như cha mẹ chết."),t("br"),t("br"),n._v("Chuyện này là sao."),t("br"),t("br"),n._v("Tựu uống một chút rượu, nhìn thấy cái mỹ nữ đùa giỡn một chút, kết quả, bị người dội nước sôi rồi không nói, còn phải bồi thường đối phương một tòa phủ đệ..."),t("br"),t("br"),n._v("Thiên hạ nào có bị đánh còn muốn bồi thường tiền đạo lý?"),t("br"),t("br"),n._v("“Thế nào, không đi? Vậy thì tốt, Đại Dược Vương cho ta cái này thuốc, nghe nói ăn về sau, trong đầu hội trưởng đầy giòi bọ, từ đó làm cho thần trí hỗn loạn, nghiêm trọng thời điểm, sẽ xé y phục rách rưới, bốn phía chạy trần truồng... Tiếp tục ba ngày mới có thể chết, không biết có hợp hay không ngươi khẩu vị?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Bạch Thiềm y sư toàn thân run rẩy, dọa đến sắc mặt trắng bệch."),t("br"),t("br"),n._v("Hắn mặc dù hành vi không bị kiềm chế, nhưng nếu như thật làm cho hắn cởi sạch quần áo bốn phía chạy trần truồng, thật không cần sống."),t("br"),t("br"),n._v("“Thế nào? Không hợp khẩu vị? Cái này cũng đơn giản, lúc trước hắn cho ta mười tám loại dược vật, không hợp khẩu vị, ta có thể từng loại ở trên thân thể ngươi thử một chút!” Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Không muốn...” Mặt mũi tràn đầy khóc ý, Bạch Thiềm y sư lắc đầu liên tục: “Tiền bối không cần làm phiền, không liền tìm cái phủ đệ nha, cái này đơn giản, ta hiện tại liền đi, ngày mai liền giúp ngươi tìm xong!”"),t("br"),t("br"),n._v("Đã trúng độc, sinh tử đã bị đối phương khống chế, đã không cách nào phản kháng, vậy liền... Thuận theo đi!"),t("br"),t("br"),n._v("“Ừm, cái này còn tạm được, Tôn Cường!” Nghe hắn đáp ứng, Trương Huyền quay đầu nhìn sang."),t("br"),t("br"),n._v("“Thiếu gia!”"),t("br"),t("br"),n._v("Tôn Cường đi tới."),t("br"),t("br"),n._v("Dương sư là lão gia, vị này là Dương sư học sinh, tự nhiên là thiếu gia."),t("br"),t("br"),n._v("“Đi theo hắn, đi tìm một cái ngươi hài lòng phủ đệ, thu thập đi ra, khả năng hai ngày nữa, lão sư ta cũng sẽ tới!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Lão gia cũng tới? Vâng, vâng!” Tôn Cường tràn đầy hưng phấn gật đầu."),t("br"),t("br"),n._v("“Còn không đi, chẳng lẽ còn phải chờ đợi ăn cơm?” Trương Huyền hai tay chắp sau lưng."),t("br"),t("br"),n._v("“Vâng!” Bạch Thiềm y sư liền vội vàng gật đầu, xoay người rời đi, một khắc đều không dám dừng lại."),t("br"),t("br"),n._v("Rời đi lữ quán, lúc này mới thở phào nhẹ nhõm, nhịn không được nhìn về phía cùng lên đến Tôn Cường."),t("br"),t("br"),n._v("“Không biết... Nhà các ngươi vị thiếu gia này, tên gọi là gì?”"),t("br"),t("br"),n._v("Lần trước tại Hồng Liên thành nhìn thấy, chỉ nói là bản thân người sùng bái tựu bị nện ngất, hôm nay lần nữa gặp được, cũng không có cho thấy thân phận."),t("br"),t("br"),n._v("Mặc dù đối với hắn kiêng dè không thôi, nhưng lại không biết đến cùng kêu cái gì."),t("br"),t("br"),n._v("“Nhà chúng ta thiếu gia gọi Trương Huyền!”"),t("br"),t("br"),n._v("Tôn Cường gật đầu."),t("br"),t("br"),n._v("“Trương Huyền?” Bạch Thiềm y sư mặt mũi tràn đầy nghi hoặc, những năm này chưa từng nghe qua cái nào vương tử hoặc là lợi hại gì thiên tài gọi cái tên này ah!"),t("br"),t("br"),n._v("“Vừa rồi các ngươi nói lão gia... Không biết lão gia các ngươi là ai?”"),t("br"),t("br"),n._v("Mặc dù kỳ quái, cũng không dám có chút khinh thị, tiếp tục hỏi."),t("br"),t("br"),n._v("“Lão gia chúng ta, là vượt qua tam tinh danh sư!” Tôn Cường một mặt sùng bái."),t("br"),t("br"),n._v("“Vượt qua tam tinh... Danh sư?”"),t("br"),t("br"),n._v("Bạch Thiềm y sư thân thể run lên."),t("br"),t("br"),n._v("Thiên Vũ vương quốc lợi hại nhất Khương sư, Danh Sư đường đường chủ, cũng chỉ là một vị nhị tinh danh sư mà thôi, vượt qua tam tinh khái niệm gì?"),t("br"),t("br"),n._v("Có thể nói, chỉ cần nguyện ý, một cái tay liền có thể làm cho cả Thiên Vũ vương quốc hủy diệt!"),t("br"),t("br"),n._v("Cái này Trương Huyền, lại là loại người này vãn bối?"),t("br"),t("br"),n._v("“Đúng vậy a, thiếu gia làm của hắn học sinh thân truyền, tận được chân truyền, lần này tới vương thành tựu là khảo hạch danh sư! Không chỉ có như thế, hắn còn là một vị tam tinh thư hoạ sư, nhị tinh tuần thú sư (từ Thẩm Bích Như miệng bên trong biết được), nhất tinh luyện đan sư!” Tôn Cường nói tiếp."),t("br"),t("br"),n._v("“Tam tinh... Thư hoạ sư? Nhị tinh tuần thú sư...”"),t("br"),t("br"),n._v("Bạch Thiềm y sư thân thể cứng ngắc."),t("br"),t("br"),n._v("Khó trách tuổi còn trẻ tựu lợi hại như vậy, tùy tiện một cái thân phận xuất ra đi, đều kinh thiên động địa, chỉ sợ liên tục... Mạc Vũ công chúa đều hơi có không bằng!"),t("br"),t("br"),n._v("“Cố gắng hiệu trung thiếu gia cùng lão gia, có ngươi cả một đời đều không hưởng thụ được chỗ tốt, phản bội, kết cục ta không cần phải nói, ngươi hẳn là cũng có thể nghĩ đến!” Biết đối phương nghĩ cái gì, Tôn Cường gõ một câu."),t("br"),t("br"),n._v("“Vâng!” Bạch Thiềm y sư gật đầu, nghĩ đến cái này vài ngày kinh lịch, trong lòng đột nhiên nổi lên gợn sóng..."),t("br"),t("br"),n._v("Mặc dù hôm nay rất không may, nhưng có lẽ, cũng thật sự là một lần ngàn năm một thuở cơ hội tốt!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bạch y sư cùng Tôn Cường rời đi, Trương Huyền chính muốn nghỉ ngơi, liền thấy đám người một mặt hiếu kỳ vây quanh."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-308"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-310"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);