(window.webpackJsonp=window.webpackJsonp||[]).push([[1538],{1893:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-306-cao-nhan-【-canh-3"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-306-cao-nhan-【-canh-3"}},[n._v("#")]),n._v(" Chương 306: Cao nhân 【 Canh [3] ]")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 306: Cao nhân 【 Canh [3] ]"),t("br"),t("br"),n._v("“101?”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất thân thể run lên, suýt chút nữa không có té ngã trên đất."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Phụ tu sảnh cao nhất chỉ có 100 điểm, ngươi đến cái 101?"),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("Khó trách cái này thuộc hạ như là nổi điên, không ngừng run rẩy, đổi lại hắn, cũng muốn run rẩy ah!"),t("br"),t("br"),n._v("Hắn làm cái 60 điểm, liền tên nổi như cồn, để vô số người hâm mộ, 101 cái quỷ gì?"),t("br"),t("br"),n._v("“Mấu chốt nhất là... 101 giống như không phải điểm cuối cùng, hắn điểm số còn đang tăng thêm, phụ tu sảnh khảo hạch bây giờ còn chưa kết thúc...” Thanh niên nhịn không được nói."),t("br"),t("br"),n._v("“Còn không có kết thúc?”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất có chút nhớ nhung nắm tóc."),t("br"),t("br"),n._v("Khảo hạch nhất tinh danh sư, phụ tu sảnh vượt qua 60 điểm đều rất ít gặp, có thể nói cơ hồ không có, đến cái 101... Mấu chốt vẫn chưa xong..."),t("br"),t("br"),n._v("Điên rồi, triệt để điên rồi!"),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Rốt cuộc kìm nén không được, nhấc chân liền đi ra ngoài."),t("br"),t("br"),n._v("“Điện hạ, vậy ta trừng phạt...” Đang muốn theo tới, thanh niên chần chờ một chút, nhịn không được hỏi."),t("br"),t("br"),n._v("“Trừng phạt? Cái gì trừng phạt? Đi nhận lấy ban thưởng, Dạ Minh Châu mười khỏa, thép tinh kiếm một thanh!”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất khoát tay, nhanh chân hướng danh sư khảo hạch địa phương đi đến."),t("br"),t("br"),n._v("Có nghe hay không trừng phạt, còn có ban thưởng, thanh niên biết lần này chạy tới bẩm báo đúng, sắc mặt vui mừng, cũng vội vàng đi theo."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đồng dạng một màn cũng phát sinh ở địa phương khác."),t("br"),t("br"),n._v("Luyện đan sư công hội."),t("br"),t("br"),n._v("“Chúc mừng, chúc mừng!”"),t("br"),t("br"),n._v("“Hồng đan sư rốt cục đột phá, trở thành tam tinh Đan sư, để cho chúng ta đã hâm mộ lại bội phục!”"),t("br"),t("br"),n._v("“Đúng vậy a, lúc trước chúng ta cùng một chỗ khảo hạch nhị tinh Đan sư, kết quả là ngươi bước ra một bước này!”"),t("br"),t("br"),n._v("Đông đảo luyện đan sư vây quanh một người trung niên."),t("br"),t("br"),n._v("Người này một bộ trường bào, ngực một cái tam tinh luyện đan sư huy chương, lập loè chói mắt."),t("br"),t("br"),n._v("Chính là vừa trở thành tam tinh luyện đan sư không lâu Hồng đan sư."),t("br"),t("br"),n._v("Đoạn thời gian trước liền khảo hạch xong, hôm nay đại biểu thân phận huy chương vừa mới đến."),t("br"),t("br"),n._v("Thiên Vũ vương quốc luyện đan sư công hội, lợi hại nhất cũng liền tam tinh, đạt tới loại này cấp bậc, đã cùng hội trưởng giống nhau."),t("br"),t("br"),n._v("“Chư vị khách khí, ta cũng là cơ duyên xảo hợp, đoạn thời gian trước ra ngoài thời điểm, gặp được một vị cao nhân, chỉ điểm một cái, lúc này mới hiểu ra. Nếu là nhờ vào năng lực bản thân, còn không biết năm nào tháng nào!” Hồng đan sư nói."),t("br"),t("br"),n._v("“Gặp được cao nhân cũng muốn ngộ tính tốt mới được, cũng không biết chúng ta khi nào mới có thể đột phá!”"),t("br"),t("br"),n._v("Nghe hắn khiêm tốn, một vị luyện đan sư cười khổ lắc đầu."),t("br"),t("br"),n._v("“Không xong...”"),t("br"),t("br"),n._v("Nhưng vào lúc này, một cái luyện đan học đồ nóng nảy vọt vào."),t("br"),t("br"),n._v("“Vội vàng hấp tấp còn thể thống gì?”"),t("br"),t("br"),n._v("Thấy rõ ràng cái này học đồ chính là học sinh của hắn, Hồng đan sư khoát tay chặn lại."),t("br"),t("br"),n._v("“Vâng!” Học đồ ngừng lại, bởi vì sốt ruột, miệng lớn thở dốc."),t("br"),t("br"),n._v("“Ta vừa rồi cho ngươi đi qua hỏi Khương Thần danh sư yến hội sự tình, hắn nói thế nào, Mạc Vũ công chúa có thể hay không tham gia?” Hồng đan sư nhìn qua."),t("br"),t("br"),n._v("“Ta...” Học đồ sững sờ: “Ta... Còn không có hỏi!”"),t("br"),t("br"),n._v("“Không có hỏi?” Hồng đan sư sầm mặt lại, mang theo lửa giận: “Chút chuyện nhỏ này đều làm không xong, vội vàng hấp tấp, về sau cũng không cần tại môn hạ của ta làm học đồ!”"),t("br"),t("br"),n._v("Học đồ sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Hồng đan sư là tam tinh Đan sư, tại bọn họ bên dưới làm học đồ, điểm xuất phát so với bình thường cao hơn lên không ít, về sau khảo hạch nhất tinh Đan sư mang có rất lớn ưu thế."),t("br"),t("br"),n._v("Một khi khai trừ... Cơ hồ tương đương luyện đan sư vô vọng."),t("br"),t("br"),n._v("“Lão sư, ta... Bối rối là có nguyên nhân, vừa rồi... Có người khảo hạch nhất tinh danh sư, tiến vào phụ tu sảnh thời điểm, lựa chọn luyện đan, kết quả...” Học đồ thân thể run rẩy."),t("br"),t("br"),n._v("“Thế nào? Chẳng lẽ hắn thi ra 60 điểm, so Mạc Hoằng Nhất Mạc sư còn cao hơn hay sao?” Hồng đan sư khoát tay."),t("br"),t("br"),n._v("Học đồ run run một cái: “Hắn... Hắn... Thi ra 101 điểm?”"),t("br"),t("br"),n._v("“Cái gì? 101 điểm?”"),t("br"),t("br"),n._v("Hồng đan sư thân thể nhoáng một cái, tròng mắt suýt chút nữa trừng rơi ra đến, khắp khuôn mặt là không thể tin được."),t("br"),t("br"),n._v("Mặc dù luyện đan sư cùng danh sư, thuộc về hai loại chức nghiệp, nhưng rất bao nhiêu lợi hại danh sư phụ tu luyện đan, lẫn nhau trong lúc đó vẫn là hết sức quen thuộc."),t("br"),t("br"),n._v("Phụ tu sảnh khảo hạch, chỉ có 100 điểm, thi ra cái 101, chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Phụ tu sảnh 101 điểm?”"),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Nghe xong học đồ, đám người tất cả đều điên rồi, đồng loạt đi ra ngoài."),t("br"),t("br"),n._v("Một cái có thể tại phụ tu sảnh chỉ điểm luyện đan, làm ra 101 điểm người, nên mạnh bao nhiêu?"),t("br"),t("br"),n._v("Bọn hắn đều muốn nhìn một chút."),t("br"),t("br"),n._v("Luyện đan sư công hội cùng Danh Sư đường liền nhau, thời gian không dài liền đến đến khảo hạch địa phương, lúc này trong đại sảnh đã chất đầy người, tất cả đều đồng loạt nhìn về phía ngay phía trước."),t("br"),t("br"),n._v("“Khương sư, đây là có chuyện gì?”"),t("br"),t("br"),n._v("Nhìn quanh một tuần, Hồng đan sư tìm được một cái bóng người quen thuộc, đi tới trước mặt."),t("br"),t("br"),n._v("Chính là trước kia Khương Thần."),t("br"),t("br"),n._v("Hắn lúc này, con mắt trừng sắp rơi ra đến, thân thể không tự chủ được run rẩy, nắm đấm xiết chặt, một mặt hoảng sợ, thấy Hồng đan sư đến, chỉ về phía trước."),t("br"),t("br"),n._v("“Chính ngươi nhìn...”"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Ngẩng đầu nhìn lại, thấy rõ ràng trên vách tường biểu hiện số lượng, Hồng đan sư cũng là thân thể nhoáng một cái, suýt chút nữa không có té ngã trên đất."),t("br"),t("br"),n._v("“130? Tại sao có thể có 130? Phụ tu sảnh max điểm không mới 100 sao?”"),t("br"),t("br"),n._v("Một tiếng la lên, hắn suýt chút nữa không điên mất."),t("br"),t("br"),n._v("Chỉ thấy phía trước trên vách tường, đồng loạt bốn chữ “Một trăm ba mươi”, cái này giống như là đâm vào trái tim tất cả mọi người bên trong, đi theo phía sau hắn tới tất cả luyện đan sư, tất cả đều nguyên một đám cứng ngắc tại nguyên chỗ, trợn mắt hốc mồm."),t("br"),t("br"),n._v("“Cụ thể chuyện gì xảy ra, ta cũng không biết...”"),t("br"),t("br"),n._v("Khương Thần lắc đầu, cũng có chút phát điên: “Có thể là phụ tu sảnh xảy ra vấn đề đi, bằng không thì làm sao lại có loại này thành tích?”"),t("br"),t("br"),n._v("“Xảy ra vấn đề?”"),t("br"),t("br"),n._v("“Đúng vậy a, không xảy ra vấn đề, tại sao có thể có cao như vậy thành tích?”"),t("br"),t("br"),n._v("“Xảy ra vấn đề nên một lần nữa khảo hạch, cái thành tích này không tính!”"),t("br"),t("br"),n._v("Chung quanh có người hưởng ứng hai tiếng."),t("br"),t("br"),n._v("Max điểm một trăm, kết quả làm ra cái một trăm ba, là ai đều sẽ hướng cái phương hướng này suy nghĩ."),t("br"),t("br"),n._v("“Một lát nữa ta đi tìm Ngô sư nói một chút...” Khương Thần gật gật đầu, chính muốn nói tiếp, liền bị một thanh âm đánh gãy."),t("br"),t("br"),n._v("“Khảo hạch đại sảnh là danh sư công hội chuyên môn phái cường giả tu luyện, là một kiện Linh Bảo, không có khả năng xảy ra vấn đề!”"),t("br"),t("br"),n._v("Nghe được đối phương không cho mặt mũi như vậy, Khương Thần đang muốn quát lớn, thấy rõ ràng dung mạo của đối phương, lập tức câm xuống dưới: “Mạc sư đã nói không phải xảy ra vấn đề, cái kia... Đây rốt cuộc chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Tới chính là vương quốc đệ nhất thiên tài, Mạc Hoằng Nhất."),t("br"),t("br"),n._v("Hắn nhìn thấy trên vách tường thành tích, hiển nhiên cũng giật nảy mình."),t("br"),t("br"),n._v("“Ta nghe qua một cái truyền thuyết, là liên quan tới danh sư khảo hạch, không biết có phải hay không thật!” Mạc Hoằng Nhất sắc mặt nghiêm túc."),t("br"),t("br"),n._v("Gặp hắn vẻ mặt này, người chung quanh tất cả đều đồng loạt nhìn qua."),t("br"),t("br"),n._v("Liền liền chủ trì khảo hạch Ngô sư, cũng không nhịn được đi tới trước mặt."),t("br"),t("br"),n._v("“Phụ tu sảnh, khảo hạch là chỉ điểm, chỉ điểm sai, sẽ trừ điểm, chính xác, điểm số gia tăng, điểm ấy chắc hẳn không cần ta nói, mọi người đều biết!” Mạc Hoằng Nhất nói."),t("br"),t("br"),n._v("Đám người gật đầu."),t("br"),t("br"),n._v("“Nhưng mọi người có nghĩ tới không... Cái này chính xác, sai lầm, là căn cứ cái gì phán đoán?”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất nhìn quanh một tuần, nhìn về phía đám người: “Thật giống như hiện tại khảo hạch luyện đan, trong đó cái gọi là chính xác, là dùng tứ tinh luyện đan sư luyện chế nhất tinh đan dược làm tiêu chuẩn để cân nhắc. Nếu như là... Ngũ tinh luyện đan sư, lục tinh luyện đan sư ánh mắt đến xem cái này luyện đan đâu?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đám người toàn thân đồng thời chấn động."),t("br"),t("br"),n._v("Tri thức là lẫn nhau đúng."),t("br"),t("br"),n._v("Nhất tinh luyện đan sư cho rằng là lời lẽ chí lý, chính xác sự tình, đặt ở tam tinh trong mắt, liền chưa hẳn chính xác."),t("br"),t("br"),n._v("Hài đồng cảm thấy trời là tròn, đất là vuông, trưởng thành liền không nghĩ như vậy."),t("br"),t("br"),n._v("Phụ tu sảnh cân nhắc luyện đan tiêu chuẩn là tứ tinh luyện đan sư, cho là hắn làm được hết thảy đều là chính xác, theo mà tiến hành bình phán."),t("br"),t("br"),n._v("Chỉ cần luyện đan lên không có đạt tới tứ tinh, khẳng định cũng sẽ cho rằng cử động của hắn không có bất kỳ cái gì sai lầm, nhưng nếu như... Vượt qua tứ tinh đâu?"),t("br"),t("br"),n._v("Nếu như là ngũ tinh luyện đan sư, thậm chí lục tinh, thất tinh người tới, chẳng lẽ cũng nhận vì cái này là chính xác sao?"),t("br"),t("br"),n._v("Chỉ sợ cũng chưa hẳn."),t("br"),t("br"),n._v("Có lẽ tại loại này cường giả trong mắt, chi trước định ra tiêu chuẩn đáp án, cũng là trăm ngàn chỗ hở, sai lầm liên tục."),t("br"),t("br"),n._v("“Ngươi nói là... Bên trong khảo hạch gia hỏa, luyện đan thuật đã vượt qua tứ tinh?”"),t("br"),t("br"),n._v("Khương Thần làm sao đều tin tưởng."),t("br"),t("br"),n._v("Cái kia nhìn rất phổ thông tiểu tử, làm sao có thể có lợi hại như vậy luyện đan thuật?"),t("br"),t("br"),n._v("“Nếu như không phải, ngươi giải thích như thế nào, trước trừ điểm, sau đó bạo tăng? Ta nhớ được, danh sư khảo hạch cửa ải cuối cùng không sai đường, tìm ra một chỗ sai lầm, là một điểm, hai nơi biến thành hai điểm, ba chỗ ba điểm, cũng là theo thứ tự tăng lên!”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất vẻ mặt nghiêm túc: “Bên trong khảo hạch vị này, nên tìm ra câu trả lời chính xác bên trong lỗ thủng, vừa mới bắt đầu bị phụ tu sảnh phán định sai lầm. Bất quá, phụ tu sảnh là có linh tính bảo vật, phát hiện luyện chế đan dược quá trình bên trong, sửa lại về sau, càng thêm thông thuận, lập tức bổ về điểm số, cũng gia tăng gấp đôi làm khen thưởng.”"),t("br"),t("br"),n._v("“Cái này...” Đám người con ngươi tất cả đều kìm lòng không được co vào."),t("br"),t("br"),n._v("“Nói như vậy đi ra... Bên trong vị này, tìm ra tứ tinh luyện đan sư, trọn vẹn 15 chỗ sai lầm?”"),t("br"),t("br"),n._v("Tứ tinh luyện đan sư thủ pháp luyện đan, hoàn mỹ phát huy, có thể được 100 điểm, bây giờ lại đạt được 130 điểm, nói rõ chí ít tìm ra tiêu chuẩn đáp án bên trong 15 chỗ sai lầm!"),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Còn là người sao?"),t("br"),t("br"),n._v("“Ngoại trừ điểm ấy... Ta thực sự không nghĩ ra đến cùng chuyện gì xảy ra!” Mạc Hoằng Nhất lắc đầu."),t("br"),t("br"),n._v("“Mạc sư vừa nói như vậy, ta nhớ ra rồi, trước đó nhìn Danh Sư đường khảo hạch tương quan thư tịch lúc, tựa hồ trong một quyển sách đề cập tới loại tình huống này. Chỉ bất quá, thời gian lâu dài, không có người làm đến điểm ấy, dần dần bị quên lãng!”"),t("br"),t("br"),n._v("Ngô sư giống là nhớ ra cái gì đó, nhịn không được nói."),t("br"),t("br"),n._v("Nghe được hai vị danh sư xác nhận, đám người giờ mới hiểu được tới, vẫn như cũ đầu não choáng váng, không thể tin được."),t("br"),t("br"),n._v("Không chỉ tìm ra khôi lỗi cố ý lưu lại sai lầm, liền tứ tinh luyện đan sư lưu lại sai lầm đều có thể tìm ra, bên trong vị này, đến cùng tại luyện đan thuật lên đạt đến mấy sao?"),t("br"),t("br"),n._v("“Bên trong vị này kêu cái gì, có phải hay không là công hội vị tiền bối nào đi, không được ta đi dò tra?”"),t("br"),t("br"),n._v("Hồng đan sư nhịn không được nói."),t("br"),t("br"),n._v("“Không phải tiền bối, chỉ là cái nhất tinh luyện đan sư, hơn nữa niên kỷ không đủ hai mươi, hắn gọi...”"),t("br"),t("br"),n._v("Khương Thần hừ một tiếng, đang muốn nói nổi danh tự, liền nghe đến trước mắt phụ tu sảnh đại môn “Kẹt kẹt” một tiếng mở ra."),t("br"),t("br"),n._v("Một thanh niên chậm rãi đi ra."),t("br"),t("br"),n._v("“Ah... Trương Huyền? Hắn làm sao đến nơi này?”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Hồng đan sư nhịn không được la lên."),t("br"),t("br"),n._v("“Hồng đan sư, ngươi biết người này?”"),t("br"),t("br"),n._v("Theo ở phía sau một vị nhị tinh luyện đan sư vội vàng nhìn qua."),t("br"),t("br"),n._v("“Hắn... Hắn...”"),t("br"),t("br"),n._v("Hồng đan sư sắc mặt trắng nhợt: “Chính là ta mới vừa nói vậy cái kia vị, chỉ điểm để cho ta nhanh như vậy mới đột phá... Cao nhân!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-314"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-316"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);